INSERT INTO `t_han_nga` VALUES (3929, '圓丘;大壇', '', '圓丘、大壇，祭天也。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '3913/5233;6598/9146', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3930, '方澤;大折', '', '方澤、大折，祭地也。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '5455/7222;6598/628', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3931, '大昭', '', '大昭，祭四時也。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '6598/4199', '', 'null', 'null');
