INSERT INTO `t_han_nga` VALUES (3937, '四坎壇', '', '四坎壇，祭四方也。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '9720/9086/9146', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3938, '廟;祧;壇;場;鬼', '', '廟、祧、壇、場、鬼，祭先祖也。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '5966;74;9146;9147;5821', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3939, '昔;曹;卒;䬽;膢;臘;祓;絜;餟;祼;軷;䄟;𥛻;夌;禖;禪;祧;醮;禬;卷;望;𩴪;祥;禫;禱;禜;禳;祭', '', '䄍{昔}1、䄚{曹}2、祽{卒}3、祱{䬽}4、䄛{膢}5、臘、祓、禊{絜}6、餟7、祼8、軷9、䄟10、𥛻11、祾{夌}12、禖13、禪、祧14、醮15、禬16、飬{卷}、望、禨{𩴪}17、祥、禫、禱18、禜、禳，祭也。', '[音義-1]：士駕反\n[音義-2]：曹\n[音義-3]：七外\n[音義-4]：税\n[音義-5]：力侯切\n[音義-6]：乎計\n[音義-7]：加税\n[音義-8]：古奐反\n[音義-9]：步末\n[音義-10]：毳\n[音義-11]：布庚\n[音義-12]：陵，又力登\n[音義-13]：梅\n[音義-14]：他聊\n[音義-15]：子咲反\n[音義-16]：古外\n[音義-17]：巨衣\n[音義-18]：倒', '釋天', '廣雅', '', DEFAULT, DEFAULT, '4252;3051;5361;3265;2668;2667;49;8754;3264;45;9573;46;37;3366;58;55;74;9837;54;5786;8415;5831;18;72;51;52;53;30', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3940, '臘;獵/接;蜡;索;淸祀;嘉平;大蜡;臘', '', '臘，〔□{獵/接}也〕。〔蜡〕，索也。夏曰淸祀1，殷曰嘉平2，周曰大蜡，秦曰臘。', '[音義-1]：以淸潔而祭祀\n[音義-2]：嘉，善也；平，成也。以歲終萬物善成，就而報其功', '釋天', '廣雅', '', DEFAULT, DEFAULT, '2667;6360/0/7919;8883;3873;7202/31;3082/3074;6598/8883;2667', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3941, '鬯;薰;茝蘭;蕭;艾', '', '天子祭以鬯，諸侯以薰，卿、大夫以茝蘭，士以蕭，庶人〔以〕艾。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '3201;294;292/286;443;389', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3942, '肆兵', '肄兵', '{肆兵}[肄兵]王者以四時畋，以奉宗廟，因簡戎事。刈草爲防，敺1而射之，不題禽，不垝[恑]遇2，不捷草，越防不追。天子取三十焉，一爲乾梪，二爲賓客，三曰充君之庖，其餘以與士。', '[音義-1]：欺于\n[音義-2]：車美', '釋天', '廣雅', '1', DEFAULT, DEFAULT, '6070/1768', '1938/1768', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3943, '旞;旌;旗', '', '全羽曰旞。析羽曰旌。熊虎曰旗。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '4297;4294;4291', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3944, '杠', '', '天子杠高九仞。諸侯七仞。〔卿〕大夫五仞。〔士三仞〕。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '3676', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3945, '游', '', '{游}天子十二斿，至地。諸侯九斿，至軫。卿大夫七斿，至軹1。士三斿，至肩。', '[音義-1]：紙', '釋天', '廣雅', '', DEFAULT, DEFAULT, '4306', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3946, '年;稔;秋;穀熟', '', '〔年、稔、秋，穀熟也。〕', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '4484;4486;4493;4485/1886', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3947, '四海九州', '', '{四海九州}神農度四海內，東西九十萬里，南北八十一萬里。帝堯所治九州，地二千四百三十萬八千二十四頃，其墾者九百一十萬八千二十四頃。夏禹所治四海內，地東西二萬八千里、南北二萬六千里，出水者八千里，受水者八千里。', '', '釋地', '廣雅', '', DEFAULT, DEFAULT, '9720/7131/9730/7492', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3948, '湖;藪;陂;塘;都;㽘;斥澤;埏;衍;皋;沼;池', '唐;沆;㚖', '湖、藪、陂、塘[唐]、都、㽘[沆]1、斥澤、埏2、衍、皋[㚖]3、沼，池也。', '[音義-1]：古朗\n[音義-2]：延\n[音義-3]：古豪', '釋地', '廣雅', '4;6;10', DEFAULT, DEFAULT, '7260;563;9627;9159;4007;9188;5969/7222;9154;7138;6662;7258;7259', '878;7167;6671', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3949, '都野;孟豬;彭蠡;少厡;振澤;諸毗;沛澤;雷澤;幽都', '孟都/望諸', '都野、孟豬[孟都/望諸]、彭蠡、少厡1、振澤、諸毗2、沛澤3、雷澤、幽都。', '[音義-1]：原\r\n[音義-2]：符夷\n[音義-3]：盃妹', '釋地', '廣雅', '2', DEFAULT, DEFAULT, '4007/9171;9774/6079;3081/8967;705/7496;7963/7222;1488/6702;7100/7222;7528/7222;2502/4007', '9774/4007/0/8415/1488', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3950, '瓊支;瑾瑜;昭華;白珩;琁;璜;弁和;璵璠;垂棘;碧黸;藍田;琜𧶠;琬;琰;璐;唐;璑;囷;赤瑕', '苕華', '瓊支、瑾瑜、昭華[苕華]、白珩1、璇{琁}2、璜、弁和、璵璠、垂棘、碧瓐{碧黸}、藍田、琜瓄{琜𧶠}3、琬、琰、璐4、瑭{唐}5、璑、㻒{囷}6、赤瑕。', '[音義-1]：衡\n[音義-2]：旋\n[音義-3]：來瀆\n[音義-4]：音路\n[音義-5]：唐\n[音義-6]：渠慜', '釋地', '廣雅', '3', DEFAULT, DEFAULT, '94/1935;90/91;4199/3886;4931/123;94;109;5469/846;89/88;9149/4399;185/6543;282/9172;93/3993;113;115;98;878;101;3919;6587/144', '664/3886', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3951, '水精;石英;𤥗離;珊瑚;玟瑰;夜光;隨侯;虎魄;金精;璣', '石瑛', '水精謂之石英[石瑛]。瑠璃{𤥗離}、珊瑚、玟瑰1、夜光、隋侯{隨侯}2、虎魄、金精、璣。', '[音義-1]：梅。古回\n[音義-2]：隋侯見虵傷治之，後虵銜珠以報', '釋地', '廣雅', '2', DEFAULT, DEFAULT, '6984/4353;6011/502;202/2294;200/201;195/196;4328/6511;1102/3313;3118/5824;9261/4353;197', '6011/100', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3952, '蜀石;碝;玟;車渠;馬匘;武夫;琨吾;珉石;咸力;珂', '玪𤨙', '蜀石、碝1、玟2、硨磲{車渠}3、碼碯{馬匘}4、武夫、琨珸{琨吾}5、瑉石{珉石}、瑊玏{咸力}[玪𤨙]6、珂。', '[音義-1]：而兗\n[音義-2]：忙巾\n[音義-3]：車渠\n[音義-4]：馬。奴道\n[音義-5]：昆吾\n[音義-6]：古咸，又咸。勒', '釋地', '廣雅', '9', DEFAULT, DEFAULT, '8828/6011;6014;195;9514/7265;6137/5222;8397/6676;186/837;187/6011;872/9212;213', '158/159', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3953, '合;鷩付;玃如;相繇;化虵', '', '東方有魚焉，如鯉、六足、鳥尾，其名曰鮯{合}1。南方有鳥焉，三首、六目、六足、三翼，其名曰鷩𩿧{鷩付}2。西方有獸焉，如鹿、白尾、馬足、人手、四角，其名曰玃如3。北方有民焉，九首、虵身，其名曰相繇4。中央有虵焉，人面、豺身、鳥翼、虵行，其名曰化虵。此五方之異物也。', '[音義-1]：古合\n[音義-2]：必舌。付于反\n[音義-3]：九縳\n[音義-4]：由', '釋地', '廣雅', '', DEFAULT, DEFAULT, '3271;2466/5058;6374/8245;2162/8516;5216/8995', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3954, '鄰;朋;里;邑;都;師', '', '八家爲鄰，三鄰爲朋，三朋爲里，五里爲邑，十邑爲都，十都爲師，州十有二師焉1。', '[音義-1]：見尙書', '釋地', '廣雅', '', DEFAULT, DEFAULT, '4008;2370;9169;4004;4007;3864', '', 'null', 'null');
