INSERT INTO `t_han_nga` VALUES (4386, '殷虞;晉獒;楚黃;韓黸;宋足;狼狐;㹥;賁', '楚獷;宋獡', '殷虞。晉獒。楚黃[楚獷]1。韓獹{韓黸}。宋㹱{宋足}[宋獡]2。狼狐。㹥3。獖{賁}4。', '[音義-1]：楚有犬名女黃\n[音義-2]：七勺反\n[音義-3]：霔\n[音義-4]：扶粉', '釋嘼', '廣雅', '3;5', DEFAULT, DEFAULT, '5253/3110;4205/6337;3851/9203;3399/6543;4622/1326;6379/6382;6312;3946', '3851/6334;4622/6333', 'null', 'null');
