INSERT INTO `t_han_nga` VALUES (4388, '初;哉;首;基;肈;祖;元;胎;俶;落;權輿;始', '才;肁;路;灌渝', '初、哉[才]1、首、基、肈[肁]23、祖、元、胎4、俶56、落[路]、權輿[灌渝]7，始也。89', '[釋文-1]：子來反，亦作栽\n[音釋-2]：兆。\n[釋文-3]：音趙\n[釋文-4]：天才反，孫炎大才反，本或作台。補某氏曰：「生之始也」\n[音釋-5]：昌叔。\n[釋文-6]：字又作（⿰亻𠦑），尺叔反\n[釋文-7]：巨貟反。音余\n[郭注-8]：尚書曰：「三月哉生魄。」詩曰：「令終有俶。」又曰：「俶載南畝。」又曰：「訪予落止。」又曰：「胡不承權輿。」胚胎未成，亦物之始也。其餘皆義之常行者耳。此所以釋古今之異言，通方俗之殊語。\n[釋文-9]：令，力政反。胚，字又作衃，同，普才反，又匹尤反。淮南子及文子並云：「婦孕三月而胚。」說文云：「胚，婦孕一月也。」「衃，凝血也。」', '釋詁弟一', '爾雅', '2;5;10;11', DEFAULT, DEFAULT, '2754;856;5703;9046;8376;36;2;2597;5017;549;3521/9528;8184', '3858;7728;1410;7044/7441', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4389, '林;烝;天;帝;皇;王;后;辟;公;侯;君', '', '林、烝1、天、帝、皇、王、后、辟23、公、侯，君也。45', '[釋文-1]：本又作蒸，同，之仍反\n[音釋-2]：并亦。\n[釋文-3]：必亦反\n[郭注-4]：詩曰：「有壬有林。」又曰：「文王烝哉。」其餘義皆通見詩、書。\n[釋文-5]：壬，而心反。見，賢遍反，下迄卷終注悉放此。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '3848;6431;3;7;80;78;5771;5798;716;3313;839', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4390, '弘;郭;宏;溥;介;純;夏;幠;厖;墳;嘏;丕;奕;洪;誕;戎;駿;假;京;碩;濯;訏;宇;穹;壬;路;淫;甫;景;廢;壯;冢;𥳑;菿;昄;至;將;業;蓆;大', '廣;𡗦;𡗥;嘏;𣞤;濃;峻;嘏;卓;任;嘏;父;京;㚕;僩/橌;𢧤;壯', '弘、廓{郭}[廣]1、宏2、溥3、介[𡗦]4、純[𡗥]、夏[嘏]5、幠[𣞤]67、厖89、墳10、嘏11、丕12、奕13、洪、誕1415、戎[濃]、駿[峻]16、假[嘏]17、京、碩、濯[卓]18、訏1920、宇、穹21、壬[任]、路[嘏]22、淫、甫[父]、景[京]、廢[㚕]、壯23、冢24、𥳑[僩/橌]、菿2526、昄2728、晊{至}[𢧤]2930、將[壯]、業、蓆31，大也。3233', '[釋文-1]：補孫炎曰：「張之大也」\n[釋文-2]：戸萌反\n[釋文-3]：音普\n[釋文-4]：音界\n[釋文-5]：戸雅反\n[音釋-6]：呼。\n[釋文-7]：火吳反，下同\n[音釋-8]：亡江。\n[釋文-9]：亡江反，又亡項反，方言云：「厖，深之大也」\n[釋文-10]：符云反\n[釋文-11]：古雅反\n[釋文-12]：字又作㔻，同，普悲反\n[釋文-13]：以昔反\n[音釋-14]：但。\n[釋文-15]：音但\n[釋文-16]：子浚反，又荀閏反\n[釋文-17]：古雅反\n[釋文-18]：直角反\n[音釋-19]：吁。\n[釋文-20]：本又作盱，同，香于反\n[釋文-21]：起弓反\n[釋文-22]：補舍人曰：「輅，車之大者」\n[釋文-23]：側狀反\n[釋文-24]：補舍人曰：「封之大也」\n[音釋-25]：罩。\n[釋文-26]：郭陟孝反，顧野王都角反，說文云：「草大也。」孫都耗反\n[音釋-27]：蒲板。\n[釋文-28]：沈旋蒲板反，此依詩讀也，孫、郭方滿反，字林方但、方旦二反，施乾蒲滿反，顧音板，又普姦、普練二反\n[音釋-29]：之日。\n[釋文-30]：舊音之日反，本又作至，又作胵，顧音充尸反。補樊光曰：「可見之大也」\n[釋文-31]：音席\n[郭注-32]：詩曰：「我受命溥將。」又曰：「亂如此幠。」「爲下國駿厖。」「湯孫奏假。」「王公伊濯。」「訏謨定命。」「有壬有林。」「厥聲載路。」「旣有淫威。」「廢爲殘賊。」「爾土宇昄章。」「緇衣之蓆兮。」廓落、宇宙、穹隆、至極，亦爲大也。菿，義未聞。尸子曰：「此皆大，有十餘名而同一實。」\n[釋文-33]：謨，亡乎反。', '釋詁弟一', '爾雅', '2;5;6;7;8;16;17;18;21;25;26;28;29;30;33;36;37', DEFAULT, DEFAULT, '8495;4152;4569;7133;714;8523;3373;4900;5997;9143;1462;4;6669;7135;1644;8379;6170;5076;3331;5630;7421;1670;4566;4672;9764;1410;7223;2083;4213;5963;228;5815;2893;569;4241;7708;1983;1749;578;6598', '5940;6610;6613;1462;3849;7333;5878;1462;5224;5089;1462;1908;3331;6612;5009/0/3586;6606;228', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4391, '幠;厖;有', '撫', '幠[撫]、厖，有也。1', '[郭注-1]：二者又爲有也。詩曰：「遂幠大東。」', '釋詁弟一', '爾雅', '1', DEFAULT, DEFAULT, '4900;5997;4320', '7922', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4392, '迄;臻;極;到;赴;來;弔;艐;格;戾;懷;摧;詹;至', '𨑩;徦;瞻', '迄1、臻2、極3、到、赴、來、弔[𨑩]45、艐67、格[徦]8、戾9、懷、摧1011、詹[瞻]12，至也。1314', '[釋文-1]：許訖反\n[釋文-2]：側巾反\n[音釋-3]：紀力\n[音釋-4]：的。\n[釋文-5]：如字，又音的\n[音釋-6]：宗。\n[釋文-7]：郭音届，孫云：「古届字。」顧子公反\n[釋文-8]：更伯反，字或作𢓜\n[釋文-9]：力帝反\n[音釋-10]：昨雷。\n[釋文-11]：昨雷反，又祖雷反\n[釋文-12]：音占\n[郭注-13]：齊 楚之會郊曰懷，宋曰届。詩曰：「先祖于摧。」又曰：「六日不詹。」詹、摧，皆楚語，方言云。\n[釋文-14]：屆，音界。', '釋詁弟一', '爾雅', '7;9;13', DEFAULT, DEFAULT, '1216;7710;3626;7709;981;3348;5185;5445;3603;6356;6764;7853;713;7708', '1208;1248;2157', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4393, '如;適;之;嫁;徂;逝;往', '', '如、適1、之、嫁、徂、逝，往也。2', '[釋文-1]：傷亦反\n[郭注-2]：方言云：「自家而出謂之嫁，猶女出爲嫁。」', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '8245;1109;3861;8127;1106;1105;1229', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4394, '賚;貢;錫;畀;予;貺;賜', '贛;賜', '賚1、貢[贛]2、錫[賜]、畀34、予56、貺7，賜也。8', '[釋文-1]：力代反，又力臺反\n[釋文-2]：字或作贛，同\n[音釋-3]：必寐。\n[釋文-4]：必寐反，下及注同\n[音釋-5]：羊汝。\n[釋文-6]：羊汝反，下同\n[釋文-7]：許誑反，本或作況\n[郭注-8]：皆賜與也。', '釋詁弟一', '爾雅', '2;3', DEFAULT, DEFAULT, '3959;3948;9266;3024;2510;3995;3961', '3958;3961', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4395, '儀;若;祥;淑;鮮;省;臧;嘉;令;類;綝;彀;攻;穀;介;徽;善', '誼;俶;鱻;鱻;靈;琛;工;价;美', '儀[誼]、若、祥1、淑[俶]2、鮮[鱻]34、省[鱻]56、臧7、嘉、令[靈]89、類、綝[琛]1011、彀1213、攻[工]、穀、介[价]、徽[美]14，善也。1516', '[釋文-1]：補李巡曰：「福之善也」\n[釋文-2]：市六反\n[音釋-3]：息淺。\n[釋文-4]：息淺反，又音仙，本或作𧬊，沈云：「古斯字。」郭音義云：「本或作尠，非古斯字。」案字書：𧬊，先奚反，亦訓善\n[音釋-5]：先郢。\n[釋文-6]：先郢反\n[釋文-7]：子郎反\n[音釋-8]：力政。\n[釋文-9]：力政反\n[音釋-10]：勑金。\n[釋文-11]：勑金反，郭勑淫反\n[音釋-12]：古豆。\n[釋文-13]：古豆反，一音古侯反\n[釋文-14]：音暉\n[郭注-15]：詩曰：「儀刑文王。」左傳曰：「禁禦不若。」詩曰：「永錫爾類。」「我車旣攻。」「介人維藩。」「大姒嗣徽音。」省、綝、彀，未詳其義。餘皆常語。\n[釋文-16]：藩，方元反。大，音泰。', '釋詁弟一', '爾雅', '1;4;5;6;9;11;13;15;16', DEFAULT, DEFAULT, '5084;599;18;7199;7648;2220;1955;3082;5779;6371;8587;8500;2052;4485;714;8691;1735', '1556;5017;7682;7682;206;210;3030;5107;2356', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4396, '舒;業;順;敘;舒;業;順;敘;緒', '', '舒、業、順，敘也。1舒、業、順、敘2，緒也。3', '[郭注-1]：皆謂次敘。\n[釋文-2]：補孫炎曰：「端緒也」\n[郭注-3]：四者又爲端緒。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '2511;1749;5654;2065;2511;1749;5654;2065;8521', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4397, '怡;懌;悅;欣;衎;喜;愉;豫;愷;康;冘;般;樂;悅;懌;愉;釋;賓;恊;服', '媅;昪;𠬝', '怡1、懌2、悅、欣、衎3、喜、愉4、豫、愷5、康、妉{冘}[媅]67、般[昪]89，樂也1011。12悅、懌、愉、釋、賓、恊13，服[𠬝]也。14', '[釋文-1]：以之反。補某氏曰：「心之樂也」\n[釋文-2]：音亦。補某氏曰：「意解之樂也」\n[釋文-3]：苦旦反\n[釋文-4]：羊朱反\n[釋文-5]：苦在反\n[音釋-6]：丁含。\n[釋文-7]：丁含反\n[音釋-8]：盤。\n[釋文-9]：蒲安反\n[音釋-10]：洛。\n[釋文-11]：音洛\n[郭注-12]：皆見詩。\n[釋文-13]：胡頰反\n[郭注-14]：皆謂喜而服從。', '釋詁弟一', '爾雅', '11;12;20', DEFAULT, DEFAULT, '6746;6980;1545;5538;1274;3075;6827;6136;3094;4459;3326;5448;3764;1545;6980;6827;724;3968;9258;5449', '8237;4238;1921', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4398, '遹;遵;率;循;由;從;自;遹;遵;率;循', '述;述', '遹12、遵、率[述]3、循4、由、從，自也。5遹、遵、率[述]，循也。67', '[音釋-1]：聿。\n[釋文-2]：孫云：「古述字。」讀者亦尹橘反\n[釋文-3]：所律反\n[釋文-4]：音旬\n[郭注-5]：自猶從也。\n[郭注-6]：三者又爲循行。\n[釋文-7]：行，下孟反。', '釋詁弟一', '爾雅', '3;10', DEFAULT, DEFAULT, '1157;1108;8785;1233;8516;5227;2224;1157;1108;8785;1233', '1107;1107', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4399, '靖;惟;漠;圖;詢;度;咨;諏;究;如;慮;謨;猷;肈;基;訪;謀', '靜;謨;茹;𠧙;諅', '靖[靜]1、惟、漠[謨]2、圖、詢、度34、咨、諏56、究7、如[茹]、慮、謨8、猷9、肈[𠧙]10、基[諅]11、訪，謀也12。1314', '[釋文-1]：音靜\n[釋文-2]：孫音莫，舍人云：「心之謨也」\n[音釋-3]：鐸。\n[釋文-4]：徒洛反，注同\n[音釋-5]：子須。\n[釋文-6]：子須反\n[釋文-7]：九又反\n[釋文-8]：亡胡反\n[釋文-9]：音猶\n[釋文-10]：音趙\n[釋文-11]：本或作𧫠，音同\n[釋文-12]：莫浮反\n[郭注-13]：國語曰：「詢于八虞，咨于二虢，度于閎夭，謀于南宮，諏于蔡、原，訪于辛、尹。」通謂謀議耳。如、肈，所未詳。餘皆見詩。\n[釋文-14]：虢，古伯反。閎夭，音宏，下於兆反。原，音元。', '釋詁弟一', '爾雅', '1;3;10;14;15', DEFAULT, DEFAULT, '6686;6763;7132;3915;1725;1929;841;1510;4673;8245;6704;1508;6375;8376;9046;1509;1507', '3190;1508;614;2080;1641', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4400, '典;彝;法;則;㓝;範;矩;庸;恆;律;戛;職;秩;常;柯;憲;㓝;範;辟;律;矩;則;法', '陳;笵;楷;豑;儀;笵', '典[陳]1、彝23、法、則、㓝、範[笵]4、矩、庸、恆、律、戛[楷]5、職6、秩[豑]7，常也。8柯[儀]9、憲、㓝、範[笵]、辟1011、律、矩、則，法也。1213', '[釋文-1]：補孫炎、李巡曰：「禮之常也」\n[音釋-2]：夷。\n[釋文-3]：以而反\n[釋文-4]：音犯，字或作范，同\n[釋文-5]：居黠反，郭苦八反\n[釋文-6]：之力反\n[釋文-7]：長栗反\n[郭注-8]：庸、戛、職、秩，義見詩、書。餘皆謂常法耳。\n[釋文-9]：古河反\n[音釋-10]：婢亦。\n[釋文-11]：婢亦反，下同\n[郭注-12]：詩曰：「伐柯伐柯，其則不遠。」論語曰：「不踰矩。」\n[釋文-13]：踰，以朱反。矩，俱宇反。', '釋詁弟一', '爾雅', '1;6;11;13;15;18', DEFAULT, DEFAULT, '3023;8765;6262;2756;3194;9574;3033;2084;9017;1257;8378;7807;4468;4872;3746;6723;3194;9574;5798;1257;3033;2756;6262', '9688;2897;3429;3103;5084;2897', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4401, '辜;辟;戾;辠', '', '辜12、辟、戾，辠也34。5', '[音釋-1]：孤。\n[釋文-2]：古胡反\n[音釋-3]：罪。\n[釋文-4]：古罪字，秦始皇以其字似皇字，改從罒，非\n[郭注-5]：皆㓝罪。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '9758;5798;6356;9757', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4402, '黃髮;齯齒;鮐背;耇;老;壽', '', '黃髮1、齯齒23、鮐背45、耇67、老，壽也8。910', '[釋文-1]：補舍人曰：「老人髮白複黃也」\n[音釋-2]：倪。\n[釋文-3]：兒，本今皆作齯，五兮反，一音如字\n[音釋-4]：台。\n[釋文-5]：天才反，一音夷。博內反。補舍人曰：「老人氣衰，皮膚消瘠，背若鮐魚也」\n[音釋-6]：狗。\n[釋文-7]：音苟。補舍人曰：「耇，覯也，血氣精華覯竭，言色赤黑如狗矣」\n[釋文-8]：𦓆，音壽，本又作壽\n[郭注-9]：黃髮，髮落更生黃者。齯齒，齒墮更生細者。鮐背，背皮如鮐魚。耇猶耆也。皆壽考之通稱。\n[釋文-10]：更，古孟反。隋，徒火反，又作墮，同。細，先計反。耆，巨伊反。稱，尺證反。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '9203/5730;1297/1278;7652/2617;5381;5377;5384', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4403, '允;孚;亶;展;諶;誠;亮;詢;信;展;諶;允;慎;亶;誠', '坦;諒;恂;坦', '允1、孚2、亶[坦]34、展、諶56、誠、亮[諒]、詢[恂]，信也。78展、諶、允、慎、亶[坦]，誠也。9', '[釋文-1]：音尹\n[釋文-2]：音敷\n[音釋-3]：丁但。\n[釋文-4]：丁但反\n[音釋-5]：市林。\n[釋文-6]：甚針反\n[郭注-7]：方言曰：「荆 吳 淮 汭之閒曰展，燕 岱 東齊曰諶，宋 衞曰詢。」亦皆見詩。\n[釋文-8]：汭，仁銳反。岱，音待。\n[郭注-9]：轉相訓也。詩曰：「慎爾優遊。」', '釋詁弟一', '爾雅', '3;7;8;14', DEFAULT, DEFAULT, '5460;1881;3344;5407;1522;1525;5463;1725;1523;5407;1522;5460;6714;3344;1525', '9072;1480;6761;9072', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4404, '戲謔', '', '「謔浪𥬇敖12」，戲謔也3。45', '[音釋-1]：五報。\n[釋文-2]：許虐反。蘇誚反。五報反。補舍人曰：「謔，戲謔也。浪，意明也。𥬇，心樂也。敖，意舒也。戯，𥬇邪戯也。謔，𥬇之貌也」\n[釋文-3]：虛寄反\n[郭注-4]：謂調戲也，見詩。\n[釋文-5]：調，徒弔反。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '8385/1646', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4405, '粤;于;爰;曰;爰;粤;于;爰;粤;于;那;都;繇;於', '', '粤12、于、爰3，曰也。45爰、粤，于也。6爰、粤、于、那7、都、繇89，於也1011。1213', '[音釋-1]：曰。\n[釋文-2]：音越\n[釋文-3]：音袁\n[郭注-4]：書曰：「土爰稼穡。」詩曰：「對越在天。」「王于出征。」\n[釋文-5]：稼，古乍反。穡，音色。征，之成反。\n[郭注-6]：轉相訓。\n[釋文-7]：乃河反\n[音釋-8]：由。\n[釋文-9]：除又反，注同，孫音由，又音遥\n[音釋-10]：烏。\n[釋文-11]：音烏，注同\n[郭注-12]：左傳曰：「棄甲則那。」那猶今人云那那也。書曰：「臯陶曰：都。」繇，辭。於乎。皆語之韻絶。\n[釋文-13]：陶，音遥。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '3072;3070;2517;3045;2517;3072;3070;2517;3072;3070;4110;4007;8516;2488', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4406, '㪉;郃;盍;翕;仇;偶;妃;匹;會;合;仇;讎;敵;妃;知;儀;匹;妃;合;會;對;妃;媲', '雔;雔;雔', '㪉12、郃34、盍56、翕7、仇[雔]8、偶9、妃1011、匹、會，合也。12仇[雔]13、讎[雔]14、敵、妃、知、儀，匹也。1516妃、合、會，對也。17妃，媲也1819。20', '[音釋-1]：閤。\n[釋文-2]：古荅反\n[音釋-3]：合。\n[釋文-4]：音合，又音洽\n[音釋-5]：胡臘。\n[釋文-6]：胡獵反\n[釋文-7]：許急反\n[釋文-8]：音求\n[釋文-9]：五口反\n[音釋-10]：配。\n[釋文-11]：音配，又芳非反，下同\n[郭注-12]：皆謂對合也。\n[釋文-13]：補李巡曰：「怨之匹也」\n[釋文-14]：市周反。補李巡曰：「怨耦曰讎」\n[郭注-15]：詩云：「君子好仇。」「樂子之無知。」「實維我儀。」國語亦云：「丹朱憑身以儀之。」讎猶儔也。廣雅云：「讎，輩也。」\n[釋文-16]：樂，音洛。馮，皮氷反。\n[郭注-17]：皆相當對。\n[音釋-18]：普計。\n[釋文-19]：普計反，郭音譬，字林匹地反\n[郭注-20]：相偶媲也。', '釋詁弟一', '爾雅', '5;11;12', DEFAULT, DEFAULT, '2027;4031;3181;2258;5173;5184;8133;8424;3276;3271;5173;1487;2029;8133;3317;5084;8424;8133;3271;3276;1751;8133;8134', '2363;2363;2363', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4407, '紹;胤;嗣;續;纂;緌;績;武;係;繼', '纘', '紹1、胤2、嗣、續、纂[纘]34、緌5、績6、武、係7，繼也8。9', '[釋文-1]：市小反。補舍人曰：「繼道也」\n[釋文-2]：羊忍反，又以刃反。補舍人曰：「繼世也」\n[音釋-3]：子管。\n[釋文-4]：子管反\n[釋文-5]：汝誰反\n[釋文-6]：子狄反\n[釋文-7]：戸帝反\n[釋文-8]：音計\n[郭注-9]：詩曰：「下武維同。」緌，見釋水。餘皆常語。', '釋詁弟一', '爾雅', '5', DEFAULT, DEFAULT, '8552;2645;1433;8550;8652;8644;8735;8397;5166;8549', '8551', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4408, '忥;侐;侐;蟄;慎;貉;謐;顗;頠;密;寧;靜', '訖;嗼;宓;寍', '忥[訖]12、謚{侐}34、溢{侐}5、蟄6、慎、貉[嗼]78、謐910、顗1112、頠1314、密[宓]、寧[寍]，靜也。15', '[音釋-1]：戲。\n[釋文-2]：本或作氣，同，許氣反\n[音釋-3]：侍。\n[釋文-4]：時至反\n[釋文-5]：以日反\n[釋文-6]：直立反\n[音釋-7]：陌。\n[釋文-8]：莫白反，又武博反\n[音釋-9]：密。\n[釋文-10]：彌畢反\n[音釋-11]：擬。\n[釋文-12]：魚豈反，又五愷反\n[音釋-13]：魚毀。\n[釋文-14]：魚毀反，沈五罪反，孫、郭五果反\n[郭注-15]：忥、顗、頠，未聞其義。餘皆見詩傳。', '釋詁弟一', '爾雅', '1;6;10;11', DEFAULT, DEFAULT, '6870;5057;5057;8912;6714;6131;1554;5670;5648;5876;3057;3190', '1577;941;4579;4575', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4409, '隕;磒;湮;下;降;墜;摽;蘦;落', '𠬪;零', '隕12、磒34、湮5、下、降6、墜7、摽[𠬪]89、蘦[零]1011，落也。1213', '[音釋-1]：于閔。\n[釋文-2]：于閔反\n[音釋-3]：于敏。\n[釋文-4]：于敏反，石落也\n[釋文-5]：郭音因，又音烟，又音翳\n[釋文-6]：古巷反\n[釋文-7]：本又作隊，同，直類反\n[音釋-8]：婢眇。\n[釋文-9]：婢眇反，又普交、符表二反\n[音釋-10]：零。\n[釋文-11]：力丁反，字或作苓，說文云：「草曰苓，木曰落」\n[郭注-12]：磒猶隕也，方俗語有輕重耳。湮，沈落也。摽、蘦，見詩。\n[釋文-13]：沈，直今反。', '釋詁弟一', '爾雅', '7;8', DEFAULT, DEFAULT, '9650;6025;7297;9;9649;9162;7931;465;549', '2516;7540', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4410, '命;令;禧;畛;祈;請;謁;訊;誥;告', '視;誶;誥', '命、令12、禧3、畛[視]45、祈、請、謁、訊[誶]6、誥7，告[誥]也89。10', '[音釋-1]：力政。\n[釋文-2]：力政反，說文云：「發號也」\n[釋文-3]：許其反\n[音釋-4]：稹。\n[釋文-5]：之忍反\n[釋文-6]：誶，沈音粹，郭音碎，告也，本作訊，音信\n[釋文-7]：羔報反，又古酷反\n[音釋-8]：谷。\n[釋文-9]：古篤反\n[郭注-10]：禧，未聞。禮記曰：「畛於鬼神。」', '釋詁弟一', '爾雅', '4;8;10', DEFAULT, DEFAULT, '840;5779;13;9190;50;1482;1483;1518;1528;778', '5477;1695;1528', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4411, '永;悠;迥;違;遐;逷;闊;遠;永;悠;迥;遠;遐', '攸;攸', '永、悠[攸]1、迥23、違、遐、逷45、闊6，遠也。7永、悠[攸]、迥、遠，遐也。8', '[釋文-1]：音由。補舍人曰：「行之遠也」\n[音釋-2]：戸頂。\n[釋文-3]：戸頂反\n[音釋-4]：惕。\n[釋文-5]：說文云：「古逖字。」他歷反，郭湯革反\n[釋文-6]：苦活反\n[郭注-7]：書曰：「逷矣！西土之人。」\n[郭注-8]：遐亦遠也，轉相訓。', '釋詁弟一', '爾雅', '2;10', DEFAULT, DEFAULT, '7497;6929;1200;1159;1215;1199;7786;1198;7497;6929;1200;1198;1215', '2034;2034', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4412, '虧;壞;圮;垝;毀', '', '虧1、壞23、圮45、垝67，毀也。89', '[釋文-1]：字又作𧇾，袪危反，郭又許宜反\n[音釋-2]：怪。\n[釋文-3]：音怪，說文云：「敗也。籀文作𣀩。」字林云：「壞，自敗也，下怪反。」「𣀩，毀也，公壞反」\n[音釋-4]：房美。\n[釋文-5]：孫房美反，「岸毀也」\n[音釋-6]：鬼。\n[釋文-7]：古委反\n[郭注-8]：書曰：「方命圮族。」詩曰：「乗彼垝垣。」虧，通語耳。\n[釋文-9]：垣，音袁。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '3071;9120;9112;9111;9118', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4413, '矢;雉;引;延;順;薦;劉;繹;尸;旅;陳', '荐', '矢1、雉、引、延、順、薦[荐]、劉、繹2、尸、旅3，陳也。4', '[釋文-1]：𡱁，本作矢，同，失耳反\n[釋文-2]：音亦\n[釋文-3]：音呂\n[郭注-4]：禮記曰：「尸，陳也。」雉、順、劉，皆未詳。', '釋詁弟一', '爾雅', '6', DEFAULT, DEFAULT, '3309;2289;8493;1265;5654;6261;9446;8520;5402;4311;9688', '580', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4414, '尸;職;主;尸;寀;寀;寮;官', '宰;宰;勞', '尸、職，主也。1尸，寀[宰]也23。4寀[宰]、寮[勞]5，官也。6', '[郭注-1]：左傳曰：「殺老牛，莫之敢尸。」詩曰：「誰其尸之。」又曰：「職爲亂階。」\n[音釋-2]：七代。\n[釋文-3]：李、孫、郭並七代反，下句及注同，樊七在反\n[郭注-4]：謂寀地。\n[釋文-5]：字又作僚，同，力彫反\n[郭注-6]：官地爲寀，同官爲寮。', '釋詁弟一', '爾雅', '5;6;7', DEFAULT, DEFAULT, '5402;7807;3184;5402;4629;4629;4643;9618', '4595;4595;9236', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4415, '績;緒;采;業;服;宜;貫;公;事', '遦', '績、緒、采1、業、服2、宜、貫[遦]3、公，事也。4', '[釋文-1]：七在反\n[釋文-2]：本或作𦩟，同，符福反，又作𦨕字\n[釋文-3]：古玩反\n[郭注-4]：論語曰：「仍舊貫。」餘皆見詩、書。', '釋詁弟一', '爾雅', '7', DEFAULT, DEFAULT, '8735;8521;3794;1749;5449;4599;4341;716;1934', '1111', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4416, '永;羕;引;延;融;駿;長', '演;峻', '永、羕12、引[演]3、延、融、駿[峻]4，長也5。6', '[音釋-1]：㨾。\n[釋文-2]：羊讓反，字從永，說文云：「水長也」\n[釋文-3]：以忍反\n[釋文-4]：本或作俊，又作峻，同，子閏反\n[釋文-5]：直良反\n[郭注-6]：宋 衞 荆 吳之閒曰融。羕，所未詳。', '釋詁弟一', '爾雅', '3;6', DEFAULT, DEFAULT, '7497;7498;8493;1265;1863;6170;6069', '7148;5878', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4417, '喬;嵩;崇;高;崇;充', '', '喬1、嵩2、崇，高也。3崇，充也4。5', '[釋文-1]：郭音橋，或音驕\n[釋文-2]：宿忠反\n[郭注-3]：皆高大貌。左傳曰：「師叔，楚之崇也。」\n[釋文-4]：補舍人曰：「威大充盛」\n[郭注-5]：亦爲充盛。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '6624;5911;5882;3320;5882;5462', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4418, '犯;奢;果;毅;克;捷;功;肩;堪;勝;勝;肩;戡;劉;殺;克;劉;𤣐;斬;刺;殺', '', '犯、奢、果1、毅2、剋{克}、捷3、功、肩4、堪5，勝也6。78勝、肩、戡9、劉、殺，克也。10劉、獮{𤣐}1112、斬、刺1314，殺也。1516', '[釋文-1]：惈，音果，本今作果。補孫炎曰：「決之勝也」\n[音釋-2]：義\n[釋文-3]：才接反\n[釋文-4]：音堅。補舍人曰：「強之勝也」\n[釋文-5]：字又作戡，同，苦含反。補孫炎曰：「任之勝也」\n[釋文-6]：尸證反，注及下同\n[郭注-7]：陵犯、誇奢、果毅，皆得勝也。左傳曰：「殺敵爲果。」肩卽剋耳。書曰：「西伯堪黎。」\n[釋文-8]：夸，口花反，或作誇，非。𨛫，郎兮反，國名，本作黎。殺，所黠反，舊所例反，說文云：「戮也。」\n[音釋-9]：堪\n[郭注-10]：轉相訓耳。公羊傳曰：「克之者何？殺之也。」\n[音釋-11]：尠。\n[釋文-12]：息淺反，說文或作𤣐\n[音釋-13]：次。\n[釋文-14]：七賜反，說文云：「君殺大夫曰刺。刺，直傷也。」周禮「司刺」掌三刺之法，以聽獄訟，或七亦反\n[郭注-15]：書曰：「咸劉厥敵。」秋獵爲獮，應殺氣也。公羊傳曰：「刺之者何？殺之也。」\n[釋文-16]：應，應對之應。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '6343;6653;3568;1975;4414;8089;9214;2624;9054;9229;9229;2624;8392;9446;1979;4414;9446;6359;9610;2806;1979', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4419, '釁釁;愐沒;孟;敦;勖;釗;茂;劭;愐;勉', '忞忞;邵;懋', '亹亹{釁釁}[忞忞]12、蠠沒{愐沒}34、孟、敦5、勖6、釗[邵]78、茂[懋]9、劭1011、勔{愐}1213，勉也。1415', '[音釋-1]：尾。\n[釋文-2]：字或作斖，同，亡匪反\n[音釋-3]：蜜。\n[釋文-4]：彌畢反，又亡忍反，本或作𧖅，說文曰：「𧖅，古蜜字。」\n[釋文-5]：𣀦，丁門反，本今作敦\n[釋文-6]：許玉反\n[音釋-7]：招。\n[釋文-8]：古堯反，又之遥反\n[釋文-9]：字又作懋，亦作𢘅，同，亡候反\n[音釋-10]：邵。\n[釋文-11]：上照反，或上遥反\n[音釋-12]：泯。\n[釋文-13]：字本作僶，又作黽，或音泯，又彌兖反\n[郭注-14]：詩曰：「亹亹文王。」蠠沒猶黽勉。書曰：「茂哉茂哉。」方言云：「周 鄭之閒相勸勉爲勔釗。」孟，未聞。\n[釋文-15]：僶，字又作黽，亡忍反，又亡衍反。哉，或作才。', '釋詁弟一', '爾雅', '1;6;7', DEFAULT, DEFAULT, '1790/1790;6790/7299;9774;2039;9224;2795;522;9223;6790;9222', '6788/6788;4051;6792', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4420, '騖;務;昏;暋;強', '敃/忞;敃/忞;彊', '騖12、務、昏[敃/忞]3、暋[敃/忞]45，強[彊]也67。8', '[音釋-1]：務。\n[釋文-2]：音務\n[釋文-3]：補孫炎曰：「夙夜之強也」\n[音釋-4]：閔。\n[釋文-5]：音閔，或作旻\n[音釋-6]：其丈。\n[釋文-7]：其丈反，注同\n[郭注-8]：馳騖、事務，皆自勉強。書曰：「不昏作勞。」「暋不畏死。」', '釋詁弟一', '爾雅', '3;4;5', DEFAULT, DEFAULT, '6211;9219;4223;2058;8826', '2000/0/6788;2000/0/6788;8491', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4421, '卬;吾;台;予;朕;身;甫;余;言;我;朕;余;躬;身;台;朕;賚;畀;卜;陽;予', '姎;嬯;夫;鼻;僕;姎', '卬[姎]12、吾、台[嬯]34、予5、朕、身、甫[夫]、余6、言，我也。78朕、余9、躬10，身也。11台、朕、賚、畀[鼻]1213、卜[僕]、陽[姎]1415，予也1617。1819', '[音釋-1]：五剛。\n[釋文-2]：五剛反\n[音釋-3]：怡，下同。\n[釋文-4]：孫羊而反，下同\n[釋文-5]：羊如反\n[釋文-6]：羊如反\n[郭注-7]：卬猶姎也，語之轉耳。書曰：「非台小子。」古者貴賤皆自稱朕。禮記云：「授政任功，曰『予一人』，畛於鬼神，曰『有某甫』。」言，見詩。\n[釋文-8]：姎，烏郎、烏黨、烏浪三反，說文云：「女人稱我曰姎。」任，而鴆反。畛，之忍反。某，音母。\n[釋文-9]：補舍人曰：「卑謙之身也」\n[釋文-10]：音弓\n[郭注-11]：今人亦自呼爲身。\n[音釋-12]：必二。\n[釋文-13]：必二反\n[音釋-14]：賜。\n[釋文-15]：音賜，又如字，本或作賜\n[音釋-16]：與。\n[釋文-17]：羊汝反\n[郭注-18]：賚、卜、畀，皆賜與也。與猶予也，因通其名耳。魯詩云：「陽如之何。」今巴 濮之人自呼「阿陽」。\n[釋文-19]：巴，伯家反。濮，音卜。', '釋詁弟一', '爾雅', '1;3;7;18;19;20', DEFAULT, DEFAULT, '5223;837;868;2510;5446;5250;2083;718;1474;8403;5446;718;4633;5250;868;5446;3959;3024;2074;9624;2510', '8309;8318;6676;2233;1753;8309', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4422, '肅;延;誘;薦;炎;晉;寅;藎;進;羞;餞;迪;烝;進', '羞;荐;引;進', '肅[羞]、延、誘1、薦[荐]、餤{炎}23、晉4、寅[引]、藎[進]56，進也。7羞、餞8、迪9、烝10，進也。11', '[釋文-1]：余九反\n[音釋-2]：談。\n[釋文-3]：沈大甘反，徐仙民詩音閻，餘占反，郭羽鹽反\n[釋文-4]：本又作㬜\n[音釋-5]：燼。\n[釋文-6]：本又作燼，同，徐刃反\n[郭注-7]：禮記曰：「主人肅客。」詩曰：「亂是用餤。」「王之藎臣。」易曰：「晉，進也。」寅，未詳。\n[釋文-8]：徂淺反\n[釋文-9]：大的反\n[釋文-10]：之仍反\n[郭注-11]：皆見詩、禮。', '釋詁弟一', '爾雅', '1;4;7;8', DEFAULT, DEFAULT, '1939;1265;5847;6261;6534;4205;9793;304;1113;9792;3249;1132;6431;1113', '9792;580;8493;1113', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4423, '詔;亮;左;右;相;導;詔;相;導;左;右;助;𠣊;亮;介;尚;右;左;右;亮', '召;𢪇;召;𢪇;上;𢪇', '詔[召]、亮[𢪇]、左、右1、相23，導也4。5詔[召]、相、導、左、右、助，勴{𠣊}也67。8亮[𢪇]、介9、尚[上]，右也。10左、右，亮[𢪇]也。1112', '[釋文-1]：音佐佑，下同\n[音釋-2]：息亮。\n[釋文-3]：息亮反，下文同\n[釋文-4]：道，徒報反，本或作導，注及下同\n[郭注-5]：皆謂敎導之。\n[音釋-6]：慮。\n[釋文-7]：力庶反\n[郭注-8]：勴謂贊勉。\n[釋文-9]：音界\n[郭注-10]：紹介、勸尚，皆相佑助。\n[郭注-11]：反覆相訓，以盡其義。\n[釋文-12]：覆，芳服反。', '釋詁弟一', '爾雅', '1;2;7;14;16;20', DEFAULT, DEFAULT, '1529;5463;3028;874;2162;1987;1529;2162;1987;3028;874;9215;9216;5463;714;711;874;3028;874;5463', '842;7857;842;7857;6;7857', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4424, '緝熙;烈;顯;昭;晧;熲;光', '㬎熙', '緝熙[㬎熙]1、烈、顯、昭、晧2、熲34，光也。5', '[釋文-1]：七入反。許其反\n[釋文-2]：胡老反\n[音釋-3]：古迥。\n[釋文-4]：古迥反\n[郭注-5]：詩曰：「學有緝熙于光明。」又曰：「休有烈光。」', '釋詁弟一', '爾雅', '1', DEFAULT, DEFAULT, '8733/6527;6427;5692;4199;4214;6439;6511', '4247/6527', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4425, '劼;鞏;堅;篤;掔;虔;膠;固', '硈;䈞;艮', '劼[硈]12、鞏3、堅、篤[䈞]4、掔56、虔[艮]7、膠8，固也。9', '[音釋-1]：苦黠。\n[釋文-2]：苦黠反，郭苦八反，或作硈字，古黠反\n[釋文-3]：九勇反\n[釋文-4]：丁毒反\n[音釋-5]：牽。\n[釋文-6]：音牽，又卻閑反，郭音義本與「慳惜物」同\n[釋文-7]：音乾\n[釋文-8]：音交\n[郭注-9]：劼、虔，皆見詩、書。易曰：「鞏用黃牛之革。」「固志也。」掔然亦牢固之意。', '釋詁弟一', '爾雅', '1;4;6', DEFAULT, DEFAULT, '9218;1801;1951;6198;7955;3112;2729;3929', '6031;3335;5225', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4426, '疇;孰;誰', '𠷎;𠷎', '疇[𠷎]12、孰[𠷎]，誰也。34', '[音釋-1]：直留。\n[釋文-2]：本又作𠷎，直留反\n[郭注-3]：易曰：「疇離祉。」\n[釋文-4]：祉，音恥。', '釋詁弟一', '爾雅', '1;2', DEFAULT, DEFAULT, '9175;1886;1705', '879;879', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4427, '暀暀;皇皇;藐藐;穆穆;休;嘉;珍;褘;懿;鑠;美', '𢤧𢤧;㣎㣎;好;偉/覣', '暀暀12、皇皇、藐藐[𢤧𢤧]34、穆穆[㣎㣎]、休[好]、嘉、珍、褘[偉/覣]56、懿7、鑠8，美也。910', '[音釋-1]：旺。\n[釋文-2]：于況反，又于𣓈反\n[音釋-3]：邈。\n[釋文-4]：亡角反\n[音釋-5]：衣。\n[釋文-6]：於宜反\n[釋文-7]：音意\n[釋文-8]：舒灼反\n[郭注-9]：自「穆穆」已上，皆美盛之貌。其餘常語。\n[釋文-10]：上，時掌反。盛，或作晠，同。', '釋詁弟一', '爾雅', '3;4;5;8', DEFAULT, DEFAULT, '4240/4240;80/80;381/381;4430/4430;3818;3082;148;5272;6645;9281;2356', '6717/6717;5720/5720;8191;4982/0/5479', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4428, '諧;輯;協;關關;雍雍;勰;燮;和', '饔饔', '諧1、輯23、協，和也。4關關、噰噰{雍雍}[饔饔]56，音聲和也。7勰89、燮10，和也。11', '[釋文-1]：下階反\n[音釋-2]：集。\n[釋文-3]：音集，又七入反\n[郭注-4]：書曰：「八音克諧。」左傳曰：「百姓輯睦。」\n[音釋-5]：於恭。\n[釋文-6]：於恭反\n[郭注-7]：皆鳥鳴相和。\n[音釋-8]：恊。\n[釋文-9]：本又作恊，同，戸牒反\n[釋文-10]：蘇頰反\n[郭注-11]：書曰：「燮友柔克。」', '釋詁弟一', '爾雅', '5', DEFAULT, DEFAULT, '1547;9527;9260;7773/7773;2300/2300;9259;1910;846', '3210/3210', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4429, '從;申;神;加;弼;崇;重', '陳;陳', '從、申[陳]、神[陳]、加、弼1、崇，重也23。45', '[釋文-1]：白筆反\n[音釋-2]：直龍。\n[釋文-3]：直龍反，注同\n[郭注-4]：隨從、弼輔、增崇，皆所以爲重疊。神，所未詳。\n[釋文-5]：疊，音牒。', '釋詁弟一', '爾雅', '2;3', DEFAULT, DEFAULT, '5227;9802;25;9243;8508;5882;5244', '9688;9688', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4430, '觳;悉;卒;泯;忽;滅;罄;空;畢;罊;殲;拔;殄;盡', '㱿;𣨛;欻;㪤;撥', '觳[㱿]12、悉、卒[𣨛]3、泯4、忽[欻]、滅、罄5、空、畢[㪤]、罊67、殱{殲}89、拔[撥]10、殄11，盡也。1213', '[音釋-1]：學。\n[釋文-2]：胡角反，又胡谷反\n[釋文-3]：子恤反，字或作𣨛\n[釋文-4]：亡忍反。補李巡曰：「沒之盡也」\n[釋文-5]：苦定反。補孫炎曰：「竭之盡也」\n[音釋-6]：苦計。\n[釋文-7]：苦計反，說文口地反，云：「器中盡也。」本或作𢢞字，音同。廣雅云：「𢢞，劇也」\n[音釋-8]：尖。\n[釋文-9]：子廉反。補舍人曰：「殱衆之盡也」\n[釋文-10]：步八反\n[釋文-11]：大典反\n[郭注-12]：觳，今直語耳。忽然，盡貌。今江東呼厭極爲罊。餘皆見詩。\n[釋文-13]：厭，一豔反。', '釋詁弟一', '爾雅', '1;3;5;9;12', DEFAULT, DEFAULT, '2864;723;5361;7454;6845;7443;3305;4648;2492;3306;2553;7997;2552;3157', '1963;2535;5551;2047;7980', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4431, '苞;蕪;茂;豐', '葆/飽;𣞤', '苞[葆/飽]1、蕪[𣞤]2、茂，豐也3。4', '[釋文-1]：百交反\n[釋文-2]：音無，或亡甫反，蕃滋生長也，古本作隷\n[釋文-3]：敷馮反\n[郭注-4]：苞叢、繁蕪，皆豐盛。', '釋詁弟一', '爾雅', '1;2', DEFAULT, DEFAULT, '388;544;522;3104', '674/0/3244;3849', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4432, '揫;斂;屈;收;戢;蒐;襃;鳩;樓;聚', '詘;𢯱;勼;摟', '揫12、斂3、屈[詘]4、收、戢5、蒐[𢯱]67、裒{襃}8、鳩[勼]9、樓[摟]10，聚也。1112', '[音釋-1]：子由。\n[釋文-2]：孫子由反，郭音遒。案：遒，音子由、徂秋二反\n[釋文-3]：力儉反\n[釋文-4]：丘勿反\n[釋文-5]：側立反\n[音釋-6]：搜。\n[釋文-7]：所求反\n[釋文-8]：古字作襃，蒲侯反，下同，字本或作捊\n[釋文-9]：居牛反，說文作勼，音九尤反\n[釋文-10]：摟，力侯反，從手。本或作樓，非\n[郭注-11]：禮記曰：「秋之言揫。」「揫，斂也。」「春獵爲蒐。」「蒐者，以其聚人衆也。」詩曰：「屈此羣醜。」「原隰裒矣。」左傳曰：「以鳩其民。」樓，猶今言拘樓，聚也。\n[釋文-12]：隰，音習。拘，古侯反。', '釋詁弟一', '爾雅', '3;6;8;9', DEFAULT, DEFAULT, '3398;2024;5430;2048;8398;383;5303;2376;3653;5238', '1700;8092;5807;7945', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4433, '肅;齊;遄;速;亟;屢;數;迅;疾;寁;駿;肅;亟;遄;速', '齌;䞭', '肅、齊[齌]、遄12、速、亟34、屢5、數67、迅8，疾也。910寁1112、駿[䞭]、肅、亟1314、遄，速也。15', '[音釋-1]：船。\n[釋文-2]：市專反\n[音釋-3]：欺冀。\n[釋文-4]：欺冀反，又居力反\n[釋文-5]：力具反\n[音釋-6]：朔。\n[釋文-7]：色角反\n[釋文-8]：信、峻二音\n[郭注-9]：詩曰：「仲山甫徂齊。」\n[釋文-10]：徂，才孤反。\n[音釋-11]：昝。\n[釋文-12]：本或作疌，同，子感反。補舍人曰：「意之速也」\n[音釋-13]：居力。\n[釋文-14]：字又作茍，同，居力反。經典亦作棘，同\n[郭注-15]：詩曰：「不寁故也。」駿猶迅。速亦疾也。', '釋詁弟一', '爾雅', '2;11', DEFAULT, DEFAULT, '1939;4395;1120;1121;9016;5425;2011;1122;4696;4607;6170;1939;9016;1120;1121', '6459;1048', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4434, '壑;阬;滕;徵;隍;漮;虛', '盅', '壑1、阬23、阬、滕[盅]4、徵、隍5、漮67，虛也8。910', '[釋文-1]：本或作㕡，同，許各反，郭胡郭反\n[音釋-2]：坑。\n[釋文-3]：苦衡反\n[釋文-4]：徒登反\n[釋文-5]：音皇，城池也\n[音釋-6]：康。\n[釋文-7]：字又作㱂，同，苦郎反。說文云：「水之空也。」方言作䆲，亦空也。郭云：「本或作荒」，荒亦丘墟之空無\n[釋文-8]：許居反\n[郭注-9]：壑，谿壑也。阬阬，謂阬壍也。隍，城池無水者。方言云：「漮之言空也。」皆謂丘墟耳。滕、徵，未詳。\n[釋文-10]：谿，音溪。壍，七豔反。墟，去魚反。', '釋詁弟一', '爾雅', '3', DEFAULT, DEFAULT, '2526;9656;7172;5241;9703;7346;5234', '3158', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4435, '黎;庶;烝;多;醜;師;旅;衆;洋;觀;襃;衆;那;多', '雔;讙;葆;夥', '𥠖{黎}、庶、烝1、多、醜[雔]、師、旅，衆也。2洋3、觀[讙]4、裒{襃}[葆]、衆、那[夥]5，多也。6', '[釋文-1]：之仍反\n[郭注-2]：皆見詩。\n[釋文-3]：音羊，洋溢，多貌。讀羊者、或爲詳，非也\n[釋文-4]：顧、謝音官，施古喚反，注同\n[釋文-5]：奴多反，本或作𡖔\n[郭注-6]：詩曰：「薄言觀者。」又曰：「受福不那。」洋溢，亦多貌。', '釋詁弟一', '爾雅', '5;10;11;13', DEFAULT, DEFAULT, '4515;5958;6431;4336;5836;3864;4311;5237;7083;5486;5303;5237;4110;4336', '2363;1657;674;4337', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4436, '流;差;柬;擇', '', '流、差12、柬34，擇也。5', '[音釋-1]：叉。\n[釋文-2]：楚佳反\n[音釋-3]：𥳑。\n[釋文-4]：音𥳑\n[郭注-5]：皆選擇，見詩。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '7476;3029;3900;7899', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4437, '戰;栗;震;驚;戁;竦;恐;慴;懼', '', '戰、慄{栗}12、震、驚、戁34、竦5、恐67、慴89，懼也。10', '[音釋-1]：栗。\n[釋文-2]：六日反\n[音釋-3]：女板。\n[釋文-4]：女版反\n[釋文-5]：息勇反\n[音釋-6]：丘勇。\n[釋文-7]：丘勇反\n[音釋-8]：之涉。\n[釋文-9]：之涉反\n[郭注-10]：詩曰：「不戁不竦。」慴卽懾也。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '8384;4393;7533;6218;6725;6684;6944;6945;6774', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4438, '痡;瘏;虺穨;玄黃;劬勞;咎;顇;瘽;瘉;鰥;戮;鼠;臠;𢟤/悝;痒;疧;疵;閔;逐;疚;晦;瘥;痱;亶;瘵;瘼;齊;病', '齂嘳;悁/悹;𤻲;瘏;攣;愍;疛;癉;膌', '痡12、瘏34、虺頽{虺穨}[齂嘳]56、玄黃7、劬勞8、咎9、顇1011、瘽1213、瘉1415、鰥[悁/悹]16、戮[𤻲]、癙{鼠}[瘏]1718、癵{臠}[攣]1920、㾖{𢟤/悝}2122、痒2324、疧2526、疵27、閔[愍]、逐[疛]、疚28、痗{晦}2930、瘥3132、痱3334、𤺺{亶}[癉]3536、瘵3738、瘼39、癠{齊}[膌]4041，病也。4243', '[音釋-1]：普胡。\n[釋文-2]：普胡、芳膚二反，詩作鋪。補孫炎曰：「人疲不能行之病」\n[音釋-3]：徒。\n[釋文-4]：音徒，詩作屠。補孫炎曰：「馬疲不能進之病」\n[音釋-5]：灰。\n[釋文-6]：虎回反，又呼懷反。徒回反。補孫炎曰：「馬疲不能升高之病」\n[釋文-7]：補孫炎曰：「馬更黃色之病」\n[釋文-8]：求于反\n[釋文-9]：音臼\n[音釋-10]：悴。\n[釋文-11]：字或作悴，在醉反\n[音釋-12]：勤。\n[釋文-13]：音勤，字亦作懃\n[音釋-14]：俞。\n[釋文-15]：羊朱反，又羊主反\n[釋文-16]：古頑反，注瘝同\n[音釋-17]：鼠。\n[釋文-18]：傷汝反，舍人云：「癙、癵、㾖、痒，皆心憂憊之病也。」孫炎云：「癙者，畏之病也。」字林音恕，詩作鼠\n[音釋-19]：力專。\n[釋文-20]：郭作拘攣，同，力專反\n[音釋-21]：里。\n[釋文-22]：音里。補孫炎曰：「疫病也」\n[音釋-23]：羊。\n[釋文-24]：音羊\n[音釋-25]：祁。\n[釋文-26]：祈支反，或丁禮反，本作㽻，字書云：「㽻，病也。」聲類猶以爲疧字，又音支。孫炎云：「滯之病也」\n[釋文-27]：音慈\n[釋文-28]：音救\n[音釋-29]：妹。\n[釋文-30]：音昧，一音晦\n[音釋-31]：伹何。\n[釋文-32]：徂何反，又子衰反\n[音釋-33]：肥。\n[釋文-34]：符非反，又符沸反，說文蒲愷反，「風病也」\n[音釋-35]：亶。\n[釋文-36]：丁但反，又徒丹反，孫炎云：「疫病」\n[音釋-37]：債。\n[釋文-38]：側界反，字林側例反\n[釋文-39]：音莫\n[音釋-40]：徂細。\n[釋文-41]：徂細反，或徂犂反\n[郭注-42]：虺頽、玄黃，皆人病之通名，而說者便謂之馬病，失其義也。詩曰：「生我劬勞。」書曰：「智藏瘝在。」相戮辱，亦可恥病也。今江東呼病曰瘵，東齊曰瘼。禮記曰：「親癠，色容不盛。」戮、逐，未詳。餘皆見詩。虺音㾯。\n[釋文-43]：㾯，呼回反，字林云：「病也。」今經注無此字。', '釋詁弟一', '爾雅', '3;10;11;12;13;18;19;24;27', DEFAULT, DEFAULT, '4698;4712;8802/5475;2507/9203;9252/9236;5175;5687;4702;4795;7595;8391;6393;2655;6932/0/6851;4719;4781;4710;7787;1178;4614;4227;4793;4736;3344;4703;4705;4395;4697', '2237/830;6875/0/6770;4790;4712;8000;6908;4730;4772;2656', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4439, '恙;寫;悝;盱;䍃;慘;恤;罹;憂', '獡;𢖳;䌛;𢝊', '恙1、寫[獡]2、悝34、盱[𢖳]56、繇{䍃}[䌛]78、慘9、恤10、罹11，憂[𢝊]也1213', '[釋文-1]：羊讓反。補孫炎曰：「病之憂也」\n[釋文-2]：悉冶反\n[音釋-3]：里。\n[釋文-4]：音里\n[音釋-5]：吁。\n[釋文-6]：本或作𢖳，同，香于反\n[音釋-7]：遥。\n[釋文-8]：音遥\n[釋文-9]：七感反\n[釋文-10]：補孫炎曰：「救之憂也」\n[釋文-11]：力知反\n[郭注-12]：。\n[郭注-13]：今人云無恙，謂無憂也。寫，有憂者思散寫也。詩曰：「悠悠我悝。」「云何盱矣。」繇役，亦爲憂愁也。', '釋詁弟一', '爾雅', '2;4;5;9', DEFAULT, DEFAULT, '6919;4600;6851;2134;3300;6902;6801;4851;3368', '6333;6933;8516;6937', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4440, '倫;勩;邛;敕;勤;愉;庸;癉;勞;勞;來;強;事;謂;翦;篲;勤', '掄;功;輸;勑;習', '倫[掄]、勩12、邛[功]3、敕4、勤、愉[輸]5、庸、癉67，勞也8。910勞11、來[勑]1213、強1415、事、謂、翦16、篲[習]17，勤也。1819', '[音釋-1]：與世。\n[釋文-2]：字或作𠡫，與世反，郭音謚，字亦作肄。補孫炎曰：「習事之勞也」\n[釋文-3]：巨凶反\n[釋文-4]：本又作飭，竝恥力反。案說文、字林來旁作力，是勞來之字，束旁作攵，是治，音丑力反\n[釋文-5]：羊主反，又羊朱反\n[音釋-6]：丁賀。\n[釋文-7]：丁賀反，本或作憚，音同\n[音釋-8]：力報\n[郭注-9]：詩曰：「莫知我勩。」「維王之邛。」「哀我癉人。」國語曰：「無功庸者。」倫理事務，以相約敕，亦爲勞。勞苦者多情愉。愉音窳。\n[釋文-10]：窳，羊主反，字林云：「污也，音烏。」說文云：「污窬也。」案：污窬猶污邪也。今字或作窳，同。\n[釋文-11]：力報反，注同。補舍人曰：「力極也」\n[音釋-12]：賚。\n[釋文-13]：本又作勑，力代反，本或作賚\n[音釋-14]：其丈。\n[釋文-15]：其丈反，注同\n[釋文-16]：子淺反\n[釋文-17]：本又作彗，同，息遂反，又徂歲反\n[郭注-18]：詩曰：「職勞不來。」自勉強者亦勤力者，由事事，故爲勤也。詩曰：「迨其謂之。」翦、篲，未詳。\n[釋文-19]：迨，音待。', '釋詁弟一', '爾雅', '1;3;6;11;16', DEFAULT, DEFAULT, '5038;9239;4126;2022;9242;6827;2084;4772;9236;9236;3348;8826;1934;1479;2248;1927;9242', '7898;9214;9578;9217;2240', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4441, '悠;傷;憂;思;懷;惟;慮;願;念;惄;思', '慯;𢝊', '悠、傷[慯]1、憂[𢝊]，思也23。4懷、惟、慮、願、念、惄56，思也。78', '[釋文-1]：字書作慯，尸羊反\n[音釋-2]：司嗣。\n[釋文-3]：司嗣反，注同\n[郭注-4]：皆感思也。\n[音釋-5]：溺。\n[釋文-6]：乃歷反。補舍人曰：「志而不得之思也」\n[郭注-7]：詩曰：「惄如調飢。」\n[釋文-8]：調，竹留反，毛詩傳云：「朝也。」', '釋詁弟一', '爾雅', '2;3', DEFAULT, DEFAULT, '6929;5158;3368;6703;6764;6763;6704;5635;6721;6805;6703', '6925;6937', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4442, '禄;祉;履;戩;祓;禧;禠;祜;福', '', '禄、祉1、履、戩2、祓34、禧5、禠67、祜8，福也。910', '[釋文-1]：音恥\n[釋文-2]：音翦，又章善反，孫音箭\n[音釋-3]：廢。\n[釋文-4]：音廢，又音拂，又方妹反。補孫炎曰：「除之福」\n[釋文-5]：許其反\n[音釋-6]：斯。\n[釋文-7]：音斯，郭常支、巨移二反\n[釋文-8]：音戸\n[郭注-9]：詩曰：「福履綏之。」「俾爾戩穀。」「祓禄康矣。」禠、禧，書傳不見，其義未詳。\n[釋文-10]：俾，必爾反。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '15;19;5432;8395;49;13;16;11;20', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4443, '禋;祀;祠;蒸;甞;龠;祭', '烝;礿', '禋1、祀2、祠3、蒸[烝]4、甞5、禴{龠}[礿]67，祭也。8', '[釋文-1]：音因，注同。補孫炎曰：「絜敬之祭也」\n[釋文-2]：音似。補舍人曰：「地祭也」\n[釋文-3]：音詞，周春祭名。補舍人曰：「天祭也」\n[釋文-4]：之升反，冬祭名\n[釋文-5]：字又作常，秋祭名\n[音釋-6]：藥。\n[釋文-7]：字又作礿，同，餘若反，夏祭名\n[郭注-8]：書曰：「禋于六宗。」餘者皆以爲四時祭名也。', '釋詁弟一', '爾雅', '4;6', DEFAULT, DEFAULT, '29;31;41;623;3044;1427;30', '6431;42', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4444, '儼;愙;祗;翼;禋;恭;欽;寅;熯;敬', '趩;趛;夤;戁', '儼1、恪{愙}23、祗4、翼[趩]、諲{禋}56、恭、欽[趛]、寅[夤]、熯[戁]78，敬也。9', '[釋文-1]：魚儉反\n[音釋-2]：墟各。\n[釋文-3]：口各反\n[釋文-4]：旨夷反\n[音釋-5]：因。\n[釋文-6]：音因，又音真\n[音釋-7]：而善。\n[釋文-8]：而善反，郭音罕，說文、蒼頡篇皆同\n[郭注-9]：儼然，敬貌。書曰：「夙夜惟寅。」詩曰：「我孔熯矣。」諲，未詳。', '釋詁弟一', '爾雅', '4;7;8;9', DEFAULT, DEFAULT, '5004;6772;23;7695;29;6743;5526;9793;6434;5820', '1027;1020;4331;6725', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4445, '朝;旦;夙;晨;晙;早', '', '朝、旦、夙、晨、晙12，早也。3', '[音釋-1]：俊。\n[釋文-2]：子峻反，又音峻\n[郭注-3]：晙亦明也。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '4289;4285;4334;4318;4263;4192', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4446, '𩓣;竢;𤾕;戾;厎;止;徯;待', '底', '𩓣12、竢34、𤾕5、戾6、厎[底]78、止、徯910，待也。11', '[音釋-1]：須。\n[釋文-2]：音須\n[音釋-3]：士。\n[釋文-4]：音仕，字又作俟，亦作戺，音同\n[釋文-5]：他計反\n[釋文-6]：音麗\n[音釋-7]：止。\n[釋文-8]：之視反，字宜從厂，或作底，非也。底，音丁禮反\n[音釋-9]：胡禮。\n[釋文-10]：胡禮反\n[郭注-11]：書曰：「徯我后。」今河北人語亦然。𤾕、戾、厎者，皆止也，止亦相待。', '釋詁弟一', '爾雅', '5', DEFAULT, DEFAULT, '6691;6687;6699;6356;5984;1064;1244;1245', '5952', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4447, '噊;幾;烖;殆;危;𧰙;汽', '', '噊12、幾3、烖4、殆，危也。5𧰙67，汽也89。1011', '[音釋-1]：聿。\n[釋文-2]：郭音聿，施音述\n[釋文-3]：音祈，又音機\n[音釋-4]：哉\n[郭注-5]：幾猶殆也。噊、烖，未詳。\n[音釋-6]：析。\n[釋文-7]：音祈，又音沂，郭音剴。案剴，音公哀反，說文云：「摩也」\n[音釋-8]：蓋。\n[釋文-9]：古愛反，施音旣，樊、孫虛乞反\n[郭注-10]：謂相摩近。\n[釋文-11]：摩，莫河反。近，附近之近。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '910;2503;6489;2549;6009;3095;7341', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4448, '治;肆;古;故;肆;故;今', '巳;自;自', '治[巳]1、肆[自]2、古，故也。3肆[自]、故，今也。4', '[釋文-1]：如字，施直吏反\n[釋文-2]：音四\n[郭注-3]：治，未詳。肆、古，見詩、書。\n[郭注-4]：肆旣爲故，又爲今，今亦爲故，故亦爲今，此義相反而兼通者，事例在下，而皆見詩。', '釋詁弟一', '爾雅', '1;2;5', DEFAULT, DEFAULT, '7089;6070;1461;2005;6070;2005;3274', '9797;2224;2224', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4449, '惇;亶;祜;篤;掔;仍;肶;埤;竺;腹;厚', '䈞;䚮;𤼤/濞', '惇1、亶2、祜3、篤[䈞]、掔4、仍[䚮]5、肶[𤼤/濞]67、埤89、竺1011、腹12，厚也。1314', '[釋文-1]：字又作𢤈，丁門反\n[釋文-2]：多但反\n[釋文-3]：音戸\n[釋文-4]：卻賢反，又苦閒、苦忍二反\n[釋文-5]：本或作扔，同，汝烝反\n[音釋-6]：毗。\n[釋文-7]：音毗，本或作脾，同\n[音釋-8]：脾。\n[釋文-9]：避支反，又音椑\n[音釋-10]：篤。\n[釋文-11]：字又作篤，同，丁毒反\n[釋文-12]：分伏反\n[郭注-13]：頻仍、埤益、肶輔，皆重厚。掔然，厚貌。餘皆見詩、書。\n[釋文-14]：重，直龍反。', '釋詁弟一', '爾雅', '4;6;7', DEFAULT, DEFAULT, '6729;3344;11;6198;7955;5049;2680;9093;9019;2631;3339', '3335;1521;4731/0/7169', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4450, '載;謨;食;詐;僞', '戴;誣', '載[戴]、謨[誣]1、食2、詐3，僞也。45', '[釋文-1]：亡胡反，郭音慕\n[釋文-2]：補孫炎曰：「言之僞也」\n[釋文-3]：側駕反\n[郭注-4]：載者，言而不信。謨者，謀而不忠。書曰：「朕不食言。」\n[釋文-5]：謀，亡侯反。', '釋詁弟一', '爾雅', '1;2', DEFAULT, DEFAULT, '9571;1508;3206;1669;5136', '1779;1609', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4451, '話;猷;載;行;吪;言', '譌', '話1、猷2、載、行3、訛{吪}[譌]4，言也。56', '[釋文-1]：胡快反。補舍人曰：「政之善言也」\n[釋文-2]：音由，字亦從犭\n[釋文-3]：下庚反，郭下孟反，注同\n[釋文-4]：字又作吪，亦作譌，同，五戈反\n[郭注-5]：詩曰：「慎爾出話。」猷者，道，道亦言也。周禮曰：「作盟詛之載。」今江東通謂語爲行。世以妖言爲訛。\n[釋文-6]：盟，音明。詛，側慮反。祅，於喬反，本又作妖，同。', '釋詁弟一', '爾雅', '5', DEFAULT, DEFAULT, '1550;6375;9571;1266;930;1474', '1660', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4452, '遘;逢;遇;遘;逢;遇;遻;遘;逢;遇;遻;見', '', '遘12、逢，遇也。3遘、逢、遇，遻也45。67遘、逢、遇、遻，見也。8', '[音釋-1]：搆。\n[釋文-2]：古豆反\n[郭注-3]：謂相遭遇。\n[音釋-4]：悟。\n[釋文-5]：字又作迕，同，五故反\n[郭注-6]：轉復爲相觸遻。\n[釋文-7]：復，扶又反。\n[郭注-8]：行而相值卽見。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '1129;1130;1127;1129;1130;1127;1131;1129;1130;1127;1131;5476', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4453, '顯;昭;覲;釗;覿;見;監;瞻;臨;位;覜;相;視', '昭;䇐', '顯、昭、覲、釗[昭]1、覿，見也2。3監45、瞻、臨、涖{位}[䇐]67、覜89、相10，視也。11', '[釋文-1]：工堯反，又章堯反\n[釋文-2]：賢遍反，注同\n[郭注-3]：顯、昭，明見也。逸書曰：「釗我周王。」\n[音釋-4]：鑒。\n[釋文-5]：音鑒，又工杉反，字又作𥌈\n[音釋-6]：利。\n[釋文-7]：音利，又音類\n[音釋-8]：眺。\n[釋文-9]：他弔反\n[釋文-10]：施息亮反，又息良反\n[郭注-11]：皆謂察視也。', '釋詁弟一', '爾雅', '4;10', DEFAULT, DEFAULT, '5692;4199;5515;2795;5521;5476;5247;2157;5248;5031;5516;2162;5477', '4199;6680', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4454, '鞠;訩;溢;盈', '𡫬;洶', '鞠[𡫬]、訩[洶]1、溢，盈也。2', '[釋文-1]：音凶\n[郭注-2]：詩曰：「降此鞠訩。」', '釋詁弟一', '爾雅', '1;2', DEFAULT, DEFAULT, '1810;1677;7400;3156', '4618;7191', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4455, '孔;魄;哉;延;虛;無;之;言;閒', '㕣', '孔、魄、哉、延[㕣]、虛、無、之、言，閒也1。23', '[釋文-1]：古閑反，𨻶也，舊音閑\n[郭注-2]：孔穴、延魄、虛無，皆有閒隙。餘未詳。\n[釋文-3]：隙，去戟反。', '釋詁弟一', '爾雅', '4', DEFAULT, DEFAULT, '7704;5824;856;1265;5234;8416;3861;1474;7762', '959', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4456, '瘞;幽;隱;匿;蔽;竄;微', '', '瘞12、幽、隱、匿34、蔽5、竄6，微也。7', '[音釋-1]：於計。\n[釋文-2]：猗例反，郭音翳\n[音釋-3]：女力。\n[釋文-4]：女力反。補舍人曰：「藏之微也」\n[釋文-5]：必曳反。補舍人曰：「覆障使微也」\n[釋文-6]：麤亂反。補舍人曰：「行之微也」\n[郭注-7]：微謂逃藏也，左傳曰：「其徒微之」是也。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '9139;2502;9669;8420;550;4668;1236', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4457, '訖;徽;妥;懷;安;按;𤾕;戾;底;厎;尼;定;曷;遏;止', '褱', '訖、徽、妥12、懷[褱]、安、按3、𤾕、戾、底4、厎5、尼6、定、曷7、遏89，止也。1011', '[音釋-1]：他窠。\n[釋文-2]：孫他果反，郭他回反、又他罪反，劉昌宗音儀禮同，字林亦同\n[釋文-3]：一旦反\n[釋文-4]：丁禮反\n[釋文-5]：之視反\n[釋文-6]：施女乙反，謝羊而反\n[釋文-7]：何末反\n[音釋-8]：烏曷。\n[釋文-9]：烏割反\n[郭注-10]：妥者，坐也。懷者，至也。按，抑按也。𤾕，廢。皆止住也。戾、厎，義見詩傳。國語曰：「戾久將底。」孟子曰：「行或尼之。」今以逆相止爲遏。徽，未詳。\n[釋文-11]：抑，於力反。', '釋詁弟一', '爾雅', '4', DEFAULT, DEFAULT, '1577;8691;8349;6764;4578;7887;6699;6356;5952;5984;5412;4576;3047;1185;1064', '5291', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4458, '豫;射;厭', '斁;猒', '豫、射[斁]12，厭[猒]也3。4', '[音釋-1]：亦。\n[釋文-2]：羊石反，字又作斁，同\n[釋文-3]：於豔反\n[郭注-4]：詩曰：「服之無斁。」豫，未詳。', '釋詁弟一', '爾雅', '2;3', DEFAULT, DEFAULT, '6136;3310;6003', '2032;3041', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4459, '烈;績;業;績;勲;功', '', '烈、績，業也。1績、勲，功也。2', '[郭注-1]：謂功業也。\n[郭注-2]：謂功勞也。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '6427;8735;1749;8735;9213;9214', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4460, '功;績;質;登;平;明;考;就;成', '憕;攷', '功、績、質、登[憕]、平、明、考[攷]、就，成也。1', '[郭注-1]：功、績，皆有成。詩曰：「質爾民人。」禮記曰：「年穀不登。」穀梁傳曰：「平者，成也。」事有分明，亦成濟也。', '釋詁弟一', '爾雅', '4;7', DEFAULT, DEFAULT, '9214;8735;3972;1079;3074;4323;5385;3332;9749', '6724;2050', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4461, '梏;梗;較;頲;庭;道;直', '亢;𡈼;導', '梏12、梗[亢]3、較45、頲[𡈼]67、庭、道[導]，直也。8', '[音釋-1]：谷。\n[釋文-2]：古沃反，郭音角\n[釋文-3]：古杏反\n[音釋-4]：角。\n[釋文-5]：古學反\n[音釋-6]：他頂。\n[釋文-7]：他鼎反\n[郭注-8]：梏、梗、較、頲，皆正直也。詩曰：「旣庭且碩。」頲、道，無所屈。', '釋詁弟一', '爾雅', '2;4;6', DEFAULT, DEFAULT, '3823;3543;9533;5647;5928;1205;8412', '6655;5240;1987', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4462, '密;康;靜', '宓', '密[宓]1、康，靜也。2', '[釋文-1]：亡筆反\n[郭注-2]：皆安靜也。', '釋詁弟一', '爾雅', '1', DEFAULT, DEFAULT, '5876;4459;3190', '4579', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4463, '豫;寧;綏;康;柔;安', '寍;妥', '豫、寧[寍]、綏[妥]、康、柔，安也。1', '[郭注-1]：皆見詩、書。', '釋詁弟一', '爾雅', '2;3', DEFAULT, DEFAULT, '6136;3057;8764;4459;3609;4578', '4575;8349', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4464, '平;均;夷;弟;易;矢;弛;弛;易', '𢓡;𢓡', '平、均、夷[𢓡]、弟[𢓡]，易也12。3矢，弛也45。6弛，易也7。8', '[音釋-1]：以豉。\n[釋文-2]：以豉反，注同\n[郭注-3]：皆謂易直。\n[音釋-4]：並音尸紙。\n[釋文-5]：尸紙反\n[郭注-6]：弛放。\n[釋文-7]：施、李音尸紙反，下音亦。顧、謝本弛作施，并「易」皆以豉反，注同\n[郭注-8]：相延易。', '釋詁弟一', '爾雅', '3;4', DEFAULT, DEFAULT, '3074;9030;6615;3401;6134;3309;8497;8497;6134', '1239;1239', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4465, '稀;寡;鮮;罕;鮮;寡', '尟;尟;尟', '希{稀}、寡、鮮[尟]12，罕[尟]也3。4鮮[尟]，寡也。5', '[音釋-1]：息淺。\n[釋文-2]：息淺反，下同\n[釋文-3]：火旱反\n[郭注-4]：罕亦希也。\n[郭注-5]：謂少。', '釋詁弟一', '爾雅', '3;4;5', DEFAULT, DEFAULT, '4428;4608;7648;4817;7648;4608', '1091;1091;1091', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4466, '酬;酢;侑;報', '讎;醋', '酬[讎]、酢[醋]12、侑3，報也。4', '[音釋-1]：昨。\n[釋文-2]：才各反\n[釋文-3]：本或作宥，同，于救反\n[郭注-4]：此通謂相報荅，不主于飲酒。', '釋詁弟一', '爾雅', '1;2', DEFAULT, DEFAULT, '9840;9862;8257;6651', '1487;9841', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4467, '毗劉;㬥樂;覭髳;茀離', '𣔵落;弗離', '毗劉1，㬥樂[𣔵落]也23。45覭髳67，茀離[弗離]也8。910', '[釋文-1]：如字，樊光本作庇，云：「蔭也」\n[音釋-2]：剥。洛。\n[釋文-3]：本又作𤒺，同，邦角反。本又作爍，郭音洛，又力角反。補舍人曰：「木枝葉稀疏不均也」\n[郭注-4]：謂樹木葉缺落、蔭䟽。㬥樂，見詩。\n[釋文-5]：廕，於禁反，又作蔭，同。\n[音釋-6]：陌。蒙。\n[釋文-7]：郭亡革反，又莫經反。音蒙\n[釋文-8]：音弗\n[郭注-9]：謂草木之叢茸翳薈也。茀離卽彌離，彌離猶蒙蘢耳。孫叔然字別爲義，失矣。\n[釋文-10]：叢，才工反。茸，如容反，或如融反。薈，烏會反。', '釋詁弟一', '爾雅', '2;4', DEFAULT, DEFAULT, '6702/9446;4248/3764;5494/5741;571/2294', '3602/549;8363/2294', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4468, '蠱;𩥅;貳;疑', '𠑹', '蠱[𠑹]1、謟{𩥅}23、貳，疑也。4', '[釋文-1]：音古\n[音釋-2]：叨。\n[釋文-3]：郭音縚，他刀反，字或作慆，沈勑檢反\n[郭注-4]：蠱惑、有貳心者，皆疑也。左傳曰：「天命不謟。」音縚。', '釋詁弟一', '爾雅', '1', DEFAULT, DEFAULT, '8978;6197;3967;9780', '5470', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4469, '楨;翰;儀;榦', '', '楨1、翰2、儀，榦也3。4', '[釋文-1]：音貞。補舍人曰：「正也。築牆所立兩木也」\n[釋文-2]：補舍人曰：「所以當牆兩邊障土者也」\n[釋文-3]：本又作幹，胡旦反，又作翰\n[郭注-4]：詩曰：「維周之翰。」儀表亦體榦。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '3608;2244;5084;3620', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4470, '弼;棐;輔;比;俌', '𠨒', '弼、棐12、輔、比[𠨒]34，俌也56。78', '[音釋-1]：匪。\n[釋文-2]：音匪\n[音釋-3]：毗志。\n[釋文-4]：毗志反，注同\n[音釋-5]：甫。\n[釋文-6]：音輔，郭方輔反，字林音甫\n[郭注-7]：書曰：「天畏棐忱。」易曰：「比，輔也。」俌猶輔也。\n[釋文-8]：諶，市林反，本今作忱。', '釋詁弟一', '爾雅', '4', DEFAULT, DEFAULT, '8508;3755;9567;5229;5046', '5780', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4471, '疆;界;邊;衞;圉;垂', '', '疆1、界、邊、衞、圉23，垂也。45', '[釋文-1]：壃，字又作畺，音姜。經典作疆，假借字\n[音釋-2]：語。\n[釋文-3]：魚呂反。補舍人曰：「拒邊垂也」\n[郭注-4]：疆埸、竟界、邊旁、營衞、守圉，皆在外垂也。左傳曰：「聊以固吾圉也。」\n[釋文-5]：埸，羊石反，廣雅云：「界也。」旁，步郎反。聊，音遼。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '9202;9187;1209;1277;6649;9149', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4472, '昌;敵;彊;應;丁;當', '正', '昌、敵1、彊2、應3、丁[正]，當也。45', '[釋文-1]：音狄\n[釋文-2]：巨良反，下注同\n[釋文-3]：本或作膺，同，於矜反\n[郭注-4]：書曰：「禹拜昌言。」彊者，好與物相當值。\n[釋文-5]：好，呼報反。', '釋詁弟一', '爾雅', '5', DEFAULT, DEFAULT, '4239;2029;8491;6713;9747;9193', '1087', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4473, '孛;肩;揺;動;蠢;迪;俶;厲;作', '開;倏', '浡{孛}1、肩[開]、揺、動、蠢2、迪、俶[倏]3、厲，作也。4', '[釋文-1]：步忽反\n[釋文-2]：尺允反\n[釋文-3]：昌育反\n[郭注-4]：浡然，興作貌。蠢，動作。公羊傳曰：「俶，甚也。」穀梁傳曰：「始厲樂矣。」肩，見書。迪，未詳。', '釋詁弟一', '爾雅', '2;7', DEFAULT, DEFAULT, '3872;2624;7951;9233;8971;1132;5017;5985;5075', '7756;6350', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4474, '茲;斯;咨;呰;已;此', '些', '兹{茲}、斯、咨、呰[些]12、已3，此也。4', '[音釋-1]：紫。\n[釋文-2]：子爾反，或子移反，郭音些。案廣雅：「些，辭也。」息計反，又息賀反，謂語餘聲也\n[釋文-3]：音以\n[郭注-4]：呰、已，皆方俗異語。', '釋詁弟一', '爾雅', '4', DEFAULT, DEFAULT, '526;9485;841;898;9797;1083', '1086', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4475, '𧪰;咨;𧪰', '嗞', '嗟{𧪰}、咨[嗞]，𨲠{𧪰}也12。34', '[音釋-1]：嗟。\n[釋文-2]：本或作䦈，字林云：「皆古嗟字」\n[郭注-3]：今河北人云𨲠歎，音兔罝。\n[釋文-4]：罝，音嗟。', '釋詁弟一', '爾雅', '2', DEFAULT, DEFAULT, '1671;841;1671', '922', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4476, '閑;狎;遦;貫;習', '遦', '閑、狎1、串{遦}23、貫[遦]45，習也。67', '[釋文-1]：乎甲反\n[音釋-2]：五患。\n[釋文-3]：郭音五患反，沈、謝古患反。補舍人曰：「心之習也」\n[音釋-4]：慣。\n[釋文-5]：慣，本又作貫，又作遦，同，古患反\n[郭注-6]：串，厭串。貫，貫忕也。今俗語皆然。\n[釋文-7]：厭，於豔反。忕，音逝，又時設反，張揖雜字音曳，云：「狃忕，過度。」', '釋詁弟一', '爾雅', '4', DEFAULT, DEFAULT, '7769;6340;1111;4341;2240', '1111', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4477, '曩;塵;佇;淹;留;久', '陳;眝;腌', '曩1、塵[陳]2、佇[眝]、淹[腌]、留，久也。3', '[釋文-1]：奴黨反\n[釋文-2]：補孫炎曰：「居之久，久則塵生」\n[郭注-3]：塵垢、佇企、淹滯，皆稽久也。', '釋詁弟一', '爾雅', '2;3;4', DEFAULT, DEFAULT, '4234;6290;5211;7003;9197;3409', '9688;2176;2718', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4478, '逮;及;曁;與', '臮', '逮1、及、曁[臮]2，與也。3', '[釋文-1]：音代，一音大計反\n[釋文-2]：其器反\n[郭注-3]：公羊傳曰：「會、及、暨，皆與也。」逮亦及也。', '釋詁弟一', '爾雅', '3', DEFAULT, DEFAULT, '1147;1918;4286;1782', '5239', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4479, '騭;假;格;陟;躋;登;升', '陟;叚;登', '騭[陟]12、假[叚]34、格、陟、躋5、登，陞{升}[登]也6。7', '[音釋-1]：質。\n[釋文-2]：之實反，又音陟\n[音釋-3]：遐。\n[釋文-4]：音遐\n[釋文-5]：子兮反\n[釋文-6]：音升\n[郭注-7]：方言曰：「魯 衞之閒曰騭，梁 益曰格。」禮記曰：「天王登遐。」公羊傳曰：「躋者何？陞也。」', '釋詁弟一', '爾雅', '1;2;7', DEFAULT, DEFAULT, '6138;5076;3603;9643;1347;1079;9507', '9643;1928;1079', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4480, '揮;淥;歇;涸;竭', '渴', '揮1、盝{淥}2、歇3、涸45，竭[渴]也6。78', '[釋文-1]：許韋反\n[釋文-2]：音鹿\n[釋文-3]：虛謁反\n[音釋-4]：鶴。\n[釋文-5]：戸各反\n[釋文-6]：渴，音竭，本或作竭\n[郭注-7]：月令曰：「無漉陂池。」國語曰：「水涸而成梁。」揮振去水亦爲竭。歇，通語。\n[釋文-8]：毋，音無，本今作無。漉，音鹿。去，起呂反。', '釋詁弟一', '爾雅', '5', DEFAULT, DEFAULT, '8010;7376;5536;7342;6690', '7345', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4481, '挋;式;刷;淸', '飾;㕞', '挋12、拭{式}[飾]3、刷[㕞]45，淸也6。78', '[音釋-1]：振。\n[釋文-2]：音震\n[釋文-3]：音式\n[音釋-4]：所劣。\n[釋文-5]：字又作㕞，所劣反，說文云：「刮也。」廣雅云：「削也」\n[釋文-6]：如字，劉音儀禮慈性反\n[郭注-7]：振訊、抆拭、掃刷，皆所以爲絜淸。\n[釋文-8]：抆，亡粉反。掃，素老反。', '釋詁弟一', '爾雅', '2;3', DEFAULT, DEFAULT, '7915;3031;2784;7202', '4896;1917', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4482, '鴻;昏;於;顯;閒;代', '', '鴻、昏、於1、顯、閒23，代也。4', '[音釋-1]：烏\n[音釋-2]：㵎。\n[釋文-3]：古莧反，注同，謝古閑反，郭古鴈反，施胡瞎反。補孫炎曰：「廁之代也」\n[郭注-4]：鴻鴈知運代。昏主代明，明亦代昏。顯卽明也。閒錯亦相代，於義未詳。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '2415;4223;2488;5692;7762;5083', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4483, '饁;饟;饋', '', '饁12、饟34，饋也56。7', '[音釋-1]：叶。\n[釋文-2]：于輒反，字林于怯反。補孫炎曰：「野之饋也」\n[音釋-3]：餉。\n[釋文-4]：式亮反。補舍人曰：「自家之野也」\n[音釋-5]：櫃。\n[釋文-6]：本或作餽，同，巨愧反\n[郭注-7]：國語曰：「其妻饁之。」', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '3231;3232;3234', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4484, '遷;運;徙', '', '遷1、運，徙也2。3', '[釋文-1]：七延反，注同\n[釋文-2]：斯尒反\n[郭注-3]：今江東通言遷徙。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '1137;1138;1135', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4485, '秉;拱;執', '廾', '秉、拱[廾]1，執也。2', '[釋文-1]：九勇反\n[郭注-2]：兩手持爲拱。', '釋詁弟一', '爾雅', '2', DEFAULT, DEFAULT, '1919;7842;6648', '1755', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4486, '廞;熙;興', '', '廞12、熙，興也3。4', '[音釋-1]：許金。\n[釋文-2]：郭音歆，又音欽，字林火欽反\n[釋文-3]：如字，又許應反\n[郭注-4]：書曰：「庶績咸熙。」廞，見周官。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '5970;6527;1783', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4487, '衞;蹶;假;嘉', '偉;話', '衞[偉]、蹶[話]12、假34，嘉也。5', '[音釋-1]：居衛。\n[釋文-2]：居衞反，下同\n[音釋-3]：暇。\n[釋文-4]：戸嫁反\n[郭注-5]：詩序曰：「假樂，嘉成王也。」餘未詳。', '釋詁弟一', '爾雅', '1;2', DEFAULT, DEFAULT, '1277;1369;5076;3082', '4982;1550', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4488, '廢;稅;赦;舍', '脫;捨', '廢1、稅[脫]2、赦，舍[捨]也34。5', '[釋文-1]：字亦作癈，同，甫穢反\n[釋文-2]：始銳反\n[音釋-3]：捨。\n[釋文-4]：音捨，注同\n[郭注-5]：詩曰：「召伯所稅。」舍，放置。', '釋詁弟一', '爾雅', '2;4', DEFAULT, DEFAULT, '5963;4488;2033;3275', '2653;7885', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4489, '棲遟;舌;休;苦;𦳋;齂;呬;息', '屖遟;愒;夃;喟', '棲遟[屖遟]1、憩{舌}[愒]2、休、苦[夃]、㕟{𦳋}[喟]34、齂56、呬78，息也。9', '[釋文-1]：音西。直釐反。補舍人曰：「行步之息也」\n[釋文-2]：起例反，本或作愒，同。補舍人曰：「臥之息也」\n[音釋-3]：苦怪。\n[釋文-4]：苦怪反，又墟季反，字林以爲喟，丘愧反。孫本作快，郭音苦槩反。又作嘳，墟愧、苦怪二反\n[音釋-5]：訶誡。\n[釋文-6]：郭、施、謝海拜反，孫虛貴反，顧乎被反\n[音釋-7]：許四。\n[釋文-8]：郭許四反，孫許器反，施火季反\n[郭注-9]：棲遲，遊息也。苦勞者，宜止息。憩，見詩。㕟、齂、呬，皆氣息貌。今東齊呼息爲呬也。', '釋詁弟一', '爾雅', '1;2;4;5', DEFAULT, DEFAULT, '7714/1148;1444;3818;323;376;2237;824;6706', '5417/1148;6808;3407;830', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4490, '供;歭;共;具', '庤', '供1、歭[庤]23、共45，具也。6', '[釋文-1]：如字，又居用反\n[音釋-2]：直紀。\n[釋文-3]：直紀反。補舍人曰：「偫，具事也」\n[音釋-4]：恭。\n[釋文-5]：音恭。補舍人曰：「具物也」\n[郭注-6]：皆謂備具。', '釋詁弟一', '爾雅', '2', DEFAULT, DEFAULT, '5027;1067;1776;1771', '5959', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4491, '𢜮;憐;惠;愛', '㤅', '𢜮12、憐3、惠，愛[㤅]也。4', '[音釋-1]：某。\n[釋文-2]：亡矩反，又音無\n[釋文-3]：力田反\n[郭注-4]：𢜮，韓 鄭語，今江東通呼爲憐。', '釋詁弟一', '爾雅', '4', DEFAULT, DEFAULT, '6787;6962;2505;3369', '6781', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4492, '娠;蠢;震;戁;妯;騷;感;吪;蹶;動', '蝡;慅', '娠12、蠢3、震、戁[蝡]4、妯56、騷[慅]7、感、訛{吪}8、蹶，動也。910', '[音釋-1]：振。\n[釋文-2]：指慎反，又音身\n[釋文-3]：尺允反\n[釋文-4]：奴板反\n[音釋-5]：抽。\n[釋文-6]：顧依詩勑留反，郭盧篤反，又徒歷反\n[釋文-7]：蘇刀反\n[釋文-8]：五禾反\n[郭注-9]：娠猶震也。詩曰：「憂心且妯。」「無感我帨兮。」「或寢或訛。」蠢、戁、騷、蹶，皆揺動貌。\n[釋文-10]：帨，始銳反，毛詩傳云：「佩巾也。」', '釋詁弟一', '爾雅', '4;6', DEFAULT, DEFAULT, '8136;8971;7533;6725;8289;6231;6913;930;1369;9233', '8884;6912', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4493, '覆;察;副;審', '覆', '覆1、察、副[覆]2，審也。34', '[釋文-1]：芳福反，後注同\n[釋文-2]：音赴\n[郭注-3]：覆校、察視、副長，皆所爲審諦。\n[釋文-4]：校，音敎。長，丁丈反，下文并注同。諦，音帝。', '釋詁弟一', '爾雅', '3', DEFAULT, DEFAULT, '4854;4583;2766;722', '4854', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4494, '契;滅;殄;絕', '㓞', '契[㓞]12、滅、殄3，絶{絕}也。45', '[音釋-1]：苦結。\n[釋文-2]：郭苦計反，字又作挈，顧苦結反，注同。左傳云：「盡借邑人之車，契其軸」是也\n[釋文-3]：大典反\n[郭注-4]：今江東呼刻斷物爲契斷。\n[釋文-5]：刻，音克。斷，大管反。', '釋詁弟一', '爾雅', '1', DEFAULT, DEFAULT, '6614;7443;2552;8548', '2815', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4495, '郡;臻;仍;迺;侯;乃', '窘/屈/出;晉;候', '郡[窘/屈/出]、臻[晉]、仍、迺、侯[候]，乃也。1', '[郭注-1]：迺卽乃。餘未詳。', '釋詁弟一', '爾雅', '1;2;5', DEFAULT, DEFAULT, '4006;7710;5049;3053;3313;3052', '4670/0/5430/0/3865;4205;5080', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4496, '迪;䍃;訓;道', '䌛;順', '迪1、繇{䍃}[䌛]23、訓[順]，道也4。5', '[釋文-1]：音狄\n[音釋-2]：由。\n[釋文-3]：音由\n[釋文-4]：如字，或徒報反，非\n[郭注-5]：義皆見詩、書。', '釋詁弟一', '爾雅', '2;3', DEFAULT, DEFAULT, '1132;3300;1495;1205', '8516;5654', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4497, '僉;咸;胥;皆', '', '僉12、咸、胥3，皆也。4', '[音釋-1]：七廉。\n[釋文-2]：七廉反\n[釋文-3]：息廬反\n[郭注-4]：東齊曰胥，見方言。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '3272;872;2699;2227', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4498, '育;孟;耆;艾;正;伯;長', '餀', '育、孟、耆1、艾[餀]2、正、伯3，長也4。5', '[釋文-1]：巨之反，禮記云：「六十曰耆」\n[釋文-2]：五蓋反，下同，禮記曰：「五十曰艾」\n[釋文-3]：補舍人曰：「位之長也」\n[音釋-4]：丁丈\n[郭注-5]：育養亦爲長。正、伯，皆官長。', '釋詁弟一', '爾雅', '4', DEFAULT, DEFAULT, '9788;9774;5380;389;1087;4967;6069', '3248', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4499, '艾;歷', '䢃;厤', '艾[䢃]，歷[厤]也。12', '[郭注-1]：長者多更歷。\n[釋文-2]：更，音庚。', '釋詁弟一', '爾雅', '1;2', DEFAULT, DEFAULT, '389;1070', '5800;5988', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4500, '歷;秭;算;數', '𣀷/秝;筭', '歷[𣀷/秝]、秭12、算[筭]34，數也5。6', '[音釋-1]：姊。\n[釋文-2]：音姊\n[音釋-3]：筭。\n[釋文-4]：字又作筭，素緩反，郭息轉反。補舍人曰：「釋數也」\n[釋文-5]：色具反，注同，謝色主反\n[郭注-6]：歷，歷數也。今以十億爲秭。論語云：「何足算也。」', '釋詁弟一', '爾雅', '1;3', DEFAULT, DEFAULT, '1070;4499;3010;2011', '2010/0/4505;3009', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4501, '歷;傅', '䕻', '歷[䕻]，傅也12。3', '[音釋-1]：附。\n[釋文-2]：符付反，注同\n[郭注-3]：傅，近。', '釋詁弟一', '爾雅', '1', DEFAULT, DEFAULT, '1070;5044', '577', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4502, '艾;歷;覛;胥;相', '䢃;厤;眽', '艾[䢃]、歷[厤]、覛[眽]12、胥，相也3。4', '[音釋-1]：脉。\n[釋文-2]：字又作脈，亡革反\n[釋文-3]：息羊反，讀者或息亮反，今不用\n[郭注-4]：覛謂相視也。公羊傳曰：「胥盟者何？相盟也。」艾、歷，未詳。', '釋詁弟一', '爾雅', '1;2;3', DEFAULT, DEFAULT, '389;1070;7501;2699;2162', '5800;5988;2147', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4503, '乂;亂;靖;神;弗;淈;治', '䢃;淸;敒;弼', '乂[䢃]1、亂2、靖[淸]、神[敒]、弗[弼]、淈34，治也5。67', '[釋文-1]：字又作䢃，亦作刈，同，魚廢反\n[釋文-2]：補舍人曰：「意之治也」\n[音釋-3]：骨。\n[釋文-4]：郭古沒反，又胡忽反\n[釋文-5]：直吏反，謝如字\n[郭注-6]：論語曰：「子有亂臣十人。」淈，書序作汩，音同耳。神，未詳。餘並見詩、書。\n[釋文-7]：汩，古沒反。', '釋詁弟一', '爾雅', '1;3;4;5', DEFAULT, DEFAULT, '8362;9744;6686;25;8363;7208;7089', '5800;7202;2018;8508', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4504, '頤;艾;育;養', '宧', '頤[宧]1、艾、育，養也。2', '[釋文-1]：以之反\n[郭注-2]：汝 潁 梁 宋之閒曰艾，方言云。', '釋詁弟一', '爾雅', '1', DEFAULT, DEFAULT, '7827;389;9788;3221', '4562', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4505, '犬;汏;渾;隕;墜', '隕;隕', '汱{犬}[隕]{汏}12、渾[隕]34、隕5，墜也6。7', '[音釋-1]：古犬。\n[釋文-2]：姑犬反，施胡犬反，顧徒蓋反，字宜作汏\n[音釋-3]：胡本。\n[釋文-4]：胡本反\n[釋文-5]：于敏反\n[釋文-6]：直類反\n[郭注-7]：汱、渾，皆水落貌。', '釋詁弟一', '爾雅', '1;3', DEFAULT, DEFAULT, '6303;7369;7197;9650;9162', '9650;9650', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4506, '際;接;翜;捷', '', '際、接、翜12，捷也3。4', '[音釋-1]：所甲。\n[釋文-2]：所甲反\n[釋文-3]：才接反\n[郭注-4]：捷謂相接續也。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '9697;7919;2263;8089', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4507, '毖;神;侐;慎', '', '毖12、神、溢{侐}3，慎也。4', '[音釋-1]：祕。\n[釋文-2]：音祕\n[釋文-3]：音𨓜。補舍人曰：「行之慎也」\n[郭注-4]：神，未詳。餘見詩、書。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '5230;25;5057;6714', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4508, '鬱陶;䍃;喜', '䌛', '鬱陶1、繇{䍃}[䌛]23，喜也。4', '[釋文-1]：徒刀反\n[音釋-2]：由。\n[釋文-3]：音由，一音遥\n[郭注-4]：孟子曰：「鬱陶思君。」禮記曰：「人喜則斯陶，陶斯詠，詠斯猶。」猶卽繇也，古今字耳。', '釋詁弟一', '爾雅', '2', DEFAULT, DEFAULT, '3850/9689;3300;3075', '8516', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4509, '馘;穧;獲', '', '馘12、穧34，獲也。56', '[音釋-1]：古獲。\n[釋文-2]：古獲反，本又作聝，音同\n[音釋-3]：才細。\n[釋文-4]：本或作齊，同，才細反，依注字宜從禾\n[郭注-5]：今以獲賊耳爲馘，獲禾爲穧。並見詩。\n[釋文-6]：穫，一本作獲，戸郭反。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '7821;4464;6364', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4510, '阻;艱;難', '', '阻、艱，難也12。3', '[音釋-1]：乃旦。\n[釋文-2]：奴旦反，注同，一音如字\n[郭注-3]：皆險難。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '9633;9168;2402', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4511, '剡;㗉;利', '', '剡12、㗉34，利也。56', '[音釋-1]：羊冉。\n[釋文-2]：羊冉反\n[音釋-3]：略。\n[釋文-4]：力約反，詩本作略\n[郭注-5]：詩曰：「以我剡耜。」\n[釋文-6]：耜，音似。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '2753;2746;2752', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4512, '允;任;壬;佞', '俊', '允[俊]、任12、壬3，佞也。4', '[音釋-1]：壬鴆。\n[釋文-2]：而鴆反，又而淫反，注同\n[釋文-3]：而今反，注同\n[郭注-4]：書曰：「而難任人。」允信者，佞人似信。壬猶任也。', '釋詁弟一', '爾雅', '1', DEFAULT, DEFAULT, '5460;5089;9764;8277', '4962', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4513, '俾;并;抨;使;俾;并;抨;使;從', '𢖊;𢖊;𢖊;𢖊', '俾1、拼{并}[𢖊]23、抨[𢖊]45，使也。67俾、拼{并}[𢖊]、抨[𢖊]、使，從也。8', '[釋文-1]：必尒反\n[音釋-2]：北萌。\n[釋文-3]：北萌反，以利使人曰拼，從手\n[音釋-4]：烹。\n[釋文-5]：普耕反，案字書拼、抨並音普耕、𥙷耕二反，訓義亦同，今旣二字相隨，故多互其讀也。亦從手，彈也。字又作伻，音同，使人也\n[郭注-6]：皆謂使令，見詩。\n[釋文-7]：令，力呈反。\n[郭注-8]：四者又爲隨從。', '釋詁弟一', '爾雅', '2;3;6;7', DEFAULT, DEFAULT, '5098;5228;8046;5101;5098;5228;8046;5101;5227', '1240;1240;1240;1240', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4514, '襄;仍;因', '相', '儴{襄}[相]12、仍，因也。3', '[音釋-1]：攘。\n[釋文-2]：樊、孫如羊反，引論語「其父攘羊」，釋之作攘，注云：「因來而盜曰攘。」施息羊反\n[郭注-3]：皆謂因緣。', '釋詁弟一', '爾雅', '1', DEFAULT, DEFAULT, '5324;5049;3924', '2162', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4515, '蕫;督;正', '中', '董{蕫}[中]、督1，正也。2', '[釋文-1]：多毒反\n[郭注-2]：皆謂御正。', '釋詁弟一', '爾雅', '1', DEFAULT, DEFAULT, '402;2172;1087', '231', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4516, '享;孝;珍;享;獻', '', '享1，孝也。2珍、享，獻也。3', '[釋文-1]：虛丈反，下注同\n[郭注-2]：享祀孝道。\n[郭注-3]：珍物宜獻。穀梁傳曰：「諸侯不享覲。」', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '3333;5386;148;3333;6366', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4517, '縱;縮;亂', '', '縱1、縮2，亂也。34', '[釋文-1]：子用反\n[釋文-2]：所六反\n[郭注-3]：縱放、掣緒，皆亂法也。\n[釋文-4]：掣，昌世反。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '8555;8565;9744', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4518, '探;篡;俘;取', '', '探12、篡34、俘56，取也。78', '[音釋-1]：貪。\n[釋文-2]：吐南反\n[音釋-3]：初患。\n[釋文-4]：初患反\n[音釋-5]：孚。\n[釋文-6]：音孚。補李巡曰：「囚敵曰俘。伐執之曰取」\n[郭注-7]：書曰：「俘厥寳玉。」篡者，奪取也。探者，摸取也。\n[釋文-8]：摸，亡各、模胡二反。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '8003;5846;5168;1926', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4519, '徂;在;存;在;存;省;士;察', '㡹', '徂[㡹]、在，存也。12在3、存、省4、士，察也。56', '[郭注-1]：以徂爲存，猶以亂爲治、以曩爲曏、以故爲今。此皆詁訓，義有反覆旁通，美惡不嫌同名。\n[釋文-2]：治，直吏反。曩，乃朗反。嚮，許亮反，本今作曏。\n[釋文-3]：補舍人曰：「見物之察」\n[釋文-4]：息丼反\n[郭注-5]：書曰：「在璿璣玉衡。」士，理官，亦主聽察。存卽在。\n[釋文-6]：璿，音旋，又作琁。', '釋詁弟一', '爾雅', '1', DEFAULT, DEFAULT, '1106;9068;9778;9068;9778;2220;226;4583', '5967', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4520, '烈;𣡌;餘', '', '烈、枿{𣡌}12，餘也。3', '[音釋-1]：五割。\n[釋文-2]：五割反，本或作𣖂，又作𣎴，注作蘖，並同，說文作𣡌，以𣖂、𣎴爲古文，蘖爲或體，云：「伐木之餘。」補李巡曰：「槁木之餘也」\n[郭注-3]：晉 衞之閒曰櫱，陳 鄭之閒曰烈。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '6427;3804;3247', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4521, '迓;迎', '', '迓1，迎也。23', '[釋文-1]：訝，五駕反，本又作迓\n[郭注-2]：公羊傳曰：「跛者迓跛者。」\n[釋文-3]：跛，布我反。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '1579;1125', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4522, '元;良;首', '郎', '元、良[郎]，首也。12', '[郭注-1]：左傳曰：「狄人歸先軫之元。」良，未聞。\n[釋文-2]：軫，之忍反。', '釋詁弟一', '爾雅', '2', DEFAULT, DEFAULT, '2;3341;5703', '4139', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4523, '薦;摯;臻', '瀳;𡠗', '薦[瀳]12、摯[𡠗]3，臻也4。5', '[音釋-1]：曹練。\n[釋文-2]：遭練反\n[釋文-3]：之二反\n[釋文-4]：則巾反\n[郭注-5]：薦，進也。摯，至也。故皆爲臻。臻，至也。', '釋詁弟一', '爾雅', '1;2', DEFAULT, DEFAULT, '6261;7862;7710', '7217;8250', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4524, '賡;揚;續', '', '賡12、揚，續也3。4', '[音釋-1]：古孟。\n[釋文-2]：古孟反，沈、孫音庚，說文以爲古文續\n[釋文-3]：似欲反\n[郭注-4]：書曰：「乃賡載歌。」揚，未詳。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '8550;7958;8550', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4525, '祔;祪;祖', '', '祔12、祪34，祖也。5', '[音釋-1]：附。\n[釋文-2]：音附，郭音付\n[音釋-3]：鬼。\n[釋文-4]：俱毀反\n[郭注-5]：祔，付也，付新死者於祖廟。祪，毀廟主。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '35;34;36', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4526, '卽;尼;尼;定;邇;幾;暱;近', '', '卽，尼也12。3尼4，定也。5邇、幾67、暱89，近也。10', '[音釋-1]：女乙。\n[釋文-2]：本亦作昵，同，女乙反，謝羊而反，顧奴啟反，下同\n[郭注-3]：卽猶今也。尼者，近也。尸子曰：「悅尼而來遠。」\n[釋文-4]：補舍人曰：「私之定也」\n[郭注-5]：尼者，止也。止亦定。\n[音釋-6]：機。\n[釋文-7]：音機，又音祈\n[音釋-8]：女乙。\n[釋文-9]：女乙反。補舍人曰：「戚之近也」\n[郭注-10]：暱，親近也。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '3198;5412;5412;4576;1184;2503;4253;1180', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4527, '妥;安坐', '', '妥12，安坐也。34', '[音釋-1]：他回。\n[釋文-2]：郭他回反，沈他果反\n[郭注-3]：禮記曰：「妥而后傳命。」\n[釋文-4]：傳，直攣反。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '8349;4578/9069', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4528, '貉;縮;綸', '幕;摍;䜌', '貉[幕]12、縮[摍]，綸[䜌]也3。45', '[音釋-1]：陌。\n[釋文-2]：亡白反，下同，施胡各反\n[釋文-3]：音倫\n[郭注-4]：綸者，繩也，謂牽縛縮貉之。今俗語亦然。\n[釋文-5]：繩，音乗。', '釋詁弟一', '爾雅', '1;2;3', DEFAULT, DEFAULT, '6131;8565;8654', '4883;7992;1618', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4529, '貉;嗼;安;定', '嗼', '貉[嗼]、嗼12、安，定也。3', '[音釋-1]：莫。\n[釋文-2]：音莫，本亦作莫\n[郭注-3]：皆靜定。見詩。', '釋詁弟一', '爾雅', '1', DEFAULT, DEFAULT, '6131;941;4578;4576', '941', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4530, '伊;維;伊;維;侯', '', '伊，維也。1伊、維，侯也。2', '[郭注-1]：發語辭。\n[郭注-2]：詩曰：「侯誰在矣。」互相訓。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '4969;8703;4969;8703;3313', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4531, '時;寔;是', '', '時、寔1，是也。2', '[音釋-1]：石\n[郭注-2]：公羊傳曰：「寔來者何？是來也。」', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '4191;4577;1089', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4532, '卒;猷;假;輟;已;求;酋;在;卒;就;終;崩;薨;無禄;卒;徂落;殪;死', '𣨛;徦;柮;究;遒/傮;𣨛;𣨛;殂落', '卒[𣨛]12、猷3、假[徦]4、輟[柮]56，已也7。8求[究]9、酋[遒/傮]1011、在、卒[𣨛]、就12，終也13。14崩、薨1516、無禄、卒[𣨛]、徂落[殂落]17、殪1819，死也。2021', '[音釋-1]：子䘏。\n[釋文-2]：子恤反，又作𣨛，下同。補李巡曰：「事之已也」\n[釋文-3]：音由\n[釋文-4]：古雅反\n[音釋-5]：丁劣。\n[釋文-6]：丁劣反\n[釋文-7]：音以\n[郭注-8]：猷、假，未詳。\n[釋文-9]：殏，巨牛反，又作求\n[音釋-10]：在由。\n[釋文-11]：在由反，又子由反，郭音遒\n[釋文-12]：如字，或作噈，子六、子合二反，又作殧，同\n[釋文-13]：𣧩，音終，本又作終\n[郭注-14]：詩曰：「嗣先公爾酋矣。」成就，亦終也。其餘未詳。\n[音釋-15]：呼弘。\n[釋文-16]：火弘反\n[釋文-17]：殂𣧳，音徂落，本又作徂落\n[音釋-18]：於計。\n[釋文-19]：於計反\n[郭注-20]：古者死亡尊卑同稱耳，故尚書堯曰「殂落」，舜曰「陟方乃死」。\n[釋文-21]：稱，尺證反。', '釋詁弟一', '爾雅', '1;3;4;6;7;9;15;16', DEFAULT, DEFAULT, '5361;6375;5076;4834;9797;5375;9880;9068;5361;3332;8590;5896;2563;8416/15;5361;1106/549;2541;2562', '2535;1248;3809;4673;1179/0/5183;2535;2535;2539/549', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4533, '殷;齊;中', '𨽌', '殷[𨽌]、齊，中也1。23', '[釋文-1]：如字，又音仲，又知衆反\n[郭注-2]：書曰：「以殷仲春。」釋地曰：「岠齊州以南。」\n[釋文-3]：距，又作岠，同，音巨。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '5253;4395;231', '3753', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4534, '斯;誃;離', '澌', '斯[澌]1、誃23，離也。4', '[釋文-1]：私貲、所宜二反。補孫炎曰：「析之離」\n[音釋-2]：侈。\n[釋文-3]：尺氏反\n[郭注-4]：齊 陳曰斯。誃，見詩。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '9485;1616;2294', '7340', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4535, '畟;興;起', '慉', '謖{畟}[慉]12、興，起也。3', '[音釋-1]：所六。\n[釋文-2]：所六反\n[郭注-3]：禮記曰：「尸謖。」', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '3374;1783;1017', '6768', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4536, '還;復;返', '', '還12、復3，返也4。', '[音釋-1]：旋。\n[釋文-2]：音旋\n[釋文-3]：音服\n[釋文-4]：音反，說文同，云：「春秋傳作𢓉，從彳」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '1142;1226;1141', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4537, '宣;徇;徧', '', '宣、徇12，徧也3。4', '[音釋-1]：辝峻。\n[釋文-2]：本又作侚，樊本作𢓈，並辭峻反，郭音巡。張揖字詁云：「徇，今巡。」施音詢進，本又爲均序\n[釋文-3]：古遍字\n[郭注-4]：皆周徧也。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '4560;1256;1247', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4538, '馹;遽;傳', '', '馹12、遽3，傳也45。67', '[音釋-1]：日。\n[釋文-2]：而實反，郭音義云：「本或作𨖹。」聲類云：「亦馹字。」同。補舍人曰：尊者之傳也\n[釋文-3]：其據反\n[音釋-4]：張戀。\n[釋文-5]：張戀反，注同\n[郭注-6]：皆轉車、驛馬之名。\n[釋文-7]：車，音居。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '6238;1206;5105', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4539, '蒙;荒;奄', '冡', '蒙[冡]1、荒2，奄也。34', '[釋文-1]：莫公反，小爾雅云：「覆也」\n[釋文-2]：補孫炎曰：「大之奄也」\n[郭注-3]：奄，奄覆也。皆見詩。\n[釋文-4]：覆，音副。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '649;546;6601', '4805', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4540, '告;謁;請', '誥', '告[誥]12、謁，請也。3', '[音釋-1]：谷。\n[釋文-2]：古毒反\n[郭注-3]：皆求請也。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '778;1483;1482', '1528', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4541, '肅;雍;聲', '嘯', '肅[嘯]、噰{雍}1，聲也。2', '[釋文-1]：於容反\n[郭注-2]：詩曰：「肅噰和鳴。」', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '1939;2300;7810', '867', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4542, '格;懷;來', '', '格、懷，來也1。2', '[釋文-1]：徠，音來，本今作來\n[郭注-2]：書曰：「格尔衆庶。」懷，見詩。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '3603;6764;3348', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4543, '畛;厎;致', '致', '畛[致]1、厎2，致也。3', '[釋文-1]：之忍反\n[釋文-2]：之視反\n[郭注-3]：皆見詩傳。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '9190;5984;3367', '3367', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4544, '垑;怙;恃', '', '恀{垑}12、怙3，恃也4。5', '[音釋-1]：是。\n[釋文-2]：音是\n[釋文-3]：音戸\n[釋文-4]：音市\n[郭注-5]：今江東呼母爲恀，音是。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '9109;6775;6776', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4545, '律;遹;述', '', '律、遹1，述也。2', '[釋文-1]：古述字，一音餘橘反\n[郭注-2]：皆敘述也，方俗語耳。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '1257;1157;1107', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4546, '俞;荅;然', '諭;嘫', '俞[諭]1、畣{荅}23，然[嘫]也。45', '[釋文-1]：羊朱反\n[音釋-2]：荅。\n[釋文-3]：古荅字，一本作荅\n[郭注-4]：禮記曰：「男唯女俞。」畣者，應也，亦爲然。\n[釋文-5]：唯，維癸反。應，音譍。', '釋言弟二', '爾雅', '1;3', DEFAULT, DEFAULT, '5439;247;6423', '1501;861', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4547, '豫;臚;敘', '', '豫、臚1，敘也。2', '[釋文-1]：呂居反\n[郭注-2]：皆陳敘也。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '6136;2599;2065', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4548, '庶幾;尚', '', '庶幾1，尚也。2', '[釋文-1]：音機\n[郭注-2]：詩曰：「不尚息焉。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '5958/2503;711', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4549, '觀;指;示', '', '觀1、指，示也。2', '[釋文-1]：施音館，謝音官，注同\n[郭注-2]：國語曰：「且觀之兵。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '5486;7832;10', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4550, '若;惠;順', '', '若、惠，順也。12', '[郭注-1]：詩曰：「惠然肯來。」\n[釋文-2]：肯，口等反。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '599;2505;5654', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4551, '敖;幠;傲', '荒', '敖1、幠[荒]2，傲也3。4', '[釋文-1]：五刀反\n[釋文-2]：郭火孤反，沈亡甫反\n[釋文-3]：五報反\n[郭注-4]：禮記曰：「無幠無傲。」傲，慢也。', '釋言弟二', '爾雅', '2', DEFAULT, DEFAULT, '2514;4900;5001', '546', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4552, '幼;鞠;稺', '毓', '幼、鞠[毓]，稺也1。2', '[釋文-1]：直利反，又作稚\n[郭注-2]：書曰：「不念鞠子哀。」', '釋言弟二', '爾雅', '2', DEFAULT, DEFAULT, '2499;1810;4424', '9788', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4553, '逸;諐;過', '', '逸、諐12，過也。3', '[音釋-1]：𠍴。\n[釋文-2]：去虔反\n[郭注-3]：書曰：「汝則有逸罰。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '6296;6863;1110', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4554, '疑;休;戾', '', '疑、休，戾也1。2', '[釋文-1]：力細反\n[郭注-2]：戾，止也。疑者亦止。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '9780;3818;6356', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4555, '疾;齊;壯', '齌', '疾、齊[齌]，壯也1。2', '[釋文-1]：阻亮反\n[郭注-2]：壯，壯事，謂速也。齊亦疾。', '釋言弟二', '爾雅', '2', DEFAULT, DEFAULT, '4696;4395;228', '6459', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4556, '悈;褊;急', '亟', '悈[亟]12、褊34，急也。56', '[音釋-1]：紀力。\n[釋文-2]：本或作極，又作亟，同，紀力反，或音戒\n[音釋-3]：必淺。\n[釋文-4]：必淺反，說文云：「小衣也。」廣雅云：「褊、狹，𨹟也」\n[郭注-5]：皆急狹。\n[釋文-6]：狹，戸甲反。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '6755;5321;6811', '9016', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4557, '貿;賈;市', '', '貿1、賈23，市也。4', '[釋文-1]：音茂，字又作𧶝，同\n[音釋-2]：古。\n[釋文-3]：音古\n[郭注-4]：詩曰：「抱布貿絲。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '3973;3977;3325', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4558, '厞;陋;隱', '㔷', '厞12、陋[㔷]，隱也。34', '[音釋-1]：符沸。\n[釋文-2]：符沸反，字又作陫，同\n[郭注-3]：禮記曰：「厞用席。」書曰：「揚側陋。」\n[釋文-4]：側，音仄。', '釋言弟二', '爾雅', '2', DEFAULT, DEFAULT, '6002;9641;9669', '8421', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4559, '遏;噬;逮', '逝', '遏、遾{噬}[逝]12，逮也3。4', '[音釋-1]：誓。\n[釋文-2]：嘗例反\n[釋文-3]：音代，一音徒帝反\n[郭注-4]：東齊曰遏，北燕曰遾，皆相及逮。', '釋言弟二', '爾雅', '2', DEFAULT, DEFAULT, '1185;811;1147', '1105', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4560, '征;邁;行', '', '征、邁，行也。1', '[郭注-1]：詩曰：「王于出征。」邁亦行。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '1101;1096;1266', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4561, '圮;敗;覆', '', '圮12、敗，覆也3。4', '[音釋-1]：皮美。\n[釋文-2]：皮美反\n[釋文-3]：芳服反，注同\n[郭注-4]：謂毀覆。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '9112;2041;4854', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4562, '荐;原;再', '謜', '荐12、原[謜]3，再也4。56', '[音釋-1]：賤。\n[釋文-2]：徂薦反，又徂遜反，郭徂很反。補孫炎曰：「草生之再也」\n[釋文-3]：舍人本作𧔞，音同\n[釋文-4]：子代反\n[郭注-5]：易曰：「水荐至。」今呼重蠶爲𧔞。\n[釋文-6]：重，直用反，又直龍反。', '釋言弟二', '爾雅', '2', DEFAULT, DEFAULT, '580;7496;2496', '1499', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4563, '憮;敉;撫', '', '憮12、敉34，撫也。5', '[音釋-1]：武。\n[釋文-2]：亡甫反\n[音釋-3]：亡婢。\n[釋文-4]：亡婢反，郭敷靡反，孫敷是反\n[郭注-5]：憮，愛撫也。敉，義見書。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '6780;2036;7922', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4564, '臞;脙;膌', '', '臞12、脙34，瘠{膌}也5。6', '[音釋-1]：衢。\n[釋文-2]：字又作癯，同，求俱反。補舍人曰：「瘦也」\n[音釋-3]：求。\n[釋文-4]：音求\n[釋文-5]：秦昔反\n[郭注-6]：齊人謂瘠瘦爲脙。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '2652;2654;2656', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4565, '桄;熲;充', '', '桄1、熲2，充也。3', '[釋文-1]：孫作光，古黃反\n[釋文-2]：古迥反\n[郭注-3]：皆充盛也。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '3798;6439;5462', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4566, '屢;暱;亟', '', '屢1、暱2，亟也34。56', '[釋文-1]：婁，本又作屢，同，力住反\n[釋文-2]：女乙反\n[音釋-3]：墟記。\n[釋文-4]：墟記反\n[郭注-5]：親暱者亦數。亟亦數也。\n[釋文-6]：數，色角反。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '5425;4253;9016', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4567, '靡;罔;無', '', '靡、罔，無也。', '', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '7698;4815;8416', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4568, '爽;差;爽;忒', '', '爽，差也1。爽，忒也2。3', '[音釋-1]：初牙\n[釋文-2]：他得反，或作𠈸\n[郭注-3]：皆謂用心差錯，不專一。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '2091;3029;2091;6825', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4569, '佴;貳', '', '佴12，貳也。3', '[音釋-1]：而志。\n[釋文-2]：而志反\n[郭注-3]：佴次爲副貳。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '5051;3967', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4570, '劑;翦;齊', '𣦃', '劑12、翦[𣦃]3，齊也。4', '[音釋-1]：卽隨。\n[釋文-2]：卽隨反\n[釋文-3]：子淺反\n[郭注-4]：南方人呼翦刀爲劑刀。', '釋言弟二', '爾雅', '2', DEFAULT, DEFAULT, '2783;2248;4395', '2755', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4571, '饙;餾;稔', '飪', '饙12、餾34，稔[飪]也5。67', '[音釋-1]：紛。\n[釋文-2]：方云反，字又作餴，同，說文作𩞳，云：「脩飯也。」饙、餴並同𩞳也，字書云：「一蒸米。」補孫炎曰：「烝之曰饙」\n[音釋-3]：力又。\n[釋文-4]：力又反。補孫炎曰：「均之曰餾」\n[釋文-5]：字又作餁，同，而審反\n[郭注-6]：今呼𩛢飯爲饙，饙熟爲餾。\n[釋文-7]：𩛢，音脩，又西九反，又所九反，一音孫。廣雅：「饙謂之𩛢也。」蒼頡篇云：「𩛢，饙也。」飰，字又作𩚳，俗作飯，同，符萬反。字林云：「飯，食也，扶晚反，飤也。」', '釋言弟二', '爾雅', '3', DEFAULT, DEFAULT, '3207;3208;4486', '3209', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4572, '㑞;將;送', '', '媵{㑞}12、將3，送也。4', '[音釋-1]：以證。\n[釋文-2]：以證反，又繩證反。方言云：「媵，寄也、記也、送也。」補孫炎曰：「送女曰媵」\n[釋文-3]：補孫炎曰：「行之送也」\n[郭注-4]：左傳曰：「以媵秦穆姬。」詩曰：「遠于將之。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '5109;1983;1144', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4573, '作;造;爲', '', '作、造1，爲也。', '[釋文-1]：才早反', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '5075;1114;1882', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4574, '餥;餱;食', '', '餥12、餱3，食也。45', '[音釋-1]：非。\n[釋文-2]：音非\n[釋文-3]：音侯\n[郭注-4]：方言云：「陳 楚之閒相呼食爲餥。」\n[釋文-5]：呼，或作𧦝，同。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '3218;3217;3206', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4575, '𥷤;究;窮', '', '鞫{𥷤}1、究2，窮也。3', '[釋文-1]：居六反，字又作鞠，同\n[釋文-2]：音救\n[郭注-3]：皆窮盡也。見詩。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '6652;4673;4674', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4576, '鹵;矜;鹹;苦', '䰼', '滷{鹵}1、矜[䰼]2、鹹3，苦也。45', '[釋文-1]：音魯\n[釋文-2]：鹶，音矜，本又作矜\n[釋文-3]：音咸\n[郭注-4]：滷，苦地也。可矜憐者亦辛苦，苦卽大鹹。\n[釋文-5]：粦，力田反，本今作憐，同。', '釋言弟二', '爾雅', '2', DEFAULT, DEFAULT, '7716;9512;7718;323', '7663', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4577, '干;流;求;流;覃;覃;延', '尋', '干、流，求也。1流，覃[尋]也2。覃，延也。34', '[郭注-1]：詩曰：「左右流之。」\n[釋文-2]：徒南反，本又作（⿰爻尋）字，孫叔然云：「古覃字，同」\n[郭注-3]：皆謂蔓延相被及。\n[釋文-4]：蔓，音萬。莚，以戰反，相連不斷，又音延，本今作延。', '釋言弟二', '爾雅', '5', DEFAULT, DEFAULT, '1441;7476;5375;7476;3338;3338;1265', '1984', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4578, '佻;愉', '', '佻12，偷{愉}也3。4', '[音釋-1]：挑。\n[釋文-2]：他雕反，郭唐了反\n[釋文-3]：他侯反。補李巡曰：「偷薄」\n[郭注-4]：謂苟且。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '5129;6827', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4579, '濳;深;濳;深;測', '勘;探', '濳1，深也2。濳[勘]、深[探]，測也3。4', '[釋文-1]：捷鹽反\n[釋文-2]：如字，又尸鴆反\n[釋文-3]：初力反\n[郭注-4]：測亦水深之別名。', '釋言弟二', '爾雅', '3;4', DEFAULT, DEFAULT, '7290;7036;7290;7036;7185', '9254;8003', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4580, '穀;鞠;生', '毓', '穀、鞠[毓]1，生也。2', '[釋文-1]：居六反\n[郭注-2]：詩曰：「糓則異室。」', '釋言弟二', '爾雅', '2', DEFAULT, DEFAULT, '4485;1810;3876', '9788', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4581, '啜;茹;茹;虞;度', '', '啜1，茹也23。4茹5、虞，度也67。8', '[釋文-1]：常悅反，郭音銳，顧豬芮反，施丑衞、尺銳二反，說文云：「啜，嘗也。」廣雅云：「食也。」補舍人曰：「茹食也」\n[音釋-2]：如庶。\n[釋文-3]：音汝，食也\n[郭注-4]：啜者，拾食。\n[釋文-5]：如庶反，注同\n[音釋-6]：鐸。\n[釋文-7]：待各反，注及下同\n[郭注-8]：皆測度也。詩曰：「不可以茹。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '804;614;614;3110;1929', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4582, '試;式;用', '', '試、式，用也。1', '[郭注-1]：見詩、書。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '1540;3031;2082', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4583, '誥;誓;謹', '梏', '誥[梏]、誓，謹也。1', '[郭注-1]：皆所以約勒，謹戒衆。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '1528;1530;1520', '3823', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4584, '競;逐;彊', '', '競、逐，彊也12。3', '[音釋-1]：巨丈。\n[釋文-2]：強，巨丈反，注同，本或作彊字，又其良反\n[郭注-3]：皆自勉彊。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '1736;1178;8491', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4585, '禦;圉;禁', '', '禦1、圉，禁也2。3', '[釋文-1]：本或作御，同，俱魚呂反\n[釋文-2]：補舍人曰：「禦、圉，謂未有而預防之也」\n[郭注-3]：禁制。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '56;6649;71', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4586, '窒;薶;塞', '', '窒12、薶34，塞也。5', '[音釋-1]：豬乙。\n[釋文-2]：豬乙反，又丁栗反\n[音釋-3]：埋。\n[釋文-4]：亡皆反\n[郭注-5]：謂塞孔穴。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '4666;626;9095', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4587, '黼;黻;彰', '', '黼12、黻34，彰也。56', '[音釋-1]：甫。\n[釋文-2]：字或作𦓞，同，音甫，白與黑曰黼，爲斧形\n[音釋-3]：弗。\n[釋文-4]：字或作𦓗，同，音弗，靑與黑曰黻，黻，戾也\n[郭注-5]：黼文如斧。黻文如兩己相背。\n[釋文-6]：己，音紀。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '4946;4947;5717', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4588, '膺;身;親', '', '膺1、身，親也。2', '[釋文-1]：於矜反\n[郭注-2]：謂躬親。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '2615;5250;5514', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4589, '愷悌;發', '闓𠂆', '愷悌[闓𠂆]1，發也。2', '[釋文-1]：苦亥反。徒禮反。補舍人、李、孙曰：「闓，明」\n[郭注-2]：發，發行也。詩曰：「齊子愷悌。」', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '3094/6979;8504', '7757/8365', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4590, '髦士;官', '覒士/𢤧士', '髦士[覒士/𢤧士]1，官也。23', '[釋文-1]：音毛，毛中之長豪曰髦，士之俊傑者，借譬爲名\n[郭注-2]：取俊士，令居官。\n[釋文-3]：令，力呈反。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '5736/226;9618', '5517/226/0/6717/226', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4591, '畯', '', '畯12，農夫也。34', '[音釋-1]：俊。\n[釋文-2]：子峻反，主田大夫、嗇夫也。補孫炎曰：「田官也」\n[郭注-3]：今之嗇夫是也。\n[釋文-4]：嗇，音色。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '9194', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4592, '蓋;割;裂', '害', '蓋[害]1、割，裂也2。3', '[釋文-1]：古害反，舍人本作害\n[釋文-2]：音列\n[郭注-3]：蓋，未詳。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '586;2778;5340', '4616', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4593, '邕;支;載', '', '邕1、支，載也。2', '[釋文-1]：字又作擁\n[郭注-2]：皆方俗語，亦未詳。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '7489;1935;9571', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4594, '諈諉;累', '絫', '諈諉12，累[絫]也34。56', '[音釋-1]：竹睡。女睡。\n[釋文-2]：謝之睡反，郭置睡反。郭女睡反，顧汝恚反，謝音𣨙，孫云：「楚人曰諈，秦人曰諉」\n[音釋-3]：劣僞。\n[釋文-4]：本又作纍，劣僞反，字又作絫，同，注同\n[郭注-5]：以事相屬，累爲諈諉。\n[釋文-6]：屬，之欲反。', '釋言弟二', '爾雅', '2', DEFAULT, DEFAULT, '1551/1552;8686', '9718', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4595, '漠;察;淸', '殺', '漠1、察[殺]，淸也。2', '[釋文-1]：音莫，樊光云：「漠然，淸貌」\n[郭注-2]：皆淸明。', '釋言弟二', '爾雅', '2', DEFAULT, DEFAULT, '7132;4583;7202', '1979', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4596, '庇;庥;蔭', '', '庇1、庥2，廕{蔭}也3。4', '[釋文-1]：必寐反，又音祕。補舍人曰：「蔽也」\n[釋文-2]：字又作休，虛求反，郭許虯反。補舍人曰：「依止也」\n[釋文-3]：字亦作蔭，於鴆反\n[郭注-4]：今俗語呼樹蔭爲庥。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '5957;3818;524', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4597, '穀;履;禄;履;禮', '', '穀、履，禄也。1履，禮也。2', '[郭注-1]：書曰：「旣富方穀。」詩曰：「福履將之。」\n[郭注-2]：禮可以履行，見易。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '4485;5432;15;5432;12', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4598, '隱;占', '𨽌', '隱[𨽌]，占也。12', '[郭注-1]：隱度。\n[釋文-2]：度，大各反。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '9669;2079', '3753', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4599, '逆;迎', '', '逆，迎也。', '', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '1124;1125', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4600, '㦧;曾', '朁', '㦧[朁]12，曾也。3', '[音釋-1]：慘。\n[釋文-2]：七感反\n[郭注-3]：發語辭，見詩。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '6901;710', '3049', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4601, '增;益', '', '增，益也。1', '[郭注-1]：今江東通言增。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '9092;3155', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4602, '寠;貧', '婁', '窶{寠}[婁]12，貧也。3', '[音釋-1]：求矩。\n[釋文-2]：求矩反，毛詩傳云：「窶者無禮」\n[郭注-3]：謂貧陋。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '4613;3986', '8323', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4603, '𥴨;隱;僾;悒', '旡', '薆{𥴨}1，隱也。2僾[旡]34，唈{悒}也56。7', '[音釋-1]：愛\n[郭注-2]：謂隱蔽。\n[音釋-3]：愛。\n[釋文-4]：補孫炎曰：「心唈也」\n[音釋-5]：烏合。\n[釋文-6]：薆、僾，皆音愛，烏槩反。烏合反\n[郭注-7]：嗚唈，短氣。皆見詩。', '釋言弟二', '爾雅', '3', DEFAULT, DEFAULT, '3006;9669;5019;6823', '5597', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4604, '基;經;基;設', '', '基，經也。1基，設也。2', '[郭注-1]：基業，所以自經營。\n[郭注-2]：亦爲造設。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '9046;8532;9046;1561', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4605, '祺;祥;祺;吉', '', '祺1，祥也。2祺，吉也。34', '[釋文-1]：音其，下同\n[郭注-2]：謂徵祥。\n[郭注-3]：祥，吉之先見。\n[釋文-4]：見，賢遍反。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '22;18;22;876', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4606, '兆;域', '垗', '兆[垗]1，域也。23', '[釋文-1]：廣雅云：「葬地也。」本又作垗，音同\n[郭注-2]：謂塋界。\n[釋文-3]：塋，音營。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '2081;8387', '9145', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4607, '肈;敏', '劭;誨', '肈[劭]，敏[誨]也。1', '[郭注-1]：書曰：「肈牽車牛。」', '釋言弟二', '爾雅', '1;2', DEFAULT, DEFAULT, '8376;1999', '9223;1496', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4608, '挾;藏', '', '挾12，藏也。3', '[音釋-1]：叶。\n[釋文-2]：戸牒反\n[郭注-3]：今江東通言挾。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '7871;695', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4609, '浹;徹', '', '浹12，徹也3。45', '[音釋-1]：接。\n[釋文-2]：子協反，郭音接\n[釋文-3]：直列反\n[郭注-4]：謂霑徹。\n[釋文-5]：霑，竹簾反。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '7470;1997', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4610, '𤾕;廢;替;滅', '', '𤾕1，廢也。替，滅也。2', '[釋文-1]：補李巡曰：「去之廢也」\n[郭注-2]：亦爲滅絶。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '6699;5963;6699;7443', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4611, '速;徵;徵;召', '誎', '速[誎]，徵也。徵，召也。1', '[郭注-1]：易曰：「不速之客。」', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '1121;5241;5241;842', '1534', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4612, '琛;寶', '', '琛12，寶也。3', '[音釋-1]：勑金。\n[釋文-2]：勑金反，郭舒金反。補舍人曰：「美寶」\n[郭注-3]：詩曰：「來獻其琛。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '210;4592', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4613, '探;試', '', '探1，試也。23', '[釋文-1]：吐南反\n[郭注-2]：刺探甞試。\n[釋文-3]：刺，七赤反。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '8003;1540', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4614, '髦;選;髦;俊', '覒;𢤧', '髦[覒]，選也1。2髦[𢤧]，俊也。3', '[釋文-1]：宣戀反\n[郭注-2]：俊士之選。\n[郭注-3]：士中之俊，如毛中之髦。', '釋言弟二', '爾雅', '1;3', DEFAULT, DEFAULT, '5736;1143;5736;4962', '5517;6717', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4615, '俾;職', '', '俾1，職也。2', '[釋文-1]：必爾反，沈方寐反\n[郭注-2]：使供職。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '5098;7807', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4616, '紕;飾', '俾', '紕[俾]12，飾也3。45', '[音釋-1]：備。\n[釋文-2]：婢寐反，謝房彌反，郭方寐反\n[釋文-3]：申職反\n[郭注-4]：謂緣飾，見詩。\n[釋文-5]：緣，悅面反。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '8761;4896', '5098', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4617, '淩;栗;栗;慼', '凌', '淩[凌]12，慄{栗}也3。45慄{栗}，慼也6。7', '[音釋-1]：凌。\n[釋文-2]：力升反，案郭注意當作陵，埤蒼云：「淩，慄也。」樊注作凌，「冰澟也。」力膺反\n[釋文-3]：音栗\n[郭注-4]：淩懅戰慄。\n[釋文-5]：懅，舊音遽，其據反，亦謂戰慄也。\n[釋文-6]：字又作戚，七歷反\n[郭注-7]：戰慄者，憂慼。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '7068;4393;4393;6936', '7513', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4618, '蠲;明;茅;明;明;朗', '炯;萌', '蠲[炯]1，明也。2茅3，明[萌]也。4明，朗也5。', '[釋文-1]：古玄反，又音圭。補樊光曰：「蠲除垢穢，使令淸明」\n[郭注-2]：蠲，淸明貌。\n[釋文-3]：補舍人曰：「昧之明也」\n[郭注-4]：左傳曰：「前茅慮無。」\n[釋文-5]：補樊光曰：「詩云『高朗令終』，日月光明」', '釋言弟二', '爾雅', '1;4', DEFAULT, DEFAULT, '8829;4323;326;4323;4323;4279', '6505;491', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4619, '猷;圖;猷;若', '', '猷，圖也。1猷，若也。2', '[郭注-1]：周官曰：「以猷鬼神祇。」謂圖畫。\n[郭注-2]：詩曰：「寔命不猷。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '6375;3915;6375;599', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4620, '偁;舉;稱;好', '', '偁，舉也。1稱2，好也3。4', '[郭注-1]：書曰：「偁爾戈。」\n[釋文-2]：尺證反\n[釋文-3]：呼報反，又如字，注同\n[郭注-4]：物稱人意亦爲好。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '5067;7959;4495;8191', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4621, '坎;律;銓', '勘', '坎[勘]1、律，銓也2。3', '[釋文-1]：字又作欿，苦感反\n[釋文-2]：七全反，銓卽稱\n[郭注-3]：易坎卦主法。法、律，皆所以銓量輕重。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '9086;1257;9373', '9254', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4622, '矢;誓', '', '矢，誓也。1', '[郭注-1]：相約誓。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '3309;1530', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4623, '舫;舟', '方', '舫[方]1，舟也。23', '[釋文-1]：方訪反，又音方\n[郭注-2]：並兩船。\n[釋文-3]：併，步頂反，字又作並。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '5447;5438', '5455', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4624, '泳;游', '', '泳1，游也。23', '[釋文-1]：音詠\n[郭注-2]：濳行游水底。\n[釋文-3]：底，丁禮反。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '7289;4306', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4625, '𨽿;及', '', '迨{𨽿}1，及也。2', '[釋文-1]：音待\n[郭注-2]：東齊曰迨。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '1947;1918', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4626, '冥;幼', '窈', '冥1，幼[窈]也。2', '[釋文-1]：覓經反，字林亡經、亡定二反。補孫炎曰：「深闇之窈也」\n[郭注-2]：幼稚者，冥昧。', '釋言弟二', '爾雅', '2', DEFAULT, DEFAULT, '4313;2499', '4678', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4627, '降;下', '', '降1，下也2。', '[釋文-1]：古巷反，一讀戸江反\n[釋文-2]：如字，一讀戸嫁反', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '9649;9', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4628, '傭;均', '', '傭12，均也3。4', '[音釋-1]：勑容。\n[釋文-2]：勑恭反\n[釋文-3]：音鈞\n[郭注-4]：齊等。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '5018;9030', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4629, '強;暴', '彊;虣', '強[彊]，暴[虣]也1。2', '[釋文-1]：字又作虣，同，蒲報反\n[郭注-2]：強梁，淩暴。', '釋言弟二', '爾雅', '1;2', DEFAULT, DEFAULT, '8826;4248', '8491;3133', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4630, '窕;肆;肆;力', '', '窕1，肆也。23肆，力也。4', '[釋文-1]：吐彫反\n[郭注-2]：輕窕者，好放肆。\n[釋文-3]：好，呼報反。\n[郭注-4]：肆，極力。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '4671;6070;6070;9212', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4631, '俅;戴', '', '俅1，戴也。23', '[釋文-1]：音求\n[郭注-2]：詩曰：「戴弁俅俅。」\n[釋文-3]：弁，音卞。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '4959;1779', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4632, '瘞;幽', '', '瘞1，幽也。23', '[釋文-1]：於例反，又於計反，或作𨻊，同\n[郭注-2]：幽亦薶也。\n[釋文-3]：薶，莫皆反。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '9139;2502', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4633, '氂;罽', '毳', '氂[毳]12，罽也34。5', '[音釋-1]：離。\n[釋文-2]：力知反，又力才反，李本作毳，昌銳反。補舍人曰：「所謂毛罽也，胡人續羊毛而作衣」\n[音釋-3]：計。\n[釋文-4]：居例反\n[郭注-5]：毛氂，所以爲罽。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '776;4825', '5400', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4634, '烘;燎', '', '烘1，燎也23。4', '[釋文-1]：沈、顧火公反，郭巨凶反，孫音恭，字林巨凶、甘凶二反。補舍人曰：「以火燎也」\n[音釋-2]：料。\n[釋文-3]：力皎反，又力召、力弔二反\n[郭注-4]：謂燒燎。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '6458;6485', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4635, '煁;烓', '', '煁12，烓也34。56', '[音釋-1]：市針。\n[釋文-2]：市針反。補舍人曰：「烓竈也」\n[音釋-3]：頃。\n[釋文-4]：郭音恚，字林口穎反，說文云：「行竈也。」顧口丼、烏攜二反\n[郭注-5]：今之三隅竈，見詩。\n[釋文-6]：隅，音虞。竈，則到反。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '6455;6454', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4636, '陪;朝', '', '陪1，朝也2。3', '[釋文-1]：蒲回反\n[釋文-2]：直遥反，注同\n[郭注-3]：陪位爲朝。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '9699;4289', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4637, '康;苛', '庚', '康[庚]，苛也12。3', '[音釋-1]：何。\n[釋文-2]：音何\n[郭注-3]：謂苛刻。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '4459;543', '9755', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4638, '樊;藩', '棥', '樊[棥]1，藩也2。34', '[釋文-1]：音煩。補孫炎曰：「圃之藩也」\n[釋文-2]：方元反\n[郭注-3]：謂藩籬。\n[釋文-4]：籬，力支反。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '1774;590', '2088', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4639, '賦;量', '', '賦，量也1。23', '[釋文-1]：音良，一音亮\n[郭注-2]：賦稅，所以評量。\n[釋文-3]：評，音病。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '3983;5245', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4640, '粻;糧', '', '粻1，糧也2。3', '[釋文-1]：音張，字林又丈庚反\n[釋文-2]：音良\n[郭注-3]：今江東通言粻。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '4384;4371', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4641, '庶;侈;庶;幸', '', '庶1，侈也2。3庶，幸也。45', '[釋文-1]：補孫炎曰：「豐多也」\n[釋文-2]：昌氏反，又尸氏反\n[郭注-3]：庶者衆多爲奢侈。\n[郭注-4]：庶幾，僥倖。\n[釋文-5]：幾，音機。僥，古堯反。倖，胡耿反，與幸通用。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '5958;5133;5958;6625', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4642, '筑;拾', '叔', '筑[叔]12，拾也。34', '[音釋-1]：竹。\n[釋文-2]：音竹\n[郭注-3]：謂拾掇。\n[釋文-4]：掇，丁活反，說文云：「拾取。」', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '2998;7988', '1924', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4643, '奘;駔', '', '奘12，駔也34。56', '[音釋-1]：徂朗。\n[釋文-2]：徂朗反，秦 晉謂大爲奘\n[音釋-3]：粗。\n[釋文-4]：在魯反，又子朗反，沈集注本作𤖛，音同，孫、樊二本並作將且，而無奘、駔，沈集衆本合爲一字\n[郭注-5]：今江東呼大爲駔，駔猶麤也。\n[釋文-6]：𧦝，火乎反，或作呼，同。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '6670;6235', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4644, '集;會', '', '集，會也。', '', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '2368;3276', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4645, '舫;泭', '', '舫1，泭也23。45', '[釋文-1]：謝音方，詩亦作方，施甫訪反，樊本作坊，符方反，又音方\n[音釋-2]：孚。\n[釋文-3]：郭音孚，字或作𣻜，同。樊本作柎，沈音附\n[郭注-4]：水中𥴖筏。\n[釋文-5]：𥴖，皮佳反，本又作𥱼，同。筏，音伐，方言云：「泭謂之𥴖，𥴖謂之筏，秦 晉通語。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '5447;7284', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4646, '洵;均;洵;龕', '旬;咸', '洵[旬]1，均也。2洵，龕[咸]也3。4', '[釋文-1]：郭音巡，注同，謝音荀，下句放此。補李巡曰：「徧之均也」\n[郭注-2]：謂調均。\n[釋文-3]：苦南反，字或作含，本今作龕\n[郭注-4]：未詳。', '釋言弟二', '爾雅', '1;4', DEFAULT, DEFAULT, '7113;9030;7113;7691', '5808;872', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4647, '逮;遝', '眔', '逮1，遝[眔]也23。45', '[釋文-1]：音代，一音大計反，下同\n[音釋-2]：沓。\n[釋文-3]：孫、郭徒荅反\n[郭注-4]：今荆楚人皆云遝，音沓。\n[釋文-5]：沓，與上同，亦徒荅反。', '釋言弟二', '爾雅', '2', DEFAULT, DEFAULT, '1147;1116', '2142', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4648, '是;則', '徥', '是[徥]，則也。1', '[郭注-1]：是事可法則。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '1089;2756', '1237', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4649, '畫;形', '', '畫12，形也。3', '[音釋-1]：𦘕。\n[釋文-2]：胡卦反\n[郭注-3]：畫者爲形象。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '1944;5714', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4650, '賑;富', '', '賑12，富也。3', '[音釋-1]：之忍。\n[釋文-2]：之忍反，又之人、之刃二反，字林云：「富也，丑引反」\n[郭注-3]：謂隱賑富有。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '3944;4586', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4651, '局;分', '', '局1，分也23。4', '[釋文-1]：彊六反\n[音釋-2]：符問。\n[釋文-3]：符問反，注同\n[郭注-4]：謂分部。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '958;708', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4652, '齌;怒', '', '懠{齌}12，怒也。3', '[音釋-1]：才細。\n[釋文-2]：才計反。補舍人曰：「怒聲也」\n[郭注-3]：詩曰：「天之方懠。」音薺。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '6459;6879', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4653, '僁;聲', '', '僁12，聲也。3', '[音釋-1]：屑。\n[釋文-2]：音屑，動草聲也，郭音與稷、契同，施私秩反，字又作𠋱\n[郭注-3]：謂聲音。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '5022;7810', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4654, '葵;揆;揆;度', '', '葵1，揆也2。3揆，度也45。6', '[釋文-1]：求維反\n[釋文-2]：其水反\n[郭注-3]：詩曰：「天子葵之。」\n[音釋-4]：徒各。\n[釋文-5]：徒各反，注同\n[郭注-6]：商度。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '260;7975;7975;1929', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4655, '逮;及', '', '逮，及也。', '', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '1147;1918', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4656, '惄;飢', '', '惄1，飢也。2', '[釋文-1]：本又作（⿱𠦑心），同，奴歷反。補李巡曰：「宿不食之飢也」\n[郭注-2]：惄然，飢意。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '6805;3261', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4657, '眕;重', '', '眕12，重也。3', '[音釋-1]：之忍。\n[釋文-2]：之忍反\n[郭注-3]：謂厚重，見左傳。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '2138;5244', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4658, '獵;虐', '邋', '獵[邋]，虐也。1', '[郭注-1]：淩獵，暴虐。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '6360;3115', '1181', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4659, '土;田', '', '土，田也。12', '[郭注-1]：別二名。\n[釋文-2]：別，彼列反。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '9021;9172', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4660, '戍;遏', '', '戍1，遏也。23', '[釋文-1]：式喻反\n[郭注-2]：戍守，所以止寇賊。\n[釋文-3]：寇，苦候反。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '8383;1185', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4661, '師;人', '', '師，人也。1', '[郭注-1]：謂人衆。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '3864;4950', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4662, '硈;鞏', '', '硈12，鞏也3。4', '[音釋-1]：苦角。\n[釋文-2]：苦角反\n[釋文-3]：九勇反\n[郭注-4]：硈然，堅固。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '6031;1801', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4663, '棄;忘', '', '棄1，忘也2。', '[釋文-1]：丘異反\n[釋文-2]：音亡', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '2494;6846', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4664, '嚻;閑', '悠;閒', '嚻[悠]12，閑[閒]也。3', '[音釋-1]：五刀。\n[釋文-2]：五刀反，又許嬌反\n[郭注-3]：嚻然，閑暇貌。', '釋言弟二', '爾雅', '1;2', DEFAULT, DEFAULT, '1437;7769', '6929;7762', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4665, '謀;心', '', '謀，心也。1', '[郭注-1]：謀慮以心。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '1507;6705', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4666, '獻;聖', '虔', '獻[虔]，聖也。1', '[郭注-1]：謚法曰：「聰明睿智曰獻。」', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '6366;7803', '3112', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4667, '里;邑', '', '里1，邑也。2', '[釋文-1]：補李巡曰：「居之邑也」\n[郭注-2]：謂邑居。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '9169;4004', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4668, '襄;除', '', '襄1，除也。2', '[釋文-1]：四羊反，或而羊反\n[郭注-2]：詩曰：「不可襄也。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '5324;9692', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4669, '振;古', '陳', '振[陳]，古也。1', '[郭注-1]：詩曰：「振古如兹。」猶云久若此。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '7963;1461', '9688', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4670, '懟;怨', '', '懟12，怨也。', '[音釋-1]：隊。\n[釋文-2]：直類反', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '6888;6878', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4671, '縭;介', '', '縭12，介也3。45', '[音釋-1]：離。\n[釋文-2]：力知反\n[釋文-3]：音界，李、孫、顧、舍人本並云：「縭，羅也。介，別也」\n[郭注-4]：縭者，繫。介猶閡。\n[釋文-5]：閡，五代反。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '8687;714', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4672, '號;謼', '', '號12，謼也34。5', '[音釋-1]：毫。\n[釋文-2]：戸羔反，又胡到反\n[音釋-3]：火故。\n[釋文-4]：火故反，又如字，又作呼\n[郭注-5]：今江東皆言謼。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '3069;1576', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4673, '凶;咎', '', '凶，咎也1。', '[釋文-1]：求九反', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '4529;5175', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4674, '苞;稹', '', '苞1，稹也2。34', '[釋文-1]：補茅反\n[釋文-2]：謝之忍反，郭振、真二音\n[郭注-3]：今人呼物叢緻者爲稹。\n[釋文-4]：緻，直吏反。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '388;4425', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4675, '吾;寤', '午', '逜{吾}[午]12，寤也3。4', '[音釋-1]：悟。\n[釋文-2]：五故反，孫本吾字作午，吾補反\n[釋文-3]：五故反\n[郭注-4]：相干寤。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '837;4688', '9799', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4676, '定;題', '頂', '顁{定}[頂]12，題也3。45', '[音釋-1]：丁佞。\n[釋文-2]：丁佞反，字又作定，注同\n[釋文-3]：徒兮反\n[郭注-4]：題，額也。詩曰：「麟之定。」\n[釋文-5]：麟，力仁反。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '4576;5611', '5609', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4677, '猷;肎;可', '', '猷、肎1，可也。2', '[釋文-1]：肯，苦等反，或作古𦚋字，字林作𦚋，並同\n[郭注-2]：詩曰：「猷來無棄。」肎，今通言。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '6375;2734;3059', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4678, '務;侮', '', '務，侮也1。2', '[釋文-1]：亡甫反\n[郭注-2]：詩曰：「外禦其侮。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '9219;5150', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4679, '貽;遺', '', '貽1，遺也23。45', '[釋文-1]：以之反\n[音釋-2]：唯季。\n[釋文-3]：唯季反，注同\n[郭注-4]：相歸遺。\n[釋文-5]：歸，巨位反。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '3999;1174', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4680, '貿;買', '', '貿1，買也。2', '[釋文-1]：音茂\n[郭注-2]：廣二名。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '3973;3980', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4681, '賄;財', '', '賄1，財也。', '[釋文-1]：火罪反', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '3938;3939', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4682, '甲;狎', '', '甲，狎也1。2', '[釋文-1]：乎甲反\n[郭注-2]：謂習狎。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '9741;6340', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4683, '菼;騅;菼;薍', '', '菼12，騅也3。菼，薍也45。67', '[音釋-1]：他敢。\n[釋文-2]：字又作𦵹，他敢反，或待感反\n[釋文-3]：章誰反，如騅馬色也\n[音釋-4]：五患。\n[釋文-5]：五患反\n[郭注-6]：詩曰：「毳衣如菼。」菼，草色如騅，在靑白之閒。\n[釋文-7]：毳，昌銳反。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '422;6149;422;421', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4684, '粲;餐', '', '粲1，餐也23。4', '[釋文-1]：七旦反\n[音釋-2]：孫。\n[釋文-3]：飧，謝素昆反，說文云：「餔也。」字林云：「水澆飯也。」本又作䬸，施七丹反，字林作飧，云：「吞食」\n[郭注-4]：今河北人呼食爲餐。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '4351;3229', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4685, '渝;變', '', '渝1，變也。2', '[釋文-1]：音榆，舍人作䞕，同\n[郭注-2]：謂變易。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '7441;2020', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4686, '宜;肴', '', '宜1，肴也2。3', '[釋文-1]：補李巡曰：「飲酒之肴也」\n[釋文-2]：音爻\n[郭注-3]：詩曰：「與子宜之。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '4599;2674', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4687, '夷;說', '咦', '夷[咦]1，悅{說}也。2', '[釋文-1]：恞，本又作夷，音同\n[郭注-2]：詩曰：「我心則夷。」', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '6615;1545', '823', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4688, '顚;頂', '', '顚1，頂也。2', '[釋文-1]：丁田反\n[郭注-2]：頭上。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '5608;5609', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4689, '耋;老', '', '耋12，老也。3', '[音釋-1]：迭。\n[釋文-2]：田結反，孫他結反，云：「老人面如鐵色。」補舍人曰：「年六十稱也」\n[郭注-3]：八十爲耋。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '5378;5377', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4690, '輶;輕', '', '輶12，輕也。3', '[音釋-1]：酋。\n[釋文-2]：由久反，又餘周反\n[郭注-3]：詩曰：「德輶如毛。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '9522;9521', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4691, '俴;淺', '', '俴12，淺也。3', '[音釋-1]：踐。\n[釋文-2]：慈瞚反\n[郭注-3]：詩曰：「小戎俴收。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '5125;7228', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4692, '匋;絞', '絛;筊', '綯{匋}[絛]12，絞[筊]也3。45', '[音釋-1]：陶。\n[釋文-2]：徒刀反。補李巡曰：「繩之絞也」\n[釋文-3]：古卯反\n[郭注-4]：糾絞繩索。\n[釋文-5]：糾，吉黝反。索，悉各反。', '釋言弟二', '爾雅', '1;2', DEFAULT, DEFAULT, '3289;6629', '8666;2953', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4693, '吪;化', '', '訛{吪}，化也。1', '[郭注-1]：詩曰：「四國是訛。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '930;5216', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4694, '跋;獵', '', '跋1，躐{獵}也23。45', '[釋文-1]：蒲末反，郭音貝，又補葛反。補李巡曰：「前行」\n[音釋-2]：獵。\n[釋文-3]：力輒反\n[郭注-4]：詩曰：「狼跋其胡。」\n[釋文-5]：狼，音郎。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '1383;6360', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4695, '疐;跲', '', '疐12，跲也34。5', '[音釋-1]：致。\n[釋文-2]：竹利反，又得異反，又竹季反，說文云：「礙足不行。」與躓同。補李巡曰：「卻頓」\n[音釋-3]：其葉。\n[釋文-4]：其業反，又居業反，郭又音甲，廣雅云：「跲，代也」\n[郭注-5]：詩曰：「載疐其尾。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '2506;1380', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4696, '烝;塵', '', '烝1，塵也。23', '[釋文-1]：之仍反。補孫炎曰：「物久之塵」\n[郭注-2]：人衆，所以生塵埃。\n[釋文-3]：埃，音哀。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '6431;6290', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4697, '戎;相', '胥/仍', '戎[胥/仍]1，相也2。3', '[釋文-1]：如字，本或作𢫨，顧如勇反，沈如升反\n[釋文-2]：如字，又息亮反\n[郭注-3]：相佐助。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '8379;2162', '2699/0/5049', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4698, '𩜈;私', '醧;厶', '飫{𩜈}[醧]1，私[厶]也。2', '[釋文-1]：於庶反。補孫炎曰：「非公朝，私飲酒也」\n[郭注-2]：宴飲之私。', '釋言弟二', '爾雅', '1;2', DEFAULT, DEFAULT, '3243;4431', '9846;5845', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4699, '孺;屬', '', '孺1，屬也2。3', '[釋文-1]：𡦗，如戍反，本今作孺。補李巡曰：「骨內相親屬也」\n[釋文-2]：雛欲反\n[郭注-3]：謂親屬。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '9772;5429', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4700, '幕;莫', '', '幕1，暮{莫}也。2', '[釋文-1]：音莫\n[郭注-2]：幕然，暮夜。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '4883;701', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4701, '煽;熾;熾;盛', '', '煽1，熾也2。熾，盛也3。4', '[釋文-1]：音扇\n[釋文-2]：昌至反\n[釋文-3]：晠，市正反，本作盛，同\n[郭注-4]：互相訓。煽，義見詩。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '6529;6513;6513;3141', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4702, '柢;本', '', '柢12，本也。3', '[音釋-1]：帝。\n[釋文-2]：謝音帝\n[郭注-3]：謂根本。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '3562;3561', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4703, '窕;閒', '', '窕1，閒也23。45', '[釋文-1]：郭徒了反，舍人本作跳，云：「跳者，躍之閒」\n[音釋-2]：閑。\n[釋文-3]：音閑，或如字\n[郭注-4]：窈窕，閒隙。\n[釋文-5]：窈，音杳。𨻶，去逆反。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '4671;7762', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4704, '淪;率', '', '淪，率也。1', '[郭注-1]：相率使。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '7178;8785', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4705, '罹;毒', '', '罹1，毒也。23', '[釋文-1]：力知反\n[郭注-2]：憂思慘毒。\n[釋文-3]：思，息吏反。慘，七感反。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '4851;236', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4706, '檢;同', '僉', '檢[僉]1，同也。23', '[釋文-1]：李、郭居儉反\n[郭注-2]：模範同等。\n[釋文-3]：模，亡胡反。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '3771;4803', '3272', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4707, '郵;過', '', '郵1，過也2。3', '[釋文-1]：音尤。補舍人曰：「尤，怨人也」\n[釋文-2]：古臥反\n[郭注-3]：道路所經過。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '4014;1110', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4708, '遜;遯', '遁', '遜，遯[遁]也1。2', '[釋文-1]：字又作遁，又作𨔡，同，徒頓反\n[郭注-2]：謂逃去。', '釋言弟二', '爾雅', '2', DEFAULT, DEFAULT, '1140;1172', '1139', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4709, '斃;踣;僨;僵', '', '斃12，踣也34。56僨78，僵也910。11', '[音釋-1]：弊。\n[釋文-2]：獘，字亦作弊，又作斃，婢世反，又婢設反，郭步計反\n[音釋-3]：蒲北。\n[釋文-4]：蒲北反，又音赴，或孚豆、蒲侯二反。補孫炎作仆\n[郭注-5]：前覆。\n[釋文-6]：覆，芳服反。\n[音釋-7]：糞。\n[釋文-8]：甫問反\n[音釋-9]：姜。\n[釋文-10]：居良反。補舍人曰：「皆踣意也」\n[郭注-11]：卻偃。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '6365;1392;5154;5155', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4710, '畛;殄', '', '畛1，殄也2。3', '[釋文-1]：之引反，又之人反\n[釋文-2]：大典反\n[郭注-3]：謂殄絶。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '9190;2552', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4711, '曷;盍', '', '曷，盍也1。2', '[釋文-1]：戸臘反\n[郭注-2]：盍，何不。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '3047;3181', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4712, '虹;潰', '訌;䜋', '虹[訌]1，潰[䜋]也23。4', '[釋文-1]：音洪，顧作訌，音同。李本作降，下江反\n[音釋-2]：會。\n[釋文-3]：乎內反\n[郭注-4]：謂潰敗。', '釋言弟二', '爾雅', '1;2', DEFAULT, DEFAULT, '8935;7226', '1648;1649', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4713, '暗;闇', '晻', '隌{暗}[晻]12，闇也3。456', '[音釋-1]：唵。\n[釋文-2]：字林或作晻，同，烏感反\n[釋文-3]：音暗\n[郭注-4]：隌然，冥貌。\n[音釋-5]：冥，莫定。\n[釋文-6]：冥，莫定反。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '4226;7772', '4225', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4714, '䵑;膠', '', '䵑12，膠也3。45', '[音釋-1]：女乙。\n[釋文-2]：字又作䵒，同，女乙反，郭音馹\n[釋文-3]：音交\n[郭注-4]：膠，黏䵑。\n[釋文-5]：黏，女廉反，字林云：「相著也。」字書云：「糊也。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '4514;2729', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4715, '孔;甚', '', '孔，甚也。', '', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '7704;3042', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4716, '厥;其', '𠄌', '厥[𠄌]，其也。', '', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '5986;3019', '8406', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4717, '戛;禮', '楷', '戛[楷]1，禮也。2', '[釋文-1]：居八反\n[郭注-2]：謂常禮。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '8378;12', '3429', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4718, '闍;臺', '', '闍12，臺也。3', '[音釋-1]：都。\n[釋文-2]：丁胡反。補孫炎曰：「積土如水渚，所以望氣祥也」\n[郭注-3]：城門臺。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '7745;7712', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4719, '囚;拘', '', '囚，拘也1。2', '[釋文-1]：音俱\n[郭注-2]：謂拘執。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '3928;1458', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4720, '攸;所', '𠧴;疋', '攸[𠧴]，所[疋]也。', '', '釋言弟二', '爾雅', '1;2', DEFAULT, DEFAULT, '2034;9484', '3054;1421', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4721, '展;適', '', '展，適也。1', '[郭注-1]：得自申展，皆適意。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '5407;1109', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4722, '鬱;氣', '气', '鬱1，氣[气]也。2', '[釋文-1]：補李巡曰：「盛氣也」\n[郭注-2]：鬱然，氣出。', '釋言弟二', '爾雅', '2', DEFAULT, DEFAULT, '3850;4376', '224', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4723, '宅;居', '', '宅，居也。', '', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '4558;5404', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4724, '休;慶', '好;祥', '休[好]1，慶[祥]也。', '[釋文-1]：虛虯反，下注同', '釋言弟二', '爾雅', '1;2', DEFAULT, DEFAULT, '3818;6757', '8191;18', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4725, '祈;叫', '', '祈1，叫也2。34', '[釋文-1]：補孫炎曰：「祈，爲民祈福。叫，告之辭也」\n[釋文-2]：古弔反\n[郭注-3]：祈，祭者叫呼而請事。\n[釋文-4]：呼，火故反。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '50;924', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4726, '濬;幽;深', '', '濬1、幽，深也。2', '[釋文-1]：音峻。補舍人曰：「下之深也」\n[郭注-2]：濬亦深也。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '7509;2502;7036', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4727, '哲;智', '', '哲1，智也。', '[釋文-1]：補舍人曰：「大知也」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '838;2231', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4728, '弄;玩', '', '弄，玩也。', '', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '1763;149', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4729, '尹;正;皇;匡;正', '', '尹，正也。1皇、匡，正也。2', '[郭注-1]：謂官正也。\n[郭注-2]：詩曰：「四國是皇。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '1914;1087;80;8428;1087', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4730, '服;整', '𠬝', '服[𠬝]，整也1。23', '[釋文-1]：之領反\n[郭注-2]：服御之，令齊整。\n[釋文-3]：令，力呈反。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '5449;2003', '1921', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4731, '聘;問', '', '聘，問也。1', '[郭注-1]：見穀梁傳。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '7812;843', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4732, '愧;慙', '', '愧1，慙也。', '[釋文-1]：九位反，本亦作媿，小爾雅云：「不直失節謂之慙。慙，愧也。面慙曰戁，心慙曰恧，體慙曰逡。」方言云：「挴、㥾、𧹞，愧也。晉曰挴，或曰㥾。秦 晉之閒凡愧而見上謂之𧹞，梁、宋曰㥾。」又云：「㥏、恧，慙也。荆 揚 靑 徐之閒曰㥏，若梁 益 秦 晉之閒言心內慙矣，山之東、西自愧曰恧，趙 魏之閒謂之䀣。」㥾音匿，㥏音腆，恧，女六反，䀣音密，亦音祕', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '8346;6959', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4733, '殛;誅', '', '殛12，誅也。34', '[音釋-1]：紀力。\n[釋文-2]：紀力反\n[郭注-3]：書曰：「鯀則殛死。」\n[釋文-4]：鯀，古本反。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '2540;1711', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4734, '克;能', '', '克，能也。', '', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '4414;6413', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4735, '昱;明', '', '翌{昱}，明也。12', '[郭注-1]：書曰：「翌日乃瘳。」\n[釋文-2]：瘳，勑留反。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '4242;4323', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4736, '訩;訟', '', '訩，訟也。12', '[郭注-1]：言訩譊。\n[釋文-2]：訩，音凶，許容反，說文作詾，同。譊，卽讙字。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '1677;1678', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4737, '晦;冥', '', '晦，冥也12。', '[音釋-1]：亡定。\n[釋文-2]：亡定反', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '4227;4313', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4738, '奔;走', '', '奔，走也。', '', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '6626;979', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4739, '逡;退', '竣', '逡[竣]1，退也。23', '[釋文-1]：七旬反\n[郭注-2]：外傳曰：「巳復於事而逡。」\n[釋文-3]：傳，直戀反，下「左傳」皆同。已，韋昭云：「已，畢也。」復，芳福反。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '1161;1249', '6693', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4740, '疐;仆', '躓', '疐[躓]1，仆也23。45', '[釋文-1]：都麗反，一音致\n[音釋-2]：赴。\n[釋文-3]：音赴\n[郭注-4]：頓躓，倒仆。\n[釋文-5]：躓，音致。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '2506;5157', '1379', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4741, '亞;次', '', '亞，次也。', '', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '9725;5586', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4742, '諗;念', '', '諗12，念也。3', '[音釋-1]：審。\n[釋文-2]：音審，相思念也，說文式荏反\n[郭注-3]：相思念。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '1538;6721', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4743, '届;極', '', '届1，極也。2', '[釋文-1]：音戒\n[郭注-2]：有所限極。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '5408;3626', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4744, '奄;同;弇;蓋', '弇', '奄[弇]，同也。1弇23，蓋也。45', '[郭注-1]：詩曰：「奄有龜蒙。」\n[音釋-2]：掩。\n[釋文-3]：於𥳑反，音揜\n[郭注-4]：謂覆蓋。\n[釋文-5]：覆，敷救反。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '6601;4803;1759;586', '1759', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4745, '恫;痛', '', '恫1，痛也。2', '[釋文-1]：音通\n[郭注-2]：詩曰：「神罔時恫。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '6904;4699', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4746, '握;具', '楃', '握[楃]1，具也。2', '[釋文-1]：李本作幄，云：「居位處之具也。」郭云：「謂備具」\n[郭注-2]：謂備具。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '7875;1771', '3674', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4747, '振;訊', '迅', '振，訊[迅]也1。2', '[釋文-1]：音信\n[郭注-2]：振者，奮迅。', '釋言弟二', '爾雅', '2', DEFAULT, DEFAULT, '7963;1518', '1122', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4748, '鬩;恨', '', '鬩12，恨也3。4', '[音釋-1]：呼歷。\n[釋文-2]：呼歷反\n[釋文-3]：孫炎作很，云：「相很戾也。」很，音戸墾反\n[郭注-4]：相怨恨。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '1900;6887', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4749, '越;揚', '粵', '越[粵]，揚也。1', '[郭注-1]：謂發揚。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '990;7958', '3072', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4750, '對;遂', '㒸', '對，遂[㒸]也。1', '[郭注-1]：詩曰：「對揚王休。」', '釋言弟二', '爾雅', '2', DEFAULT, DEFAULT, '1751;1175', '712', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4751, '燬;火', '', '燬12，火也。3', '[音釋-1]：毀。\n[釋文-2]：音毀，李巡云：「燬，一音火。」孫炎云：「方言有輕重，故謂火爲燬。」郭云：「燬，齊人語」\n[郭注-3]：詩曰：「王室如燬。」燬，齊人語。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '6419;6416', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4752, '懈;怠', '', '懈，怠也。', '', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '6840;6839', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4753, '宣;緩', '', '宣，緩也。1', '[郭注-1]：謂寬緩。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '4560;8781', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4754, '偶;遇', '', '偶，遇也。1', '[郭注-1]：偶爾相值也。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '5184;1127', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4755, '曩;曏', '', '曩1，曏也2。3', '[釋文-1]：本亦作𪱒，乃黨反\n[釋文-2]：許亮反，音向\n[郭注-3]：國語曰：「曩而言戲也。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '4234;4233', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4756, '皇;暇', '廣', '偟{皇}[廣]12，暇也。34', '[音釋-1]：皇。\n[釋文-2]：音皇。補舍人曰：「閒暇無事也」\n[郭注-3]：詩曰：「不遑啟處。」\n[釋文-4]：遑，音皇，呼王反，或作偟，通作皇。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '80;4236', '5940', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4757, '宵;夜', '', '宵1，夜也。', '[釋文-1]：音消，舍人云：「陽氣消也」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '4601;4328', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4758, '奥;忨;愒;貪', '忨;㵣', '懊{奥}[忨]12，忨也34。5愒[㵣]67，貪也。8', '[音釋-1]：烏報。\n[釋文-2]：烏報反\n[音釋-3]：五館。\n[釋文-4]：五館反\n[郭注-5]：謂愛忨。\n[音釋-6]：苦蓋。\n[釋文-7]：苦蓋反，杜預注左傳云：「忨、愒，皆貪也」\n[郭注-8]：謂貪羨。', '釋言弟二', '爾雅', '1;3', DEFAULT, DEFAULT, '4564;6860;6808;3984', '6860;5564', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4759, '榰;柱', '', '榰12，柱也34。5', '[音釋-1]：枝。\n[釋文-2]：搘，音枝，說文作榰\n[音釋-3]：注。\n[釋文-4]：拄，音注，說文作柱，皆從木旁\n[郭注-5]：相榰柱。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '3631;3627', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4760, '裁;節', '', '裁，節也。', '', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '5255;2877', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4761, '竝;併', '', '竝，併也1。2', '[釋文-1]：必頂反\n[郭注-2]：詩曰：「竝坐鼓瑟。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '6698;5043', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4762, '卒;旣', '𣨛;𧰙', '卒[𣨛]1，旣[𧰙]也。2', '[釋文-1]：子忽反\n[郭注-2]：旣巳。', '釋言弟二', '爾雅', '1;2', DEFAULT, DEFAULT, '5361;3199', '2535;3095', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4763, '慒;慮', '', '慒12，慮也。3', '[音釋-1]：囚。\n[釋文-2]：音囚，字書作悰\n[郭注-3]：謂謀慮也。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '6777;6704', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4764, '將;資', '齎', '將，資[齎]也。1', '[郭注-1]：謂資裝。', '釋言弟二', '爾雅', '2', DEFAULT, DEFAULT, '1983;3942', '3951', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4765, '黹;紩', '', '黹12，紩也34。56', '[音釋-1]：致耻。\n[釋文-2]：致恥反\n[音釋-3]：袟。\n[釋文-4]：音抶，郭云：「今人呼紩衣爲黹」\n[郭注-5]：今人呼縫紩衣爲黹。\n[釋文-6]：縫，奉容反。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '4944;8681', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4766, '遞;迭', '', '遞1，迭也2。34', '[釋文-1]：音悌，他計反。補李巡曰：「更迭閒厠相代之義」\n[釋文-2]：待結反，音絰\n[郭注-3]：更迭。\n[釋文-4]：更，音庚。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '1133;1166', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4767, '矧;況', '', '矧1，況也。23', '[釋文-1]：詩忍反\n[郭注-2]：譬況。\n[釋文-3]：辟，本亦作譬，同。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '3316;7162', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4768, '廩;鮮', '', '廩1，廯{鮮}也23。4', '[釋文-1]：力錦反\n[音釋-2]：息淺。\n[釋文-3]：息淺反，廣雅云：「廯，倉也。」孫炎云：「廯，藏穀鮮絜也。」舍人云：「廩，少鮮也。」郭云：「或說云卽倉廩，所未詳」\n[郭注-4]：或說云卽倉廩，所未詳。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '3342;7648', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4769, '逭;逃', '', '逭12，逃也。3', '[音釋-1]：換。\n[釋文-2]：音換。補樊光曰：「行相避逃也」\n[郭注-3]：亦見禮記。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '1171;1176', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4770, '訊;言', '', '訊，言也。1', '[郭注-1]：相問訊。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '1518;1474', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4771, '閒;俔', '', '閒12，俔也34。56', '[音釋-1]：諫。\n[釋文-2]：音諫\n[音釋-3]：胡典。\n[釋文-4]：胡典反\n[郭注-5]：左傳謂之諜，今之細作也。\n[釋文-6]：諜，徒協反。杜預注左傳云：「諜，伺也。」說文云：「軍中反閒也。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '7762;5090', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4772, '沄;沆', '', '沄12，沆也34。56', '[音釋-1]：云。\n[釋文-2]：音云\n[音釋-3]：胡黨。\n[釋文-4]：胡黨反，說文云：「沄，轉流也。一曰沆」\n[郭注-5]：水流漭沆。\n[釋文-6]：漭，莫廣反，一音莫郎反，大水貌。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '7165;7167', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4773, '干;扞', '', '干1，扞也2。3', '[釋文-1]：補孫炎曰：「干，楯，所以自蔽扞也」\n[釋文-2]：戸旦反\n[郭注-3]：相扞衞。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '1441;8067', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4774, '止;足', '', '趾{止}1，足也。2', '[釋文-1]：音止\n[郭注-2]：足，腳。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '1064;1326', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4775, '䠊;刖', '', '䠊12，刖也3。4', '[音釋-1]：扶味。\n[釋文-2]：本亦作剕，同，扶味反，又枝迷反，刖足曰剕\n[釋文-3]：音月，鄭注周禮云：「斷足也」\n[郭注-4]：斷足。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '1405;2790', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4776, '襄;駕', '䉴;落', '襄[䉴]，駕[落]也。1', '[郭注-1]：書曰：「懷山襄陵。」', '釋言弟二', '爾雅', '1;2', DEFAULT, DEFAULT, '5324;6188', '2958;549', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4777, '忝;辱', '', '忝1，辱也。', '[釋文-1]：他簟反', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '6958;9796', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4778, '燠;煖', '', '燠12，煖也。3', '[音釋-1]：於六。\n[釋文-2]：於六反。補舍人曰：「溫煖也」\n[郭注-3]：今江東通言燠。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '6514;6515', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4779, '塊;堛', '', '塊1，堛也23。4', '[釋文-1]：本作凷，說文云：「塊，俗凷字也。」凷，一名堛\n[音釋-2]：孚逼。\n[釋文-3]：孚逼反\n[郭注-4]：土塊也。外傳曰：「枕凷以堛。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '9040;9041', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4780, '將;齊', '', '將，齊也12。3', '[音釋-1]：才細。\n[釋文-2]：才細反，郭云：「謂分齊。」王肅云：「分齊其肉，所當用也。」分，如字\n[郭注-3]：謂分齊也。詩曰：「或肆或將。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '1983;4395', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4781, '餬;饘', '𩱍', '餬[𩱍]1，饘也23。45', '[釋文-1]：戸吳反，說文云：「寄食也」\n[音釋-2]：之然。\n[釋文-3]：之然反\n[郭注-4]：糜也。\n[釋文-5]：糜，靡爲反，粥之稠者曰糜。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '3241;3216', '1870', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4782, '啟;跪', '跪', '啟[跪]1，跪也2。34', '[釋文-1]：補李巡曰：「小跪也」\n[釋文-2]：求委反\n[郭注-3]：小跽。\n[釋文-4]：跽，渠几反，說文云：「長跪也。」莊子云：「擎跽曲拳，臣之禮也。」', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '1996;1332', '1332', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4783, '𥌗;密', '', '𥌗12，密也。34', '[音釋-1]：武延。\n[釋文-2]：武延反，郭云：「謂緻密」\n[郭注-3]：謂緻密。\n[釋文-4]：緻，之侍反，一音智，又音至。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '2104;5876', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4784, '開;闢', '', '開，闢也1。2', '[釋文-1]：本亦作辟，婢亦反，又甫亦反\n[郭注-2]：書曰：「闢四門。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '7756;7753', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4785, '袍;襺', '', '袍1，襺也23。45', '[釋文-1]：包毛反\n[音釋-2]：吉典。\n[釋文-3]：古典反，本亦作繭，緜衣也，禮玉藻云：「纊爲襺，緼爲袍。」鄭注云：「衣有著之別名也」\n[郭注-4]：左傳曰：「重襺衣裘。」\n[釋文-5]：重，直龍反。衣，於旣反。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '5275;5276', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4786, '障;畛', '', '障1，畛也2。34', '[釋文-1]：知亮反，說文云：「隔也。」又界也、蔽也，亦作鄣\n[釋文-2]：之忍反，田閒道\n[郭注-3]：謂壅障。\n[釋文-4]：壅，於勇反。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '9668;9190', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4787, '靦;姡', '', '靦12，姡也34。5', '[音釋-1]：他典。\n[釋文-2]：他典反，舍人云：「擅也。一曰面貌也。」謂自專擅之貌\n[音釋-3]：滑。\n[釋文-4]：戸刮反，又戸括反。孫、李云：「靦，人面姡然也。」方言云：「楚 鄭或謂狡獪爲姡。姡猶獪也，凡小兒多詐謂之姡。」郭注：「言黠姡也。」獪，音古外反，又音夬\n[郭注-5]：面姡然。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '5698;8227', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4788, '鬻;糜', '', '鬻12，糜也。34', '[音釋-1]：之六。\n[釋文-2]：之六反，字林亦作粥，云：「淖糜也。」又與六反\n[郭注-3]：淖糜。\n[釋文-4]：淖，奴孝反，又丈卓反，字林云：「濡甚也。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '1869;4362', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4789, '舒;緩', '', '舒，緩也。1', '[郭注-1]：謂遲緩。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '2511;8781', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4790, '周;毒;毒;翳', '壽;𦒇;𦒇', '翢{周}[壽]12，纛{毒}[𦒇]也34。56纛{毒}[𦒇]，翳也。7', '[音釋-1]：徒刀。\n[釋文-2]：翿，徒刀反，又徒報反\n[音釋-3]：徒到。\n[釋文-4]：字又作𦒇，徒報反，鄭衆云：「羽葆幢也。」蔡伯喈云：「以旄牛尾爲之，大如斗，在左騑馬軛上，所謂黃屋左纛。」補李巡曰：「舞者所持纛也」\n[郭注-5]：今之羽葆幢。\n[釋文-6]：葆，音保。幢，丈江反。\n[郭注-7]：舞者，所以自蔽翳。', '釋言弟二', '爾雅', '1;2;3', DEFAULT, DEFAULT, '877;236;236;2274', '5384;2273;2273', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4791, '隍;壑', '', '隍，壑也1。2', '[釋文-1]：火各反。補舍人曰：「隍，城池。壑，溝」\n[郭注-2]：城池空者爲壑。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '9703;2526', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4792, '芼;搴', '覒', '芼[覒]1，搴也2。34', '[釋文-1]：莫報反\n[釋文-2]：九輦反，取也，與𢷘同，郭又音騫，音義云：「本又作『毛，蹇』」\n[郭注-3]：謂拔取菜。\n[釋文-4]：拔，步八反。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '537;8002', '5517', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4793, '典;經;威;則', '', '典，經也。威，則也。1', '[郭注-1]：威儀可法則。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '3023;8532;8147;2756', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4794, '苛;妎', '疥/齘', '苛1，妎[疥/齘]也23。4', '[釋文-1]：胡柯反\n[音釋-2]：胡計。\n[釋文-3]：胡計反，又胡界反，又音害，說文云：「妎，妬也」\n[郭注-4]：煩苛者，多嫉妎。', '釋言弟二', '爾雅', '2', DEFAULT, DEFAULT, '543;8273', '4744/0/1283', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4795, '巿;小', '蔽/尐', '芾{巿}[蔽/尐]12，小也。3', '[音釋-1]：貝。\n[釋文-2]：芳味反，又方蓋反\n[郭注-3]：芾者，小貌。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '4927;704', '550/0/706', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4796, '迷;惑', '', '迷，惑也。', '', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '1167;6865', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4797, '狃;復', '𢔟', '狃[𢔟]12，復也34。56', '[音釋-1]：女九。\n[釋文-2]：女九反\n[音釋-3]：扶又。\n[釋文-4]：扶又反，注同，孫、郭云：「狃忕，復爲也。」李音服，云：「狃能屈申曰復」\n[郭注-5]：狃忕，復爲。\n[釋文-6]：忕，石世反。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '6341;1226', '1227', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4798, '逼;迫', '', '逼1，迫也2。', '[釋文-1]：彼力反，本又作偪\n[釋文-2]：音百', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '1213;1182', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4799, '般;還', '', '般12，還也34。5', '[音釋-1]：班。\n[釋文-2]：郭音班，左傳云：「役將般矣」是也。一音蒲安反，周易云：「般桓」是也。說文云：「般，辟也」\n[音釋-3]：旋。\n[釋文-4]：音旋，或音環\n[郭注-5]：左傳曰：「般馬之聲。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '5448;1142', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4800, '班;賦', '', '班，賦也。1', '[郭注-1]：謂布與。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '222;3983', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4801, '濟;渡;濟;成;濟;益', '𠂔;積', '濟1，渡也。濟[𠂔]，成也。濟[積]，益也2。3', '[釋文-1]：子細反，下同\n[釋文-2]：於亦反\n[郭注-3]：所以廣異訓，各隨事爲義。', '釋言弟二', '爾雅', '3;5', DEFAULT, DEFAULT, '7095;7285;7095;9749;7095;3155', '3874;4467', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4802, '緍;綸', '', '緍12，綸也3。45', '[音釋-1]：民。\n[釋文-2]：亡巾反\n[釋文-3]：音倫\n[郭注-4]：詩曰：「維絲伊緍。」緡，繩也。江東謂之綸。\n[釋文-5]：繩，音乗。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '8725;8654', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4803, '辟;歷', '厤', '辟12，歷[厤]也。3', '[音釋-1]：婢亦。\n[釋文-2]：婢亦反，一音壁\n[郭注-3]：未詳。', '釋言弟二', '爾雅', '2', DEFAULT, DEFAULT, '5798;1070', '5988', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4804, '漦;渌', '', '漦12，盝{渌}也3。45', '[音釋-1]：仕其。\n[釋文-2]：仕其反，又呂其反，郭音牛齝反，又丑之反，李云：「吐沫漦也」\n[釋文-3]：音鹿\n[郭注-4]：漉漉出涎沫。\n[釋文-5]：漉，音鹿。唌，囚延反，字當作㳄，又作涎，字林云：「口液。」沫，音末。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '7145;7376', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4805, '寬;綽', '', '寬1，綽也2。34', '[釋文-1]：補孫炎曰：「性之裕者」\n[釋文-2]：昌斫反\n[郭注-3]：謂寬𥙿也。\n[釋文-4]：裕，音喻。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '4605;8780', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4806, '衮;黻', '', '衮1，黻也2。3', '[釋文-1]：古本反，說文云：「從衣從㕣也。」㕣，羊耎反。或云：「從公衣」\n[釋文-2]：音弗\n[郭注-3]：衮衣有黻文。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '5256;4947', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4807, '華;皇', '葟', '華12，皇[葟]也3。4', '[音釋-1]：胡瓜。\n[釋文-2]：胡瓜反\n[釋文-3]：胡光反\n[郭注-4]：釋草曰：「葟、華，榮。」', '釋言弟二', '爾雅', '2', DEFAULT, DEFAULT, '3886;80', '3383', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4808, '昆;後', '𥊽', '昆[𥊽]，後也。1', '[郭注-1]：謂先後，方俗語。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '4256;1250', '3402', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4809, '镾;終', '', '彌{镾}1，終也。2', '[釋文-1]：亡移反\n[郭注-2]：终，竟也。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '6071;8590', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4810, '明明;斤斤;察', '', '明明、斤斤12，察也。34', '[音釋-1]：居覲。\n[釋文-2]：樊居覲反，舍人云：「明明，甚明也。斤斤，物精詳之察。」孫云：「斤斤，重慎之察也」\n[郭注-3]：皆聰明鑒察。\n[釋文-4]：聰，七公反。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '4323/4323;9476/9476;4583', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4811, '條條;秩秩;智', '悠悠;逹達', '條條[悠悠]12、秩秩[逹達]3，智也。45', '[音釋-1]：由。\n[釋文-2]：舍人本作攸攸，沈亦音條\n[釋文-3]：直栗反\n[郭注-4]：皆智思深長。\n[釋文-5]：思，息嗣反，下文并注同。', '釋訓弟三', '爾雅', '1;2', DEFAULT, DEFAULT, '3572/3572;4468/4468;2231', '6929/6929;1163/1163', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4812, '穆穆;肅肅;敬', '㣎㣎', '穆穆[㣎㣎]、肅肅，敬也。1', '[郭注-1]：皆容儀謹敬。', '釋訓弟三', '爾雅', '1', DEFAULT, DEFAULT, '4430/4430;1939/1939;5820', '5720/5720', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4813, '諸諸;便便;辯', '諞諞', '諸諸、便便[諞諞]12，辯也。3', '[音釋-1]：婢緜。\n[釋文-2]：婢緜反\n[郭注-3]：皆言辭辯給。', '釋訓弟三', '爾雅', '2', DEFAULT, DEFAULT, '1488/1488;5088/5088;9763', '1634/1634', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4814, '肅肅;翼翼;恭', '趩䟆', '肅肅、翼翼[趩䟆]，恭也。1', '[郭注-1]：皆恭敬。', '釋訓弟三', '爾雅', '2', DEFAULT, DEFAULT, '1939/1939;7695/7695;6743', '1027/1059', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4815, '廱廱;優優;和', '饔饔;憂憂', '廱廱[饔饔]12、優優[憂憂]3，和也。45', '[音釋-1]：於容。\n[釋文-2]：於容反\n[釋文-3]：音憂\n[郭注-4]：皆和樂。\n[釋文-5]：樂，音洛。', '釋訓弟三', '爾雅', '1;2', DEFAULT, DEFAULT, '5925/5925;5091/5091;846', '3210/3210;3368/3368', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4816, '兢兢;繩繩;戒', '懲懲', '兢兢1、憴憴{繩繩}[懲懲]2，戒也3。4', '[釋文-1]：音矜\n[釋文-2]：繩，本或作憴，同，食蒸反\n[釋文-3]：音界\n[郭注-4]：皆戒慎。', '釋訓弟三', '爾雅', '2', DEFAULT, DEFAULT, '5465/5465;8694/8694;1767', '6967/6967', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4817, '戰戰;蹌蹌;動', '', '戰戰、蹌蹌1，動也。23', '[釋文-1]：七羊反\n[郭注-2]：皆恐動趨步。\n[釋文-3]：恐，丘勇反。趨，七俞反。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '8384/8384;1345/1345;9233', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4818, '晏晏;溫溫;柔', '', '晏晏、溫溫，柔也。1', '[郭注-1]：皆和柔。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '4211/4211;6997/6997;3609', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4819, '業業;翹翹;危', '隉隉;堯堯', '業業[隉隉]1、翹翹[堯堯]2，危也。34', '[釋文-1]：魚怯反，郭五荅反\n[釋文-2]：巨遥反\n[郭注-3]：皆縣危。\n[釋文-4]：縣，音玄。', '釋訓弟三', '爾雅', '1;2', DEFAULT, DEFAULT, '1749/1749;2252/2252;6009', '9651/9651;9166/9166', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4820, '惴惴;嘵嘵;懼', '', '惴惴12、憢憢{嘵嘵}34，懼也。5', '[音釋-1]：之瑞。\n[釋文-2]：之瑞反\n[音釋-3]：許堯。\n[釋文-4]：許堯反，本又作嘵，音同，字林云：「懼也。」案詩云：「予維音嘵嘵」是也\n[郭注-5]：皆危懼。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '6920/6920;914/914;6774', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4821, '番番;矯矯;勇', '伴伴;赳赳', '番番[伴伴]12、矯矯[赳赳]34，勇也。5', '[音釋-1]：波。\n[釋文-2]：布何反，詩云：「申伯番番」\n[音釋-3]：居兆。\n[釋文-4]：居兆反，舍人云：「矯矯，得勝之勇也。」詩云：「矯矯虎臣」\n[郭注-5]：皆壯勇之貌。', '釋訓弟三', '爾雅', '1;2', DEFAULT, DEFAULT, '721/721;3311/3311;9245', '5007/5007;985/985', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4822, '桓桓;烈烈;威', '狟狟', '桓桓[狟狟]、烈烈，威也。1', '[郭注-1]：皆嚴猛之貌。', '釋訓弟三', '爾雅', '1', DEFAULT, DEFAULT, '3673/3673;6427/6427;8147', '6351/6351', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4823, '洸洸;赳赳;武', '', '洸洸1、赳赳2，武也。34', '[釋文-1]：古皇反，舍人本作僙，音同\n[釋文-2]：居黝反，說文云：「輕勁有才也。」詩云：「赳赳武夫」\n[郭注-3]：皆果毅之貌。\n[釋文-4]：惈，音果，本亦作果，同。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '7174/7174;985/985;8397', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4824, '藹藹;濟濟;止', '', '藹藹12、濟濟34，止也。5', '[音釋-1]：烏害。\n[釋文-2]：烏害反\n[音釋-3]：子禮。\n[釋文-4]：咨禮反\n[郭注-5]：皆賢士盛多之容止。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '1533/1533;7095/7095;1064', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4825, '悠悠;洋洋;思', '', '悠悠1、洋洋2，思也3。4', '[釋文-1]：音由\n[釋文-2]：音羊\n[音釋-3]：賜\n[郭注-4]：皆憂思。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '6929/6929;7083/7083;6703', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4826, '蹶蹶;踖踖;敏', '适适;迮迮', '蹶蹶[适适]1、踖踖[迮迮]23，敏也。45', '[釋文-1]：居衞反\n[音釋-2]：夕。\n[釋文-3]：音夕，又音藉\n[郭注-4]：皆便速敏捷。\n[釋文-5]：便，婢面反。捷，才接反。', '釋訓弟三', '爾雅', '1;2', DEFAULT, DEFAULT, '1369/1369;1336/1336;1999', '1123/1123;1118/1118', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4827, '薨薨;增增;衆', '雄雄', '薨薨[雄雄]1、增增，衆也2。34', '[釋文-1]：虎弘反，顧、舍人本作雄雄\n[釋文-2]：諸仲反\n[郭注-3]：皆衆夥之貌。\n[釋文-4]：夥，戸果反，漢書云：「楚人謂多爲夥。」', '釋訓弟三', '爾雅', '1', DEFAULT, DEFAULT, '2563/2563;9092/9092;5237', '2313/2313', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4828, '烝烝;遂遂;作', '穟穟', '烝烝1、遂遂[穟穟]，作也2。3', '[釋文-1]：蒸蒸，諸仍反，本今作烝烝\n[釋文-2]：子洛反\n[郭注-3]：皆物盛興作之貌。', '釋訓弟三', '爾雅', '2', DEFAULT, DEFAULT, '6431/6431;1175/1175;5075', '4451/4451', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4829, '委委;佗佗;美', '逶逶/覣覣;迆迆', '委委[逶逶/覣覣]1、佗佗[迆迆]23，美也。4', '[釋文-1]：於危反，詩云：「委委佗佗，如山如河」是也。諸儒本並作褘，於宜反。舍人云：「褘褘者，心之美。」引詩云亦作褘\n[音釋-2]：陀。\n[釋文-3]：本或作它字，音徒河反，顧、舍人引詩釋云：「褘褘它它，如山如河。」謝羊兒反\n[郭注-4]：皆佳麗美豔之貌。', '釋訓弟三', '爾雅', '1;2', DEFAULT, DEFAULT, '8215/8215;5024/5024;2356', '1155/1155/0/5479/5479;1156/1156', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4830, '忯忯;惕惕;愛', '㤅', '忯忯12、惕惕3，愛[㤅]也。4', '[音釋-1]：徒啟。\n[釋文-2]：郭徒啟反，與愷悌音同，顧、舍人渠支反。李余之反。忯忯，和適之愛也\n[釋文-3]：他狄反\n[郭注-4]：詩云：「心焉惕惕。」韓詩以爲悅人，故言愛也。忯忯，未詳。', '釋訓弟三', '爾雅', '3', DEFAULT, DEFAULT, '6748/6748;6947/6947;3369', '6781', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4831, '偁偁;格格;舉', '閣閣', '偁偁1、格格[閣閣]，舉也。2', '[釋文-1]：尺仍反，本亦作稱，同\n[郭注-2]：皆舉持物。', '釋訓弟三', '爾雅', '2', DEFAULT, DEFAULT, '5067/5067;3603/3603;7959', '7761/7761', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4832, '蓁蓁;孼孼;戴', '䡾䡾', '蓁蓁1、孽孽{孼孼}[䡾䡾]2，戴也。3', '[釋文-1]：郭側巾反，又子人反\n[釋文-2]：魚謁反，又五葛反\n[郭注-3]：皆頭戴物。', '釋訓弟三', '爾雅', '2', DEFAULT, DEFAULT, '531/531;9775/9775;1779', '9575/9575', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4833, '懕懕;媞媞;安', '', '懕懕12、媞媞34，安也。5', '[音釋-1]：於占。\n[釋文-2]：於占反，說文云：「安靜也」\n[音釋-3]：題。\n[釋文-4]：徒低反。補孫炎曰：「行步之安也」\n[郭注-5]：皆好人安詳之容。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '6798/6798;8230/8230;4578', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4834, '祁祁;遟遟;徐', '徥徥', '祁祁[徥徥]1、遟遟，徐也。2', '[釋文-1]：巨移反\n[郭注-2]：皆安徐。', '釋訓弟三', '爾雅', '1', DEFAULT, DEFAULT, '4063/4063;1148/1148;1238', '1237/1237', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4835, '丕丕;𥳑𥳑;大', '伾伾;僩僩', '丕丕[伾伾]1、𥳑𥳑[僩僩]2，大也。3', '[釋文-1]：本或作㔻，同，普悲反\n[釋文-2]：補李巡曰：「降福之大也」\n[郭注-3]：皆多大。', '釋訓弟三', '爾雅', '1;2', DEFAULT, DEFAULT, '4/4;2893/2893;6598', '5010/5010;5009/5009', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4836, '存存;萌萌;䉍䉍;在', '明明', '存存、萌萌[明明]{䉍䉍}1，在也。2', '[釋文-1]：郭武耕反，施亡朋反，字或作䉍\n[郭注-2]：萌萌，未見所出。', '釋訓弟三', '爾雅', '2', DEFAULT, DEFAULT, '9778/9778;491/491;6911/6911;9068', '4323/4323', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4837, '懋懋;慔慔;勉', '', '懋懋12、慔慔34，勉也。56', '[音釋-1]：茂。\n[釋文-2]：古茂字\n[音釋-3]：暮。\n[釋文-4]：音暮，亦作慕\n[郭注-5]：皆自勉強。\n[釋文-6]：強，其丈反。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '6792/6792;6789/6789;9222', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4838, '庸庸;慅慅;勞', '', '庸庸1、慅慅23，勞也。4', '[釋文-1]：音容\n[音釋-2]：騷。\n[釋文-3]：郭騷、草、蕭三音\n[郭注-4]：皆劬勞也。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '2084/2084;6912/6912;9236', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4839, '赫赫;躍躍;迅', '奭奭', '赫赫[奭奭]12、躍躍3，迅也。4', '[音釋-1]：釋。\n[釋文-2]：郭音釋，舍人本作奭，失石反，說文云：「奭，盛也。」謝許格反。補孫炎曰：「顯著之迅」\n[釋文-3]：余斫反，樊本作濯，引詩釋云「濯濯厥靈」\n[郭注-4]：皆盛疾之貌。', '釋訓弟三', '爾雅', '1', DEFAULT, DEFAULT, '6595/6595;1348/1348;1122', '2239/2239', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4840, '綽綽;爰爰;緩', '', '綽綽、爰爰，緩也。12', '[郭注-1]：皆寬緩也。悠悠、偁偁、丕丕、𥳑𥳑、存存、懋懋、庸庸、綽綽，盡重語。\n[釋文-2]：重，直用反，又直龍反。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '8780/8780;2517/2517;8781', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4841, '坎坎;墫墫;喜', '竷竷', '坎坎[竷竷]1、墫墫23，喜也。4', '[釋文-1]：苦感反\n[音釋-2]：七旬。\n[釋文-3]：七旬反，舍人云：「舞貌。」毛傳同，郭云：「謂鼓舞歡喜也。」說文云：「墫，士舞也。宜從士，尊也。」本或作蹲，同\n[郭注-4]：皆鼓無懽喜。', '釋訓弟三', '爾雅', '1', DEFAULT, DEFAULT, '9086/9086;229/229;3075', '3371/3371', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4842, '瞿瞿;休休;儉', '', '瞿瞿12、休休3，儉也。4', '[音釋-1]：居具。\n[釋文-2]：居具反\n[釋文-3]：虛求反，又虛虯反\n[郭注-4]：皆良士，節儉。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '2361/2361;3818/3818;5095', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4843, '旭旭;蹻蹻;驕', '叴叴', '旭旭[叴叴]1、蹻蹻23，憍{驕}也45。6', '[釋文-1]：謝許玉反，郭呼老反\n[音釋-2]：巨虐。\n[釋文-3]：郭居夭反，案詩小雅：「小子蹻蹻。」音巨虐反，今依詩讀\n[音釋-4]：嬌夭。\n[釋文-5]：九苗反。補孫炎曰：「謂驕慢之貌」\n[郭注-6]：皆小人得志憍蹇之貌。', '釋訓弟三', '爾雅', '1', DEFAULT, DEFAULT, '4204/4204;1343/1343;6173', '904/904', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4844, '夢夢;屯屯;亂', '', '夢夢12、訰訰{屯屯}34，亂也。56', '[音釋-1]：亡工。\n[釋文-2]：亡工、亡棟二反，沈、施亡增反。補孫炎曰：「昏昏之亂也」\n[音釋-3]：之閏。\n[釋文-4]：之閏、之屯二反，或作諄，音同，顧、舍人云：「夢夢、訰訰，煩懣亂也」\n[郭注-5]：皆闇亂。訰音紃。\n[釋文-6]：紃，囚春、昌㳂二反，本無此字。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '4329/4329;234/234;9744', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4845, '𧬉𧬉;邈邈;悶', '', '懪懪{𧬉𧬉}12、邈邈3，悶也。4', '[音釋-1]：雹。\n[釋文-2]：本又作𧬉，蒲卓反，又布卓反，說文云：「大呼也，自冤也」\n[釋文-3]：亡角反。補舍人曰：「藐藐，憂悶也」\n[郭注-4]：皆煩悶。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '1665/1665;1214/1214;6894', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4846, '懜懜/𠐿𠐿;洄洄;惛', '䙟䙟', '儚儚{懜懜/𠐿𠐿}12、洄洄[䙟䙟]3，惛也4。5', '[音釋-1]：亡崩。\n[釋文-2]：字或作懜，孫亡崩、亡冰二反\n[釋文-3]：沈音回，郭音義云：「本或作（⿰巾圍），音韋。」案字林：「（⿰巾圍），重衣貌，于回反」\n[釋文-4]：音昬\n[郭注-5]：皆迷惛。', '釋訓弟三', '爾雅', '2', DEFAULT, DEFAULT, '6862/6862/0/5122/5122;7288/7288;6869', '5306/5306', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4847, '版版;盪盪;僻', '', '版版1、盪盪2，僻也3。45', '[釋文-1]：詩作板，並如字，李云：「版版者，失道之僻也」\n[釋文-2]：蕩蕩，本或作盪，徒朗反，李云：「蕩蕩者，弗思之僻也」\n[釋文-3]：匹亦反\n[郭注-4]：皆邪僻。\n[釋文-5]：衺，本又作邪，似嗟反。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '4401/4401;3162/3162;5130', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4848, '爞爞;炎炎;薰', '熏', '爞爞12、炎炎3，薰[熏]也4。56', '[音釋-1]：同。\n[釋文-2]：郭徒冬反，又直忠反\n[釋文-3]：于廉反\n[釋文-4]：熏，本亦作燻，或作薫，許云反\n[郭注-5]：皆旱熱薫炙人。\n[釋文-6]：炙，之石反。', '釋訓弟三', '爾雅', '3', DEFAULT, DEFAULT, '6528/6528;6534/6534;294', '239', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4849, '居居;究究;惡;仇仇;敖敖;傲', '倨倨;叴叴;叴叴;嚻嚻', '居居[倨倨]1、究究[叴叴]2，惡也。3仇仇[叴叴]4、敖敖[嚻嚻]56，傲也78。9', '[釋文-1]：補李巡曰：「不狎習之惡」\n[釋文-2]：九又反。補孫炎曰：「窮極人之惡」\n[郭注-3]：皆相憎惡。\n[釋文-4]：音求\n[音釋-5]：五高。\n[釋文-6]：本又作謷，又作嚻，同，五高反\n[音釋-7]：五耗。\n[釋文-8]：本或作慠，同，五報反。舍人本傲作毀，釋云：「仇仇，無倫理之貌。謷謷，衆口毀人之貌。」李同\n[郭注-9]：皆傲慢賢者。', '釋訓弟三', '爾雅', '1;2;4;5', DEFAULT, DEFAULT, '5404/5404;4673/4673;6882;5173/5173;2514/2514;5001', '5003/5003;904/904;904/904;1437/1437', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4850, '此此;瑣瑣;小', '𠈈𠈈', '佌佌{此此}[𠈈𠈈]12、瑣瑣3，小也。4', '[音釋-1]：此。\n[釋文-2]：顧音此，郭音徙，謝音紫，舍人云：「形容小貌」\n[釋文-3]：星果反，亦作璅。補舍人曰：「計謀褊淺之貌」\n[郭注-4]：皆才器細陋。', '釋訓弟三', '爾雅', '1', DEFAULT, DEFAULT, '1083/1083;154/154;704', '5127/5127', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4851, '悄悄;慘慘;愠', '', '悄悄1、慘慘2，愠也3。4', '[釋文-1]：七小反\n[釋文-2]：七感反。補李巡曰：「憂怒之愠」\n[釋文-3]：於問反\n[郭注-4]：皆賢人愁恨。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '6935/6935;6902/6902;6881', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4852, '悹悹;庾庾;病', '', '痯痯{悹悹}12、瘐瘐{庾庾}34，病也。5', '[音釋-1]：管。\n[釋文-2]：郭古卵反，又古玩反\n[音釋-3]：羊主。\n[釋文-4]：羊主反，又羊朱反，本今作庾庾\n[郭注-5]：皆賢人失志，懷憂病也。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '6770/6770;5942/5942;4697', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4853, '殷殷;𢞛𢞛;刀刀;專專;欽欽;京京;忡忡;惙惙;怲怲;奕奕;憂', '慇慇;憌憌;惆惆;怛怛;𣄴𣄴;迹迹;𢝊', '殷殷[慇慇]1、惸惸{𢞛𢞛}[憌憌]23、忉忉{刀刀}[惆惆]4、慱慱{專專}[怛怛]56、欽欽、京京[𣄴𣄴]、忡忡7、惙惙8、怲怲910、奕奕[迹迹]11，憂[𢝊]也。12', '[釋文-1]：慇慇，於斤反，樊光於謹反，本今作殷殷\n[音釋-2]：瓊。\n[釋文-3]：本或作煢，巨營反\n[釋文-4]：都勞反\n[音釋-5]：團。\n[釋文-6]：徒端反，施逋莫反，郭徂兖、徂㳂二反\n[釋文-7]：恥忠反\n[釋文-8]：丁劣反\n[音釋-9]：柄。\n[釋文-10]：彼病反\n[釋文-11]：音亦\n[郭注-12]：此皆作者歌事以詠心憂。', '釋訓弟三', '爾雅', '1;2;3;4;6;10;11', DEFAULT, DEFAULT, '5253/5253;3065/3065;2744/2744;1985/1985;5526/5526;3331/3331;6934/6934;6924/6924;6922/6922;6669/6669;3368', '6909/6909;6921/6921;6895/6895;6900/6900;5599/5599;1093/1093;6937', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4854, '均均;田', '', '畇畇{均均}12，田也。34', '[音釋-1]：巡。\n[釋文-2]：本或作𤱬，郭音巡，沈居賓反，謝蘇旬反。字林云：「均均，田也。」又羊倫反\n[郭注-3]：言墾辟也。\n[釋文-4]：墾，苦很反，蒼頡篇云：「耕也。」廣雅云：「治也。」辟，婢亦反，開也。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '9030/9030;9172', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4855, '畟畟;耜', '', '畟畟12，耜也3。4', '[音釋-1]：楚力。\n[釋文-2]：楚力反，字或作稷。補舍人曰：「耜入地之貌」\n[釋文-3]：音似\n[郭注-4]：言嚴利。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '3374/3374;3689', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4856, '郝郝;耕', '捇捇', '郝郝[捇捇]12，耕也。34', '[音釋-1]：釋。\n[釋文-2]：音釋，又呼各反。補舍人曰：「釋釋猶藿藿，解散之意」\n[郭注-3]：言土解。\n[釋文-4]：解，音蟹。', '釋訓弟三', '爾雅', '1', DEFAULT, DEFAULT, '4028/4028;2821', '8023/8023', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4857, '繹繹;生', '', '繹繹1，生也。23', '[釋文-1]：音亦。補舍人曰：「穀皆生之貌」\n[郭注-2]：言種調。\n[釋文-3]：種，之用反。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '8520/8520;3876', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4858, '穟穟;苗', '', '穟穟12，苗也。3', '[音釋-1]：遂。\n[釋文-2]：郭音遂，說文云：「禾垂之貌」\n[郭注-3]：言茂好也。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '4451/4451;542', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4859, '緜緜;穮', '', '緜緜1，穮也23。45', '[釋文-1]：彌延反。補孫炎曰：「言詳密也」\n[音釋-2]：方遥。\n[釋文-3]：麃，字書作穮，同，「方遥反，耘也。」字林云：「耕禾閒也。」左傳云：「譬如農夫，是穮是蓘」是也。說文云：「穮，耨鋤田也」\n[郭注-4]：言芸精。\n[釋文-5]：耘，字亦作芸，同，音云。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '8515/8515;4461', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4860, '挃挃;穫', '', '挃挃12，穫也34。56', '[音釋-1]：丁秩。\n[釋文-2]：郭丁秩反，小雅云：「截穎謂之挃」\n[音釋-3]：戸郭。\n[釋文-4]：戸郭反\n[郭注-5]：刈禾聲。\n[釋文-6]：刈，魚廢反。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '8039/8039;4465', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4861, '栗栗;衆', '', '栗栗，衆也1。23', '[釋文-1]：諸仲反\n[郭注-2]：積聚緻。\n[釋文-3]：緻，直吏反。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '4393/4393;5237', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4862, '蚤蚤;淅', '𣸈𣸈/滫滫', '溞溞{蚤蚤}[𣸈𣸈/滫滫]12，淅也34。56', '[音釋-1]：蘇刀。\n[釋文-2]：郭蘇刀反，詩云：「淅之溞溞。」謝所留反\n[音釋-3]：錫。\n[釋文-4]：蘇歷反\n[郭注-5]：洮米聲。\n[釋文-6]：洮，徒刀反，洮猶淅也。', '釋訓弟三', '爾雅', '1', DEFAULT, DEFAULT, '8950/8950;7371', '7373/7373/0/7380/7380', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4863, '烰烰;烝', '', '烰烰1，烝也2。3', '[釋文-1]：呂、郭並音浮，又符彪反，詩作浮。補孫炎曰：「炊之氣」\n[釋文-2]：蒸，之升反，本今作烝\n[郭注-3]：氣出盛。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '6432/6432;6431', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4864, '俅俅;服', '', '俅俅1，服也。23', '[釋文-1]：音求，本亦作絿，同\n[郭注-2]：謂戴弁服。\n[釋文-3]：載，丁代反，本今作戴。弁，音卞。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '4959/4959;5449', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4865, '峨峨;祭', '', '峨峨1，祭也。23', '[釋文-1]：補舍人曰：「奉璋之貌」\n[郭注-2]：謂執圭璋助祭。\n[釋文-3]：璋，音章。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '5892/5892;30', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4866, '鍠鍠;樂', '', '鍠鍠12，樂也3。4', '[音釋-1]：橫。\n[釋文-2]：韹韹，詩作喤喤，華盲反。字書：「鍠鍠，樂之聲也。」又作鍠。一音胡光反。補舍人曰：「喤喤，鐘鼓之樂也」\n[釋文-3]：如字\n[郭注-4]：鐘鼓音。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '9391/9391;3764', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4867, '穰穰;福', '禳禳/䑋䑋', '穰穰[禳禳/䑋䑋]1，福也。2', '[釋文-1]：禳禳，而羊反，本今作穰。補舍人曰：「衆多之貌也」\n[郭注-2]：言饒多。', '釋訓弟三', '爾雅', '1', DEFAULT, DEFAULT, '4480/4480;20', '53/53/0/2649/2649', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4868, '子子孫孫;引無極', '', '子子孫孫，引無極也1。2', '[釋文-1]：余忍、余慎二反，引長多也。補舍人曰：「子孫長行美道，引無極也」\n[郭注-2]：世世昌盛，長無窮。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '9766/9766/8514/8514;8493/8416/3626', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4869, '顒顒;卬卬', '', '顒顒1、卬卬2，君之德也。3', '[釋文-1]：魚恭反。補孫炎曰：「體貌溫順也」\n[釋文-2]：五剛反，郭魚殃反。補孫炎曰：「志氣高遠也」\n[郭注-3]：道君人者之德望。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '5632/5632;5223/5223', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4870, '丁丁;嚶嚶;相切直', '', '丁丁12、嚶嚶3，相切直也。45', '[音釋-1]：爭。\n[釋文-2]：豬耕反\n[釋文-3]：烏耕反\n[郭注-4]：丁丁，斫木聲。嚶嚶，兩鳥鳴。以喻朋友切磋相正。\n[釋文-5]：斫，音灼。磋，七何反。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '9747/9747;952/952;2162/2760/8412', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4871, '藹藹;萋萋;臣盡力', '', '藹藹1、萋萋2，臣盡力也34。5', '[釋文-1]：於蓋反\n[釋文-2]：七西反\n[音釋-3]：咨忍。\n[釋文-4]：咨忍反\n[郭注-5]：梧桐茂，賢士衆；地極化，臣竭忠。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '1533/1533;504/504;1953/3157/9212', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4872, '饔饔;喈喈;民協服', '', '噰噰{饔饔}1、喈喈23，民協服也。45', '[釋文-1]：本或作雍，又作廱，同，於恭反\n[音釋-2]：皆。\n[釋文-3]：古諧反\n[郭注-4]：鳯凰應徳鳴相和，百姓懷附興頌歌。\n[釋文-5]：應，音譍。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '3210/3210;947/947;8359/9260/5449', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4873, '佻佻;契契;俞遐急;宴宴;粲粲;尼居息', '忦忦', '佻佻12、契契[忦忦]34，愈遐急{俞遐急}也5。67宴宴8、粲粲9，尼居息也1011。1213', '[音釋-1]：勑料。\n[釋文-2]：徒彫反，詩云：「佻佻獨行。」歎息也\n[音釋-3]：苦結。\n[釋文-4]：苦計反，詩云「契契寤歎」。字又作挈，苦結反\n[釋文-5]：本或作俞，同，瑜、庾二音\n[郭注-6]：賦役不均，小國困竭；賢人憂歎，遠益急切。\n[釋文-7]：竭，本又作渴，巨列反。\n[釋文-8]：燕燕，字又作宴，烏殿、烏顯二反\n[釋文-9]：七旦反\n[音釋-10]：女乙。\n[釋文-11]：女乙反，謝羊而反，又奴啟反\n[郭注-12]：盛飾宴安，近處優閑。\n[釋文-13]：飾，音式。處，音杵。閒，音閑，本今作閑。', '釋訓弟三', '爾雅', '2', DEFAULT, DEFAULT, '5129/5129;6614/6614;5439/1215/6811;4581/4581;4351/4351;5412/5404/6706', '6918/6918', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4874, '哀哀;悽悽;懐', '', '哀哀、悽悽1，懐報德也。23', '[釋文-1]：郭本或作萋，同，七兮反\n[郭注-2]：悲苦征役，思所生也。\n[釋文-3]：苦，如字，又丘故反。思，息嗣反，郭音如字。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '936/936;6903/6903;6764', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4875, '儵儵;嘒嘒', '悠悠', '儵儵[悠悠]12、嘒嘒34，罹禍毒也5。67', '[音釋-1]：徒的。\n[釋文-2]：郭徒的反，顧舒育反，樊本作攸，引詩云「攸攸我里」\n[音釋-3]：呼惠。\n[釋文-4]：虎惠反\n[釋文-5]：力支反\n[郭注-6]：悼王道穢塞，羨蟬鳴自得，傷己失所，遭䜛賊。\n[釋文-7]：悼，音盜。己，音紀。', '釋訓弟三', '爾雅', '1', DEFAULT, DEFAULT, '6571/6571;860/860', '6929/6929', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4876, '晏晏;旦旦;悔爽忒', '妟妟;𢘇𢘇', '晏晏[妟妟]、旦旦[𢘇𢘇]1，悔爽忒也2。3', '[釋文-1]：本或作𢘇，同，都歎反\n[釋文-2]：佗得反\n[郭注-3]：傷見絕棄，恨士失也。', '釋訓弟三', '爾雅', '1;2', DEFAULT, DEFAULT, '4211/4211;4285/4285;6889/2091/6825', '8252/8252;6900/6900', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4877, '皋皋;鞙鞙', '璿璿', '皋皋1、琄琄{鞙鞙}[璿璿]23，刺素食也45。67', '[釋文-1]：古豪反，樊本作浩，古老反。補舍人曰：「不治之貌」\n[音釋-2]：胡犬。\n[釋文-3]：鞙，胡犬、古犬二反\n[音釋-4]：七賜。\n[釋文-5]：字又作𧧒，同，七賜反\n[郭注-6]：譏無功德，尸寵禄也。鞙音贙。\n[釋文-7]：贙，胡犬反。', '釋訓弟三', '爾雅', '2', DEFAULT, DEFAULT, '6662/6662;1838/1838', '103/103', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4878, '懽懽;搖搖;憂無告', '悹悹;擾擾', '懽懽[悹悹]12、愮愮{搖搖}[擾擾]34，憂無告也5。67', '[音釋-1]：貫。\n[釋文-2]：灌，本或作懽，同，古玩反\n[音釋-3]：遥。\n[釋文-4]：音遥，本又作摇，樊本作遥，又作恌，恌與愮同訓也。書云：「愮愮，憂無告也。」廣雅云：「愮，亂也」\n[釋文-5]：如字，又古毒反\n[郭注-6]：賢者憂懼，無所訴也。\n[釋文-7]：訴，音素。', '釋訓弟三', '爾雅', '1;2', DEFAULT, DEFAULT, '6803/6803;7951/7951;3368/8416/778', '6770/6770;7935/7935', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4879, '憲憲;洩洩', '眔眔', '憲憲、洩洩[眔眔]1，制法則也2。3', '[釋文-1]：泄，余誓反，或作呭，同\n[釋文-2]：補李巡曰：「皆惡黨爲制法則也」\n[郭注-3]：佐興虐政，設敎令也。', '釋訓弟三', '爾雅', '2', DEFAULT, DEFAULT, '6723/6723;7065/7065', '2142/2142', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4880, '謔謔;熇熇', '', '謔謔1、熇熇23，崇讒慝也4。56', '[釋文-1]：虛虐反\n[音釋-2]：虛各。\n[釋文-3]：許各、火沃二反。本亦作謞，郭虛各反，或火角反。補孫炎曰：「厲王暴虐，大臣謔謔然喜、謞謞然盛，以興讒惡也」\n[釋文-4]：謝他得反，諸儒並女陟反，言隱匿其情以飾非\n[郭注-5]：樂禍助虐，增譖惡也。\n[釋文-6]：樂，如字，又音洛。譖，側禁反。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '1646/1646;6442/6442', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4881, '翕翕;訿訿', '', '翕翕、訿訿12，莫供職也3。45', '[音釋-1]：子尔。\n[釋文-2]：子爾反，字林云：「不思稱乎上之意」\n[釋文-3]：音恭\n[郭注-4]：賢者陵替姦黨熾，背公恤私曠職事。\n[釋文-5]：熾，尺志反。背，音佩。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '2258/2258;1626/1626', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4882, '速速;蹙蹙', '', '速速、蹙蹙1，惟逑鞫也2。34', '[釋文-1]：子六反\n[釋文-2]：巨鳩反，郭云：「迫也。」字林云：「斂聚也」，本亦作求。居六反\n[郭注-3]：陋人專禄國侵削，賢士永哀念窮迫。\n[釋文-4]：迫，音伯。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '1121/1121;1417/1417', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4883, '抑抑;密;秩秩;淸', '瘱瘱;𠂆𠂆', '抑抑[瘱瘱]1，密也。23秩秩[𠂆𠂆]4，淸也。56', '[釋文-1]：音億。補舍人曰：「威儀密靜也」\n[郭注-2]：威儀審諦。\n[釋文-3]：諦，音帝。\n[釋文-4]：直乙反\n[郭注-5]：德音淸泠。\n[釋文-6]：泠，郎丁反。', '釋訓弟三', '爾雅', '1;3', DEFAULT, DEFAULT, '5792/5792;5876;4468/4468;7202', '6738/6738;8365/8365', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4884, '甹夆;制曳', '𢖊𢓱;𤸪曳', '甹夆[𢖊𢓱]12，掣曳{制曳}[𤸪曳]也34。56', '[音釋-1]：普經。芳逢。\n[釋文-2]：普經反。孚逢反\n[音釋-3]：充世。\n[釋文-4]：本或作𤸪，同，充世反，說文云：「引而縱之。」餘世反。補孫炎曰：「相掣曳入於惡也」\n[郭注-5]：謂牽拕。\n[釋文-6]：扡，本或作拕，同，泰何、達可二反。廣雅云：「引也。」說文云：「曳也。」', '釋訓弟三', '爾雅', '1;2', DEFAULT, DEFAULT, '3056/3405;2796/9805', '1240/1241;7948/9805', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4885, '朔;北方', '', '朔1，北方也。2', '[釋文-1]：所角反。補舍人曰：「盡也。北方萬物盡，故言朔」\n[郭注-2]：謂幽朔。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '4276;5231/5455', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4886, '不俟;不來;不遹;不蹟;不徹;不道;勿念;勿忘', '不𥏳;不述;不轍', '不俟[不𥏳]1，不來也2。34不遹[不述]56，不蹟也7。89不徹[不轍]10，不道也。11勿念，勿忘也12。13', '[釋文-1]：𥏳，事已反，待也，宜從來，本今作俟字\n[釋文-2]：本或作倈、逨，同，力胎反\n[郭注-3]：不可待，是不復來。\n[釋文-4]：復，如字。\n[音釋-5]：述。\n[釋文-6]：古述字，一音聿\n[釋文-7]：子亦反，詩云：「念彼不蹟。」傳云：「不循道也」\n[郭注-8]：言不循軌跡也。\n[釋文-9]：循，音巡。\n[釋文-10]：直列反\n[郭注-11]：徹亦道也。\n[釋文-12]：音亡，下同\n[郭注-13]：勿念，念也。', '釋訓弟三', '爾雅', '1;3;5', DEFAULT, DEFAULT, '7706/4993;7706/3348;7706/1157;7706/1093;7706/1997;7706/1205;6073/6721;6073/6846', '7706/3349;7706/1107;7706/9615', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4887, '蕿;諼;忘', '蕿', '萲{蕿}12、諼[蕿]34，忘也。56', '[音釋-1]：袁。\n[釋文-2]：施音袁，謝許袁反，郭云：「義見伯兮。」詩云：「焉得萲草。」毛傳云：「萲草令人善忘。」則謝讀爲是\n[音釋-3]：喧。\n[釋文-4]：許爰反\n[郭注-5]：義見伯兮、考盤詩。\n[釋文-6]：槃，步干反，本又作盤。', '釋訓弟三', '爾雅', '2', DEFAULT, DEFAULT, '283;1594;6846', '283', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4888, '每;雖', '', '每有，雖也。1', '[郭注-1]：詩曰：「每有良朋。」辭之雖也。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '235;8801', '', NULL, NULL);
INSERT INTO `t_han_nga` VALUES (4889, '饎;酒食', '', '饎1，酒食也。23', '[釋文-1]：尺志反，字林云：「熟食也。」又充之反。舍人本作喜，釋云：「古曰饎」\n[郭注-2]：猶今云饎饌，皆一語而兼通。\n[釋文-3]：饌，仕戀反。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '3219;9807/3206', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4890, '舞號;雩', '', '舞號12，雩也34。56', '[音釋-1]：毫。\n[釋文-2]：戸高反，孫炎云：「雩之祭，有舞有號」\n[音釋-3]：于。\n[釋文-4]：音于，祭名\n[郭注-5]：雩之祭，舞者吁嗟而請雨。\n[釋文-6]：吁，許于反。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '3380/3069;7570', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4891, '曁;不及', '', '曁12，不及也。34', '[音釋-1]：忌。\n[釋文-2]：其器反\n[郭注-3]：公羊傳曰：「及，我欲之。曁，不得巳。」「曁不得巳。」是不得及。\n[釋文-4]：已，音以。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '4286;7706/1918', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4892, '蠢;不遜', '不愻', '蠢1，不遜[不愻]也。2', '[釋文-1]：昌允反\n[郭注-2]：蠢動爲惡，不謙遜也。', '釋訓弟三', '爾雅', '2', DEFAULT, DEFAULT, '8971;7706/1140', '7706/6759', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4893, '切差', '', '「如切如磋{切差}1」，道學也。2', '[釋文-1]：七何反\n[郭注-2]：骨象須切磋而为器，人須學問以成徳。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '2760/3029', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4894, '琢䃺;脩', '修', '「如琢如磨{琢䃺}1」，自脩[修]也。2', '[釋文-1]：丁角反，治玉也，本或作𤥨，非也\n[郭注-2]：玉石之被雕磨，猶人自脩飾。', '釋訓弟三', '爾雅', '2', DEFAULT, DEFAULT, '145/6049;2686', '5716', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4895, '瑟;僩;恂栗', '', '「瑟兮僩兮12」，恂慄{恂栗}也3。45', '[音釋-1]：限。\n[釋文-2]：本或作𢵧，同，下板反，郭音𥳑\n[釋文-3]：音荀，郭音峻，鄭玄注禮記同，云：「謂嚴慄也。」謝私尹反。音栗\n[郭注-4]：恆戰竦。\n[釋文-5]：竦，思勇反。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '8408;5009;6761/4393', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4896, '赫;烜', '', '「赫兮烜兮12」，威儀也。3', '[音釋-1]：喧。\n[釋文-2]：火格反。吁遠反，烜者，光明宣著，今並作喧字，音同\n[郭注-3]：貌光宣。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '6595;6523', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4897, '斐;諼;盛;忘', '蕿', '「有斐君子1，終不可諼[蕿]兮。」2道盛德至善，民之不能忘也。3', '[釋文-1]：孚尾反\n[郭注-2]：斐，文貌。\n[郭注-3]：常思詠。', '釋訓弟三', '爾雅', '2', DEFAULT, DEFAULT, '5726;1594;3141;6846', '283', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4898, '微;𡰒', '未', '「旣微且尰12」，骭瘍爲微[未]34，腫足爲尰{𡰒}5。67', '[音釋-1]：時勇。\n[釋文-2]：如字，字書作癓，三蒼云：「足創。」本或作𡰒，同，並籀文瘇字也，蜀勇、時踵二反。補孫炎曰：「皆水溼之疾也」\n[音釋-3]：莧。羊。\n[釋文-4]：下諫反，郭作扞，音古案反，謂腳脛也。音羊\n[釋文-5]：之勇反\n[郭注-6]：骭，腳脛。瘍，瘡。\n[釋文-7]：脛，戸定反。創，初良反。', '釋訓弟三', '爾雅', '1', DEFAULT, DEFAULT, '1236;4758', '9801', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4899, '鑊;煑', '', '「是刈是穫1」，鑊2，煑之也3。45', '[釋文-1]：乂，音刈，本亦作刈。補舍人曰：「是刈，刈取之；是濩，煮治之」\n[釋文-2]：又作濩，同，戸郭反\n[釋文-3]：之與反\n[郭注-4]：煑葛爲絺綌。\n[釋文-5]：絺，丑尼反。綌，去逆反。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '9300;1878', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4900, '武;迹;敏;拇', '', '「履帝武敏」，武，迹也；敏，拇也12。34', '[音釋-1]：𠭇。\n[釋文-2]：如字，足大指。舍人本作𠭇，釋云：「古者姜嫄，履天帝之迹於畎𠭇之中，而生后稷」\n[郭注-3]：拇迹，大指處。\n[釋文-4]：處，昌慮反。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '8397;1093;1999;7831', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4901, '善;孝;友', '', '「張仲孝友1」，2善父母爲孝，善兄弟爲友。', '[釋文-1]：補李巡曰：張，姓；仲，字。其人孝，故稱孝友\n[郭注-2]：周宣王時賢臣。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '1735;5386;1930', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4902, '宿宿;信信', '㑗㑗', '「有客宿宿1」，言再宿也。「有客信信[㑗㑗]」，言四宿也。23', '[釋文-1]：先六反\n[郭注-2]：再宿爲信，重言之，故知四宿。\n[釋文-3]：重，直用反。', '釋訓弟三', '爾雅', '2', DEFAULT, DEFAULT, '4602/4602;1523/1523', '5187/5187', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4903, '媛;彥', '', '美女爲媛12，34美士爲彥5。6', '[音釋-1]：于眷。\n[釋文-2]：爲眷反\n[郭注-3]：所以結好媛。\n[釋文-4]：好，呼報反。援，音媛，本今作媛。\n[釋文-5]：喭，音彥，本今作彥。補舍人曰：「國有美士，爲人所言道」\n[郭注-6]：人所彥詠。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '8263;5724', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4904, '虛;徐', '亏', '「其虛[亏]其徐1」，威儀容止也。2', '[釋文-1]：補孫炎曰：「虛、徐，威儀謙退也」\n[郭注-2]：雍容都雅之貌。', '釋訓弟三', '爾雅', '1', DEFAULT, DEFAULT, '5234;1238', '3070', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4905, '名', '䫤', '「猗嗟名兮12」，目上爲名[䫤]3。45', '[音釋-1]：於宜。\n[釋文-2]：於宜反\n[釋文-3]：補孫炎曰：「目上平博」\n[郭注-4]：眉眼之閒。\n[釋文-5]：眼，五限反，或作目。', '釋訓弟三', '爾雅', '1', DEFAULT, DEFAULT, '836', '5603', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4906, '式微', '', '「式微式微」者，微乎微者也。1', '[郭注-1]：言至微。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '3031/1236', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4907, '之;是', '', '「之子」者1，是子也。23', '[釋文-1]：補李巡曰：「論五方之言是子也」\n[郭注-2]：斥所詠。\n[釋文-3]：斥，音尺。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '3861;1089', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4908, '徒;輦', '', '「徒御不驚」，徒，輦者也。12', '[郭注-1]：步挽輦車。\n[釋文-2]：挽，本亦作輓，同，亡遠反。聲類云：「引也。」車，音居。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '1099;9606', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4909, '亶裼;肉袒', '但裼;肉但', '襢裼{亶裼}[但裼]12，肉袒[肉但]也。34', '[音釋-1]：但。息。\n[釋文-2]：本或作袒，同，徒坦、徒丹二反，下同。蘇歷反\n[郭注-3]：脫衣而見體。\n[釋文-4]：脫，佗活反。見，賢遍反。', '釋訓弟三', '爾雅', '1;2', DEFAULT, DEFAULT, '3344/5348;2594/5342', '5169/5348;2594/5169', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4910, '暴虎;徒搏;馮河;徒涉', '𧇭虎;淜河', '暴虎[𧇭虎]1，徒搏也2。3馮河[淜河]45，徒涉也。67', '[釋文-1]：步報反。補舍人曰：「無兵空手搏之」\n[釋文-2]：逋莫反，郭音付\n[郭注-3]：空手執也。\n[音釋-4]：平。\n[釋文-5]：字又作憑，皮冰反，依字當作淜，說文云：「無船渡河」\n[郭注-6]：無舟楫。\n[釋文-7]：檝，本或作擑，又作接，同，子葉、才入二反，方言曰：「檝，橈也。」說文云：「檝，舟棹也。」釋名曰：「在旁撥水曰櫂，又謂之檝。檝，捷也。」', '釋訓弟三', '爾雅', '1;3', DEFAULT, DEFAULT, '4248/3118;1099/7866;6203/6986;1099/7477', '3133/3118;7282/6986', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4911, '籧篨;口柔;戚施;面柔;夸毗;體柔', '𪓰𪓿;侉𢟡', '籧篨12，口柔也。3戚施[𪓰𪓿]4，面柔也。5夸毗[侉𢟡]67，體柔也。89', '[音釋-1]：渠。除。\n[釋文-2]：本或作𥴧，同，巨魚反。直閭反，舍人云：「籧篨，巧言也。」李云：「籧篨，巧言好辭、以口饒人謂之口柔。」孫、郭並云：「籧篨之疾不能俯，口柔之人視人顏色，常亦不伏，因以名云」\n[郭注-3]：籧篨之疾不能俯，口柔之人視人顏色，常亦不伏，因以名云。\n[釋文-4]：七歷反，下式支反，舍人曰：「令色誘人。」李曰：「和顏悅色以誘人，是謂面柔也。」賈逵注國語曰：「僂也。」孫、郭並云：「戚施之疾不能仰，面柔之人常俯，似之，因以名云。」字書作𧠪䙾，同\n[郭注-5]：戚施之疾不能仰，面柔之人常俯似之，亦以名云。\n[音釋-6]：誇。\n[釋文-7]：苦瓜反，下鼻尸反。舍人曰：「卑身屈己也。」樊引詩云：「無爲夸毗。」李、孫、郭云：「屈己卑身，以柔順人也。」字書作䠸𨉊\n[郭注-8]：屈己卑身，以柔順人也。\n[釋文-9]：己，音紀。', '釋訓弟三', '爾雅', '3;5', DEFAULT, DEFAULT, '2913/2914;780/3609;8402/4303;5697/3609;6602/6702;2587/3609', '9001/9002;5160/6954', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4912, '波娑;舞', '媻娑', '婆娑{波娑}[媻娑]1，舞也。2', '[釋文-1]：素河反。補李巡曰：「盤辟舞也」\n[郭注-2]：舞者之容。', '釋訓弟三', '爾雅', '1', DEFAULT, DEFAULT, '7175/8256;3380', '8255/8256', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4913, '擗;拊', '捭', '擗[捭]12，拊心也34。56', '[音釋-1]：婢亦。\n[釋文-2]：辟，婢亦反，字宜作擗，詩云：「寤擗有摽」\n[音釋-3]：撫。\n[釋文-4]：本亦作撫，同，芳武反\n[郭注-5]：謂椎胷也。\n[釋文-6]：椎，本或作槌，同，直追反，字林云：「擊也。」胷，音凶。', '釋訓弟三', '爾雅', '1', DEFAULT, DEFAULT, '8021;7892', '8058', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4914, '矜;憐;撫掩', '', '矜1、憐2，撫掩之也。34', '[釋文-1]：几陵反\n[釋文-2]：力堅反\n[郭注-3]：撫掩猶撫拍，謂慰恤也。\n[釋文-4]：拍，普伯反。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '9512;6962;7922/8035', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4915, '𪑝', '', '緎{𪑝}12，羔裘之縫也34。5', '[音釋-1]：域。\n[釋文-2]：許域反，又音域\n[音釋-3]：逢。\n[釋文-4]：扶用反\n[郭注-5]：縫飾羔皮之名。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '6572', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4916, '殿𡲴;呻', '唸吚', '殿屎{殿𡲴}[唸吚]12，呻也34。5', '[音釋-1]：丁練。希。\n[釋文-2]：丁練反，下虛伊反。或作敜吹，又作（⿱殿心）䐖，說文作唸吚。郭音香惟反。又上音丁念反、下許利反\n[音釋-3]：申。\n[釋文-4]：音申\n[郭注-5]：呻吟之聲。', '釋訓弟三', '爾雅', '1', DEFAULT, DEFAULT, '1970/1135;920', '917/918', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4917, '幬;帳', '', '幬謂之帳12。3', '[音釋-1]：紬。\n[釋文-2]：本或作𢃖，同，直留反。陟亮反\n[郭注-3]：今江東亦謂帳爲幬。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '4879;4882', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4918, '侜張;誑', '譸張', '侜張[譸張]12，誑也3。45', '[音釋-1]：張留。\n[釋文-2]：本或作倜，同，張留反\n[釋文-3]：俱放反\n[郭注-4]：書曰：「無或侜張爲幻。」幻惑欺誑人者。\n[釋文-5]：幻，胡辦反。', '釋訓弟三', '爾雅', '1', DEFAULT, DEFAULT, '5124/8488;1605', '1612/8488', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4919, '誰昔;昔', '', '誰昔，昔也。1', '[郭注-1]：誰，發語辭。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '1705/4252;4252', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4920, '不辰;不時', '', '不辰，不時也。1', '[郭注-1]：辰亦時也。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '7706/9795;7706/4191', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4921, '曲;罶', '', '凡曲者爲罶12。34', '[音釋-1]：力九。\n[釋文-2]：本或作𦊑，同，力九反\n[郭注-3]：毛詩傳曰：「罶，曲梁也。」凡以簿爲魚笱者，名爲罶。\n[釋文-4]：薄，蒲博反，本今作簿。笱，音苟。', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '8444;4828', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4922, '鬼;歸', '', '鬼之爲言歸也。1', '[郭注-1]：尸子曰：「古者謂死人爲歸人。」', '釋訓弟三', '爾雅', '', DEFAULT, DEFAULT, '5821;1073', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4923, '考;妣', '', '父爲考，母爲妣12。34', '[音釋-1]：比。\n[釋文-2]：必里反\n[郭注-3]：禮記曰：「生曰父、母、妻，死曰考、妣、嬪。」今世學者從之。按尚書曰：「大傷厥考心。」「事厥考厥長。」「聰聽祖考之彝訓。」「如䘮考妣。」公羊傳曰：「惠公者何？隱之考也。仲子者何？桓之母也。」蒼頡篇曰：「考妣延年。」書曰：「嬪于虞。」詩曰：「韋嬪于京。」周禮有「九嬪」之官，明此非死生之異稱矣。其義猶今謂兄爲晜，妹爲媦，卽是此例也。\n[釋文-4]：𡣕，毗真反。長，丁丈反，下皆同。彝，音夷。喪，息浪反，又如字。頡，戸結反。稱，尺證反。晜，音昆，本亦作昆，下同。媦，于貴反，廣雅云：「媦，妹也。」說文云：「楚人謂女弟爲媦。」纂文云：「河南人云妹媦也。」', '釋親弟四', '爾雅', '', DEFAULT, DEFAULT, '5385;8148', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4924, '王父;王母;曾祖王父;曾祖王母;高祖王父;高祖王母', '', '父之考爲王父，父之妣爲王母。1王父之考爲曾祖王父，王父之妣爲曾祖王母，23曾祖王父之考爲高祖王父，曾祖王父之妣爲高祖王母。4', '[郭注-1]：加王者，尊之也。\n[郭注-2]：曾猶重也。\n[釋文-3]：重，直龍反，下皆同。\n[郭注-4]：高者，言最在上。', '釋親弟四', '爾雅', '', DEFAULT, DEFAULT, '78/1908;78/8141;710/36/78/1908;710/36/78/8141;3320/36/78/1908;3320/36/78/8141', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4925, '從祖祖父;從祖祖母;世父;叔父', '', '父之世父、叔父爲從祖祖父，父之世母、叔母爲從祖祖母1。23父之晜弟4，先生爲世父，後生爲叔父。56', '[音釋-1]：從，並才用切\n[郭注-2]：從祖而別，世統異故。\n[釋文-3]：別，彼列反，又如字，下皆同。\n[音釋-4]：昆\n[郭注-5]：世有爲嫡者，嗣世統故也。\n[釋文-6]：適，本或作嫡，同，丁歷反。', '釋親弟四', '爾雅', '', DEFAULT, DEFAULT, '5227/36/36/1908;5227/36/36/8141;1473/1908;1924/1908', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4926, '兄;弟;姊;妹;姑', '', '男子先生爲兄，後生爲弟。男子謂女子先生爲姊，後生爲妹。父之姊妹爲姑1。', '[釋文-1]：補孫炎曰：「姑之言古，尊老之名也」', '釋親弟四', '爾雅', '', DEFAULT, DEFAULT, '5464;3401;8149;8150;8146', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4927, '從祖父;族父;族晜弟;親同姓;從父晜弟', '', '父之從父晜弟爲從祖父，父之從祖晜弟爲族父。族父之子相謂爲族晜弟，族晜弟之子相謂爲親同姓。1兄之子、弟之子相謂爲從父晜弟。2', '[郭注-1]：同姓之親無服屬。\n[郭注-2]：從父而別。', '釋親弟四', '爾雅', '', DEFAULT, DEFAULT, '5227/36/1908;4312/1908;4312/0/3401;5514/4803/8112;5227/1908/0/3401', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4928, '孫;曾孫;玄孫;來孫;昆孫;仍孫;雲孫', '𥊽孫', '子之子爲孫，1孫之子爲曾孫，2曾孫之子爲玄孫，3玄孫之子爲來孫4，5來孫之子爲晜孫{昆孫}[𥊽孫]，67晜孫之子爲仍孫，8仍孫之子爲雲孫。9', '[郭注-1]：孫猶後也。\n[郭注-2]：曾猶重也。\n[郭注-3]：玄者，言親屬微昧也。\r\n[釋文-4]：徠，音來，本亦作來\n[郭注-5]：言有往來之親。\n[郭注-6]：晜，後也。汲冢竹書曰：「不窟之晜孫。」\n[釋文-7]：汲，居及反。冢，竹奉反。窋，丁律反，又丁滑反。不窋，后稷之子。\n[郭注-8]：仍亦重也。\n[郭注-9]：言輕遠如浮雲。', '釋親弟四', '爾雅', '5', DEFAULT, DEFAULT, '8514;710/8514;2507/8514;3348/8514;4256/8514;5049/8514;7578/8514', '3402/8514', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4929, '王姑;曾祖王姑;高祖王姑;從祖姑;族祖姑', '', '王父之姊妹爲王姑，曾祖王父之姊妹爲曾祖王姑，高祖王父之姊妹爲高祖王姑。父之從父姊妹爲從祖姑，父之從祖姊妹爲族祖姑。', '', '釋親弟四', '爾雅', '', DEFAULT, DEFAULT, '78/8146;710/36/78/8146;3320/36/78/8146;5227/36/8146;4312/36/8146', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4930, '從祖王母;族祖王母;世母;叔母;從祖母;族祖母', '', '父之從父晜弟之母爲從祖王母，父之從祖晜弟之母爲族祖王母。父之兄妻爲世母，父之弟妻爲叔母。父之從父晜弟之妻爲從祖母，父之從祖晜弟之妻爲族祖母。', '', '釋親弟四', '爾雅', '', DEFAULT, DEFAULT, '5227/36/78/8141;4312/36/78/8141;1473/8141;1924/8141;5227/36/8141;4312/36/8141', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4931, '族曾王父;族曾王母', '', '父之從祖祖父爲族曾王父，父之從祖祖母爲族曾王母。', '', '釋親弟四', '爾雅', '', DEFAULT, DEFAULT, '4312/710/78/1908;4312/710/78/8141', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4932, '庶母', '', '父之妾爲庶母。', '', '釋親弟四', '爾雅', '', DEFAULT, DEFAULT, '5958/8141', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4933, '祖;王父;昆;兄', '𥊽', '祖，王父也。晜{昆}[𥊽]，兄也。1', '[郭注-1]：今江東人通言晜。', '釋親弟四', '爾雅', '3', DEFAULT, DEFAULT, '36;78/1908;4256;5464', '3402', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4934, '外王父;外王母;外曾王父;外曾王母', '', '母之考爲外王父，母之妣爲外王母。母之王考爲外曾王父，母之王妣爲外曾王母。1', '[郭注-1]：異姓故言外。', '釋親弟四', '爾雅', '', DEFAULT, DEFAULT, '4333/78/1908;4333/78/8141;4333/710/78/1908;4333/710/78/8141', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4935, '舅;從舅', '', '母之晜弟爲舅1，母之從父晜弟爲從舅2。', '[釋文-1]：補孫炎曰：「舅之言舊，尊長之稱」\n[釋文-2]：才用反，下注「娣從」同，下音臼', '釋親弟四', '爾雅', '', DEFAULT, DEFAULT, '9210;5227/9210', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4936, '從母;從母晜弟;從母姊妹', '', '母之姊妹爲從母，從母之男子爲從母晜弟，其女子子爲從母姊妹。', '', '釋親弟四', '爾雅', '', DEFAULT, DEFAULT, '5227/8141;5227/8141/0/3401;5227/8141/8149/8150', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4937, '外舅;外姑', '', '妻之父爲外舅，妻之母爲外姑。12', '[郭注-1]：「謂我舅者，吾謂之甥。」然則亦宜呼壻爲甥，孟子曰：「帝館甥于二室」是也。\n[釋文-2]：甥，音生。壻，音細。館，古半反。', '釋親弟四', '爾雅', '', DEFAULT, DEFAULT, '4333/9210;4333/8146', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4938, '甥', '', '{甥}姑之子爲甥，舅之子爲甥，妻之晜弟爲甥，姊妹之夫爲甥。12', '[郭注-1]：四人體敵，故更相爲甥。甥猶生也。今人相呼蓋依此。\n[釋文-2]：更，音庚。', '釋親弟四', '爾雅', '', DEFAULT, DEFAULT, '9211', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4939, '姨;私', '', '妻之姊妹同出爲姨。1女子謂姊妹之夫爲私2。34', '[郭注-1]：同出謂俱巳嫁。詩曰：「邢侯之姨。」\n[釋文-2]：補孫炎曰：「私，無正親之言」\n[郭注-3]：詩曰：「譚公維私。」\n[釋文-4]：譚，大南反。', '釋親弟四', '爾雅', '', DEFAULT, DEFAULT, '8155;4431', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4940, '出;姪;離孫;歸孫;外孫', '', '男子謂姊妹之子爲出。1女子謂晜弟之子爲姪23。4謂出之子爲離孫，謂姪之子爲歸孫。女子子之子爲外孫。', '[郭注-1]：公羊傳曰：「蓋舅出。」\n[音釋-2]：徒結。\n[釋文-3]：大結反，字林云：「兄女。」又丈乙反\n[郭注-4]：左傳曰：「姪其從姑。」', '釋親弟四', '爾雅', '', DEFAULT, DEFAULT, '3865;8154;2294/8514;1073/8514;4333/8514', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4941, '㠯;娣', '始', '女子同出，謂先生爲姒{㠯}[始]12，後生爲娣34。56', '[音釋-1]：似。\n[釋文-2]：音似\n[音釋-3]：第。\n[釋文-4]：大計反\n[郭注-5]：同出謂俱嫁事一夫。公羊傳曰：「諸侯娶一國，二國往媵之，以姪娣從。娣者何？弟也。」此卽其義也。\n[釋文-6]：娶，七具反。媵，以證反。', '釋親弟四', '爾雅', '1', DEFAULT, DEFAULT, '9798;8151', '8184', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4942, '嫂;婦', '', '女子謂兄之妻爲嫂1，弟之妻爲婦。2', '[釋文-1]：㛮，素早反，本今作嫂\n[郭注-2]：猶今言新婦是也。', '釋親弟四', '爾雅', '', DEFAULT, DEFAULT, '8153;8132', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4943, '娣婦;㠯婦', '始婦', '長婦謂稚婦爲娣婦1，娣婦謂長婦爲姒婦{㠯婦}[始婦]。23', '[釋文-1]：稺，直吏反，又作稚\n[郭注-2]：今相呼先後，或云妯娌。\n[釋文-3]：蘇練反。胡遘反，廣雅云：「先後，妯娌。」韋昭云：「先謂姒，後謂娣。」妯娌，音逐，下音里，廣雅云：「先後也。」郭注方言云：「今關西兄弟婦相呼爲妯娌。」', '釋親弟四', '爾雅', '2', DEFAULT, DEFAULT, '8151/8132;9798/8132', '8184/8132', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4944, '舅;姑;君舅;君姑;先舅;先姑;少姑', '', '婦稱夫之父曰舅1，稱夫之母曰姑。姑、舅在則曰君舅、君姑，沒則曰先舅、先姑。2謂夫之庶母爲少姑3。', '[釋文-1]：稱，如字\n[郭注-2]：國語曰：「吾聞之先姑。」\n[釋文-3]：詩照反', '釋親弟四', '爾雅', '', DEFAULT, DEFAULT, '9210;8146;839/9210;839/8146;5472/9210;5472/8146;705/8146', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4945, '兄公;叔;女公;女妹', '', '夫之兄爲兄公12，34夫之弟爲叔，夫之姊爲女公，夫之女弟爲女妹。5', '[音釋-1]：鍾。\n[釋文-2]：妐，音鍾，本今作公\n[郭注-3]：今俗呼兄鐘，語之轉耳。\n[釋文-4]：轉，丁戀反，又如字。\n[郭注-5]：今謂之女妹是也。', '釋親弟四', '爾雅', '', DEFAULT, DEFAULT, '5464/716;1924;8111/716;8111/8150', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4946, '婦;嫡婦;庶婦', '適婦', '子之妻爲婦，長婦爲嫡婦[適婦]1，衆婦爲庶婦。', '[音釋-1]：的', '釋親弟四', '爾雅', '2', DEFAULT, DEFAULT, '8132;8239/8132;5958/8132', '1109/8132', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4947, '壻', '', '女子子之夫爲壻。', '', '釋親弟四', '爾雅', '', DEFAULT, DEFAULT, '227', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4948, '姻;婚', '', '壻之父爲姻1，婦之父爲婚。', '[釋文-1]：音因', '釋親弟四', '爾雅', '', DEFAULT, DEFAULT, '8130;8129', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4949, '宗族;兄弟', '', '父之黨爲宗族，母與妻之黨爲兄弟。', '', '釋親弟四', '爾雅', '', DEFAULT, DEFAULT, '4624/4312;5464/3401', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4950, '婚姻;亞', '', '婦之父母、壻之父母相謂爲婚姻。兩壻相謂爲亞1。23', '[釋文-1]：一駕反，又作婭\n[郭注-2]：詩曰：「瑣瑣姻亞。」今江東人呼同門爲僚壻。\n[釋文-3]：瑣，桑果反。僚，力彫反。', '釋親弟四', '爾雅', '', DEFAULT, DEFAULT, '8129/8130;9725', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4951, '婚兄弟;姻兄弟', '', '婦之黨爲婚兄弟，壻之黨爲姻兄弟。1', '[郭注-1]：古者皆謂婚姻爲兄弟。', '釋親弟四', '爾雅', '', DEFAULT, DEFAULT, '8129/5464/3401;8130/5464/3401', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4952, '嬪;婦', '', '嬪1，婦也。2', '[音釋-1]：頻\n[郭注-2]：書曰：「嬪于虞。」', '釋親弟四', '爾雅', '', DEFAULT, DEFAULT, '8249;8132', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4953, '舅;甥', '', '謂我舅者，吾謂之甥也。', '', '釋親弟四', '爾雅', '', DEFAULT, DEFAULT, '9210;9211', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4954, '宮;室', '', '宮謂之室，室謂之宮。1', '[郭注-1]：皆所以通古今之異語，明同實而兩名。', '釋宮弟五', '爾雅', '', DEFAULT, DEFAULT, '4630;4559', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4955, '扆;家;序', '', '牖戸之閒謂之扆12，34其內謂之家。5東西墻謂之序6。78', '[音釋-1]：𠋣。\n[釋文-2]：羊九反。於豈反，郭音依，又意尾反\n[郭注-3]：窻東、戸西也。禮云：「斧扆者，以其所在處名之。」\n[釋文-4]：窻，楚江反。\n[郭注-5]：今人稱家，義出於此。\n[釋文-6]：補舍人曰：「殿東西堂，序尊卑處」\n[郭注-7]：所以序別內外。\n[釋文-8]：別，彼列反。', '釋宮弟五', '爾雅', '', DEFAULT, DEFAULT, '7729;4557;5938', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4956, '奥;屋漏;宧;窔', '𡧮', '西南隅謂之奥1，2西北隅謂之屋漏3，45東北隅謂之宧67，89東南隅謂之㝔{窔}[𡧮]1011。1213', '[釋文-1]：本或作隩，同，於耗反，尚書并說文皆云：「奥，室也。」孔注論語云：「內也。」鄭注禮記云：「主也。」廣雅云：「藏也」\n[郭注-2]：室中隱奥之處。\n[釋文-3]：補舍人曰：「古者徹屋西北䨾，以炊浴汲者訖而復之，古謂之屋漏。」孫炎曰：「當室之白，日光所漏入也」\n[郭注-4]：詩曰：「尚不媿於屋漏。」其義未詳。\n[釋文-5]：媿，居位反。\n[音釋-6]：夷。\n[釋文-7]：音怡，李云：「東北者陽氣始起，育養萬物，故曰宧，宧，養也。」說文訓同，與周易頤卦養義同。補舍人同李。孫炎曰：「日側之明」\n[郭注-8]：宧，見禮，亦未詳。\n[釋文-9]：見，賢遍反，他皆放此。\n[音釋-10]：要。\n[釋文-11]：烏叫反，字林同，郭又音杳，說文云：「深貌。」本或作𡧮，又作宎，同。補舍人曰：「東南萬物生，蟄蟲必出，無不由戸穾」\n[郭注-12]：禮曰：「婦室聚㝔。」㝔亦隱闇。\n[釋文-13]：埽，素老反。', '釋宮弟五', '爾雅', '4', DEFAULT, DEFAULT, '4564;5422/7446;4562;4676', '4563', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4957, '失;閾;棖;楔;楣;梁;樞;椳;落時;𢨪;扈', '𣕋;𣥺;𣔺', '柣{失}[𣕋]木謂之閾12，3棖[𣥺]謂之楔45，67楣[𣔺]謂之梁8，9樞謂之椳1011。12樞達北方謂之落時13，1415落時謂之𢨪{扈}1617。18', '[音釋-1]：千結。域。\n[釋文-2]：郭千結反，顧丈乙反，呂伯雍大一反，廣雅云：「砌也。」域、洫二音\n[郭注-3]：閾，門限。\n[音釋-4]：古黠。\n[釋文-5]：直庚反。古黠反，李謂「閫上兩旁木」\n[郭注-6]：門兩旁木。\n[釋文-7]：旁，步郎反。\n[釋文-8]：亡悲反，或作𣔺，亡報反，埤蒼云：「梁也。」呂伯雍云：「門樞之橫梁。」說文云：「楣，秦名屋櫋聯也，齊謂之檐，楚謂之梠」\n[郭注-9]：門戸上橫梁。\n[音釋-10]：昌朱。於回。\n[釋文-11]：昌朱反。烏回反，郭又吾回反，呂、沈一罪反\n[郭注-12]：門戸扉樞。\n[釋文-13]：大末反\n[郭注-14]：門持樞者，或逹北檼以爲固也。\n[釋文-15]：檼，於靳反，字林云：「棼也。」廣雅云：「棟也。」下同。\n[音釋-16]：士。\n[釋文-17]：本或作戺，同，音俟，廣雅云：「戺，砌也。」本或作扈，同，音戸\n[郭注-18]：道二名也。', '釋宮弟五', '爾雅', '1;3;5', DEFAULT, DEFAULT, '7978;7751;3736;3669;3643;3787;3651;3661;549/4191;7828;4025', '3664;1066;3662', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4958, '垝;坫;牆;墉', '庪', '垝[庪]謂之坫12，34牆謂之墉5。67', '[音釋-1]：店。\n[釋文-2]：居毀反，本又作庋，同。丁念反，說文云：「屏牆」\n[郭注-3]：在堂隅。坫，㙐也。\n[釋文-4]：墆，達結、達計二反，高貌也，或作㙐，丁果反，本或作端。\n[釋文-5]：音容，本或作䧡，同。補李巡曰：「謂垣牆也」\n[郭注-6]：書曰：「旣勤垣墉。」\n[釋文-7]：垣，音袁。', '釋宮弟五', '爾雅', '1', DEFAULT, DEFAULT, '9111;9058;3347;9084', '5975', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4959, '鏝;杇;甚;虔;地;黝;牆;堊', '砧', '鏝謂之杇12，3椹{甚}[砧]謂之榩{虔}45，67地謂之黝89，1011牆謂之堊1213。14', '[音釋-1]：烏。\n[釋文-2]：本或作槾，又作墁，同，亡旦、武安二反，說文云：「鐵杇也。」音烏，又音胡，李云：「泥鏝，一名杇，塗工之作具。」說文云：「所以塗也，秦謂之杇，關東謂之槾」\n[郭注-3]：泥鏝。\n[音釋-4]：砧。虔。\n[釋文-5]：本或作砧，同，張林反。音虔，本亦作虔，詩云：「方斲是虔」\n[郭注-6]：斫木櫍也。\n[釋文-7]：斫，音灼。櫍，音質。\n[音釋-8]：於糾。\n[釋文-9]：於糾反，郭殃柳反\n[郭注-10]：黑飾地也。\n[釋文-11]：飾，音式。\n[音釋-12]：於故。\n[釋文-13]：於故反，又於各反\n[郭注-14]：白飾牆也。', '釋宮弟五', '爾雅', '3', DEFAULT, DEFAULT, '9370;3659;3042;3112;9022;6553;3347;9063', '6064', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4960, '樴;杙;楎;臬;共;閣', '杠;格', '樴謂之杙12，34在牆者謂之楎56，78在地者謂之臬910，11大者謂之栱{共}[杠]12，長者謂之閣[格]13。14', '[音釋-1]：徒得。亦。\n[釋文-2]：音特，又之力反。羊式、羊特二反，下句「雞棲於弋」音同\n[郭注-3]：橜也。\n[釋文-4]：橜，其厥反，後注同。\n[音釋-5]：暉。\n[釋文-6]：許韋反\n[郭注-7]：禮記曰：「不敢縣於夫之楎箷。」\n[釋文-8]：縣，音玄。箷，羊支反，字林云：「竿也。」本或作椸，音同，字林云：「榻前机也。」\n[音釋-9]：魚列。\n[釋文-10]：魚列反\n[郭注-11]：卽門橜也。\n[釋文-12]：九勇反，郭又音邛\n[釋文-13]：音各\n[郭注-14]：別杙所在長、短之名。', '釋宮弟五', '爾雅', '5;6', DEFAULT, DEFAULT, '3740;3480;3691;3761;1776;7761', '3676;3603', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4961, '闍;臺;榭', '', '闍謂之臺12，3有木者謂之榭4。5', '[音釋-1]：都。\n[釋文-2]：郭音都，徐特遮反\n[郭注-3]：積土四方。\n[釋文-4]：音謝\n[郭注-5]：臺上起屋。', '釋宮弟五', '爾雅', '', DEFAULT, DEFAULT, '7745;7712;3836', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4962, '桀;塒', '楬', '雞棲於弋爲榤{桀}[楬]12，鑿垣而棲爲塒34。56', '[音釋-1]：竭。\n[釋文-2]：栖，音西，下同，又作棲。巨列反，字林云：「弋也」\n[音釋-3]：袁。時。\n[釋文-4]：在各反。音時\n[郭注-5]：今寒鄉穿牆棲雞。皆見詩。\n[釋文-6]：穿，音川。', '釋宮弟五', '爾雅', '1', DEFAULT, DEFAULT, '3410;9082', '3833', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4963, '植;傳;突', '叀', '植謂之傳[叀]1，傳謂之突2。34', '[釋文-1]：音殖。音椽，下同\n[釋文-2]：本又作𣔻，徒忽反\n[郭注-3]：戸持鏁植也，見埤蒼。\n[釋文-4]：鏁，本又作瑣，桑果反。埤，避移反。', '釋宮弟五', '爾雅', '2', DEFAULT, DEFAULT, '3650;5105;4667', '2504', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4964, '杗廇;梁;楹;梲;閞;疾;栭;楶;棟;桴;桷;榱;閱;交;檐;樀', '䆯;枅', '杗廇謂之梁12，3其上楹謂之梲[䆯]45，67閞謂之槉{疾}[枅]89，1011栭謂之楶1213，1415棟謂之桴1617，1819桷謂之榱2021，2223桷直而遂謂之閱24，25直不受檐謂之交2627，28檐謂之樀2930。3132', '[音釋-1]：云。力又。\n[釋文-2]：音亡。力又反\n[郭注-3]：屋大梁也。\n[音釋-4]：拙。\n[釋文-5]：棳，之劣反，本或作梲，音同\n[郭注-6]：侏儒柱也。\n[釋文-7]：侏，音朱。儒，日朱反。\n[音釋-8]：卞。疾。\n[釋文-9]：皮彥反，本亦作弁，同。音疾。補舍人曰：「侏儒下小方木」\n[郭注-10]：柱上欂也。亦名枅，又曰㭼。\n[釋文-11]：欂，皮麥反。字林云：「櫨也。」又平各反。枅，音雞，字林音肩，云：「柱上方木也。」㭼，達合反。\n[音釋-12]：節。\n[釋文-13]：音而。作截反，又音節，本或作㮞，同，字林云：「欂櫨也。」舊本及論語、禮記皆作節\n[郭注-14]：卽櫨也。\n[釋文-15]：櫨，力奴反，卽欂也，字林云：「柱上柎也。」\n[音釋-16]：浮。\n[釋文-17]：多洞反。郭音浮，又音孚，字林云：「極也，棟也」\n[郭注-18]：屋檼。\n[釋文-19]：檼，於靳反。\n[音釋-20]：衰，\n[釋文-21]：音角。疎追反，說文云：「秦名屋椽也，周謂之榱，齊 魯謂之桷。」字林云：「周人名椽曰榱，齊 魯名榱曰桷」\n[郭注-22]：屋椽。\n[釋文-23]：椽，直專反。\n[釋文-24]：音悅\n[郭注-25]：謂五架屋際，椽正相當。\n[音釋-26]：簷。\n[釋文-27]：餘占反，下同\n[郭注-28]：謂五架屋際，椽不直，上檐交於檼上。\n[音釋-29]：滴。\n[釋文-30]：丁狄反，字從木旁作啇，郭又他赤反，字合辵旁作適\n[郭注-31]：屋梠。\n[釋文-32]：梠，音呂。', '釋宮弟五', '爾雅', '4;6', DEFAULT, DEFAULT, '3657/5929;3787;3628;3744;7747;4696;3637;3632;3625;3624;3640;3642;7783;6627;3647;3649', '8270;3635', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4965, '容;防', '邕', '容[邕]謂之防。12', '[郭注-1]：形如今牀頭小曲屏風，唱射者所以自防隱。見周禮。\n[釋文-2]：牀，助良反。屏，步形反。', '釋宮弟五', '爾雅', '1', DEFAULT, DEFAULT, '4589;9658', '7489', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4966, '連;移', '簃/疊', '連謂之移[簃/疊]1。23', '[音釋-1]：丈知\n[郭注-2]：堂樓閣邊小屋，今呼之「簃廚」，連觀也。\n[釋文-3]：簃，丈知反。廚，本或作𨅒，丈誅反。', '釋宮弟五', '爾雅', '2', DEFAULT, DEFAULT, '1168;4447', '3013/0/4319', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4967, '薄;佻', '迫', '屋上薄[迫]謂之筄{佻}12。34', '[音釋-1]：曜。\n[釋文-2]：音曜，又羊昭反\n[郭注-3]：屋笮。\n[釋文-4]：𥯧，阻格反，本或作笮。', '釋宮弟五', '爾雅', '1', DEFAULT, DEFAULT, '561;5129', '1182', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4968, '鄉;位;宁;樹', '𢾅', '兩階閒謂之鄉12，3中庭之左右謂之位，4門屏之閒謂之宁56，78屏謂之樹[𢾅]9。10', '[音釋-1]：向。\n[釋文-2]：本又作曏，許亮反，注同\n[郭注-3]：人君南鄉，當階閒。\n[郭注-4]：羣臣之列位也。\n[音釋-5]：卑丼。佇。\n[釋文-6]：卑丼反。音佇。補李巡曰：「正門內兩塾閒」\n[郭注-7]：人君視朝，所宁立處。\n[釋文-8]：朝，直遥反。\n[釋文-9]：補舍人曰：「以垣當門蔽爲樹」\n[郭注-10]：小牆當門中。', '釋宮弟五', '爾雅', '4', DEFAULT, DEFAULT, '4187;5031;9721;3560', '2045', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4969, '祊;門', '', '閍{祊}謂之門12。34', '[音釋-1]：補耕。\n[釋文-2]：補耕反，說文作𥛱，云或作祊，「門內祭。先祖所彷徨也。」補舍人曰：「廟門也」\n[郭注-3]：詩曰：「祝祭於祊。」\n[釋文-4]：祊，音同閍。', '釋宮弟五', '爾雅', '', DEFAULT, DEFAULT, '37;7732', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4970, '正門;應門', '', '正門謂之應門1。2', '[釋文-1]：補舍人曰：「南向大門」\n[郭注-2]：朝門。', '釋宮弟五', '爾雅', '', DEFAULT, DEFAULT, '1087/7732;6713/7732', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4971, '觀;闕', '', '觀謂之闕12。3', '[音釋-1]：貫。\n[釋文-2]：古玩反\n[郭注-3]：宮門雙闕。', '釋宮弟五', '爾雅', '', DEFAULT, DEFAULT, '5486;7746', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4972, '闈;閨;閤;閎', '', '宮中之門謂之闈1，2其小者謂之閨。小閨謂之閤，3衖門謂之閎45。67', '[釋文-1]：音韋，劉昌宗儀禮音揮\n[郭注-2]：謂相通小門也。\n[郭注-3]：大小異名。\n[音釋-4]：巷。\n[釋文-5]：戸絳反，廣雅云：「道也。」聲類猶以爲巷字。獲耕反，左傳云：「高其閈閎」\n[郭注-6]：左傳曰：「盟諸僖閎。」閎，衖頭門。\n[釋文-7]：僖，許其反。', '釋宮弟五', '爾雅', '', DEFAULT, DEFAULT, '7734;7737;7738;7736', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4973, '塾', '', '門側之堂謂之塾12。34', '[音釋-1]：熟。\n[釋文-2]：音熟，劉儀禮又音育\n[郭注-3]：夾門堂也。\n[釋文-4]：夾，古合反。', '釋宮弟五', '爾雅', '', DEFAULT, DEFAULT, '9157', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4974, '橛;闑;闔;扉;閣', '', '橛謂之闑12。34闔謂之扉5。67所以止扉謂之閎{閣}8。910', '[音釋-1]：其月。魚列。\n[釋文-2]：其月反。魚列反\n[郭注-3]：門閫。\n[釋文-4]：閫，說文作梱，同，苦本反。鄭注禮記云：「閫，門限也。」\n[釋文-5]：胡臘反。音非\n[郭注-6]：公羊傳曰：「齒著于門闔。」\n[釋文-7]：著，直畧反。\n[釋文-8]：音宏，本亦作閣，音各，郭注本無此字\n[郭注-9]：門辟旁長橛也。左傳曰：「高其閈閎。」閎，長杙，卽門橜也。\n[釋文-10]：辟，匹亦反。閈，戸旦反，說文云：「閭也，汝南 平輿里門曰閈。」', '釋宮弟五', '爾雅', '', DEFAULT, DEFAULT, '3739;7750;7749;7723;7761', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4975, '令適;甓', '', '瓴甋{令適}謂之甓12。34', '[音釋-1]：靈。的。蒲覓。\n[釋文-2]：力丁反，詩傳作令，音陵。丁歷反，詩傳作適，音同。蒲覓反\n[郭注-3]：㼾甎也。今江東呼瓴甓。\n[釋文-4]：㼾，力斛反。甎，章㳂反，字林作塼，同。', '釋宮弟五', '爾雅', '', DEFAULT, DEFAULT, '5779/1109;8469', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4976, '壼;唐;陳', '場;墀', '宮中衖謂之壼12，3廟中路謂之唐[場]4，5堂途謂之陳[墀]。67', '[音釋-1]：苦本。\n[釋文-2]：𠁐，本或作壼，苦本反，郭、呂並丘屯反，或作韋\n[郭注-3]：巷閤閒道。\n[釋文-4]：䧜，音唐，本今作唐\n[郭注-5]：詩曰：「中唐有甓。」\n[郭注-6]：堂下至門徑也。\n[釋文-7]：徑，古定反。', '釋宮弟五', '爾雅', '2;3', DEFAULT, DEFAULT, '3918;878;9688', '9147;9064', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4977, '路;旅;除;路;場;猷;行;道', '䌛', '路、旅1，途{除}也。2路、場3、猷[䌛]、行，道也。4', '[釋文-1]：音呂\n[郭注-2]：途卽道也。\n[釋文-3]：直良反，字從昜，昜音陽\n[郭注-4]：博說「道」之異名。', '釋宮弟五', '爾雅', '6', DEFAULT, DEFAULT, '1410;4311;9692;1410;9147;6375;1266;1205', '8516', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4978, '道路;岐旁;劇旁;衢;康;劇驂;崇期;逵', '㽘', '一逹謂之道路，1二逹謂之岐旁2，3三逹謂之劇旁45，67四逹謂之衢8，9五逹謂之康[㽘]10，1112六逹謂之莊，1314七逹謂之劇驂15，1617八逹謂之崇期，18九逹謂之逵19。20', '[郭注-1]：長道。\n[釋文-2]：郭如字，樊本作𭍾，音支\n[郭注-3]：岐道旁出也。\n[音釋-4]：極。\n[釋文-5]：巨戟反\n[郭注-6]：今南陽 冠軍 樂鄉，數道交錯，俗呼之五劇鄉。\n[釋文-7]：冠，古亂反。樂音岳，又音洛。數，色主反。\n[釋文-8]：補李巡曰：「各有所至」\n[郭注-9]：交道四出。\n[釋文-10]：苦郎反\n[郭注-11]：史記所謂「康莊之衢」。\n[釋文-12]：莊，側良反。\n[郭注-13]：左傳曰：「得慶氏之木，百車於莊。」\n[釋文-14]：車，昌蛇反。\n[釋文-15]：七南反\n[郭注-16]：三道交復有一岐岀者，今北海 劇縣有此道。\n[釋文-17]：復，扶又反，下同。\n[郭注-18]：四道交出。\n[釋文-19]：求追反，本或作馗，字林云：「隱也。」與逵同。補李巡曰：「並容九軌」\n[郭注-20]：四道交出，復有旁通。', '釋宮弟五', '爾雅', '5', DEFAULT, DEFAULT, '1205/1410;4020/8;2810/8;1269;4459;2810/6191;5882/4282;9731', '9188', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4979, '時;行;步;趨;走;奔', '止', '室中謂之時[止]，堂上謂之行，堂下謂之步，門外謂之趨1，中庭謂之走2，大路謂之奔。3', '[釋文-1]：七朱反\n[釋文-2]：祖口反\n[郭注-3]：此皆人行步趨走之處，因以名云。', '釋宮弟五', '爾雅', '1', DEFAULT, DEFAULT, '4191;1266;1081;980;979;6626', '1064', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4980, '隄;梁;石杠;徛', '', '隄謂之梁12，34石杠謂之徛56。78', '[音釋-1]：低。\n[釋文-2]：都奚、徒雞二反。補李巡曰：「隄，防也，障也」\n[郭注-3]：卽橋也。或曰石絕水者爲梁。見詩傳。\n[釋文-4]：橋，音喬。\n[音釋-5]：江。寄。\n[釋文-6]：音江。郭居義反，顧丘奇反。說文云：「舉腳有度也。」廣雅云：「步橋也。」案：今關西呼徛與郭同\n[郭注-7]：聚石水中，以爲步渡彴也。孟子曰：「歲十月徒杠成。」或曰今之石橋。\n[釋文-8]：彴，音斫，廣雅云：「獨梁也。」案：今江東呼彴音約，沈徒的反。', '釋宮弟五', '爾雅', '', DEFAULT, DEFAULT, '9659;3787;6011/3676;1255', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4981, '廟;寢;榭;臺;樓', '寑', '室有東西廂曰廟1，2無東西廂有室曰寢[寑]3，4無室曰榭。56四方而高曰臺，陜而修曲曰樓78。9', '[釋文-1]：音相。補舍人曰：「殿有東西小堂也」\n[郭注-2]：夾室前堂。\n[釋文-3]：七甚反\n[郭注-4]：但有大室。\n[郭注-5]：榭卽今堂堭。\n[釋文-6]：堭，音皇。\n[音釋-7]：狹。\n[釋文-8]：戸夾反，說文云：「隘也。從𠂤，夾聲。」俗作狹，或作狎字，下甲反。今人以「陝 弘農縣」字，書「陜」之字，音失冉反，狹代陜，行之久矣\n[郭注-9]：脩，長也。', '釋宮弟五', '爾雅', '2', DEFAULT, DEFAULT, '5966;4686;3836;7712;3653', '4603', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4982, '豆;籩;登', '𢍪', '木豆謂之豆1。2竹豆謂之籩34。5瓦豆謂之登[𢍪]6。78', '[釋文-1]：如字，本又作梪\n[郭注-2]：豆，禮器也。\n[音釋-3]：邊。\n[釋文-4]：音邊\n[郭注-5]：籩亦禮器。\n[釋文-6]：五寡反。本又作鐙\n[郭注-7]：卽膏登也。\n[釋文-8]：膏，音高。', '釋器弟六', '爾雅', '3', DEFAULT, DEFAULT, '3096;2942;1079', '3101', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4983, '盎;缶;甌瓿;瓵;康瓠;甈', '', '盎謂之缶12。3甌瓿謂之瓵45。67康瓠謂之甈89。1011', '[音釋-1]：烏浪。方九。\n[釋文-2]：烏浪反。方九反。補孫炎曰：「缶，瓦器」\n[郭注-3]：盆也。\n[音釋-4]：蒲口。移。\n[釋文-5]：烏侯反。步口、步侯二反。弋之反。補孫炎曰：「瓵，瓦器，受斗六升」\n[郭注-6]：瓿甊，小甖。長沙謂之瓵。\n[釋文-7]：甊，路口反，方言云：「罃也。」甖，乙耕反，字亦作罃。\n[音釋-8]：胡。契。\n[釋文-9]：孫、郭如字，字書、埤蒼作𤮊，音同，李本作光，字林作㼹，口光反。音護。丘例反\n[郭注-10]：瓠，壺也。賈誼曰：「寳康瓠」是也。\n[釋文-11]：壺，音胡。', '釋器弟六', '爾雅', '', DEFAULT, DEFAULT, '3147;3287;8460/8467;8458;4459/4554;8471', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4984, '斪斸;定', '朾', '斪斸謂之定[朾]12。34', '[音釋-1]：衢。丁録。多佞。\n[釋文-2]：郭巨俱反，謝古侯、鳩于二反，本或作拘，非。本或作欘，同，丁録反，說文云：「齊謂之兹箕，一曰斤柄自曲。」李云：「斪斸，鋤也。」多佞反，或作錠，郭云：「鋤屬。」李云：「鋤別名」\n[郭注-3]：鋤屬。\n[釋文-4]：鋤，士魚反。屬，音蜀。', '釋器弟六', '爾雅', '2', DEFAULT, DEFAULT, '9480/9481;4576', '3801', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4985, '斫;櫡', '', '斫謂之鐯{櫡}12。34', '[音釋-1]：張略。\n[釋文-2]：音灼，字又作𣛰，直畧反，字林竹畧反，郭云：「钁也」\n[郭注-3]：钁也。\n[釋文-4]：钁，九縛反，字林云：「大鋤也。」', '釋器弟六', '爾雅', '', DEFAULT, DEFAULT, '9479;3694', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4986, '𣂁;疀', '', '𣂁謂之疀12。3', '[音釋-1]：鍬。插。\n[釋文-2]：本或作𣂥，郭云：「古鍬字。」並七遥反。𤳲，本或作疀，郭云：「古鍤字。」並楚洽反\n[郭注-3]：皆古鍬、鍤字。', '釋器弟六', '爾雅', '', DEFAULT, DEFAULT, '9506;8448', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4987, '稯罟;九罭;笱;罶;樔;汕;篧;罩;槮;涔', '九𪑝;鈔;籢;罧', '緵罟{稯罟}謂之九罭[九𪑝]12。九罭，魚罔也。34嫠婦之笱謂之罶56，78罺{樔}[鈔]謂之汕[籢]910，1112篧謂之罩1314，1516槮[罧]謂之涔1718。19', '[音釋-1]：子弄。域。\n[釋文-2]：子弄、子公二反，緵，𦌵也。音古。音域\n[郭注-3]：今之「百囊罟」是，亦謂之𦌵。今江東呼爲緵。\n[釋文-4]：囊，乃當反。𦌵，力回反。\n[音釋-5]：離。狗。\n[釋文-6]：字亦作釐，力其反，杜注左傳云：「寡婦爲嫠。」音狗。力九反，字書作𦊿\n[郭注-7]：毛詩傳曰：「罶，曲梁也。」謂以簿爲魚笱。\n[釋文-8]：薄，步各反。\n[音釋-9]：嘲。所諫。\n[釋文-10]：側交反。所諫反。補舍人、李巡曰：「以薄汕魚也」\n[郭注-11]：今之橑罟。\n[釋文-12]：撩，郭力堯反，取也。又力弔反，沈力到反。\n[音釋-13]：士角。\n[釋文-14]：郭士角反，又捉、廓二音。陟孝、陟角二反，字林云竹卓反，字又作䈇。補李巡曰：「編細竹以爲罩捕魚也，無竹則以荆爲之。」補孫炎曰：「今楚篧也」\n[郭注-15]：捕魚籠也。\n[釋文-16]：捕，音步。\n[音釋-17]：桑感。\n[釋文-18]：沈桑感反，謝胥寢反，郭霜甚、䟽廕二反，爾雅舊文并詩傳並米旁作，小爾雅木旁作，其文云：「魚之所息謂之橬。」橬，槮也，積柴水中而魚舍焉，郭因改米從木，字林作罧，山沁反，其義同。郭岑、潛二音，詩作潛字，小爾雅作橬字，亦音潛，又時占反，猶取積柴之義。補李巡曰：「以木投水中養魚也」\n[郭注-19]：今之作槮者，聚積柴木於水中，魚得寒，入其裏藏隱，因以簿圍捕取之。', '釋器弟六', '爾雅', '2;5;6;9', DEFAULT, DEFAULT, '4498/4827;9730/4849;1459;4828;3793;7273;2951;4822;3598;7326', '9730/6572;9438;2936;4831', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4988, '羅;罝;矛;䜌;罛;繴;罿;罬;罦', '冒;罠', '鳥罟謂之羅1，23兔罟謂之罝45，67麋罟謂之罞{矛}[冒]89，1011彘罟謂之羉{䜌}[罠]1213，1415魚罟謂之罛1617，18繴謂之罿1920。罿，罬也2122。罬謂之罦2324。罦，覆車也25。2627', '[釋文-1]：補李巡曰：「鳥飛張網以羅之」\n[郭注-2]：謂羅絡之。\n[釋文-3]：絡，音洛。\n[音釋-4]：嗟。\n[釋文-5]：又作菟，同，土故反。子邪反，說文子余反，補舍人曰：「兔自作徑路，故張罝以捕之也」\n[郭注-6]：罝猶遮也，見詩。\n[釋文-7]：遮，之蛇反。\n[音釋-8]：茆。\n[釋文-9]：亡悲反。本或作茅，同，亡包反，又音䝉。補舍人曰：「麋有難制，故張茅也」\n[郭注-10]：冒其頭也。\n[釋文-11]：冒，莫報反。\n[音釋-12]：滯。鸞。\n[釋文-13]：直例反。力端反，又莫潘反，本或作罠，亡巾反，字林云：「罠，釣也。」補舍人曰：「彘剛惡齕人，故張網以羅之也」\n[郭注-14]：羉，幕也。\n[釋文-15]：幕，音莫。\n[音釋-16]：孤。\n[釋文-17]：工胡反。補李巡曰：「捕魚具也」\n[郭注-18]：最大罟也，今江東云。\n[音釋-19]：壁。衝。\n[釋文-20]：郭卑覓反，孫芳麥反，或彼麥反。昌凶反，字林上凶反\n[音釋-21]：拙。\n[釋文-22]：謝丁劣反，郭姜悅反，或九劣反\n[音釋-23]：浮。\n[釋文-24]：浮、孚二音\n[釋文-25]：音副，又孚福反。尺蛇反\n[郭注-26]：今之翻車也，有兩轅，中施罥以捕鳥。展轉相解，廣異語。\n[釋文-27]：翻，孚袁反。罥，古縣反，又古犬反，廣雅云：「罟也。」解，古買反。', '釋器弟六', '爾雅', '3;4', DEFAULT, DEFAULT, '4833;4840;9508;1618;4826;8724;4835;4834;4836', '4809;4832', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4989, '絇;救', '絿', '絇謂之救[絿]12。3', '[音釋-1]：其俱。\n[釋文-2]：謝其俱反，孫九遇反，施苦侯反\n[郭注-3]：救絲以爲絇，或曰亦罥名。', '釋器弟六', '爾雅', '2', DEFAULT, DEFAULT, '8697;2030', '8582', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4990, '律;分', '', '律謂之分1。2', '[音釋-1]：粉\n[郭注-2]：律管可以分氣。', '釋器弟六', '爾雅', '', DEFAULT, DEFAULT, '1257;708', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4991, '業;繩;縮', '', '大版謂之業1，2繩之謂之縮之3。4', '[釋文-1]：布綰反。㸣，音業，本或作業。補孫炎曰：「業，所以飾栒刻板，捷業如鋸齒也」\n[郭注-2]：築牆版也。\n[釋文-3]：音乗。所六反。補孫炎曰：「繩束築版謂之縮」\n[郭注-4]：縮者，約束之。詩曰：「縮版以載。」', '釋器弟六', '爾雅', '', DEFAULT, DEFAULT, '1749;8694;8565', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4992, '彝;酉;罍;器;坎', '㔶', '彝1、卣{酉}23、罍4，器也。56小罍謂之坎[㔶]7。89', '[釋文-1]：補孫炎曰：「尊彝爲上，罍爲下，卣居中也」\n[音釋-2]：由。\n[釋文-3]：由、酉二音，下同。補李巡曰：「鬯之罇也」\n[釋文-4]：音雷\n[郭注-5]：皆盛酒尊。彝，其總名。\n[釋文-6]：盛，音成。尊，本又作罇，酒器也，又作樽，同，案曹憲文字指歸檢字無此從缶、從木者，說文云：「字從酋、寸，酒官法度也。」今之尊卑從此得名，故尊亦爲君父之稱。揔，尊董反。\n[釋文-7]：口感反。補孫炎曰：「酒罇也」\n[郭注-8]：罍形似壷，大者受一斛。\n[釋文-9]：斛，乎卜反。', '釋器弟六', '爾雅', '5', DEFAULT, DEFAULT, '8765;9806;3713;1440;9086', '8431', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4993, '流;𢏱;黼領;襮;緣;純;穴;褮;衣眥;禁;及;裾;紟;存;佩紟;援;袺;襭;襜;褘;縭;緌;纀', '褸;䆷;䘳;袷;紳;幃;佗', '衣裗{流}[褸]謂之䘽{𢏱}12。34黼領謂之襮56，78緣謂之純910，11袕{穴}[䆷]謂之褮1213。14衣眥謂之襟{禁}[䘳]1516，17衱{及}[袷]謂之裾1819，20衿{紟}謂之袸{存}[紳]2122，23佩衿{佩紟}謂之褑{援}2425。2627執衽謂之袺2829。30扱衽謂之襭3132，33衣蔽前謂之襜3435，3637婦人之褘[幃]謂之縭[佗]3839。縭，緌也4041。4243裳削幅謂之纀4445。4647', '[音釋-1]：流。倪。\n[釋文-2]：力求反，本又作流。五兮反，又五啟反，又五結反，俱謂裗也\n[郭注-3]：衣縷也。齊人謂之攣，或曰袿衣之飾。\n[釋文-4]：縷，又作褸，洛侯反。攣，力專反。袿，音圭，重繒爲飾。飾，音式。\n[音釋-5]：博。\n[釋文-6]：音甫。袊，本又作領，音同。音博，又方沃反\n[郭注-7]：繡刺黼文以褗領。\n[釋文-8]：刺，七亦反。褗，音偃，又作偃，衣領也。\n[音釋-9]：餘絹。之閏。\n[釋文-10]：悅絹反，注同。之閏反，又章允反\n[郭注-11]：衣緣飾也。\n[音釋-12]：穴。營。\n[釋文-13]：胡決反，一音術。胡扃反，又於營反\n[郭注-14]：衣開孔也。\n[音釋-15]：才細。\n[釋文-16]：才細反，又子移反。補李巡曰：「衣領之襟」\n[郭注-17]：交領。\n[音釋-18]：劫。\n[釋文-19]：居業反。郭居、渠二音\n[郭注-20]：衣後裾也。\n[音釋-21]：賤。\n[釋文-22]：又作紟，郭同，今、鉗二音，顧渠鴆、渠金二反。郭辭見反，孫音荐，謝徂悶反\n[郭注-23]：衣小帶。\n[音釋-24]：院。\n[釋文-25]：于眷反，埤蒼云：「佩絞也」\n[郭注-26]：佩玉之帶，上屬。\n[釋文-27]：屬，音燭，後注同。上，持掌反。\n[音釋-28]：稔。結。\n[釋文-29]：至入反。而甚反，杜預云：「裳際。」音結，郭居黠反\n[郭注-30]：持衣上衽。\n[音釋-31]：插。\n[釋文-32]：楚洽反。胡結反，廣雅云：「懷也」\n[郭注-33]：扱衣上衽於帶。\n[音釋-34]：昌占。\n[釋文-35]：必袂反。幨，本或作襜，方言作袡，郭同，昌占反\n[郭注-36]：今蔽膝也。\n[釋文-37]：膝，音悉。\n[音釋-38]：暉。离。\n[釋文-39]：幃，本或作褘，又作徽，同，暉、韋二音。本或作褵，同，力知反。補孫炎曰：「褘，帨巾也」\n[音釋-40]：汝誰。\n[釋文-41]：汝誰反\n[郭注-42]：卽今之香纓也。褘邪交落，帶繫於體，因名爲褘。緌，繫也。\n[釋文-43]：邪，字亦作衺，似嗟反。\n[音釋-44]：卜。\n[釋文-45]：息畧反。甫服反。音卜\n[郭注-46]：削殺其幅，深衣之裳。\n[釋文-47]：殺，所例反，又色戒反。', '釋器弟六', '爾雅', '1;7;10;11;14;20;21', DEFAULT, DEFAULT, '7476;8481;4946/5620;5265;8659;8523;4634;5368;5254/2100;71;1918;5296;8658;9778;4960/8658;7994;5351;5350;5292;5272;8687;8644;8660', '5269;4651;5271;5322;8646;4901;5024', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4994, '鞎;茀;禦;蔽;環;捐;鑣;钀;轙;革', '㘣;勒', '輿革前謂之鞎1，23後謂之笰{茀}4。5竹前謂之禦6，78後謂之蔽。9環謂之捐[㘣]1011，1213鑣謂之钀1415，1617載轡謂之轙1819，2021轡首謂之革[勒]22。23', '[釋文-1]：音余。姑麥反。胡根反。補李巡曰：「輿前以革爲車飾曰鞎」\n[郭注-2]：以韋靶車軾。\n[釋文-3]：靶，音霸，字林云：「轡革也。」本或作鞔，亡安反，次句同。車，音居。軾，音式。\n[釋文-4]：音弗。補李巡曰：「車後戸名也」\n[郭注-5]：以韋靶後戸。\n[釋文-6]：魚呂反。補李巡曰：「編竹當車前，以擁蔽名之曰禦。禦，止也」\n[郭注-7]：以簟衣軾。\n[釋文-8]：衣，於旣反，下皆同。\n[郭注-9]：以簟衣後戸。\n[音釋-10]：囚絹。\n[釋文-11]：呂、沈囚絹反，顧辭玄反，郭與專反\n[郭注-12]：著車衆環。\n[釋文-13]：著，直畧反。\n[音釋-14]：表驕。魚列。\n[釋文-15]：表驕反。郭魚列反，沈魚桀反\n[郭注-16]：馬勒旁鐵。\n[釋文-17]：鐵，他結反。\n[音釋-18]：儀。\n[釋文-19]：音祕。郭音儀，施音蟻\n[郭注-20]：車軛上環，轡所貫也。\n[釋文-21]：軛，於革反。\n[釋文-22]：本或作古𩠐字\n[郭注-23]：轡靶勒，見詩。', '釋器弟六', '爾雅', '6;10', DEFAULT, DEFAULT, '1814;571;56;550;108;8085;9425;9565;9565;1791', '3911;1837', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4995, '餀;喙;餲;爛;糪;敗;餒', '', '餀謂之餯{喙}12。34食饐謂之餲56。78摶者謂之糷{爛}910。1112米者謂之糪1314。1516肉謂之敗。17魚謂之餒1819。20', '[音釋-1]：呼蓋。許穢。\n[釋文-2]：呼蓋、苦蓋二反，字林火刈反，郭呼帶反。許穢反，李云：「餀、餯，皆穢臭也」\n[郭注-3]：說物臭也。\n[釋文-4]：臭，昌又反。\n[音釋-5]：意。隘。\n[釋文-6]：於器反，葛洪音懿，釋云：「饐，餿臭也。」餿，色留反，字林云：「飯傷𤍠濕也。央例、央冀二反。」於介反，字林：「乙例反，一音於葛反，食敗也。」補李巡曰：「皆飲食壞敗之名也」\n[郭注-7]：飯饖臭，見論語。\n[釋文-8]：飯，扶萬反。饖，於吠反，說文云：「飯傷熱也。」字林乙大反，蒼頡篇云：「食臭敗也。」\n[音釋-9]：輦。\n[釋文-10]：徒端反。郭音輦，謝力丹反，沈力旦反，字林力但反，李云：「糷，飯淖糜相著也」\n[郭注-11]：飯相著。\n[釋文-12]：著，直畧反。\n[音釋-13]：柏。\n[釋文-14]：郭普厄反，施孚八反，李云：「米飯半腥半熟名糪」\n[郭注-15]：飯中有腥。\n[釋文-16]：腥，音星。\n[郭注-17]：臭壞。\n[音釋-18]：奴罪。\n[釋文-19]：奴罪反，說文云：「魚敗曰餒。」字書作鯘，同\n[郭注-20]：肉爛。', '釋器弟六', '爾雅', '', DEFAULT, DEFAULT, '3248;783;3256;6470;4361;2041;3260', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4996, '脫;斮', '', '肉曰脫之1。23魚曰斮之45。67', '[釋文-1]：吐奪反。補李巡曰：「肉去其骨曰脫」\n[郭注-2]：剥其皮也，今江東呼麋鹿之屬通爲肉。\n[釋文-3]：麋，音眉。\n[音釋-4]：荘略。\n[釋文-5]：莊畧、牀畧二反，字林云：「斬也。」補樊光曰：「斫也。」李巡斮作作，曰：「作之，魚骨小無所去」\n[郭注-6]：謂削鱗也。\n[釋文-7]：鰭，巨夷反，上林賦云：「揵鰭掉尾」是也，或作鱗。', '釋器弟六', '爾雅', '', DEFAULT, DEFAULT, '2653;9486', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4997, '冰;脂', '', '冰1，脂也。2', '[釋文-1]：彼凌反，說文云：「水堅也。」孫本作凝，牛烝反，「膏凝曰脂」\n[郭注-2]：莊子云：「肌膚若冰雪。」冰雪，脂膏也。', '釋器弟六', '爾雅', '', DEFAULT, DEFAULT, '7511;2706', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4998, '羹;鮨', '', '肉謂之羹1，23魚謂之鮨45。67', '[釋文-1]：又作𦢋，同，古衡、下庚二反\n[郭注-2]：肉臛也，廣雅曰湆。見左傳。\n[釋文-3]：臛，火各、火沃二反，字林云：「肉羮也。」湆，丘及反，汁也。\n[音釋-4]：祁。\n[釋文-5]：巨伊反，字林止尸反\n[郭注-6]：鮨，鮓屬也，見公食大夫禮。\n[釋文-7]：鮓，側下反。食，音嗣，又作飤，音同。', '釋器弟六', '爾雅', '', DEFAULT, DEFAULT, '1871;7661', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4999, '醢;臡', '', '肉謂之醢12，3有骨者謂之臡45。6', '[音釋-1]：海。\n[釋文-2]：虎改反\n[郭注-3]：肉醤。\n[音釋-4]：泥。\n[釋文-5]：本又作臡，同，奴黎反，字林作腝，音人兮反，謂有骨醢也\n[郭注-6]：雜骨醤，見周禮。', '釋器弟六', '爾雅', '', DEFAULT, DEFAULT, '9865;2696', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5000, '康;蠱', '鼓', '康謂之蠱[鼓]1。2', '[釋文-1]：說文作穅，或省禾，口郎反。音古\n[郭注-2]：米皮。', '釋器弟六', '爾雅', '2', DEFAULT, DEFAULT, '4459;8978', '3083', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5001, '澱;垽', '', '澱謂之垽12。34', '[音釋-1]：魚靳。\n[釋文-2]：徒薦反。魚靳反\n[郭注-3]：滓澱也，今江東呼垽。\n[釋文-4]：滓，側里反。', '釋器弟六', '爾雅', '', DEFAULT, DEFAULT, '7381;9131', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5002, '鼐;鼒;弋;鬲', '翼', '鼎絕大謂之鼐12，3圜弇上謂之鼒45，6附耳外謂之釴{弋}[翼]78，9款足者謂之鬲1011。1213', '[音釋-1]：耐。\n[釋文-2]：沈奴戴反，郭音乃\n[郭注-3]：最大者。\n[音釋-4]：袁。咨。\n[釋文-5]：音貟。古奄字，於檢反。音咨，施音災，郭音才，字林音載\n[郭注-6]：鼎斂上而小口。\n[音釋-7]：亦。\n[釋文-8]：音弋\n[郭注-9]：鼎耳在表。\n[音釋-10]：苦管。力。\n[釋文-11]：本或作窾，苦管反，闊也。力的反。補舍人曰：「鼎足相去疏閒也」\n[郭注-12]：鼎曲腳也。\n[釋文-13]：腳，居畧反。', '釋器弟六', '爾雅', '3', DEFAULT, DEFAULT, '4411;4410;8366;1854', '7695', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5003, '䰝;鬵;鉹', '', '䰝謂之鬵1。23鬵4，鉹也56。7', '[釋文-1]：本或作甑，卽凌反，又子孕反。徐林反，又嗣廉反，郭財金反\n[郭注-2]：詩曰：「漑之釡鬵。」\n[釋文-3]：漑，古代反。釡，音父。\n[音釋-4]：尋\n[音釋-5]：侈。\n[釋文-6]：昌紙反\n[郭注-7]：涼州呼鉹。', '釋器弟六', '爾雅', '', DEFAULT, DEFAULT, '1860;1859;9292', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5004, '遂;瑞;區', '', '璲{遂}12，瑞也。34玉十謂之區56。78', '[音釋-1]：遂。\n[釋文-2]：音遂\n[郭注-3]：詩曰：「鞙鞙佩璲。」璲者，玉瑞。\n[釋文-4]：鞙，胡犬反，又作琄，同。\n[音釋-5]：羌于。\n[釋文-6]：羌于反，又烏侯反\n[郭注-7]：𩀱玉曰瑴，五瑴爲區。\n[釋文-8]：本或作𤤴，古學反，說文云：「二玉相合爲𤤴。」', '釋器弟六', '爾雅', '', DEFAULT, DEFAULT, '1175;121;8419', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5005, '翮;箴;縳;緷', '專;㯻', '羽本謂之翮12，3一羽謂之箴4，十羽謂之縳[專]56，百羽謂之緷[㯻]78。910', '[音釋-1]：戸革。\n[釋文-2]：戸革反，羽本\n[郭注-3]：鳥羽根也。\n[釋文-4]：章諶反，案周禮羽人職云：「十羽爲審，百羽爲摶，十摶爲縳。」鄭注云：「審、摶、縳，羽數束名也。爾雅曰：『一羽謂之箴，十羽謂之縳，百羽謂之緷。』其名音相近也。一羽則有名，蓋失之矣。」孫同鄭意，云蓋誤。郭云：「凡物數無不從一爲始，以爾雅不失，周官未爲得也」\n[音釋-5]：篆。\n[釋文-6]：音篆，又竹眷反\n[音釋-7]：衮。\n[釋文-8]：古本反，又戸本、苦本二反，埤蒼云：「大束也」\n[郭注-9]：別羽數多少之名。\n[釋文-10]：別，彼列反。', '釋器弟六', '爾雅', '3;4', DEFAULT, DEFAULT, '2254;2985;8597;8539', '1985;3903', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5006, '虡;藣', '', '木謂之虡12。34旄謂之藣56。7', '[音釋-1]：巨。\n[釋文-2]：音巨\n[郭注-3]：縣鐘磬之木，植者名虡。\n[釋文-4]：縣，音玄。植，直吏反。\n[音釋-5]：卑。\n[釋文-6]：音毛。方皮、方賜二反\n[郭注-7]：旄牛尾也。', '釋器弟六', '爾雅', '', DEFAULT, DEFAULT, '3117;472', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5007, '菜;束', '疏', '菜謂之蔌{束}[疏]12。34', '[音釋-1]：速。\n[釋文-2]：速、藪二音，詩云：「其䔩維何，維筍及蒲」\n[郭注-3]：蔌者，菜茹之揔名，見詩。\n[釋文-4]：茹，如庶反。', '釋器弟六', '爾雅', '2', DEFAULT, DEFAULT, '558;3899', '9789', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5008, '苫', '', '白蓋謂之苫12。3', '[音釋-1]：合。\n[釋文-2]：音盍。失占反。補李巡曰：「編菅茅以覆屋」\n[郭注-3]：白茅苫也，今江東呼爲盖。', '釋器弟六', '爾雅', '', DEFAULT, DEFAULT, '587', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5009, '璗;鏐;銀;鐐;餅;版;錫;鈏', '', '黃金謂之璗12，其美者謂之鏐34。白金謂之銀，其美者謂之鐐56。78鉼{餅}金謂之鈑{版}910，11錫謂之鈏1213。1415', '[音釋-1]：蕩。\n[釋文-2]：徒黨反，說文云：「金與玉同色也」\n[音釋-3]：留。\n[釋文-4]：力幽、其幽、力幼三反\n[音釋-5]：遼。\n[釋文-6]：力彫反，說文云：「白金也。」字林力召反，云：「美金也」\n[郭注-7]：此皆道金、銀之別名及精者。鏐卽紫磨金。\n[釋文-8]：磨，莫佐反。\n[音釋-9]：餅。板。\n[釋文-10]：必領反。音版，本亦作版\n[郭注-11]：周禮曰：「祭五帝卽供金鈑」是也。\n[音釋-12]：引。\n[釋文-13]：余𦂳、弋刃、常刃三反\n[郭注-14]：白鑞。\n[釋文-15]：鑞，力盍反，周禮職方氏：「揚州之利金錫。」鄭注云：「錫，鑞也。」字或作𨭛。', '釋器弟六', '爾雅', '', DEFAULT, DEFAULT, '205;9410;9262;9263;3214;4401;9266;9267', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5010, '告;觷;厝;剫;雕', '觷;琱', '象謂之鵠{告}[觷]12，角謂之觷34，犀謂之剒{厝}56，木謂之剫78，玉謂之雕[琱]9。1011', '[音釋-1]：斛。\n[釋文-2]：胡酷、古毒二反，白也。本亦作䶜，同，廣雅作䚛\n[音釋-3]：嶽。\n[釋文-4]：五角反，沈音學\n[音釋-5]：錯。\n[釋文-6]：蘇齎反。本或作厝，同，七各反\n[音釋-7]：鐸。\n[釋文-8]：徒各反\n[釋文-9]：丁𦝫反\n[郭注-10]：左傳曰：「山有木，工則剫之。」五者皆治樸之名。\n[釋文-11]：璞，字又作樸，丕角反。', '釋器弟六', '爾雅', '1;5', DEFAULT, DEFAULT, '778;2843;5996;2045;2295', '2843;146', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5011, '鏤;刻;切;差;琢;䃺', '', '金謂之鏤1，木謂之刻，骨謂之切2，象謂之磋{差}3，玉謂之𤥨{琢}4，石謂之磨{䃺}。5', '[釋文-1]：字又作𨫒，音漏\n[釋文-2]：本或作𪙑，同，千結反\n[釋文-3]：七何反\n[釋文-4]：琢，丁角反\n[郭注-5]：六者皆冶器之名。', '釋器弟六', '爾雅', '', DEFAULT, DEFAULT, '9273;2765;2760;3029;145;6049', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5012, '璆;琳;玉', '', '璆12、琳，玉也。3', '[音釋-1]：求。\n[釋文-2]：音虯，本或作球字，渠周反\n[郭注-3]：璆、琳，美玉名。', '釋器弟六', '爾雅', '', DEFAULT, DEFAULT, '104;105;81', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5013, '𥳑;畢;不律;筆;滅;㸃', '篳;㓠', '𥳑謂之畢[篳]1，2不律謂之筆3，4滅謂之㸃[㓠]5。6', '[釋文-1]：如字，禮記云：「呻其佔畢。」謂呻吟佔視𥳑畢之文也，李本作篳，同\n[郭注-2]：今𥳑札也。\n[釋文-3]：說文云：「吳謂筆爲不律」\n[郭注-4]：蜀人呼筆爲不律也，語之變轉。\n[釋文-5]：丁簟、丁念二反，李本作沾，孫本作玷\n[郭注-6]：以筆滅字爲㸃。', '釋器弟六', '爾雅', '2;6', DEFAULT, DEFAULT, '2893;2492;7706/1257;1941;7443;6555', '3005;2797', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5014, '銑', '', '絕澤謂之銑12。34', '[音釋-1]：蘇典。\n[釋文-2]：蘇典反\n[郭注-3]：銑卽美金，言最有光澤也。國語曰：「玦之以金銳者。」謂此也。\n[釋文-4]：決，又作玦。', '釋器弟六', '爾雅', '', DEFAULT, DEFAULT, '9275', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5015, '鏃;鍭;志', '族;翭', '{鏃}[族]金鏃翦羽謂之鍭[翭]12，34骨鏃不翦羽謂之志。56', '[音釋-1]：作木。\n[釋文-2]：作木反。侯、候二音。補李巡曰：「以金爲箭鏑也」\n[郭注-3]：今之錍箭是也。\n[釋文-4]：錍，匹迷反，方言云：「箭廣長而薄廉者謂之錍」。\n[郭注-5]：今之骨骲是也。\n[釋文-6]：骲，火交反，埤蒼云：「骨鏃也。」沈五爪反，顧蒲交反。', '釋器弟六', '爾雅', '1;2', DEFAULT, DEFAULT, '9443;9411;6709', '4312;2253', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5016, '弓;弭;銑;珧;珪', '', '弓有緣者謂之弓12，34無緣者謂之弭56。78以金者謂之銑9，以蜃者謂之珧1011，以玉者謂之珪。1213', '[音釋-1]：掾。\n[釋文-2]：悅絹反，下同。補李巡曰：「骨飾兩頭曰弓，不以骨飾兩頭曰弭」\n[郭注-3]：緣者，繳纏之，卽今宛轉也。\n[釋文-4]：繳，章弱反，字林云：「生絲縷也。」纏，直連反。宛，於阮反。\n[音釋-5]：尾。\n[釋文-6]：亡婢反\n[郭注-7]：今之角弓也。左傳曰：「左執鞭弭。」\n[釋文-8]：鞭，卑緜反。\n[釋文-9]：蘇典反\n[音釋-10]：腎。姚。\n[釋文-11]：市忍反。余招反，蜯屬也，案：以蜯飾弓弭\n[郭注-12]：用金、蚌、玉，飾弓兩頭，因取其類以爲名。珧，小蚌。\n[釋文-13]：蚌，本又作蜯，蒲項反。', '釋器弟六', '爾雅', '', DEFAULT, DEFAULT, '8479;8481;9275;194;9151', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5017, '珪;玠;璋;琡;璧;宣;璧;瑗;環', '圭;璹;瑄', '珪[圭]大尺二寸謂之玠1，2璋大八寸謂之琡[璹]34，5璧大六寸謂之宣[瑄]6，7肉倍好謂之璧89。10好倍肉謂之瑗1112，13肉好若一謂之環。14', '[釋文-1]：音介\n[郭注-2]：詩曰：「錫爾玠珪。」\n[音釋-3]：俶。\n[釋文-4]：昌育、常育二反\n[郭注-5]：璋，半珪也。\n[釋文-6]：如字，本或作瑄，音同\n[郭注-7]：漢書所云：「瑄玉」是也。\n[音釋-8]：耗。\n[釋文-9]：如字，又如授反，璧邊也，注及下同。如字，又音耗，璧孔也，注及下同\n[郭注-10]：肉，邊。好，孔。\n[音釋-11]：院。\n[釋文-12]：爲眷反，蒼頡篇云：「玉珮名」\n[郭注-13]：孔大而邊小。\n[郭注-14]：孔、邊適等。', '釋器弟六', '爾雅', '1;4;6', DEFAULT, DEFAULT, '9151;116;114;218;106;4560;106;107;108', '9151;136;219', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5018, '遂;綬', '', '繸{遂}1，綬也2。34', '[釋文-1]：音遂\n[釋文-2]：音受\n[郭注-3]：卽佩玉之組，所以連繫瑞玉者，因通謂之繸。\n[釋文-4]：組，音祖。', '釋器弟六', '爾雅', '', DEFAULT, DEFAULT, '1175;8648', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5019, '縓;赬;纁;蔥;黝;黼', '', '一染謂之縓12，3再染謂之赬45，6三染謂之纁78，9靑謂之蔥10，11黑謂之黝1213，14斧謂之黼。15', '[音釋-1]：綵。\n[釋文-2]：如琰反，下同。七絹反，說文云：「帛黃赤色」\n[郭注-3]：今之紅也。\n[音釋-4]：敕呈。\n[釋文-5]：恥貞反\n[郭注-6]：淺赤。\n[音釋-7]：勲。\n[釋文-8]：許云反，說文云：「淺絳也。」字林同\n[郭注-9]：纁，絳也。\n[釋文-10]：七公反\n[郭注-11]：淺靑。\n[音釋-12]：於糾。\n[釋文-13]：於糾反。補孫炎曰：「黝，靑黑；葱則靑之異色」\n[郭注-14]：黝，黑貌。周禮曰：「陰祀用黝牲。」\n[郭注-15]：黼文畫斧形，因名云。', '釋器弟六', '爾雅', '', DEFAULT, DEFAULT, '8624;6591;8617;633;6553;4946', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5020, '邸;柢', '', '邸謂之柢12。3', '[音釋-1]：氐。\n[釋文-2]：丁以反。丁計反\n[郭注-3]：根、柢皆物之邸。邸卽底，通語也。', '釋器弟六', '爾雅', '', DEFAULT, DEFAULT, '4012;3562', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5021, '雕;琢', '彫', '雕[彫]謂之琢。1', '[郭注-1]：治玉名也。', '釋器弟六', '爾雅', '1', DEFAULT, DEFAULT, '2295;145', '5718', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5022, '蓐;茲;竿;椸;簀;笫', '', '蓐謂之兹{茲}12，34竿謂之箷{椸}56，78簀謂之笫910。11', '[音釋-1]：辱。\n[釋文-2]：音辱\n[郭注-3]：公羊傳曰：「屬負兹。」兹者，蓐席也。\n[釋文-4]：屬，之欲反。兹，子斯反。\n[音釋-5]：移。\n[釋文-6]：乾、幹二音。李本作䈕，同，羊支反，字林上支反\n[郭注-7]：衣架。\n[釋文-8]：架，音駕。\n[音釋-9]：側士。\n[釋文-10]：音責。側士反\n[郭注-11]：牀版。', '釋器弟六', '爾雅', '', DEFAULT, DEFAULT, '698;526;2950;3838;2909;2910', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5023, '辨;韏', '', '革中絕謂之辨12，34革中辨謂之韏56。78', '[音釋-1]：片。\n[釋文-2]：郭普遍反，下同，孫蒲莧反，釋云：「辨，半分也」\n[郭注-3]：中斷皮也。\n[釋文-4]：斷，都管反。\n[音釋-5]：眷。\n[釋文-6]：音眷，又九萬反\n[郭注-7]：復分半也。\n[釋文-8]：復，扶又反。', '釋器弟六', '爾雅', '', DEFAULT, DEFAULT, '2768;3397', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5024, '鏤;叜', '鏤', '鏤1，𨨿{叜}[鏤]也23。4', '[釋文-1]：音漏\n[音釋-2]：蘇婁。\n[釋文-3]：字又作鎪，同，蘇婁反，又色留反，字書云：「鎪，鏉也。」鏉音速\n[郭注-4]：刻鏤物爲𨨿。', '釋器弟六', '爾雅', '2', DEFAULT, DEFAULT, '9273;1909', '9273', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5025, '酉', '', '卣{酉}12，中尊也。3', '[音釋-1]：酉。\n[釋文-2]：酉、由二音\n[郭注-3]：不大不小者。', '釋器弟六', '爾雅', '', DEFAULT, DEFAULT, '9806', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5026, '宮;重;商;敏;角;經;徵;迭;羽;柳', '', '宮謂之重1，商謂之敏2，角謂之經3，徵謂之迭45，羽謂之柳6。7', '[釋文-1]：直冢反，劉歆云：「宮，中也，居中央，暢四方，唱始施生，爲四聲綱也。」孫云：「宮音濁而遲，故曰重也」\n[釋文-2]：亡謹反，疾也，成也，審也，達也，劉歆云：「商，章也，物成孰可章度也。」白虎通云：「商，彊也。」案：商爲臣，而佐君成政，故曰敏。敏，成也\n[釋文-3]：經，常也，劉歆云：「角，觸也，物觸地而出，戴芒角也」\n[音釋-4]：知矢。\n[釋文-5]：知里反。大結反，劉歆云：「徵，祉也，物盛大而繁祉也。」白虎通云：「徵，止也」\n[釋文-6]：劉歆云：「羽，宇也，物聚藏宇覆之也。」白虎通云：「羽，舒也。」鄭注禮云：「柳，聚也」\n[郭注-7]：皆五音之別名。其義未詳。', '釋樂弟七', '爾雅', '', DEFAULT, DEFAULT, '4630;5244;1453;1999;2827;8532;5241;1166;2242;3514', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5027, '瑟;灑', '', '大瑟謂之灑12。34', '[音釋-1]：所蟹。\n[釋文-2]：字林云：「庖羲作瑟。」所蟹、所綺二反，又所買反，孫云：「音多變，布出如灑也」\n[郭注-3]：長八尺一寸，廣一尺八寸，二十七絃。\n[釋文-4]：長，直亮反，又如字，下放此。廣，古曠反。', '釋樂弟七', '爾雅', '', DEFAULT, DEFAULT, '8408;7425', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5028, '琴;離', '', '大琴謂之離1。2', '[釋文-1]：字林云：「神農作琴。」補孫炎曰：「音多變，聲流離也」\n[郭注-2]：或曰琴大者二十七絃，未詳長短。廣雅曰：「琴長三尺六寸六分，五絃。」', '釋樂弟七', '爾雅', '', DEFAULT, DEFAULT, '8407;2294', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5029, '鼓;鼖;應', '', '大鼓謂之鼖12，3小者謂之應45。67', '[音釋-1]：墳。\n[釋文-2]：扶云反\n[郭注-3]：鼖，長八尺。\n[音釋-4]：膺。\n[釋文-5]：音譍，注同，李云：「小者音聲相承故曰應，應，承也。」孫云：「和應大鼓也」\n[郭注-6]：詩曰：「應朄縣鼔。」在大鼓側。\n[釋文-7]：（⿰車柬），本亦作朄，余刃反，詩云：「應朄縣鼓」是也，案：𣌾，引也，謂擊小鼓引樂聲。縣，音玄。', '釋樂弟七', '爾雅', '', DEFAULT, DEFAULT, '3083;3085;6713', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5030, '磬;熇', '', '大磬謂之毊{熇}12。34', '[音釋-1]：虛嬌。\n[釋文-2]：口定反。虛嬌反，又音喬，孫云：「毊，喬也。喬，高也，謂其聲高也。」李云：「大磬聲淸燥也，故曰毊。毊，燥也」\n[郭注-3]：毊，形似犂錧，以玉石爲之。\n[釋文-4]：犂，郎奚反。錧，古緩反，沈古亂反，字林云：「田器也，江南人呼犂刃爲錧。」本亦作貫，同。', '釋樂弟七', '爾雅', '', DEFAULT, DEFAULT, '6041;6442', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5031, '笙;巢;和', '', '大笙謂之巢1，23小者謂之和4。5', '[釋文-1]：世本云：「隨作笙。」孫、顧並仕交、莊交二反，孫又徂交反，「巢，高也，言其聲高」\n[郭注-2]：列管瓠中，施簧管端，大者十九簧。\n[釋文-3]：瓠，胡故反。簧，音黃。\n[釋文-4]：胡戈反，下同，李云：「小者聲少，音相和也。」郭引儀禮云：「三笙一和而成聲。」鄭注云：「三人吹笙，一人吹和。」又胡臥反，孫云：「應和於笙。」補舍人曰：「大笙，音聲衆而高也；小者音相和也」\n[郭注-5]：十三簧者。鄉射記曰：「三笙一和而成聲。」', '釋樂弟七', '爾雅', '', DEFAULT, DEFAULT, '2988;3894;846', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5032, '篪;沂', '慇', '大篪謂之沂[慇]12。34', '[音釋-1]：池。銀。\n[釋文-2]：字又作䶵，同，直知反，管有七孔，世本云：「蘇成公所作，長一尺二寸。」郭魚斤反，又魚靳反，李、孫云：「篪聲悲，沂，悲也。」或作𪛊，又作䇵，音宜肌反。補舍人曰：「大箎，其聲悲沂鏘然也，詩云『仲氏吹箎』」\n[郭注-3]：箎以竹爲之，長尺四寸，圍三寸，一孔，上出一寸三分，名翹，橫吹之。小者尺二寸。廣雅云：「八孔。」\n[釋文-4]：翹，巨遥反。', '釋樂弟七', '爾雅', '2', DEFAULT, DEFAULT, '1429;7082', '6909', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5033, '員;嘂', '壎', '大塤{員}[壎]謂之嘂12。34', '[音釋-1]：喧。叫。\n[釋文-2]：本或作壎字，同，許袁反，說文云：「壎，樂器名，從土熏聲。」釋名云：「塤，喧也，聲濁喧喧然。」案世本云：「暴辛公所作也，圍五寸半，長三寸半，六孔也。」本或作叫字，同，居弔反，李云：「大壎也」\n[郭注-3]：塤，燒土爲之，大如鵝子，銳上平底，形如稱錘，六孔，小者如雞子。\n[釋文-4]：鵞，如字。銳，余祭反。稱，尺證反。錘，直危、直僞二反，廣雅云：「錘謂之權。」', '釋樂弟七', '爾雅', '1', DEFAULT, DEFAULT, '3934;1438', '9075', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5034, '鐘;鏞;剽;棧', '標;淺', '大鐘謂之鏞1，23其中謂之剽[標]45，小者謂之棧[淺]67。', '[釋文-1]：鍾，章容反，說文作鐘，云：「樂器也。」字林同，世本云：「垂所作。」以此鍾爲酒器，今經典通爲樂器。音容。補李巡曰：「大鍾音聲大。鏞，大也。」孫炎曰：「鏞，深長之聲」\n[郭注-2]：書曰：「笙鏞以閒。」亦名鑮，音博。\n[釋文-3]：閒，閒廁之閒。鑮，音博，字林云：「匹各反，又音薄。」字書云：「大鍾也。」\n[音釋-4]：瓢。\n[釋文-5]：郭音瓢，孫匹妙反，釋云：「剽者，聲輕疾。」李云：「其中微小故曰剽，剽，小也」\n[音釋-6]：盞。\n[釋文-7]：郭側𥳑反，李云：「淺也。」東晉 太興元年，會稽 剡縣人家丼中得一鍾，長三寸，口徑四寸，上有銘古文云：「棧，鍾之小者，旣長三寸。」自然淺也。或助板反', '釋樂弟七', '爾雅', '3;4', DEFAULT, DEFAULT, '9388;9387;2786;3732', '3582;7228', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5035, '簫;言;筊', '䡾;悄', '大簫謂之言[䡾]1，23小者謂之筊[悄]45。67', '[釋文-1]：如字，本或作䇾，音同。補李巡曰：「聲大者言言也」\n[郭注-2]：編二十三管，長尺四寸。\n[釋文-3]：編，卑緜反，又方千反，或音步典反。\n[音釋-4]：爻。\n[釋文-5]：本或作笅字，戸交反。補李巡曰：「小者聲揚而小，故言筊。筊，小也」\n[郭注-6]：十六管，長尺二寸。簫，一名籟。\n[釋文-7]：籟，音賴。', '釋樂弟七', '爾雅', '2;3', DEFAULT, DEFAULT, '2991;1474;2953', '9575;6935', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5036, '管;熇;涅;篎', '䵒', '大管謂之簥{熇}12，34其中謂之篞{涅}[䵒]56，小者謂之篎78。', '[音釋-1]：嬌。\n[釋文-2]：九遥反。補舍人曰：「大管聲高大故曰簥，簥者，高也；中者聲精密故曰篞，篞，密也；小者聲音淸妙也」\n[郭注-3]：管長尺，圍寸，併漆之，有底。賈氏以爲如箎，六孔。\n[釋文-4]：併，步頂反，又「并」之去聲。漆，音七。\n[音釋-5]：乃結。\n[釋文-6]：乃結反\n[音釋-7]：妙。\n[釋文-8]：郭音妙，又亡小反', '釋樂弟七', '爾雅', '3', DEFAULT, DEFAULT, '2995;6442;7234;2996', '4514', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5037, '籥;產;仲;箹', '龠;崝', '大籥[龠]謂之產[崝]12，34其中謂之仲5，小者謂之箹67。', '[音釋-1]：藥。\n[釋文-2]：羊灼反，管三孔也，本或作龠。簅，音產，字又作產\n[郭注-3]：籥，如笛，三孔而短小。廣雅云「七孔。」\n[釋文-4]：笛，徒歷反，字或作篴。\n[釋文-5]：或作筗，同。補舍人曰：「聲適仲呂」\n[音釋-6]：渥。\n[釋文-7]：烏角反，又音約。補舍人曰：「形、聲細小」', '釋樂弟七', '爾雅', '1;2', DEFAULT, DEFAULT, '2891;3878;4968;2994', '1427;5893', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5038, '步;和;謡;亏;修;蹇', '龢;㖾', '徒鼔瑟謂之步，1徒吹謂之和[龢]23，徒歌謂之謡4，5徒擊鼔謂之咢{亏}[㖾]67，8徒鼔鐘謂之修9，徒鼔磬謂之寋{蹇}1011。12', '[郭注-1]：獨作之。\n[音釋-2]：昌睡。胡臥。\n[釋文-3]：本或作龡字，同，昌睡反\n[釋文-4]：補孫炎曰：「聲消搖也」\n[郭注-5]：詩云：「我歌且謡。」\n[音釋-6]：五各。\n[釋文-7]：五各反，字林或作㖾，又本作鄂。補孫炎曰：「聲驚咢也」\n[郭注-8]：詩云：「或歌或咢。」\n[釋文-9]：如字，本作脩字\n[音釋-10]：紀展。\n[釋文-11]：李云：「置擊衆聲寋連也。」本或作謇字，同，紀展反。或作蹇字，非。郭云：「未見義所出。」未知李何所據。上「重、敏、經、迭、柳」，郭云：「皆五音別名，其義未詳。」諸家或有音訓，亦可爲義，上下皆類此\n[郭注-12]：未見義所出。', '釋樂弟七', '爾雅', '2;4', DEFAULT, DEFAULT, '1081;846;1571;3070;5716;1395', '1430;974', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5039, '柷;止;敔;甄', '', '所以鼔柷謂之止12，34所以鼔敔謂之籈{甄}56。78', '[音釋-1]：昌孰。\n[釋文-2]：昌熟反\n[郭注-3]：柷，如漆桶，方二尺四寸，深一尺八寸，中有椎，柄連底，桐之令左右擊。止者，其椎名。\n[釋文-4]：桶，音動，又音甬，甬卽斛也。深，尸鴆反，或如字。椎，直追反，下同。柄，兵命反。底，丁禮反。挏，大孔反。令，力呈反。\n[音釋-5]：語。真。\n[釋文-6]：魚呂反，鄭衆云：「木虎。」郭之仁反，又音戰，謝居延反。郭云：「以木，長尺，擽之，名籈。」字林云：「以竹，長尺也」\n[郭注-7]：敔，如伏虎，背上有二十七鉏鋙，刻以木，長尺，擽之。籈者，其名。\n[釋文-8]：鉏，事呂反，又作鋤。鋙，魚呂反。擽，力的反，廣雅云：「擽，擊也。」漢書音義云：「擽，捎也。」', '釋樂弟七', '爾雅', '', DEFAULT, DEFAULT, '3768;1064;2059;8454', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5040, '鼗;麻;料', '靡;遼', '大鼗謂之麻[靡]12，小者謂之料[遼]34。56', '[音釋-1]：桃。\n[釋文-2]：徒刀反，本或作鞀，同，說文云：「鞀，遼也。」或作鞉、又作𪔛、籀文作㲈，同。如字\n[音釋-3]：聊。\n[釋文-4]：力彫反\n[郭注-5]：麻者，音穊而長也。料者，聲淸而不亂。\n[釋文-6]：穊，居器反。', '釋樂弟七', '爾雅', '2;3', DEFAULT, DEFAULT, '1811;4536;9494', '7698;1197', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5041, '節', '', '和樂謂之節。', '', '釋樂弟七', '爾雅', '', DEFAULT, DEFAULT, '2877', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5042, '穹蒼;蒼天;昊天;旻天;上天', '', '穹蒼1，蒼天也2。34春爲蒼天，5夏爲昊天6，78秋爲旻天9，1011冬爲上天12。13', '[釋文-1]：起宮反。且剛反\n[釋文-2]：郭以「穹」及「蒼蒼」俱爲天稱，毛詩傳則以「蒼天」釋「穹蒼」\n[郭注-3]：天形穹隆，其色蒼蒼，因名云。\n[釋文-4]：隆，呂穹反。\n[郭注-5]：萬物蒼蒼然生。\n[釋文-6]：胡駕反，下同。胡杲反\n[郭注-7]：言氣皓旰。\n[釋文-8]：晧，胡老反，本亦作昊，光明也，日出也。旰，古案反，日光出也。\n[釋文-9]：亡巾反\n[郭注-10]：旻猶愍也，愍萬物彫落。\n[釋文-11]：愍，亡忍反。彫，都聊反。\n[釋文-12]：時羕反\n[郭注-13]：言時無事，在上臨下而已。', '釋天弟八', '爾雅', '', DEFAULT, DEFAULT, '4672/538;538/3;6666/3;4190/3;6/3', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5043, '靑陽;朱明;白藏;玄英;玉燭', '', '春爲靑陽，1夏爲朱明，2秋爲白藏3，4冬爲玄英5。6四氣和謂之玉燭7。8', '[郭注-1]：氣靑而溫，陽。\n[郭注-2]：氣赤而光明。\n[釋文-3]：徂倉反\n[郭注-4]：氣白而收藏。\n[釋文-5]：於京反\n[郭注-6]：氣黑而淸英。\n[釋文-7]：李云：「人君德美如玉而明若燭」\n[郭注-8]：道光照。', '釋天弟八', '爾雅', '', DEFAULT, DEFAULT, '3189/9624;3563/4323;4931/695;2507/502;81/6477', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5044, '發生;長嬴;收成;安寧;通正;景風', '長贏;安寍', '春爲發生，夏爲長嬴[長贏]12，秋爲收成，冬爲安寧[安寍]。34四時和爲通正，5謂之景風6。7', '[音釋-1]：丁丈。盈。\n[釋文-2]：謝丁兩反，李云：「萬物各發生長也。」施直良反。本或作贏，以征反\n[郭注-3]：此亦四時之別號，尸子皆以爲太平祥風。\n[釋文-4]：大，音泰，或作太。\n[郭注-5]：道平暢也。\n[釋文-6]：補李巡曰：「太平之風也」\n[郭注-7]：所以致景風。', '釋天弟八', '爾雅', '2;4', DEFAULT, DEFAULT, '8504/3876;6069/8116;2048/9749;4578/3057;1134/1087;4213/8979', '6069/3963;4578/4575', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5045, '醴泉', '', '甘雨時降，萬物以嘉，1謂之醴泉2。3', '[郭注-1]：莫不善之。\n[釋文-2]：本或作澧字，音禮\n[郭注-3]：所以出醴泉。', '釋天弟八', '爾雅', '', DEFAULT, DEFAULT, '9817/7493', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5046, '饑;饉;荒;荐', '', '穀不熟爲饑1，2蔬不熟爲饉3，4果不熟爲荒。5仍饑爲荐67。8', '[釋文-1]：居疑反，本或作飢，又作古𩚮字，說文、字林皆云：「饑，穀不熟。」「飢，餓也」\n[郭注-2]：五穀不成。\n[釋文-3]：音踈。巨靳反\n[郭注-4]：凡草菜可食者，通名爲蔬。\n[郭注-5]：果，木子。\n[音釋-6]：賤。\n[釋文-7]：李本作薦字，音同，在見反\n[郭注-8]：連歲不熟。左傳曰：「今又荐饑。」', '釋天弟八', '爾雅', '', DEFAULT, DEFAULT, '3257;3258;546;580', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5047, '閼逢;旃蒙;柔兆;強圉;著雍;屠維;上章;重光;玄弋;昭陽', '', '太歲在甲曰閼逢12，在乙曰旃蒙3，在丙曰柔兆4，在丁曰強圉5，在戊曰著雍67，在己曰屠維8，在庚曰上章，在辛曰重光910，在壬曰玄黓{玄弋}1112，在癸曰昭陽。', '[音釋-1]：烏割。\n[釋文-2]：大，音泰，下放此，本今作太。烏割反，又於歇反，又於虔反。符隆反。補李巡曰：「萬物鋒芒欲出，擁遏未通」\n[釋文-3]：之然反。莫東反\n[釋文-4]：補李巡曰：「萬物皆垂枝布葉」\n[釋文-5]：本或作彊字，音同，渠良反。魚呂反。補李巡曰：「萬物皆剛盛未通」\n[音釋-6]：直略。\n[釋文-7]：音茂。施直魚反，孫直畧反，又陟慮、遲慮二反，本或作祝字，宜章六反。廱，字又作雍，同，於恭反，本或作黎字，宜力低反\n[釋文-8]：音紀\n[音釋-9]：直龍。\n[釋文-10]：直龍反\n[音釋-11]：亦。\n[釋文-12]：余職反', '釋天弟八', '爾雅', '', DEFAULT, DEFAULT, '7764/1130;4299/649;3609/2081;8826/6649;2929/2300;5420/8703;6/1742;5244/6511;2507/8366;4199/9624', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5048, '攝提格;單閼;執徐;大荒落;敦牂;恊洽;涒灘;作咢;閹茂;大淵獻;困敦;赤奮若', '', '太歲在寅曰攝提格1，在卯曰單閼2，在辰曰執徐3，在巳曰大荒落4，在午曰敦牂56，在未曰恊洽78，在申曰涒灘910，在酉曰作噩{作咢}1112，在戌曰閹茂1314，在亥曰大淵獻15，在子曰困敦1617，在丑曰赤奮若18。', '[釋文-1]：徒兮反。補李巡曰：「言萬物承陽而起。格，起也」\n[釋文-2]：亡巧反。音丹，李云：「盡也。」又音蟬，或音善。於葛反，李云：「止也。」又於虔反\n[釋文-3]：此二字依文讀，李云：「執，蟄也，徐，舒也，言蟄物皆敷舒而出，故曰執徐也」\n[釋文-4]：音祀。補李巡曰：「言萬物皆熾茂而大出，霍然落落。荒，大也」\n[音釋-5]：臧。\n[釋文-6]：如字，韋昭音頓。子郎反。補李巡曰：「言萬物皆茂壯，猗那其枝，故曰敦牂。敦，茂也。牂，壯也」\n[音釋-7]：峽。\n[釋文-8]：協，音叶。戸夾反。補李巡曰：「言陰陽化生萬物和合，故曰協洽。協，和也。洽，合也」\n[音釋-9]：湯昆。\n[釋文-10]：湯昆反。本或作攤，郭勑丹、勑旦二反，字林大安、他安二反。補李巡曰：「言萬物皆循脩其精氣，故曰涒灘。灘，單。單，盡也」\n[音釋-11]：五各。\n[釋文-12]：本或作咢字，同，五各反，漢書作詻，韋昭音折（⿰扌咢），案聲類詻音五格反。補李巡曰：「言萬物墜落，故曰作鄂。作，索也。鄂，茂也」\n[音釋-13]：掩。\n[釋文-14]：先律反。於檢反，漢書作掩，同。補李巡曰：「言萬物皆蔽冒，故曰閹茂。閹，蔽也。茂，冒也」\n[釋文-15]：補李巡曰：「言萬物落於亥，大小深藏屈迎陽，故曰淵獻。淵，藏也。獻，迎也」\n[音釋-16]：頓。\n[釋文-17]：都鈍反。補李巡曰：「言陽氣皆混沌，萬物芽孽也。困，混。敦，沌也。」\n[釋文-18]：方問反。補李巡曰：「言陽氣奮迅，萬物而起，無不若其性，故曰赤奮若。赤，陽色。奮，迅也。若，順也」', '釋天弟八', '爾雅', '', DEFAULT, DEFAULT, '7868/7881/3603;975/7764;6648/1238;6598/546/549;2039/2342;9258/7332;7393/7272;5075/974;7777/522;6598/7211/6366;3931/2039;6587/2321/599', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5049, '歲;祀;年;載', '', '載1，歲也。夏曰歲2，3商曰祀4，5周曰年6，7唐虞曰載8。9', '[釋文-1]：補李巡曰：「莫不覆載也」又曰：「各自紀事，堯 舜三代示不相襲也」\n[釋文-2]：胡雅反，𦙍征云：「每歲孟春」\n[郭注-3]：取歲星行一次。\n[釋文-4]：太甲云：「惟元祀」\n[郭注-5]：取四時一終。\n[釋文-6]：見春秋、尚書\n[郭注-7]：取禾一熟。\n[釋文-8]：堯典云：「朕在位七十載」\n[郭注-9]：取物終更始。', '釋天弟八', '爾雅', '', DEFAULT, DEFAULT, '1082;31;4484;9571', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5050, '畢;橘;修;圉;厲;則;窒;塞;終;極', '㪤;䢦;攸;𡫳', '月在甲曰畢[㪤]，在乙曰橘[䢦]1，在丙曰修[攸]2，在丁曰圉3，在戊曰厲，在己曰則4，在庚曰窒56，在辛曰塞[𡫳]78，在壬曰終，在癸曰極。', '[釋文-1]：均筆反\n[釋文-2]：本亦作脩\n[釋文-3]：音語\n[釋文-4]：音紀\n[音釋-5]：知乙。\n[釋文-6]：知乙反\n[音釋-7]：先北。\n[釋文-8]：先北反', '釋天弟八', '爾雅', '1;2;3;8', DEFAULT, DEFAULT, '2492;3414;5716;6649;5985;2756;4666;9095;8590;3626', '2047;1095;2034;3035', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5051, '陬;如;寎;余;臯;且;相;壯;玄;陽;辜;涂', '', '正月爲陬12，3二月爲如4，三月爲寎56，四月爲余7，五月爲臯8，六月爲且910，七月爲相1112，八月爲壯13，九月爲玄14，15十月爲陽16，17十一月爲辜18，十二月爲涂1920。21', '[音釋-1]：側留。\n[釋文-2]：音征。側留、子侯二反，隅也，又子瑜反。補楚帛作取\n[郭注-3]：離騷云：「攝提貞於孟陬。」\n[釋文-4]：補李巡曰：「萬物載甲負莩，其性自如也。」楚帛作女\n[音釋-5]：孚柄。\n[釋文-6]：本或作窉字，同，郭孚柄反，又況病反，又匡詠反，李陂病反。補楚帛作秉\n[釋文-7]：餘、舒二音，孫作舒，李云：「余，舒也，萬物生枝葉，故曰舒也」\n[釋文-8]：或作高，同。補李巡曰：「萬物盛壯，故曰臯。臯，大也。」楚帛作𬅠\n[音釋-9]：子余。\n[釋文-10]：子余反。補李巡曰：「陰氣將盛，萬物將衰，故曰且。且，將也」\n[音釋-11]：息亮。\n[釋文-12]：息亮反。補李巡曰：「萬物勁剛，大小善惡皆可視而相。」楚帛作倉\n[釋文-13]：側亮反。補李巡曰：「萬物成熟，形體剛。」楚帛作臧\n[釋文-14]：補李巡曰：「萬物畢盡，陰氣侵寒，其色皆黑」\n[郭注-15]：國語云：「至於玄月」是也。\n[釋文-16]：本或作霷字，同，詩云：「日月陽止」是也。補李巡曰：「萬物深藏，伏而待陽也」\n[郭注-17]：純陰用事，嫌於無陽，故以名云。\n[釋文-18]：音姑。補李巡曰：「萬物虛無，須陽任養，故曰辜。辜，任也。」楚帛作姑\n[音釋-19]：徒。\n[釋文-20]：音徒。補李巡曰：「萬物始牙，陽氣尙微，故曰塗。塗，微也」\n[郭注-21]：皆月之別名。自歲陽至此，其事義皆所未詳通者，故闕而不論。', '釋天弟八', '爾雅', '', DEFAULT, DEFAULT, '9629;8245;4692;718;6662;9473;2162;228;2507;9624;9758;7001', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5052, '愷風;谷風;涼風;泰風', '𩘁風', '南風謂之凱風{愷風}12，3東風謂之谷風4，5北風謂之涼風[𩘁風]6，7西風謂之泰風8。910', '[音釋-1]：口改。\n[釋文-2]：颽，口海反，又作凱。補李巡曰：「長養萬物，萬物喜樂，故曰凱風。凱，樂也」\n[郭注-3]：詩曰：「凱風自南。」\n[釋文-4]：補孫炎曰：「谷之言穀，穀，生也，谷風者，生長之風也」\n[郭注-5]：詩云：「習習谷風。」\n[釋文-6]：本或作古𩘁字，同，力張反\n[郭注-7]：詩云：「北風其涼。」\n[釋文-8]：補孫炎曰：「西風成，物物豐泰也」\n[郭注-9]：詩云：「泰風有隧。」\n[釋文-10]：隧，音遂。', '釋天弟八', '爾雅', '3', DEFAULT, DEFAULT, '3094/8979;7502/8979;7391/8979;7428/8979', '8980/8979', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5053, '穨;猋;庉;飄', '飆;焞', '焚輪謂之穨12，3扶揺謂之猋[飆]45，67風與火爲庉[焞]89，1011迴風爲飄1213。14', '[音釋-1]：徒回。\n[釋文-2]：棼，符云反，本或作焚。頽，本或作穨、隤，同，徒回反\n[郭注-3]：暴風從上下。\n[音釋-4]：必遥。\n[釋文-5]：如字，字林作𩖬，同。音遥，字林作颻，同。必遥反，字林作飊，音同\n[郭注-6]：暴風從下上。\n[釋文-7]：上，時掌反。\n[音釋-8]：徒衮。\n[釋文-9]：徒衮、徒昆二反，本或作炖字，同\n[郭注-10]：庉庉，熾盛之貌。\n[釋文-11]：熾，尺志反。\n[音釋-12]：瓢。\n[釋文-13]：音瓢\n[郭注-14]：旋風也。', '釋天弟八', '爾雅', '2;3', DEFAULT, DEFAULT, '5475;6385;5930;8983', '8982;6495', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5054, '暴;霾;曀', '', '日出而風爲暴1，2風而雨土爲霾34，5陰而風爲曀67。8', '[釋文-1]：薄報反。補孫炎曰：「陰雲不興而大風暴起」\n[郭注-2]：詩云：「終風且暴。」\n[音釋-3]：芋。\n[釋文-4]：音芋，下「雨䨘」、注「雨雪」同。亡皆反，字林云亡戒反，本今作霾。補孫炎曰：「大風揚塵土，從上下也」\n[郭注-5]：詩曰：「終風且霾。」\n[音釋-6]：於計。\n[釋文-7]：於計反。補孫炎曰：「雲風，曀日光」\n[郭注-8]：詩曰：「終風且曀。」', '釋天弟八', '爾雅', '', DEFAULT, DEFAULT, '4248;7566;4229', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5055, '霿;霚;晦', '', '天氣下，地不應曰霿1。2。3地氣發，天不應曰霧{霚}4。霿謂之晦5。67', '[音釋-1]：譍。蒙\n[釋文-2]：於證反，下同。或作霧字，同，亡公、亡侯二反\n[郭注-3]：言蒙昧。\n[釋文-4]：亡弄反，又亡付反，字林作𩅬，音司，本亦作霿\n[釋文-5]：音誨\n[郭注-6]：言晦冥。\n[釋文-7]：冥，亡定反。', '釋天弟八', '爾雅', '', DEFAULT, DEFAULT, '7567;7565;4227', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5056, '螮蝀;雩;虹;蜺;挈貳', '霓', '螮蝀謂之雩12。螮蝀，虹也3。4蜺[霓]爲挈貳56。7', '[音釋-1]：帝。丁孔。于句。\n[釋文-2]：丁計反，本或作蝃、蚚，同。丁孔反，詩作東，音同，又德紅反。案字林越俱反，今借爲芌，音于句反\n[釋文-3]：胡公反，字林云工弄反，陳國武古巷反，郭音講，俗亦呼爲靑絳也\n[郭注-4]：俗名爲美人虹，江東呼雩音芌。\n[音釋-5]：苦結。\n[釋文-6]：霓，五兮反，如淳五結反，郭五擊反，音義云：「雄曰虹，雌曰霓。」說文曰：「屈虹，靑赤也。一曰白色陰氣也。」故孟子云：「若大旱之望雲霓也。」本或作蜺，漢書同。本或作契字，同，苦結反。而至反\n[郭注-7]：蜺，雌虹也，見離騷。挈貳，其別名，見尸子。', '釋天弟八', '爾雅', '4', DEFAULT, DEFAULT, '8936/8937;7570;8935;8870;7859/3967', '7568', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5057, '蔽雲', '', '弇日爲蔽雲12。34', '[音釋-1]：掩。\n[釋文-2]：音掩\n[郭注-3]：卽暈氣五彩覆日也。\n[釋文-4]：暈，音運。', '釋天弟八', '爾雅', '', DEFAULT, DEFAULT, '550/7578', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5058, '霆霓', '', '疾雷爲霆霓12。34', '[音釋-1]：廷。倪。\n[釋文-2]：徒丁反，字林同，又徒佞、徒頂二反，說文云：「雷餘聲鈴鈴，所以挺出萬物也」\n[郭注-3]：雷之急激者謂霹靂。\n[釋文-4]：激，古歷反。霹，普覓反。靂，力狄反，楊雄羽獵賦云：「霹靂列缺，吐火施鞭。」史記云：「霹靂者，陽氣動也。」', '釋天弟八', '爾雅', '', DEFAULT, DEFAULT, '7530/7568', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5059, '霄雪', '', '雨䨘爲霄雪12。3', '[音釋-1]：酥練。\n[釋文-2]：本或作霰、𩆵，同，悉練反。音消，本亦作消，說文曰：「雨䨘爲霄，齊語也」\n[郭注-3]：詩曰：「如彼雨雪，先集維䨘。」䨘，水雪雜下者，故謂之消雪。', '釋天弟八', '爾雅', '', DEFAULT, DEFAULT, '7535/7534', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5060, '涷;霢霂;淫;霖;霽', '驟;𩂢', '暴雨謂之涷[驟]12，34小雨謂之霡霂{霢霂}56，7久雨謂之淫[𩂢]，8淫謂之霖9，1011濟謂之霽1213。14', '[音釋-1]：東。\n[釋文-2]：都貢反，郭音東\n[郭注-3]：今江東呼夏月暴雨爲涷雨。離騷云：「令飄風兮先驅，使涷雨兮灑塵」是也。涷音東西之東。\n[釋文-4]：令，力呈反。先驅，如字，下豈俱、羌句二反。灑，所買、所綺二反。\n[音釋-5]：麥。木。\n[釋文-6]：亡革反，字林作霢，音同，又亡狄反。亡禄反。補李巡曰：「冰雪俱下」\n[郭注-7]：詩曰：「益之以霡霂。」\n[郭注-8]：左傳曰：「天作淫雨。」\n[音釋-9]：林\n[郭注-10]：雨自三日已上爲霖。\n[釋文-11]：上，時掌反。\n[音釋-12]：祖細。徂計。\n[釋文-13]：祖細反。字林子系反，云：「雨止也。」郭祖禮反，一音徂細反\n[郭注-14]：今南陽人呼雨止爲霽，音薺。', '釋天弟八', '爾雅', '1;3', DEFAULT, DEFAULT, '6988;7542/7543;7223;7550;7560', '6206;7551', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5061, '壽星;角;亢;天根;氐', '', '壽星，角、亢也12。34天根，氐也56。7', '[音釋-1]：剛。\n[釋文-2]：音剛，又口浪反，或戸剛反\n[郭注-3]：數起角、亢，列宿之長，故曰壽。\n[釋文-4]：數，色住反。宿，夙又反，下同。長，丁丈反。\n[音釋-5]：低。\n[釋文-6]：都黎反，郭音觝，丁禮反，注云：「若木之有根。」尋義應作丁計反\n[郭注-7]：角、亢下繫於氐，若木之有根。', '釋天弟八', '爾雅', '', DEFAULT, DEFAULT, '5384/4316;2827;6655;3/3564;8371', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5062, '天駟;房;大辰;大火', '', '天駟，房也。1大辰，房心、尾也。2大火謂之大辰。3', '[郭注-1]：龍爲天馬，故房四星謂之天駟。\n[郭注-2]：龍星明者，以爲時候，故曰大辰。\n[郭注-3]：大火，心也，在中最明，故時候主焉。', '釋天弟八', '爾雅', '', DEFAULT, DEFAULT, '3/6192;7725;6598/9795;6598/6416', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5063, '析木;箕;斗;漢津', '', '析木謂之津12。3箕、斗之閒，漢津也。4', '[音釋-1]：惜。\n[釋文-2]：星歷反\n[郭注-3]：卽漢津也。\n[郭注-4]：箕，龍尾。斗，南斗。天漢之津梁。', '釋天弟八', '爾雅', '', DEFAULT, DEFAULT, '3810/3413;3019;9491;7009/7281', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5064, '星紀;斗;牽牛', '', '星紀，斗、牽牛也。1', '[郭注-1]：牽牛、斗者，日月五星之所終始，故謂之星紀。', '釋天弟八', '爾雅', '', DEFAULT, DEFAULT, '4316/8542;9491;755/728', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5065, '玄枵;虛;顓頊;北陸', '', '玄枵12，虛也。34顓頊之虛56，虛也。7北陸，虛也8。9', '[音釋-1]：許嬌。\n[釋文-2]：許嬌反，左傳云：「枵，耗名也」\n[郭注-3]：虛在正北，北方色黑。枵之言耗，耗亦虛意。\n[釋文-4]：耗，呼報反。\n[音釋-5]：專。旭。墟。\n[釋文-6]：音專。許玉反。顓頊，帝高陽氏。此一字音墟，下如字\n[郭注-7]：顓頊水德，位在北方。\n[釋文-8]：補孫炎曰：「陸，中也，北方之宿，虚爲中也」\n[郭注-9]：虛星之名凡四。', '釋天弟八', '爾雅', '', DEFAULT, DEFAULT, '2507/3587;5234;5657/5658;5231/9625', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5066, '營室;定;諏觜', '', '營室謂之定12。3娵觜{諏觜}之口45，營室東壁也6。7', '[音釋-1]：多佞。\n[釋文-2]：顁，本或作定，同，多佞反\n[郭注-3]：定，正也。作宮室皆以營室中爲正。\n[音釋-4]：子瑜。咨。\n[釋文-5]：子瑜反。咨移反，又子髓反\n[釋文-6]：辟，本又作壁，布覓反，今案：此星有人居之角象，宜爲壁。補李巡曰：「娵觜，玄武宿也。營室東壁，北方宿名」\n[郭注-7]：營室東壁，星四方似口，因名云。', '釋天弟八', '爾雅', '', DEFAULT, DEFAULT, '4631/4559;4576;1510/2850', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5067, '降婁;奎;婁', '', '降婁12，奎、婁也3。4', '[音釋-1]：胡江。\n[釋文-2]：胡江反，又江巷反，注同。郎侯反。補李巡曰：「白虎宿」\n[釋文-3]：口圭反\n[郭注-4]：奎爲溝瀆，故名降。', '釋天弟八', '爾雅', '', DEFAULT, DEFAULT, '9649/8323;6599;8323', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5068, '大梁;昴;西陸', '', '大梁，昴也1。西陸，昴也。23', '[釋文-1]：音卯，本或作𣆻，同。補孫炎曰：「畢、昴之閒，日月五星出入要道若津梁」\n[郭注-2]：昴，西方之宿，別名旄頭。\n[釋文-3]：旄，音毛。', '釋天弟八', '爾雅', '', DEFAULT, DEFAULT, '6598/3787;4232;7714/9625', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5069, '濁;畢', '噣', '濁[噣]謂之畢1。23', '[釋文-1]：補李巡曰：「噣，陰氣獨起，陽氣必止，故曰畢。畢，止也」\n[郭注-2]：掩兔之畢，或呼爲濁，因星形以名。\n[釋文-3]：兔，佗故反。', '釋天弟八', '爾雅', '1', DEFAULT, DEFAULT, '7084;2492', '782', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5070, '咮;柳;鶉火', '', '咮謂之柳12。3柳，鶉火也4。5', '[音釋-1]：渚究。\n[釋文-2]：猪究反，本或作喙，許穢反，說文云：「喙，口也。」昌銳反。力九反\n[郭注-3]：咮，朱鳥之口。\n[釋文-4]：音純\n[郭注-5]：鶉，鳥名。火屬南方。', '釋天弟八', '爾雅', '', DEFAULT, DEFAULT, '951;3514;2307/6416', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5071, '北極;北辰', '', '北極謂之北辰。1', '[郭注-1]：北極，天之中，以正四時。', '釋天弟八', '爾雅', '', DEFAULT, DEFAULT, '5231/3626;5231/9795', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5072, '何鼓;牽牛', '', '何鼓謂之牽牛12。34', '[音釋-1]：胡可。\n[釋文-2]：郭胡可反，又胡多反，注同，小爾雅云：「任也。」說文云：「檐也。」注作荷字，音同。補李巡曰：「皆二十八宿名也。」孫炎曰：「河鼓之旗十二星，在牽牛之北，故名河鼓爲牽牛也」\n[郭注-3]：今荆楚人呼牽牛星爲檐鼓。檐者，荷也。\n[釋文-4]：檐，丁甘反，字林：「負也。」', '釋天弟八', '爾雅', '', DEFAULT, DEFAULT, '5025/3083;755/728', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5073, '明星;启明', '', '明星謂之启明12。34', '[音釋-1]：啟。\n[釋文-2]：口禮反\n[郭注-3]：太白星也。晨見東方爲启明，昏見西方爲太白。\n[釋文-4]：見，賢遍反，下同。', '釋天弟八', '爾雅', '', DEFAULT, DEFAULT, '4323/4316;870/4323', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5074, '彗星;鑱槍', '', '彗星爲欃槍{鑱槍}12。34', '[音釋-1]：初銜。初庚。\n[釋文-2]：篲，恤遂反，又似酢、似銳二反，本今作彗。初銜、仕杉二反。槍，初庚、七羊二反\n[郭注-3]：亦謂之孛，言其形孛孛似埽彗。\n[釋文-4]：孛，蒲忽反，或音佩。埽，素報反。彗，似銳反，又音遂。', '釋天弟八', '爾雅', '', DEFAULT, DEFAULT, '1927/4316;9368/3666', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5075, '奔星;仢約', '', '奔星爲彴約{仢約}12。3', '[音釋-1]：蒲握。\n[釋文-2]：蒲博、步角、皮約三反。如字，又於詔反，又音握\n[郭注-3]：流星。', '釋天弟八', '爾雅', '', DEFAULT, DEFAULT, '6626/4316;5036/8570', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5076, '祠;礿;甞;蒸', '', '春祭曰祠1，23夏祭曰礿45，67秋祭曰甞，8冬祭曰蒸9。10', '[釋文-1]：如字，或音祀\n[郭注-2]：祠之言食。\n[釋文-3]：食，音嗣。\n[音釋-4]：藥。\n[釋文-5]：本或作禴字，同，餘弱反\n[郭注-6]：新菜可汋。\n[釋文-7]：汋，余弱反，燂菜也。\n[郭注-8]：甞新穀。\n[釋文-9]：之升反\n[郭注-10]：進品物也。', '釋天弟八', '爾雅', '', DEFAULT, DEFAULT, '41;42;3044;623', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5077, '燔柴;瘞薶', '燔祡', '祭天曰燔柴[燔祡]12，3祭地曰瘞薶4。5', '[音釋-1]：煩。\n[釋文-2]：音煩，猶焚也。仕皆反，說文作祡，云：「燒柴燎祭天也」\n[郭注-3]：旣祭，積薪燒之。\n[釋文-4]：於例、於計二反。音埋\n[郭注-5]：旣祭，埋藏之。', '釋天弟八', '爾雅', '1', DEFAULT, DEFAULT, '6425/3613;9139/626', '6425/32', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5078, '庪縣;浮沈', '浮湛', '祭山曰庪縣12，3祭川曰浮沈[浮湛]4。5', '[音釋-1]：居委。玄。\n[釋文-2]：本或作庋，又作攱，同，居委、居僞二反。音玄，注同。補李巡曰：「黃玉及璧庋置几上，遙遙而眡之若縣」\n[郭注-3]：或庪或縣，置之於山，山海經曰：「縣以吉玉」是也。\n[釋文-4]：直今反\n[郭注-5]：投祭水中，或浮或沈。', '釋天弟八', '爾雅', '2', DEFAULT, DEFAULT, '5975/5707;7180/7320', '7180/7296', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5079, '布;磔', '', '祭星曰布1，2祭風曰磔34。56', '[釋文-1]：李曰：「祭星者以祭布露地，故曰布。」孫曰：「旣祭，布散於地，似星布列也。」郭云：「布㪚祭於地也。」指謂敷列羅布也\n[郭注-2]：布散祭於地。\n[音釋-3]：責。\n[釋文-4]：張格反。補李巡曰：「以牲頭、蹄及皮，破之以祭。」孫炎曰：「旣祭，披磔其牲，以風散之也」\n[郭注-5]：今俗當大道中磔狗，云以止風，此其象。\n[釋文-6]：狗，音苟。', '釋天弟八', '爾雅', '', DEFAULT, DEFAULT, '4912;3411', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5080, '禡', '', '{禡}「是禷是禡12」，師祭也。3', '[音釋-1]：類。罵。\n[釋文-2]：音類，經典作類。亡駕反\n[郭注-3]：師出征伐，類於上帝，禡於所征之地。', '釋天弟八', '爾雅', '', DEFAULT, DEFAULT, '62', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5081, '䮻', '', '{䮻}「旣伯旣禱」1，馬祭也。2', '[釋文-1]：丁老反，說文亦作禂，同\n[郭注-2]：伯，祭馬祖也。將用馬力，必先祭其先。', '釋天弟八', '爾雅', '', DEFAULT, DEFAULT, NULL, NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5082, '禘', '', '禘12，大祭也。3', '[音釋-1]：大計。\n[釋文-2]：大計反\n[郭注-3]：五年一大祭。', '釋天弟八', '爾雅', '', DEFAULT, DEFAULT, '43', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5083, '繹;䑣;復胙', '', '繹1，又祭也。23周曰繹，4商曰肜{䑣}56，7夏曰復胙89。10', '[釋文-1]：以石反，五經及爾雅皆作此字，本或作𥜃，字書爲䭞、鐸二字，同，下同\n[郭注-2]：祭之明日，尋繹復祭。\n[釋文-3]：復，扶又反，下注「不復」同。\n[郭注-4]：春秋經曰：「壬午猶繹。」\n[音釋-5]：容。\n[釋文-6]：余終反。補孫炎曰：「相尋不絕之意」\n[郭注-7]：書曰：「高宗肜日。」\n[音釋-8]：昨。\n[釋文-9]：戸雅反。音服。昨，才各反，本又作祚，亦作胙，同，才故反。祚，福也；胙，祭肉也，於義並同\n[郭注-10]：未見義所出。', '釋天弟八', '爾雅', '', DEFAULT, DEFAULT, '8520;5441;1226/2670', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5084, '蒐;苗;𤣗;狩', '𢯱;守', '春獵爲蒐[𢯱]12，34夏獵爲苗5，67秋獵爲獮{𤣗}89，10冬獵爲狩[守]1112。13', '[音釋-1]：搜。\n[釋文-2]：力涉反。色留反\n[郭注-3]：搜索取不任者。\n[釋文-4]：捜，音同上。索，色白反。任，而鴆反。\n[釋文-5]：補孫炎曰：「治苗去不秀實者」\n[郭注-6]：爲苗稼除害。\n[釋文-7]：爲，于僞反。\n[音釋-8]：息淺。\n[釋文-9]：息淺反，說文從繭，或作𥜲，從示\n[郭注-10]：順殺氣也。\n[音釋-11]：手又。\n[釋文-12]：手又反\n[郭注-13]：得獸取之，無所擇。', '釋天弟八', '爾雅', '1;4', DEFAULT, DEFAULT, '383;542;6359;6362', '8092;4596', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5085, '獠;狩', '', '宵田爲獠12，34火田爲狩。5', '[音釋-1]：遼。\n[釋文-2]：音消，夜也，字從宀，若雲霄之字卽從雨。郭音遼，夜獵也，又力召、力弔二反，或作燎，宵田也\n[郭注-3]：管子曰：「獠、獵畢弋。」今江東亦呼獵爲獠，音遼。或曰：卽今夜獵載鑪照也。\n[釋文-4]：罼，音畢，本又作畢。鑪，力吳反，字或作壚。\n[郭注-5]：放火燒草，獵亦爲狩。', '釋天弟八', '爾雅', '', DEFAULT, DEFAULT, '6361;6362', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5086, '社', '', '{社}「乃立冢土1，戎醜攸行2。」34起大事，動大衆，必先有事乎社而後出，謂之宜。5', '[釋文-1]：竹勇反\n[釋文-2]：音由\n[郭注-3]：冢土，大社。戎醜，大衆。\n[釋文-4]：大，音泰，下「大常」同。\n[郭注-5]：有事，祭也。周官所謂「宜乎社」。', '釋天弟八', '爾雅', '', DEFAULT, DEFAULT, '64', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5087, '闐闐', '嗔嗔', '{闐闐}[嗔嗔]「振旅闐闐12。」34出爲治兵5，尚威武也。6入爲振旅，反尊卑也。7', '[音釋-1]：田。\n[釋文-2]：徒天反\n[郭注-3]：振旅，整衆。闐闐，羣行聲。\n[釋文-4]：整，之領反。\n[釋文-5]：音持\n[郭注-6]：幼賤在前，貴勇力，\n[郭注-7]：尊老在前，復常儀也。', '釋天弟八', '爾雅', '1', DEFAULT, DEFAULT, '7775/7775', '863/863', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5088, '杠;縿', '', '素錦綢杠12，34纁帛縿56，78素陞龍于縿9，1011練旒九，12飾以組，1314維以縷。15', '[音釋-1]：叨。江。\n[釋文-2]：他刀反。音江，廣雅云：「天子之杠，高九仞，諸侯七仞，卿大夫五仞，士三仞」\n[郭注-3]：以白地錦韜旗之竿。\n[釋文-4]：韜，他刀反。\n[音釋-5]：勲。杉。\n[釋文-6]：許云反，本或作𧞬，又作襂、𧛡、衫字，同，所銜反，下同\n[郭注-7]：纁帛，絳也。縿，衆旒所著。\n[釋文-8]：旒，力周反，經典亦作流，廣雅云：「天子十二旒至地，諸侯九旒至軫，卿大夫七旒至轂，士三旒至肩。」著，直畧反。\n[釋文-9]：音升\n[郭注-10]：畫白龍於縿，令上向。\n[釋文-11]：上，力呈反。\n[郭注-12]：練，絳練也。\n[郭注-13]：用綦組飾旒之邊。\n[釋文-14]：綦，音其，本亦作纂，祖管反。音式。\n[郭注-15]：用朱縷維連持之，不欲令曳地，周禮曰：「六人維王之太常」是也。', '釋天弟八', '爾雅', '', DEFAULT, DEFAULT, '3676;8690', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5089, '旐;旆', '', '緇廣充幅長尋曰旐12，3繼旐曰旆45。6', '[音釋-1]：古曠。直亮。兆。\n[釋文-2]：側基反。古曠反。方言云：「幅廣曰充。」音福。直亮反，注同。持小反。補孫炎曰：「緇，黑繒也。帛續旐末，亦長尋，詩云『帛旆英英』是也」\n[郭注-3]：帛全幅長八尺。\n[音釋-4]：佩。\n[釋文-5]：蒲蓋反\n[郭注-6]：帛續旐末爲燕尾者，義見詩。', '釋天弟八', '爾雅', '', DEFAULT, DEFAULT, '4292;4293', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5090, '旌', '', '注旄首曰旌12。34', '[音釋-1]：毛。\n[釋文-2]：之樹反。亡襃反。音精，本又作旍\n[郭注-3]：載旄於竿頭，如今之幢，亦有旒。\n[釋文-4]：竿，音干。幢，直江反。', '釋天弟八', '爾雅', '', DEFAULT, DEFAULT, '4294', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5091, '旂', '', '有鈴曰旂1。23', '[釋文-1]：郎丁反。音祈。補李巡曰：「以鈴著旐端」\n[郭注-2]：縣鈴於竿頭，畫蛟龍於旒。\n[釋文-3]：縣，音玄。', '釋天弟八', '爾雅', '', DEFAULT, DEFAULT, '4296', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5092, '旟', '', '錯革鳥曰旟12。34', '[音釋-1]：餘。\n[釋文-2]：七各反，又七故反。羊諸反。補李巡曰：「以革爲之，置於旒端。」孫炎曰：「錯，置也。革，急也。言畫急疾之鳥於旐上也，周官所謂『鳥準爲旟』者矣」\n[郭注-3]：此謂合剥鳥皮毛，置之竿頭，卽禮記云「載鴻」及「鳴鳶」。\n[釋文-4]：剥，北角反。鳶，以專反，鴟也。', '釋天弟八', '爾雅', '', DEFAULT, DEFAULT, '4295', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5093, '旃', '', '因章曰旃1。2', '[釋文-1]：之然反，世本云：「黃帝作旃」\n[郭注-2]：以帛練爲旒，因其文章，不復畫之。周禮云：「通帛爲旃。」', '釋天弟八', '爾雅', '', DEFAULT, DEFAULT, '4299', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5094, '冀州;豫州;雝州;荆州;揚州;兖州;徐州;幽州;營州', '楊州', '兩河閒曰冀州1。2河南曰豫州3。4河西曰雝州56。7漢南曰荆州8。9江南曰揚州[楊州]10。11濟 河閒曰兖州1213。14濟東曰徐州15。16燕曰幽州17。18齊曰營州19。20', '[釋文-1]：郭云：「自東河至西河。」周禮云：「河內曰冀州。」韋昭注漢書云：「東西南北皆有河，故曰河內。」馬融曰：「在東河之西，西河之東，南河之北。」李巡云：「兩河閒其氣淸，厥性相近，故曰冀，冀，近也」\n[郭注-2]：自東河至西河。\n[釋文-3]：郭云：「自南河至漢也。」周禮亦云：「河南曰豫州。」尚書云：「荆 河惟豫州。」孔傳曰：「西南至荆山，北距河水。」李巡云：「河南其氣著密，厥性安舒，故曰豫，豫，舒也。」春秋元命包云：「豫之言序也，言陽氣分布，各得其處，故其氣平靜多序也」\n[郭注-4]：自南河至漢。\n[音釋-5]：於用。\n[釋文-6]：於用反，郭云：「自西河至黑水。」尚書云：「黑水西河惟雍州。」孔傳云：「西距黑水，東據河。」周禮云：「正西曰雍州。」雍者，擁也，東崤，西漢，南商於，北居庸，四山之內擁翳也。李巡云：「河西其氣蔽壅，厥性急凶，故曰雍，雍，壅也。」太康地記云：「雍州兼得梁州之地，西北之位，陽所不及，陰氣壅閼，故取名焉。」案周禮及爾雅皆無「梁州」，則雍州兼有梁州之地也。禹貢云：「華陽 黑水惟梁州。」孔傳云：「東據華山之南，西距黑水。」韋昭云：「今益州也。」太康地記云：「梁州者，言西方金剛之氣彊梁，故因以爲名，漢時改雍州爲梁州，改梁州爲益州也」\n[郭注-7]：自西河至黑水。\n[釋文-8]：郭云：「自漢南至衡山之陽。」尚書云：「荆及衡陽惟荆州。」孔傳云：「北據荆山，南及衡山之陽。」周禮云：「正南曰荆州。」釋名云：「荆州者，取荆山之名，荆，警也，南蠻數爲寇逆，嘗警備故也。」補李巡曰：「漢南其氣燥剛，禀性強梁，故曰荊。荊，強也」\n[郭注-9]：自漢南至衡山之陽。\n[釋文-10]：郭云：「自江南至海。」尚書云：「淮 海惟揚州。」孔傳云：「北據淮，南距海。」周禮云：「東南曰揚州。」李巡云：「江南其氣燥勁，厥性輕揚。」太康地記云：「以揚州漸太陽位，天氣奮揚，履正含文明，故取名焉」\n[郭注-11]：自江南至海。\n[音釋-12]：子礼。\n[釋文-13]：本又作㴉，子禮反，下同。悅轉反，郭云：「自河東至濟。」尚書云：「濟 河惟兖州。」孔傳云：「東南據濟，西北距河。」周禮云：「河東曰兖州。」李巡云：「濟 河閒其氣專質，厥性信謹，故曰兖，兖，信也。」釋名以爲取兖水之義\n[郭注-14]：自河東至濟。\n[釋文-15]：郭云：「自濟東至海。」尚書云：「海 岱及淮惟徐州。」孔傳云：「東至海，北至岱，南及淮。」釋名云：「徐，舒也，土氣舒緩。」太康地記以爲取徐丘爲名，云：「周合其地於靑州。」案，周禮無營州，而有靑州\n[郭注-16]：自濟東至海。\n[釋文-17]：烏賢反。郭云：「自易水至北狄。」周禮云：「東北曰幽州。」李巡云：「燕其氣深要，厥性剽疾，故曰幽，幽，要也。」太康地記以爲因於幽都爲名，或云，北方太陰，故以幽冥爲號，二者相依也。周禮云：「正北曰并州。」春秋元命包云：「并之言併也，陽合交并，其氣勇壯，抱誠信也。」太康地記云：「并州不以衞水爲號，又不以恒山爲稱，而言并者，蓋以其在兩谷之閒也」\n[郭注-18]：自易水至北狄。\n[釋文-19]：郭云：「自岱東至海。」尚書云：「海 岱惟靑州。」孔傳云：「東北據海，西南距岱。」則爾雅營州爲禹貢之靑州矣。營者，蓋取營丘以爲號。周禮云：「正東曰靑州。」博物志云：「營與靑同，海東有靑丘，齊有營丘，豈是名乎？」太康地記云：「東方少陽，其色靑，其氣淸，歲之首，事之始，故以靑爲名焉」\n[郭注-20]：自岱東至海，此蓋殷制。', '釋地弟九', '爾雅', '5', DEFAULT, DEFAULT, '5232/7492;6136/7492;2300/7492;488/7492;7958/7492;7029/7492;1238/7492;2502/7492;4631/7492', '3512/7492', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5095, '大野;大陸;楊紆;孟諸;雲夢;具區;海隅;昭余祁;圃田;焦護', '昭餘祁;甫田', '魯有大野，12晉有大陸，3秦有楊陓{楊紆}45，67宋有孟諸8，910楚有雲夢11，12吳 越之閒有具區，1314齊有海隅15，1617燕有昭余祁[昭餘祁]18，1920鄭有圃田[甫田]21，2223周有焦護24。2526', '[郭注-1]：今高平 鉅野縣東北大澤是也。\n[釋文-2]：鉅，音巨。\n[郭注-3]：今鉅鹿北廣河澤是也。\n[音釋-4]：於于。\n[釋文-5]：孫於于反，郭烏花反，本或作紆字，非也\n[郭注-6]：今在扶風 汧縣西。\n[釋文-7]：汧，苦堅反，字林：「水出隴右 扶風也。」\n[釋文-8]：如字，左傳同，尚書作豬，丁魚反\n[郭注-9]：今在梁國 睢陽縣東北。\n[釋文-10]：濉，蘇維反，水名也，本今作睢。\n[釋文-11]：本或作䝉，亡貢、亡工二反\n[郭注-12]：今南郡 華容縣東南巴丘湖是也。\n[郭注-13]：今吳縣南太湖，卽震澤是也。\n[釋文-14]：大，音泰，本今作太。\n[釋文-15]：本或作嵎，同，仰于反\n[郭注-16]：海濵廣斥。\n[釋文-17]：斥，音赤。\n[釋文-18]：羊如反。巨伊反，又止尸反，孫本作厎，音之視反\n[郭注-19]：今太原 鄔陵縣北九澤是也。\n[釋文-20]：鄔，於慮反，字林云：「太原縣也。」又乙袪反。\n[釋文-21]：本或作𡇊字，同，布古反，又音布\n[郭注-22]：今滎陽 中牟縣西圃田澤是也。\n[釋文-23]：熒，乎銘反。\n[釋文-24]：穫，胡故反，又作護，同。補孫炎曰：「周，岐周也」\n[郭注-25]：今扶風 池陽縣 瓠中是也。\n[釋文-26]：瓠，胡故反。', '釋地弟九', '爾雅', '8;9', DEFAULT, DEFAULT, '6598/9171;6598/9625;3512/8558;9774/1488;7578/4329;1771/8419;7131/9630;4199/718/4063;3923/9172;6488/1562', '4199/3247/4063;2083/9172', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5096, '阠;息慎;威夷;朱滕;西隃;加陵;狊梁;河墳', '河坋', '東陵阠12、南陵息慎、西陵威夷、中陵朱滕3，北陵西隃45，鴈門是也。6陵莫大於加陵，7梁莫大於湨梁{狊梁}89，1011墳莫大於河墳[河坋]12。1314', '[音釋-1]：信。\n[釋文-2]：音信，郭尸慎反，字林所人反，又所慎反\n[釋文-3]：𣽨，徒登反，又作滕，同\n[音釋-4]：戍。\n[釋文-5]：戍、輸二音\n[郭注-6]：卽鴈門山也。\n[郭注-7]：今所在未聞。\n[音釋-8]：古壁。\n[釋文-9]：古壁反\n[郭注-10]：湨，水名。梁，隄也。\n[釋文-11]：隄，丁兮反。\n[釋文-12]：扶云反\n[郭注-13]：墳，大防。\n[釋文-14]：防，音房。', '釋地弟九', '爾雅', '8', DEFAULT, DEFAULT, '9711;6706/6714;8147/6615;3563/7172;7714/9680;9243/9620;6315/3787;6986/9143', '6986/9127', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5097, '珣玗璂;竹箭;犀;象;金石;珠;玉;璆琳;琅玕;筋角;文皮;五穀;魚;鹽', '', '東方之美者，有醫無閭之珣玗琪{珣玗璂}焉12。3東南之美者，有會稽之竹箭焉45。67南方之美者，有梁山之犀、象焉8。9西南之美者，有華山之金石焉10。1112西方之美者，有霍山之多珠、玉焉13。14西北之美者，有崐崘虛之璆琳、琅玕焉1516。17北方之美者，有幽都之筋角焉18。19東北之美者，有斥山之文皮焉2021。2223中有岱岳，與其五穀、魚、鹽生焉。2425', '[音釋-1]：荀。于。\n[釋文-2]：於其反，李本作毉，音同。胥均反，又音峻，說文云：「周書所謂夷玉，讀若宣。」音于。巨疑反\n[郭注-3]：醫無閭，山名，今在遼東。珣玗琪，玉屬。\n[音釋-4]：古外。古兮。\n[釋文-5]：古外反。古兮反。子賤反\n[郭注-6]：會稽，山名，今在山陰縣南。竹箭，篠也。\n[釋文-7]：篠，悉了反。\n[釋文-8]：音西\n[郭注-9]：犀牛皮角，象牙骨，\n[釋文-10]：戸花反，又戸化反\n[郭注-11]：黃金、礝石之屬。\n[釋文-12]：礝，本或作碝，同，如兖反，應劭注子虛賦云：「礝石出鴈門，白者若冰，半有赤色。」\n[釋文-13]：呼郭反\n[郭注-14]：霍山，今在平陽 永安縣東北。珠，如今雜珠而精好。\n[音釋-15]：路昆。虛。\n[釋文-16]：音昆。路昆反。羌魚反。其樛反，又音求。音林。瑯，音郎，又作琅，同。音干\n[郭注-17]：璆琳，美玉名。琅玕，狀似珠也。山海經曰：「崐崘山有琅玕樹。」\n[釋文-18]：音斤，本或作䈥字，非\n[郭注-19]：幽都，山名。謂多野牛筋角。\n[音釋-20]：尺。\n[釋文-21]：𢇛，昌亦反，又昌夜反\n[郭注-22]：虎豹之屬，皮有縟綵者。\n[釋文-23]：縟，音辱。\n[郭注-24]：言泰山有魚鹽之饒。\n[釋文-25]：饒，而遥反。', '釋地弟九', '爾雅', '', DEFAULT, DEFAULT, '97/182/133;2866/2867;769;6135;9261/6011;189;81;104/105;198/199;2741/2827;5725/1988;9727/4485;7580;7719', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5098, '鰈;兼兼;邛邛;巨虛;蟨;枳', '魼;蛩蛩;岐', '東方有比目魚焉，不比不行，其名謂之鰈[魼]12。34南方有比翼鳥焉5，不比不飛，其名謂之鶼鶼{兼兼}67。89西方有比肩獸焉10，與邛邛[蛩蛩]、岠虛{巨虛}比1112，爲邛邛、岠虛齧甘草1314，卽有難1516，邛邛、岠虛負而走，其名謂之蟨1718。1920北方有比肩民焉，迭食而迭望2122，2324中有枳[岐]首蛇焉2526。27此四方中國之異氣也。', '[音釋-1]：牒。\n[釋文-2]：本或作鰨，同，音牒，又勑臘、他盍二反\n[郭注-3]：狀似牛脾，鱗細，紫黑色，一眼，兩片相合乃得行。今水中所在有之，江東又呼爲王餘魚。\n[釋文-4]：脾，婢支反。\n[釋文-5]：本或作翌，又作𩙺，又作𦏵字，同，羊式反\n[音釋-6]：兼。\n[釋文-7]：古恬反\n[郭注-8]：似鳬，靑赤色，一目一翼，相得乃飛。\n[釋文-9]：鳧，音扶。\n[釋文-10]：音堅\n[音釋-11]：巨凶。巨。\n[釋文-12]：本或作（⿱邛馬），巨凶反。駏，音巨，本或作岠，又作𤝙，音同。驉，本或作虛，又作𤡣，同，許俱反\n[音釋-13]：五結。\n[釋文-14]：于僞反。五結反，本或作噬，謝音逝\n[音釋-15]：乃旦。\n[釋文-16]：奴旦反\n[音釋-17]：厥。\n[釋文-18]：郭音厥，孫居衞反，李云：「卭卭岠虛能走，蟨知美草，卽若驚難者，卭卭岠虛便負蟨而走，故曰比肩獸。」孫云：「卭卭岠虛狀如馬，前足鹿、後足兔，前高不得食而善走，蟨前足鼠、後足兔，善求食，走則倒，故齧甘草則仰食卭卭岠虛，卭卭岠虛負以走。」郭云：「今鴈門 夏屋山中有獸，形如兔而大，相負共行，土俗名之爲蟨鼠。」穆天子傳云：「卭卭岠虛走百里」之類也。司馬相如子虛賦云：「蹵卭卭，轔岠虛。」又爲二獸也\n[郭注-19]：呂氏春秋曰：「北方有獸，其名爲蟨，鼠前而兔後，趨則頓，走則顛。」然則邛邛岠虛，亦宜鼠後而兔前。前高不得取甘草，故須蟨食之。今鴈門 廣武縣 夏屋山中有獸形如兔而大，相負共行，土俗名之爲蟨鼠，音厥。\n[釋文-20]：趨，七俞反，作趍非。食，音嗣。夏，戸雅反。\n[音釋-21]：徒結。\n[釋文-22]：徒結反\n[郭注-23]：此卽半體之人，各有一鼻一孔、一臂一腳，亦猶魚鳥之相合，更望備驚急。\n[釋文-24]：更，音庚。\n[音釋-25]：居是。\n[釋文-26]：本或作䅩，顧音居是、諸是二反，郭巨宜反，孫音支，云：「蛇有枝首者，名曰率然。」施音指。案：枳首謂蛇有兩頭。舒酉反\n[郭注-27]：岐頭蛇也。或曰：今江東呼兩頭蛇爲「越王約髮」，亦名「弩絃」。', '釋地弟九', '爾雅', '1;3;6', DEFAULT, DEFAULT, '7683;4506/4506;4126/4126;3033/5234;8930;3519', '7583;8929/8929;4020', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5099, '郊;野;林;坰', '', '邑外謂之郊，郊外（謂之牧1，牧外）謂之野2，野外謂之林，林外謂之坰34。56', '[釋文-1]：亡卜反，養也，此謂牧養之地。說文從牛，或作目。李本牧作田字，釋云：「田，敶也，謂敶列種穀之處。」敶音陳\n[釋文-2]：本或作埜，古字，羊者反\n[音釋-3]：古螢。\n[釋文-4]：古塋反\n[郭注-5]：邑，國都也。假令百里之國，五十里之界，界各十里也。\n[釋文-6]：令，力呈反。', '釋地弟九', '爾雅', '', DEFAULT, DEFAULT, '4011;9171;3848;3324', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5100, '隰;平;原;陸;阜;陵;阿', '邍', '下溼曰隰12。大野曰平，廣平曰原[邍]3，高平曰陸4，大陸曰阜，大阜曰陵，大陵曰阿。', '[音釋-1]：濕。\n[釋文-2]：俗作濕，申入反。𨻽，本或作隰，音習。補李巡曰：「土地窊下常沮洳」\n[釋文-3]：補舍人曰：「土廣而平」\n[釋文-4]：補李巡曰：「土地豐正」', '釋地弟九', '爾雅', '3', DEFAULT, DEFAULT, '9645;3074;7496;9625;9619;9620;9626', '1204', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5101, '原;阪;溼', '', '可食者曰原。12陂者曰阪34。56下者曰溼7。8', '[郭注-1]：可種穀給食。\n[釋文-2]：種，之用反。\n[音釋-3]：披。反。\n[釋文-4]：彼宜反，字林或彼義反。又作坡，郭音普何反。甫晚反，字林父板反。補李巡曰：「高峯山陂」\n[郭注-5]：阪陀不平。\n[釋文-6]：陀，大何反。\n[釋文-7]：補李巡作隰，曰：「下，下溼之地。隰，溼也」\n[郭注-8]：公羊傳曰：「下平曰隰。」', '釋地弟九', '爾雅', '', DEFAULT, DEFAULT, '7496;9628;7347', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5102, '葘;新田;畬', '', '田一歲曰葘12。3二歲曰新田。4三歲曰畬5。67', '[音釋-1]：緇。\n[釋文-2]：本或作甾，同，側基反，孫音災\n[郭注-3]：今江東呼初耕地反草爲葘。\n[郭注-4]：詩曰：「于彼新田。」\n[音釋-5]：餘\n[郭注-6]：易曰：「不菑畬。」\n[釋文-7]：本或作畭，同，羊如反，字林弋恕反。', '釋地弟九', '爾雅', '', DEFAULT, DEFAULT, '564;9489/9172;9177', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5103, '泰遠;邠國;濮鉛;祝栗', '太遠;汃國', '東至於泰遠[太遠]1，西至於邠國[汃國]23，南至於濮鉛45，北至於祝栗6，謂之四極。7', '[釋文-1]：音太\n[音釋-2]：彬。\n[釋文-3]：本或作豳字，同，彼貧反，說文作汃字，音同\n[音釋-4]：卜。\n[釋文-5]：音卜。悅全反\n[釋文-6]：章六反，又昌六反\n[郭注-7]：皆四方極遠之國。', '釋地弟九', '爾雅', '1;2', DEFAULT, DEFAULT, '7428/1198;4021/3917;7069/9265;47/4393', '6598/1198;6985/3917', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5104, '觚竹;北戸;西王母;日下', '', '觚竹12、北戸、西王母、日下，謂之四荒。34', '[音釋-1]：孤。\n[釋文-2]：姜胡反，本又作孤，同\n[郭注-3]：觚竹在北，北戸在南，西王母在西，日下在東，皆四方昬荒之國，次四極者。\n[釋文-4]：西王母，是西方昬荒國名。又曰：西王母，神名，狀如人，虎齒豹尾，蓬頭戴勝，善處穴宿，周穆王巡狩至崐崘山見之，西王母亦來賓昭宮見。出竹書及穆天子傳。', '釋地弟九', '爾雅', '', DEFAULT, DEFAULT, '2857/2866;5231/7722;7714/78/8141;4189/9', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5105, '夷;狄;戎;蠻', '', '九夷、八狄、七戎、六蠻，謂之四海。1', '[郭注-1]：九夷在東，八狄在北，七戎在西，六蠻在南，次四荒者。', '釋地弟九', '爾雅', '', DEFAULT, DEFAULT, '6615;6372;8379;8933', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5106, '丹穴;空桐;太平;太蒙', '', '岠齊州以南戴日爲丹穴1，2北戴斗極爲空桐。3東至日所出爲太平4，西至日所入爲太蒙5。67太平之人仁，丹穴之人智，太蒙之人信，空桐之人武。8', '[釋文-1]：音巨。補舍人曰：「自中州以南，日光所照」\n[郭注-2]：岠，去也。齊，中也。\n[郭注-3]：戴，值。\r\n[釋文-4]：大，音泰，下同\r\n[釋文-5]：濛，音蒙，本今作蒙\r\n[郭注-6]：卽蒙汜也。\n[釋文-7]：汜，音祀，一音似，或云：祀、似同音，後放此。\n[郭注-8]：地氣使之然也。', '釋地弟九', '爾雅', '', DEFAULT, DEFAULT, '3186/4634;4648/3539;6598/3074;6598/649', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5107, '敦丘;陶丘;融丘;崐崘丘', '𠂤丘', '丘一成爲敦丘[𠂤丘]12，34再成爲陶丘5，67再成銳上爲融丘89，1011三成爲崐崘丘。12', '[音釋-1]：都昆。\n[釋文-2]：郭云音頓，或丁回反，謝如字，讀注宜如後二音\n[郭注-3]：成猶重也，周禮曰：「爲壇三成。」今江東呼地高堆者爲敦。\n[釋文-4]：重，直龍反，下同。壇，大干反。堆，丁回反。\n[釋文-5]：徒刀反，孫云：「形如累兩盂」\n[郭注-6]：今濟陰 定陶城中有陶丘。\n[釋文-7]：濟，子禮反。\n[音釋-8]：惠。\n[釋文-9]：唯歲反\n[郭注-10]：鑯頂者。\n[釋文-11]：鑯，子廉反。\n[郭注-12]：崐崘山三重，故以名云。', '釋丘弟十', '爾雅', '1', DEFAULT, DEFAULT, '2039/5233;9689/5233;1863/5233;5912/5913/5233', '9616/5233', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5108, '椉丘;陼丘', '', '如椉者椉丘12，34如陼者陼丘56。7', '[音釋-1]：繩正。\n[釋文-2]：乗，本又作椉，繩證反，注「車乗」同，李、郭皆云：「形如車乗。」又市陵反，或云：如稻田塍埒，下文並放此\n[郭注-3]：形似車椉也。或云椉謂稻田塍埒。\n[釋文-4]：塍，市陵反，許叔重云：「稻田畦隄埒畔。」埒，音劣。\n[音釋-5]：渚。\n[釋文-6]：章汝反，下同\n[郭注-7]：水中小洲爲陼。', '釋丘弟十', '爾雅', '', DEFAULT, DEFAULT, '3412/5233;9687/5233', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5109, '泥丘', '屔丘', '水潦所止12，泥丘[屔丘]3。45', '[音釋-1]：老。\n[釋文-2]：力道反\n[釋文-3]：乃兮反，依字作尼，又作屔\n[郭注-4]：頂上污下者。\n[釋文-5]：污，音烏，又烏花反，本或作洿。', '釋丘弟十', '爾雅', '1', DEFAULT, DEFAULT, '7108/5233', '5235/5233', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5110, '方丘;胡丘', '匚丘;柧丘', '方丘[匚丘]，胡丘[柧丘]。1', '[郭注-1]：形四方。', '釋丘弟十', '爾雅', '1;2', DEFAULT, DEFAULT, '5455/5233;2678/5233', '8425/5233;3802/5233', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5111, '京;丘', '', '絕高爲之，京。12非人爲之，丘。3', '[郭注-1]：人力所作。\n[釋文-2]：作，子各反。\n[郭注-3]：地自然生。', '釋丘弟十', '爾雅', '', DEFAULT, DEFAULT, '3331;5233', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5112, '埒丘', '', '水潦所還1，埒丘。23', '[釋文-1]：戸關反，又音患，下同，施音旋\n[郭注-2]：謂丘邊有界，埒水繞環之。\n[釋文-3]：繞，音遶。', '釋丘弟十', '爾雅', '', DEFAULT, DEFAULT, '9053/5233', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5113, '章丘', '敞丘', '上正，章丘[敞丘]。1', '[郭注-1]：頂平。', '釋丘弟十', '爾雅', '1', DEFAULT, DEFAULT, '1742/5233', '2017/5233', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5114, '都丘', '', '澤中有丘，都丘。1', '[郭注-1]：在池澤中。', '釋丘弟十', '爾雅', '', DEFAULT, DEFAULT, '4007/5233', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5115, '梧丘;畫丘;戴丘;昌丘', '', '當途1，梧丘2。3途出其右而還之4，畫丘5。6途出其前，戴丘，7途出其後，昌丘。8', '[釋文-1]：涂，字又作途，音圖\n[釋文-2]：五故反，又音吾\n[郭注-3]：途道。\n[音釋-4]：旋\n[釋文-5]：郭音獲，謝胡卦反\n[郭注-6]：言爲道所規畫。\n[郭注-7]：道出丘南。\n[郭注-8]：道出丘北。', '釋丘弟十', '爾雅', '', DEFAULT, DEFAULT, '3537/5233;1944/5233;1779/5233;4239/5233', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5116, '渻丘;沮丘;正丘;營丘', '', '水出其前，渻丘12，水出其後，沮丘34。水出其右，正丘。水出其左，營丘。56', '[音釋-1]：所景。\n[釋文-2]：本或作𣹴字，所景反\n[音釋-3]：辭與。\n[釋文-4]：孫、郭同，辭與、慈呂二反，謝子預反，施子余反\n[郭注-5]：今齊之營丘，淄水過其南及東。\n[釋文-6]：湽，仄其反。過，古臥反。', '釋丘弟十', '爾雅', '', DEFAULT, DEFAULT, '7230/5233;6999/5233;1087/5233;4631/5233', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5117, '敦丘', '𠂤丘', '如覆敦者12，敦丘[𠂤丘]。3', '[音釋-1]：堆。\n[釋文-2]：孚服反。丁回反，下及注同。補孫炎曰：「丘一成之，象形」\n[郭注-3]：敦，盂也。', '釋丘弟十', '爾雅', '1', DEFAULT, DEFAULT, '2039/5233', '9616/5233', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5118, '沙丘', '', '邐迆12，沙丘。3', '[音釋-1]：呂紙。余紙。\n[釋文-2]：呂紙反，說文云：「邐，行也。」字或作迤，余紙、余支二反，說文云：「迤，邪行也」\n[郭注-3]：旁行連延。', '釋丘弟十', '爾雅', '', DEFAULT, DEFAULT, '7238/5233', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5119, '咸丘;臨丘;旄丘;陵丘;阿丘', '', '左高，咸丘。右高，臨丘。前高1，旄丘2。3後高，陵丘。偏高4，阿丘。5', '[釋文-1]：補李巡曰：「前高後卑下」\n[釋文-2]：謝音毛，字林作嵍，又作堥，俱亡付反\n[郭注-3]：詩云：「旄丘之葛兮。」\n[釋文-4]：音篇。補李巡曰：「邱邊高」\n[郭注-5]：詩云：「陟彼阿丘。」', '釋丘弟十', '爾雅', '', DEFAULT, DEFAULT, '872/5233;5248/5233;4309/5233;9620/5233;9626/5233', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5120, '宛丘', '', '宛中1，宛丘。23', '[釋文-1]：施於阮反，孫云：「謂中央污也。」郭於粉反，「謂藴聚隆高也。」下同\n[郭注-2]：宛，謂中央隆高。\n[釋文-3]：隆，力躬反。', '釋丘弟十', '爾雅', '', DEFAULT, DEFAULT, '4609/5233', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5121, '負丘', '', '丘背有丘爲負丘。12', '[郭注-1]：此解宛丘，中央隆峻，狀如負一丘於背上。\n[釋文-2]：解，古買反。背，如字。', '釋丘弟十', '爾雅', '', DEFAULT, DEFAULT, '3965/5233', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5122, '定丘;泰丘', '', '左澤，定丘12，右陵，泰丘。3', '[音釋-1]：丁佞。\n[釋文-2]：丁佞反\n[郭注-3]：宋有太丘社，亡，見史記。', '釋丘弟十', '爾雅', '', DEFAULT, DEFAULT, '4576/5233;7428/5233', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5123, '𠭇丘;陵丘', '', '如𠭇1，𠭇丘。23如陵，陵丘。4', '[釋文-1]：本或作畞\n[郭注-2]：丘有壟界，如田𠭇。\n[釋文-3]：隴，力勇反，本又作壟。\n[郭注-4]：陵，大阜也。', '釋丘弟十', '爾雅', '', DEFAULT, DEFAULT, '9181/5233;9620/5233', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5124, '宛丘', '', '丘上有丘爲宛丘。12', '[郭注-1]：嫌人不了，故重曉之。\n[釋文-2]：了，或作憭。重，直用反。', '釋丘弟十', '爾雅', '', DEFAULT, DEFAULT, '4609/5233', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5125, '宛丘;濳丘;州黎丘', '', '陳有宛丘，1晉有濳丘2，3淮南有州黎丘4。5', '[郭注-1]：今在陳郡 陳縣。\n[釋文-2]：昨鹽反\n[郭注-3]：今在太原 晉陽縣。\n[釋文-4]：力兮反\n[郭注-5]：今在壽春縣。', '釋丘弟十', '爾雅', '', DEFAULT, DEFAULT, '4609/5233;7290/5233;7492/4515/5233', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5126, '名丘', '', '{名丘}天下有名丘五，其三在河南，其二在河北。12', '[郭注-1]：說者多以州黎、宛、營爲河南，濳、敦爲河北者。案：此方稱天下之名丘，恐此諸丘碌碌，未足用當之，殆自別更有魁梧桀大者五，但未詳其名號、今者所在耳。\n[釋文-2]：敦，丁回反。碌，音禄。更，古孟反。魁，口回反。梧，五故、五胡二反。傑，渠列反，本今作桀。', '釋丘弟十', '爾雅', '', DEFAULT, DEFAULT, '836/5233', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5127, '岸', '', '望厓洒而高12，岸。3', '[音釋-1]：牙。先典。\n[釋文-2]：字又作涯，魚佳反。蘇典反，又西禮反，下同\n[郭注-3]：厓，水邊。洒謂深也。視厓峻而水深者曰岸。', '釋丘弟十', '爾雅', '', DEFAULT, DEFAULT, '5918', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5128, '不漘', '', '夷上洒下，不漘12。34', '[音釋-1]：脣。\n[釋文-2]：音脣\n[郭注-3]：厓上平坦而下水深者爲漘。不，發聲。\n[釋文-4]：坦，土但反。', '釋丘弟十', '爾雅', '', DEFAULT, DEFAULT, '7706/7244', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5129, '隩;隈;尻', '', '隩12，隈34。56厓內爲隩，外爲隈{尻}7。89', '[音釋-1]：奥。\n[釋文-2]：字林烏到反，郭於六反，注及下同，本或作澳\n[音釋-3]：烏回。\n[釋文-4]：本或作渨，同，烏回反，字林一凷反\n[郭注-5]：今江東呼爲浦隩。淮南子曰：「漁者不爭隈。」\n[釋文-6]：漁，音魚。\n[釋文-7]：鞫，如字，字林作㘲，云：「隈厓外也。」九六反，本今作隈。補李巡曰：「厓內近水爲隩，其外爲鞫」\n[郭注-8]：別厓表裏之名。\n[釋文-9]：別，彼列反。裏，音里。', '釋丘弟十', '爾雅', '', DEFAULT, DEFAULT, '9670;9671;5409', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5130, '畢;堂牆', '篳', '畢[篳]1，堂牆。2', '[釋文-1]：本又作𡻞，卑吉反\n[郭注-2]：今終南山道名。畢，其邊若堂之牆。', '釋丘弟十', '爾雅', '1', DEFAULT, DEFAULT, '2492;9056/3347', '3005', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5131, '岸;汻', '', '重厓12，岸。3岸上，滸{汻}45。6', '[音釋-1]：直龍。\n[釋文-2]：直龍反\n[郭注-3]：兩厓累者爲岸。\n[音釋-4]：虎。\n[釋文-5]：呼五反\n[郭注-6]：岸上地。', '釋丘弟十', '爾雅', '', DEFAULT, DEFAULT, '5918;7242', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5132, '墳;大防', '坋', '墳[坋]1，大防2。34', '[釋文-1]：符紛反。補李巡曰：「厓岸狀如墳墓」\n[釋文-2]：音房\n[郭注-3]：謂隄。\n[釋文-4]：隄，丁兮反。', '釋丘弟十', '爾雅', '1', DEFAULT, DEFAULT, '9143;6598/9658', '9127', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5133, '涘;厓', '', '涘爲厓12。3', '[音釋-1]：士。\n[釋文-2]：舊音仕\n[郭注-3]：謂水邊。', '釋丘弟十', '爾雅', '', DEFAULT, DEFAULT, '7241;5979', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5134, '汜;溦', '湄', '窮瀆1，汜23。4谷者，溦[湄]56。7', '[釋文-1]：音獨\n[音釋-2]：似。\n[釋文-3]：音似\n[郭注-4]：水無所通者。\n[音釋-5]：眉。\n[釋文-6]：本又作湄，亡悲反，又音微\n[郭注-7]：通於谷。', '釋丘弟十', '爾雅', '2', DEFAULT, DEFAULT, '7250;7318', '7267', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5135, '華;嶽;岱;恆;衡', '崋', '河南華[崋]1，2河西嶽，34河東岱，5河北恆，6江南衡。7', '[釋文-1]：戸花、戸化二反，字林作崋，同\n[郭注-2]：華陰山。\n[郭注-3]：吳嶽。\n[釋文-4]：吳，音吾，周禮職方氏：「正西曰雍州，其山鎮曰嶽。」後鄭云：「嶽，吳嶽也。」與郭同。\n[郭注-5]：岱宗，泰山。\n[郭注-6]：北嶽，恆山。\n[郭注-7]：衡山，南嶽。', '釋山弟十一', '爾雅', '1', DEFAULT, DEFAULT, '3886;5851;5852;9017;2844', '5863', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5136, '陟;英;坏', '', '山三襲1，陟；23再成，英；4一成，坯{坏}56。7', '[釋文-1]：音習\n[郭注-2]：襲亦重。\n[釋文-3]：重，直龍反，下皆同。\n[郭注-4]：兩山相重。\n[音釋-5]：備悲。\n[釋文-6]：或作伾，備悲反，又備美反，沈五窟反，韋昭音噽，說文作坏\n[郭注-7]：書曰：「至于太伾。」', '釋山弟十一', '爾雅', '', DEFAULT, DEFAULT, '9643;502;9134', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5137, '崧', '', '山大而高，崧12。3', '[音釋-1]：嵩。\n[釋文-2]：思忠反，又作嵩，崧卽嵩也，俱是高大之貌\n[郭注-3]：今中嶽 嵩高山，蓋依此名。', '釋山弟十一', '爾雅', '', DEFAULT, DEFAULT, '5911', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5138, '岑;嶠;扈;歸', '嘏', '山小而高，岑1；23銳而高，嶠45；67卑而大8，扈[嘏]9；10小而衆，巋{歸}1112。1314', '[釋文-1]：士金反，字林才心反\n[郭注-2]：言岑崟。\n[釋文-3]：崟，魚金反，字又作崯。\n[音釋-4]：喬。\n[釋文-5]：渠驕反，郭又音驕，字林作㠐，云：「山銳而長也，巨照反」\n[郭注-6]：言鑯峻。\n[釋文-7]：鑯，子廉反。\n[釋文-8]：如字，又音婢\n[釋文-9]：𡻮，音戸，或作扈\n[郭注-10]：扈，廣貌。\n[音釋-11]：丘鬼。\n[釋文-12]：丘軌反，巋然，高峻貌，字林：「丘追反，小山而衆也」\n[郭注-13]：小山叢羅。\n[釋文-14]：叢，才公反。', '釋山弟十一', '爾雅', '3', DEFAULT, DEFAULT, '5872;5906;4025;1073', '1462', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5139, '岌;垣/亙', '馺', '小山岌[馺]大山12，峘{垣/亙}34。56', '[音釋-1]：魚泣。\n[釋文-2]：魚泣反，岌，高也\n[音釋-3]：桓。\n[釋文-4]：胡官反，一音袁。埤蒼云：「峘，大山。」又音恒\n[郭注-5]：岌謂高過。\n[釋文-6]：過，古臥反。', '釋山弟十一', '爾雅', '1', DEFAULT, DEFAULT, '5905;9047/0/3819', '6202', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5140, '嶧;蜀', '', '屬者12，嶧34。56獨者，蜀。7', '[音釋-1]：燭。\n[釋文-2]：章玉、時欲二反，謂相連屬\n[音釋-3]：亦。\n[釋文-4]：羊石反，說文：「葛嶧山，在東海下邳。」尚書云：「嶧陽孤桐」\n[郭注-5]：言駱驛相連屬。\n[釋文-6]：駱，音洛。驛，音亦。\n[郭注-7]：蜀亦孤獨。', '釋山弟十一', '爾雅', '', DEFAULT, DEFAULT, '5855;8828', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5141, '章;隆', '敞', '上正，章[敞]。1宛中2，隆。3', '[郭注-1]：山上平。\n[釋文-2]：於粉反\n[郭注-3]：山中央高。', '釋山弟十一', '爾雅', '1', DEFAULT, DEFAULT, '1742;3879', '2017', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5142, '岡;翠微', '', '山脊1，岡2。3未及上，翠微。4。56', '[釋文-1]：音積\n[釋文-2]：又作𡷇，皆古郎反\n[郭注-3]：謂山長脊。\n[釋文-4]：七遂反。亡非反\n[郭注-5]：近上旁陂。\n[釋文-6]：近，附近之近。陂，普河反，又彼義反。', '釋山弟十一', '爾雅', '', DEFAULT, DEFAULT, '5871;2247/1236', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5143, '冢;崒;厜㕒', '', '山頂，冢。1崒者23，厜㕒45。67', '[郭注-1]：山巔。\n[音釋-2]：子恤。\n[釋文-3]：子恤、才戌二反，字林才沒、子出二反\n[音釋-4]：姊規。危。\n[釋文-5]：姊規反，郭才規反，字林同，顧視規反，本或作𠪉，又作嵳，皆才何反。郭語規反，字林音危，顧魚奇反，本或作𠩙，又作峩，皆五何反\n[郭注-6]：謂山峯頭、巉巖。\n[釋文-7]：巉，士杉反，又士咸反。嵒，五咸反，本又作巖，字林云：「巉巖，山貌也。」', '釋山弟十一', '爾雅', '', DEFAULT, DEFAULT, '5815;5874;5980/5981', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5144, '密;盛', '', '山如堂者，密1；23如防者，盛45。6', '[釋文-1]：又作𡶇\n[郭注-2]：形似堂室者。尸子曰：「松柏之鼠，不知堂密之有美樅。」\n[釋文-3]：樅，七容反，木名。\n[音釋-4]：成。\n[釋文-5]：時征反，又市政反，謂山形如黍稷之在器\n[郭注-6]：防，隄。', '釋山弟十一', '爾雅', '', DEFAULT, DEFAULT, '5876;3141', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5145, '巒;嶞', '', '巒，山嶞12。34', '[音釋-1]：湯果。\n[釋文-2]：力官反，埤蒼云：「山小而銳。」湯果反，狹而長也，字林云：「山之施嶞者，大果反」\n[郭注-3]：謂山形長狹者。荆州謂之巒。詩曰：「嶞山喬嶽。」\n[釋文-4]：狹，乎夾反。喬，巨苗反。', '釋山弟十一', '爾雅', '', DEFAULT, DEFAULT, '5875;5879', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5146, '重甗;隒', '', '重甗12，隒34。56', '[音釋-1]：直龍。言。\n[釋文-2]：直龍反。魚蹇反，郭音言，又音彥，字林牛建反\n[音釋-3]：儼。\n[釋文-4]：本或作嵰字，同，郭魚檢反。字林云：「山形似重甑，居儉反。」顧力儉、力儼二反。補李巡曰：「隒，阪也。」孫炎曰：「山基有重岸也」\n[郭注-5]：謂山形如累兩甗。甗，甑，山狀似之，因以名云。\n[釋文-6]：甑，子孕反。', '釋山弟十一', '爾雅', '', DEFAULT, DEFAULT, '5244/8457;9665', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5147, '㧁', '陜', '左右有岸，厒{㧁}[陜]12。3', '[音釋-1]：口閤。\n[釋文-2]：口閤反\n[郭注-3]：夾山有岸。', '釋山弟十一', '爾雅', '1', DEFAULT, DEFAULT, '7730', '9642', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5148, '霍;鮮', '護', '大山宮小山，霍[護]1；2小山別大山34，鮮56。7', '[釋文-1]：許郭反\n[郭注-2]：宮謂圍繞之，禮記曰：「君爲廬宮之」是也。\n[音釋-3]：彼列。\n[釋文-4]：彼列反，下注同\n[音釋-5]：息淺。\n[釋文-6]：息淺反，李云：「大山少，故曰鮮。」或作𡾮字，又音仙\n[郭注-7]：不相連。', '釋山弟十一', '爾雅', '1', DEFAULT, DEFAULT, '2364;7648', '1562', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5149, '陘', '', '山絕，陘12。3', '[音釋-1]：形。\n[釋文-2]：郭胡經、古定二反\n[郭注-3]：連山中斷絕。', '釋山弟十一', '爾雅', '', DEFAULT, DEFAULT, '9661', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5150, '嶅;礐', '嶨', '多小石，磝{嶅}12；34多大石，礐[嶨]56。78', '[音釋-1]：苦交。\n[釋文-2]：字或作磽，同，字林口交反，郭五交、五角二反\n[郭注-3]：多礓礫。\n[釋文-4]：礓，居羊反，字林云：「礫也。」礫，力的反，說文云：「小石也。」\n[音釋-5]：殻。\n[釋文-6]：字或作确，又作㙾，又作埆，郭苦角反，又戸角反\n[郭注-7]：多盤石。\n[釋文-8]：磐，步丸反，今作盤，同。', '釋山弟十一', '爾雅', '2', DEFAULT, DEFAULT, '5869;6029', '5868', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5151, '岵;亥;屺', '', '多草木，岵12。無草木，峐{亥}{屺}34。5', '[音釋-1]：戸。\n[釋文-2]：音戸\n[音釋-3]：起。\n[釋文-4]：三蒼、字林、聲類並云猶屺字，音起，阮孝緒字畧音古開反\n[郭注-5]：皆見詩。', '釋山弟十一', '爾雅', '', DEFAULT, DEFAULT, '5866;9883;5867', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5152, '埒', '', '山上有水，埒1。23', '[釋文-1]：音劣，字或作浖\n[郭注-2]：有停泉。\n[釋文-3]：渟，音亭，亦作停，同。', '釋山弟十一', '爾雅', '', DEFAULT, DEFAULT, '9053', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5153, '澩', '', '夏有水、冬無水，澩12。34', '[音釋-1]：學。\n[釋文-2]：字林火篤反，郭同，又徂學反\n[郭注-3]：有停潦。\n[釋文-4]：潦，音老。', '釋山弟十一', '爾雅', '', DEFAULT, DEFAULT, '7271', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5154, '谿', '', '山豄無所通12，谿3。4', '[音釋-1]：讀。\n[釋文-2]：說文云：「古文𨽍字。」徒木反\n[釋文-3]：苦奚反\n[郭注-4]：所謂窮瀆者。雖無所通，與水注川同名。', '釋山弟十一', '爾雅', '', DEFAULT, DEFAULT, '7503', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5155, '崔嵬;岨', '', '石戴土謂之崔嵬12。3土戴石爲砠{岨}45。6', '[音釋-1]：徂回。五回。\n[釋文-2]：本或作載字，同，丁代反，下同。徂回反。五回反\n[郭注-3]：石山上有土者。\n[音釋-4]：七余。\n[釋文-5]：說文亦作岨，七余反\n[郭注-6]：土山上有石者。', '釋山弟十一', '爾雅', '', DEFAULT, DEFAULT, '5902/5848;5870', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5156, '澗;虞', '汻', '山夾水1，澗；陵夾水，澞{虞}[汻]23。4', '[釋文-1]：古洽反\n[音釋-2]：虞。\n[釋文-3]：魚俱反，本又作虞\n[郭注-4]：別山陵閒水者之名。', '釋山弟十一', '爾雅', '2', DEFAULT, DEFAULT, '7269;3110', '7242', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5157, '岫', '', '山有穴爲岫1。2', '[釋文-1]：徐究反，郭音胄，又音由，字林弋又反\n[郭注-2]：謂巖穴。', '釋山弟十一', '爾雅', '', DEFAULT, DEFAULT, '5877', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5158, '夕陽;朝陽', '', '山西曰夕陽，1山東曰朝陽。2', '[郭注-1]：暮乃見日。\n[郭注-2]：旦卽見日。', '釋山弟十一', '爾雅', '', DEFAULT, DEFAULT, '4327/9624;4289/9624', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5159, '泰山;華山;霍山;恆山;嵩高', '崋山', '泰山爲東嶽1，華山[崋山]爲西嶽2，霍山爲南嶽3，4恆山爲北嶽5，6嵩高爲中嶽7。8', '[釋文-1]：一名岱宗，在兖州界，漢在泰山 博縣，又云在奉高縣\n[釋文-2]：在豫州界，漢在弘農 華陰縣\n[釋文-3]：一名衡山，在荆州界，漢在長沙 湘南縣，又云在廬江 潛縣\n[郭注-4]：卽天柱山，濳水所出。\n[釋文-5]：在并州界，漢在常山 上曲陽縣，以犯漢文帝諱，改爲常山\n[郭注-6]：常山。\n[釋文-7]：在豫州界，漢在河南\n[郭注-8]：大室山也。', '釋山弟十一', '爾雅', '2', DEFAULT, DEFAULT, '7428/5850;3886/5850;2364/5850;9017/5850;5911/3320', '5863/5850', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5160, '梁山', '', '梁山，晉望也。12', '[郭注-1]：晉國所望祭者。今在馮翊 夏陽縣西北臨沂上。\n[釋文-2]：翊，音翼。夏，戸雅反。河，或作沂，魚依反。', '釋山弟十一', '爾雅', '', DEFAULT, DEFAULT, '3787/5850', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5161, '瀸', '', '泉一見一否爲瀐{瀸}12。34', '[音釋-1]：現。卑美。纎。\n[釋文-2]：賢遍反。方有、卑美二反，廣雅云：「否，不也。」息廉反，又子廉反\n[郭注-3]：瀐，纔有貌。\n[釋文-4]：纔，音才。', '釋水弟十二', '爾雅', '', DEFAULT, DEFAULT, '7224', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5162, '瀱汋', '', '丼一有水一無水，爲瀱汋12。3', '[音釋-1]：計。仕捉。\n[釋文-2]：居例反，孫許廢反。仕捉反，又上若反\n[郭注-3]：山海經曰：「天丼夏有水，冬無水。」卽此類也。', '釋水弟十二', '爾雅', '', DEFAULT, DEFAULT, '7196/7195', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5163, '濫泉;沃泉;氿泉', '厬泉', '濫泉正出1，正出，涌出也2。3沃泉縣出45，縣出，下出也。67氿泉[厬泉]穴出89，穴出，仄出也1011。12', '[釋文-1]：胡覽反\n[釋文-2]：音勇。補李巡曰：「水泉從下上出曰涌泉」\n[郭注-3]：公羊傳曰：「直出。」直猶正也。\n[音釋-4]：玄。\n[釋文-5]：烏鹿反。音玄\n[郭注-6]：從上溜下。\n[釋文-7]：𩅸，本又作溜，力又反。\n[音釋-8]：軌。\n[釋文-9]：音軌，詩云：「有洌氿泉」\n[音釋-10]：側。\n[釋文-11]：菹棘反，本亦作側、㳁，同\n[郭注-12]：從旁出也。', '釋水弟十二', '爾雅', '3', DEFAULT, DEFAULT, '7181/7493;7278/7493;7243/7493', '5983/7493', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5164, '湀闢;過辨', '湀辟;蝸辨', '湀闢[湀辟]12，流川；3過辨[蝸辨]45，回川6。7', '[音釋-1]：揆。\n[釋文-2]：郭巨癸反，孫苦穴反，字林音圭。婢亦反，一音匹亦反\n[郭注-3]：通流。\n[音釋-4]：古禾。片。\n[釋文-5]：本或作渦，同，古禾反。普見反\n[釋文-6]：又作洄，戸恢反\n[郭注-7]：旋流。', '釋水弟十二', '爾雅', '1;2', DEFAULT, DEFAULT, '7251/7753;1110/2768', '7251/5798;8906/2768', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5165, '灉', '', '灉12，反入。34', '[音釋-1]：於用。\n[釋文-2]：於用反，又於恭反\n[郭注-3]：卽河水決岀復還入者。河之有灉，猶江之有沱。\n[釋文-4]：復，扶又反，下及注並同。沱，徒河反，或作汜，音似。', '釋水弟十二', '爾雅', '', DEFAULT, DEFAULT, '7078', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5166, '單', '灘', '潬{單}[灘]12，沙出。34', '[音釋-1]：但。\n[釋文-2]：徒坦反\n[郭注-3]：今江東呼水中沙堆爲潬，音但。\n[釋文-4]：堆，字或作㕍，又作塠字，同，都回反。', '釋水弟十二', '爾雅', '1', DEFAULT, DEFAULT, '975', '7272', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5167, '汧', '', '汧12，出不流。3', '[音釋-1]：牽。\n[釋文-2]：口千、口見二反\n[郭注-3]：水泉濳出，便自停成污池。', '釋水弟十二', '爾雅', '', DEFAULT, DEFAULT, '7013', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5168, '肥;瀵', '', '歸異、出同流1，肥。2瀵34，大出尾下5。67', '[釋文-1]：補舍人曰：「水異出流行合同」\n[郭注-2]：毛詩傳曰：「所出同、所歸異爲肥。」\n[音釋-3]：糞。\n[釋文-4]：敷問反，義或方問反，水本同而出異\n[釋文-5]：字或作浘、（⿸尸先），同，亡鬼反\n[郭注-6]：今河東 汾陰縣有水，口如車輪許，濆沸涌出，其深無限，名之爲瀵。馮翊 郃陽縣復有瀵，亦如之。相去數里而夾河，河中陼上又有一瀵，瀵源皆潛相通。在汾陰者，人壅其流以爲陂，種稻，呼其本所出處爲「瀵魁」，此是也。尾猶底也。\n[釋文-7]：汾，方云反。車，昌蛇反。濆，扶粉反。翊，音弋。郃，戸荅反。數，色主反。夾，古洽反。壅，於勇反。陂，彼爲反。處，昌慮反，下同。魁，口回反。', '釋水弟十二', '爾雅', '', DEFAULT, DEFAULT, '2650;7356', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5169, '醮;厬', '潐;氿', '水醮[潐]曰厬[氿]12。3', '[音釋-1]：軌。\n[釋文-2]：子召反，盡也，字或作潐，同。字又作𣷾，音軌\n[郭注-3]：謂水醮盡。', '釋水弟十二', '爾雅', '1;2', DEFAULT, DEFAULT, '9837;5983', '7344;7243', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5170, '灉;楚;闡;波;濳;汻;沱;洵;沙;濆;涓', '衍', '水自河出爲灉1。23濟[泲]爲濋{楚}45，汶爲灛{闡}[衍]67，洛爲波，漢爲濳，89淮爲滸{汻}10，江爲沱11，1213濄爲洵1415，潁爲沙1617，汝爲濆{涓}1819。20', '[釋文-1]：於用反，或於凶反，字又作澭，注及下同\n[郭注-2]：書曰：「灉沮會同。」\n[釋文-3]：沮，七余反。\n[音釋-4]：楚。\n[釋文-5]：本又作㴉，子禮反。初呂反\n[音釋-6]：問。單。\n[釋文-7]：音問。字或作𨼒，同，昌善反，李云：「溢也」\n[郭注-8]：書曰：「沱濳旣道。」\n[釋文-9]：沱，徒河反，下文及注並同，字亦作沲。\n[釋文-10]：呼五反\n[音釋-11]：陀\n[郭注-12]：書曰：「岷山導江，東別爲沱。」\n[釋文-13]：岷，亡巾反。道，徒報反，本或作導。\n[音釋-14]：烏禾。\n[釋文-15]：又作渦，謝古禾反，又烏禾反，本或作過。私旬反\n[音釋-16]：餘頃。\n[釋文-17]：餘頃反，水名，在汝南，呂伯雍云：「水出陽成 陽乾山」\n[音釋-18]：墳。\n[釋文-19]：符云反，下同，字林作㳙，工玄反，衆爾雅本亦作㳙\n[郭注-20]：詩曰：「遵彼汝濆。」皆大水溢出，別爲小水之名。', '釋水弟十二', '爾雅', '3', DEFAULT, DEFAULT, '7078;3851;7755;7175;7290;7242;6992;7113;7238;7240;7142', '7138', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5171, '汧;汜', '', '水決之澤爲汧1，2決復入爲汜3。4', '[釋文-1]：苦見、苦堅二反\n[郭注-2]：水決入澤中者，亦名爲汧。\n[釋文-3]：音似\n[郭注-4]：水出去復還。', '釋水弟十二', '爾雅', '', DEFAULT, DEFAULT, '7013;7250', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5172, '瀾;淪;涇', '巠', '河水淸且瀾漪12，大波爲瀾，34小波爲淪5，67直波爲涇[巠]8。910', '[音釋-1]：爛。衣。\n[釋文-2]：郭力旦反，又力安反，下及注同，李依詩作漣，音連。本又作猗，於宜反\n[郭注-3]：言渙瀾。\n[釋文-4]：渙，呼貫反。\n[釋文-5]：音輪\n[郭注-6]：言藴淪。\n[釋文-7]：藴，紆云、紆粉二反。\n[釋文-8]：俓，古定反，字或作徑，注同\n[郭注-9]：有涇涏。\n[釋文-10]：侹，字又作挺，他定反，又徒頂反。俓侹，直也。', '釋水弟十二', '爾雅', '3', DEFAULT, DEFAULT, '7177;7178;7006', '7484', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5173, '沱;灉;濆', '', '江有沱1，河有灉，汝有濆。23', '[釋文-1]：補李巡曰：「江、河、汝旁有肥美之地名」\n[郭注-2]：此故上水別出耳。所作者重見。\n[釋文-3]：作，子各反。重，直用反。見，賢徧反。', '釋水弟十二', '爾雅', '', DEFAULT, DEFAULT, '6992;7078;7240', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5174, '汻', '', '滸{汻}，水厓1。2', '[釋文-1]：五街反\n[郭注-2]：水邊地。', '釋水弟十二', '爾雅', '', DEFAULT, DEFAULT, '7242', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5175, '湄', '', '水草交爲湄12。3', '[音釋-1]：眉。\n[釋文-2]：本或作（⿱洌眉）、𣽪、㵟、𤃰四字，同，亡悲反。補舍人曰：「水中有草木交合也」\n[郭注-3]：詩曰：「居河之湄。」', '釋水弟十二', '爾雅', '', DEFAULT, DEFAULT, '7267', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5176, '揭;厲;涉', '濿', '「濟有深涉12，3深則厲4，淺則揭56。」揭者，揭衣也7。89以衣涉水爲厲[濿]，1011繇膝以下爲揭1213，繇膝以上爲涉1415，繇帶以上爲厲。16', '[音釋-1]：子細。\n[釋文-2]：子細反\n[郭注-3]：渭濟渡之處。\n[釋文-4]：如字，本或作濿，說文云：「濿或作砅，履石渡水也。」俱力曳反\n[音釋-5]：上二字音憩，下丘竭。\n[釋文-6]：起例反，褰衣渡水也，下「揭者」、「爲揭」同\n[釋文-7]：起例反，或丘竭反，說文云：「高舉也」\n[郭注-8]：謂褰裳也。\n[釋文-9]：褰，去焉反。\n[郭注-10]：衣謂褌。\n[釋文-11]：褌，音昆。\n[音釋-12]：由。\n[釋文-13]：古由字，下同。字又作厀，辛七反\n[音釋-14]：時掌。\n[釋文-15]：時掌反，下同\n[郭注-16]：繇，自也。', '釋水弟十二', '爾雅', '2', DEFAULT, DEFAULT, '7961;5985;7477', '7294', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5177, '泳', '', '濳行爲泳1。23', '[釋文-1]：于柄反\n[郭注-2]：水底行也。晏子春秋曰：「濳行，逆流百步，順流七里。」\n[釋文-3]：底，丁禮反。', '釋水弟十二', '爾雅', '', DEFAULT, DEFAULT, '7289', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5178, '紼;𦆾;縭;緌', '佗', '「汎汎楊舟1，紼縭維之23。」紼，𦆽{𦆾}也45。67縭[佗]，緌也89。10', '[釋文-1]：孚劒反\n[音釋-2]：弗。离。\n[釋文-3]：本或作綍，又作紱，同，甫勿反。力知反，下同\n[音釋-4]：律。\n[釋文-5]：本或作繂字，同，音律。補李巡曰：「𦆽，竹爲索，所以維持舟者」\n[郭注-6]：𦆽，索。\n[釋文-7]：索，悉各反。\n[音釋-8]：如誰。\n[釋文-9]：如誰反\n[郭注-10]：緌，繫。', '釋水弟十二', '爾雅', '3', DEFAULT, DEFAULT, '8759;8779;8687;8644', '5024', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5179, '造舟;維舟;方舟;特舟;乗泭', '', '天子造舟12，3諸侯維舟，4大夫方舟5，67士特舟8，9庶人乗泭1011。12', '[音釋-1]：七到。\n[釋文-2]：草報反，廣雅作艁，音同。又造舟爲浮梁，謂比舟船造作橋梁，或音皁，案說文：「艁，古文造也。」郭圖云：「天子並七船，諸侯四，大夫二，士一」\n[郭注-3]：比船爲橋。\n[郭注-4]：維連四船。\n[釋文-5]：音舫，或作舫，又音方\n[郭注-6]：併兩船。\n[釋文-7]：併，步丁反。\n[釋文-8]：大得反，本或作犆，同\n[郭注-9]：單船。\n[音釋-10]：桴。\n[釋文-11]：音桴，併木以渡\n[郭注-12]：併木以渡。', '釋水弟十二', '爾雅', '', DEFAULT, DEFAULT, '1114/5438;8703/5438;5455/5438;731/5438;3412/7284', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5180, '谿;谷;溝;澮;瀆', '巜', '水注川曰谿1，注谿曰谷2，注谷曰溝，注溝曰澮[巜]34，注澮曰瀆。56', '[釋文-1]：之樹反，下同。苦兮反。補李巡曰：「水出於山，入於川」\n[釋文-2]：補李巡曰：「水相屬」\n[音釋-3]：古外。\n[釋文-4]：古外反\n[郭注-5]：此皆道水轉相灌注所入之處名。\n[釋文-6]：灌，古亂反。處，昌預反。', '釋水弟十二', '爾雅', '4', DEFAULT, DEFAULT, '7503;7502;7263;7022;7264', '7481', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5181, '泝洄;泝游', '', '逆流而上曰泝洄12，順流而下曰泝游3。4', '[音釋-1]：素，\n[釋文-2]：蘇故反，詩作遡。胡恢反。補孫炎曰：「逆渡者，逆流也；順渡者，順流也」\n[釋文-3]：音由\n[郭注-4]：皆見詩。', '釋水弟十二', '爾雅', '', DEFAULT, DEFAULT, '7287/7288;7287/4306', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5182, '亂', '', '正絕流曰亂。1', '[郭注-1]：直橫渡也。書曰：「亂于河。」', '釋水弟十二', '爾雅', '', DEFAULT, DEFAULT, '9744', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5183, '江;河;淮;濟;瀆', '', '江1、河2、淮3、濟4，爲四瀆5。四瀆者，發原注海者也。', '[釋文-1]：出蜀郡 岷山\n[釋文-2]：出崑崙山，或云出積石\n[釋文-3]：出南陽 平氏縣 胎簪山，亦云出桐柏山\n[釋文-4]：子禮反，出河東 垣縣 王屋山，或云出河內 溫西北平地\n[釋文-5]：徒木反，廣雅云：「江，貢也；河，何也；淮，均也；濟，濟也」', '釋水弟十二', '爾雅', '', DEFAULT, DEFAULT, '6991;6986;7053;7095;7264', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5184, '洲;陼;沚;坻;潏', '渚', '水中可居者曰洲1。小洲曰陼[渚]23，小陼曰沚4，小沚曰坻56，人所爲爲潏78。9', '[釋文-1]：音州。補李巡曰：「四方皆有水，中央獨可居，但大小異其名耳。」孫炎曰：「水有平地可居者也」\n[音釋-2]：渚。\n[釋文-3]：字又作渚，章汝反，本或直云「小洲曰渚」\n[釋文-4]：音止，本或作洔，音同，又音市\n[音釋-5]：池。\n[釋文-6]：本或作墀，同，直基反，本又作泜，音同\n[音釋-7]：述。\n[釋文-8]：郭述、決二音，呂伯雍音同，案郭圖「水中自然可居者爲洲，人亦於水中作洲，而小不可止住者名潏，水中地也。」補舍人曰：「人力以水爲居止」\n[郭注-9]：人力所作。', '釋水弟十二', '爾雅', '2', DEFAULT, DEFAULT, '7492;9687;7246;9088;7173', '7093', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5185, '河', '', '河出崐崘虛12，色白3；45所渠并千七百，一川色黃6。78百里一小曲，千里一曲一直9。10', '[音釋-1]：墟。\n[釋文-2]：古門反。力門反。去魚反，本亦作墟\n[釋文-3]：李云：「河水始出，其色白也。」孫云：「崑崙，山名也。墟者，山下之地。白者，西方之色也。」郭云：「山海經曰：『河出崐崘西北隅。』墟者，山下基也，發源處高激峻湊，故水色白也。」郭音義云：「禹本紀及山海經皆云河出崐崘山。漢書曰：『張騫使西域，窮河源，其山多玉石，而不見崐崘也，世人皆以此疑河不出崐崘。」案山海經曰：「東望泑澤，河水之所潛也，其源渾渾泡泡。」又云：「敦夢之水注于泑澤，出乎崐崘之西北隅，實惟河源也。」西域傳又云：「河有兩源，一出蔥嶺山，一出于闐，于闐在南山下，其河北流，與蔥嶺之河合，東注鹽澤，鹽澤一名蒲昌海，去玉門、陽關三百餘里，輪廣三四百里，其水停，冬夏不增減，皆以爲潛行地下，而南出於積石山，而爲中國河云。」然則河出崐崘便潛行地下，至蔥嶺及于闐復分流歧出也。張騫所見，蓋謂此矣。其去崐崘里數遠近，所未得而詳也。泑澤卽鹽澤也。泑，音於糾反，闐，徒徧反。圖讃云：「崐崘三層，號曰天柱，實惟河源，水之靈府」是也\n[郭注-4]：山海經曰：「河出崐崘西北隅。」虛，山下基也。\n[釋文-5]：隅，又作嵎、堣，同，音魚吁反。\n[釋文-6]：李云：「水流而分，交錯相穿，故曰川也。」孫云：「所受渠多，轉流溷濁，故色黃。」郭云：「潛流地中，汩潄沙壤，所受渠多，衆水溷淆，宜其濁黃」\n[郭注-7]：濳流地中，汩漱沙壤。所受渠多，衆水溷淆，宜其濁黃。\n[釋文-8]：汩，于筆反，流水也。方言云：「汩、遥，疾行也。」字林云：「水聲急也。」潄，色救反。溷，戸本反，又戸困反，謂雜亂，字或作渾，同。淆，戸交反，本亦作濁。\n[釋文-9]：李云：「水勢小曲乃大直也，故曰小曲，水陰節，每一曲一直，通無極也，故曰千里一曲一直。」郭云：「公羊傳云：『河曲流，河千里一曲一直也』」\n[郭注-10]：公羊傳曰：「河曲流，河千里一曲一直。」', '釋水弟十二', '爾雅', '', DEFAULT, DEFAULT, '6986', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5186, '徒駭;太史;馬頰;覆鬴;胡蘇;𥳑;絜;鉤盤;鬲津', '搹津', '徒駭12。3太史4。5馬頰67。89覆鬴1011。12胡蘇13。14𥳑15。1617絜18。19鉤盤20。21鬲津[搹津]2223。2425', '[音釋-1]：諧楷。\n[釋文-2]：李云：「禹䟽九河，以徒衆起，故曰徒駭。」孫云：「禹䟽九河，此河功難，衆懼不成，故曰徒駭」\n[郭注-3]：今在成平縣，義所未聞。\n[釋文-4]：大，謝音泰，孫如字，本今作太。李、孫云：「禹大使徒衆於此通水，故曰大史。」或云：「太史者，史官記事之處」\n[郭注-5]：今所在未詳。\n[音釋-6]：劫。\n[釋文-7]：李、孫、郭並云：「河勢上廣下狹，狀如馬頰也」\n[郭注-8]：河勢上廣下狹，狀如馬頰。\n[釋文-9]：狹，胡夾反。\n[音釋-10]：扶服。父。\n[釋文-11]：孚腹反，郭云：「古釡字。」李、孫、郭並云：「水中多渚，往往而有可居之處，狀如覆釡之形」\n[郭注-12]：水中可居，往往而有，狀如覆釡。\n[釋文-13]：李云：「其水下流，故曰胡蘇。胡，下；蘇，流也。」孫云：「水流多散，胡蘇然也」\n[郭注-14]：東莞縣今有胡蘇亭，其義未詳。\n[釋文-15]：古限反，李、孫並云：「河水深而𥳑大也。」郭云：「水道𥳑易」\n[郭注-16]：水道𥳑易，\n[釋文-17]：易，以豉反。\n[釋文-18]：戸結反，孫、郭並云：「水多約絜。」又苦八反，李云：「河水多山石之苦，故曰絜，絜，苦也。」或音呼節反，又音結\n[郭注-19]：水多約絜。\n[釋文-20]：古侯反。般，步干反，本又作盤，李本作股，云：「水曲如鉤，折如人股，故曰鉤股。」孫、郭同，云：「水曲如鉤，流盤桓不直前也」\n[郭注-21]：水曲如鉤流盤桓也。\n[音釋-22]：革。\n[釋文-23]：音革，施力的反，與今注不同。李云：「河水狹小，可隔以爲津，故曰鬲津。」孫、郭同，云：「水多阨狹，可隔以爲津而橫渡」\n[郭注-24]：水多阨狹，可隔以爲津而橫渡。\n[釋文-25]：阨，於懈反。隔，音革。', '釋水弟十二', '爾雅', '9', DEFAULT, DEFAULT, '1099/6219;6598/1933;6137/5615;4854/1861;2678/256;2893;8754;1460/3706;1854/7281', '7878/7281', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5187, '蒮;韱;山韭;茖;山蔥;莖;山䪥;蒚;山蒜', '', '蒮{韱}12，山韭34。茖56，山蔥78。葝{莖}910，山䪥1112。蒚1314，山蒜15。1617', '[音釋-1]：育。\n[釋文-2]：羊六反\n[音釋-3]：九。\n[釋文-4]：居有反，說文云：「菜名也，一種而長久，故曰韭。象形。在一之上。一，地也」\n[音釋-5]：革。\n[釋文-6]：古百反\n[音釋-7]：忩。\n[釋文-8]：本又作䓗，音聰\n[音釋-9]：巨盈。\n[釋文-10]：字又作𦳲，巨盈反\n[音釋-11]：薤。\n[釋文-12]：本又作薤，同，戸界反，說文云：「菜也，菜似韭，故從韭也」\n[音釋-13]：力。\n[釋文-14]：力的反\n[釋文-15]：西亂反，說文云：「葷菜也。」一本云：「菜之美者，雲夢之葷菜。」補孫炎曰：「帝登蒚山，遭蕕芋毒，將死，得蒜乃嚙之，解毒。乃收植之，能殺蟲魚之毒，攝諸腥羶」\n[郭注-16]：今山中多有此菜，皆如人家所種者。茖蔥，細莖大葉。\n[釋文-17]：種，之用反。莖，戸耕反，或作英。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '634;4545;5850/4541;301;5850/633;493;5850/4544;339;5850/631', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5188, '薜;山蘄', '', '薜12，山蘄34。56', '[音釋-1]：百。\n[釋文-2]：方奭反，郭布革反\n[音釋-3]：芹。\n[釋文-4]：古芹字，巨斤反\n[郭注-5]：廣雅云：「山蘄，當歸。」當歸，今似蘄而麤大。\n[釋文-6]：麆，七奴反，後皆同，又在古反，本今作麤。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '386;5850/328', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5189, '段;木堇;櫬', '蕣;蕣', '椴{段}[蕣]12，木槿{木堇}34；櫬[蕣]56，木槿。78', '[音釋-1]：段。\n[釋文-2]：徒亂反\n[音釋-3]：謹。\n[釋文-4]：堇，本或作槿，音謹，下同\n[音釋-5]：襯。\n[釋文-6]：楚靳反，本又作藽\n[郭注-7]：別二名也。似李樹，華朝生夕隕，可食。或呼爲日及，亦曰王蒸。\n[釋文-8]：別，彼列反，下放此。隕，于敏反。日，人逸反。烝，之仍反，本今作蒸。', '釋草弟十三', '爾雅', '1;3', DEFAULT, DEFAULT, '1972;3413/9167;3830', '483;483', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5190, '术;山薊;楊枹薊', '𦬸', '术[𦬸]1，山薊23。4楊枹薊56。7', '[釋文-1]：徒律反，本或作𦬸\n[音釋-2]：計。\n[釋文-3]：古帝反，下同，案本草：「术，一名山薊，一名山連，一名山薑。」廣雅云：「术，山薑也」\n[郭注-4]：本草云：「术，一名山薊。」今术似薊而生山中。\n[音釋-5]：孚。\n[釋文-6]：沈音孚，又音浮，又音包\n[郭注-7]：似薊而肥大，今呼之馬薊。', '釋草弟十三', '爾雅', '1', DEFAULT, DEFAULT, '4435;5850/308;3512/3766/308', '454', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5191, '葥;王篲', '𦺍', '葥[𦺍]12，王蔧{王篲}34。56', '[音釋-1]：箭。\n[釋文-2]：音箭，一音子淺反，說文作𦺍，同\n[音釋-3]：邃。\n[釋文-4]：息遂、囚銳二反\n[郭注-5]：王帚也，似藜，其樹可以爲埽蔧，江東呼之曰落帚。\n[釋文-6]：帚，之有反。埽，素報反，下同。', '釋草弟十三', '爾雅', '1', DEFAULT, DEFAULT, '312;78/1927', '446', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5192, '菉;王芻', '', '菉12，王芻3。45', '[音釋-1]：緑。\n[釋文-2]：力辱反\n[釋文-3]：楚俱反，本又作蒭\n[郭注-4]：菉蓐也。今呼鴟腳莎。\n[釋文-5]：蓐，音辱。鴟，尺之反。莎，素和反。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '651;78/611', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5193, '拜;商藋;啇藋', '', '拜，蔏藋{商藋}{啇藋}12。3', '[音釋-1]：啇。徒的。\n[釋文-2]：音商。徒弔反，說文、廣雅皆云：「堇也」\n[郭注-3]：蔏藋亦似藜。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '7844;1453/310;875/310', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5194, '𦾴;皤蒿;蒿;菣;蔚;牡菣', '', '蘩{𦾴}12，皤蒿34。5蒿，菣67。89蔚1011，牡菣12。13', '[音釋-1]：煩。\n[釋文-2]：音煩\n[音釋-3]：婆。\n[釋文-4]：白波反，白也。好高反\n[郭注-5]：白蒿。\n[音釋-6]：去刃。\n[釋文-7]：本亦作𦸃，去刃反，讀者或作苦見反\n[郭注-8]：今人呼靑蒿。香中炙啖者爲菣。\n[釋文-9]：炙，之亦反。啗，本亦作啖，又作噉，皆徒覽反，說文云：「噍也。」廣雅云：「食也。」\n[音釋-10]：尉。\n[釋文-11]：於貴反，下同\n[釋文-12]：亡后反\n[郭注-13]：無子者。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '669;4935/670;670;438;442;729/438', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5195, '齧;彫蓬;薦;黍蓬', '', '齧1，彫蓬2。薦3，黍蓬4。56', '[釋文-1]：魚結反\n[釋文-2]：丁遼反。步公反\n[釋文-3]：作見反，孫、李本作𧄟，沈平兆反\n[釋文-4]：音著\n[郭注-5]：別蓬種類。\n[釋文-6]：種，之勇反。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '1313;5718/671;6261;4509/671', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5196, '萆;鼠莞', '', '𦸣{萆}12，鼠莞34。5', '[音釋-1]：方寐。\n[釋文-2]：方寐反，又方弭反\n[音釋-3]：官。\n[釋文-4]：謝音官，施音丸\n[郭注-5]：亦莞屬也。纎細似龍須，可以爲席。蜀中出好者。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '604;6393/329', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5197, '莖;鼠尾', '', '葝{莖}1，鼠尾。23', '[釋文-1]：巨盈反\n[郭注-2]：可以染皂。\n[釋文-3]：皁，音造。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '493;6393/5428', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5198, '析蓂;大薺', '', '菥蓂{析蓂}12，大薺3。4', '[音釋-1]：惜。覓。\n[釋文-2]：思歷反。亡歷反\n[釋文-3]：齊禮反。補舍人曰：「薺有小，故言大薺」\n[郭注-4]：似薺，葉細，俗呼之曰老薺。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '3810/455;6598/400', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5199, '荼;虎杖', '', '蒤{荼}12，虎杖。34', '[音釋-1]：途。\n[釋文-2]：大奴反\n[郭注-3]：似紅草而麤大，有細刺，可以染赤。\n[釋文-4]：刺，七賜反，下同。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '668;3118/3741', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5200, '孟;狼尾', '', '孟，狼尾1。23', '[釋文-1]：音郎\n[郭注-2]：似茅，今人亦以覆屋。\n[釋文-3]：茅，亡交反。覆，音副。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '9774;6379/5428', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5201, '瓠棲;瓣', '', '瓠棲12，瓣34。5', '[音釋-1]：戸故。\n[釋文-2]：戸故反，舍人本又作觚，釋云：「瓠也。」詩作犀，音西\n[音釋-3]：方莧。\n[釋文-4]：苻莧、苻閑二反，謝力見反，郭云：「瓠中瓣也。」字林云：「瓜中實也，父莧反」\n[郭注-5]：瓠中瓣也。詩云：「齒如瓠棲。」', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '4554/7714;4552', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5202, '茹蘆;茅蒐;茜', '', '茹藘{茹蘆}12，茅蒐3。45{茜}', '[音釋-1]：如。力居。\n[釋文-2]：音如，字亦作蕠。力居反\n[釋文-3]：色留反\n[郭注-4]：今之蒨也，可以染絳。\n[釋文-5]：蒨，本或作茜，七見反。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '614/277;326/383;384', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5203, '果羸;栝樓', '䒷婁', '果臝{果羸}之實12，栝樓[䒷婁]3。4', '[音釋-1]：力果。\n[釋文-2]：力果反。補李巡曰：「栝樓子名也」\n[釋文-3]：本或作䒷，古活反。本或作蔞，力侯反，本草：「栝樓，一名地樓，一名天瓜，一名澤姑，一名果臝。實，一名黃瓜。」陶弘景注云：「出近道，藤生，狀似土瓜而葉有叉。實中人，今以雜作手膏用也。根入土六七尺，大二三圍者，服食亦用之」\n[郭注-4]：今齊人呼之爲天瓜。', '釋草弟十三', '爾雅', '2', DEFAULT, DEFAULT, '3568/2350;3759/3653', '398/8323', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5204, '荼;苦菜', '', '荼1，苦菜。2', '[釋文-1]：音徒，說文同，案詩云：「誰謂荼苦。」大雅云：「堇荼如飴。」本草云：「苦菜，一名荼草，一名選，生益州川谷。」名醫別録云：「一名游冬，生山陵道旁，冬不死。」月令：「孟夏之月，苦菜秀。」易通卦驗玄圖云：「苦菜生於寒秋，經冬歷春，得夏乃成。」今苦菜正如此，處處皆有，葉似苦苣，亦堪食，但苦耳。今在釋草篇，本草爲菜上品，陶弘景乃疑是茗，失之矣。釋木篇有「檟，苦荼。」乃是茗耳，音佳。補舍人曰：「杏華如荼，可耕白沙」\n[郭注-2]：詩曰：「誰謂荼苦？」苦菜可食。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '668;323/558', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5205, '萑;蓷', '隹', '萑[隹]12，蓷34。56', '[音釋-1]：隹。\n[釋文-2]：音隹\n[音釋-3]：他回。\n[釋文-4]：他回反，或音推，郭云：「茺蔚也。」本草：「茺蔚子，一名益母，一名大札，一名益明，一名貞蔚。」陶弘景云：「處處生，葉如荏，方莖，子細長三楞，白華，華生節閒。」補李巡曰：「臭穢草」\n[郭注-5]：今茺蔚也，葉似荏，方莖，白華，華生節閒。又名益母，廣雅云。\n[釋文-6]：茺，音充。蔚，音尉。荏，而甚反。𦶩，音益，本今作益。', '釋草弟十三', '爾雅', '1', DEFAULT, DEFAULT, '677;335', '2279', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5206, '鷊;綬', '𧅖', '虉{鷊}[𧅖]12，綬3。4', '[音釋-1]：逆。\n[釋文-2]：又作鷊，五歷反，郭音五革反\n[釋文-3]：音受\n[郭注-4]：小草，有雜色，似綬。', '釋草弟十三', '爾雅', '1', DEFAULT, DEFAULT, '2442;8648', '410', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5207, '粢;稷;衆;秫', '秶', '粢[秶]12，稷3。4衆56，秫78。910', '[音釋-1]：咨。\n[釋文-2]：音咨，左傳云：「粢食不鑿。」杜注云：「黍稷曰粢。」郭云：「今江東人呼粟爲粢。」陶注本草云：「白糧粟，或呼爲粢。」音緇。字林云：「黏稷也，今苗及穀似粟」\n[釋文-3]：本草云：「稷米，味甘無毒，益氣補不足。」陶注云：「不識。書多云稷，恐與黍相似。詩『黍稷稻粱』、『禾麻菽麥』，此八穀世人莫能證辨，如此穀稼米不能明，而況芝英者乎？汜勝之種殖書無稷。」案：禮記云：「子卯稷食菜羹。」又云：「君沐粱，大夫沐稷，士沐粱。」相承云稷，粟也。又郭注「衆秫」云「黏粟」，而說文、字林皆云「黏稷」，今秫苗及穀全似粟，唯色及黏爲異。又衆家釋粢皆爲粟，知稷卽粟也，然本草稷米在下品，別有粟米在中品，又似二物\n[郭注-4]：今江東人呼粟爲粢。\n[音釋-5]：終。\n[釋文-6]：音終\n[音釋-7]：述。\n[釋文-8]：音述，郭云：「黏粟也。」說文云：「稷之黏者。」字林亦云：「黏稷。」本草云：「秫米，味甘微寒，主止寒熱，利大腸、治漆創。」案江東人皆呼稻米爲秫米，嚼稻米以治漆創亦驗，然北閒自有秫穀，全與粟相似，米黏，北人用之釀酒，其莖稈似禾而麤大也\n[郭注-9]：謂黏粟也。\n[釋文-10]：黏，女廉反，字林云：「相著。」', '釋草弟十三', '爾雅', '1', DEFAULT, DEFAULT, '3215;4433;5237;4435', '4434', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5208, '戎叔;荏尗', '戎尗', '戎叔[戎尗]謂之荏菽{荏尗}12。3', '[音釋-1]：叔。\n[釋文-2]：而甚反。尗，舒育反，本亦作菽\n[郭注-3]：卽胡豆也。', '釋草弟十三', '爾雅', '1', DEFAULT, DEFAULT, '8379/1924;257/4507', '8379/4507', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5209, '卉;草', '', '卉1，草。23', '[釋文-1]：虛謂反\n[郭注-2]：百草揔名。\n[釋文-3]：揔，子孔反。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '629;680', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5210, '兖;雀弁', '舜;爵弁', '萒{兖}[舜]12，雀弁[爵弁]。3', '[音釋-1]：悅轉。\n[釋文-2]：悅轉反，又古本反\n[郭注-3]：未詳。', '釋草弟十三', '爾雅', '1;2', DEFAULT, DEFAULT, '7029;2286/5469', '3382;3203/5469', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5211, '蘥;雀麥', '爵麥', '蘥12，雀麥[爵麥]3。45', '[音釋-1]：藥。\n[釋文-2]：子若反\n[釋文-3]：亡革反\n[郭注-4]：卽燕麥也。\n[釋文-5]：燕，烏見反。', '釋草弟十三', '爾雅', '2', DEFAULT, DEFAULT, '417;2286/3350', '3203/3350', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5212, '壞;烏飧;柬;薟;兔荄;𦾴;兔奚', '', '蘾{壞}12，烏薞{烏飧}34。萰{柬}{薟}56，菟荄{兔荄}78。蘩{𦾴}9，菟蒵{兔奚}1011。12', '[音釋-1]：戸怪。\n[釋文-2]：戸怪反\n[音釋-3]：孫。\n[釋文-4]：西存反\n[音釋-5]：練。\n[釋文-6]：音練\n[音釋-7]：兔。核。\n[釋文-8]：音兔。古來反，一音皆\n[釋文-9]：音煩\n[音釋-10]：兮。\n[釋文-11]：音兮\n[郭注-12]：皆未詳。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '9120;2488/3227;3900;406;6295/515;669;6295/6672', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5213, '蔩;兔瓜;茢', '', '蔩12，菟瓜{兔瓜}3，茢45。6', '[音釋-1]：演。\n[釋文-2]：羊善反，又弋仁反\n[釋文-3]：補孫炎曰：「一名瓜列」\n[音釋-4]：列。\n[釋文-5]：音列\n[郭注-6]：莬瓜似土瓜。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '357;6295/4547;428', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5214, '薽;豕首', '', '薽12，豕首34。56', '[音釋-1]：真。\n[釋文-2]：音真\n[音釋-3]：傷氏。\n[釋文-4]：傷氏反\n[郭注-5]：本草曰：「彘盧，一名蟾蠩蘭。」今江東呼豨首，可以煼蠶蛹。\n[釋文-6]：彘，音滯。蟾，音占。豨，虛豈反。煼，字或作𤌉、（⿰火𢑑）、炒、𩱈、𤑵、㷅，七字並音初卯反，三蒼云：「熬也。」說文云：「火乾物也。」蠶，雜南反。蛹，音勇。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '392;6078/5703', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5215, '荓;馬帚', '', '荓12，馬帚3。45', '[音釋-1]：瓶。\n[釋文-2]：音瓶\n[釋文-3]：之酉反\n[郭注-4]：似蓍，可以爲掃蔧。\n[釋文-5]：蓍，音尸，說文云：「蒿屬也，生千歲，三百莖。」埽，蘇早反。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '358;6137/4903', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5216, '瘣;懷羊', '', '藱{瘣}12，懷羊。3', '[音釋-1]：胡罪。\n[釋文-2]：胡罪反\n[郭注-3]：未詳。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '4700;6764/2333', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5217, '茭;牛蘄', '', '茭1，牛蘄2。3', '[釋文-1]：郭胡卯反，又音交，或居交反\n[釋文-2]：音芹\n[郭注-3]：今馬蘄，葉細銳，似芹，亦可食。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '612;728/328', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5218, '突;蘆萉', '蘆菔', '葖{突}12，蘆萉[蘆菔]34。56', '[音釋-1]：他忽。\n[釋文-2]：孫、郭並他忽反，施徒忽反\n[音釋-3]：羅。蒲北。\n[釋文-4]：郭音力何反，謝力吳反。郭音菔，蒲北反\n[郭注-5]：萉宜爲菔。蘆菔，蕪菁屬，紫華，大根，俗呼雹葖。\n[釋文-6]：蕪，音無，本或作蔓，音萬。菁，音精，又子丁反。雹，步角反。', '釋草弟十三', '爾雅', '2', DEFAULT, DEFAULT, '4667;277/253', '277/278', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5219, '淔灌;囚芝', '', '淔灌12，3苬芝{囚芝}45。6', '[音釋-1]：勑。\n[釋文-2]：恥力反，聲類云：「淔灌，苬芝也」\n[郭注-3]：未詳。\n[音釋-4]：囚。\n[釋文-5]：沈、顧音祥由反，謝音由。音之，瑞草也\n[郭注-6]：芝，一歲三華，瑞草。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '7116/7044;3928/243', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5220, '筍;竹萌;簜;竹', '', '筍1，竹萌2。3簜45，竹。6', '[釋文-1]：息尹反，竹初生也\n[釋文-2]：亡耕反\n[郭注-3]：初生者。\n[音釋-4]：蕩。\n[釋文-5]：徒朗反，說文云：「大竹也。」尚書云：「篠簜旣敷」是也，本或作𥯕，音同，案說文：「𥮬，大竹筩也。」字林他莽反。補李巡曰：「竹節相去一丈。」孫炎曰：「竹闊節者曰簜」\n[郭注-6]：竹別名。儀禮曰：「簜在建鼔之閒。」謂簫管之屬。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '2873;2866/491;2871;2866', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5221, '莪;蘿', '', '莪1，蘿2。34', '[釋文-1]：五河反\n[釋文-2]：力何反\n[郭注-3]：今莪蒿也，亦曰䕲蒿。\n[釋文-4]：䕲，良甚反，廣雅云：「莪蒿，䕲蒿也。」', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '439;440', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5222, '尼;底尼;絰履', '薾', '苨{尼}[薾]12，菧苨{底尼}34。5絰履67。8', '[音釋-1]：禰。\n[釋文-2]：奴禮反\n[音釋-3]：底。\n[釋文-4]：丁禮反\n[郭注-5]：薺苨。\n[音釋-6]：待節。\n[釋文-7]：待節反\n[郭注-8]：未詳。', '釋草弟十三', '爾雅', '1', DEFAULT, DEFAULT, '5412;5952/5412;8749/5432', '503', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5223, '莕;接余;付', '菨餘', '莕1，接余[菨餘]2，其葉苻{付}。3', '[釋文-1]：音杏，本亦作荇，詩云：「參差荇菜。」說文作䓷\n[釋文-2]：如字，說文作菨，音同。羊如反，本或作荼，非\n[郭注-3]：叢生水中，葉圓，在莖端，長短隨水深淺。江東食之，亦呼爲莕，音杏。', '釋草弟十三', '爾雅', '2', DEFAULT, DEFAULT, '461;7919/718;5058', '462/3247', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5224, '白華;野菅', '', '白華，野菅12。3', '[音釋-1]：姦。\n[釋文-2]：古顏反\n[郭注-3]：菅，茅屬。詩曰：「白華菅兮。」', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '4931/3886;9171/327', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5225, '薜;白蘄', '', '薜1，白蘄2。3', '[釋文-1]：方麥反\n[釋文-2]：巨斤反\n[郭注-3]：卽上「山蘄」。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '386;4931/328', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5226, '菲;芴', '', '菲12，芴34。5', '[音釋-1]：匪。\n[釋文-2]：芳尾反。\n[音釋-3]：物。\n[釋文-4]：音物\n[郭注-5]：卽土瓜也。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '641;642', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5227, '葍;䔰', '', '葍12，䔰34。5', '[音釋-1]：福。\n[釋文-2]：方服反，說文云：「亦名舜，楚謂之葍，秦謂之藑，蔓地生而連花」\n[音釋-3]：富。\n[釋文-4]：音富\n[郭注-5]：大葉，白華，根如指，正白，可啖。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '369;368', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5228, '熒;委萎;胊;艼熒', '委蕤', '熒1，委萎[委蕤]23。45葋{胊}67，艼熒89。10', '[釋文-1]：戸坰反\n[音釋-2]：威。\n[釋文-3]：荽，謝於薳反，孫於爲反，郭女委反，本今作委。謝於危反，孫人垂反，郭音痿癖，同，案：痿，音人垂反，字林云：「痹也。」韓信云：「痿人不忘起」是也，讀史、漢者或於危反\n[郭注-4]：藥草也。葉似竹，大者如箭，竿有節，葉狹而長，表白裏靑，根大如指，長一二尺，可啖。\n[釋文-5]：竿，音幹，又音干，或古但反。\n[音釋-6]：劬。\n[釋文-7]：求于反\n[音釋-8]：他丁。迥。\n[釋文-9]：天頂反，又天丁反。音迥，又音螢\n[郭注-10]：未詳。', '釋草弟十三', '爾雅', '2', DEFAULT, DEFAULT, '6582;8215/616;2691;468/6582', '8215/507', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5229, '竹;萹蓄', '茿;萹茿', '竹[茿]1，萹蓄[萹茿]23。45', '[釋文-1]：竹，本又作筑，張六反。補孫炎曰：「詩云『菉竹猗猗』」\n[音釋-2]：匹善。\n[釋文-3]：匹善反，顧補殄、匹緜二反。勑六反，陶弘㬌云：「萹蓄，亦呼爲萹竹」\n[郭注-4]：似小藜，赤莖節，好生道旁，可食，又殺蟲。\n[釋文-5]：好，呼報反。蟲，直中反。', '釋草弟十三', '爾雅', '1;2', DEFAULT, DEFAULT, '2866;296/682', '297;296/297', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5230, '葴;寒漿', '', '葴12，寒漿3。4', '[音釋-1]：針。\n[釋文-2]：之金反\n[釋文-3]：𦺦，何干反，本今作寒\n[郭注-4]：今酸漿草，江東呼曰苦葴，音針。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '374;4615/7390', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5231, '薢茩;決光', '', '薢茩12，芵茪{決光}34。56', '[音釋-1]：皆。狗。\n[釋文-2]：郭音皆，一音古買反。古口反，秦人名䔖曰薢茩，廣雅云：「䔖、芰，薢茩」\n[音釋-3]：決。光。\n[釋文-4]：古穴反，本亦作決。光，本或作茪，古黃反\n[郭注-5]：芵明也。葉黃銳，赤華，實如山茱萸。或曰䔖也。關西謂之薢茩，音皆。\n[釋文-6]：茱，常朱反。萸，羊朱反。䔖，字又作菱，音陵。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '413/414;7274/6511', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5232, '巫荑;殺蘠', '蕪荑', '莁荑{巫荑}[蕪荑]12，蔱蘠{殺蘠}34。56', '[音釋-1]：巫。夷。\n[釋文-2]：亡符反，讀者或常制反，又戸耕反。羊而反\n[音釋-3]：殺。牆。\n[釋文-4]：所黠反。在羊反\n[郭注-5]：一名白蕢。\n[釋文-6]：蕢，巨貴反。', '釋草弟十三', '爾雅', '1', DEFAULT, DEFAULT, '3036/321;1979/450', '544/321', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5233, '瓞;勺;瓞', '㼎', '瓞12，瓝{勺}[㼎]34。其紹瓞5。67', '[音釋-1]：大結。\n[釋文-2]：大結反，詩云：「緜緜瓜瓞」\n[音釋-3]：步角。\n[釋文-4]：步角反，字林作㼎，云：「小瓜也」\n[釋文-5]：市沼反。補舍人曰：「紹，繼，謂瓞子。漢中小瓜曰瓞」\n[郭注-6]：俗呼瓝瓜爲瓞。紹者，瓜蔓緒，亦著子，但小如瓝。\n[釋文-7]：蔓，音萬。著，丁畧反。', '釋草弟十三', '爾雅', '2', DEFAULT, DEFAULT, '4549;9467;4549', '4548', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5234, '芍;鳧茈', '', '芍12，鳧茈3。4', '[音釋-1]：戸了。\n[釋文-2]：戸了反。補樊光曰：「澤草，可食」\n[釋文-3]：音扶。本又作𦶉，沈、顧徂斯反，謝徂咨反\n[郭注-4]：生下田，苗似龍須而細，根如指頭，黑色，可食。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '445;1958/380', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5235, '類;鼎蕫', '', '蘱{類}12，薡蕫{鼎蕫}34。5', '[音釋-1]：類。\n[釋文-2]：力愧反，又力對反\n[音釋-3]：鼎。董。\n[釋文-4]：音頂。本或作董，丁動反，施音童\n[郭注-5]：似蒲而細。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '6371;4409/402', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5236, '蕛苵', '', '蕛苵12。34', '[音釋-1]：啼。大結。\n[釋文-2]：大兮反，本又作稊，莊子云：「道在稊稗」是也。大結反\n[郭注-3]：蕛，似稗，布地生，穢草。\n[釋文-4]：稗，蒲賣反，字林云：「禾別名也。」', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '466/467', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5237, '鉤;芺', '', '鉤1，芺2。34', '[釋文-1]：古侯反\n[釋文-2]：於表反，又於老反\n[郭注-3]：大如拇指，中空，莖頭有臺，似，初生可食。\n[釋文-4]：拇，音母。薊，音計。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '1460;353', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5238, '䪥;鴻薈', '', '䪥1，鴻薈23。4', '[釋文-1]：胡界反\n[音釋-2]：會。\n[釋文-3]：烏外反\n[郭注-4]：卽䪥菜也。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '4544;2415/535', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5239, '蘇;桂荏', '', '蘇，桂荏。1', '[郭注-1]：蘇，荏類，故名桂荏。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '256;3431/257', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5240, '薔虞;蓼', '', '薔虞1，蓼23。4', '[釋文-1]：師力反。說文作𮒙，云：「虞蓼也。」音色\n[音釋-2]：了。\n[釋文-3]：音了\n[郭注-4]：虞蓼，澤蓼。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '663/3110;262', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5241, '條;蓨', '', '蓧{條}12，蓨34。5', '[音釋-1]：他凋。\n[釋文-2]：他彫反\n[音釋-3]：愓。\n[釋文-4]：他的反\n[郭注-5]：未詳。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '3572;370', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5242, '虋;芑;秬;秠', '', '虋12，赤苗。34芑56，白苗。7秬8，黑黍。9秠1011，一稃二米1213。1415', '[音釋-1]：門。\n[釋文-2]：詩作穈，字林亡昆反，郭亡津反，本亦作𧄸。補舍人曰：「別米赤白苗也，是伯夷、叔齊所食首陽草也」\n[郭注-3]：今之赤粱粟。\n[釋文-4]：粱，音良。\n[音釋-5]：起。\n[釋文-6]：羌紀反\n[郭注-7]：今之白粱粟，皆好穀。\n[釋文-8]：音巨，黑黍也，或云今蜀黍也，米白，穀黑。說文作𩰤。或作粔字\n[郭注-9]：詩曰：「維秬維秠。」\n[音釋-10]：孚鄙。\n[釋文-11]：孚鄙反，又孚丕反，字林匹几、匹九、夫九三反\n[音釋-12]：敷。\n[釋文-13]：音敷，本作殍、𦖀，並同\n[郭注-14]：此亦黑黍，但中米異耳。漢和帝時，任城生黑黍，或三四實，實二米，得黍三斛八斗，是。\n[釋文-15]：任，音壬。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '246;660;3204;4456', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5243, '稌;稻', '', '稌1，稻2。34', '[音釋-1]：杜\n[釋文-2]：待古反，又他古反，詩云：「豐年多黍多稌，爲酒爲醴。」禮記云：「牛宜稌，羊宜黍。」本草云：「秔米主益氣，止煩泄，稻米主溫中，令人多𤍠。」陶注云：「道家方藥有俱用稻米、稉米，此則是兩物，云稻米糠白如霜，今江東無，通呼稉米爲稻米耳，不知其色類復云何。」案說文云：「沛國謂稻爲穤，秔，稻屬也。」字林云：「穤，黏稻也。秔，稻不黏者。」李登聲類亦以秔爲不黏稻，但江東人呼爲稬。字林曰：「稬，乃亂反，字亦作穤，北人呼爲穤。」稉與粳皆俗秔字也，音庚。今稉、穤甚相類，但黏與不黏耳，依說文，穤卽稻也\n[郭注-3]：今沛國呼稌。\n[釋文-4]：沛，音貝，國名。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '4438;4437', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5244, '葍;藑茅', '', '葍1，藑茅23。45', '[釋文-1]：音福\n[音釋-2]：瓊。\n[釋文-3]：巨營反，又詳兖反\n[郭注-4]：葍華有赤者爲藑。藑、葍一種耳，亦猶菱、苕華黃、白異名。\n[釋文-5]：種，章勇反。苕，音條。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '369;367/326', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5245, '臺;夫須', '菭', '臺[菭]1，夫須23。45', '[釋文-1]：字又作薹，同\n[音釋-2]：扶。\n[釋文-3]：音扶。蕦，音須，本今作須\n[郭注-4]：鄭箋詩云：「臺，可以爲禦雨笠。」\n[釋文-5]：箋，卽田反。禦，音語。笠，音立。', '釋草弟十三', '爾雅', '1', DEFAULT, DEFAULT, '7712;6676/5708', '489', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5246, '搴;罰', '', '藆{搴}12，藅{罰}34。5', '[音釋-1]：蹇。\n[釋文-2]：居輦反，本亦作搴字\n[音釋-3]：伐。\n[釋文-4]：音伐，本又作罰\n[郭注-5]：未詳。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '8002;2798', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5247, '莔;貝母', '', '莔12，貝母。3', '[音釋-1]：萌。\n[釋文-2]：亡庚反，詩作蝱，同，本草云：「貝母，一名空草，一名藥實，一名苦華，一名苦菜，一名商草，一名勤母」\n[郭注-3]：根如小貝，貟而白華，葉似韭。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '453;3936/8141', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5248, '荍;蚍衃', '蚍芣', '荍12，蚍衃[蚍𧉈][蚍芣]34。5', '[音釋-1]：翹。\n[釋文-2]：祁堯反，或巨遶反\n[音釋-3]：毗。浮。\n[釋文-4]：婢夷反，郭音疕。本或作芣，又作𧉈，房尤反，郭芳九反\n[郭注-5]：今荆葵也，似葵，紫色。謝氏云：「小草，多華，少葉，葉又翹起。」', '釋草弟十三', '爾雅', '2', DEFAULT, DEFAULT, '318;8975/3170', '8975/497', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5249, '艾;冰臺', '', '艾1，冰臺2。3', '[釋文-1]：五蓋反\n[釋文-2]：彼升反\n[郭注-3]：今艾蒿。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '389;7511/7712', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5250, '蕇;亭歷', '', '蕇12，亭歷3。45', '[音釋-1]：典。\n[釋文-2]：音典\n[釋文-3]：字或作葶，同。字或作藶，廣雅云：「狗薺、大室，亭歷也。」本草云：「一名大室，一名大適丁歷反，一名丁歷，一名蕇。」今江東人呼爲公薺\n[郭注-4]：實葉皆似芥。一名狗薺，廣雅云。音典。\n[釋文-5]：芥，音界。狗，音苟。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '635;3322/1070', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5251, '付;鬼目', '', '苻{付}，鬼目。12', '[郭注-1]：今江東有鬼目草，莖似葛，葉貟而毛，子如耳璫也，赤色，叢生。\n[釋文-2]：璫，音當。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '5058;5821/2096', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5252, '薜;庾草', '', '薜1，庾草2。3', '[釋文-1]：布麥反\n[釋文-2]：字或作𦺮，謝羊主反，孫音臾\n[郭注-3]：未詳。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '386;5942/680', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5253, '𦺋;敖縷', '', '𦺋12，蔜𧃒{敖縷}34。56', '[音釋-1]：㛮。\n[釋文-2]：字或作䕅，謝先老反，沈、施所留反，本草呼爲繁𧃒\n[音釋-3]：五高。縷。\n[釋文-4]：𦽠，五高反，本今作蔜\n[郭注-5]：今蘩𧃒也，或曰鷄膓草。\n[釋文-6]：蘩，音煩。𧃒，字亦作縷，力主反，今蘩𧃒草。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '404;2514/8676', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5254, '離南;活脫', '', '離南1，活莌{活脫}23。45', '[釋文-1]：蘺，力知反，本今作離\n[音釋-2]：奪。\n[釋文-3]：胡闊反。字或作𦸍，徒活反\n[郭注-4]：草生江南，高丈許，大葉，莖中有瓤，正白，零陵人植而日灌之以爲樹。\n[釋文-5]：瓤，女良反，三蒼云：「瓜中子也。」日，人一反。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '2294/3875;7151/2653', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5255, '蘢;天蘥;葑;須從', '', '蘢12，天蘥3。葑45，須蓯{須從}67。8', '[音釋-1]：聾。\n[釋文-2]：郭音聾，施音龍\n[釋文-3]：餘若反\n[音釋-4]：方孔。\n[釋文-5]：方孔反\n[音釋-6]：總。\n[釋文-7]：蕦，音須，本今作須。音揔\n[郭注-8]：未詳。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '436;3/417;399;5708/5227', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5256, '芳;隱荵', '', '蒡{芳}12，隱荵34。56', '[音釋-1]：旁。\n[釋文-2]：郭音彭，又音旁\n[音釋-3]：忍。\n[釋文-4]：音忍，注同\n[郭注-5]：似蘇，有毛，今江東呼爲隱荵，藏以爲葅，亦可瀹食。\n[釋文-6]：葅，莊居反。瀹，餘若反，字林云：「煮也。」本亦作汋，音同，後放此。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '574;9669/306', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5257, '莤;蔓于', '蕕', '莤[蕕]12，蔓于3。4', '[音釋-1]：由。\n[釋文-2]：郭音由，又音酉\n[釋文-3]：或作曼，通音萬\n[郭注-4]：草生水中，一名軒于，江東呼莤，音猶。', '釋草弟十三', '爾雅', '1', DEFAULT, DEFAULT, '9856;459/3070', '359', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5258, '蓾;苴', '', '蓾12，蔖{苴}34。56', '[音釋-1]：魯。\n[釋文-2]：音魯\n[音釋-3]：才古。\n[釋文-4]：施、謝才古反，郭才河、采苦二反，字林千古反\n[郭注-5]：作履苴草。\n[釋文-6]：苴，將呂反，一云將慮反，字苑云：「𩏈苴，履底。」', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '375;606', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5259, '柱夫;揺車', '', '柱夫12，揺車3。4', '[音釋-1]：主。扶。\n[釋文-2]：張縷反，本或作拄，同。音扶，或如字\n[釋文-3]：尺蛇反，又音居\n[郭注-4]：蔓生，細葉，紫華，可食。今俗呼曰翹揺車。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '3627/6676;7951/9514', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5260, '出遂;蘧蔬', '氍毹', '出隧{出遂}12，蘧蔬[氍毹]34。56', '[音釋-1]：遂。\n[釋文-2]：音遂\n[音釋-3]：巨俱。山俱。\n[釋文-4]：郭音氍，巨俱反，謝音渠。郭音𣮸，山俱反，謝音踈\n[郭注-5]：蘧蔬似土菌，生菰草中，今江東啖之，甜滑。音氊氍㲣。\n[釋文-6]：菌，巨隕反。菰，音孤。廣雅云：「蔣也。」啖，大敢反。甜，徒謙反。滑，乎八反。氊，之延反，本亦作旃，同。氍，音衢，又音渠，字又作瞿。𣮸，所俱反，又所魚反，本今作㲣。', '釋草弟十三', '爾雅', '2', DEFAULT, DEFAULT, '3865/1175;272/691', '5394/5395', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5261, '蘄茝;蘪蕪', '', '蘄茝12，蘪蕪3。45', '[音釋-1]：昌改。\n[釋文-2]：巨斤反。昌改、昌敗二反，本草云：「白芷，一名白茝」\n[釋文-3]：麋，亡悲反，本今作蘪。亡符反，本草：「蘪蕪，一名微蕪，一名江離，芎藭苗也。」陶注云：「葉似蛇牀而香。」補樊光曰：「藁本，一名蘪蕪，根名蘄芷」\n[郭注-4]：香草，葉小如萎狀，淮南子云：「似蛇牀。」山海經云：「臭如蘪蕪。」\n[釋文-5]：萎，於危反，字林於僞反。臭，昌又反。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '328/292;293/544', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5262, '茨;疾黎', '薺', '茨[薺]1，蒺蔾{疾黎}2。34', '[釋文-1]：徂咨反，或作薋，同\n[釋文-2]：音疾。䔧，音梨，案本草：「蒺䔧，一名旁通，一名屈人，一名止行，一名豺羽，一名升推，一名卽梨，一名茨，多生道上，布地，子及葉並有刺，狀如雞菱」\n[郭注-3]：布地蔓生，細葉，子有三角，刺人。見詩。\n[釋文-4]：刺，七亦反。見，賢遍反，後放此。', '釋草弟十三', '爾雅', '1', DEFAULT, DEFAULT, '584;4696/4515', '400', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5263, '罽挐;竊衣', '', '蘮蒘{罽挐}12，竊衣3。45', '[音釋-1]：計。如。\n[釋文-2]：居例反，郭巨例反。女居反\n[釋文-3]：音切\n[郭注-4]：似芹，可食，子大如麥，兩兩相合，有毛，著人衣。\n[釋文-5]：芹，音勤。著，直畧反。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '4825/7879;4383/5254', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5264, '髦;顚棘', '', '髦12，顚蕀{顚棘}34。5', '[音釋-1]：毛。\n[釋文-2]：音毛\n[音釋-3]：棘。\n[釋文-4]：都年反。古力反。補孫炎曰：「一名白棘」\n[郭注-5]：細葉，有刺，蔓生，一名商蕀。廣雅云：「女木也。」', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '5736;5608/4399', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5265, '雚;芄蘭', '莞', '雚[莞]12，芄蘭3。45', '[音釋-1]：貫。\n[釋文-2]：郭音灌，謝音官，沈、施音丸\n[釋文-3]：音桓。力丹反\n[郭注-4]：雚芄，蔓生，斷之有白汁，可啖。\n[釋文-5]：斷，丁管反。汁，之什反。', '釋草弟十三', '爾雅', '1', DEFAULT, DEFAULT, '2324;289/286', '329', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5266, '𧂇;䒞藩', '', '𧂇12，莐藩{䒞藩}34。5', '[音釋-1]：徒南。\n[釋文-2]：孫云：「古藫字，徒南反。」說文云：「或作蕁字」\n[音釋-3]：沉。\n[釋文-4]：字或作䒞，音直林反。甫煩反，又音煩，郭云：「一名提母。」本草謂之「知母，一名蚔母，一名連母，一名野蓼，一名地參，一名水參，一名貨母，一名蝭母，一名女雷，一名女理，一名兒草，一名鹿列，一名韭逢，一名兒踵，一名東根，一名水須，一名莐藩，一名𧂇。」陶注云：「形似菖蒲而柔潤，葉至難死，掘出隨生，須枯燥乃止，堪治𤍠病，亦主瘧疾」\n[郭注-5]：生山上，葉如韭，一曰提母。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '341;448/590', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5267, '渝;舄', '', '蕍{渝}12，蕮{舄}34。5', '[音釋-1]：俞。\n[釋文-2]：羊朱反\n[音釋-3]：昔。\n[釋文-4]：本又作舄，私夕反，下同，郭云：「今澤舄。」案本草云：「一名水舄，一名及寫，一名芒芋。」陶注云：「葉狹而長，叢生淺水中，仙經服食用之，令人身輕，能步行水上」\n[郭注-5]：今澤蕮。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '7441;2489', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5268, '圈;藨;鹿𧆑;莥', '麕', '蔨{圈}[麕]{藨}12，鹿𧆑34，其實莥56。78', '[音釋-1]：巨隕。\n[釋文-2]：謝其隕反，郭巨阮反，施其免反，沈巨轉反\n[音釋-3]：霍。\n[釋文-4]：蔍，力斛反，本今作鹿。火郭反，說文云：「菽之少也。」字又作藿\n[音釋-5]：女九。\n[釋文-6]：女久、其久二反\n[郭注-7]：今鹿豆也，葉似大豆，根黃而香，蔓延生。\n[釋文-8]：蔓，音萬。莚，以戰反，又音延，本又作延。', '釋草弟十三', '爾雅', '1', DEFAULT, DEFAULT, '3920;409;6263/249;250', '6277', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5269, '鎬侯;莎;媞', '緹', '薃侯{鎬侯}12，莎3，其實媞[緹]45。67', '[音釋-1]：浩。\n[釋文-2]：胡老反\n[釋文-3]：先禾反\n[音釋-4]：提。\n[釋文-5]：尼兮反\n[郭注-6]：夏小正曰：「薃也者，莎䔺。媞者，其實。」\n[釋文-7]：夏，音下。正，之盈反。䔺，弋垂、徂規二反，廣雅云：「蔕也。」又云：「地毛，莎䔺也。」本或作蓨，他狄反。', '釋草弟十三', '爾雅', '3', DEFAULT, DEFAULT, '9307/3313;638;8230', '8623', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5270, '莞;付蘺;蒚', '𦺊;夫離', '莞[𦺊]12，苻蘺{付蘺}[夫離]3，其上蒚45。6', '[音釋-1]：官。\n[釋文-2]：本或作𦺊，謝音官，郭音桓，字林音緩，俗音關\n[釋文-3]：力知反，本或作離\n[音釋-4]：力。\n[釋文-5]：郭音翮，又音歷\n[郭注-6]：今西方人呼蒲爲莞蒲，蒚謂其頭臺首也。今江東謂之苻蘺。西方亦名蒲，中莖爲蒚，用之爲席，音羽翮。', '釋草弟十三', '爾雅', '1;2', DEFAULT, DEFAULT, '329;5058/291;339', '338;6676/2294', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5271, '荷;芙渠;茄;蔤;菡𧂄;蓮;藕;旳;𦺳', '扶渠', '荷1，芙渠[扶渠]2，34其莖茄56，其葉蕸78，其本蔤910，1112其華菡萏{菡𧂄}1314，15其實蓮16，17其根藕18，其中的{旳}19，20的中薏{𦺳}2122。23', '[釋文-1]：音河\n[釋文-2]：音苻，本或作扶。本又作蕖，音同\n[郭注-3]：別名芙蓉，江東呼荷。\n[釋文-4]：蓉，音容，本亦作容。\n[音釋-5]：加。\n[釋文-6]：戸耕反。古牙反\n[音釋-7]：遐。\n[釋文-8]：字或作葭，音遐，又音加。衆家並無此句，唯郭有，然就郭本中或復脫此一句，亦並闕讀\n[音釋-9]：密。\n[釋文-10]：亡筆反\n[郭注-11]：莖下白蒻在泥中者。\n[釋文-12]：蒻，音若。\n[音釋-13]：戸瓜。戸感。徒感。\n[釋文-14]：戸感反。𦽜，字又作𧂄，徒感反，說文云：「菡萏，華未發也，已發名芙蓉。」張揖同，亦曰芙渠，本今作萏\n[郭注-15]：見詩。\n[釋文-16]：力田反\n[郭注-17]：蓮謂房也。\n[釋文-18]：字亦作蕅，同，五口反，案本草云：「一名水芝丹」\n[釋文-19]：丁歷反，又戸了反，或作菂，同\n[郭注-20]：蓮中子也。\n[音釋-21]：憶。\n[釋文-22]：於力反\n[郭注-23]：中心苦。', '釋草弟十三', '爾雅', '2', DEFAULT, DEFAULT, '433;685/7265;432;434;429/430;431;435;4201;325', '7856/7265', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5272, '紅;蘢古;差', '', '紅，蘢古1，其大者蒫{差}23。45', '[釋文-1]：力恭反，又力公反\n[音釋-2]：才何。\n[釋文-3]：才河反，又子邪反\n[郭注-4]：俗呼紅草爲蘢鼓，語轉耳。\n[釋文-5]：龍，如字，本今作蘢。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '8626;436/1461;3029', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5273, '蘬', '', '蘬12，薺實。3', '[音釋-1]：丘軌。\n[釋文-2]：謝丘軌反，郭匡龜反\n[郭注-3]：薺子名。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '673', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5274, '黂;枲;麻', '', '黂12，枲實34。56枲，麻。78', '[音釋-1]：扶刃。\n[釋文-2]：本或作蕡，苻刃反，或扶沸反\n[音釋-3]：皁以。\n[釋文-4]：息似反\n[郭注-5]：禮記曰：「苴麻之有黂。」\n[釋文-6]：苴，七徐反，下同。\n[郭注-7]：別二名。\n[釋文-8]：別，彼列反。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '253;4532;4536', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5275, '須;飧蕪', '', '須，薞蕪{飧蕪}1。23', '[釋文-1]：音孫\n[郭注-2]：薞蕪似羊蹄，葉細，味酢，可食。\n[釋文-3]：酢，七故反。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '5708;3227/544', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5276, '菲;息菜', '', '菲12，蒠菜{息菜}34。5', '[音釋-1]：匪。\n[釋文-2]：孚匪反，又音妃\n[音釋-3]：息。\n[釋文-4]：音息，本又作息\n[郭注-5]：菲草生下溼地，似蕪菁，華紫赤色，可食。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '641;6706/558', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5277, '蕢;赤莧', '', '蕢12，赤莧3。4', '[音釋-1]：巨貴。\n[釋文-2]：字亦作𧂟，巨愧反，又苦怪反\n[釋文-3]：閑辨反\n[郭注-4]：今之莧赤莖者。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '608;6587/269', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5278, '蘠靡;虋冬', '', '蘠蘼{蘠靡}12，虋冬3。4', '[音釋-1]：美。\n[釋文-2]：音牆。亡彼反，又作靡，同\n[釋文-3]：音門，本皆作門，郭云：「門，俗字。」亦作𧄸字。山海經云：「條谷山其草多芍藥、虋冬。」郭云：「本草一名滿冬。」案本草：「天門冬，一名顚勒。」麥門冬，陶注云：「秦名羊韭，齊名愛韭，楚名馬韭，越名羊蓍，一名禹葭，一名禹餘糧，葉如韭，冬夏生。」無名滿冬者\n[郭注-4]：門冬，一名滿冬，本草云。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '450/7698;246/7516', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5279, '萹付;止濼;貫衆', '', '萹苻{萹付}12，3止濼45，貫衆67。8', '[音釋-1]：偏。\n[釋文-2]：匹緜反\n[郭注-3]：未詳。\n[音釋-4]：爍。\n[釋文-5]：郭舒若反，孫餘若反\n[音釋-6]：終。\n[釋文-7]：古亂反。音終，本草云：「貫衆，一名貫節，一名貫渠，一名百頭，一名虎卷，一名萹苻，一名伯藥，一名藥藻。此謂草鴟頭也。」衆爾雅：「萹苻，止。」郭云：「未詳。」本草乃是貫衆\n[郭注-8]：葉貟銳，莖毛黑，布地，冬不死。一名貫渠。廣雅云：「貫節。」', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '296/5058;1064/7070;4341/5237', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5280, '莙;牛薻', '', '莙12，牛薻34。5', '[音釋-1]：其隕。\n[釋文-2]：其隕反，孫居筠反\n[音釋-3]：早。\n[釋文-4]：音早\n[郭注-5]：似薻，葉大，江東呼爲馬薻。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '337;728/650', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5281, '逐𦳝;馬尾', '', '蓫薚{逐𦳝}12，馬尾。34', '[音釋-1]：他六。他羊。\n[釋文-2]：他六反。呂、郭他羊反，謝他唐反，廣雅云：「馬尾，蔏陸也。」郭云：「江東呼爲當陸。」一名薚。根，一名夜呼，如人形者有神\n[郭注-3]：廣雅曰：「馬尾，蔏陸。」本草云別名薚。今關西亦呼爲薚，江東呼爲當陸。\n[釋文-4]：蔏，音商，本亦作商。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '1178/372;6137/5428', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5282, '萍;蓱;頻', '薲', '萍12，蓱34。56其大者蘋{頻}[薲]7。8', '[音釋-1]：平。\n[釋文-2]：苹，音平\n[音釋-3]：瓶。\n[釋文-4]：萍，本或作蓱，音瓶\n[郭注-5]：水中浮蓱，江東謂之薸，音瓢。\n[釋文-6]：薸，郭音瓢，婢遥反，廣雅云：「䕯，蓱也。」\n[釋文-7]：毗人反，說文作𦿜\n[郭注-8]：詩曰：「于以采蘋。」', '釋草弟十三', '爾雅', '3', DEFAULT, DEFAULT, '7448;639;7478', '281', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5283, '莃;兔葵', '', '莃12，菟葵{兔葵}3。45', '[音釋-1]：希。\n[釋文-2]：虛祈反\n[釋文-3]：湯故反。夬唯反\n[郭注-4]：頗似葵而小葉，狀如藜，有毛，汋啖之滑。\n[釋文-5]：汋，以灼反。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '362;6295/260', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5284, '芹;楚葵', '', '芹，楚葵。1', '[郭注-1]：今水中芹菜。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '391;3851/260', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5285, '隤;牛穨', '', '藬{隤}12，牛蘈{牛穨}34。56', '[音釋-1]：吐回。\n[釋文-2]：本又作（⿱艸𨽠），同，吐回反\n[音釋-3]：頽。\n[釋文-4]：大回反\n[郭注-5]：今江東呼草爲牛蘈者，高尺餘許，方莖，葉長而銳，有穗，穗閒有華，華紫縹色，可淋以爲飲。\n[釋文-6]：穗，音遂，說文作𥝩，云：「禾成秀，人所收也。穗，俗字。」廣雅云：「𥝩、䆃，𥝩也。」縹，匹眇反，又匹妙反，字林云：「靑白色。」淋，音林，字林云：「以水沃也。」', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '9647;728/5475', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5286, '藚;牛脣', '', '藚12，牛脣3。45', '[音釋-1]：續。\n[釋文-2]：音續\n[釋文-3]：䔚，音脣，本今作脣\n[郭注-4]：毛詩傳曰：「水蕮也。」如藚斷，寸寸有節，拔之可復。\n[釋文-5]：蕮，音昔。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '661;728/2602', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5287, '苹;賴蕭', '', '苹1，藾蕭{賴蕭}23。4', '[釋文-1]：皮英反\n[音釋-2]：賴。\n[釋文-3]：力大反\n[郭注-4]：今藾蒿也，初生亦可食。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '279;3964/443', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5288, '連;異翹', '', '連，異翹1。23', '[釋文-1]：字亦作𧄍，祁饒反，本草：「連翹，一名異翹，一名軹，一名蘭華，一名折根，一名三廉」\n[郭注-2]：一名連苕，又名連草，本草云。\n[釋文-3]：苕，音條。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '1168;1778/2252', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5289, '澤;烏飧', '', '澤，烏薞{烏飧}1。2', '[釋文-1]：蘇存反\n[郭注-2]：卽上「蘾」也。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '7222;2488/3227', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5290, '傅;橫目', '𦺉', '傅[𦺉]1，橫目2。34', '[釋文-1]：音付\n[釋文-2]：如字，胡彭反，或音黃\n[郭注-3]：一名結縷，俗謂之鼔箏草。\n[釋文-4]：𥿍，音姑，本今作結。縷，本亦作𧃒，力主反。箏，側耕反。', '釋草弟十三', '爾雅', '1', DEFAULT, DEFAULT, '5044;3796/2096', '519', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5291, '釐;蔓華', '萊', '釐[萊]1，蔓華。23', '[釋文-1]：力基反\n[郭注-2]：一名蒙華。\n[釋文-3]：蒙，亡公反。', '釋草弟十三', '爾雅', '1', DEFAULT, DEFAULT, '9170;459/3886', '647', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5292, '蔆;蕨麋;蕨攈', '', '蓤{蔆}12，蕨攗{蕨麋}{蕨攈}34。56', '[音釋-1]：凌。\n[釋文-2]：䔖，字又作菱，力矜反，本今作蓤\n[音釋-3]：眉。\n[釋文-4]：居月反。亡悲反，孫居郡反，又居羣反\n[郭注-5]：蔆，今水中芰。\n[釋文-6]：芰，巨義反，字林云：「楚人名䔖曰芰。」', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '411;637/6272;637/7987', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5293, '大菊;蘧麥', '', '大菊1，蘧麥2。34', '[釋文-1]：居六反\n[釋文-2]：音渠，或音劬，字或作遽，音同。字亦作麦，郭云：「蘧麥，一名麥句薑。」卽瞿麥，廣雅云：「茈萎，麥句薑，蘧麥。」本草：「瞿麥，一名巨句麥，一名大匊，一名大蘭。」陶注云：「一莖生細葉，華紅紫赤可愛」\n[郭注-3]：一名麥句薑，卽瞿麥。\n[釋文-4]：瞿，求于反。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '6598/273;272/3350', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5294, '薜;牡贊', '', '薜12，牡𧄽{牡贊}34。5', '[音釋-1]：百。\n[釋文-2]：彼麥反\n[音釋-3]：贊。\n[釋文-4]：亡后反。子旦反\n[郭注-5]：未詳。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '386;729/3949', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5295, '葥;山莓', '', '葥12，山莓34。56', '[音釋-1]：箭。\n[釋文-2]：子賤反\n[音釋-3]：每。\n[釋文-4]：音每，又音梅，後注同\n[郭注-5]：今之木莓也。實似藨莓而大，亦可食。\n[釋文-6]：藨，皮苗反，又皮表反。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '312;5850/300', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5296, '齧;苦蓳', '', '齧1，苦蓳2。34', '[釋文-1]：五結反\n[釋文-2]：音謹\n[郭注-3]：今蓳葵也，葉似栁，子如米，汋食之滑。\n[釋文-4]：汋，以灼反。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '1313;323/640', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5297, '潭;石衣;菭', '', '藫{潭}12，石衣。{菭}34', '[音釋-1]：潭。\n[釋文-2]：徒南反\n[郭注-3]：水苔也，一名石髮，江東食之。或曰藫，葉似䪥而大，生水底，亦可食。\n[釋文-4]：菭，徒來反，郭云：「一名石髮。」說文云：「水靑衣也。」或丈之反，本今作苔。底，丁禮反。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '7041;6011/5254;489', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5298, '蘜;治牆', '', '蘜12，治蘠{治牆}。3', '[音釋-1]：菊。\n[釋文-2]：字或作菊，居六反，說文云：「蘜，治牆也。」又作鞠，云：「日精也」\n[郭注-3]：今之秋華菊。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '449;7089/3347', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5299, '唐;蒙;女蘿;兔絲', '', '唐1、蒙，女蘿2。女蘿，菟絲{兔絲}。34', '[釋文-1]：蓎，音唐，本今作唐\n[釋文-2]：音羅\n[郭注-3]：別四名。詩云：「爰采唐矣。」\n[釋文-4]：別，彼列反。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '878;649;8111/440;6295/8782', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5300, '苖;蓨', '', '苖1，蓨23。4', '[釋文-1]：郭他六反，又徒的反，說文云：「從由聲」\n[音釋-2]：他凋。\n[釋文-3]：郭湯彫、他周二反，顧他迪反\n[郭注-4]：未詳。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '371;370', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5301, '茥;缺盆', '', '茥12，蒛葐{缺盆}34。56', '[音釋-1]：奎。\n[釋文-2]：苦圭反\n[音釋-3]：缺。盆。\n[釋文-4]：去悅反。步昆反，注同\n[郭注-5]：覆葐也，實似莓而小，亦可食。\n[釋文-6]：覆，芳服反。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '336;3303/3148', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5302, '芨;蓳草', '', '芨12，蓳草3。4', '[音釋-1]：急。\n[釋文-2]：居及反，又起及反\n[釋文-3]：堇，音謹，下注同，郭音靳，居覲反，云：「卽烏頭也。」案本草：「蒴藋，一名堇草，一名芨。」非烏頭也\n[郭注-4]：卽烏頭也。江東呼爲蓳，音靳。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '311;640/680', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5303, '韱;百足', '', '虃{韱}12，百足。3', '[音釋-1]：纎。\n[釋文-2]：息廉、子廉二反\n[郭注-3]：未詳。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '4545;2232/1326', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5304, '肩;戎葵', '', '菺{肩}12，戎葵3。4', '[音釋-1]：肩。\n[釋文-2]：古田反\n[釋文-3]：茙，音戎，本今作戎\n[郭注-4]：今蜀葵也，似葵，華如木槿華。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '2624;8379/260', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5305, '蘻;狗毒', '', '蘻12，狗毒3。4', '[音釋-1]：計。\n[釋文-2]：郭音古系反，又苦系反\n[釋文-3]：音苟\n[郭注-4]：樊光云：「俗語：苦如蘻。」', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '403;6304/236', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5306, '垂;比葉', '', '垂，比葉。1', '[郭注-1]：未詳。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '9149;5229/495', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5307, '蕧;盜庚', '', '蕧12，盜庚3。4', '[音釋-1]：服。\n[釋文-2]：郭音服，施孚服反，郭云：「旋蕧也。」本草云：「一名金沸草，一名盛椹，一名戴葚。」陶注云：「花似菊花而大」\n[釋文-3]：本又作羹，同，古衡反\n[郭注-4]：旋蕧，似菊。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '364;5596/9755', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5308, '芓;麻母', '', '茡{芓}12，麻母。34', '[音釋-1]：字。\n[釋文-2]：孫音嗣，本又作字，說文作芓，云：「卽枲也」\n[郭注-3]：苴麻，盛子者。\n[釋文-4]：苴，七徐反。盛，音成。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '254;4536/8141', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5309, '勺;九葉', '㼎', '瓝{勺}[㼎]1，九葉。2', '[釋文-1]：步角反，舍人云：「瓝，九葉，九枚共一莖。」樊本瓝字作駮，釋云：「駮也，一名九葉」\n[郭注-2]：今江東有草：五葉，共叢生一莖，俗因名爲五葉，卽此類也。', '釋草弟十三', '爾雅', '1', DEFAULT, DEFAULT, '9467;9730/495', '4548', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5310, '藐;茈草', '', '藐12，茈草34。56', '[音釋-1]：亡角。\n[釋文-2]：亡角反，又亡校反\n[音釋-3]：子爾。\n[釋文-4]：子爾反\n[郭注-5]：可以染紫。一名茈䓞，廣雅云。\n[釋文-6]：䓞，力計反。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '381;380/680', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5311, '𠋣商;活脫', '', '𠋣商1，活脫23。4', '[釋文-1]：謝於綺反，或其綺反，舍人本作猗，音同。蔏，音商，本今作商\n[音釋-2]：奪。\n[釋文-3]：如字，孫音括。徒活反，字又作𦮀\n[郭注-4]：卽離南也。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '5047/1453;7151/2653', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5312, '職;黃蒢', '', '蘵{職}12，黃蒢34。56', '[音釋-1]：職。\n[釋文-2]：字又作職，諸弋反\n[音釋-3]：除。\n[釋文-4]：直居反\n[郭注-5]：蘵草葉似酸漿，草小而白，中心黃，江東以作葅食。\n[釋文-6]：菹，側居反。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '7807;9203/331', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5313, '藒車;芞輿', '', '藒車12，芞輿34。5', '[音釋-1]：挈。\n[釋文-2]：去竭反，謝起例反，郭去謁反。音居，本多無此字\n[音釋-3]：乞。\n[釋文-4]：謝去訖反，沈又虛訖反。字或作藇，音餘，唯郭、謝及舍人本同，衆家並作蒢\n[郭注-5]：藒車，香草，見離騷。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '298/9514;299/9528', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5314, '權;黃華', '', '權1，黃華。23', '[釋文-1]：其圎反\n[郭注-2]：今謂牛芸草爲黃華，華黃，葉似苜蓿。\n[釋文-3]：芸，音云。𦱒，音牧，本亦作目。蓿，音肅。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '3521;9203/3886', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5315, '弭;春草', '', '葞{弭}12，春草。3', '[音釋-1]：尾。\n[釋文-2]：彌爾反\n[郭注-3]：一名芒草，本草云。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '8481;683/680', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5316, '終葵;䋣露', '', '蔠葵{終葵}12，蘩露{䋣露}3。4', '[音釋-1]：終。\n[釋文-2]：終，本亦作蔠，同\n[釋文-3]：音煩\n[郭注-4]：承露也。大莖，小葉，華紫黃色。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '8590/260;8707/7563', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5317, '菋;荎藸', '', '菋12，荎藸34。5', '[音釋-1]：未。\n[釋文-2]：音味，又亡戒反\n[音釋-3]：直其。除。\n[釋文-4]：直其反。音除\n[郭注-5]：五味也，蔓生，子叢在莖頭。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '456;457/349', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5318, '荼;委葉', '', '蒤{荼}12，委葉3。45', '[音釋-1]：徒。\n[釋文-2]：荼，字亦作蒤，音徒\n[釋文-3]：於詭反，或於危反。字或作萎，同\n[郭注-4]：詩云：「以茠蒤蓼。」\n[釋文-5]：茠，本或作薅，火羔反，又作𣐾，同，說文茠或作薅字。音了。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '668;8215/495', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5319, '皇;守田', '', '皇，守田。1', '[郭注-1]：似燕麥，子如彫胡米，可食，生廢田中，一名守氣。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '80;4596/9172', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5320, '鉤;睽姑', '', '鉤1，藈姑{睽姑}23。45', '[釋文-1]：古侯反\n[音釋-2]：圭。\n[釋文-3]：本或作睽，顧、謝同音圭，孫苦圭反。菇，音姑，本今作姑\n[郭注-4]：鉤𤬏也，一名王瓜，實如瓝瓜，正赤，味苦。\n[釋文-5]：𤫱，音鉤，又五侯反。𤬏，力侯反，字林云：「𤫱𤬏，王瓜也。」', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '1460;2143/8146', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5321, '望;椉車;𧂘', '', '望，椉車12。34{𧂘}', '[音釋-1]：繩。\n[釋文-2]：本又作乗，施音繩，謝市證反。居，本亦作車，音同\n[郭注-3]：可以爲索，長丈餘。\n[釋文-4]：索，悉各反，下同。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '8415;3412/9514;348', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5322, '困;及夅;及縫', '稛', '困[稛]，衱袶{及夅}{及縫}12。3', '[音釋-1]：劫。絳。\n[釋文-2]：居業反。施音綘，孫蒲空反，本今作袶\n[郭注-3]：未詳。', '釋草弟十三', '爾雅', '1', DEFAULT, DEFAULT, '3931;1918/3406;1918/8679', '4469', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5323, '攫;烏階', '衢', '攫[衢]12，烏階。34', '[音釋-1]：钁。\n[釋文-2]：沈居縛反\n[郭注-3]：卽烏杷也。子連相著，狀如杷齒，可以染皂。\n[釋文-4]：杷，白麻反，字從木，下同。著，直畧反。皁，音造。', '釋草弟十三', '爾雅', '1', DEFAULT, DEFAULT, '7984;2488/9693', '1269', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5324, '杜;土鹵;若', '', '杜1，土鹵2。34{若}', '[釋文-1]：郭云：「杜衡也，似葵而香。」案本草云：「杜衡，味辛，香人衣體。」陶注云：「根葉都似細辛，唯氣小異耳。」本草經又有「杜若，一名杜衡。」陶注云：「葉似薑，根亦似高良薑而細，氣味辛香，又絶似旋蕧根，殆欲相亂。」如陶之言，二種並不似葵，或恐郭誤耳\n[釋文-2]：他覩反。蓾，本又作鹵，音魯\n[郭注-3]：杜衡也，似葵而香。\n[釋文-4]：衡，字或作蘅，音行。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '3433;9021/7716;599', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5325, '盱;虺牀', '', '盱12，虺牀3。4', '[音釋-1]：吁。\n[釋文-2]：香于反，又音于\n[釋文-3]：虛鬼反，郭云：「蛇牀也。」廣雅云：「一名馬牀。」本草云：「蛇牀子，一名蛇粟，一名蛇米，一名虺牀，一名思益，一名繩毒，一名棗棘，一名牆蘼。」陶注云：「華葉正似蘪蕪」\n[郭注-4]：蛇牀也，一名馬牀，廣雅云。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '2134;8802/3679', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5326, '眯;敖', '', '蔝{眯}12，蔜{敖}34。5', '[音釋-1]：米。\n[釋文-2]：音米\n[音釋-3]：五刀。\n[釋文-4]：樊本作「蔝，蔜麥。」音五刀反\n[郭注-5]：未聞。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '2191;2514', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5327, '赤;袍薊;𦬸', '', '赤，袍薊。12{𦬸}', '[郭注-1]：卽上「枹薊」。\n[釋文-2]：枹，音包，又音浮。薊，音計。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '6587;5275/308;454', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5328, '兔奚;顆涷', '款涷', '菟奚{兔奚}，顆涷[款涷]12。3', '[音釋-1]：東。\n[釋文-2]：苦果反，或音款。謝音東，施都弄反，讀者亦音冬，郭云：「款涷也，生水中。」案本草云：「款冬，一名槖吾，一名顆涷，一名虎鬚，一名菟奚，一名氐冬。」陶注云：「其冬月在冰下生。」則應是冬，恐承音作字異耳\n[郭注-3]：款涷也。紫赤華，生水中。', '釋草弟十三', '爾雅', '2', DEFAULT, DEFAULT, '6295/6672;5645/6988', '5540/6988', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5329, '中馗;𡴆;菌', '', '中馗{𡴆}12。菌34。56小者，菌。7', '[音釋-1]：逵。\n[釋文-2]：求龜反，郭音仇，字則當作頄，舍人本作「中鳩」，云：「菟奚名顆東，顆東名中鳩」\n[音釋-3]：巨隕。\n[釋文-4]：𦽖，郭巨隕反，孫去貧反，本今作菌\n[郭注-5]：地蕈也，似蓋，今江東名爲土菌，亦曰馗廚，可啖之。\n[釋文-6]：蕈，辭荏反，案今人呼菌爲蕈，葛洪字苑同，云：「世作椹、𧀯二字，非也。」字林式甚反，或云：「桑䓴也。」沈徒感反。䓴，人兖反。\n[郭注-7]：大小異名。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '231/9731;238;477', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5330, '菆;小葉', '', '菆12，小葉。3', '[音釋-1]：輙。\n[釋文-2]：𦯍，豬葉反，又阻留反，字又作菆\n[郭注-3]：未聞。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '681;704/495', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5331, '苕;陵苕;蔈;茇', '', '苕12，陵苕。3黃華，蔈45；白華，茇67。8', '[音釋-1]：調。\n[釋文-2]：徒彫反，下同\n[郭注-3]：一名陵時，本草云。\n[音釋-4]：標。\n[釋文-5]：必遥反\n[音釋-6]：沛。\n[釋文-7]：郭音沛，補蓋反，又音撥，說文云：「草之白華爲茇。」音布末反。補舍人曰：「別華色之名也」\n[郭注-8]：苕華色異，名亦不同。音沛。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '664;9620/664;501;517', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5332, '蘪;薇;湄', '', '蘪12，從水生。3薇4，垂水。5{湄}', '[音釋-1]：眉。\n[釋文-2]：亡悲反，蘪草生江水中\n[郭注-3]：生於水中。\n[釋文-4]：音微，又音眉，顧云：「水濵生，故曰垂水」\n[郭注-5]：生於水邊。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '293;265;7267', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5333, '薜', '𣏟', '薜[𣏟]1，山麻。2', '[釋文-1]：卑麥反\n[郭注-2]：似人家麻，生山中。', '釋草弟十三', '爾雅', '1', DEFAULT, DEFAULT, '386', '4533', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5334, '莽;桃枝;粼;閔;仲無;䈚;筱', '虔;𥵸', '莽1，數節23。45桃枝，四寸有節。6粼[虔]78，堅中。9簢{閔}[𥵸]1011，筡中1213。14仲無，笐1516。17䈚1819，箭萌20。2122篠{筱}23，箭。2425', '[釋文-1]：莫朗反\n[音釋-2]：朔。\n[釋文-3]：色角反，數猶促也\n[郭注-4]：竹類也，節閒促。\n[釋文-5]：促，七玉反。\n[郭注-6]：今桃枝節閒相去多四寸。\n[音釋-7]：吝。\n[釋文-8]：字又作𦺸，音吝，又音鱗\n[郭注-9]：竹類也，其中實。\n[音釋-10]：閔。\n[釋文-11]：字或作𥴲，密謹反，又亡忍反\n[音釋-12]：徒。\n[釋文-13]：郭音徒，又音攄，施音儲\n[郭注-14]：言其中空，竹類。\n[音釋-15]：杭。\n[釋文-16]：本又作亢，戸剛反\n[郭注-17]：亦竹類，未詳。\n[音釋-18]：待。\n[釋文-19]：音待，說文云：「竹萌生也。」字林大才反\n[釋文-20]：𥳭，音箭，本今作箭。亡耕反\n[郭注-21]：萌，筍屬也。周禮曰：「䈚葅鴈醢。」\n[釋文-22]：筍，思尹反。菹，側於反。醢，音海。\n[釋文-23]：思了反，字林作筱，云：「小竹也」\n[郭注-24]：別二名。\n[釋文-25]：別，彼列反。', '釋草弟十三', '爾雅', '3;4', DEFAULT, DEFAULT, '702;3426/3571;7482;7787;4968/8416;2874;2870', '3112;2879', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5335, '枹;霍首;素華;軌鬷', '苞', '枹[苞]12，霍首3。素華4，軌鬷5。6', '[音釋-1]：包。\n[釋文-2]：音包\n[釋文-3]：戸各反\n[釋文-4]：蘇故反，又作索\n[釋文-5]：子工反，郭音揔\n[郭注-6]：皆未詳。', '釋草弟十三', '爾雅', '1', DEFAULT, DEFAULT, '3766;2364/5703;8776/3886;9584/1857', '388', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5336, '土夫;王藄', '', '芏夫{土夫}12，王藄34。56', '[音釋-1]：杜。\n[釋文-2]：郭他古反，案今南人以此草作席，呼爲芏，音杜。謝方于反，孫音苻\n[音釋-3]：其。\n[釋文-4]：𦸀，郭音其，字亦作藄，紫藄菜也。說文：「王藄，土夫也。」或作萁，非也，案說文云：「萁，豆莖。」施、謝並音其\n[郭注-5]：芏草生海邊，似莞藺，今南方越人采以爲席。\n[釋文-6]：莞，古丸反，字又作雚，誤。藺，力刃反，字林云：「莞屬也。」', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '9021/6676;78/361', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5337, '萁;月爾', '', '萁，月爾。12', '[郭注-1]：卽紫藄也，似蕨，可食。\n[釋文-2]：蕨，音厥。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '248;4275/2090', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5338, '葴;馬藍', '', '葴12，馬藍3。4', '[音釋-1]：針。\n[釋文-2]：之林反，又音咸\n[釋文-3]：力甘反\n[郭注-4]：今大葉冬藍也。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '374;6137/282', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5339, '姚莖;涂薺', '荼薺', '姚莖1，涂薺[荼薺]2。3', '[釋文-1]：謝戸耕反，施、郭於耕反\n[釋文-2]：蒤，音徒，本今作涂\n[郭注-3]：未詳。', '釋草弟十三', '爾雅', '2', DEFAULT, DEFAULT, '8117/493;7001/400', '668/400', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5340, '芐;地黃', '', '芐12，地黃。34', '[音釋-1]：戸。\n[釋文-2]：音戸，本草：「乾地黃，一名地髓，一名芐，一名芑」\n[郭注-3]：一名地髓，江東呼芐，音怙。\n[釋文-4]：髓，素累反。怙，音戸。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '405;9022/9203', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5341, '蒙;王女', '', '蒙，王女。12', '[郭注-1]：蒙卽唐也，女蘿別名。\n[釋文-2]：蓎，音唐，本今作唐。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '649;78/8111', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5342, '拔;蘢葛', '', '拔1，蘢葛2。3', '[釋文-1]：步八反\n[釋文-2]：力恭反\n[郭注-3]：似葛，蔓生，有節，江東呼爲龍尾，亦謂之虎葛，細葉，赤莖。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '7997;436/458', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5343, '藗;牡茅', '', '藗12，牡茅3。4', '[音釋-1]：速。\n[釋文-2]：蔌，本又作藗，音速\n[釋文-3]：木后反。亡交反\n[郭注-4]：白茅屬。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '418;729/326', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5344, '卷耳;苓耳', '', '菤耳{卷耳}12，苓耳3。45', '[音釋-1]：捲。\n[釋文-2]：謝作卷，九轉反。詩「卷耳」是也，本草作「枲耳」，云：「一名胡枲，一名地葵，一名葹，一名常思。」陶注云：「一名羊負來，昔中國無此物，言從外國逐羊毛中來也。」廣雅云：「苓耳、蒼耳、葹、常枲，胡枲之類耳」\n[釋文-3]：音零\n[郭注-4]：廣雅云：「枲耳也。」亦云「胡枲。」江東呼爲常枲，或曰苓耳，形似鼠耳，叢生如盤。\n[釋文-5]：枲，音死。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '5786/7794;365/7794', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5345, '蕨;鼈', '', '蕨，𧆊{鼈}12。3', '[音釋-1]：鱉。\n[釋文-2]：卑滅反，字亦作鱉，案此卽今蕨菜也，葉初出鱉蔽，因以名云。廣雅云「紫藄」，非也\n[郭注-3]：廣雅云：「紫藄」，非也。初生無葉，可食，江西謂之𧆊。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '637;9003', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5346, '喬;邛鉅', '', '蕎{喬}1，邛鉅2。34', '[釋文-1]：居喬反，又音喬\n[釋文-2]：巨恭反。音巨，案本草云：「大戟，一名邛鉅。」陶注：「今近道處處有」\n[郭注-3]：今藥草大戟也，本草云。\n[釋文-4]：戟，九逆反。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '6624;4126/9448', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5347, '𦾴;由胡', '', '繁{𦾴}1，由胡。2', '[釋文-1]：蘩，音煩，字今作繁\n[郭注-2]：未詳。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '669;8516/2678', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5348, '莣;杜榮', '', '莣12，杜榮3。45', '[音釋-1]：亡。\n[釋文-2]：音亡，字亦作芒\n[釋文-3]：徒土反，舍人作牡\n[郭注-4]：今莣草，似茅，皮可以爲繩索、履屩也。\n[釋文-5]：屩，九畧反。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '387;3433/3538', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5349, '稂;童粱', '童蓈', '稂1，童粱[童蓈]。23', '[釋文-1]：音郎，說文云：「禾粟之莠，生而不成者」\n[郭注-2]：稂，莠類也。\n[釋文-3]：莠，羊久反，詩云：「不稂不莠。」', '釋草弟十三', '爾雅', '2', DEFAULT, DEFAULT, '251;1746/4349', '1746/251', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5350, '藨;麃', '', '藨12，麃34。56', '[音釋-1]：蒲苗。\n[釋文-2]：謝蒲苗反，或力驕反，孫蒲矯反，字林工兆反，顧平表、白交、普苗三反\n[音釋-3]：平表。\n[釋文-4]：謝蒲表反，郭又苻囂反\n[郭注-5]：麃卽莓也，今江東呼爲藨莓，子似覆葐而大、赤，酢甜可啖。\n[釋文-6]：覆，芳伏反。酢，七故反。甜，大廉反。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '409;6280', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5351, '旳;覈', '', '的{旳}1，薂{覈}23。4', '[釋文-1]：菂，丁歷反，本今作的\n[音釋-2]：亦。\n[釋文-3]：字又作敫，戸歷反\n[郭注-4]：卽蓮實也。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '4201;4855', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5352, '購;商蔞', '', '購12，蔏蔞{商蔞}34。5', '[音釋-1]：古豆。\n[釋文-2]：古豆反，又古侯反\n[音釋-3]：力朱。\n[釋文-4]：音商。孫力朱反，郭力侯反，注同\n[郭注-5]：蔏蔞，蔞蒿也。生下田，初出可啖，江東用羮魚。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '3989;1453/377', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5353, '茢;勃茢', '', '茢12，勃茢3。4', '[音釋-1]：列。\n[釋文-2]：施、謝二茢皆音列，沈上音例，下音列\n[釋文-3]：蒲沒反\n[郭注-4]：一名石芸，本草云。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '428;9246/428', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5354, '葽繞;棘蒬', '', '葽繞12，蕀蒬{棘蒬}34。56', '[音釋-1]：烏了。\n[釋文-2]：烏了反\n[音釋-3]：冤。\n[釋文-4]：棘，居力反，字或作蕀，同。於袁反，又於阮反，廣雅云：「棘蒬，遠志也，其上謂之小草。」案本草：「遠志，一名棘蒬，一名葽繞，一名細草」\n[郭注-5]：今遠莣也，似麻黃，赤華，葉銳而黃。其上謂之小草，廣雅云。\n[釋文-6]：遠志，字又作薳䓌，非。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '475/8573;4399/379', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5355, '茦;刺', '莿', '茦12，刺[莿]34。56', '[音釋-1]：𠕋。\n[釋文-2]：初革反，方言云：「凡草木而刺人者，北燕、朝鮮之閒謂之茦」\n[音釋-3]：次。\n[釋文-4]：字又作㓨，又作莿，七賜反，注同，方言云：「關西呼茦、壯爲莿」\n[郭注-5]：草刺針也。關西謂之刺，燕北 朝鮮之閒曰茦，見方言。\n[釋文-6]：朝鮮，直遥反，下音仙。', '釋草弟十三', '爾雅', '2', DEFAULT, DEFAULT, '397;2806', '401', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5356, '蕭;萩', '', '蕭，萩12。3', '[音釋-1]：秋。\n[釋文-2]：音秋\n[郭注-3]：卽蒿。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '443;444', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5357, '𧂇;海薻', '', '𧂇1，海薻2。3', '[釋文-1]：徒南反\n[釋文-2]：子老反，本亦作藻，本草：「一名落首，一名𧂇。」郭云：「一名海蘿」\n[郭注-3]：藥草也。一名海蘿，如亂髮，生海中，本草云。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '341;7131/650', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5358, '長楚;銚弋', '萇楚', '長楚[萇楚]1，銚芅{銚弋}23。45', '[釋文-1]：萇，直良反，本今作長\n[音釋-2]：姚。亦。\n[釋文-3]：羊招反，或羊召反，字或作𦾺。音翼，字亦作弋\n[郭注-4]：今羊桃也，或曰鬼桃，葉似桃，華白，子如小麥，亦似桃。\n[釋文-5]：麥，亡革反。', '釋草弟十三', '爾雅', '1', DEFAULT, DEFAULT, '6069/3851;9309/8366', '307/3851', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5359, '蘦;大苦', '', '蘦1，大苦。23', '[釋文-1]：詩作苓，力丁反\n[郭注-2]：今甘草也。蔓延生，葉似荷，靑黃，莖赤有節，節有枝相當。或云：蘦似地黃。\n[釋文-3]：蔓，音萬。莚，餘見反，本今作延。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '465;6598/323', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5360, '芣苢;馬舄;車前', '', '芣苢12，馬舄3。馬舄，車前4。56', '[音釋-1]：浮。以。\n[釋文-2]：音浮。字亦作苡，音以，見詩，說文云：「芣苢，馬舄也，其實如李，令人宜子，周書所說」\n[釋文-3]：蕮，四夕反，本今作舄\n[釋文-4]：昌遮反\n[郭注-5]：今車前草，大葉，長穗，好生道邊，江東呼爲蝦蟆衣。\n[釋文-6]：穗，音遂。好，呼報反。蝦，音遐。蟆，字又作蟇，亡巴反，本草云：「車前，一名當道，一名芣苢，一名蝦蟆衣，一名牛遺，一名勝舄，久服令人身輕不老。」', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '497/340;6137/2489;9514/1069', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5361, '綸;組;帛;布', '', '綸似綸12，組似組3，東海有之。45帛似帛，布似布，華山有之。6', '[音釋-1]：古頑。\n[釋文-2]：古頑反，下同\n[釋文-3]：作古反，下同\n[郭注-4]：綸，今有秩嗇夫所帶糾靑絲綸。組，綬也。海中草生彩理有象之者，因以名云。\n[釋文-5]：秩，直乙反。嗇，音色。\n[郭注-6]：草葉有象布帛者，因以名云，生華山中。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '8654;8649;4929;4912', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5362, '亢;東蠡', '東荔', '苀{亢}12，東蠡[東荔]34。5', '[音釋-1]：亡岡。\n[釋文-2]：戸剛反，本亦作芫\n[音釋-3]：禮。\n[釋文-4]：音禮\n[郭注-5]：未詳。', '釋草弟十三', '爾雅', '2', DEFAULT, DEFAULT, '6655;3846/8967', '3846/648', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5363, '緜馬;羊齒', '', '緜馬1，羊齒。23', '[釋文-1]：武延反\n[郭注-2]：草細葉，葉羅生而毛，有似羊齒，今江東呼爲鴈齒。繅者以取繭緒。\n[釋文-3]：繅，先刀反，字林云：「繹繭也。」本亦作繰，又作縿，非也，繰宜音千小反，縿音所銜反。繭，古典反，張揖云：「繭纊，新緜。」', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '8515/6137;2333/1278', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5364, '䒷;麋舌', '', '萿{䒷}12，麋舌3。4', '[音釋-1]：古活。\n[釋文-2]：古活反\n[釋文-3]：麇，俱綸反，本或作麋，音眉\n[郭注-4]：今麋舌草，春生，葉有似於舌。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '398;6272/1444', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5365, '搴;柜朐', '', '搴12，柜朐34。5', '[音釋-1]：居展。\n[釋文-2]：施居展反，謝去虔反\n[音釋-3]：巨。劬。\n[釋文-4]：音巨。巨俱反\n[郭注-5]：未詳。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '8002;3522/2691', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5366, '𦾴;蒿', '', '蘩{𦾴}之醜，秋爲蒿。12', '[郭注-1]：醜，類也。春時各有種名，至秋老成，皆通呼爲蒿。\n[釋文-2]：種，章勇反。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '669;670', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5367, '芺;薊;荂', '', '芺12、薊3，其實荂45。67', '[音釋-1]：𪴃。\n[釋文-2]：沈、顧烏老反，謝烏兆反。說文云：「味苦，江東食以下氣」\n[釋文-3]：音計\n[音釋-4]：吁。\n[釋文-5]：香于、芳于二反，下同，注「音俘」，同\n[郭注-6]：芺與薊莖頭皆有蓊臺名荂，荂卽其實，音俘。\n[釋文-7]：蓊，烏孔反。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '353;308;3884', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5368, '蔈;荂;荼;猋;藨;芀', '', '蔈12、荂，荼3。4猋56、藨78，芀910。11', '[音釋-1]：方腰。\n[釋文-2]：芳𦝫反，又必招反\n[釋文-3]：郭音徒，又音蛇\n[郭注-4]：卽芀。\n[音釋-5]：必遥。\n[釋文-6]：必遥反，又方瓢反，又方幺反，字從三犬，俗從三火，非也。焱，字林弋劍反，云：「火花也」\n[音釋-7]：方驕。\n[釋文-8]：郭方驕反，謝苻苗反，一音皮兆反\n[音釋-9]：調。\n[釋文-10]：芀，徒彫反，字或作苕，下同\n[郭注-11]：皆芀、荼之別名。方俗異語，所未聞。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '501;3884;668;6385;409;427', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5369, '葦;芀;葭;華', '', '葦醜1，芀。2葭3，華。4', '[釋文-1]：于鬼反，謝于歸反\n[郭注-2]：其類皆有芀秀。\n[釋文-3]：音加\n[郭注-4]：卽今蘆也。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '645;427;646;3886', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5370, '蒹;薕;葭;蘆;菼;薍;萌;灌渝', '𦿏', '蒹12，薕34。56葭，蘆。7菼89，薍1011。1213其萌[𦿏]，虇蕍{灌渝}1415。1617', '[音釋-1]：兼。\n[釋文-2]：古謙反\n[音釋-3]：廉。\n[釋文-4]：力占反\n[郭注-5]：似萑而細，高數尺，江東呼爲蒹藡，音廉。\n[釋文-6]：本今作𦻃，音桓，字林作雚。數，所主反。藡，徒的反。\n[郭注-7]：葦也。\n[音釋-8]：他敢。\n[釋文-9]：他敢反，說文作𦵹，云：「菼，或𦵹字。」補樊光曰：「初生葸，騂色，海濱曰薍」\n[音釋-10]：五患。\n[釋文-11]：五患反。補舍人曰：「蘆、薍一草」\n[郭注-12]：似葦而小，實中，江東呼爲烏蓲，音丘。\n[釋文-13]：蓲，郭音丘，說文云：「烏蓲，草也。」張揖云：「未秀曰烏蓲。」\n[音釋-14]：丘阮。俞。\n[釋文-15]：郭音綣，丘阮反，本或作𢑆，非。𢑆，音權，說文云：「弓曲也。」羊朱反\n[郭注-16]：今江東呼蘆笋爲虇，然則萑葦之類，其初生者皆名虇，音繾綣。\n[釋文-17]：繾，弃善反，或去忍反。', '釋草弟十三', '爾雅', '7', DEFAULT, DEFAULT, '420;423;646;277;422;421;491;7044/7441', '363', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5371, '芛;葟;華;榮', '', '芛12、葟34、華，榮。5', '[音釋-1]：羊捶。\n[釋文-2]：郭音䝐，羊捶反，顧羊述反，謝私尹反，樊本作葦\n[音釋-3]：皇。\n[釋文-4]：音皇，本亦作皇\n[郭注-5]：釋言云：「華，皇也。」今俗呼草木華初生者爲芛，音䝐豬。蕍猶敷蕍，亦華之貌，所未聞。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '499;3383;3886;3538', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5372, '卷施草', '', '卷施草1，拔心不死。2', '[釋文-1]：並如字，施或作葹，同\n[郭注-2]：宿莽也，離騷云。', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '5786/4303/680', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5373, '荺;茭;荄;根', '茇', '荺12、茭[茇]34、56荄7，根。89', '[音釋-1]：于閔。\n[釋文-2]：于閔反\n[音釋-3]：胡巧。\n[釋文-4]：字又作䕧，胡巧反，又胡交反，廣雅云：「根也」\n[郭注-5]：今江東呼藕紹緒如指，空中可啖者爲茭，茭卽此類。\n[釋文-6]：藕，五口反。啖，大敢反。\n[釋文-7]：顧、謝音該，郭音皆，說文云：「草根也」\n[郭注-8]：別二名。俗呼韭根爲荄。\n[釋文-9]：別，彼列反。韭，音久。', '釋草弟十三', '爾雅', '2', DEFAULT, DEFAULT, '516;612;515;3564', '517', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5374, '檴;𣐺;含;華;荂;華;荂;榮', '𢎘', '檴、𣐺、含[𢎘]、1華，荂也2。3華、荂，榮也。4', '[郭注-1]：未詳。\n[釋文-2]：香于、芳于二反，下同，說文云：「草木華也」\n[郭注-3]：今江東呼華爲荂，音敷。\n[郭注-4]：轉相解。', '釋草弟十三', '爾雅', '3', DEFAULT, DEFAULT, '3507;4390;815;3886;3884;3886;3884;3538', '4343', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5375, '華;榮;秀;英', '', '木謂之華，草謂之榮，不榮而實者謂之秀1，榮而不實者謂之英。', '[釋文-1]：衆家並無「不」字，郭雖不注，而音義引不榮之物證之，則郭本有「不」字', '釋草弟十三', '爾雅', '', DEFAULT, DEFAULT, '3886;3538;4417;502', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5376, '舀;山夏', '楰;山檟', '槄{舀}[楰]12，山榎{山夏}[山檟]34。56', '[音釋-1]：叨。\n[釋文-2]：他刀反，郭又他皓反。補孫炎曰：「詩云『有條有梅』」\n[音釋-3]：賈。\n[釋文-4]：古雅反，舍人本又作檟\n[郭注-5]：今之山楸。\n[釋文-6]：楸，音秋。', '釋木弟十四', '爾雅', '1;2', DEFAULT, DEFAULT, '4527;5850/3373', '3553;5850/3462', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5377, '考;山樗', '𣐊', '栲{考}[𣐊]1，山樗23。45', '[釋文-1]：音考，郭姑老反，案方志云：「櫄、樗、栲、漆，相似如一。」櫄，音勑倫反\n[音釋-2]：丑於。\n[釋文-3]：丑於反\n[郭注-4]：栲似樗，色小白，生山中，因名云。亦類漆樹。\n[釋文-5]：桼，字又作漆，音七。', '釋木弟十四', '爾雅', '1', DEFAULT, DEFAULT, '5385;5850/3455', '3469', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5378, '柏;鞠', '', '柏1，椈{鞠}23。45', '[釋文-1]：音百\n[音釋-2]：菊。\n[釋文-3]：弓六反\n[郭注-4]：禮記曰：「鬯曰以椈。」\n[釋文-5]：鬯臼，勑亮反，下其九反。', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '3549;1810', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5379, '髡;梱', '梡;㮯/𩓽', '髡[梡]12，梱[㮯/𩓽]34。5', '[音釋-1]：坤。\n[釋文-2]：苦門反\n[音釋-3]：五門。\n[釋文-4]：五門反\n[郭注-5]：未詳。', '釋木弟十四', '爾雅', '1;2', DEFAULT, DEFAULT, '5762;3663', '3812;3813/0/5672', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5380, '段;杝', '', '椴{段}12，柂{杝}34。5', '[音釋-1]：段。\n[釋文-2]：徒亂反，字林云：「木似白楊，一名柂」\n[音釋-3]：夷。\n[釋文-4]：弋支反\n[郭注-5]：白椴也，樹似白楊。', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '1972;3671', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5381, '梅;柟', '', '梅1，柟23。45', '[釋文-1]：莫迴反。補樊光曰：「荊州曰梅，楊州曰柟，益州曰赤楩，葉似豫章，無子」\n[音釋-2]：而占。\n[釋文-3]：而占反，又音南\n[郭注-4]：似杏，實酢。\n[釋文-5]：杏，戸猛反。酢，七故反。', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '3422;3421', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5382, '柀;煔', '', '柀12，煔34。56', '[音釋-1]：彼。\n[釋文-2]：音彼，又匹彼反\n[音釋-3]：衫。\n[釋文-4]：字或作杉，所咸反，郭音芟，又音纎\n[郭注-5]：煔似松，生江南，可以爲船及棺材，作柱，埋之不腐。\n[釋文-6]：棺，音官。腐，音父。', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '3466;6538', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5383, '橃;椵', '', '櫠{橃}12，椵34。56', '[音釋-1]：廢。\n[釋文-2]：音廢\n[音釋-3]：賈。\n[釋文-4]：古雅反\n[郭注-5]：柚屬也，子大如盂，皮厚二三寸，中似枳，食之少味。\n[釋文-6]：柚，羊又反。盂，音于。厚，戸豆反，又如字。枳，諸氏反。', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '3789;3487', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5384, '丑;檍;杶', '輮;𣚍', '杻{丑}[輮]12，檍[𣚍]34。56{杶}', '[音釋-1]：女九。\n[釋文-2]：女九反，呂、郭並汝九反\n[音釋-3]：億。\n[釋文-4]：於力反，字又作億，說文云：「檍，梓屬也」\n[郭注-5]：似棣，細葉，葉新生可飼牛。材中車𨊾。關西呼杻子，一名土橿。\n[釋文-6]：棣，大細反。飤，囚志反，字林云：「糧也，一曰𩚳也。」經典並止作食字，借作嗣音。本又作飯，扶晚反，本今作飼。車，昌蛇反。音罔，下同，字林云：「𨎶也。」𨎶，音渠。橿，居良反。', '釋木弟十四', '爾雅', '1;2', DEFAULT, DEFAULT, '9790;3453;3470', '9547;3465', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5385, '楙;木瓜', '', '楙12，木瓜。3', '[音釋-1]：茂。\n[釋文-2]：音茂\n[郭注-3]：實如小瓜，酢可食。', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '3853;3413/4547', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5386, '椋;卽來', '', '椋12，卽來3。4', '[音釋-1]：良。\n[釋文-2]：音良\n[釋文-3]：棶，力臺反，埤蒼、字林並作棶，云：「椋也。」本今作來\n[郭注-4]：今椋，材中車𨊾。', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '3452;3198/3348', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5387, '栵;栭', '', '栵12，栭34。56', '[音釋-1]：例。\n[釋文-2]：字林音例，詩音同，又音列\n[音釋-3]：而。\n[釋文-4]：檽，字又作栭，音而，毛詩草木䟽云：「葉如榆，木理堅韌而赤，今人謂之芝檽也。」補舍人曰：「江 淮之閒呼小栗爲栭栗」\n[郭注-5]：樹似檞樕而庳小，子如細栗，可食。今江東亦呼爲栭栗。\n[釋文-6]：𣘳，胡木反，本今作槲。樕，桑木反，詩云：「林有樸樕。」毛傳云：「小木也。」或作㯈，同。庳，音婢。', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '3636;3637', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5388, '檴;落', '', '檴12，落。34', '[音釋-1]：鑊。\n[釋文-2]：戸郭反，詩云：「無浸檴薪」\n[郭注-3]：可以爲杯器素。\n[釋文-4]：桮，必回反，本今作杯。', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '3507;549', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5389, '柚;條', '', '柚1，條2。34', '[釋文-1]：羊又反，或作櫾\n[釋文-2]：字又作樤\n[郭注-3]：似橙，實酢，生江南。\n[釋文-4]：橙，直耕反，上林賦云：「黃甘橙楱」是也，楱，音直奏反，呂忱云：「橙，橘屬。」', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '3416;3572', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5390, '時;英梅', '柍梅', '時，英梅[柍梅]。1', '[郭注-1]：雀梅。', '釋木弟十四', '爾雅', '2', DEFAULT, DEFAULT, '4191;502/3422', '3441/3422', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5391, '楥;柜桏', '', '楥12，柜桏34。5', '[音釋-1]：袁。\n[釋文-2]：孫音袁，又于眷反\n[音釋-3]：邛。\n[釋文-4]：謝音巨，郭音舉。郭音邛，又作柳，良久反\n[郭注-5]：未詳。或曰：桏當爲柳，柜桏似柳，皮可以煑作飲。', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '3729;3522/3438', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5392, '栩;杼', '𣏗', '栩12，杼[𣏗]34。56', '[音釋-1]：香羽。\n[釋文-2]：香羽反，郭又音羽，字林丑與反\n[音釋-3]：甞汝。\n[釋文-4]：謝嘗汝反，施音佇，孫昌汝反，施或音序\n[郭注-5]：柞樹。\n[釋文-6]：柞，子各反。', '釋木弟十四', '爾雅', '2', DEFAULT, DEFAULT, '3477;3727', '3478', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5393, '味;荎著', '菋;荎藸', '味[菋]1，荎著[荎藸]23。45', '[釋文-1]：菋，音味，又亡戒反，本今作味\n[音釋-2]：直之。儲。\n[釋文-3]：直之反，舍人本作柢，丁計反。音儲，舍人本作都，樊本作屠\n[郭注-4]：釋草已有此名，疑誤重出。\n[釋文-5]：重，直用反，又直龍反。', '釋木弟十四', '爾雅', '1;2', DEFAULT, DEFAULT, '817;457/2929', '456;457/349', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5394, '蓲;荎;梗', '', '藲{蓲}12，荎34。5{梗}', '[音釋-1]：歐。\n[釋文-2]：烏侯反，詩云：「山有藲」是也，本或作蓲，同\n[音釋-3]：大結。\n[釋文-4]：謝大結反，郭直基反\n[郭注-5]：今之刺榆。', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '343;457;3543', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5395, '杜;甘棠', '', '杜，甘棠。1', '[郭注-1]：今之杜梨。', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '3433;3038/3432', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5396, '狄;臧橰;貢綦', '臧𣓌', '狄，臧橰[臧𣓌]12。貢綦34。5', '[音釋-1]：皐。\n[釋文-2]：孫子郎反。舍人本作皐，樊本作𣓌，同，音羔\n[音釋-3]：其。\n[釋文-4]：音其\n[郭注-5]：皆未詳。', '釋木弟十四', '爾雅', '2', DEFAULT, DEFAULT, '6372;1955/3842;3948/8629', '1955/3443', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5397, '朹;檕梅', '', '朹12，檕梅34。5', '[音釋-1]：求。\n[釋文-2]：音求\n[音釋-3]：計。\n[釋文-4]：工系反，本亦作繫，樊本作槅，工厄反\n[郭注-5]：朹樹狀似梅，子如指頭，赤色，似小㮈，可食。', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '2940;3723/3422', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5398, '朻;聊;莍', '', '朻者12，聊3。4{莍}', '[音釋-1]：糾。\n[釋文-2]：郭音糾，又居幽反，又音皎\n[釋文-3]：音寮\n[郭注-4]：未詳。', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '3591;7802;487', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5399, '魄;徯醯', '', '魄，榽橀{徯醯}12。34', '[音釋-1]：兮計。許兮。\n[釋文-2]：兮計反，又音奚。許兮反，本亦作醯\n[郭注-3]：魄，大木，細葉，似檀，今江東多有之。齊人諺曰：「上山斫檀，榽橀先彈。」\n[釋文-4]：檀，大丹反。上，時掌反。斫，章畧反。殫，音丹，字林云：「極盡也。」又云：「斃也。」', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '5824;1244/3153', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5400, '梫;木桂', '', '梫12，木桂。34', '[音釋-1]：寢。\n[釋文-2]：字林音寢，郭音浸，或初林反，一音侵\n[郭注-3]：今南人呼桂厚皮者爲木桂。桂樹葉似枇杷而大，白華，華而不著子，叢生巖嶺，枝葉冬夏常靑，閒無雜木。\n[釋文-4]：枇，房私反，又音毗。杷，步巴反。著，丁畧反，又直畧反。', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '3430;3413/3431', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5401, '棆;無疵', '母疵', '棆12，無疵[母疵]3。45', '[音釋-1]：倫。\n[釋文-2]：音倫，又致均反\n[釋文-3]：本又作𣓄，辭貲反，字書云：「無𣓄，棆也」\n[郭注-4]：棆，楩屬，似豫章。\n[釋文-5]：楩，鼻緜反，又婢衍反，上林賦云：「楩柟豫章」是也，字指云：「楩木似豫章。」章，本或作樟。', '釋木弟十四', '爾雅', '2', DEFAULT, DEFAULT, '3439;8416/4710', '8141/4710', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5402, '椐;樻', '', '椐12，樻34。56', '[音釋-1]：祛。\n[釋文-2]：音祛，字林己庶反，又音舉\n[音釋-3]：起愧。\n[釋文-4]：起愧反，又去軌反，字林巨位反，樊、孫並云：「椐、樻，腫節可作杖。」毛詩草木䟽云：「節中腫，似扶老，卽今靈壽是也，今人以爲馬鞭及杖，弘農 共北山皆有之」\n[郭注-5]：腫節可以爲杖。\n[釋文-6]：腫，之勇反。', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '3475;3476', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5403, '檉;河柳;旄;澤柳;楊;蒲柳', '', '檉1，河柳。2旄3，澤柳。4楊，蒲柳。5', '[釋文-1]：勑貞反\n[郭注-2]：今河旁赤莖小楊。\n[釋文-3]：音毛\n[郭注-4]：生澤中者。\n[郭注-5]：可以爲箭。左傳所謂「董澤之蒲」。', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '3513;6986/3514;4309;7222/3514;3512;332/3514', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5404, '權;黃英;輔;小木', '', '權1，黃英。輔，小木。2', '[釋文-1]：音拳\n[郭注-2]：權、輔皆未詳。', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '3521;9203/502;9567;704/3413', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5405, '杜;棠', '', '杜1，赤棠；白者，棠。2', '[釋文-1]：補舍人曰：「杜赤色名赤棠，白者亦名棠」\n[郭注-2]：棠色異，異其名。', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '3433;3432', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5406, '諸慮;山蘽;𣠞;虎蘽', '', '諸慮1，山櫐{山蘽}23。45欇{𣠞}67，虎櫐{虎蘽}8。910', '[釋文-1]：如字，施力據反，字又作櫖，力余反\n[音釋-2]：壘。\n[釋文-3]：字或作蘽，音誄，力水反，下同\n[郭注-4]：今江東呼櫐爲藤，似葛而麤大。\n[釋文-5]：藤，徒登反。麆，七胡反，本今作麤。\n[音釋-6]：涉。\n[釋文-7]：郭音涉，本又作聶，又作𣠞，並同\n[釋文-8]：呼戸反\n[郭注-9]：今虎豆，纏蔓林樹而生，莢有毛刺，今江東呼爲㯿欇，音涉。\n[釋文-10]：莢，古協反，下同，字林云：「草實也。」廣雅云：「豆角謂之莢。」周禮云：「墳衍之地，其植物宜莢物。」刺，七豉反。㯿，力輒反，又餘涉反。', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '1488/6704;5850/3457;3576;3118/3457', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5407, '杞;枸檵', '', '杞1，枸檵23。4', '[釋文-1]：去己反\n[音釋-2]：計。\n[釋文-3]：音苟。音繼\n[郭注-4]：今枸杞也。', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '3527;3503/3526', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5408, '芫;魚毒', '', '杬{芫}1，魚毒。23', '[釋文-1]：音元，又作芫\n[郭注-2]：杬，大木，子似栗，生南方，皮厚，汁赤，中藏卵果。\n[釋文-3]：卵，力管反。', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '464;7580/236', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5409, '毀;大茮', '', '檓{毀}1，大椒{大茮}2。3', '[釋文-1]：況彼反\n[釋文-2]：字又作茮，子消反\n[郭注-3]：今椒樹，叢生，實大者名爲檓。', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '9118;6598/486', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5410, '楰;鼠梓', '', '楰12，鼠梓3。4', '[音釋-1]：庾。\n[釋文-2]：郭音庾，又音瑜\n[釋文-3]：音子\n[郭注-4]：楸屬也，今江東有虎梓。', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '3553;6393/3463', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5411, '楓;欇欇', '', '楓1，欇欇23。45', '[釋文-1]：甫隆反，說文云：「木厚葉弱枝，善摇。」字林音方廉反。補舍人曰：「楓爲樹，厚葉弱莖，大風則鳴，故曰欇欇。」孫炎曰：「欇欇，生江上，有寄生枝，高三四丈，生毛，一名楓子，天旱以泥塗之，卽雨」\n[音釋-2]：輙。\n[釋文-3]：之涉反\n[郭注-4]：楓樹似白楊，葉貟而岐，有脂而香，今之楓香是。\n[釋文-5]：岐，音祁。脂，音之。', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '3520;3576/3576', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5412, '寓木;宛童', '', '寓木12，宛童3。45', '[音釋-1]：魚具。\n[釋文-2]：魚具反\n[釋文-3]：於院反\n[郭注-4]：寄生樹，一名蔦。\n[釋文-5]：蔦，音鳥，或音弔，說文或作樢，字林云：「寄生也。」', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '4612/3413;4609/1746', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5413, '无姑;夷;梗', '', '无姑，其實夷1。2{梗}', '[釋文-1]：舍人本作桋，同\n[郭注-2]：无姑，姑榆也，生山中，葉貟而厚，剥取皮合漬之，其味辛香，所謂无夷。', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '8416/8146;6615;3543', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5414, '櫟;梂', '', '櫟1，其實梂23。45', '[釋文-1]：力的反\n[音釋-2]：求。\n[釋文-3]：字林音求，舍人云：「櫟實名梂也。」孫云：「櫟實，橡也，有梂彙自裹。」郭同。補其掬反，一音釘鋦\n[郭注-4]：有梂彙自裹。\n[釋文-5]：彙，音謂。裹，音果。', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '3530;3531', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5415, '𣔾;羅', '', '檖{𣔾}12，羅。3', '[音釋-1]：遂。\n[釋文-2]：音遂\n[郭注-3]：今楊檖也。實似梨而小，酢可食。', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '3486;4833', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5416, '楔;荆桃', '', '楔12，荆桃。34', '[音釋-1]：戛。\n[釋文-2]：古黠反，施音結\n[郭注-3]：今櫻桃。\n[釋文-4]：櫻，乙耕反，廣雅云：「櫻桃，含桃也。」禮記月令云：「羞以含桃。」鄭注云：「今之櫻桃。」', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '3669;488/3426', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5417, '旄;冬桃;榹桃;山桃', '㮘', '旄[㮘]1，冬桃。2榹桃34，山桃。56', '[釋文-1]：音毛。字林作楙，亡到反\n[郭注-2]：子冬熟。\n[音釋-3]：斯。\n[釋文-4]：郭音斯，又音雌\n[郭注-5]：實如桃而小，不解核。\n[釋文-6]：解，古買反。核，胡革反，下同。', '釋木弟十四', '爾雅', '1', DEFAULT, DEFAULT, '4309;7516/3426;3707/3426;5850/3426', '3427', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5418, '休;痤;椄慮;駁', '', '休1，无實李。2痤3，椄慮李4。5駁6，赤李。7', '[釋文-1]：虛求反，又作𣐾\n[郭注-2]：一名趙李。\n[釋文-3]：徂禾反\n[釋文-4]：如字，施音驢\n[郭注-5]：今之麥李。\n[釋文-6]：字亦作駮，邦角反\n[郭注-7]：子赤。', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '3818;4738;3757/6704;6161', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5419, '棗;壷棗;邊;要棗;櫅;白棗;樲;酸棗;楊徹;齊棗;遵;羊棗;洗;大棗;煑;塡棗;蹶洩;苦棗;晳;還味;稔棗', '越洩;𣟳味', '棗1，壷棗2。34邊，要棗56。7櫅8，白棗。9樲1011，酸棗。12楊徹13，齊棗。14遵，羊棗。1516洗1718，大棗。1920煑，塡棗2122。23蹶洩[越洩]2425，苦棗。26晳，無實棗。2728還味[𣟳味]2930，棯棗{稔棗}3132。33', '[釋文-1]：音早\n[釋文-2]：字或作㯛，孫云：「棗形，上小下大，似瓠，故曰壺，音胡」\n[郭注-3]：今江東呼棗大而銳上者爲壷，壷猶瓠也。\n[釋文-4]：瓠，乎故反。\n[音釋-5]：腰。\n[釋文-6]：一遥反，注同，字或作楆\n[郭注-7]：子細𦝫，今謂之鹿盧棗。\n[釋文-8]：子兮反\n[郭注-9]：卽今棗，子白熟。\n[音釋-10]：二。\n[釋文-11]：而至反\n[郭注-12]：樹小，實酢。孟子曰：「養其樲棗。」\n[釋文-13]：本或作㯙，直列反\n[郭注-14]：未詳。\n[郭注-15]：實小而貟，紫黑色，今俗呼之爲羊矢棗。孟子曰：「曾晢嗜羊棗。」\n[釋文-16]：晳，思歷反，下同。嗜，時至反。\n[音釋-17]：屑典。\n[釋文-18]：屑典反\n[郭注-19]：今河東 猗氏縣出大棗，子如雞卵。\n[釋文-20]：猗，乙奇反，又於寄反。\n[音釋-21]：田。\n[釋文-22]：音田，本或作㒹，同\n[郭注-23]：未詳。\n[音釋-24]：屑。\n[釋文-25]：本亦作𨇙，居衞反。泄，息列反\n[郭注-26]：子味苦。\n[郭注-27]：不著子者。\n[釋文-28]：著，丁畧反，下同。\n[音釋-29]：旋。\n[釋文-30]：音旋，郭云：「還味，短味。」字林作𣟳，一縣反\n[音釋-31]：稔。\n[釋文-32]：而審反，又作菍，同\n[郭注-33]：還味短苦。', '釋木弟十四', '爾雅', '17;20', DEFAULT, DEFAULT, '4398;6642/4398;1209;1785/4398;3490;4931/4398;3493;9859/4398;3512/1997;4395/4398;1108;2333/4398;7415;6598/4398;1878;9071/4398;1369/7065;323/4398;4934;1142/817;4486/4398', '990/7065;3536/817', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5420, '櫬;梧', '', '櫬1，梧2。3', '[釋文-1]：初靳反，下同\n[釋文-2]：音吳\n[郭注-3]：今梧桐。', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '3830;3537', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5421, '樸;枹;謂;柞;櫬;采薪;卽薪', '㯷;彙', '樸[㯷]12。枹者3，45謂[彙]6。{柞}櫬，采薪7；采薪，卽薪8。910', '[音釋-1]：卜。\n[釋文-2]：音卜，字又作僕\n[釋文-3]：逋勞反，注同\n[郭注-4]：樸屬，叢生者爲枹，詩所謂「棫樸」「枹櫟」。\n[釋文-5]：屬，之欲反。棫，于逼反。櫟，音歷。\n[釋文-6]：謂，舍人本作彙，同\n[釋文-7]：七在反，下並同\n[釋文-8]：舍人引上句「櫬，梧」來合在此句，以「謂」字作「彙」，釋云：「櫬，梧。樸；枹者，彙——櫬者，其理也。樸者，相迫附也。如竹箭曰枹也；彙者，莖也。櫬名采薪，又名卽薪。」樊引詩云：「『薪是穫薪』，荆州 柞木曰采木，詩人不曉『薪』意，言『薪』謂身卽『薪』伐之也。」李云：「采薪，一名彙櫬，言『卽薪』，謂二薪也。」孫引詩云：「『薪是穫薪』，薪一名彙櫬。」郭云：「指解今樵薪。」今依郭氏說\n[郭注-9]：指解今樵薪。\n[釋文-10]：樵，字又作藮，徂焦反，字林云：「薪也。」案左傳云：「不樵樹。」史記云：「樵蘇後爨，師不宿飽。」注云：「樵取薪，蘇取草。」', '釋木弟十四', '爾雅', '1;3', DEFAULT, DEFAULT, '3607;3766;1479;3483;3830;3794/622;3198/622', '3494;6103', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5422, '棪;速其', '', '棪12，㯈其{速其}34。5', '[音釋-1]：琰。\n[釋文-2]：餘念反，山海經：「堂庭之山多棪木。」注云：「子似㮏而赤，可食」\n[音釋-3]：速。\n[釋文-4]：音速\n[郭注-5]：棪實似柰，赤，可食。', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '3450;1121/3019', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5423, '劉;劉杙', '', '劉，劉杙1。23', '[釋文-1]：音弋\n[郭注-2]：劉子生山中，實如梨，酢甜，核堅。出交趾。\n[釋文-3]：阯，音止，本今作趾。', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '9446;9446/3480', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5424, '褱;槐;守宮槐;聶;炕', '㘝;抗', '櫰{褱}12，槐大葉而黑34。5守宮槐，葉晝聶[㘝]宵炕[抗]67。8', '[音釋-1]：苦回。\n[釋文-2]：郭古回反，字林下罪反，又音懷\n[音釋-3]：懷。\n[釋文-4]：音懷，或音回\n[郭注-5]：槐樹葉大色黑者名爲櫰。\n[音釋-6]：輙。呼郎。\n[釋文-7]：之涉反，合也。郭呼郎反，又口浪反，顧云：「張也。」樊本作抗\n[郭注-8]：槐葉晝日聶合而夜炕布者，名爲守宮槐。', '釋木弟十四', '爾雅', '4;5', DEFAULT, DEFAULT, '5291;3523;4596/4630/3523;7825;6518', '3925;8068', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5425, '槐;夏;昔;楸', '檟;𧢼', '槐1，小葉曰榎{夏}[檟]2。3大而皵{昔}[𧢼]4，楸5。6小而皵，榎。7', '[釋文-1]：郭讀槐爲楸，音秋，或如字\n[釋文-2]：古雅反，及下同\n[郭注-3]：槐，當爲楸。楸細葉者爲榎。\n[釋文-4]：𣛵，孫七各、七路二反，字或作（⿰木𥀟），下同。樊云：「大者，老也；𣛵，棤皮也。謂麤𣛵而老者爲楸也。」孫、郭云：「老乃皮麆皵者爲楸。」本今作皵\n[釋文-5]：音秋\n[郭注-6]：老乃皮麤皵者爲楸。\n[郭注-7]：小而皮麤皵者爲榎。左傳曰：「使擇美榎。」', '釋木弟十四', '爾雅', '2;3', DEFAULT, DEFAULT, '3523;3373;4252;3464', '3462;2838', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5426, '椅;梓', '', '椅12，梓3。4', '[音釋-1]：於寄。\n[釋文-2]：於奇反，郭云：「卽楸也。」案：椅與楸唯子爲異耳\n[釋文-3]：字林音子\n[郭注-4]：卽楸。', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '3461;3463', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5427, '桋;栜', '', '桋1，赤栜23。白者，栜。45', '[釋文-1]：音夷\n[音釋-2]：山厄。\n[釋文-3]：又作㮦，同，山厄反，郭霜狄反，下同\n[郭注-4]：赤栜樹，葉細而岥銳，皮理錯戾，好叢生山中，中爲車𨊾。白栜葉貟而岥，爲大木。\n[釋文-5]：岐，巨伊反，下同。好，呼報反。輞，音罔，本或作軔，音刃，又音如戰反，本今作𨊾。', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '3458;3845', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5428, '終;牛棘', '眾', '終[眾]，牛棘。1', '[郭注-1]：卽馬棘也，其刺麤而長。', '釋木弟十四', '爾雅', '1', DEFAULT, DEFAULT, '8590;728/4399', '5237', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5429, '灌木;叢木', '𦼉木', '灌木[𦼉木]1，叢木2。3', '[釋文-1]：樌，古亂反，字又作灌，音同\n[釋文-2]：才公反，本或作樷\n[郭注-3]：詩曰：「集於灌木。」', '釋木弟十四', '爾雅', '1', DEFAULT, DEFAULT, '7044/3413;1750/3413', '644/3413', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5430, '瘣木;附婁', '', '瘣木12，苻婁{附婁}3。45', '[音釋-1]：胡罪。\n[釋文-2]：郭胡罪反，說文云：「病也。一曰腫旁出也。」又音回\n[釋文-3]：謝力侯反，施力俱反，樊引詩云：「譬彼瘣木，疾用無枝。苻婁者，尫傴內病，磈磊無枝也。」李云：「苻婁，一名瘣木，無枝木也」\n[郭注-4]：謂木病尫傴癭腫，無枝條。\n[釋文-5]：尫，烏皇反，字書云：「傴也。」傴，紆禹反，廣雅云：「曲也。」癭，於丼反，字林云：「頸瘤也。」腫，章勇反，本或作瘇，常勇反。', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '4700/3413;9662/8323', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5431, '蕡;藹', '䔽', '蕡1，藹[䔽]2。34', '[釋文-1]：扶云反，孫苻粉反\n[釋文-2]：烏害反\n[郭注-3]：樹實繁茂菴藹。\n[釋文-4]：菴，於檢反。', '釋木弟十四', '爾雅', '2', DEFAULT, DEFAULT, '575;1533', '588', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5432, '枹;遒木', '', '枹1，遒木23。', '[釋文-1]：音包\n[音釋-2]：徂由。\n[釋文-3]：又作逎，子由反，又徂由反，說文云：「遒，迫也。」謂叢攅迫而生', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '3766;1179/3413', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5433, '魁;瘣', '', '魁1，瘣2。34', '[釋文-1]：字亦作（⿺鬼升），謝苦罪反，施苦回反\n[釋文-2]：郭盧罪反，施胡罪反\n[郭注-3]：謂樹木叢生，根枝節目盤結磈磊。\n[釋文-4]：磈，本或作傀，苦罪反。磊，本或作儡，力罪反。', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '9497;4700', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5434, '棫;白桵', '', '棫12，白桵34。56', '[音釋-1]：域。\n[釋文-2]：音域，字林于狊反\n[音釋-3]：人隹。\n[釋文-4]：本或作㮃，字林人隹反\n[郭注-5]：桵，小木，叢生，有刺，實如耳璫，紫赤，可啖。\n[釋文-6]：刺，七智反。', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '3473;4931/3472', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5435, '棃;離', '', '棃1，山樆{離}23。4', '[釋文-1]：字亦作梨\n[音釋-2]：离。\n[釋文-3]：音離，本亦作離，非\n[郭注-4]：卽今棃樹。', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '3418;2294', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5436, '梔;女桑;桋桑', '稺桑', '桑辨有葚12，梔3，4女桑，桋桑[稺桑]5。6', '[音釋-1]：片。\n[釋文-2]：㸤，普遍反，又皮莧反。音甚，說文云：「桑實也。」本或作椹，非，字林式忍反\n[釋文-3]：章移反，下注同，舍人云：「桑樹一半有葚、半無葚，名梔也。」樊本同\n[郭注-4]：辨，半也。\n[釋文-5]：大兮反，或音夷\n[郭注-6]：今俗呼桑樹小而條長者爲女桑樹。', '釋木弟十四', '爾雅', '3', DEFAULT, DEFAULT, '3554;8111/3860;3458/3860', '4424/3860', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5437, '榆白;枌', '', '榆白1，枌23。45', '[釋文-1]：以朱反。補孫炎曰：「榆白者，名枌」\n[音釋-2]：墳。\n[釋文-3]：符云反\n[郭注-4]：枌榆先生葉，卻著莢，皮色白。\n[釋文-5]：卻，去畧反。著，丁畧反。莢，古叶反，字林云：「草實也。」', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '3541/4931;3542', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5438, '唐棣;栘;常棣;棣;唐逮;移', '棠棣', '唐棣[棠棣]1，栘23。45常棣，棣。6{唐逮}{移}', '[釋文-1]：大計反，下同，字林大內反\n[音釋-2]：移。\n[釋文-3]：以支反，字林上泥反\n[郭注-4]：似白楊，江東呼夫栘。\n[釋文-5]：夫，音苻。\n[郭注-6]：今山中有棣樹，子如櫻桃，可食。', '釋木弟十四', '爾雅', '1', DEFAULT, DEFAULT, '878/3518;3517;4872/3518;3518;878/1147;4447', '3432/3518', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5439, '檟;苦荼', '', '檟1，苦荼2。34', '[釋文-1]：古雅反，與榎同\n[釋文-2]：音徒，下同，埤蒼作𣘻，案今蜀人以作飲，音直加反，茗之類\n[郭注-3]：樹小，似梔子，冬生，葉可煑作羹飲。今呼早采者爲荼，晚取者爲茗。一名荈，蜀人名之苦荼。\n[釋文-4]：茗，亡頂反。荈，尺兖反。荈、𣘻、茗，其實一也，張揖雜字云：「茗之別名也。」', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '3462;323/668', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5440, '樕樸;心', '', '樕樸1，心。23', '[釋文-1]：音速。㯷，音卜，本今作樸。補某氏曰：「樸樕，槲樕也，有心能溼，江河閒以作柱」\n[郭注-2]：槲樕別名。\n[釋文-3]：槲，音斛。', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '3445/3607;6705', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5441, '榮;桐木', '', '榮，桐木。1', '[郭注-1]：卽梧桐。', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '3538;3539/3413', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5442, '棧木;干木', '殘木;櫱木', '棧木[殘木]1，干木[櫱木]2。34', '[釋文-1]：仕板反\n[釋文-2]：古丹反，樊本作杆，同\n[郭注-3]：橿木也。江東呼木觡。\n[釋文-4]：殭，居良反，字書云：「死而不朽。」本或作僵，說文云：「僵，偃也。」又作橿。觡，加客反。', '釋木弟十四', '爾雅', '1;2', DEFAULT, DEFAULT, '3732/3413;1441/3413', '2551/3413;3804/3413', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5443, '檿桑;山桑', '', '檿桑12，山桑。34', '[音釋-1]：烏㸃。\n[釋文-2]：烏簟反\n[郭注-3]：似桑，材中作弓及車轅。\n[釋文-4]：車，昌蛇反。轅，音袁。', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '3533/3860;5850/3860', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5444, '獘;申;立;甾;蔽;翳', '槙', '木自獘1，柛{申}[槙]23。45立死，椔{甾}67。8蔽者9，翳10。1112', '[釋文-1]：婢世反，字又作斃\n[音釋-2]：伸。\n[釋文-3]：音申\n[郭注-4]：獘，踣。\n[釋文-5]：踣，蒲北反，或作仆。\n[音釋-6]：側吏。\n[釋文-7]：甾，字林作椔，同，側吏反，一音側其反\n[郭注-8]：不獘頓。\n[釋文-9]：必世反，注同\n[釋文-10]：音殪，注同\n[郭注-11]：樹蔭翳覆地者。詩云：「其椔其翳。」\n[釋文-12]：蔭，於禁反。', '釋木弟十四', '爾雅', '2', DEFAULT, DEFAULT, '6365;9802;6679;564;550;2274', '3579', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5445, '槸;錯;梢;梢櫂', '梢擢', '木相磨，槸1。2棤{錯}34，皵56。7梢89，梢櫂[梢擢]1011。1213', '[釋文-1]：魚逝反，郭云逝、魚例二反\n[郭注-2]：樹枝相切磨。\n[音釋-3]：錯。\n[釋文-4]：七各反\n[音釋-5]：舄。\n[釋文-6]：謝音舄，郭音夕\n[郭注-7]：謂木皮甲錯。\n[音釋-8]：朔。\n[釋文-9]：郭音朔\n[音釋-10]：濁。\n[釋文-11]：擢，直角反，方言云：「拔也。」蒼頡篇云：「抽也。」廣雅云：「出也。」小爾雅云：「拔根曰擢」\n[郭注-12]：謂木无枝柯，梢櫂長而殺者。\n[釋文-13]：殺，色界反，又色例反。', '釋木弟十四', '爾雅', '4', DEFAULT, DEFAULT, '3604;9329;3497;3497/3841', '3497/7996', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5446, '樅;檜', '', '樅12，松葉柏身3。45檜6，柏葉松身。78', '[音釋-1]：七容。\n[釋文-2]：七容反\n[釋文-3]：字林象容反\n[郭注-4]：今大廟梁材用此木，尸子所謂「松柏之鼠，不知堂密之有美樅」。\n[釋文-5]：大，音泰。\n[釋文-6]：古活反，又古外反\n[郭注-7]：詩曰：「檜楫松舟。」\n[釋文-8]：楫，本又作檝，子葉反，又一音集。', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '3548;3547', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5447, '朻;喬;苞;茂', '樛', '句如羽1，喬2。34下句曰朻[樛]5，上句曰喬。如木楸曰喬，6如竹箭曰苞7，89如松柏曰茂，1011如槐曰茂。12', '[釋文-1]：居具反，下同\n[釋文-2]：阮孝緒音橋，郭音驕，下皆同\n[郭注-3]：樹枝曲卷，似鳥毛羽。\n[釋文-4]：卷，巨貟反。\n[釋文-5]：居虯反，本又作樛，同，字林九稠反\n[郭注-6]：楸樹性，其上竦。\n[釋文-7]：如字，本或作枹\n[郭注-8]：篠竹性叢生。\n[釋文-9]：篠，先了反。\n[郭注-10]：枝葉婆娑。\n[釋文-11]：娑，素河反。\n[郭注-12]：言亦扶踈茂盛。', '釋木弟十四', '爾雅', '1', DEFAULT, DEFAULT, '3591;6624;388;522', '3590', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5448, '柷;州木;髦;柔英', '', '柷1，州木。髦2，柔英。3', '[釋文-1]：章六反，本今作祝\n[釋文-2]：音毛\n[郭注-3]：皆未詳。', '釋木弟十四', '爾雅', '', DEFAULT, DEFAULT, '3768;7492/3413;5736;3609/502', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5449, '喬;條;莍;核', '覈', '槐棘醜1，喬。2桑柳醜，條。34椒、榝醜56，莍7。89桃李醜，核[覈]10。11', '[釋文-1]：居力反\n[郭注-2]：枝皆翹竦。\n[郭注-3]：阿那垂條。\n[釋文-4]：阿，烏河反，又於可反。那，奴何反，又奴可反。\n[音釋-5]：殺。\n[釋文-6]：茮，音焦，本今作椒。所黠反，字林云：「似茱萸，出淮南。」本草云：「吳茱萸，一名藙。」案今樹極似茱萸，唯子赤細\n[釋文-7]：音求，說文云：「榝、椒實裹如裘也。」一音巨六反\n[郭注-8]：莍蓃子，聚生成房貌。今江東亦呼莍。榝似茱萸而小，赤色。\n[釋文-9]：蓃，所留反，又所于反。茱，音殊。萸，以朱反。\n[釋文-10]：何革反\n[郭注-11]：子中有核人。', '釋木弟十四', '爾雅', '4', DEFAULT, DEFAULT, '6624;3572;487;3730', '4855', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5450, '華;膽;疐;鑽', '刳;蔕', '瓜曰華[刳]之12，桃曰膽之3，棗、李曰疐[蔕]之45，樝、棃曰鑽之67。89', '[音釋-1]：胡化。\n[釋文-2]：胡化反，鄭注禮記云：「謂中裂不四柝也」\n[釋文-3]：丁敢反\n[音釋-4]：帝。\n[釋文-5]：亦作𢈻，同，丁計反\n[音釋-6]：子管。\n[釋文-7]：側加反，字亦作查。子官反，郭徂端反\n[郭注-8]：皆啖食治擇之名。樝似棃而酢澁，見禮記。\n[釋文-9]：澁，字又作濇，又作澀，所立反。', '釋木弟十四', '爾雅', '1;3', DEFAULT, DEFAULT, '3886;2609;2506;9371', '2771;514', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5451, '喬;檄;灌', '𦼉', '小枝上繚爲喬12。3無枝爲檄45。67木族生爲灌[𦼉]8。9', '[音釋-1]：了。\n[釋文-2]：音了\n[郭注-3]：謂細枝皆翹繚上句者，名爲喬木。\n[音釋-4]：亦。\n[釋文-5]：形的反\n[郭注-6]：檄櫂直上。\n[釋文-7]：擢，直角反，字從手。上，時掌反。\n[釋文-8]：樌，古半反，或作灌\n[郭注-9]：族，叢。', '釋木弟十四', '爾雅', '3', DEFAULT, DEFAULT, '6624;3772;7044', '644', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5452, '㱿;天螻', '', '螜{㱿}12，天螻3。45', '[音釋-1]：斛。\n[釋文-2]：胡木反\n[釋文-3]：力侯反\n[郭注-4]：螻蛄也。夏小正曰：「螜則鳴。」\n[釋文-5]：蛄，古乎反。夏，胡雅反。正，音征。', '釋蟲弟十五', '爾雅', '', DEFAULT, DEFAULT, '1963;3/8833', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5453, '蜚;盧蜰', '', '蜚12，蠦蜰{盧蜰}34。56', '[音釋-1]：費。\n[釋文-2]：扶味反\n[音釋-3]：盧。肥。\n[釋文-4]：力胡反。苻非反，孫甫尾反\n[郭注-5]：蜰卽負盤，臭蟲。\n[釋文-6]：盤，字又作螌，蒲安反，廣雅云：「負盤，蟅也。」蟅，音章夜反。臭，昌又反。', '釋蟲弟十五', '爾雅', '', DEFAULT, DEFAULT, '8977;3144/8852', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5454, '螾衍;入耳', '', '螾𧊔{螾衍}12，入耳。34', '[音釋-1]：引。演。\n[釋文-2]：以忍反。以善反，本又作𧍢。補李巡曰：「靑而大者曰螾𧎘，黃而小者謂之入耳」\n[郭注-3]：蚰蜒。\n[釋文-4]：蚰，音由。蜒，音延，方言云：「宋 魏之閒，蚰蜒謂之入耳。」字林云：「北燕人謂蚰蜒爲䖡蚭。」上音女六反，下音女其反。', '釋蟲弟十五', '爾雅', '', DEFAULT, DEFAULT, '8791/7138;3281/7794', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5455, '蜩;蜋蜩;唐蜩;札;蜻蜻;蠽;茅蜩;蝒;馬蜩;蜺;寒蜩;蜓沐;螇鹿', '蠽', '蜩12，蜋蜩3。4螗蜩{唐蜩}5。67蚻{札}[蠽]89，蜻蜻10。1112蠽1314，茅蜩15。16蝒1718，馬蜩。19蜺20，寒蜩。2122蜓蚞{蜓沐}2324，螇螰{螇鹿}25。2627', '[音釋-1]：調。\n[釋文-2]：直彫反，下同\n[釋文-3]：音郎，又音良\n[郭注-4]：夏小正傳曰：「蜋蜩者，五彩具。」\n[釋文-5]：音唐\n[郭注-6]：夏小正傳曰：「螗蜩者，蝘。」俗呼爲胡蟬，江南謂之螗𧋘，音荑。\n[釋文-7]：蟬，示延反，字林云：「蟪蛄也。」𧋘，郭音荑，徒低反。\n[音釋-8]：札。\n[釋文-9]：側黠反\n[釋文-10]：郭音情，又音精\n[郭注-11]：如蟬而小。方言云：「有文者謂之螓。」夏小正曰：「鳴蚻，虎懸。」\n[釋文-12]：縣，音玄。\n[音釋-13]：節。\n[釋文-14]：𧕾，子列反，又徂節反，本今作蠽\n[釋文-15]：本或作𧓿，萌交反\n[郭注-16]：江東呼爲茅截，似蟬而小，靑色。\n[音釋-17]：緜。\n[釋文-18]：音緜，字林云亡千反\n[郭注-19]：蜩中最大者爲馬蟬。\n[釋文-20]：五兮反，呂、郭牛結反\n[郭注-21]：寒螿也，似蟬而小，靑赤。月令曰：「寒蟬鳴。」\n[釋文-22]：螿，子羊反。\n[音釋-23]：挺。木。\n[釋文-24]：謝徒頂反，沈音殄，施音亭。音木，本或作沐，非\n[釋文-25]：音奚。音鹿。補舍人曰：「皆蟬也，方語不同，三輔以西爲蜩；梁 宋以東謂蜩爲蝘；楚地謂之蟪蛄，楚詞云『蟪蛄鳴兮啾啾』是也」\n[郭注-26]：卽蝭螃也，一名蟪蛄，齊人呼螇螰。\n[釋文-27]：蝭，音提，又音帝。蟧，力刀反，字林同，又力幺反。蟪，音惠。蛄，音姑。', '釋蟲弟十五', '爾雅', '4', DEFAULT, DEFAULT, '8868;8844/8868;878/8868;3770;8875/8875;8954;326/8868;8841;6137/8868;8870;4615/8868;8805/7411;8871/6263', '8954', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5456, '蛣羌;羌蜋', '𧍕蜋', '蛣蜣{蛣羌}12，蜣蜋{羌蜋}[𧍕蜋]3。45', '[音釋-1]：起吉。羌。\n[釋文-2]：起吉反。音羌\n[釋文-3]：音良\n[郭注-4]：黑甲蟲，噉糞土。\n[釋文-5]：噉，大敢反。糞，方問反。', '釋蟲弟十五', '爾雅', '2', DEFAULT, DEFAULT, '8813/2357;2357/8844', '8853/8844', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5457, '蝎;蛣䖦', '', '蝎12，蛣𧌑{蛣䖦}34。56', '[音釋-1]：曷。\n[釋文-2]：戸葛反\n[音釋-3]：屈。\n[釋文-4]：起勿反\n[郭注-5]：木中蠧虫。\n[釋文-6]：蠧，丁故反，字林云：「木中蟲也。」', '釋蟲弟十五', '爾雅', '', DEFAULT, DEFAULT, '8825;8813/8814', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5458, '蠰;齧桑;諸慮;奚相', '', '蠰12，齧桑3。45諸慮6，奚相7。8', '[音釋-1]：餉。\n[釋文-2]：郭音餉，又音霜，孫音傷\n[釋文-3]：五結反\n[郭注-4]：似天牛，長角，體有白㸃。喜齧桑樹，作孔入其中，江東呼爲齧髮。\n[釋文-5]：喜，虛記反，下同。\n[釋文-6]：本或作𧓻，施音驢，一音力據反\n[釋文-7]：施音葙，謝息亮反，舍人本作桑\n[郭注-8]：未詳。', '釋蟲弟十五', '爾雅', '', DEFAULT, DEFAULT, '8843;1313/3860;1488/6704;6672/2162', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5459, '蜉游;渠略', '浮游;螶𧎾/渠𧍕', '蜉蝣{蜉游}[浮游]12，渠略[螶𧎾/渠𧍕]3。45', '[音釋-1]：浮。遊。\n[釋文-2]：音孚，又音浮。郭音由，本又作䖻，謝音流\n[釋文-3]：如字，略或作𧕌，音同。補舍人曰：「南陽以東曰蜉蝣，梁 宋之閒曰渠略」\n[郭注-4]：似蛣蜣，身狹而長，有角，黃黑色，叢生糞土中，朝生暮死，豬好啖之。\n[釋文-5]：陜，乎夾反，本今作狹。好，呼報反，下同。', '釋蟲弟十五', '爾雅', '1;2', DEFAULT, DEFAULT, '8969/4306;7265/9192', '7180/4306;8963/8878/0/7265/8853', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5460, '犮蟥;蛢', '𧑐蟥', '蛂蟥{犮蟥}[𧑐蟥]12，蛢34。5', '[音釋-1]：步結。黃。\n[釋文-2]：謝音弗，沈符結反，字林大替反。音黃，郭音王，本或作黃\n[音釋-3]：瓶。\n[釋文-4]：郭音瓶\n[郭注-5]：甲蟲也，大如虎豆，緑色，今江東呼黃蛢，音瓶。', '釋蟲弟十五', '爾雅', '1', DEFAULT, DEFAULT, '6355/8848;8846', '8847/8848', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5461, '蠸;輿父;守瓜', '', '蠸12，輿父34，守瓜。5', '[音釋-1]：權。\n[釋文-2]：音權\n[音釋-3]：甫。\n[釋文-4]：音余。音甫，下同，字或作𧉊\n[郭注-5]：今瓜中黃甲小蟲，喜食瓜葉，故曰守瓜。', '釋蟲弟十五', '爾雅', '', DEFAULT, DEFAULT, '8807;9528/1908;4596/4547', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5462, '蝚;駹螻', '', '蝚12，蛖螻{駹螻}34。5', '[音釋-1]：柔。\n[釋文-2]：如由反\n[音釋-3]：武江。\n[釋文-4]：武江反，又亡工反，或作駹，非。來侯反\n[郭注-5]：蛖螻，螻蛄類。', '釋蟲弟十五', '爾雅', '', DEFAULT, DEFAULT, '8812;6154/8833', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5463, '不蜩;王父', '', '不蜩，王蚥{王父}。1', '[郭注-1]：未詳。', '釋蟲弟十五', '爾雅', '', DEFAULT, DEFAULT, '7706/8868;78/1908', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5464, '姑䗐;強羊', '', '蛄䗐{姑䗐}12，強䖹{強羊}34。5', '[音釋-1]：施。\n[釋文-2]：字又作施，式移反，又式豉反，字林弋豉反\n[音釋-3]：亡婢。\n[釋文-4]：巨良反。郭音芈，亡婢反，本或作芈，說文作羊，字林作蛘，弋丈反，云：「搔蛘也」\n[郭注-5]：今米穀中蠹小黑蟲是也。建平人呼爲䖹子，音芈姓。', '釋蟲弟十五', '爾雅', '', DEFAULT, DEFAULT, '8146/8849;8826/2333', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5465, '不過;蟷蠰;蜱蛸', '', '不過1，蟷蠰23。45其子蜱蛸67。89', '[釋文-1]：本或作𧒖，字林古禾反，謝古臥反\n[音釋-2]：丁郎。箱。\n[釋文-3]：丁郎反。息詳反，字林乃郎反\n[郭注-4]：蟷蠰，螗蜋別名。\n[釋文-5]：螳，音唐，本今作螗。蜋，音郎。\n[音釋-6]：毗。消。\n[釋文-7]：音裨，又婢貽反。音蕭\n[郭注-8]：一名䗚蟭，蟷蠰卵也。\n[釋文-9]：䗚，普莫反，又補莫反。蟭，音焦，字林子彫反。', '釋蟲弟十五', '爾雅', '', DEFAULT, DEFAULT, '7706/1110;8842/8843;8960/8845', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5466, '疾黎;卽且', '悉𧍓', '蒺蔾{疾黎}1，蝍蛆{卽且}[悉𧍓]23。45', '[釋文-1]：音疾，字亦作（⿰虫蒺），同。音梨，字亦作䖿\n[音釋-2]：卽。子余。\n[釋文-3]：施音卽，孫子逸反。子余反\n[郭注-4]：似蝗而大腹，長角，能食虵腦。\n[釋文-5]：蝗，華孟反，下同，字林音皇，說文榮庚反，范宣禮記音音橫。聲類、韻集並以蝗協庚韻。腹，音福。腦，奴老反。', '釋蟲弟十五', '爾雅', '2', DEFAULT, DEFAULT, '4696/4515;3198/9473', '723/8840', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5467, '蝝;蝮陶', '復陶', '蝝12，蝮蜪{蝮陶}[復陶]34。56', '[音釋-1]：緣。\n[釋文-2]：以全反，字林尹絹反，說文云：「劉歆說蚍蜉子也。董仲舒說蝗子也。」何休注公羊云：「卽螽也，始生曰蝝，長大曰螽。」杜預亦云：「螽子。」郭依董說\n[音釋-3]：孚福。陶。\n[釋文-4]：孚福反，郭蒲篤反。郭音陶，字林他牟反\n[郭注-5]：蝗子未有翅者。外傳曰：「蟲舍蚳蝝。」\n[釋文-6]：翅，式智反。舍，音捨。蚳，直其反。', '釋蟲弟十五', '爾雅', '2', DEFAULT, DEFAULT, '8832;8787/9689', '1226/9689', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5468, '悉率;蛩', '悉𧍓', '蟋蟀{悉率}[悉𧍓]1，蛬{蛩}23。45', '[釋文-1]：音悉，說文作悉，本或作𧒓，音瑟。所律反，詩同，本或作𧍓，說文同\n[音釋-2]：拱。\n[釋文-3]：九勇反\n[郭注-4]：今促織也，亦名靑𧊿。\n[釋文-5]：促，字亦作趗，七玉反。蜻，子盈反，本今作靑。蛚，音列，廣雅云：「蜻蛚，促織也。」字林云：「蟋蟀也。」本今作𧊿。', '釋蟲弟十五', '爾雅', '1', DEFAULT, DEFAULT, '723/8785;8929', '723/8840', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5469, '敬;蟆', '鼃', '蟼{敬}[鼃]12，蟆3。45', '[音釋-1]：驚。\n[釋文-2]：郭驚、㬌二音，孫音京\n[釋文-3]：武巴反\n[郭注-4]：蛙類。\n[釋文-5]：蛙，戸蝸反。史游急就云：「蛙蝦蟇。」', '釋蟲弟十五', '爾雅', '1', DEFAULT, DEFAULT, '5820;8916', '9000', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5470, '艮;馬戔', '蠲', '蛝{艮}[蠲]12，馬䗃{馬戔}34。56', '[音釋-1]：閑。\n[釋文-2]：音閑\n[音釋-3]：棧。\n[釋文-4]：郭仕板反，字林仕免反，或仕𥳑反，施仕娩反\n[郭注-5]：馬蠲，蚐，俗呼馬𧏿。\n[釋文-6]：蠲，古玄反，說文云：「明堂月令：『腐草爲蠲。』」蚐，音均。馬𧏿，丈六反，字林云：「𧏿馬。」', '釋蟲弟十五', '爾雅', '1', DEFAULT, DEFAULT, '5225;6137/8400', '8829', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5471, '𨸏螽;蠜;草螽;負蠜;蜤螽;蜙蝑;毄螽;螇斥;土螽;蠰谿', '', '蛗螽{𨸏螽}12，蠜34。56草螽，負蠜7，89蜤螽1011，蜙蝑1213。1415蟿螽{毄螽}1617，螇蚸{螇斥}1819。20土螽，蠰谿2122。2324', '[音釋-1]：阜。終。\n[釋文-2]：音阜。本或作𧕠，音終，下同\n[音釋-3]：煩。\n[釋文-4]：音煩，下同，本或作螌\n[郭注-5]：詩曰：「趯趯阜蟲。」\n[釋文-6]：趯，他歷反。\n[釋文-7]：蝜，音負，字或作負\n[郭注-8]：詩云：「喓喓草蟲。」謂常羊也。\n[釋文-9]：喓，於遥反。\n[音釋-10]：斯。\n[釋文-11]：本又作蟴，詩作斯，同，音私支反\n[音釋-12]：嵩。胥。\n[釋文-13]：𧌻，字亦作蜙，字林先凶反，郭先工反，說文思弓反。相魚反，郭才與反，字林先呂反，說文云：「蚣蝑以股鳴」\n[郭注-14]：蜙䗥也，俗呼𧐍䗲。\n[釋文-15]：蚣，本亦作螉，同，烏公反，字林云：「蟲在牛馬皮者。」說文云：「蚣或蜙字省。」息忠反。䗥，寸東反，字林云：「螉䗥似䗲。」舂黍，傷容反，本或作𧐍𧑓。\n[音釋-16]：契。\n[釋文-17]：音棄，字林云口地反\n[音釋-18]：歷。\n[釋文-19]：音奚。或作蚗，郭音歷，孫音昔\n[郭注-20]：今俗呼似蜙䗥而細長、飛翅作聲者爲螇蚸。\n[音釋-21]：壤。\n[釋文-22]：音壤，孫音囊，字又作𧖗，誤，又思諒反，或式尚反。苦兮反，孫音奚\n[郭注-23]：似蝗而小，今謂之土𧎩。\n[釋文-24]：𧎩，字又作虴，竹宅反，誥幼云：「虴，虴蜢也，善跳。」蜢，音猛。', '釋蟲弟十五', '爾雅', '', DEFAULT, DEFAULT, '9619/8952;8839;680/8952;3965/8839;8803/8952;8864/8865;1962/8952;8871/5969;9021/8952;8843/7503', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5472, '螼蚓;臤天', '', '螼蚓12，蜸蚕{臤天}34。56', '[音釋-1]：羌引。\n[釋文-2]：羌引反。音引，郭餘忍反，說文以爲螾字，同\n[音釋-3]：苦顯。他典。\n[釋文-4]：苦顯反。他典反\n[郭注-5]：卽䖤蟺也，江東呼寒蚓。\n[釋文-6]：䖤，於阮反。蟺，音善，廣雅云：「䖤蟺，蚯蚓也。」𧑚，音寒，字又作寒。蚓，郭許謹反，殷仲堪許偃反。', '釋蟲弟十五', '爾雅', '', DEFAULT, DEFAULT, '8790/8791;1949/3', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5473, '莫貈;堂蜋;蛑', '', '莫貈12，蟷蜋{堂蜋}3，蛑45。67', '[音釋-1]：鶴。\n[釋文-2]：本或作𧕤，同，武博反。本又作貉，孫戸各反，下同\n[釋文-3]：螳蜋，說文云：「名斫父」\n[音釋-4]：謀。\n[釋文-5]：郭音牟，字林云：「蝗蜋也。」又亡牢反\n[郭注-6]：螗蜋，有斧蟲，江東呼石蜋。孫叔然以方言說此義，亦不了。\n[釋文-7]：斧，音甫。', '釋蟲弟十五', '爾雅', '', DEFAULT, DEFAULT, '701/6123;9056/8844;8974', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5474, '丁蛵;負勞', '', '虰蛵{丁蛵}12，負勞3。45', '[音釋-1]：丁。馨。\n[釋文-2]：音丁。虛形反\n[釋文-3]：蟧，力刀反，本今作勞\n[郭注-4]：或曰卽蜻蛉也，江東呼狐棃，所未聞。\n[釋文-5]：靑，本今作蜻。蛉，力丁反，字林云：「蜻蛉，一名桑根。」狐，音乎。', '釋蟲弟十五', '爾雅', '', DEFAULT, DEFAULT, '9747/8816;3965/9236', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5475, '蜭;毛蠹', '', '蜭12，毛蠹。34', '[音釋-1]：戸感。\n[釋文-2]：戸感反\n[郭注-3]：卽蛓。\n[釋文-4]：蛓，七志反，說文云：「毛蟲也，讀若笥。」案今俗呼爲毛蛓，有毒螫人，楚辭云：「蛓緣兮我裳。」', '釋蟲弟十五', '爾雅', '', DEFAULT, DEFAULT, '8817;5387/8966', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5476, '墨;蛅斯', '', '蟔{墨}12，蛅蟴{蛅斯}34。56', '[音釋-1]：墨。\n[釋文-2]：字又作䘃，亡北反\n[音釋-3]：而占。斯。\n[釋文-4]：字或作蚺，而占反。音斯\n[郭注-5]：蛓屬也，今靑州人呼蛓爲蛅蟴。孫叔然云「八角螫蟲」，失之。\n[釋文-6]：𧏅，式亦反，𧏅猶蠚也，蠚，火各反，字林云：「蟲行毒也。」', '釋蟲弟十五', '爾雅', '', DEFAULT, DEFAULT, '9078;8850/9485', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5477, '蟠;鼠負', '鼠婦', '蟠12，鼠負[鼠婦]3。45', '[音釋-1]：煩。\n[釋文-2]：音煩，字或作𪖇\n[釋文-3]：本亦作蝜，又作婦，亦作𧌈，音同。本草：「鼠負，一名負蟠，一名伊威，一名委黍。」陶注云：「多在鼠坎中，鼠背負之。今作婦字，則似乖理。一名鼠姑」\n[郭注-4]：瓮器底蟲。\n[釋文-5]：瓮，烏弄反。底，丁禮反。', '釋蟲弟十五', '爾雅', '2', DEFAULT, DEFAULT, '8862;6393/3965', '6393/8132', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5478, '蟫;白魚', '', '蟫12，白魚。34', '[音釋-1]：淫。\n[釋文-2]：郭音淫，又徒南反\n[郭注-3]：衣書中蟲，一名蛃魚。\n[釋文-4]：蛃，音丙。', '釋蟲弟十五', '爾雅', '', DEFAULT, DEFAULT, '8815;4931/7580', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5479, '䖸;羅', '蛾', '䖸[蛾]12，羅。34', '[音釋-1]：娥。\n[釋文-2]：本又作蛾，說文同，我河反\n[郭注-3]：蠶䖸。\n[釋文-4]：蠶，徂南反，下同。', '釋蟲弟十五', '爾雅', '1', DEFAULT, DEFAULT, '8949;4833', '8836', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5480, '翰;天雞', '', '螒{翰}12，天雞。34', '[音釋-1]：汗。\n[釋文-2]：翰，胡旦反，字林作螒，同\n[郭注-3]：小蟲，黑身赤頭，一名莎雞，又曰樗雞。\n[釋文-4]：莎，蘇禾反。樗，恥豬反，廣雅云：「樗鳩，樗雞也。」', '釋蟲弟十五', '爾雅', '', DEFAULT, DEFAULT, '2244;3/2291', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5481, '傅;負版', '', '傅1，負版2。3', '[釋文-1]：音付\n[釋文-2]：字亦作蝂，甫𥳑反\n[郭注-3]：未詳。', '釋蟲弟十五', '爾雅', '', DEFAULT, DEFAULT, '5044;3965/4401', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5482, '強;蚚', '', '強1，蚚23。45', '[釋文-1]：其良反，說文云：「籀文作𧖑」\n[音釋-2]：祈。\n[釋文-3]：字林巨希反，又下枚反，郭胡輩反\n[郭注-4]：卽「強醜捋」。\n[釋文-5]：捋，力活反。', '釋蟲弟十五', '爾雅', '', DEFAULT, DEFAULT, '8826;8827', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5483, '蛶;商何', '', '蛶12，螪何{商何}34。5', '[音釋-1]：劣。\n[釋文-2]：本或作捋，郭音劣，又力活反\n[音釋-3]：商。\n[釋文-4]：失羊反，字林之亦反。本或作蚵，音河\n[郭注-5]：未詳。', '釋蟲弟十五', '爾雅', '', DEFAULT, DEFAULT, '8882;1453/5025', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5484, '螝;蛹', '', '螝12，蛹3。4', '[音釋-1]：龜。\n[釋文-2]：郭音龜，字林音潰，施音愧\n[釋文-3]：音勇\n[郭注-4]：蠶蝚。', '釋蟲弟十五', '爾雅', '', DEFAULT, DEFAULT, '8798;8797', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5485, '蜆;縊女', '', '蜆12，縊女3。45', '[音釋-1]：演。\n[釋文-2]：下顯反，字林下研反，孫音俔。案：俔字下顯、苦見二反\n[釋文-3]：一賜反，一音翳是反\n[郭注-4]：小黑蟲，赤頭，喜自經死，故曰縊女。\n[釋文-5]：憙，許記反，本今作喜。經，古刑反。', '釋蟲弟十五', '爾雅', '', DEFAULT, DEFAULT, '8851;8763/8111', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5486, '蚍蜉;蠪;朾;𧕈;蚳;螘', '𧹙', '蚍蜉1，大螘23。4小者，螘。56蠪78，朾[𧹙]螘910。1112螱{𧕈}1314，飛螘，1516其子蚳1718。1920{螘}', '[釋文-1]：音毗。音浮\n[音釋-2]：魚綺。\n[釋文-3]：魚綺反，本亦作蛾，俗作蟻字，音同，案說文：「蟻，羅也。」蟻或作義。「蛾，蠶化飛蛾也。」並非螘字\n[郭注-4]：俗呼爲馬蚍蜉。\n[郭注-5]：齊人呼蟻，蟻蛘。\n[釋文-6]：蛘，以丈反，郭云：「齊人呼蟻也。」字林云：「北燕人謂蚍蜉曰蟻蛘。」\n[音釋-7]：聾。\n[釋文-8]：謝音聾，郭音龍\n[音釋-9]：直耕。\n[釋文-10]：本又作虰，郭唐耕反，孫丈耕反，字林音丁\n[郭注-11]：赤駮蚍蜉。\n[釋文-12]：駮，布角反。\n[音釋-13]：尉。\n[釋文-14]：於貴反，說文、字林從䖵\n[郭注-15]：有翅。\n[釋文-16]：翅，矢豉反，又吉豉反。\n[音釋-17]：池。\n[釋文-18]：直其反\n[郭注-19]：蚳，蟻卵。周禮曰：「蜃，蚳醬。」\n[釋文-20]：蜃，市軫反。醬，子亮反。', '釋蟲弟十五', '爾雅', '3', DEFAULT, DEFAULT, '8975/8969;8835;3801;8972;8838;8837', '6591', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5487, '次出;䵹鼄;鼄蝥;土䵹鼄;草䵹鼄', '次蠿;鼄蟊', '次蟗{次出}[次蠿]12，䵹鼄34。䵹鼄，鼄蝥[鼄蟊]56。78土䵹鼄，9草䵹鼄。10', '[音釋-1]：秋。\n[釋文-2]：本或作蝵，郭音秋\n[音釋-3]：知。誅。\n[釋文-4]：音知，說文作䵹，云：「或作蜘。」音誅，說文云：「或作蛛」\n[音釋-5]：謀。\n[釋文-6]：音謀，又音無，說文作蟊，音茅，云：「㡭蟊，作網蛛蟊也。」以此亦爲「蟊𧈱」字\n[郭注-7]：今江東呼蝃蝥，音掇。\n[釋文-8]：蝃，或作䖦，音章悅反。掇，章悅反，本或作掇拾字，非。\n[郭注-9]：在地中布網者。\n[郭注-10]：絡幕草上者。', '釋蟲弟十五', '爾雅', '1;3', DEFAULT, DEFAULT, '5586/3865;9009/9010;9010/8861;9021/9009/9010;680/9009/9010', '5586/8955;9010/8956', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5488, '土蠭;木蠭', '', '土蠭12，34木蠭。5', '[音釋-1]：蜂。\n[釋文-2]：本又作𧒒，匹凶反，字又作蜂，說文云：「古𧒒字。」字林云：「飛蟲螫人者」\n[郭注-3]：今江東呼大蠭在地中作房者爲土蠭，啖其子，卽馬蠭。今荆 巴閒呼爲蟺，音憚。\n[釋文-4]：蟺，音墠，又示延反，蓋方言也。郭音憚，徒旦反。\n[郭注-5]：似土蠭而小，在樹上作房，江東亦呼爲木蠭，又食其子。', '釋蟲弟十五', '爾雅', '', DEFAULT, DEFAULT, '9021/8961;3413/8961', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5489, '賁;蠐𧕐;蝤蠐;蝎', '蜰', '蟦{賁}[蜰]12，蠐螬{蠐𧕐}3。4蝤蠐56，蝎7。8', '[音釋-1]：費。\n[釋文-2]：扶味反，又扶云反\n[釋文-3]：本又作齊，徂西反。音曹\n[郭注-4]：在糞土中。\n[音釋-5]：囚。\n[釋文-6]：徂秋反\n[釋文-7]：音曷\n[郭注-8]：在木中，今雖通名爲蝎，所在異。', '釋蟲弟十五', '爾雅', '1', DEFAULT, DEFAULT, '3946;8824/8958;8823/8824;8825', '8852', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5490, '𧉅威;委黍', '', '𧉅威12，委黍3。45', '[音釋-1]：伊。\n[釋文-2]：蛜，音伊，本今作伊\n[釋文-3]：今爾雅本旁或並加虫，並如字\n[郭注-4]：舊說鼠𧌈別名，然所未詳。\n[釋文-5]：𧌈，音婦。', '釋蟲弟十五', '爾雅', '', DEFAULT, DEFAULT, '8863/8147;8215/4509', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5491, '蟰蛸;長踦', '', '蟰蛸12，長踦34。5', '[音釋-1]：蕭。所交。\n[釋文-2]：詩作蠨，同，悉彫反，或音肅。所交反，說文云：「蟰蛸，長股者」\n[音釋-3]：巨綺。\n[釋文-4]：郭云：「音﨑嶇之﨑。」柣宜反，字林巨綺反、或居綺反，廣雅云：「踦，脛也。」字從足，虫旁作者，非\n[郭注-5]：小䵹鼄長腳者，俗呼爲喜子。', '釋蟲弟十五', '爾雅', '', DEFAULT, DEFAULT, '8880/8845;6069/1331', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5492, '蛭蝚;至掌', '', '蛭蝚12，至掌。3', '[音釋-1]：猪秩。\n[釋文-2]：郭豬秩反，施徒結反。如由反\n[郭注-3]：未詳。', '釋蟲弟十五', '爾雅', '', DEFAULT, DEFAULT, '8811/8812;7708/7830', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5493, '國貉;蠁蟲', '', '國貉1，蠁蟲2。34', '[釋文-1]：戸各反\n[釋文-2]：許兩反，說文云：「知聲蟲也。司馬相如作䖮字」\n[郭注-3]：今呼蛹蟲爲蠁。廣雅云：「土蛹蠁蟲。」\n[釋文-4]：蛹，音勇。', '釋蟲弟十五', '爾雅', '', DEFAULT, DEFAULT, '3917/6131;8794/8973', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5494, '蠖;尺蠖', '', '蠖12，蚇蠖{尺蠖}34。56', '[音釋-1]：於郭。\n[釋文-2]：枉畧反，字林於郭反。補舍人曰：「一名步屈，宋地曰尋桑，吳人名桑闔」\n[音釋-3]：尺。\n[釋文-4]：音尺，易云：「尺蠖之屈，以求伸也。」亦作尺\n[郭注-5]：今蝍𧑙。\n[釋文-6]：蝍，子逸反，又音卽。𧑙，子六反，字林云：「蝍𧑙，蚇蠖。」', '釋蟲弟十五', '爾雅', '', DEFAULT, DEFAULT, '8831;5426/8831', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5495, '蜾蠃;蒲盧;螟蛉;桑蟲;蝎;桑蠹', '', '果臝{蜾蠃}1，蒲盧。23螟蛉45，桑蟲。67蝎8，桑蠹。910', '[釋文-1]：本又作蜾，又作蝸，同，工火反。魯果反\n[郭注-2]：卽細𦝫蠭也。俗呼爲蠮螉。\n[釋文-3]：要，一遥反，本今作𦝫。蠮，於結反，又於計反。螉，烏紅反，廣雅云：「蠮螉，土蜂也。」案：今俗呼細𦝫小蜂爲蠮螉，在物中作房，用土爲隔，非土蜂也。\n[音釋-4]：冥。零。\n[釋文-5]：亡丁反。力丁反。補舍人曰：「桑上小靑蟲也，似步屈」\n[郭注-6]：俗謂之桑蟃，亦曰戎女。\n[釋文-7]：蟃，音萬。\n[釋文-8]：音曷\n[郭注-9]：卽蛣𧌑。\n[釋文-10]：蛣，去一反。𧌑，丘勿反。', '釋蟲弟十五', '爾雅', '', DEFAULT, DEFAULT, '8854/8855;332/3144;8808/8876;3860/8973;8825;3860/8966', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5496, '熒火;卽照', '', '熒火1，卽炤{卽照}23。4', '[釋文-1]：螢，戸扃反，本今作熒\n[音釋-2]：照。\n[釋文-3]：音照\n[郭注-4]：夜飛，腹下有火。', '釋蟲弟十五', '爾雅', '', DEFAULT, DEFAULT, '6582/6416;3198/6498', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5497, '密肌;繼英;𧒔', '', '密肌1，繼英2。3{𧒔}', '[釋文-1]：居疑反\n[釋文-2]：字又作𧓓，音計\n[郭注-3]：未詳。', '釋蟲弟十五', '爾雅', '', DEFAULT, DEFAULT, '5876/2598;8549/502;8968', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5498, '戹;烏噣', '', '蚅{戹}12，烏蠋{烏噣}34。5', '[音釋-1]：厄。\n[釋文-2]：烏革反\n[音釋-3]：蜀。\n[釋文-4]：螐，音烏，本又作烏。音蜀，說文云：「桑中蟲也。」郭云：「大蟲如指，似蠶也」\n[郭注-5]：大蟲，如指，似蠶，見韓子。', '釋蟲弟十五', '爾雅', '', DEFAULT, DEFAULT, '7727;2488/782', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5499, '蠓;蔑蠓', '', '蠓12，蠛蠓{蔑蠓}34。56', '[音釋-1]：莫孔。\n[釋文-2]：莫孔反，下同\n[音釋-3]：亡結。\n[釋文-4]：亡結反。補孫炎曰：「蟲小於蛟」\n[郭注-5]：小蟲，似蚋，喜亂飛。\n[釋文-6]：蜹，人銳反，字林人劣反，又作蚋字，同，秦人謂蚊爲蜹。', '釋蟲弟十五', '爾雅', '', DEFAULT, DEFAULT, '8877;2332/8877', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5500, '王失昜', '王蛭𧒾', '王蛈蝪{王失昜}[王蛭𧒾]12。34', '[音釋-1]：大結。湯。\n[釋文-2]：大結反，字林音秩。字林音湯，或音蕩，又音唐\n[郭注-3]：卽螲蟷，似䵹鼄，在穴中，有蓋，今河北人呼蛈蝪。\n[釋文-4]：螲，丁結反。蟷，丁郎反。', '釋蟲弟十五', '爾雅', '1', DEFAULT, DEFAULT, '78/7978/6074', '78/8811/8842', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5501, '象;雔由;亢;繭', '', '蟓{象}12，桑繭3。4雔由56：樗繭7、8棘繭9、10欒繭11。12蚢{亢}，蕭繭1314。15{繭}', '[音釋-1]：象。\n[釋文-2]：音象\n[釋文-3]：古典反\n[郭注-4]：食桑葉作繭者，卽今蠶。\n[音釋-5]：讎。\n[釋文-6]：讎，市由反，本今作雔\n[釋文-7]：恥余反\n[郭注-8]：食樗葉。\n[釋文-9]：居力反\n[郭注-10]：食棘葉。\n[釋文-11]：力丸反\n[郭注-12]：食欒葉。\n[音釋-13]：杭。\n[釋文-14]：戸剛反。先條反\n[郭注-15]：食蕭葉者。皆蠶類。', '釋蟲弟十五', '爾雅', '', DEFAULT, DEFAULT, '6135;2363/8516;6655;8518', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5502, '罅;奮;捋;螸;扇', '𧎥', '翥醜鏬{罅}12，34螽醜奮5，67強醜捋8，9蠭醜螸1011，1213蠅醜扇[𧎥]14。15', '[音釋-1]：之庶。呼暇。\n[釋文-2]：于據反。𧇍，呼暇反，本今作鏬\n[郭注-3]：剖母背而生。\n[釋文-4]：剖，普口反。\n[釋文-5]：甫問反\n[郭注-6]：好奮迅作聲。\n[釋文-7]：迅，音信，又音峻。\n[釋文-8]：其良反。力活反，李、孫云：「以口捋其翅。」郭云：「以腳自摩捋」\n[郭注-9]：以腳自摩捋。\n[音釋-10]：俞。\n[釋文-11]：李、孫、郭並闕讀，而謝孚逢反，施作螽，音終，案上有「螽醜奮」，依謝爲得。螸，亦作𧐄，羊朱反，說文云：「垂腴」\n[郭注-12]：垂其腴。\n[釋文-13]：腴，羊朱反。\n[釋文-14]：餘仍反。如字，說文作𧎥，云：「摇翼也。」音同\n[郭注-15]：好揺翅。', '釋蟲弟十五', '爾雅', '5', DEFAULT, DEFAULT, '3304;2321;7894;8888;7724', '8889', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5503, '螟;𧎢;賊;蟊', '蝥', '食苗心，螟1。食葉，𧎢23。食節，賊4。食根，蟊[蝥]56。7', '[釋文-1]：亡丁反，蟲食苗心者，說文云：「蟲食穀葉者，吏冥冥犯法卽生螟。」補舍人曰：「言冥冥然難知也。」又曰：「此四種蟲，皆蝗也，實不同，故分別釋之」\n[音釋-2]：特。\n[釋文-3]：𧈱，字又作𧎢，又作𧊇，同，徒得反，蟲食葉者，說文云：「蟲食草葉者，吏乞貸卽生𧈱」\n[釋文-4]：𧒿，音賊，本今作賊\n[音釋-5]：謀。\n[釋文-6]：亡侯反，本亦作蛑，說文作蟊，蛑，古蟊字，云：「吏抵冒取民則生蟊也。」字林蟊音亡牛反\n[郭注-7]：分別蟲啖食禾所在之名耳。皆見詩。', '釋蟲弟十五', '爾雅', '4', DEFAULT, DEFAULT, '8808;8809;8382;8956', '8861', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5504, '蟲;豸', '', '有足謂之蟲，無足謂之豸12。', '[音釋-1]：丈爾。\n[釋文-2]：丈爾反，無足者，說文云：「獸長脊，行豸豸，欲有所伺殺也」', '釋蟲弟十五', '爾雅', '', DEFAULT, DEFAULT, '8973;6112', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5505, '鯉;鱣', '', '鯉。1鱣23。45', '[郭注-1]：今赤鯉魚。\n[音釋-2]：張連。\n[釋文-3]：張連反，卽黃魚也\n[郭注-4]：鱣，大魚，似鱏而短鼻，口在頷下，體有邪行甲，無鱗，肉黃，大者長二三丈，今江東呼爲黃魚。\n[釋文-5]：鱏，音尋，又音淫，字林云：「長鼻魚也，重千斤。」頷，戸感反。行，戸郎反。', '釋魚弟十六', '爾雅', '', DEFAULT, DEFAULT, '7596;7597', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5506, '鰋;鮎', '', '鰋12。34鮎5。67', '[音釋-1]：偃。\n[釋文-2]：音偃，白魚\n[郭注-3]：今鰋額白魚。\n[釋文-4]：頟，魚格反，本今作額。\n[釋文-5]：郭奴謙反，舍人本無此字\n[郭注-6]：別名鯷，江東通呼鮎爲鮧。\n[釋文-7]：鯷，音提，字林云：「靑州人呼鮎鯷。」鮧，大兮反，說文云：「大鮎也。」陶弘景云：「今人並呼慈音。」', '釋魚弟十六', '爾雅', '', DEFAULT, DEFAULT, '7630;7629', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5507, '鱧', '𩽵', '鱧[𩽵]1。23', '[釋文-1]：音禮，字或作鱺，又作䗍，同，廣雅云：「鱺、鰑，鮦也。」本草作䗍，云：「一名鮦魚。」陶注云：「今皆作鱧字，舊言是公蠣蛇所變，今亦有相生者」\n[郭注-2]：鮦也。\n[釋文-3]：鮦，大勇反。', '釋魚弟十六', '爾雅', '1', DEFAULT, DEFAULT, '7618', '7600', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5508, '鯇', '', '鯇12。34', '[音釋-1]：華板。\n[釋文-2]：華板反，郭胡本反，字林下短反\n[郭注-3]：今鯶魚，似鱒而大。\n[釋文-4]：鯶，胡本反，又下短反，一本作故本反。鱒，才損反。', '釋魚弟十六', '爾雅', '', DEFAULT, DEFAULT, '7625', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5509, '沙;鮀', '', '鯊{沙}1，鮀23。4', '[釋文-1]：本又作魦，音沙，詩云：「魚麗于罶」，鱨鯊。補舍人曰：「石鮀也」\n[音釋-2]：陀。\n[釋文-3]：徒何反\n[郭注-4]：今吹沙小魚，體貟而有㸃文。', '釋魚弟十六', '爾雅', '', DEFAULT, DEFAULT, '7238;7628', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5510, '囚;玆', '鯫', '鮂{囚}[鯫]黑12，鰦{玆}34。56', '[音釋-1]：囚。\n[釋文-2]：徂秋反\n[音釋-3]：兹。\n[釋文-4]：音茲\n[郭注-5]：卽白儵，江東呼爲鮂。\n[釋文-6]：鯈，音條，本亦作鮋，音由，又直留反，又音酬。', '釋魚弟十六', '爾雅', '1', DEFAULT, DEFAULT, '3928;2508', '7637', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5511, '鰼;鰌', '', '鰼12，鰌34。56', '[音釋-1]：習。\n[釋文-2]：音習\n[音釋-3]：秋。\n[釋文-4]：音秋，郭云：「泥鰌也。」字林云：「似鱣短小也」\n[郭注-5]：今泥鰌。\n[釋文-6]：泥，乃兮反。', '釋魚弟十六', '爾雅', '', DEFAULT, DEFAULT, '7623;7624', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5512, '堅;鮦;兌', '', '鰹{堅}12，大鮦34，小者鮵{兌}56。78', '[音釋-1]：堅。\n[釋文-2]：音堅\n[音釋-3]：同。\n[釋文-4]：音童，又逐拱反\n[音釋-5]：奪。\n[釋文-6]：大活反\n[郭注-7]：今靑州呼小鱺爲鮵。\n[釋文-8]：鱺，音禮，又力知反。', '釋魚弟十六', '爾雅', '', DEFAULT, DEFAULT, '1951;7599;5461', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5513, '魾;鱯;鮡', '', '魾12，大鱯34，小者鮡56。7', '[音釋-1]：皮。\n[釋文-2]：蒲悲反，或音丕\n[音釋-3]：畫。\n[釋文-4]：下化反，一音獲，字林下虢反\n[音釋-5]：兆。\n[釋文-6]：音兆，又音姚\n[郭注-7]：鱯似鮎而大，白色。', '釋魚弟十六', '爾雅', '', DEFAULT, DEFAULT, '7617;7616;7680', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5514, '鰝;鰕', '', '鰝12，大鰕34。56', '[音釋-1]：浩。\n[釋文-2]：郭音鄗，戸老反\n[音釋-3]：霞。\n[釋文-4]：戸加反\n[郭注-5]：鰕大者岀海中，長二三丈，鬚長數尺。今靑州呼鰕魚爲鰝，音酆鄗。\n[釋文-6]：數，所主反。酆，芳弓反。', '釋魚弟十六', '爾雅', '', DEFAULT, DEFAULT, '7667;7666', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5515, '昆', '卵', '鯤{昆}[卵]1，魚子。2', '[釋文-1]：音昆\n[郭注-2]：凡魚之子總名鯤。', '釋魚弟十六', '爾雅', '1', DEFAULT, DEFAULT, '4256', '9013', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5516, '曁;是逐', '', '鱀{曁}12，是鱁{是逐}3。45', '[音釋-1]：忌。\n[釋文-2]：其冀反，字林作𩺺，音旣，云：「胎生魚」\n[釋文-3]：本或作鯷，非。音逐，本亦作逐\n[郭注-4]：鱀，䱜屬也，體似鱏，尾如䱡魚，大腹，喙小銳而長齒羅生，上下相銜，鼻在額上，能作聲。少肉多膏，胎生，健啖細魚，大者長丈餘，江中多有之。\n[釋文-5]：𩻶，七畧、七各二反，本今作䱜。鱏，尋、淫二音。䱡，居六、巨六二反，字林云：「魚有兩乳，出樂浪。一曰出江東。」說文同。喙，香穢反。膏，音高。胎，他來反。啗，大敢反。', '釋魚弟十六', '爾雅', '', DEFAULT, DEFAULT, '4286;1089/1178', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5517, '繩', '鮞', '鱦{繩}[鮞]12，小魚。3', '[音釋-1]：承。\n[釋文-2]：郭音繩，顧音孕，本或作䱆，同\n[郭注-3]：家語曰：「其小者，鱦魚也。」今江東亦呼魚子未成者爲鱦，音繩。', '釋魚弟十六', '爾雅', '1', DEFAULT, DEFAULT, '8694', '7582', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5518, '鮥;叔鮪', '', '鮥12，鮛鮪{叔鮪}34。56', '[音釋-1]：洛。\n[釋文-2]：郭音洛，字林作鯦，巨救反\n[音釋-3]：叔。偉。\n[釋文-4]：叔，字林作鮛，同，書育反。于軌反，字林于九反，或曰：卽鱏魚也，似鱣而長鼻，體無鱗甲\n[郭注-5]：鮪，鱣屬也。大者名王鮪，小者名鮛鮪。今宜都郡自京門以上，江中通出鱏鱣之魚。有一魚狀似鱣而小，建平人呼鮥子，卽此魚也。音洛。\n[釋文-6]：鱣，知連反。上，時掌反。', '釋魚弟十六', '爾雅', '', DEFAULT, DEFAULT, '7593;1924/7590', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5519, '鯦;當互', '', '鯦12，當魱{當互}34。56', '[音釋-1]：俱救。\n[釋文-2]：具救反，又徐秋反，似鯿而長三尺，字林作鮥，音格，云：「當魱也。」顧作鯦，同\n[音釋-3]：胡。\n[釋文-4]：郭音胡，一音互\n[郭注-5]：海魚也，似鯿而大鱗，肥美多鯁，今江東呼其最大長三尺者爲當魱，音胡。\n[釋文-6]：鯿，字又作鯾，方仙反，字林云：「魚也。」案魚似魴而大，腝細而長。鯁，工杏反，說文云：「魚骨也。」字林工孟反。', '釋魚弟十六', '爾雅', '', DEFAULT, DEFAULT, '7668;9193/2959', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5520, '列;蔑刀', '𥵸刀', '鮤{列}12，鱴刀{蔑刀}[𥵸刀]34。56', '[音釋-1]：列。\n[釋文-2]：音列，顧閭結反\n[音釋-3]：滅。\n[釋文-4]：亡節反，字亦作魛，字林云：「刀魚，飲而不食」\n[郭注-5]：今之鮆魚也，亦呼爲魛魚。\n[釋文-6]：鮆，徂禮反，又徐爾反，字林云：「刀魚也，才豉反。」字或作鱭。', '釋魚弟十六', '爾雅', '2', DEFAULT, DEFAULT, '2772;2332/2744', '2879/2744', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5521, '矞夸;鱖婦', '', '鱊鮬{矞夸}12，鱖鯞{鱖婦}34。56', '[音釋-1]：聿。步。\n[釋文-2]：郭古滑反，字林于一反，沈音述，又音聿。郭音步，字林丘于反，施蒲悲反\n[音釋-3]：厥。章酉。\n[釋文-4]：音厥，本亦作厥，字林几綴、巨月二反。章酉反，本或作帚\n[郭注-5]：小魚也，似鮒子而黑，俗呼爲魚婢，江東呼爲妾魚。\n[釋文-6]：鮒，符付反，廣雅云：「鰿也。」音積，字林子狄反。', '釋魚弟十六', '爾雅', '', DEFAULT, DEFAULT, '1452/6602;7636/8132', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5522, '微', '揮', '魚有力者，鰴{微}[揮]12。3', '[音釋-1]：暉。\n[釋文-2]：字或作𩽚，許韋反\n[郭注-3]：強大多力。', '釋魚弟十六', '爾雅', '1', DEFAULT, DEFAULT, '1236', '8010', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5523, '魵;鰕', '', '魵12，鰕3。45', '[音釋-1]：墳。\n[釋文-2]：符云反，又符粉反，顧孚粉反，郭云：「小鰕別名」\n[釋文-3]：下家反，字或作蝦\n[郭注-4]：出穢邪頭國，見呂氏字林。\n[釋文-5]：邪，羊嗟反。', '釋魚弟十六', '爾雅', '', DEFAULT, DEFAULT, '7640;7666', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5524, '鮅;鱒;魴;魾;𥝤;來', '', '鮅12，鱒34，56魴78。魾910，1112鯬{𥝤}1314，鯠{來}1516。17', '[音釋-1]：必。\n[釋文-2]：音必，字林云：「魴也」\n[音釋-3]：才損。\n[釋文-4]：才損反，說文云：「赤目魚。」一音仕轉反，或直轉反\n[郭注-5]：似鯶子，赤眼。\n[釋文-6]：鯶，呼本反。\n[音釋-7]：房。\n[釋文-8]：字林音房，云：「赤尾魚」\n[音釋-9]：毗。\n[釋文-10]：音丕，又音毗\n[郭注-11]：江東呼魴魚爲鯿，一名魾，音毗。\n[釋文-12]：鯿，必連反。\n[音釋-13]：黎。\n[釋文-14]：力兮反，又音棃，廣雅云：「魾，鯬」\n[音釋-15]：來。\n[釋文-16]：郭音來，埤蒼云：「鯬、鯠，魾也。」字林作𩺲，音七\n[郭注-17]：未詳。', '釋魚弟十六', '爾雅', '', DEFAULT, DEFAULT, '7672;7586;7606;7617;2752;3348', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5525, '蜎;蠉', '', '蜎12，蠉34。56', '[音釋-1]：狂兖。\n[釋文-2]：郭狂兖反，字林：「一全反，又一耎反，蟲貌也，一曰蟲也」\n[音釋-3]：香兖。\n[釋文-4]：郭香兖反，呂火全反\n[郭注-5]：丼中小蛣蟩，赤蟲，一名孑孓，廣雅云。\n[釋文-6]：蛣，古節反，又音吉，或五結反。蟩，居月反。孑，紀列反，字林云：「無右臂，古𤍠反。」孓，九月反，字林云：「無左臂也。」廣雅云：「孑孓，蜎也。」', '釋魚弟十六', '爾雅', '', DEFAULT, DEFAULT, '8908;8886', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5526, '蛭;蟣', '', '蛭1，蟣23。4', '[釋文-1]：沈、呂豬秩反，謝豬悌反，一音之逸反，本草謂之「水蛭，一名蚑，一名至掌。」案說文，今俗呼爲馬蜞，亦名馬耆，卽楚王食寒葅所得而吞之，能去結積也。蚑、蜞同，音其，然釋蟲已有「蛭蝚，至掌」，郭云：「未詳」，依本草卽是水蛭也\n[音釋-2]：祈。\n[釋文-3]：郭音祈，字林云：「齊人名蛭也。」本草又作蚑\n[郭注-4]：今江東呼水中蛭蟲入人肉者爲蟣。', '釋魚弟十六', '爾雅', '', DEFAULT, DEFAULT, '8811;8810', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5527, '科斗;活東', '', '科斗1，活東2。34', '[釋文-1]：苦禾反，字或作𧎗。樊、孫云：「科斗，蟾諸子也。」郭云：「蝦蟇子」\n[釋文-2]：如字，謝、施音括，舍人本作𩓵東\n[郭注-3]：蝦蟆子。\n[釋文-4]：蝦，音遐。蟇，字又作蟆，音麻。', '釋魚弟十六', '爾雅', '', DEFAULT, DEFAULT, '4496/9491;7151/3846', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5528, '魁陸;蛤', '', '魁陸1。23{蛤}', '[釋文-1]：苦回反，郭云：「狀如海蛤。」案本草：「海蛤，一名魁蛤。」又有「魁蛤，一名魁陸，一名活東，並生東海。」說文云：「蛤有三，皆生於海：蛤厲，千歲雀所化，秦人謂之牡厲；海蛤者，百歲燕所化也；魁蛤，一名復絫，老服翼所化」\n[郭注-2]：本草云：「魁，狀如海蛤，貟而厚，外有理縱橫。」卽今之蚶也。\n[釋文-3]：從，子容反，本今作縱。蚶，火甘反，字書云：「蛤也，出會稽，可食。」', '釋魚弟十六', '爾雅', '', DEFAULT, DEFAULT, '9497/9625;8902', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5529, '陶戹', '', '蜪蚅{陶戹}1。2', '[釋文-1]：徒刀反。於革反\n[郭注-2]：未詳。', '釋魚弟十六', '爾雅', '', DEFAULT, DEFAULT, '9689/7727', '', NULL, NULL);
INSERT INTO `t_han_nga` VALUES (5530, '去𪓰;詹諸;黽', '䗇𪓰', '鼁𪓰{去𪓰}[䗇𪓰]12，蟾諸{詹諸}34。56在水者黽78。910', '[音釋-1]：去。秋。\n[釋文-2]：起據反。音秋\n[音釋-3]：占。\n[釋文-4]：音占。蠩，音諸，本今作諸\n[郭注-5]：似蝦蟆，居陸地，淮南謂之去蚥。\n[釋文-6]：蚥，音甫，又音扶甫反。\n[音釋-7]：猛。\n[釋文-8]：莫幸反\n[郭注-9]：耿黽也，似靑蛙，大腹，一名土鴨。\n[釋文-10]：耿，耕幸反。蛙，戸媧反。鴨，於甲反。', '釋魚弟十六', '爾雅', '1', DEFAULT, DEFAULT, '3165/9001;713/1488;8999', '8914/9001', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5531, '陛;螷', '', '蜌{陛}12，螷34。56', '[音釋-1]：陛。\n[釋文-2]：步禮反，又扶氐反，字林：「小蛤也」\n[音釋-3]：蒲猛。\n[釋文-4]：蠯，謝步佳反，郭毗支反，字林作𢋜，沈父幸反，施蒲鯁反，本今作螷\n[郭注-5]：今江東呼蚌長而狹者爲螷。\n[釋文-6]：蚌，步項反，本或作蜯，下注同。陜，乎夾反，本今作狹。', '釋魚弟十六', '爾雅', '', DEFAULT, DEFAULT, '9695;8903', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5532, '蚌;含漿', '', '蚌，含漿。12', '[郭注-1]：蚌卽蜃也。\n[釋文-2]：蜃，字又作𧒏，市忍反，字林：「雉入海所化也。」', '釋魚弟十六', '爾雅', '', DEFAULT, DEFAULT, '8904;815/7390', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5533, '鼈;能;龜;賁', '', '鼈三足1，能23。龜三足4，賁56。78', '[釋文-1]：字又作鱉，卑滅反\n[音釋-2]：奴來。\n[釋文-3]：如字，又奴來反\n[釋文-4]：字林古追反，字又作𤕣\n[音釋-5]：奔。\n[釋文-6]：謝音奔、又音墳，顧彼義反\n[郭注-7]：山海經曰：「從山多三足鼈，大若山多三足龜。」今吳興郡 陽羨縣 君山上有池，池中出三足鼈，又有六眼龜。\n[釋文-8]：羨，似面反。', '釋魚弟十六', '爾雅', '', DEFAULT, DEFAULT, '9003;6413;8996;3946', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5534, '付蠃;虒蝓;蠃;函', '', '蚹蠃{付蠃}12，螔蝓{虒蝓}34。56蠃，小者蜬{函}78。910', '[音釋-1]：附。羅。\n[釋文-2]：音附，一音扶卜反。力禾反，下同，注作螺字，亦同\n[音釋-3]：移。俞。\n[釋文-4]：余支反，又音斯。羊朱反\n[郭注-5]：卽蝸牛也。\n[釋文-6]：蝸，工花反，或工禾反。\n[音釋-7]：含。\n[釋文-8]：本又作函，顧古含反、又呼含反，下同，謝音含\n[郭注-9]：螺大者如斗，出日南 漲海中，可以爲酒杯。\n[釋文-10]：螺，力禾反。漲，音張，又音帳。杯，布廻反。', '釋魚弟十六', '爾雅', '', DEFAULT, DEFAULT, '5058/8855;3131/8907;8855;4344', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5535, '滑澤;勞', '', '螖蠌{滑澤}12，小者蟧{勞}34。56', '[音釋-1]：滑。澤。\n[釋文-2]：音滑，字林音骨。音澤，字林大各反\n[音釋-3]：勞。\n[釋文-4]：力刀反\n[郭注-5]：螺屬，見埤蒼。或曰：卽彭螖也，似蟹而小，音滑。\n[釋文-6]：埤，避移反。蟚，音彭，本今作彭。', '釋魚弟十六', '爾雅', '', DEFAULT, DEFAULT, '7220/7222;9236', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5536, '蜃;珧', '', '蜃，小者珧12。3', '[音釋-1]：遥。\n[釋文-2]：餘招反，山海經云：「激女之水多蜃珧。」郭注云：「蚌屬也。」字書云：「玉珧，肉不可食，唯柱可食耳。」衆家本皆作濯\n[郭注-3]：珧，王珧，卽小蚌。', '釋魚弟十六', '爾雅', '', DEFAULT, DEFAULT, '8901;194', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5537, '龜;靈;謝;諸果;諸獵;不類;不若', '', '龜，俯者靈，12仰者謝3，4前弇諸果5，6後弇諸獵7，8左倪不類910，11右倪不若。1213', '[郭注-1]：行頭低。\n[釋文-2]：低，丁兮反。\n[釋文-3]：如字，衆家本作射\n[郭注-4]：行頭仰。\n[釋文-5]：古奄字，又作揜，於檢反。衆家作裹，唯郭作此字\n[郭注-6]：甲前長。\n[釋文-7]：力輒反\n[郭注-8]：甲後長。\n[音釋-9]：五計。\n[釋文-10]：五計反，亦有本作睨，下同。力愧反，又力魏反\n[郭注-11]：行頭左庳，今江東所謂左食者，以甲卜審。\n[郭注-12]：行頭右庳，爲右食，甲形皆爾。\n[釋文-13]：俾，普計反，下同，本今作庳。', '釋魚弟十六', '爾雅', '', DEFAULT, DEFAULT, '8996;206;1570;1488/3568;1488/6360;7706/6371;7706/599', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5538, '貝;猋;函;魧;責;玄貝;貽貝;餘蚳;餘泉;巴;囷;責', '朿;枲貝;朿', '貝1，居陸贆{猋}23，在水者蜬{函}；4大者魧56，78小者鰿{責}[朿]910。11玄貝，貽貝[枲貝]12。13餘貾{餘蚳}1415，黃白文。16餘泉17，白黃文。18蚆{巴}1920，博而頯2122。23蜠{囷}2425，大而險[儉]。2627𧐐{責}[朿]2829，小而橢3031。3233', '[釋文-1]：補舍人曰：「水中蟲也」\n[音釋-2]：摽。\n[釋文-3]：字亦作猋，方遥反\n[郭注-4]：水、陸異名也。貝中肉如科斗，但有頭尾耳。\n[音釋-5]：杭。\n[釋文-6]：謝戸郎反，郭胡黨反，字林作蚢，云：「大貝也。」又口葬反\n[郭注-7]：書大傳曰：「大貝如車渠。」車渠謂車𨊾，卽魧屬。\n[釋文-8]：𨊾，音罔。\n[音釋-9]：積。\n[釋文-10]：𩼱，郭音賾，字又作鰿，字林音績\n[郭注-11]：今細貝。亦有紫色者，出日南。\n[釋文-12]：顧餘之反，本又作胎，他來反，字林作𧉟，云：「黑貝也，大才反」\n[郭注-13]：黑色貝也。\n[音釋-14]：池。\n[釋文-15]：蚳，直其反，字或作貾，同\n[郭注-16]：以黃爲質，白爲文㸃。\n[釋文-17]：如字，本或作𧍭\n[郭注-18]：以白爲質，黃爲文㸃。今之紫貝，以紫爲質，黑爲文㸃。\n[音釋-19]：巴。\n[釋文-20]：普巴反，郭音巴，字林同，云：「蠃屬，博而頯也」\n[音釋-21]：匡軌。\n[釋文-22]：布莫反。郭匡軌反，顧又巨追反\n[郭注-23]：頯者，中央廣，兩頭銳。\n[音釋-24]：求隕。\n[釋文-25]：郭求隕反，又丘筠反\n[郭注-26]：險者，謂污薄。\n[釋文-27]：污，音烏。\n[音釋-28]：責。\n[釋文-29]：施音賾，郭音責，沈音積，本或作鰿、又作資、又作𩼱，音皆同\n[音釋-30]：他果。\n[釋文-31]：他果反，狹而長也\n[郭注-32]：卽上小貝。橢謂狹而長。此皆說貝之形容。\n[釋文-33]：陜，乎夾反。', '釋魚弟十六', '爾雅', '5;7;12', DEFAULT, DEFAULT, '3936;6385;4344;7669;3976;2507/3936;3999/3936;3247/8838;3247/7493;9753;3919;3976', '4397;4532/3936;4397', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5539, '榮原;蜥易;蝘蜓;守宮', '榮蚖', '蠑螈{榮原}[榮蚖]12，蜥蜴{蜥易}34。蜥蜴，蝘蜓56。蝘蜓，守宮也。7', '[音釋-1]：榮。原。\n[釋文-2]：音榮，本或作榮。音原，字林作蚖，五丸反，云：「蠑蚖，蛇毉也。」說文同。補舍人曰：「一名黿」\n[音釋-3]：昔。亦。\n[釋文-4]：先歷反。音亦，說文、字林作易，云：「在壁曰蝘蜓，在草曰蜥蜴。」案東方朔云：「非守宮，卽蜥蜴。」是二物也，方言云：「秦晉 西夏謂之守宮，澤中曰蜥蜴，南楚謂之蛇毉，或謂之蠑螈」\n[音釋-5]：偃。徒典。\n[釋文-6]：烏典反。徒典反，字或作𧍿\n[郭注-7]：轉相解，博異語，別四名也。', '釋魚弟十六', '爾雅', '1', DEFAULT, DEFAULT, '3538/7496;8803/6134;8804/8805;4596/4630', '3538/8806', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5540, '镻;蝁;螣;螣蛇;莽;王蛇;蝮虺', '蝮虫', '镻12，蝁34。56螣78，螣蛇910。1112蟒{莽}13，王蛇。14蝮虺[蝮虫]15，博三寸16，首大如擘1718。1920', '[音釋-1]：迭。\n[釋文-2]：大結反，說文云：「蛇毒長也」\n[音釋-3]：烏洛。\n[釋文-4]：烏洛反\n[郭注-5]：蝮屬，大眼，最有毒。今淮南人呼蝁子，音惡。\n[釋文-6]：蝮，芳福反。\n[音釋-7]：朕。\n[釋文-8]：直錦反，字又作朕\n[音釋-9]：騰。\n[釋文-10]：字又作騰，又作𧑞，並同，徒登反，字林云：「神蛇也。」慎子云：「螣蛇遊霚」\n[郭注-11]：龍類也，能興雲霧而遊其中，淮南云蟒蛇。\n[釋文-12]：蟒，音莽，字林云：「大蛇也。」\n[音釋-13]：莽\n[郭注-14]：蟒，蛇最大者，故曰王蛇。\n[釋文-15]：𧐛，字亦作蝮，芳服反，又亡六反，此蛇色如綬，鼻上有針，大者百餘斤，又一名反鼻，鼻一孔。虫，卽虺字也，虛鬼反，說文云：「虫，一名蝮，博三寸，首大如擘。」字林同，舍人亦云：「蝮，一名虺。江 淮以南曰蝮，江 淮以北曰虺。」案：蝮，大蛇也，非虺之類，故郭云「別自一種蛇名蝮虺。」本今作虺\n[釋文-16]：博，廣也，謂身廣三寸\n[音釋-17]：柏。\n[釋文-18]：甫革反，劉昌宗音薄歷反，孫云：「頭如拇指。」郭注三蒼云：「擘，大指也。」案手足大指俱名擘也\n[郭注-19]：身廣三寸，頭大如人擘指。此自一種蛇，名爲蝮虺。\n[釋文-20]：種，章勇反。', '釋魚弟十六', '爾雅', '7', DEFAULT, DEFAULT, '6072;8893;8788;8788/8995;702;78/8995;8787/8802', '8787/8786', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5541, '鯢;鰕', '', '鯢1，大者謂之鰕2。34', '[釋文-1]：五兮反\n[釋文-2]：音遐\n[郭注-3]：今鯢魚，似鮎，四腳，前似獮猴，後似狗，聲如小兒啼，大者長八九尺。\n[釋文-4]：鮎，乃兼反。猴，音侯。狗，音苟。', '釋魚弟十六', '爾雅', '', DEFAULT, DEFAULT, '7622;7666', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5542, '枕;丁;腸;乙;尾;丙', '魴', '魚枕謂之丁1。23魚腸謂之乙4。魚尾謂之丙[魴]。56', '[釋文-1]：之甚反\n[郭注-2]：枕在魚頭骨中，形似篆書「丁」字，可作印。\n[釋文-3]：篆，丈轉反。印，一刃反。\n[釋文-4]：音長\n[郭注-5]：此皆似篆書字，因以名焉。禮記曰：「魚去乙。」然則魚之骨體，盡似丙、丁之屬，因形名之。\n[釋文-6]：去，羌呂反。盡，子忍反。', '釋魚弟十六', '爾雅', '6', DEFAULT, DEFAULT, '3680;9747;2612;9742;5428;9746', '7606', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5543, '神龜;靈龜;攝龜;寶龜;文龜;筮龜;山龜;澤龜;水龜;火龜', '㘝龜', '一曰神龜，1二曰靈龜，23三曰攝龜[㘝龜]45，67四曰寶龜，89五曰文龜，10六曰筮龜1112，1314七曰山龜，八曰澤龜，九曰水龜，十曰火龜。1516', '[郭注-1]：龜之最神明。\n[郭注-2]：涪陵郡出大龜，甲可以卜，緣中叉似瑇瑁，俗呼爲靈叉，卽今觜𧓈龜，一名靈𧓈，能鳴。\n[釋文-3]：涪，音浮，又音苻。緣，悅絹反。瑇，字又作𣫹，音代，或作徒妹反。瑁，字又作𤲰，音妹。觜，字又作蟕，子移反，或子隨反。𧓈，以規反，又下圭反，字林云：「大龜，以胃鳴。」\n[音釋-4]：輙。\n[釋文-5]：謝之涉反，郭袪浹反，施之協反\n[郭注-6]：小龜也，腹甲曲折，解能自張閉，好食蛇，江東呼爲陵龜。\n[釋文-7]：折，之舌反。解，音蟹。好，呼報反。\n[郭注-8]：書曰：「遺我大寶龜。」\n[釋文-9]：遺，唯季反。\n[郭注-10]：甲有文彩者。河圖曰：「靈龜負書，丹甲靑文。」\n[音釋-11]：誓。\n[釋文-12]：舌制反\n[郭注-13]：常在蓍叢下濳伏。見龜䇿傳。\n[釋文-14]：蓍，音尸。叢，才空反，說文云：「草聚生也。」見，音現。筴，初革反，本今作䇿。傳，直戀反。\n[郭注-15]：此皆說龜生之處所。火龜猶火鼠耳。物有含異氣者，不可以常理推，然亦無所怪。\n[釋文-16]：處，昌慮反。', '釋魚弟十六', '爾雅', '3', DEFAULT, DEFAULT, '25/8996;206/8996;7868/8996;4592/8996;5725/8996;2900/8996;5850/8996;7222/8996;6984/8996;6416/8996', '3925/8996', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5544, '鵻其;夫不', '', '隹其{鵻其}1，鳺鴀{夫不}23。45', '[釋文-1]：如字，旁或加鳥，非也\n[音釋-2]：方扶。方浮。\n[釋文-3]：本亦作夫，字林甫于反。本亦作不，同，方浮、方九二反。夫不，楚鳩也。補樊光曰：「孝鳥」\n[郭注-4]：今䳕鳩。\n[釋文-5]：䳕，音浮，又音孚。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '2378/3019;6676/7706', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5545, '鶌鳩;鶻鵃', '', '鶌鳩12，鶻鵃34。5', '[音釋-1]：居物。\n[釋文-2]：居勿反。九牛反。補舍人曰：「今之斑鳩也。」樊光曰：「春去冬來」\n[音釋-3]：骨。嘲。\n[釋文-4]：音骨。竹交反，或竹牛反，字林云：「鶻鵃，小種鳩也。」毛詩草木䟽云：「斑鳩也，桂陽人謂之斑隹。」補孫炎曰：「一名鳴鳩，月令云『鳴鳩，拂其羽』」\n[郭注-5]：似山鵲而小，短尾，靑黑色，多聲，今江東亦呼爲鶻鵃。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '2377/2376;2379/2380', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5546, '尸鳩;秸匊', '秸𪈓', '鳲鳩{尸鳩}12，鴶鵴{秸匊}[秸𪈓]34。56', '[音釋-1]：尸。\n[釋文-2]：音尸，字又作䲩。補樊光曰：「心平均」\n[音釋-3]：古八。菊。\n[釋文-4]：郭古八反，字林音吉。居六反\n[郭注-5]：今之布穀也，江東呼爲穫穀。\n[釋文-6]：穫，戸郭反。', '釋鳥弟十七', '爾雅', '2', DEFAULT, DEFAULT, '5402/2376;4474/5805', '4474/2381', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5547, '笠鳩;并笠', '', '鷑鳩{笠鳩}12，鵧鷑{并笠}34。56', '[音釋-1]：及。\n[釋文-2]：呂、郭巨立反，施音及，下同\n[音釋-3]：符悲。\n[釋文-4]：謝苻悲反，郭方買反、苻尸反，字林父隹反\n[郭注-5]：小黑鳥，鳴自呼，江東名爲烏䳎。\n[釋文-6]：臼，如字，本或作䳎。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '2968/2376;5228/2968', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5548, '鴡鳩;王鴡', '', '鴡鳩12，王鴡。34', '[音釋-1]：七徐。\n[釋文-2]：本又作雎，七徐反\n[郭注-3]：鵰類，今江東呼之爲鶚，好在江渚山邊食魚。毛詩傳曰：「鳥摯而有別。」\n[釋文-4]：鵰，音彫。鶚，五各反。好，呼報反，下皆同。鷙，本又作摯，音至。別，彼列反。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '2457/2376;78/2457', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5549, '鵅;忌欺', '雒', '鵅[雒]12，鵋䳢{忌欺}34。56', '[音釋-1]：格。\n[釋文-2]：古客反，注同\n[音釋-3]：忌。欺。\n[釋文-4]：巨記反，本亦作忌。去其反，本亦作欺，下「欺老」同，本今作䳢\n[郭注-5]：今江東呼鵂鶹爲鵋䳢，亦謂之鴝鵅，音格。\n[釋文-6]：鵂，音休。鶹，音留。鉤，古侯反，本今作鴝。', '釋鳥弟十七', '爾雅', '1', DEFAULT, DEFAULT, '2410;6873/5588', '2282', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5550, '甾;兔軌', '', '鶅{甾}1，鵵軌{兔軌}23。4', '[釋文-1]：側其、側事二反\n[音釋-2]：兔。\n[釋文-3]：他故反，本亦作兔，同\n[郭注-4]：未詳。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '564;6295/9584', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5551, '鴗;天狗', '', '鴗12，天狗。3', '[音釋-1]：立。\n[釋文-2]：音立\n[郭注-3]：小鳥也，靑似翠，食魚，江東呼爲水狗。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '2444;3/6304', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5552, '鷚;天蘥', '', '鷚12，天鸙{天蘥}34。56', '[音釋-1]：亡侯。\n[釋文-2]：字又作𪅡，郭音繆，亡侯反，說文力幼反，孫音流，又丘虯反\n[音釋-3]：藥。\n[釋文-4]：予若反，說文作蘥\n[郭注-5]：大如鷃雀，色似鶉，好高飛作聲，今江東名之天鷚。音綢繆。\n[釋文-6]：鷃，音晏，又一練反。鶉，音淳。綢，音儔。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '2385;3/417', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5553, '鵱;蔞鵝', '', '鵱12，鷜鵝{蔞鵝}34。5', '[音釋-1]：六。\n[釋文-2]：音六\n[音釋-3]：力于。\n[釋文-4]：郭力于反，謝、施力侯反。字亦作鵞，五河反\n[郭注-5]：今之野鵝。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '2420;377/2422', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5554, '鶬;麋鴰', '', '鶬1，麋鴰23。4', '[釋文-1]：音倉，字林七羊反\n[音釋-2]：古活。\n[釋文-3]：音眉，字林作鶥，音同。古活反，說文音刮\n[郭注-4]：今呼鶬鴰。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '2445;6272/2446', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5555, '鵅;烏𪇰', '', '鵅12，烏𪇰34。56', '[音釋-1]：洛。\n[釋文-2]：音洛\n[音釋-3]：剥。\n[釋文-4]：𪈚，郭音駮，字林方沃反，孫音暴\n[郭注-5]：水鳥也，似鶂而短頸，腹翅紫白，背上緑色，江東呼烏𪇰，音駮。\n[釋文-6]：鶂，五歷反，又五結反，水鳥也，春秋云：「六鷁退飛，過宋都」是也。頸，古郢反。駮，布角反。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '2410;2488/2411', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5556, '舒鴈;鵝;舒鳧;鶩', '', '舒鴈1，鵝。23舒鳧4，鶩56。78', '[釋文-1]：補李巡曰：「野曰鴈，家曰鵝」\n[郭注-2]：禮記曰：「出如舒鴈。」今江東呼𪀉，音加。\n[釋文-3]：𪀉，郭音加，說文音河，廣雅云：「𪀉、鵝，鴈也。」\n[釋文-4]：音符。補李巡曰：「在野曰鳧，在家曰鶩，竝鴨也」\n[音釋-5]：木。\n[釋文-6]：音木\n[郭注-7]：鴨也。\n[釋文-8]：𩿼，字又作鴨，一甲反。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '2511/2423;2422;2511/1958;2424', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5557, '鳽;鵁鶄', '', '鳽12，鵁鶄34。56', '[音釋-1]：額肩。\n[釋文-2]：郭五革反，字林音肩\n[音釋-3]：交。精。\n[釋文-4]：音交，本亦作交。音精，本又作精\n[郭注-5]：似鳧，腳高，毛冠，江東人家養之，以厭火災。\n[釋文-6]：厭，以冉反。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '2449;2447/2448', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5558, '與;徑荼', '鸒', '與[鸒]1，鵛鷋{徑荼}23。4', '[釋文-1]：音餘，樊、孫本作鸒\n[音釋-2]：經。徒。\n[釋文-3]：古形反，本亦作徑。音徒\n[郭注-4]：未詳。', '釋鳥弟十七', '爾雅', '1', DEFAULT, DEFAULT, '1782;1225/668', '2386', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5559, '鵜;污澤', '', '鵜12，鴮鸅{污澤}34。56', '[音釋-1]：啼。\n[釋文-2]：大兮反\n[音釋-3]：烏。澤。\n[釋文-4]：毛詩傳作洿，同，音烏，郭火布反。音澤，毛詩傳作澤\n[郭注-5]：今之鵜鶘也，好羣飛，沈水食魚，故名洿澤，俗呼之爲淘河。\n[釋文-6]：鶘，音胡。淘，大刀反。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '2443;7350/7222', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5560, '鶾;天鷄', '翰', '鶾[翰]12，天鷄。3', '[音釋-1]：汗。\n[釋文-2]：本又作翰，胡旦反，樊云：「一名山雞」\n[郭注-3]：鶾鷄，赤羽。逸周書曰：「文鶾若彩鷄，成王時蜀人獻之。」', '釋鳥弟十七', '爾雅', '1', DEFAULT, DEFAULT, '2477;3/2291', '2244', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5561, '鷽;山鵲', '', '鷽12，山鵲。34', '[音釋-1]：握。\n[釋文-2]：郭音握，又音學，又才五反，字林乙竺反。本亦作鵲，七約反\n[郭注-3]：似鵲而有文彩，長尾，觜、腳赤。\n[釋文-4]：𭪿，子髓反，字或作觜，廣雅云：「口也。」', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '2387;5850/2489', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5562, '覃;負雀', '鷂', '鷣{覃}[鷂]12，負雀3。45', '[音釋-1]：淫。\n[釋文-2]：音淫\n[釋文-3]：字或作𪃓，同，房九反\n[郭注-4]：鷣，鷂也，江南呼之爲鷣，善捉雀，因名云，音淫。\n[釋文-5]：鷂，以照反，字林云：「鷙鳥。」下同。', '釋鳥弟十七', '爾雅', '1', DEFAULT, DEFAULT, '3338;3965/2286', '2455', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5563, '齧齒;艾', '', '齧齒，艾。1', '[郭注-1]：未詳。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '1313/1278;389', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5564, '鶨;欺老', '', '鶨12，䳢老{欺老}。345', '[音釋-1]：丑絹。\n[釋文-2]：呂、郭丑絹反，孫勑亂反\n[郭注-3]：鳹鶨也，俗呼爲癡鳥。\n[音釋-4]：鳹，巨炎。\n[釋文-5]：鳹，巨炎反，字林云：「句喙鳥。」', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '2403;5588/5377', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5565, '鳸;鴳', '', '鳸12，鴳34。5', '[音釋-1]：戸。\n[釋文-2]：說文作雇，鳸，籀文也，左傳、詩並作扈，同，音戸\n[音釋-3]：晏。\n[釋文-4]：音晏。補舍人曰：「趣民收麥，令不得晏起也」\n[郭注-5]：今鴳雀。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '2306;2478', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5566, '桑鳸;竊脂', '', '桑鳸，竊脂1。2', '[釋文-1]：音切。音之\n[郭注-2]：俗謂之靑雀，觜曲，食肉，好盜脂膏，因名云。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '3860/2306;4383/2706', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5567, '刀鷯;剖葦', '', '鳭鷯{刀鷯}12，剖葦3。4', '[音釋-1]：力彫。\n[釋文-2]：丁堯反。力小反\n[釋文-3]：普口反\n[郭注-4]：好剖葦皮，食其中蟲，因名云。江東呼蘆虎，似雀，靑班，長尾。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '2744/2407;2767/645', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5568, '桃蟲;鷦;艾', '', '桃蟲，鷦1，其雌鴱{艾}23。45', '[釋文-1]：子遥反\n[音釋-2]：艾。\n[釋文-3]：字或作䳄，同，七移反。本又作𩾘，謝五蓋反，呂、郭音乂\n[郭注-4]：鷦𪃧，桃雀也，俗呼爲巧婦。\n[釋文-5]：𪃧，亡小、亡消二反，字林云：「澤雀。」', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '3426/8973;2399;389', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5569, '鶠;鳳;皇', '', '鶠1，鳳2，其雌皇3。45', '[釋文-1]：音偃\n[釋文-2]：郭云：「瑞應鳥也，雞頭、蛇頸、燕頷、龜背、魚尾、五彩色，高六尺許。」說文云：「神鳥也，天老曰：鳳象，麟前鹿後、蛇頸魚尾、龍文龜背、燕頷雞喙，五色備舉，出於東方君子之國，翶翔四海之外，過崑崙，飲砥柱，濯羽弱水，暮宿風穴，見則天下大安寧。」毛詩草木䟽云：「雄曰鳳，雌曰皇。一名鶠，其雛名鸑鷟。或曰：鳳，一名鸑鷟，其形鴻前鹿後，蛇頸魚尾，龍文龜身，燕頷雞喙，首戴德，頸揭義，背負仁，翼挾信，心抱忠，足履正，尾繫武，非梧桐不棲，非竹實不食，朝鳴曰發明，晝鳴曰上翔，夕鳴曰滿昌，昬鳴曰固常，夜鳴曰保長。得其屢象之，一則過之，二則翔之，三則集之，四則春秋居之，五則沒身居之」\n[釋文-3]：本亦作凰\n[郭注-4]：瑞應鳥，鷄頭、蛇頸、燕頷、龜背、魚尾，五彩色，其高六尺許。\n[釋文-5]：應，音譍。頸，吉丼反。頷，乎感反。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '2408;2370;80', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5570, '卽令;雝渠', '精列;雝𪆫', '䳭鴒{卽令}[精列]1，雝渠[雝𪆫]2。3', '[釋文-1]：𪃹，詩作脊，同，精益反，本今作䳭。詩作令，同，力丁反\n[釋文-2]：於恭反。字或作𪆂\n[郭注-3]：雀屬也，飛則鳴，行則揺。', '釋鳥弟十七', '爾雅', '1;2', DEFAULT, DEFAULT, '3198/5779;2300/7265', '4353/2772;2300/2438', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5571, '鸒斯;卑居', '', '鸒斯12，鵯鶋{卑居}34。5', '[音釋-1]：預。\n[釋文-2]：弋庶反，毛詩傳云：「鸒，鵯鶋，雅烏也。」小雅云：「小而腹下白，不反哺者，謂之雅烏。」說文、字林皆云：「楚烏也，一名鸒，一名鵯鶋，秦云雅烏。」本多無此字，案「斯」是詩人協句之言，後人因將添此字也，而俗本遂斯旁作鳥，謬甚\n[音釋-3]：匹。\n[釋文-4]：音匹。音居，本或作居。補舍人曰：「謂壁居也」\n[郭注-5]：雅烏也，小而多羣，腹下白，江東亦呼爲鵯烏，音匹。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '2386/9485;1932/5404', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5572, '燕;白脰烏', '', '燕1，白脰烏23。4', '[釋文-1]：於見反，象形字，或加鳥者，非\n[音釋-2]：豆。\n[釋文-3]：音豆\n[郭注-4]：脰，頸。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '7688;4931/2603/2488', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5573, '如;牟母', '鴑', '鴽{如}[鴑]12，鴾母{牟母}34。56', '[音釋-1]：如。\n[釋文-2]：音如\n[音釋-3]：謀。\n[釋文-4]：字或作牟，音謀。如字，李音無，舍人本作蕪\n[郭注-5]：䳺也，靑州呼鴾母。\n[釋文-6]：䳺，烏南反。', '釋鳥弟十七', '爾雅', '1', DEFAULT, DEFAULT, '8245;751/8141', '2305', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5574, '密肌;繫英;巂周', '', '密肌1，繫英2。34巂周56。7', '[釋文-1]：𪅮，音密，本今作密。音飢\n[釋文-2]：音計。鶧，音英，本今作英\n[郭注-3]：釋蟲以有此名，疑誤重。\n[釋文-4]：重，直用反。\n[音釋-5]：擕。\n[釋文-6]：戸圭反，說文：「周燕也，蜀王 望帝淫其相妻，慙亡去，爲子巂鳥，故蜀人聞子巂鳴，皆起曰是『望帝』也」\n[郭注-7]：子巂鳥，出蜀中。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '5876/2598;8731/502;2284/877', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5575, '燕燕;鳦', '', '燕燕，鳦12。3', '[音釋-1]：乙。\n[釋文-2]：音乙，本或作乙，或音軋\n[郭注-3]：詩云：「燕燕于飛。」一名玄鳥，齊人呼鳦。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '7688/7688;7703', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5576, '鴟鴞;寧鴂', '', '鴟鴞12，鸋鴂{寧鴂}34。5', '[音釋-1]：尺之。遥。\n[釋文-2]：尺之反，或作鵄。于驕反\n[音釋-3]：寧。決。\n[釋文-4]：音寧，又音𡩋。音決，鸋鴂，今之巧婦鳥\n[郭注-5]：鴟類。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '2297/2389;3057/2390', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5577, '狂;茅鴟;怪鴟;梟;鴟', '', '狂1，茅鴟2。34怪鴟5。6梟78，鴟。9', '[釋文-1]：如字，本或作鵟。補舍人曰：「喜食鼠，大目也」\n[釋文-2]：本或作鶜\n[郭注-3]：今𪁪鴟也，似鷹而白。\n[釋文-4]：𪁪，字又作𪁒，亡項反，又亡江反，字林云：「鴟也，亡董反。」廣雅云：「茅鴟，𪁪也。」鷹，於陵反。\n[釋文-5]：古拜反。補舍人曰：「一名怪鳥，一名鵂鶹，南陽名鉤鵅。晝伏夜行，鳴爲怪也」\n[郭注-6]：卽鴟鵂也，見廣雅。今江東通呼此屬爲怪鳥。\n[音釋-7]：嬌。\n[釋文-8]：古堯反\n[郭注-9]：土梟。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '6370;326/2297;6836/2297;3834;2297', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5578, '皆;劉疾', '', '鶛{皆}12，劉疾3。4', '[音釋-1]：皆。\n[釋文-2]：音皆，又音界\n[釋文-3]：字或作留，音留\n[郭注-4]：未詳。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '2227;9446/4696', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5579, '鷇;雛', '', '生哺1，鷇23。45生噣67，雛89。10', '[釋文-1]：蒲路反，說文云：「口中嚼食也」\n[音釋-2]：古候。\n[釋文-3]：謝苦候反，字林工豆反，郭音同，又古互反，鳥子須哺而食者，燕雀之屬也，史記云：「趙武靈王探雀鷇而食之」是也\n[郭注-4]：鳥子須母食之。\n[釋文-5]：食，本或作飤，同，音嗣。\n[音釋-6]：啄。\n[釋文-7]：竹角反，義當作啄\n[音釋-8]：仕俱。\n[釋文-9]：字林云：「雞子也，匠于反。又仕俱反，鳥子生而能自啄者。」禮記云：「雛尾不盈握不食」是也，字或作鶵，同。或云：仕俱反者，是鳥子也，匠于反者，爲鳳類也\n[郭注-10]：能自食。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '2480;2292', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5580, '爰居;雜縣', '', '爰居1，雜縣23。45', '[釋文-1]：本亦作鶢，音袁。本或作鶋，同，李云：「爰居，海鳥也。」樊云：「似鳳皇」\n[音釋-2]：玄。\n[釋文-3]：字亦作𣠛\n[郭注-4]：國語曰：「海鳥爰居。」漢元帝時，琅邪有大鳥如馬駒，時人謂之爰居。\n[釋文-5]：琅，音郎。邪，以嗟反。駒，音俱。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '2517/5404;5335/5707', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5581, '鳸;鳻盾;竊玄;竊藍;竊黃;竊脂;竊丹;唶唶;嘖嘖', '', '{鳸}春鳸1，鳻鶞{鳻盾}23。夏鳸，竊玄4。秋鳸，竊藍5。冬鳸，竊黃6。桑鳸，竊脂7。棘鳸，竊丹8。行鳸9，唶唶1011。宵鳸，嘖嘖1213。14', '[釋文-1]：扈，音戸\n[音釋-2]：汾。勑倫。\n[釋文-3]：本亦作分，同，扶云反，或芳云反。勑倫反，本亦作遁。補舍人曰：「主五土，宜穀種樹也」\n[釋文-4]：補舍人曰：「色黑，趣民耘苗者也」\n[釋文-5]：力甘反。補舍人曰：「色靑，趣民收斂者也」\n[釋文-6]：補舍人曰：「色黃，趣民葢藏者也」\n[釋文-7]：補舍人曰：「爲蠶驅雀者也」\n[釋文-8]：補舍人曰：「色赤，爲果驅鳥者也」\n[釋文-9]：如字\n[音釋-10]：卽。\n[釋文-11]：說文亦借字也，一云大聲也。莊百反，顧子夜反，又子亦反，廣雅云：「唶唶，鳴也。」補舍人曰：「晝行鳴，爲民驅鳥者也」\n[音釋-12]：責。\n[釋文-13]：莊革反，廣雅云：「鳴也。」說文：「嘖，呼也。」補舍人曰：「夜行鳴，爲農驅獸者也」又曰：「皆隨四時，同依諸鳸爲節候也」\n[郭注-14]：諸鳸皆因其毛、色、音、聲以爲名。𥨸藍，靑色。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '2306;2483/2221;4383/2507;4383/282;4383/9203;4383/2706;4383/3186;1590/1590;915/915', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5582, '鵖鴔;戴䋕', '', '鵖鴔12，戴鵀{戴䋕}34。56', '[音釋-1]：彼及。皮及。\n[釋文-2]：彼及反，郭房汲反，字林方立反。皮及反，郭北及反，字林房立反，又音伏\n[音釋-3]：女金。\n[釋文-4]：本亦作䋕，女金反，施汝沁反，方言云：「戴鵀，一名戴南，一名戴勝」\n[郭注-5]：鵀卽頭上勝，今亦呼爲戴勝。鵖鴔猶𪃃鶝，語聲轉耳。\n[釋文-6]：勝，尸證反。𪃃，皮逼反。鶝，音逼，又音福。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '2436/2435;1779/8535', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5583, '䲱;澤虞', '', '鶭{䲱}12，澤虞3。45', '[音釋-1]：孚往。\n[釋文-2]：本或作鴋，說文作䲱，孚往反\n[釋文-3]：字林作鸅鸆，音同\n[郭注-4]：今婟澤鳥，似水鴞，蒼黑色，常在澤中，見人輒鳴，喚不去，有象主守之官，因名云。俗呼爲護田鳥。\n[釋文-5]：婟，戸故反，下同，說文云：「嫪也。」廣雅云：「妬也。」聲類云：「婟嫪，戀惜也。」字書作嫮，一本作𧥮，皆同。嫪，力報反。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '2392;7222/3110', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5584, '鶿;鷧', '', '鶿12，鷧34。56', '[音釋-1]：慈。\n[釋文-2]：音慈，字林云：「似鶂而黑」\n[音釋-3]：於計。\n[釋文-4]：郭懿、翳二音，字林英莅反\n[郭注-5]：卽鸕鶿也。觜頭曲如鈎，食魚。\n[釋文-6]：觜，子髓反。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '2433;2434', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5585, '鷯;𨿡;皆;痺', '', '鷯1，鶉{𨿡}2，其雄鶛{皆}3、牝痺45。67', '[釋文-1]：字林力彫反\n[釋文-2]：順春反\n[釋文-3]：音皆\n[音釋-4]：脾。\n[釋文-5]：毗忍反，舊扶死反。庳，婢支反，施音婢，郭音卑\n[郭注-6]：鴞，䳺屬。\n[釋文-7]：䳺，烏含反。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '2407;2307;2227;4754', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5586, '爾;沈鳧', '湛鳧', '鸍{爾}12，沈鳧[湛鳧]3。4', '[音釋-1]：施。\n[釋文-2]：郭音施，尸支反，字林亡支反\n[釋文-3]：直今反\n[郭注-4]：似鴨而小，長尾，背上有文。今江東亦呼爲鸍，音施。', '釋鳥弟十七', '爾雅', '2', DEFAULT, DEFAULT, '2090;7320/1958', '7296/1958', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5587, '幼頭;鵁', '', '鴢頭{幼頭}12，鵁34。56', '[音釋-1]：拗。\n[釋文-2]：謝烏卯反，郭音杳，字林音幼。字或作投\n[音釋-3]：許交。\n[釋文-4]：本或作𩾾，郭音髐，虛交反，又音交\n[郭注-5]：似鳧，腳近尾，略不能行，江東謂之魚鵁，音髐箭。\n[釋文-6]：近，附近之近。箭，子賤反。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '2499/5601;2447', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5588, '鵽鳩;寇雉', '', '鵽鳩12，寇雉3。45', '[音釋-1]：丁刮。\n[釋文-2]：貞刮、直活二反\n[釋文-3]：苦候反\n[郭注-4]：鵽，大如鴿，似雌雉，鼠腳，無後指，岐尾，爲鳥愍急，羣飛，出北方沙漠地。\n[釋文-5]：鴿，古合反。岐，音祁。憨，呼濫反，字書云：「愚也。」漠，音莫。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '2419/2376;2043/2289', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5589, '雈;老兔', '', '雈12，老鵵{老兔}3。4', '[音釋-1]：丸。\n[釋文-2]：音桓\n[釋文-3]：本又作菟，音兔\n[郭注-4]：木兔也，似鴟鵂而小，兔頭，有角，毛腳，夜飛，好食鷄。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '2322;5377/6295', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5590, '突胡鳥', '', '鶟鶦鳥{突胡鳥}12。3', '[音釋-1]：突。胡。\n[釋文-2]：徒忽反，本亦作突。胡，字或作鶦。補孫炎曰：「水鳥」\n[郭注-3]：似雉，靑身，白頭。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '4667/2678/2369', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5591, '狂;㝱鳥', '', '狂1，㝱鳥2。3', '[釋文-1]：鵟，音狂，本今作狂\n[釋文-2]：亡貢反\n[郭注-3]：狂鳥，五色，有冠，見山海經。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '6370;4685/2369', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5592, '皇;黃鳥', '', '皇，黃鳥。12', '[郭注-1]：俗呼黃離留，亦名摶黍。\n[釋文-2]：摶，徒端反。黍，音暑。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '80;9203/2369', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5593, '翠;鷸', '', '翠，鷸12。34', '[音釋-1]：聿。\n[釋文-2]：聿、述二音，左傳云：「鄭 子臧好聚鷸冠」，卽翠鳥毛也。補樊光曰：「靑羽，出交州。」李巡曰：「羽可以飾物」\n[郭注-3]：似燕，紺色，生鬱林。\n[釋文-4]：紺，古闇反。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '2247;2429', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5594, '蜀;山烏', '', '鸀{蜀}12，山烏。34', '[音釋-1]：濁。\n[釋文-2]：濁、蜀二音\n[郭注-3]：似烏而小，赤觜，穴乳，出西方。\n[釋文-4]：乳，如注反，下同。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '8828;5850/2488', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5595, '蝙蝠;服翼', '', '蝙蝠1，服翼。23', '[釋文-1]：音邊。音福\n[郭注-2]：齊人呼爲蟙䘃，或謂之仙鼠。\n[釋文-3]：蟙，章弋反。䘃，亡北反。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '8931/8932;5449/7695', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5596, '晨風;鸇', '鷐風', '晨風[鷐風]1，鸇23。45', '[釋文-1]：如字，本或作鷐。補舍人曰：「鷙鳥也」\n[音釋-2]：之然。\n[釋文-3]：之然反，說文止仙反，字林巳仙反\n[郭注-4]：鷂屬。詩曰：「鴪彼晨風。」\n[釋文-5]：鷂，羊召反。鴪，戸橘反，字林云：「鸇飛貌。」', '釋鳥弟十七', '爾雅', '1', DEFAULT, DEFAULT, '4318/8979;2459', '2460/8979', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5597, '揚;白鷢', '', '鸉{揚}1，白鷢23。45', '[釋文-1]：音楊\n[音釋-2]：巨月。\n[釋文-3]：巨月反，說文云：「白鷢，王雎也。」字林同\n[郭注-4]：似鷹，尾上白。\n[釋文-5]：鷹，於陵反。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '7958;4931/2456', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5598, '寇雉;泆泆', '', '寇雉，泆泆1。2', '[釋文-1]：音逸\n[郭注-2]：卽鵽鳩也。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '2043/2289;7225/7225', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5599, '眞;蟁母', '', '鷆{眞}12，蟁母34。56', '[音釋-1]：田。\n[釋文-2]：田、真二音\n[音釋-3]：文。\n[釋文-4]：本或作鴖，郭云：「皆古蚊字，音文。」案說文蟁，正字，蚊，俗字，或作𧉬，字林亡巾反\n[郭注-5]：似烏𪇰而大，黃白雜文，鳴如鴿聲，今江東呼爲蚊母。俗說此鳥常吐蚊，故以名云。\n[釋文-6]：𪈚，布角反。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '5215;8964/8141', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5600, '鷉;須蠃', '', '鷉12，須鸁{須蠃}34。56', '[音釋-1]：梯。\n[釋文-2]：他兮反，字或作鷈\n[音釋-3]：螺。\n[釋文-4]：力戈反\n[郭注-5]：鷉，鷿鷉，似鳧而小，膏中瑩刀。\n[釋文-6]：𪇊，蒲歷反，本今作鷿。鎣，烏暝反，本今作瑩。瑩，磨瑩也。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '2431;5708/8855', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5601, '吾鼠;夷由', '', '鼯鼠{吾鼠}12，夷由3。45', '[音釋-1]：吾。\n[釋文-2]：音吾，或作𪁙\n[釋文-3]：字或作𩿬\n[郭注-4]：狀如小狐，似蝙蝠，肉翅，翅、尾、項、脅毛紫赤色，背上蒼艾色，腹下黃，喙頷雜白，腳短爪長，尾三尺許，飛且乳，亦謂之飛生，聲如人呼，食火煙，能從高赴下，不能從下上高。\n[釋文-5]：狐，音乎。項，乎講反。脅，許業反。喙，許穢反。呼，火故反。上，時丈反。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '837/6393;6615/8516', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5602, '倉庚;商庚', '', '倉庚，商庚1。23', '[釋文-1]：本或旁皆加鳥\n[郭注-2]：卽鵹黃也。\n[釋文-3]：鵹，音離。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '3279/9755;1453/9755', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5603, '鴩;餔𢻃', '', '鴩12，餔𢻃34。5', '[音釋-1]：大結。\n[釋文-2]：䳀，大結反，說文音吐節反，本今作鴩\n[音釋-3]：步。豉。\n[釋文-4]：音步，字或作鵏。豉，如字，本今作𢻃\n[郭注-5]：未詳。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '2395;3228/4508', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5604, '鷹;來鳩', '', '鷹，鶆鳩{來鳩}12。34', '[音釋-1]：來。\n[釋文-2]：來，字或作鶆，郭讀作爽，所丈反，衆家並依字，樊云：「來鳩，鷞鳩也。鷙鳥。」字林作鶆，音來，云：「鶆鳩，鷹也」\n[郭注-3]：鶆，當爲鷞，字之誤耳，左傳作「鷞鳩」是也。\n[釋文-4]：爽，本或作鷞。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '2296;3348/2376', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5605, '兼兼;比翼', '', '鶼鶼{兼兼}1，比翼。2', '[釋文-1]：古恬反，衆家作兼兼，李云：「鳥有一目一翅，相得乃飛，故曰兼兼也」\n[郭注-2]：說已在上。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '4506/4506;5229/7695', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5606, '𩁟黃;楚雀', '', '鵹黃{𩁟黃}12，楚雀。3', '[音釋-1]：離。\n[釋文-2]：詩傳作離，阮、謝同力知反，施音黎，說文作𩁟，云：「其色黎黑而黃也。」又釋云：「離黃，倉庾也，鳴則蠶生。」字林作𨿯，力兮反，毛詩草木䟽云：「黃麗留也，或謂之黃栗留也，幽州人謂之黃鸎，一名倉庚，一名商庚，一名鵹黃，一名楚雀，齊人謂之摶黍，關西謂之黃鳥。」方言云：「自關而東謂之倉庚，關西謂之黃鸝留也」\n[郭注-3]：卽倉庚也。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '2303/9203;3851/2286', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5607, '列;斲木', '', '鴷{列}12，斵木{斲木}3。45', '[音釋-1]：列。\n[釋文-2]：音列\n[釋文-3]：陟角反\n[郭注-4]：口如錐，長數寸，常斵樹食蟲，因名云。\n[釋文-5]：錐，章誰反。數，所主反。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '2772;9482/3413', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5608, '敫;唐屠', '', '鸄{敫}12，鶶鷵{唐屠}34。5', '[音釋-1]：激。\n[釋文-2]：郭古狄反，字林工了反\n[音釋-3]：唐。徒。\n[釋文-4]：音唐。音徒\n[郭注-5]：似烏，蒼白色。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '2515;878/5420', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5609, '盧;諸雉', '', '鸕{盧}1，諸雉。2', '[釋文-1]：謝力吳反，施力魚反\n[郭注-2]：未詳。或云卽今雉。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '3144;1488/2289', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5610, '鷺;舂鉏', '', '鷺1，舂鉏23。45', '[釋文-1]：音路，毛詩傳云：「白鳥也。」字林音盧\n[音釋-2]：鋤。\n[釋文-3]：舒容反。鋤，字又作鉏，仕居反\n[郭注-4]：白鷺也，頭、翅、背上皆有長翰毛，今江東人取以爲睫㰚，名之曰白鷺縗。\n[釋文-5]：翰，音汗。睫，字又作䀹，音接，說文云：「目旁毛也。」三蒼云：「眥毛也。」㰚，力知反。縗，西雷反，又西河反。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '2413;4524/9350', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5611, '鷂雉;鷮雉;卜雉;鷩雉;秩秩;海雉;翟;山雉;雗雉;卓雉;奮;翬;鷂;𠷎;甾;稀;蹲', '搖雉;搖', '鷂雉[搖雉]12。3鷮雉45。6鳪雉{卜雉}78。9鷩雉10。11秩秩12，海雉。13鸐{翟}1415，山雉。16雗雉1718，鵫雉{卓雉}1920。2122雉絶有力23，奮。24伊洛而南，素質，五采皆備成章曰翬2526。27江淮而南，靑質，五采皆備成章曰鷂[搖]。28南方曰𠷎2930，東方曰鶅{甾}3132，北方曰鵗{稀}3334，西方曰鷷{蹲}3536。37', '[音釋-1]：遥。\n[釋文-2]：郭音遥，下同\n[郭注-3]：靑質五彩。\n[音釋-4]：驕。\n[釋文-5]：音驕\n[郭注-6]：卽鷮雞也，長尾，走且鳴。\n[音釋-7]：卜。\n[釋文-8]：音卜，郭方木反，又方角反\n[郭注-9]：黃色，鳴自呼。\n[釋文-10]：謝必滅反，呂、郭方世反。補樊光曰：「立秋來，立冬去，入水爲蜃」\n[郭注-11]：似山雞而小，冠背毛黃，腹下赤，項緑，色鮮明。\n[釋文-12]：本又作失失，謝持乙反，施音逸\n[郭注-13]：如雉而黑，在海中山上。\n[音釋-14]：狄。\n[釋文-15]：翟，音狄，本又作鸐，又音濯。補樊光曰：「其羽可持而舞，詩云『右手秉翟』」\n[郭注-16]：長尾者。\n[音釋-17]：汗。\n[釋文-18]：戸旦反，字又作翰\n[音釋-19]：丁罩。\n[釋文-20]：呂、郭音罩，陟孝反，又音卓\n[郭注-21]：今白鵫也。江東呼白雗，亦名白雉。\n[釋文-22]：䳆，音白，本今作白。𨷖，丁豆反，又丁候反。\n[釋文-23]：補樊光曰：「雉者，夷也，夷平也，使度量器用平也」\n[郭注-24]：最健鬭。\n[音釋-25]：暉。\n[釋文-26]：音暉\n[郭注-27]：翬亦雉屬，言其毛色光鮮。\n[郭注-28]：卽鷂雉也。\n[音釋-29]：儔。\n[釋文-30]：本或作𪆇，直留反，郭徒留反，字林女知反。補樊光曰：「配攻金之工」\n[音釋-31]：緇。\n[釋文-32]：側其反。補樊光曰：「配摶埴之工」\n[音釋-33]：希。\n[釋文-34]：音希。補樊光曰：「配攻皮之工」\n[音釋-35]：遵。\n[釋文-36]：郭音遵，謝徂尊反。補樊光曰：「配攻木之工」\n[郭注-37]：說四方雉之名。', '釋鳥弟十七', '爾雅', '1;13', DEFAULT, DEFAULT, '2455/2289;2474/2289;2074/2289;2466/2289;4468/4468;7131/2289;2245;5850/2289;2288/2289;5224/2289;2321;2260;2455;879;564;4428;1388', '7951/2289;7951', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5612, '余;突', '', '鳥鼠同穴1，其鳥爲鵌{余}23，其鼠爲鼵{突}45。67', '[釋文-1]：補李巡曰：「共處一穴，天性然也」\n[音釋-2]：徒。\n[釋文-3]：音途\n[音釋-4]：突。\n[釋文-5]：徒忽反\n[郭注-6]：鼵，如人家鼠而短尾。鵌似鵽而小，黃黑色，入地三四尺，鼠在內，鳥在外。今在隴西 首陽縣 鳥鼠同穴山中。孔氏尚書傳云：「共爲雄雌。」張氏地理記云：「不爲牝牡。」\n[釋文-7]：鵽，丁刮反。牝，毗忍反。牡，音母。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '718;4667', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5613, '𪈩專;畐蹂', '', '鸛鷒{𪈩專}12，鶝鶔{畐蹂}34，如鵲5，短尾，射之67，銜矢射人。89', '[音釋-1]：歡。團。\n[釋文-2]：音歡。徒端反\n[音釋-3]：福。柔。\n[釋文-4]：音福，字亦作福。而由反，本亦作柔，或作蹂，音同\n[釋文-5]：七藥反\n[音釋-6]：食亦。\n[釋文-7]：食亦反，注同\n[郭注-8]：或說曰：鸛鷒、鶝鶔，一名𡡙羿。\n[釋文-9]：𡡙，字又作隋，徒課反，字書云：「古以爲懈惰字。」羿，五計反，古之善射者，言此鳥捷勁，雖羿之善射，亦懈惰不敢射也，故以名云。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '2458/1985;3340/9732', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5614, '㚇;翔;翬;樸;企;縮', '', '鵲鶪醜12，其飛也翪{㚇}34。56鳶{弋}[鷳]烏醜78，其飛也翔。9鷹隼醜10，其飛也翬11。12鳧鴈醜，其足蹼{樸}1314，1516其踵企17。1819烏鵲醜，其掌縮20。21', '[音釋-1]：工役。\n[釋文-2]：古闃反，字林工役反\n[音釋-3]：宗。\n[釋文-4]：子工反，字林作㚇，音子弄反\n[郭注-5]：竦翅上下。\n[釋文-6]：竦，思勇反。上，時掌反。\n[音釋-7]：玄。\n[釋文-8]：悅專反，字亦作䳒\n[郭注-9]：布翅翺翔。\n[釋文-10]：字或作應。西尹反，本或作鶽，案隹卽鳥也，無勞更加\n[釋文-11]：補舍人曰：「其飛疾羽聲也」\n[郭注-12]：鼓翅翬翬然疾。\n[音釋-13]：卜。\n[釋文-14]：𨆯，本又作蹼，音補木反\n[郭注-15]：腳指閒有幕，蹼屬相著。\n[釋文-16]：幕，亡博反。屬，章欲反。著，直畧反。\n[釋文-17]：章勇反，聲類云：「足跟也。」去豉反，字或作跂\n[郭注-18]：飛卽伸其腳跟企直。\n[釋文-19]：跟，音根，釋名云：「足後曰跟。」\n[釋文-20]：所六反\n[郭注-21]：飛縮腳腹下。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '3375;2268;2260;3607;4954;8565', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5615, '亢;嚨;粻;素', '𡜝;箱;橾', '亢12，鳥嚨[𡜝]3。45其粻[箱]6，嗉{素}[橾]78。910', '[音釋-1]：戸郎。\n[釋文-2]：胡郎反，郭云：「咽也。」舍人云：「鳥高飛也」\n[釋文-3]：力東反，樊云：「亢，星鳥也，嚨嚨，亢鳥之頸也。」郭云：「嚨謂喉嚨。」舍人云：「嚨嚨，財可見也」\n[郭注-4]：嚨謂喉嚨，亢卽咽。\n[釋文-5]：喉，音侯，蒼頡篇云：「咽也。」咽，於見反，說文云：「嗌也。」又於賢反。\n[釋文-6]：音張\n[音釋-7]：素。\n[釋文-8]：音素\n[郭注-9]：嗉者，受食之處，別名嗉，今江東呼粻。\n[釋文-10]：處，昌慮反。', '釋鳥弟十七', '爾雅', '2;3;4', DEFAULT, DEFAULT, '6655;785;4384;8776', '8206;2969;3780', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5616, '𨿡;文;如;寧;鷚', '鴑;雡', '鶉{𨿡}子鳼{文}12，鴽{如}[鴑]子鸋{寧}3。45雉之暮子爲鷚[雡]67。89', '[音釋-1]：文。\n[釋文-2]：音純。音文\n[釋文-3]：音如。音寧\n[郭注-4]：別䳺鶉雛之名。\n[釋文-5]：䳺，烏含反。雛，仕俱反。\n[音釋-6]：力救。\n[釋文-7]：力救反，又力求反，說文作雡，云：「鳥大雛也。一曰：雉之暮子也。」字林力幼反\n[郭注-8]：晚生者。今呼少雞爲鷚。\n[釋文-9]：少，詩照反，下文同。', '釋鳥弟十七', '爾雅', '3;5', DEFAULT, DEFAULT, '2307;5725;8245;3057;2385', '2305;2293', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5617, '雄;雌', '', '鳥之雌雄不可別者12，以翼右掩左，雄；左掩右，雌。', '[音釋-1]：彼列。\n[釋文-2]：彼列反', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '2313;2314', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5618, '鶹栗', '鶹離', '鳥少美長醜爲鶹鷅{鶹栗}[鶹離]12。34', '[音釋-1]：丁丈。留。栗。\n[釋文-2]：丁丈反。音留。音栗，本亦作栗\n[郭注-3]：鶹鷅猶留離，詩所謂「留離之子」。\n[釋文-4]：留離，詩字如此，或作鶹離，後人改耳。', '釋鳥弟十七', '爾雅', '1', DEFAULT, DEFAULT, '2401/4393', '2401/2294', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5619, '禽;獸', '', '二足而羽謂之禽，四足而毛謂之獸。', '', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '9733;9740', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5620, '鶪;伯勞', '', '鶪1，伯勞也。23', '[釋文-1]：補樊光曰：「夏至來，冬至去」\n[郭注-2]：似鶷𪆰而大，左傳曰「伯趙」是。\n[釋文-3]：鶷，音鎋。𪆰，工鎋反，又午鎋反，字林云：「鶷𪆰，似伯勞而小。」', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '2384;4967/9236', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5621, '倉庚;𩁟黃', '', '倉庚，黧黃{𩁟黃}也12。3', '[音釋-1]：力知。\n[釋文-2]：鵹，力兮反，又力知反\n[郭注-3]：其色黧黑而黃，因以名云。', '釋鳥弟十七', '爾雅', '', DEFAULT, DEFAULT, '3279/9755;2303/9203', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5622, '麋;麔;麎;夭;躔;狄', '幺/羔', '麋1，牡麔23、牝麎45，其子䴠{夭}[幺/羔]67，89其跡躔10。11絶有力，狄。', '[釋文-1]：亡悲反，字林云：「鹿屬也，以冬至日解角」\n[音釋-2]：咎。\n[釋文-3]：音母。其九反\n[音釋-4]：辰。\n[釋文-5]：毗忍反。郭音辰，又音腎，字林上尸反\n[音釋-6]：於兆。\n[釋文-7]：於兆反，又於老反，麋子也，一曰少長曰䴠\n[郭注-8]：國語曰：「獸長麛䴠。」\n[釋文-9]：長，丁丈反。麛，音迷，本或作麑，音同。\n[釋文-10]：子益反，字或作迹，又作蹟，音訓並同。郭直連反，又持展反，方言云：「躔，循也、歷也、行也」\n[郭注-11]：腳所踐處。', '釋獸弟十八', '爾雅', '4', DEFAULT, DEFAULT, '6272;6274;6273;6623;1356;6372', '2498/0/2335', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5623, '鹿;麚;麀;麛;𨒪;麉', '', '鹿，牡麚12、牝麀34，其子麛5，其跡速{𨒪}6，絶有力，𪊑{麉}78。', '[音釋-1]：加。\n[釋文-2]：音加\n[音釋-3]：於牛。\n[釋文-4]：於牛反\n[音釋-5]：迷\n[釋文-6]：𪋝，素卜反，本又作速，字林云：「鹿迹。一曰速，鹿子」\n[音釋-7]：堅。\n[釋文-8]：郭音堅，又音牽，又音磬', '釋獸弟十八', '爾雅', '', DEFAULT, DEFAULT, '6263;6264;6275;6268;1093;6269', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5624, '麕;噳;栗;且;解;豜', '麤;𨼬;麉', '麕12，牡麌{噳}34、56牝麜{栗}78，其子麆{且}[麤]910，其跡解[𨼬]11，絶有力，豜[麉]1213。', '[音釋-1]：君。\n[釋文-2]：九倫反，本又作麇，亦作麏，說文麕，籀文麇，字林云：「麇，麞也。」又九文反\n[音釋-3]：魚矩。\n[釋文-4]：一作𪋬，魚矩反，字林音吳\n[郭注-5]：詩曰：「麀鹿麌麌。」鄭康成解卽謂此也，但重言耳。\n[釋文-6]：重，直用反。\n[音釋-7]：栗。\n[釋文-8]：麜，音栗\n[音釋-9]：助。\n[釋文-10]：麆，音助，本亦作𪊹，同\n[釋文-11]： 解，音蟹，施佳買反\n[音釋-12]：五見。\n[釋文-13]：郭音與上𪊑字同，顧五見反，又古典反', '釋獸弟十八', '爾雅', '4;5;6', DEFAULT, DEFAULT, '6277;956;4393;9473;2851;6084', '6289;9673;6269', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5625, '狼;貛;敫;迅', '', '狼1，牡貛2、牝狼，其子獥{敫}34，絶有力，迅5。', '[釋文-1]：音郎，字林云：「獸似犬」\n[釋文-2]：字林于丸反\n[音釋-3]：亦。\n[釋文-4]：胡狄、古狄、工弔三反\n[釋文-5]：音信，又音峻', '釋獸弟十八', '爾雅', '', DEFAULT, DEFAULT, '6379;6128;2515;1122', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5626, '兔;嬎;迒;欣', '婏;筋', '兔1，子嬎[婏]23，45其跡迒67，絶有力，欣[筋]8。', '[釋文-1]：字又作菟，勑故反\n[音釋-2]：芳萬。\n[釋文-3]：婏，匹萬反，又匹附反，本或作嬎，敷萬反\n[郭注-4]：俗呼曰𩆟。\n[釋文-5]：𩆟，字林云：「兔子也。」乃俱、乃侯二反。\n[音釋-6]：剛。\n[釋文-7]：音剛，又戸郎反，褚詮之云：「兔道也。」阮孝緒云：「獸迹也」\n[釋文-8]：如字，本或作忻', '釋獸弟十八', '爾雅', '2;4', DEFAULT, DEFAULT, '6295;8138;1207;5538', '6298;2741', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5627, '豕;豬;䝐;豶;幺;幼;奏;薀;豵;師;特;竲;亥;刻;戹;豝', '湊;對', '豕，子豬1。23䝐45，豶67。8幺910，幼11。1213奏[湊]者1415，豱{薀}1617。1819豕生三，豵2021；二，師[對]；一，特。22所寢，橧{竲}2324。2526四豴皆白2728，豥{亥}2930。3132其跡刻33。絶有力，䝈{戹}3435。36牝，豝3738。39', '[釋文-1]：張魚反，說文云：「豕而三毛叢居者」\n[郭注-2]：今亦曰彘，江東呼豨，皆通名。\n[釋文-3]：彘，直例反，字林云：「豕後蹄廢謂之彘。」小爾雅云：「豬也。」方言云：「關東西謂之彘，或謂之豕。」豨，虛豈反，字書云：「東方名豕也。一曰豕足也。」\n[音釋-4]：偉。\n[釋文-5]：𧱫，羊箠反，本今作䝐\n[音釋-6]：墳。\n[釋文-7]：苻云反，謂犍豬，犍，音九言反\n[郭注-8]：俗呼小豶豬爲䝐子。\n[音釋-9]：腰。\n[釋文-10]：烏堯反，字林云：「小豚。」施於遥反\n[釋文-11]：伊秀反\n[郭注-12]：最後生者，俗呼爲幺豚。\n[釋文-13]：豚，大昆反。\n[音釋-14]：湊。\n[釋文-15]：七豆反，本或作湊，下同\n[音釋-16]：溫。\n[釋文-17]：音溫\n[郭注-18]：今豱豬短頭，皮理腠蹙。\n[釋文-19]：腠，七豆反，埤蒼云：「腠，膚理也。」蹙，子六反。\n[音釋-20]：宗。\n[釋文-21]：子公反，小爾雅云：「豕小者曰豵。」字林云：「豕生六月也。一曰一歲曰豵」\n[郭注-22]：豬生子常多，故別其少者之名。\n[音釋-23]：繒。\n[釋文-24]：辭陵反，舊木多作繒帛字，非，方言作木旁\n[郭注-25]：橧，其所臥蓐。\n[釋文-26]：蓐，音辱。\n[音釋-27]：滴。\n[釋文-28]：蹢，丁歷反，蹄也，本今作豴\n[音釋-29]：垓。\n[釋文-30]：工開反，字林下才反，或戸楷反\n[郭注-31]：詩云：「有豕白蹢。」蹢，蹄也。\n[釋文-32]：蹄，音啼。\n[釋文-33]：音克\n[音釋-34]：厄。\n[釋文-35]：於革反\n[郭注-36]：卽「豕高五尺」者。\n[音釋-37]：巴。\n[釋文-38]：百麻反，牝豕也，字林同，又「一曰二歲豕」\n[郭注-39]：詩云：「一發五豝。」', '釋獸弟十八', '爾雅', '7;10', DEFAULT, DEFAULT, '6078;6079;6088;6085;2498;2499;6661;552;6082;3864;731;6697;9883;2765;7727;6083', '7295;1751', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5628, '虎;虦貓', '虦苗', '虎竊毛謂之虦貓[虦苗]12。3', '[音釋-1]：棧。苗。\n[釋文-2]：字又作虥，謝士版反，或士𥳑反，施士嬾反，沈才班反，郭昨閑反，字林士山反。亡朝反\n[郭注-3]：竊，淺也。詩曰：「有貓有虎。」', '釋獸弟十八', '爾雅', '2', DEFAULT, DEFAULT, '3118;3123/6132', '3123/542', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5629, '貘;豹', '', '貘12，白豹3。45', '[音釋-1]：陌。\n[釋文-2]：亡白反，字林云：「似熊而白黃，出蜀郡。一曰白豹」\n[釋文-3]：必孝反，字林云：「似虎，員文」\n[郭注-4]：似熊，小頭，庳腳，黑白駁，能舐食銅鐵及竹，骨節強直，中實少髓，皮辟濕。或曰：豹白色者，別名貘。\n[釋文-5]：熊，音雄。庳，音婢。駁，布角反。𦧇，音舐，本今作舐。鐵，佗結反。髓，素累反。辟，必亦反。', '釋獸弟十八', '爾雅', '', DEFAULT, DEFAULT, '6119;6113', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5630, '甘;䖑;虪;虎', '䖔', '甝{甘}[䖔]{䖑}12，白虎。3虪45，黑虎。67{虎}', '[音釋-1]：含。\n[釋文-2]：字林下甘反，又亡狄反\n[郭注-3]：漢宣帝時，南郡獲白虎，獻其皮骨爪牙。\n[音釋-4]：式六。\n[釋文-5]：𧇐，式六反，本今作虪\n[郭注-6]：晉 永嘉四年，建平 秭歸縣檻得之，狀如小虎而黑，毛深者爲班。山海經云：「幽都山多玄虎、玄豹。」\n[釋文-7]：秭，音姊。檻，戸覽反，下同。', '釋獸弟十八', '爾雅', '1', DEFAULT, DEFAULT, '3038;3120;3122;3118', '3121', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5631, '貀', '', '貀12，無前足。34', '[音釋-1]：尼滑。\n[釋文-2]：本又作豽，女滑反，字林云：「獸無前足，似虎而黑」\n[郭注-3]：晉 大康七年，召陵 扶夷縣檻得一獸，似狗，豹文，有角，兩腳，卽此種類也。或說「貀，似虎而黑，無前兩足」。\n[釋文-4]：召，市照反。種，章勇反。', '釋獸弟十八', '爾雅', '', DEFAULT, DEFAULT, '6122', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5632, '狊;小驢', '', '鼳{狊}12，鼠身，長須而賊，秦人謂之小驢3。4', '[音釋-1]：古役。\n[釋文-2]：本多作狊，經文旣云「鼠身」，宜從鼠，音古闃反\n[釋文-3]：力居反\n[郭注-4]：鼳似鼠而馬蹄，一歲千斤，爲物殘賊。', '釋獸弟十八', '爾雅', '', DEFAULT, DEFAULT, '6315;704/6247', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5633, '狗;麙', '後', '熊虎醜，其子狗[後]1，絶有力，麙23。45', '[釋文-1]：古口反，本或作豿，沈、施火候反，注同\n[音釋-2]：巗。\n[釋文-3]：本或作㺂，同，五咸反\n[郭注-4]：律曰：「捕虎一，購錢三千其狗半之。」\n[釋文-5]：捕，音步。購，古豆反，贖也。', '釋獸弟十八', '爾雅', '1', DEFAULT, DEFAULT, '6304;6283', '1250', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5634, '貍;隶', '㣇', '貍1，子𧳙{隶}[㣇]23。45', '[釋文-1]：力之反，字林云：「伏獸，似貙」\n[音釋-2]：曳。\n[釋文-3]：以世反，施餘棄反，衆家作肆、又作𨽸，沈音四，舍人本作𧳙\n[郭注-4]：今或呼豾貍。\n[釋文-5]：豾，字或作狉，房悲反，一音丕，字林云：「貍也。」', '釋獸弟十八', '爾雅', '2', DEFAULT, DEFAULT, '6126;1946', '6100', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5635, '貈;貆', '', '貈12，子貆34。56', '[音釋-1]：乎各。\n[釋文-2]：乎各反，字林云：「似狐，善睡。」本作貉，亡白反，字林云：「北方人也，非獸也」\n[音釋-3]：丸。\n[釋文-4]：音桓，又音懽\n[郭注-5]：其雌者名𧴙。今江東呼貉爲𧲱𧳅。\n[釋文-6]：𤠏，字又作𧴙，同，乃老反，字林云：「雌貈。」𧲱，烏郎反。𧳅，山吏反，字林云：「貁謂之𧳅。」廣雅云：「𧳅，貁也。」貁，音餘救反。', '釋獸弟十八', '爾雅', '', DEFAULT, DEFAULT, '6123;6124', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5636, '貒;婁', '', '貒12，子貗{婁}34。56', '[音釋-1]：湍。\n[釋文-2]：他官反，說文、字林云：「獸似豕而肥」\n[音釋-3]：其禹。\n[釋文-4]：郭其禹反，字林力于反\n[郭注-5]：貒，豚也。一名貛。\n[釋文-6]：豚，音屯，本又作肫。貛，音懽。', '釋獸弟十八', '爾雅', '', DEFAULT, DEFAULT, '6127;8323', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5637, '貔;白狐;豰', '', '貔12，白狐3，其子豰45。6', '[音釋-1]：毗。\n[釋文-2]：音毗，字林云：「豹屬，出貉國。一曰白狐。」毛詩草木䟽云：「似虎，或曰似羆，一名執夷，一名白狐，其子爲豰，遼東人謂之白熊」\n[釋文-3]：如字，說文云：「祅獸也，鬼所乗，有三德，其色中和，小前大後，死則丘首」\n[音釋-4]：火卜。\n[釋文-5]：本又作㺉，火卜反，又虎枓反\n[郭注-6]：一名執夷，虎豹之屬。', '釋獸弟十八', '爾雅', '', DEFAULT, DEFAULT, '6116;4931/6382;6080', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5638, '麝父', '', '麝父12，麕足。34', '[音釋-1]：甫。\n[釋文-2]：食亦反，字林音射，云：「如小鹿，有香。」李本作澤，云：「澤父，獸名。」音甫，下同\n[郭注-3]：腳似麕，有香。\n[釋文-4]：麇，九倫反，本或作麕。', '釋獸弟十八', '爾雅', '', DEFAULT, DEFAULT, '6286/1908', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5639, '豺', '', '豺1，狗足。2', '[釋文-1]：仕皆反，字林云：「狼屬，狗足」\n[郭注-2]：腳似狗。', '釋獸弟十八', '爾雅', '', DEFAULT, DEFAULT, '6117', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5640, '貙獌', '', '貙獌12，似貍。34', '[音釋-1]：樞。萬。\n[釋文-2]：丑于反，字林云：「似貍而大。」本亦作䝡，音萬，又亡姦反，或亡半反，字林音幔，云：「狼屬，一曰貙也」\n[郭注-3]：今山民呼貙虎之大者爲貙豻，音岸。\n[釋文-4]：豻，郭音岸，字林下旦反，云：「胡地野狗。」本又作犴，說文或豻字，陳國武音子虛賦苦姦反，解云：「胡地野犬，似狐，黑喙。」', '釋獸弟十八', '爾雅', '', DEFAULT, DEFAULT, '6114/6381', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5641, '羆', '', '羆1，如熊，黃白文。23', '[釋文-1]：音碑\n[郭注-2]：似熊而長頭高腳，猛憨多力，能拔樹木，關西呼曰貑羆。\n[釋文-3]：憨，呼濫反。貑，音加。', '釋獸弟十八', '爾雅', '', DEFAULT, DEFAULT, '6415', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5642, '麢', '', '麢12，大羊。3', '[音釋-1]：零。\n[釋文-2]：力丁反，陶弘景注本草云：「今出建平、宜都諸蠻中及西域，多兩角者，一角者爲勝，角甚多節，蹙蹙貟繞，別有山羊，角極長，唯一邊有節，節亦踈大而不入用，羌夷云：『只此名麢羊，甚能步峻，短角者乃山羊。』亦未詳其正」\n[郭注-3]：麢羊，似羊而大，角貟銳，好在山崖閒。', '釋獸弟十八', '爾雅', '', DEFAULT, DEFAULT, '6284', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5643, '麠', '', '麠12，大麃34，牛尾，一角。56', '[音釋-1]：京。\n[釋文-2]：音京，本或作麖，同\n[音釋-3]：炮。\n[釋文-4]：步交反，郭云：「卽麞也。」張揖同，字林云：「麖屬」\n[郭注-5]：漢武帝郊雍，得一角獸，若麃然，謂之麟者，此是也。麃卽麞。\n[釋文-6]：雍，於用反。麞，音章。', '釋獸弟十八', '爾雅', '', DEFAULT, DEFAULT, '6279', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5644, '𪊨', '', '𪊨12，大麕，旄毛34，狗足。56', '[音釋-1]：几。\n[釋文-2]：字又作麂，音几\n[音釋-3]：帽。\n[釋文-4]：亡報反\n[郭注-5]：旄毛，㺜長。\n[釋文-6]：㺜，乃牢反，或乃容反，字林云：「多毛犬也。」', '釋獸弟十八', '爾雅', '', DEFAULT, DEFAULT, '6276', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5645, '魋', '', '魋12，如小熊，竊毛而黃。34', '[音釋-1]：頽。\n[釋文-2]：徒回反，字林云：「獸如熊，黃而小」\n[郭注-3]：今建平山中有此獸，狀如熊而小，毛麆淺赤黃色，俗呼爲赤熊，卽魋也。\n[釋文-4]：麆，七奴反。', '釋獸弟十八', '爾雅', '', DEFAULT, DEFAULT, '2318', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5646, '䝟貐', '', '䝟貐12，類貙，虎爪，食人，迅走。3', '[音釋-1]：烏八。羊主。\n[釋文-2]：字亦作猰，或作窫，褚詮之烏八反，韋昭烏繼反，服虔音毉，晉灼音內言䭇，案：字書䭇音噎。字或作窳，褚詮之以主反，字林弋父反，韋昭餘彼反。補孫炎曰：「獸中最大，龍頭、馬尾、虎爪，長四尺，善走，以人爲食。遇有道君，隱藏；無道君，出食人矣」\n[郭注-3]：迅，疾。', '釋獸弟十八', '爾雅', '', DEFAULT, DEFAULT, '6389/6118', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5647, '狻麑', '', '狻麑1，如虦貓，食虎豹。23', '[釋文-1]：先官反。字又作猊，牛奚反\n[郭注-2]：卽師子也，出西域。漢順帝時，踈勒王來獻犎牛及師子。穆天子傳曰：「狻猊日走五百里。」\n[釋文-3]：犎，音封。日，而一反。', '釋獸弟十八', '爾雅', '', DEFAULT, DEFAULT, '6373/6282', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5648, '巂;騏', '', '驨{巂}12，如馬，一角。不角者，騏3。45', '[音釋-1]：胡圭。\n[釋文-2]：本又作巂，同，戸圭反\n[釋文-3]：音其\n[郭注-4]：元康八年，九真郡獵得一獸，大如馬，一角，角如鹿茸，此卽驨也。今深山中人時或見之，亦有無角者。\n[釋文-5]：茸，而容反。', '釋獸弟十八', '爾雅', '', DEFAULT, DEFAULT, '2284;6143', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5649, '原', '萈', '羱{原}[萈]12，如羊。34', '[音釋-1]：元。\n[釋文-2]：魚袁反，又五丸反，字林云：「野羊，大角」\n[郭注-3]：羱羊，似吳羊而大角，角橢，岀西方。\n[釋文-4]：橢，字或作隋，他果反，下同。', '釋獸弟十八', '爾雅', '1', DEFAULT, DEFAULT, '7496', '6302', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5650, '麐', '', '麐12，麕身，牛尾，一角。3', '[音釋-1]：鄰。\n[釋文-2]：字林力人反，本又作麟，牝麒也，一音力珍反。補孫炎曰：「靈獸也」\n[郭注-3]：角頭有肉。公羊傳曰：「有麕而角。」', '釋獸弟十八', '爾雅', '', DEFAULT, DEFAULT, '6271', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5651, '猶', '', '猶1，如麂23，善登木。45', '[釋文-1]：羊周、羊救二反，字林弋又反，說文云：「玃屬也。一曰隴西人謂犬子也。」尸子云：「五尺大犬也。」舍人本作鬻，郭音育\n[音釋-2]：几。\n[釋文-3]：音几\n[郭注-4]：健上樹。\n[釋文-5]：上，時掌反。', '釋獸弟十八', '爾雅', '', DEFAULT, DEFAULT, '6375', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5652, '聿', '㣇', '貄{聿}[㣇]12，脩毫3。4', '[音釋-1]：四。\n[釋文-2]：本又作𧳙，亦作肆，音四\n[釋文-3]：音羞。戸高反，廣雅云：「㲦謂之毫。」本或作豪，音同，借用也\n[郭注-4]：毫，毛長。', '釋獸弟十八', '爾雅', '1', DEFAULT, DEFAULT, '1940', '6100', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5653, '貙', '', '貙，似貍。1', '[郭注-1]：今貙虎也。大如狗，文如貍。', '釋獸弟十八', '爾雅', '', DEFAULT, DEFAULT, '6114', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5654, '兕', '', '兕1，似牛。2', '[釋文-1]：本又作𠒊，徐履反\n[郭注-2]：一角，靑色，重千斤。', '釋獸弟十八', '爾雅', '', DEFAULT, DEFAULT, '6133', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5655, '犀', '', '犀1，似豕。23', '[釋文-1]：音西。俗作犀，非\n[郭注-2]：形似水牛，豬頭，大腹，庳腳，腳有三蹄，黑色。三角，一在頂上，一在額上，一在鼻上。鼻上者卽食角也。小而不橢，好食棘，亦有一角者。\n[釋文-3]：庳，音婢。', '釋獸弟十八', '爾雅', '', DEFAULT, DEFAULT, '769', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5656, '彙', '', '彙12，毛刺34。5', '[音釋-1]：謂。\n[釋文-2]：本或作猬，又作蝟，亦作𢍚，同，音謂\n[音釋-3]：次。\n[釋文-4]：七賜反\n[郭注-5]：今蝟，狀似鼠。', '釋獸弟十八', '爾雅', '', DEFAULT, DEFAULT, '6103', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5657, '弗弗', '𥝋𥝋', '狒狒{弗弗}[𥝋𥝋]12，如人，被髮34，迅走，食人。56', '[音釋-1]：費。\n[釋文-2]：字又作𦦷，或作𥝆，同，說文云：「周成王時州靡國獻𦦷。𦦷，人身，反踵，自𥬇，𥬇則上脣弇其目，食人，北方謂之土螻，讀若費費，一名梟陽。」今依許扶味反，沈音沸，郭簿昧反，又音備。郭云：「俗呼曰山都。」案：相傳云此獸人面長脣，身有毛，好食人，得人則𥬇，而脣覆其面，人亦因而獲之，故左思吳都賦云：「𦦷𦦷𥬇而被格」是也\n[音釋-3]：備。\n[釋文-4]：皮義反\n[郭注-5]：梟羊也。山海經曰：「其狀如人，面長脣黑，身有毛，反踵，見人則𥬇。」交廣及南康郡山中亦有此物，大者長丈許，俗呼之曰山都。\n[釋文-6]：梟，古堯反。脣，音純。', '釋獸弟十八', '爾雅', '1', DEFAULT, DEFAULT, '8363/8363', '9737/9737', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5658, '番;𠘯', '', '貍、狐、貒、貈醜，其足蹯{番}12，3其跡𠘯45。67', '[音釋-1]：煩。\n[釋文-2]：說文作番，古文作𥸨，竝音煩。案蹯，掌也，左傳云：「宰夫胹熊蹯不熟，殺之」是也\n[郭注-3]：皆有掌蹯。\n[音釋-4]：鈕。\n[釋文-5]：女九、人九二反，說文云：「獸足蹂地也。古文爲蹂。」字林或作狃\n[郭注-6]：𠘯，指頭處。\n[釋文-7]：處，昌慮反。', '釋獸弟十八', '爾雅', '', DEFAULT, DEFAULT, '721;9732', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5659, '蒙頌', '', '蒙頌1，猱狀23。45', '[釋文-1]：莫東反\n[音釋-2]：奴刀。\n[釋文-3]：奴刀反，本或作獶，郭女救反\n[郭注-4]：卽蒙貴也，狀如蜼而小，紫黑色，可畜，健捕鼠，勝於貓，九真、日南皆出之。猱亦獼猴之類。\n[釋文-5]：蜼，餘水反。畜，許六反。捕，音步。勝，尸證反。貓，音苗。獼，音彌。猴，音侯。', '釋獸弟十八', '爾雅', '', DEFAULT, DEFAULT, '649/5604', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5660, '柔;蝯', '夒', '猱{柔}[夒]1、蝯23，善援4。5', '[釋文-1]：補孫炎曰：「母猴也」\n[音釋-2]：袁。\n[釋文-3]：猨，音袁，本今作蝯\n[釋文-4]：音袁，猶引也\n[郭注-5]：便攀援。', '釋獸弟十八', '爾雅', '1', DEFAULT, DEFAULT, '3609;8925', '3376', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5661, '貜父', '玃父', '貜父[玃父]12，善顧。34', '[音釋-1]：钁。甫。\n[釋文-2]：字亦作玃，俱縛反，說文云：「大母猴也。」音甫\n[郭注-3]：貑貜也，似獼猴而大，色蒼黑，能攫持人，好顧眄。\n[釋文-4]：貑，本或作麚，音古牙反。眄，亡見反。', '釋獸弟十八', '爾雅', '1', DEFAULT, DEFAULT, '6121/1908', '6374/1908', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5662, '威夷;泥', '委虒;𩰞', '威夷[委虒]，長脊而泥[𩰞]12。3', '[音釋-1]：奴細。\n[釋文-2]：音積。奴細反\n[郭注-3]：泥，少才力。', '釋獸弟十八', '爾雅', '1;2', DEFAULT, DEFAULT, '8147/6615;7108', '8215/3131;1897', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5663, '麔;麚', '', '麔、麚1，短脰2。3', '[釋文-1]：音咎。音加\n[釋文-2]：音豆\n[郭注-3]：脰，項。', '釋獸弟十八', '爾雅', '', DEFAULT, DEFAULT, '6274;6264', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5664, '贙', '狟', '贙[狟]12，有力。34', '[音釋-1]：鉉。\n[釋文-2]：胡犬反\n[郭注-3]：出西海大秦國。有養者。似狗，多力，獷惡。\n[釋文-4]：獷，虢猛反。', '釋獸弟十八', '爾雅', '1', DEFAULT, DEFAULT, '3137', '6351', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5665, '豦', '', '豦12，迅頭。34', '[音釋-1]：據。\n[釋文-2]：音據\n[郭注-3]：今建平山中有豦，大如狗，似獼猴，黃黑色，多髯鬛，如𡚒迅，其頭能舉石擿人，貜類也。\n[釋文-4]：髯，而占反。鬛，力輒反。擿，字又作擲，直戟反。', '釋獸弟十八', '爾雅', '', DEFAULT, DEFAULT, '6097', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5666, '蜼', '', '蜼12，卬鼻而長尾3。45', '[音釋-1]：誄。\n[釋文-2]：音誄，字林余繡反，或餘季、餘水二反\n[釋文-3]：五剛反，又魚兩反\n[郭注-4]：蜼似獼猴而大，黃黑色，尾長數尺，似獺，尾末有岐，鼻露向上，雨卽自縣於樹，以尾塞鼻，或以兩指。江東人亦取養之，爲物捷健。\n[釋文-5]：數，所主反。獺，勑鎋反，又勑末反。岐，音祁。縣，音玄。捷，才接反。', '釋獸弟十八', '爾雅', '', DEFAULT, DEFAULT, '8927', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5667, '時;椉', '', '時，善椉領12。34', '[音釋-1]：承。\n[釋文-2]：本或作乗，事陵反\n[郭注-3]：好登山峯。\n[釋文-4]：峰，芳逢反，本今作峯。', '釋獸弟十八', '爾雅', '', DEFAULT, DEFAULT, '4191;3412', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5668, '猩猩;闕洩;狃', '蹶曳', '猩猩1，小而好啼2。34闕洩[蹶曳]多狃56。7', '[釋文-1]：音生，郭云：「人面豕身，能言語。」又云：「狀如貛㹠，聲似小兒啼。」案禮記云：「猩猩能言，不離禽獸」是也\n[釋文-2]：如字，又呼報反。嗁，社奚反，作啼，同\n[郭注-3]：山海經曰：「人面豕身，能言語。」今交阯 封谿縣出猩猩，狀貛㹠，聲似小兒啼。\n[釋文-4]：阯，音止。磎，音溪，本今作谿。豚，徒門反，字亦作㹠，字林云：「小豕也。」說文作豚，又云籀文也。\n[音釋-5]：其越。鈕。\n[釋文-6]：其越反，又如字。泄，息列反。女九反\n[郭注-7]：說者云腳饒指。未詳。', '釋獸弟十八', '爾雅', '2', DEFAULT, DEFAULT, '6319/6319;7746/7065;6341', '1369/9805', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5669, '鼢鼠;鼸鼠;鼷鼠;鼶鼠;鼬鼠;鼩鼠;時鼠;吠鼠;鼫鼠;文鼠;鼨鼠;廷鼠;狊鼠', '', '鼢鼠12。3鼸鼠45。67鼷鼠89。1011鼶鼠1213。1415鼬鼠16。1718鼩鼠1920。2122鼭鼠{時鼠}2324。25鼣鼠{吠鼠}2627。28鼫鼠2930。3132鼤鼠{文鼠}3334。鼨鼠3536，37豹文。鼮鼠{廷鼠}3839。4041鼳鼠{狊鼠}4243。4445', '[音釋-1]：憤。\n[釋文-2]：字亦作蚡，扶粉、扶云二反，說文云：「地中行鼠，伯勞所作也。」一音偃，廣雅云：「鼹鼠也。」字或作𪖅，同，方言謂之「犁鼠」\n[郭注-3]：地中行者。\n[音釋-4]：胡忝。\n[釋文-5]：下簟反，孫云：「鼸者，頰裹也。」郭云：「以頰內藏食也。」字林云：「卽鼢鼠也。」補李巡曰：「鼠從田中銜穀藏」\n[郭注-6]：以頰裏藏食。\n[釋文-7]：頰，古協反。\n[音釋-8]：奚。\n[釋文-9]：戸雞反，字林云：「小鼠也。」郭云：「有螫毒者。」博物志云：「鼠之最小者，或謂之耳鼠。」案春秋：「食郊牛角者」是也\n[郭注-10]：有螫毒者。\n[釋文-11]：螫，音釋。\n[音釋-12]：斯。\n[釋文-13]：私移反，又徒奚反\n[郭注-14]：夏小正曰：「鼶鼬則穴。」\n[釋文-15]：鼬，餘又反，字林云：「如鼠，赤黃而文。」\n[音釋-16]：佑\n[郭注-17]：今鼬似鼦，赤黃色，大屋，啖鼠，江東呼爲鼪，音牲。\n[釋文-18]：鼦，字書云：「古貂字也，音彫。」啖，大敢反。鼪，音性，莊子云：「騏𩦸驊騮，捕鼠不如貍鼪。」郭音生。\n[音釋-19]：劬。\n[釋文-20]：音劬\n[郭注-21]：小鼱鼩也，亦名𪖁鼩。\n[釋文-22]：鼱，音精，字林云：「鼱，鼩。」李云：「一名鼷鼠。」𪖁，將容反。\n[音釋-23]：時。\n[釋文-24]：音時\n[郭注-25]：未詳。\n[音釋-26]：吠。\n[釋文-27]：字或作𪖆，苻廢反，舍人云：「其鳴如犬也」\n[郭注-28]：山海經說獸云：「狀如鼣鼠。」然形則未詳。\n[音釋-29]：石。\n[釋文-30]：音石，孫云：「五技鼠也。」字林同，案：蔡伯喈勸學篇云：「五技者，能飛不能上屋，能緣不能窮木，能泅不能渡瀆，能走不能絶人，能藏不能覆身」是也，許氏說文亦云然。或云：卽螻蛄也。郭云：「形大如鼠，頭似兔，尾有毛，靑黃色，好在田中食粟豆，關西呼爲䶂鼠，見廣雅。」䶂鼠卽雀鼠也\n[郭注-31]：形大如鼠，頭似兔，尾有毛，靑黃色，好在田中食粟豆。關西呼爲鼩鼠，見廣雅，音瞿。\n[釋文-32]：䶂，郭音雀，將畧反，字林音灼，云：「䶂鼠出胡地。」郭注本雀字，或誤爲瞿字，沈旋因云：「郭以爲鼩鼠，音求于反。」非也。瞿，音劬。\n[音釋-33]：問。\n[釋文-34]：音問，又音文\n[音釋-35]：終。\n[釋文-36]：音終，又徒冬反，說文、字林皆云：「豹文鼠也」\n[郭注-37]：皆未詳。\n[音釋-38]：廷。\n[釋文-39]：徒形反，又大佞反，郭云：「文彩如豹也」\n[郭注-40]：鼠文彩如豹者，漢武帝時得此鼠，孝廉郎終軍知之，賜絹百匹。\n[釋文-41]：漢書云：「終軍，字子雲，濟南人，初入關，棄繻而去，至長安上書，拜爲謁者給事中，使南越，爲呂嘉所殺，死時年二十餘，故世號之『終童』。」\n[音釋-42]：古覓。\n[釋文-43]：古闃反，郭音覡，戸狄反\n[郭注-44]：今江東山中有鼳鼠，狀如鼠而大，蒼色，在樹木上，音巫覡。\n[釋文-45]：覡，何狄反。', '釋獸弟十八', '爾雅', '', DEFAULT, DEFAULT, '6396/6393;6405/6393;6403/6393;6398/6393;6407/6393;6404/6393;4191/6393;944/6393;6400/6393;5725/6393;6401/6393;1261/6393;6315/6393', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5670, '齝;齥;齸;素;嗛', '橾', '牛曰齝12。34羊曰齥56。78麋鹿曰齸910。1112鳥曰嗉{素}[橾]13。1415寓鼠曰嗛16。1718', '[音釋-1]：丑之。\n[釋文-2]：謝初其反，郭音笞\n[郭注-3]：食之已久，復出嚼之。\n[釋文-4]：嚼，字若反，廣雅云：「茹也。」字書云：「咀也。」說文以爲噍字。\n[音釋-5]：泄。\n[釋文-6]：齛，郭音泄，息列反，一音曳，埤蒼云：「羊粻也。」張揖音世，解云：「羊食已吐而更嚼之」\n[郭注-7]：今江東呼齝爲齥，音漏洩。\n[釋文-8]：𪗪，丑之、初其二反，字書以爲古齝字。\n[音釋-9]：益。\n[釋文-10]：字或作嗌，於亦反，埤蒼云：「鹿粻。」又音翳\n[郭注-11]：江東名咽爲齸。齸者，䶗食之所在，依名云。\n[釋文-12]：咽，於見反，又於賢反。䶗，客加反。\n[釋文-13]：音素，又私路反\n[郭注-14]：咽中裹食處。\n[釋文-15]：裹，音果。處，昌慮反，下同。\n[釋文-16]：下簟反\n[郭注-17]：頰裹貯食處。寓謂獼猴之類，寄寓木上。\n[釋文-18]：貯，丁呂反，字林云：「積也。」', '釋獸弟十八', '爾雅', '4', DEFAULT, DEFAULT, '1310;1316;1317;8776;802', '3780', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5671, '釁;撟;須;狊', '卂;𩓣', '獸曰釁[卂]12。3人曰撟45。67魚曰須[𩓣]。89鳥曰狊1011。1213', '[音釋-1]：許靳。\n[釋文-2]：許靳反\n[郭注-3]：自奮釁。\n[音釋-4]：紀小。\n[釋文-5]：几小反，又巨小反，說文云：「舉也」\n[郭注-6]：頻伸夭撟。\n[釋文-7]：伸，音申。夭，於表反。\n[郭注-8]：鼓鰓須息。\n[釋文-9]：鰓，西才反。\n[音釋-10]：古闃。\n[釋文-11]：古闃反\n[郭注-12]：張兩翅，皆氣體所須。\n[釋文-13]：翄，申豉反，本或作翅，又作翨，音同，或吉䜴反，案周禮有「翨氏」是也。', '釋獸弟十八', '爾雅', '1;3', DEFAULT, DEFAULT, '1790;7966;5708;6315', '7701;6691', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5672, '騊駼;野馬', '', '騊駼馬12。3野馬。45', '[音釋-1]：陶。徒。\n[釋文-2]：大刀反。大胡反，山海經云：「有獸狀如馬，名騊駼，靑色。」字林云：「北狄良馬也。一曰野馬也。」瑞應圖云：「幽隱之獸也，有明王在位卽至」\n[郭注-3]：山海經云：「北海內有獸，狀如馬，名騊駼，色靑。」\n[郭注-4]：如馬而小，岀塞外。\n[釋文-5]：塞，悉代反。', '釋畜弟十九', '爾雅', '', DEFAULT, DEFAULT, '6251/6252;9171/6137', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5673, '駮', '', '駮1，如馬，倨牙23，食虎豹。4', '[釋文-1]：方角反，山海經云：「可以禦兵。」補舍人曰：「多力獸也」\n[音釋-2]：鋸。\n[釋文-3]：本亦作居，同，紀慮反。五加反\n[郭注-4]：山海經云：「有獸名駮，如白馬，黑尾，倨牙，音如鼓，食虎豹。」', '釋畜弟十九', '爾雅', '', DEFAULT, DEFAULT, '6243', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5674, '昆帝', '昆蹏', '騉蹄{昆帝}[昆蹏]12，趼34，善陞甗56。78', '[音釋-1]：昆。\n[釋文-2]：古門反，本亦作昆。徒兮反，字或作蹏\n[音釋-3]：五見。\n[釋文-4]：五見反，又五堅反，本或作研\n[音釋-5]：言。\n[釋文-6]：音升，本亦作升。郭音言，又魚輦反，字林牛建反，云：「甑也。」舍人云：「𬦸蹄者，溷蹄也。研，平也，謂蹄平正，善陞甗者，能登山隒也。一云甗者，阪也，言騉善登高歷險，上下於阪。」李云：「騉者，其蹄正堅而平似研也。」顧云：「山嶺曰甗。」孫同郭云：「甗，山形似甑也，上大下小，騉蹄，蹄如研而健上山」\n[郭注-7]：甗，山形似甑，上大下小。騉蹄，蹄如趼而健上山。秦時有騉蹄苑。\n[釋文-8]：甑，子孕反。上，時掌反。苑，於遠反。', '釋畜弟十九', '爾雅', '1', DEFAULT, DEFAULT, '4256/7', '4256/1327', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5675, '昆駼', '', '騉駼{昆駼}，枝蹄趼，善陞甗1。2', '[釋文-1]：舍人云：「騉駼者，外國之名，枝蹄者，枝足也。」李云：「騉駼，其迹枝平似趼，亦能登高歷危險也。」孫云：「騉駼之馬，枝蹄如牛而下平。」郭云：「騉駼亦似馬而牛蹄也」\n[郭注-2]：騉駼，亦似馬而牛蹄。', '釋畜弟十九', '爾雅', '', DEFAULT, DEFAULT, '4256/6252', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5676, '盜驪', '', '小領，盜驪1。23', '[釋文-1]：力知反，字林力兮反，說文云：「深黑色馬」\n[郭注-2]：穆天子傳曰：「天子之駿，盜驪、緑耳。」又曰：「右服盜驪。」盜驪，千里馬。領，頸。\n[釋文-3]：緑耳，力玉反，本或作騄駬，同。', '釋畜弟十九', '爾雅', '', DEFAULT, DEFAULT, '5596/6144', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5677, '駥', '', '絶有力，駥12。3', '[音釋-1]：戎。\n[釋文-2]：而充反，本亦作戎\n[郭注-3]：卽馬高八尺。', '釋畜弟十九', '爾雅', '', DEFAULT, DEFAULT, '6255', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5678, '馵;繒;首;騱;翑;啟;踦;驤;馵', '蹈', '膝上皆白1，惟馵23。四骹皆白456，驓{繒}78。9四蹢皆白10，首[蹈]。1112前足皆白，騱1314。後足皆白，翑1516。前右足白，啟；17左白，踦1819。20後右足白，驤2122；左白，馵。23', '[釋文-1]：音悉，字又作䣛\n[音釋-2]：注。\n[釋文-3]：字林之句反，郭式喻反，下同\n[音釋-4]：敲\n[郭注-5]：。\n[釋文-6]：苦交反，字書作（⿰足爻），同，云：「脛也。」郭云：「膝下」\n[音釋-7]：繒。\n[釋文-8]：辭陵反，或辭登、辭亘二反\n[郭注-9]：骹，膝下也。\n[釋文-10]：丁歷反，毛詩傳云：「蹄也」\n[郭注-11]：俗呼爲踏雪馬。\n[釋文-12]：踏，徒臘反。\n[音釋-13]：奚。\n[釋文-14]：音奚，郭又音雞，舍人本作雞\n[音釋-15]：劬。\n[釋文-16]：郭音劬，又音矩，舍人本作豞\n[郭注-17]：左傳曰：「啟服。」\n[音釋-18]：欺。\n[釋文-19]：郭去宜反，顧居綺反\n[郭注-20]：前左腳白。\n[音釋-21]：箱。\n[釋文-22]：息羊反\n[郭注-23]：後左腳白。易曰：「震爲馵足。」', '釋畜弟十九', '爾雅', '3', DEFAULT, DEFAULT, '6162;8592;5703;6250;2255;1996;1331;6185;6162', '1355', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5679, '駵;縓;驪;驈;驠;晏;狼;馰;達;葛縣;駹', '燕;齃縣', '駵馬白腹12，騵{縓}34。5驪馬白跨6，驈78。910白州，驠1112。1314尾本白，騴{晏}[燕]1516。1718尾白，駺{狼}1920。21馰顙，白顚2223；24白達，素縣{葛縣}[齃縣]2526；2728面顙皆白，惟駹2930。31', '[音釋-1]：留。\n[釋文-2]：音留，字或作騮，說文、字林云：「赤馬黑毛尾也。」毛詩傳云：「赤身黑鬛曰駵」\n[音釋-3]：五官。\n[釋文-4]：音原\n[郭注-5]：駵，赤色，黑鬛。\n[釋文-6]：口化反，或口故反，蒼頡篇云：「兩股閒」\n[音釋-7]：聿。\n[釋文-8]：字或作䮇，字林于必反，顧餘橘反，郭音術，阮于密反\n[郭注-9]：驪，黑色。跨，髀閒。\n[釋文-10]：髀，甫爾反，又步啟反，下同，說文云：「股外也。」\n[音釋-11]：宴。\n[釋文-12]：於見反\n[郭注-13]：州，竅。\n[釋文-14]：竅，苦弔反。\n[音釋-15]：晏。\n[釋文-16]：一諫反\n[郭注-17]：尾株白。\n[釋文-18]：株，音誅。\n[音釋-19]：郎。\n[釋文-20]：本多作狼，同，音郎\n[郭注-21]：俱尾毛白。\n[音釋-22]：的。\n[釋文-23]：的，字林作馰，丁歷反，云：「馬白顙也。一曰駁。」息黨反。補舍人曰：「的，白也；顙，額也，額有白毛。今之戴星馬也。易震爲的顙」\n[郭注-24]：戴星馬也。\n[音釋-25]：玄。\n[釋文-26]：音玄\n[郭注-27]：素，鼻莖也，俗所謂「漫髗徹齒」。\n[釋文-28]：莖，戸耕反。漫，莫干反。髗，字書作顱，力胡反，謂額顙也。徹，直列反。\n[音釋-29]：尨。\n[釋文-30]：武江反\n[郭注-31]：顙，額。', '釋畜弟十九', '爾雅', '6;10', DEFAULT, DEFAULT, '6147;8624;6144;6153;6164;4211;6379;6160;1163;458/5707;6154', '7688;5613/5707', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5680, '宜乗;減陽;茀方;闋廣;居允', '渠𧍕', '回毛在膺，宜乗1；23在肘後4，減陽56；在幹7，茀方8；9在背，闋廣1011。1213逆毛，居馻{居允}[渠𧍕]1415。16', '[釋文-1]：椉，字又作乗，施市升反，謝市證反\n[郭注-2]：樊光云：「俗呼之官府馬。」伯樂相馬法：「旋毛在腹下，如乳者，千里馬。」\n[釋文-3]：樂，音洛。相，息亮反。\n[釋文-4]：竹九反\n[音釋-5]：古湛。\n[釋文-6]：古湛反，本或作駭\n[釋文-7]：古旦反\n[釋文-8]：音弗\n[郭注-9]：幹，脅。\n[音釋-10]：缺。光。\n[釋文-11]：苦穴、古穴二反。音光，本或作𩧉，同\n[郭注-12]：皆別旋毛所在之名。\n[釋文-13]：別，彼列反。\n[音釋-14]：兖。\n[釋文-15]：郭兖、允二音，字林云：「馬逆毛也」\n[郭注-16]：馬毛逆刺。', '釋畜弟十九', '爾雅', '5', DEFAULT, DEFAULT, '4599/3412;7442/9624;571/5455;7784/5940;5404/5460', '7265/8853', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5681, '騋;驪;玄駒;褭驂', '𩡧駒', '騋牝1，驪牡2。34玄駒[𩡧駒]5，褭驂67。89', '[釋文-1]：力才反，周禮云：「馬七尺也。」頻忍反，下同\n[釋文-2]：孫注改上「騋牝」爲「牡」，讀與郭異\n[郭注-3]：詩云：「騋牝三千。」馬七尺已上爲騋，見周禮。\n[釋文-4]：上，時丈反。\n[釋文-5]：字林作䮄，音同\n[音釋-6]：奴了。參。\n[釋文-7]：奴了反，又而紹反，字林云：「騕褭，良馬。」七南反\n[郭注-8]：玄駒，小馬，別名褭驂耳。或曰：此卽騕褭，古之良馬名。\n[釋文-9]：騕，烏了反，郭注上林賦云：「騕褭，神馬，日行萬里。」', '釋畜弟十九', '爾雅', '3', DEFAULT, DEFAULT, '6174;6144;2507/6140;5370/6191', '6139/6140', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5682, '𨽥;舍', '社', '牡曰𨽥1，23牝曰騇{舍}[社]45。67', '[釋文-1]：之逸反\n[郭注-2]：今江東呼䭸馬爲騭，音質。\n[釋文-3]：父，符甫反，本或作䭸，俗字。\n[音釋-4]：舍。\n[釋文-5]：音舍\n[郭注-6]：草馬名。\n[釋文-7]：草，本亦作騲，魏志云：「敎民畜牸牛、騲馬」是也。', '釋畜弟十九', '爾雅', '2', DEFAULT, DEFAULT, '6138;3275', '64', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5683, '駵;駁;皇;建', '騫', '駵白，駁1。黃白，騜{皇}2；3駵馬黃脊4，騝{建}[騫]56。', '[釋文-1]：字林云：「馬色不純也，方卓反。」補舍人曰：「駵赤色名曰駮也，駵馬名白馬。」樊光曰：「易乾爲駁馬」\n[釋文-2]：音皇，字林于亡反\n[郭注-3]：詩曰：「騜駁其馬。」\n[釋文-4]：音積\n[音釋-5]：虔。\n[釋文-6]：郭音虔，本或作騫，去虔反', '釋畜弟十九', '爾雅', '4', DEFAULT, DEFAULT, '6147;6161;80;1263', '6221', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5684, '驪;騽;駽;驒;柔;𠤏;駓;駰;騅;騢;駱;全;騧;瞯;魚', '驔;犉', '驪馬黃脊，騽[驔]12。3靑驪，駽45。67靑驪驎89，驒1011。12靑驪繁鬣1314，騥{柔}1516。1718驪白雜毛，駂{𠤏}1920。21黃白雜毛，駓2223。2425陰白雜毛，駰2627。2829蒼白雜毛，騅30。31彤白雜毛32，騢3334。3536白馬黑鬣37，駱38。3940黃馬黑脣，駩{全}[犉]4142；黑喙4344，騧4546。47一目白，瞯4849；二目白，魚50。5152', '[音釋-1]：習。\n[釋文-2]：音習，說文作驔，音簟，字林云又音譚，今爾雅本亦有作驔者\n[郭注-3]：皆背脊毛黃。\n[音釋-4]：呼縣。\n[釋文-5]：詩音及呂忱、顏延之、苟楷並呼縣反，郭火玄反，謝、孫犬縣反，顧胡眄反\n[郭注-6]：今之鐵驄。\n[釋文-7]：鐵，佗結反。驄，七工反，說文云：「靑黑雜毛馬。」\n[音釋-8]：良忍。\n[釋文-9]：粼，本或作驎，郭良忍反，注同，字林良振反，郭云：「斑剥隱粼也。」或音鄰，孫云：「似魚鱗也」\n[音釋-10]：陀。\n[釋文-11]：徒河反，說文云：「馬文如鼉魚。」韓詩、字林皆云：「白馬黑髦」\n[郭注-12]：色有深淺，班駁隱粼，今之連錢驄。\n[音釋-13]：獵。\n[釋文-14]：力涉反，髦也。補舍人曰：「馬鬉也」\n[音釋-15]：柔。\n[釋文-16]：而周反，本又作柔\n[郭注-17]：禮記曰：「周人黃馬繁鬣。」繁鬣，兩被毛。或云美髦鬣。\n[釋文-18]：被，普義反，又皮義反。\n[音釋-19]：保。\n[釋文-20]：音保，說文云：「黑馬驪白雜毛」\n[郭注-21]：今之烏驄。\n[音釋-22]：皮。\n[釋文-23]：備悲反，字林音丕\n[郭注-24]：今之桃華馬。\n[釋文-25]：華，音花，本亦作花，同。\n[音釋-26]：乙巾。\n[釋文-27]：字林乙巾反，郭央珍反，今人多作因音。補樊光曰：「目下白」\n[郭注-28]：陰，淺黑，今之泥驄。\n[釋文-29]：泥，奴兮反。\n[釋文-30]：音隹\n[郭注-31]：詩曰：「有騅有駓。」\n[釋文-32]：徒冬反，赤也\n[音釋-33]：遐。\n[釋文-34]：乎加反，說文云：「馬赤白雜色，文似鰕魚也」\n[郭注-35]：卽今之赭白馬。彤，赤。\n[釋文-36]：赭，音者。\n[釋文-37]：舍人同，衆家並作髦\n[釋文-38]：音洛，說文云：「白色馬黑毛尾也。」廣雅云：「白馬朱鬛曰駱」\n[郭注-39]：禮記曰：「夏后氏駱馬黑鬣。」\n[釋文-40]：夏，音下。\n[音釋-41]：詮。\n[釋文-42]：音詮，又音全，孫本作犉，云：「與牛同稱。汝均反。」本或作驎，音吝\n[音釋-43]：許穢。\n[釋文-44]：許穢反，又昌銳反，口也\n[音釋-45]：瓜。\n[釋文-46]：古花反，毛詩傳、說文、字林皆云：「黃馬黑喙曰騧。」郭云：「今以淺黃色爲騧馬」\n[郭注-47]：今之淺黃色者爲騧馬。\n[音釋-48]：閑。\n[釋文-49]：𥊺，音閑，本又作瞯，蒼頡篇云：「目病也，吳江湖之閒曰𥊺。」說文云：「戴目也。」字林作𩦃，音同\n[釋文-50]：本又作䁩，疑居反，字林作𩥭，音同\n[郭注-51]：似魚目也。詩曰：「有驔有魚。」\n[釋文-52]：驔，徒南、大㸃二反。', '釋畜弟十九', '爾雅', '2;12', DEFAULT, DEFAULT, '6144;6165;6145;6249;3609;5219;6157;6151;6149;6148;6150;3285;6155;2190;7580', '6163;746', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5685, '差;擇', '', '「旣差我馬12。」差，擇也。宗廟齊毫3，4戎事齊力，5田獵齊足6。7', '[音釋-1]：叉。\n[釋文-2]：楚佳反\n[釋文-3]：音豪。補李巡曰：「祭於宗廟，當加謹敬，取其同色也」\n[郭注-4]：尚純。\n[郭注-5]：尚強。\n[釋文-6]：補舍人曰：「田獵取牲於苑囿之中，追飛逐走，取其疾而已」\n[郭注-7]：尚疾。', '釋畜弟十九', '爾雅', '', DEFAULT, DEFAULT, '3029;7899', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5686, '麻牛;㬥牛;罷牛;巍牛;鬣牛;犝牛;狊牛;觭;觢;犉;袖;尉;牧;卷;犢;㸬;欣叚', '筋叚', '犘牛{麻牛}12。3犦牛{㬥牛}45。67犤牛{罷牛}89。1011魏牛{巍牛}1213。1415犣牛{鬣牛}1617。1819犝牛2021。22犑牛{狊牛}2324。25角一俯一仰26，觭2728。2930皆踴31，觢3233。34黑脣，犉3536。37黑眥3839，牰{袖}4041。42黑耳，犚{尉}4344。黑腹，牧。黑腳，犈{卷}4546。47其子犢48，4950體長，牬{㸬}5152。53絶有力，欣犌{欣叚}[筋叚]5455。', '[音釋-1]：麻。\n[釋文-2]：亡巴反\n[郭注-3]：出巴中，重千斤。\n[音釋-4]：雹。\n[釋文-5]：𤜌，步角反，張揖亡角反，字林方沃反。案：卽今之腫領牛，出合浦郡\n[郭注-6]：卽犎牛也，領上肉犦胅起，高二尺許，狀如橐駝，肉鞍一邊，健行者日三百餘里。今交州 合浦 徐聞縣出此牛。\n[釋文-7]：犎，甫逢反。𦣃，音與上犦字同，本亦作犦，鄭注考工記云：「𦣃謂墳起。」胅，大結反。橐，音託，字又作馲，音同，又音洛。駝，大河反，字林云：「馲駝，似鹿而大，肉鞌，出繞山。」日，而一反。浦，音普。\n[音釋-8]：悲。\n[釋文-9]：音碑，又音皮\n[郭注-10]：犤牛庳小，今之㹄牛也。又呼果下牛，出廣州 高涼郡。\n[釋文-11]：庳，音碑。㹄，子息反，本或作稷。涼，音良。\n[音釋-12]：危。\n[釋文-13]：犩，郭魚威反，張揖同，字林牛畏反，云：「黑色而大，重三千斤」\n[郭注-14]：卽犪牛也，如牛而大，肉數千斤，出蜀中。山海經曰：「岷山多犪牛。」\n[釋文-15]：犪，巨龜反，字林云：「牛柔謹也。」顧如小、如照二反。數，所主反。岷，亡巾反。\n[音釋-16]：獵。\n[釋文-17]：力涉反，字林云：「牛名也。」郭云：「旄牛也。」本或作鬣字，此牛多毛鬣\n[郭注-18]：旄牛也，髀、膝、尾皆有長毛。\n[釋文-19]：髦，本又作旄，同，音毛，或音亡交反。髀，必爾反，又步啟反。\n[音釋-20]：童。\n[釋文-21]：音童，字林云：「牛名」\n[郭注-22]：今無角牛。\n[音釋-23]：古覓。\n[釋文-24]：古闃反\n[郭注-25]：未詳。\n[釋文-26]：音甫\n[音釋-27]：欺，\n[釋文-28]：郭去宜反，字林丘戯、江宜二反，云：「一角低，一角仰。」樊云：「傾角曰觭」\n[郭注-29]：牛角低仰。\n[釋文-30]：卬，五剛反，又魚丈反。\n[釋文-31]：音勇\n[音釋-32]：誓。\n[釋文-33]：字或作㸷，郭常世反，字林之世反\n[郭注-34]：今竪角牛。\n[音釋-35]：閏旬。\n[釋文-36]：𤜃，閏旬反，字亦作犉，音同\n[郭注-37]：毛詩傳曰：「黃牛，黑脣。」此宜通謂黑脣牛。\n[音釋-38]：才細。\n[釋文-39]：才細反，說文云：「目匡也。」字林云：「目厓也」\n[音釋-40]：䄂。\n[釋文-41]：音䄂，字林音就，本或作褎，音同\n[郭注-42]：眼眥黑。\n[音釋-43]：尉。\n[釋文-44]：音尉\n[音釋-45]：權。\n[釋文-46]：音權，又音眷\n[郭注-47]：皆別牛黑所在之名。\n[釋文-48]：徒木反\n[郭注-49]：今靑州呼犢爲𤘽。\n[釋文-50]：𤘽，火口反，字林云：「牛鳴也。」\n[音釋-51]：貝。\n[釋文-52]：傅蓋反\n[郭注-53]：長身者。\n[音釋-54]：加。\n[釋文-55]：古牙反', '釋畜弟十九', '爾雅', '17', DEFAULT, DEFAULT, '4536/728;4248/728;4843/728;5849/728;5754/728;774/728;6315/728;2835;2833;746;5287;6472;2068;5786;733;734;5538/1928', '2741/1928', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5687, '羊;羒;牂;夏羊;羭;羖;觤;斂;羳羊;羜;奮', '', '羊，牡羒12、34牝牂56，78夏羊9，1011牡羭1213、1415牝羖16。17角不齊，觤1819。20角三觠2122，羷{斂}2324。2526羳羊2728，黃腹。29未成羊，羜3031。32絶有力，奮。', '[音釋-1]：憤。\n[釋文-2]：又作㸮，同，符云反，字林云：「牂羊」\n[郭注-3]：謂吳羊，白羝。\n[釋文-4]：羝，丁兮反，字林云：「牂羊也。」廣雅云：「三歲曰羝。」\n[音釋-5]：臧。\n[釋文-6]：子郎反，字林云：「三歲曰牂」\n[郭注-7]：詩曰：「牂羊墳首。」\n[釋文-8]：墳，扶云反。\n[釋文-9]：戸雅反，黑羖羊也\n[郭注-10]：黑羖䍽。\n[釋文-11]：羖，音古，字林云：「夏羊牝。」䍽，音歷。\n[音釋-12]：俞。\n[釋文-13]：郭羊朱反，字林羊句反\n[郭注-14]：黑羝也。歸藏曰：「兩壷兩羭。」\n[釋文-15]：壺，音乎。\n[音釋-16]：古\n[郭注-17]：今人便以「牂」「羖」爲白、黑羊名。\n[音釋-18]：鬼。\n[釋文-19]：九委反\n[郭注-20]：一短一長。\n[音釋-21]：權。\n[釋文-22]：呂、郭音權，謝居轉反\n[音釋-23]：險。\n[釋文-24]：謝許檢反，字林力冉反，一音力驗反\n[郭注-25]：觠，角三迊。\n[釋文-26]：迊，子合反。\n[音釋-27]：煩。\n[釋文-28]：音煩\n[郭注-29]：腹下黃。\n[音釋-30]：直呂。\n[釋文-31]：直呂反，又之呂反，字林云：「五月生羔」\n[郭注-32]：俗呼五月羔爲羜。', '釋畜弟十九', '爾雅', '', DEFAULT, DEFAULT, '2333;2341;2342;3373/2333;2343;2344;2847;2024;2347/2333;2336;2321', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5688, '犬;㚇;師;蘄;狗;獫;猲獢;跳;尨', '豵;對;㹞', '犬生三1，猣{㚇}[豵]23；二，師[對]；一，玂{蘄}[㹞]45。6未成毫7，狗8。910長喙11，獫1213。短喙，猲獢1415。16絶有力，狣{跳}1718。尨19，狗也。2021', '[釋文-1]：說文云：「狗縣蹄者，象形。孔子曰：視犬之字，如畫狗也」\n[音釋-2]：宗。\n[釋文-3]：子工反\n[音釋-4]：祈。\n[釋文-5]：音祁\n[郭注-6]：此與豬生子義同，名亦相出入。\n[釋文-7]：戸刀反，字又作豪\n[釋文-8]：古口反，字林云：「家獸也。」說文云：「孔子曰：狗，叩也，叩气吠以守也」\n[郭注-9]：狗子未生㲦毛者。\n[釋文-10]：㲦，下旦反，又胡肝反，謂長毛也。\n[釋文-11]：許穢反，又昌銳反\n[音釋-12]：力驗。\n[釋文-13]：力驗反，字林力劒反，呂力冉反，郭九占、沈儉二反\n[音釋-14]：歇。虛嬌。\n[釋文-15]：𤢔，許謁反，字林作猲，火遏反。虛驕反\n[郭注-16]：詩曰：「載獫猲獢。」\n[音釋-17]：兆。\n[釋文-18]：呂、郭同音兆\n[釋文-19]：亡江反，說文云：「犬之多毛。」字林同\n[郭注-20]：詩曰：「無使尨也吠。」\n[釋文-21]：吠，扶廢反。', '釋畜弟十九', '爾雅', '2;3;4', DEFAULT, DEFAULT, '6303;3375;3864;328;6304;6311;6309/6310;1370;6306', '6082;1751;6332', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5689, '雞;蜀;餘;連;奮', '練', '雞，大者蜀，1蜀子雓{餘}23。45未成雞，僆{連}[練]67。89絶有力，奮。1011', '[郭注-1]：今蜀雞。\n[音釋-2]：餘。\n[釋文-3]：音餘，字或作餘\n[郭注-4]：雛子名。\n[釋文-5]：雛，仕俱反，本今作鶵。\n[音釋-6]：練。\n[釋文-7]：郭音練，力見反，又力健、力展二反\n[郭注-8]：今江東呼雞少者曰僆，音練。\n[釋文-9]：少，詩照反。\n[郭注-10]：諸物有氣力多者，無不健自奮迅，故皆以名云。\n[釋文-11]：迅，信、峻二音。', '釋畜弟十九', '爾雅', '4', DEFAULT, DEFAULT, '2291;8828;3247;1168;2321', '8600', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5690, '駥;犉;麙;戹;獒;鶤', '', '馬八尺爲駥12。34牛七尺爲犉56。7羊六尺爲羬{麙}89。10彘五尺爲䝈{戹}1112。13狗四尺爲獒1415。16雞三尺爲鶤1718。1920', '[音釋-1]：戎。\n[釋文-2]：戎，本亦作駥，而融反\n[郭注-3]：周禮云：「馬八尺已上爲駥。」\n[釋文-4]：上，時掌反。\n[音釋-5]：閏旬。\n[釋文-6]：閏旬反\n[郭注-7]：詩曰：「九十其犉。」亦見尸子。\n[音釋-8]：五咸。\n[釋文-9]：本亦作麙，五咸反\n[郭注-10]：尸子曰：「大羊爲羬，六尺。」\n[音釋-11]：滯。厄。\n[釋文-12]：直例反。於革反，大豕也，小爾雅云：「豕之大者謂之豜」\n[郭注-13]：尸子曰：「大豕爲䝈，五尺。」今漁陽呼豬大者爲䝈。\n[音釋-14]：五刀。\n[釋文-15]：五刀反，尚書云：「西旅獻獒。」孔傳云：「大犬也。」說文云：「犬知人心可使者。」字林同，廣雅云：「殷虞、晉獒、楚獷、韓獹、宋㹱，皆良犬也。」㹱音鵲\n[郭注-16]：公羊傳曰：「靈公有害狗，謂之獒也。」尚書孔氏傳曰：「犬高四尺曰獒。」卽此義。\n[音釋-17]：昆。\n[釋文-18]：音昆，字或作鵾，同，或音運，又音輝\n[郭注-19]：陽溝巨鶤，古之名雞。\n[釋文-20]：溝，古侯反。', '釋畜弟十九', '爾雅', '', DEFAULT, DEFAULT, '6255;746;6283;7727;6337;2396', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5691, '觚', '柧', '急就奇觚[柧]與衆異，1羅列諸物名姓字。2', '[顏注-1]：觚者，學書之牘，或以記事，削木爲之，蓋簡屬也。孔子歎觚，卽此之謂。其形或六面、或八面，皆可書。觚者，棱也，以有棱角，故謂之觚。言學僮急當就此奇好之觚。其中深博，與衆書有異也。班固兩都賦曰：「上觚棱而棲金爵。」今俗猶呼小兒學書簡爲「木觚章」，蓋古之遺語也。\n[顏注-2]：言此衆章，列敘萬物之名及人姓字。', '急就篇', '急就篇', '1', DEFAULT, DEFAULT, '2857', '3802', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5692, '宋延年;鄭子方', '', '宋延年，1鄭子方。2', '[顏注-1]：微子，紂之庶兄，周武王克商，封之於宋，其後或以國爲姓，遂有宋氏。延年之義，取於壽考無疆也。漢有李延年、杜延年、田延年。篇首廣陳諸姓及名字者，以示學徒，令其識習，擬施用也。自此以下，器用物務，次敘皆同。而說者乃云是當時弟子名姓，又云是古賢聖之人，本出易緯，史游重述，此說皆非也。姓者並是古來所有，非妄造之。名字或是舊人已經稱用，或是新構義理，然非實相配屬，真有其人。所以章中自云「姓名訖，請言物」，又云「諸物訖，五官岀」。以此求之，其意可曉。至如雜寳奇繒，殊俗異服，及疾病刑獄，官曹職務，豈非當時庠校之內悉自有乎？皆汎說耳。易緯中頗有姓名與此同者，蓋後人妄取以附著之，非本聖人所說也。先儒通論舊云：緯書之作，僞起哀 平。應劭撰風俗通，亦多設人姓而爲章句偶讀，斯效急就之爲也。\n[顏注-2]：鄭桓公 友，周厲王之子，宣王母弟也。宣王封之於鄭，其後或以國爲氏。宋有鄭翩，卽其族也。子者，男子美稱。方者，言其正直不回也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '4622/1265/4484;4030/9766/5455', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5693, '衛益壽;史步昌', '', '衛益壽，1史步昌。2', '[顏注-1]：衛康叔，周文王之子也。周公旣誅管 蔡，而封康叔爲衛侯，其後亦因國爲氏。益壽，亦延年之義也。\n[顏注-2]：史氏之先，本周大夫史佚之後也。周亦有史籀，晉有史黯，秦有史顆，衛有史狗、史鰌，其後並稱史氏。此類甚多，不可具載。步昌，言高步而昌盛也。漢有李步昌。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '1277/3155/5384;1933/1081/4239', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5694, '周千秋;趙孺卿', '', '周千秋，1趙孺卿。2', '[顏注-1]：周大夫周桓公宰周公，周公忌父繼守官邑，故有周氏。周任之後，亦爲周姓。千秋，亦欲長生久視也。漢有張千秋、田千秋。\n[顏注-2]：造父善御，事周穆王，穆王封之趙城，其後因稱趙氏。晉之趙風，卽其苗裔，遂蕃衍焉。孺，幼少之號也。卿，言可爲列卿也。漢有蘇賢，字孺卿。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '877/1465/4493;1030/9772/5797', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5695, '爰展世;高辟兵', '', '爰展世，1高辟兵。2', '[顏注-1]：爰氏之先，本與陳同姓。陳申公生静伯甫，伯甫八世孫爰諸生爰濤塗，因而命氏。其後或爲轅字，又作袁字，本一族也。漢有爰盎。展者，伸也，言子孫蟬聯不絕也。或曰展，誠也。\n[顏注-2]：齊大夫高傒者，號高敬仲，本齊之公族也。其後遂稱高氏，大爲著姓。又惠公之孫曰公孫竈，亦稱高氏。辟兵，言能弭止兵戎也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '2517/5407/1473;3320/5798/1768', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5696, '鄧萬歲;秦眇房', '', '鄧萬歲，1秦妙房{秦眇房}。2', '[顏注-1]：鄧，古國名，本曼姓也，其後稱鄧氏焉。楚有鄧廖，鄭有鄧析，並其族也。萬歲猶千秋耳。\n[顏注-2]：秦本地名，後爲國號，因又命氏。魯有秦堇父、秦丕兹、秦遄，皆秦姓也。妙謂德行高妙也，房謂德之所聚也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '4084/9735/1082;4494/2198/7725', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5697, '郝利親;馮漢強', '', '郝利親，1馮漢強。2', '[顏注-1]：郝，京兆 盩厔鄉名也，因地以命氏焉。漢有郝賢。今盩厔縣猶出郝姓。利親，言其善父母也。\n[顏注-2]：馮氏之先，本歸姓也。鄭大夫有馮簡子，其後遂爲大族。漢強，意在忠於本朝也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '4028/2752/5514;6203/7009/8826', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5698, '戴護郡;景君明', '𨚵護郡', '戴護郡[𨚵護郡]，1景君明。2', '[顏注-1]：戴氏之先與宋同族。宋戴公生公子文，遂稱戴氏，戴惡卽其後也。護郡，言爲太守若都尉。\n[顏注-2]：景氏，楚之同族，本𦍋姓也。漢高祖用婁敬之計，徙齊、楚大族入關，景氏亦遷名數，今之好畤、鄭縣、華陰諸景是也。君明，言有明德，宜爲列國諸侯也。漢有京房，字君明。', '急就篇', '急就篇', '1', DEFAULT, DEFAULT, '1779/1562/4006;4213/839/4323', '4158/1562/4006', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5699, '蕫奉德;桓賢良', '', '董奉德{蕫奉德}，1桓賢良。2', '[顏注-1]：董氏之先，本己姓也。飂叔安之裔子曰董父，能擾畜龍，服事帝舜，舜賜之姓曰董。又周大夫辛有之二子適晉，與籍氏俱董督晉之史籍，因爲董氏。董狐、董叔、董安于皆其後也。奉德，言其奉持德義也。一曰言有賢德，人所戴奉也。\n[顏注-2]：宋桓公孫鱗矔爲宋司徒，號曰桓子，因爲氏焉。向魋號桓司馬，其後亦稱桓族。賢良，言可應賢良之舉也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '402/1756/1224;3673/3945/3341', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5700, '任逢時;侯仲郎', '', '任逢時，1侯仲郎。2', '[顏注-1]：周文王母，任氏之女，此古姓也。又有任國之君，太皥之後，本風姓也，其後亦爲任氏焉。逢時，言遇太平也。漢有王逢時。\n[顏注-2]：侯氏之先，鄭大夫侯宣多、侯羽之後也。魯有侯叔夏、侯犯，齊有侯朝，其後亦皆以爲姓。仲者，次伯之下。郎者，可爲郎官也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '5089/1130/4191;3313/4968/4139', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5701, '由廣國;榮惠常', '', '由廣國，1榮惠常。2', '[顏注-1]：戎人由余入秦爲相，其後遂稱由氏。楚大夫王孫由于有忠勇之節，楚人亦謂其族爲由氏焉。廣國，言爲本朝益土地也。\n[顏注-2]：周大夫有食采於榮者，因而氏焉。周書曰：「王俾榮伯作賄肅慎之命。」春秋稱：「天王使榮叔來含。」國語云：「厲王悦榮夷公。」皆榮氏也。魯有榮駕鵝，楚有榮黃，其後亦稱榮氏。孔子所見榮啟期，泰山 郕人也，或謂之榮聲期，啟、聲相近故也。漢有榮畜。惠常，言以仁惠爲常也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '8516/5940/3917;3538/2505/4872', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5702, '烏承禄;令狐橫', '令狐桄', '烏承禄，1令狐橫[令狐桄]。2', '[顏注-1]：齊大夫烏餘之後，遂以爲姓。莒亦有烏存，秦有力士曰烏獲。承，受之也。\n[顏注-2]：令狐，晉地名也，在河東。大夫魏顆食邑於此，號令狐顆，因爲氏焉。橫，充也、大也。', '急就篇', '急就篇', '2', DEFAULT, DEFAULT, '2488/7914/15;5779/6382/3796', '5779/6382/3798', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5703, '朱交便;孔何傷', '', '朱交便，1孔何傷。2', '[顏注-1]：舜臣朱武，其後以爲姓。論語有𨓜民朱張。春秋左氏傳，齊有朱毛，漢有朱家。交便，善與人交，皆便安之也。\n[顏注-2]：孔氏之先，與宋同祖。閔公生弗父何，何曾孫曰正考父，考父生孔父嘉，仲尼卽其後也。衞有大夫孔逹、孔烝鉏，其後亦稱孔氏。何傷，言無所傷害也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '3563/6627/5088;7704/5025/5158', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5704, '師猛虎;石敢當', '', '師猛虎，1石敢當。2', '[顏注-1]：師，樂人無目者之稱也。晉有師曠，魯有師己，鄭有師悝、師蠲、師觸，其後皆以爲姓。以猛獸爲名，尚其威也。\n[顏注-2]：衛有石碏、石買、石惡，鄭有石癸、石楚、石制，皆爲石氏。周有石速，齊有石之紛如，其後亦以命族。敢當，言所當無敵也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '3864/6345/3118;6011/2524/9193', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5705, '所不侵;龍未央', '', '所不侵，1龍未央。2', '[顏注-1]：所所，斫木聲也。古有虞衡之官，因主伐木，遂以爲姓。漢有所忠，武帝之近臣也。不侵，言其謹慤，不爲寇暴也。春秋左氏傳曰：「不侵不畔之臣。」\n[顏注-2]：龍，舜臣名，命爲納言者也，其後遂以爲姓。又董父旣賜姓曰董，又易氏曰豢龍，其後亦爲龍氏。漢有龍德，善鼓琴。未央，言益壽無極也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '9484/7706/5078;7689/9801/3327', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5706, '伊嬰齊;翟回慶', '', '伊嬰齊，1翟回慶。2', '[顏注-1]：伊摰、伊陟，殷之卿宰，其後遂爲著姓。漢有伊籍。嬰齊，謂嬰兒之絜齊者也。魯有公孫嬰齊，漢有南越王 趙嬰齊。\n[顏注-2]：翟氏，本齊 翟僂新之後也。魏有翟璜、翟翦。漢有翟公、翟方進。回慶者，言福喜方來也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '4969/8261/4395;2245/3914/6757', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5707, '畢隹季;昭小兄', '畢稺季', '畢稚季{畢隹季}[畢稺季]，1昭小兄。2', '[顏注-1]：周文王之子有畢公高者，其後以爲姓。晉之畢萬卽此族焉。稚季者，言非長，叔幼也。漢有杜稚季。\n[顏注-2]：昭氏亦楚同姓，共屈、景爲三族者也。當戰國時，有昭奚恤。小兄，言上有昆而下有弟也。', '急就篇', '急就篇', '1', DEFAULT, DEFAULT, '2492/2279/9773;4199/704/5464', '2492/4424/9773', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5708, '柳堯舜;樂禹湯', '', '柳堯舜，1樂禹湯。2', '[顏注-1]：魯公子展之後也。有展禽者，食采於柳下之地，因爲柳氏焉。堯舜，言依唐 虞二帝之則以自警也。\n[顏注-2]：樂氏之先，與宋同姓。戴公生樂父衎，是稱樂氏。樂喜皆其後也。楚有樂伯，亦因得姓。戰國時，燕有樂毅、樂閒。禹湯，言欽夏 殷兩王之美，順其則也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '3514/9166/3382;3764/9736/7362', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5709, '淳于登;費通光', '', '淳于登，1費通光。2', '[顏注-1]：春秋 淳于公如曹，度其國危，遂不復。淳于之地，卽北海 淳于縣是也。後以國爲姓。戰國時，齊有淳于髡。秦始皇時有淳于越。漢有淳于長。\n[顏注-2]：費氏，楚大夫費無極之後也。漢有費直。通光，猶言開明。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '7417/3070/1079;3975/1134/6511', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5710, '柘溫舒;路政陽', '', '柘溫舒，1路政陽。2', '[顏注-1]：柘，楚地名也，後更爲縣，時屬淮陽國。所居之人因而命氏。溫舒，言其溫裕而安舒。漢有路溫舒。\n[顏注-2]：路，水名也，又因爲縣，在𣵠郡界，居者氏焉。漢有路中大夫、路溫舒。政陽，言其堪爲政而有光陽也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '3534/6997/2511;1410/2006/9624', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5711, '霍聖宮;顏文章', '', '霍聖宮，1顏文章。2', '[顏注-1]：周文王之子有霍叔武者，其國在河東，今霍邑縣是也。後嗣爲晉獻公所滅，子孫遂姓霍氏。漢之霍去病、霍光，卽河東人也。聖宮，言覩聖人之奥祕，若入其宮室也。子贛曰：「譬之宮牆，夫子之牆數仞，不得其門而入，不見宗廟之美、百官之富。」\n[顏注-2]：顏氏本出顓頊之後。顓頊生老童，老童生吳回，爲高辛火正，是謂祝融。祝融生陸終，陸終生六子，其五曰安，是爲曹姓。周武王封其苗裔於邾，爲魯附庸，在魯國 鄒縣。其後邾武公名夷父，字曰顏，故春秋公羊傳謂之顏公，其後遂稱顏氏。齊 魯之閒，皆爲盛族。孔子弟子逹者七十二人，顏氏有八人焉。四科之首，回也，標爲德行，韓子稱儒分爲八，而顏氏處其一焉。漢有顏泗、顏異、顏安樂，以春秋名家。文章，言其文章也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '2364/7803/4630;5602/5725/1742', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5712, '管財智;偏呂張', '', '管財智，1偏呂張。2', '[顏注-1]：管氏之先，出自周文王，以國命氏也。齊有管至父、管夷吾、管于奚、管脩，因爲著姓。財智，言富於財而又多智也。\n[顏注-2]：楚之軍帥，使主偏者，因以爲姓也。司馬法曰：「車十五乘曰偏。」春秋左氏傳曰：「廣有一卒，卒偏之兩。」呂張，言爲心呂之臣，可張大王室也。昔者大岳爲禹心呂之臣，故封呂侯，以譬身有脊呂骨也。其爲字象形，非兩口也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '2995/3939/2231;5120/4632/8488', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5713, '魯賀喜;觀宜王', '', '魯賀喜，1觀宜王。2', '[顏注-1]：伯禽之後，有悼公者，爲楚所滅，子孫以國爲氏。賀喜，言有喜而可賀也。\n[顏注-2]：觀，夏之同姓諸侯也。國滅之後，因而氏焉。晉有觀武，楚有觀起、觀從，皆其胤也。觀，字或作灌。斟灌，亦夏時國名也，其後爲灌氏焉。漢有灌嬰。宜王，言有益於王室者也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '2228/3947/3075;5486/4599/78', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5714, '程忠信;吳仲皇', '', '程忠信，1吳仲皇。2', '[顏注-1]：程氏之先，本重黎之後。周有程伯休父，因姓程焉。晉有程鄭，實佐下軍。漢有程鄭，臨卭富室。忠信爲周，言不阿黨也。\n[顏注-2]：周大王之子太伯，始封於吳，吳氏卽其後也。仲者，次伯之下。皇者，正也、大也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '4497/6715/1523;6621/4968/80', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5715, '許終古;賈友倉;陳元始;韓魏唐', '鄦從古', '許終古[鄦從古]，1賈友倉。2陳元始，3韓魏唐。4', '[顏注-1]：許氏，許由之後也。太嶽之㣧有姜姓者，封於許國，其後爲楚所滅，末裔亦姓許。晉有許偃。鄭有許瑕。楚有許伯，亦稱許氏。終古，言不廢絕也。漢有濟北思王 終古。\n[顏注-2]：賈，本姫姓國也，晉吞滅之，後稱賈氏。晉有賈華、賈佗、賈伯、賈辛，皆其裔也。友倉，言慕倉舒，追而友之。\n[顏注-3]：舜後嬀滿，周武王封之於陳，其後爲楚所滅，子孫因姓陳焉。元始，長子之稱也。\n[顏注-4]：周武王之子封於韓，其後爲晉所滅，因稱韓氏。晉大夫韓萬卽其後也。及三家篡晉，侯王相襲，故其宗黨分布四方。或曰：韓萬，曲沃 桓叔之子也。晉滅韓，令萬食其地焉，因爲韓氏。魏唐，取二國之地以爲名也。魏本姫姓，亦爲晉所滅，其地卽河東 河北縣是也。唐卽晉地也，故詩蟋蟀敘曰：「此晉也，而謂之唐。」', '急就篇', '急就篇', '1', DEFAULT, DEFAULT, '1484/8590/1461;3977/1930/3279;9688/2/8184;3399/5849/878', '4075/5227/1461', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5716, '液容調;柏杜楊', '', '液容調，1柏杜楊。2', '[顏注-1]：上古道術之士，善於鍊化，能作液湯者，後嗣因以液爲姓。容調者，言其容止調和。\n[顏注-2]：古帝有柏皇氏，其後爲柏氏焉。一曰晉 伯宗，伯州犂之後。古通用字，柏與伯同，故爲柏氏。魏有柏直，漢有柏至。杜楊，以二木爲名也。杜，甘棠也。牡者曰棠，無子者也。牝者曰杜，有子者也。楊，一名蒲柳，可以爲矢。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '7395/4589/1549;3549/3433/3512', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5717, '曹富貴;尹李桑', '', '曹富貴，1尹李桑。2', '[顏注-1]：曹姓卽陸终之子安所出也。又周文王之子曰曹叔振鐸，武王封之陶丘，號曰曹伯。其後爲宋所滅，亦爲曹氏。而齊有曹開，亦稱曹族。富貴，言滿而不溢、高而不危也。\n[顏注-2]：尹氏本周大夫也，後爲官族。李桑，義與「杜楊」同。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '3051/4586/3981;1914/3425/3860', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5718, '蕭彭祖;屈宗談', '', '蕭彭祖，1屈宗談。2', '[顏注-1]：蕭氏本出殷之六族，成王所用封伯禽也。又有蕭國，其君子姓，宋之附庸，爲楚所滅，亦稱蕭氏。彭祖者，追慕彭鏗，尚其長年也。漢有張彭祖、嚴彭祖。\n[顏注-2]：屈氏之先，楚同姓也。屈瑕、屈重、屈蕩、屈建、屈平，皆其後焉。宗談，言有口辯，爲談者所宗也。以談合桑，古韻疏也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '443/3081/36;5430/4624/1478', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5719, '樊愛君;崔孝讓', '', '樊愛君，1崔孝讓。2', '[顏注-1]：周大夫仲山甫，宣王時爲樊侯，遂稱樊氏。樊皮卽其後也。愛君，言慕鬻拳之爲人也。\n[顏注-2]：崔，齊邑名也。丁公之子於此受封。其後有崔夭，夭之支庶，苗裔盛焉。孝讓，言旣有孝德，又能克讓也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '1774/3369/839;5902/5386/1692', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5720, '姚得賜;燕楚莊', '', '姚得賜，1燕楚莊。2', '[顏注-1]：姚，舜姓也。陟方之後，末嗣稱焉。鄭有姚句耳，漢有姚平。得賜，言多受君之寵錫也。\n[顏注-2]：燕邵公 奭，本周之同姓，其後以國爲氏云。楚莊，言慕楚莊王也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '8117/1254/3961;7688/3851/241', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5721, '薛勝客;聶干將', '', '薛勝客，1聶干將。2', '[顏注-1]：有奚仲者，爲夏車正，受封於薛，後遷于邳。而仲虺居薛，爲湯左相，其後稱爲薛侯。及爲楚所并，餘族因號薛氏。勝者，克堪之義。客者，人禮敬之爲上客也。\n[顏注-2]：聶本地名，因以爲姓也。春秋稱「齊師、宋師、曹師次于聶北，救邢。」韓有聶政，漢有聶壹。干將，古之善劒，一曰劒師名也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '322/9229/4610;7825/1441/1983', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5722, '求男弟;過說長', '', '求男弟，1過說長。2', '[顏注-1]：求氏本居衛國 裘氏之地，故稱裘焉。後又轉爲求字。漢末有求仲，卽此族也。男者，以別女。弟者，有兄之稱也。\n[顏注-2]：過者，夏時國名，因爲姓也。說長，言常悦豫也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '5375/9209/3401;1110/1545/6069', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5723, '祝恭敬;審毋妨', '', '祝恭敬，1審毋妨。2', '[顏注-1]：祝氏，鄭 祝聃之後也。又衛有祝鮀，後嗣傳職，賢而有識，亦稱祝氏。恭敬，謂不忘恭敬，慕趙盾也。\n[顏注-2]：審氏之先，爲周司空屬官，主審曲面勢者也。後因賜族。漢有審食其。毋妨，言勿有所妨害也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '47/6743/5820;722/8357/8283', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5724, '龐賞贛;來士梁', '', '龐賞贛，1來士梁。2', '[顏注-1]：龐者，高屋之名。龐氏之先，貲產殷富，好爲室屋，鄉黨榮慕，謂之龐家，遂以立氏。魏有龐涓。贛，賜也。賞贛，義與「得賜」同。\n[顏注-2]：來氏，殷之別族，本子姓。士梁，言爲士君子之棟梁。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '5951/3960/3958;3348/226/3787', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5725, '成博好;范建羌', '', '成博好，1范建羌。2', '[顏注-1]：成者，周之采地，鄉士所食，成肅公、簡公、桓公是也，故稱成氏焉。又楚令尹子玉，若敖孫也，號成得臣，亦爲成氏。大心、成熊，皆其後也。晉有成鱄、成何，何後奔燕。宋有成讙，秦有成差，並爲成姓。博，大也。\n[顏注-2]：陶唐氏之後，在夏爲御龍氏，在商爲豕韋氏，在周爲唐杜氏。其後杜伯之孫曰士蔿，爲晉司空。蔿子士縠，亦繼其官。蔿孫士會，晉大傅，食邑於范，爲范氏。建羌，言立功於西羌之地也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '9749/1468/8191;656/1263/2357', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5726, '閻歡欣;寧可忘', '', '閻歡欣，1寧可忘。2', '[顏注-1]：閻者，里中門也，因其所居，遂以爲姓。晉有閻嘉、閻沒，齊有閻職。歡欣，言得人之歡心。\n[顏注-2]：寧氏與秦同姓。秦襄公曾孫諡曰寧公，支庶因以爲氏。一曰寧、甯本一姓，衛大夫甯俞之後也。漢有甯成，史記作寧成。可忘，言其善德不可忘也。詩云：「有斐君子，終不可諼兮。」諼亦忘也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '7742/5537/5538;3057/3059/6846', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5727, '苟貞夫;苗涉臧', '茅涉臧', '苟貞夫，1苗涉臧[茅涉臧]。2', '[顏注-1]：苟，草名也，所居饒之，因以命氏。漢有苟賓，河內人也，今之河內猶有此姓焉。一曰本晉大夫荀氏之後，避難改族而稱苟云。貞者，言有歲寒之節，夫者，大夫之謂也。\n[顏注-2]：苗賁皇，本楚人，後奔於晉，其子孫遂爲苗氏。王莾時有苗訢。篇本苗，字或作茅，國名也，周公子所封，其後因以爲氏。又邾有茅地及茅夷鴻，皆茅姓也。涉臧，言其所涉皆善道也。一曰遇難逃禍，涉水而藏匿也。', '急就篇', '急就篇', '2', DEFAULT, DEFAULT, '636/2077/6676;542/7477/1955', '326/7477/1955', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5728, '田細兒;謝內黃', '', '田細兒，1謝內黃。2', '[顏注-1]：田氏之先，職賦田者也，因以爲姓。晉有田蘇，宋有田景。又陳厲公之子完奔齊，代爲卿佐，遂篡齊國，後自稱王，又稱東帝，其族亦爲田氏，葢以陳、田聲相近云。及爲秦所滅，漢興，徙田族於關中，今之高陵、櫟陽諸田是也。細兒，言小兒也。\n[顏注-2]：謝，南方國名也，周宣王后父申伯於此作邑，其後以爲氏。魯有謝息。內黃，言黃中通理也。一曰取縣爲名也。內黃屬魏郡。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '9172/8561/5459;1570/3282/9203', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5729, '柴桂林;溫直衡', '', '柴桂林，1溫直衡。2', '[顏注-1]：衞人高柴，爲孔子弟子，後居於魯，末裔以柴爲姓。漢有將軍柴武。桂林者，取其冬生不凋而衆盛之意。\n[顏注-2]：溫氏之先，出自己姓。又晉之郤至，食邑河內之溫，號曰溫季，其後亦爲溫氏。直衡，言其正而平也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '3613/3431/3848;6997/8412/2844', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5730, '奚驕叔;邴勝箱', '', '奚驕叔，1邴勝箱。2', '[顏注-1]：奚仲旣遷于邳，其後遂稱奚氏。漢有奚涓。驕者，父母愛而名之。叔者，伯仲之下也。\n[顏注-2]：邴，泰山下邑也，居之者以爲姓焉。晉有邴豫、邴師。齊有邴歜、邴夏、邴意兹。漢有邴丹。勝箱，言其堪任近侍，在東西箱也。一曰言能負重致遠，以牛爲喻也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '6672/6173/1924;4118/9229/2969', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5731, '雍弘敞;劉若芳', '', '雍弘敞，1劉若芳。2', '[顏注-1]：雍，國名也，在河內 山陽，文王之子所封，其後以爲姓。又宋有雍氏，本姞姓也。鄭有雍糾，齊有雍廪，楚有雍子，後入于晉，其裔皆雍氏。漢有雍齒。弘敞，言其大而高明也。\n[顏注-2]：劉氏之先，與范氏同祖。士會自秦歸于晉，其族留秦不去者爲劉氏。又周之采地亦名爲劉，劉康公、定公、獻公，皆王卿士也，其後亦稱劉姓。若芳，言如蘭若之芬芳也。若謂杜若，芳草名也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '2300/8495/2017;9446/599/574', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5732, '毛遺羽;馬牛羊', '', '毛遺羽，1馬牛羊。2', '[顏注-1]：文王之子封於毛國，其後以爲姓焉。又周之采地亦名爲毛，卿士所食，毛伯衛、毛伯過皆是也，其後亦稱毛氏。趙有毛遂，漢有毛釋之。遺羽，自謙之稱，言極輕賤也。\n[顏注-2]：造父旣封趙城，其後稱馬氏，葢以善御馬云。又趙將趙奢，克敗秦師，賜號馬服君，其後亦爲馬姓。牛羊者，愛而諱之，比之於畜產者也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '5387/1174/2242;6137/728/2333', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5733, '尚次倩;丘則剛', '', '尚次倩，1丘則剛。2', '[顏注-1]：呂望爲周武王太師，號尚父，其後或以爲姓。次者，長幼之弟，倩者，靑色而美也。\n[顏注-2]：陳有宛丘，居之者以爲姓。又邾有丘弱，後亦爲丘氏。則猶象也，謂法象於天，爲剛德而不干時也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '711/5586/4971;5233/2756/2757', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5734, '陰賓上;翠鴛鴦', '', '陰賓上，1翠鴛鴦。2', '[顏注-1]：陰亦周之采地，周大夫陰忌、陰不佞、陰里，其後遂爲姓焉。賓上者，見賓禮於君上，取「利用賓于王」之義。\n[顏注-2]：翠氏，楚 景翠之後也，避入關之遷，懐土逃匿，改姓爲翠。鴛鴦，取匹鳥爲名也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '9623/3968/6;2247/2417/2418', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5735, '庶霸遂;萭段卿', '', '庶霸遂，1萭段卿。2', '[顏注-1]：庶氏之先，本出衛之公族，以非正嫡，遂號庶氏。禮記曰：「子思之母死於衞。」庶氏之女也。及春秋：「邾庶其來奔。」其後亦爲庶氏。霸遂，言能遂成霸功也。\n[顏注-2]：萭亦楀字，楀者，木名，因樹以得姓也。詩云：「楀維師氏。」謂女寵之族有姓楀者，爲師氏之官也。漢有萭章。段卿者，言其厚重如石之段，可爲卿也。春秋 鄭有印段字子石、公孫段字伯石，孫子曰：「如段之投卯。」', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '5958/4278/1175;320/1972/5797', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5736, '泠幼功;武初昌', '', '泠幼功，1武初昌。2', '[顏注-1]：泠人，掌樂之官也，因爲姓焉。黃帝時有泠倫。周有泠州鳩。秦有泠至。漢有泠襃、泠豐。幼者，少小之次。功者，言能立功也。\n[顏注-2]：周大夫武氏子，其後因爲武姓。陳勝之起也，武臣自立爲趙王。項羽之客亦有武涉。初昌者，言始昌大也。漢有夏侯始昌。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '7046/2499/9214;8397/2754/4239', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5737, '褚回池;蘭偉房', '', '褚回池，1蘭偉房。2', '[顏注-1]：褚師，官名也。衛有褚師定子、聲子及褚師圃，其後因姓褚焉。又周有褚氏之地，居者亦因命族。漢有褚大、褚少孫。回池，猶顏回字子淵之義也。\n[顏注-2]：鄭穆公本因夢蘭而生，故號公子蘭。其後支庶或爲蘭氏。偉房，言其器大，衆藝所聚也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '5362/3914/7259;286/4982/7725', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5738, '減罷軍;橋竇陽', '', '減罷軍，1橋竇陽。2', '[顏注-1]：減氏之先，爲晉公族大夫。驪姫之難，晉廢公族，因謂其人爲減氏。減者，省也。漢有減宣。罷軍，猶言偃武也。一說久從戎役，故疲劳也。\n[顏注-2]：黃帝葬於橋山，羣臣追慕，守冢不去者，因爲橋氏。漢有橋庇、橋仁、橋桃。空穴曰竇，牆南曰陽，言若生於竇陽，故爲名也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '7442/4843/9572;3786/4646/9624', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5739, '原輔輻;宣棄奴', '', '原輔輻，1宣棄奴。2', '[顏注-1]：周文王之子封爲原伯，其地今在河內。於後爲晉所滅，遷之于冀，因稱原氏。而晉更以原封其大夫先軫，故號原軫。軫之苗裔亦姓原焉。又周之采地亦名爲原，原莊公、襄公皆食之也。其後亦命族。孔子之門有原憲、原壤。輔輻，言若輻之輔車也。\n[顏注-2]：周宣王中興，國人美其德，故支庶以爲氏焉。棄奴，亦愛而諱之也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '7496/9567/9555;4560/2494/8163', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5740, '殷滿息;充申屠', '', '殷滿息，1充申屠。2', '[顏注-1]：微子，本殷之族，雖封於宋，支庶或稱殷氏。又殷水在潁川，居之者亦以爲姓。秦有殷通，漢有殷仲。滿息，言充滿而蕃息也。\n[顏注-2]：充氏，古仙人充尚之後也。申屠，楚之官號，若言司徒也。又謂之信都，古者申與信同，屠、都聲相近也。漢有申屠嘉，本此官之後也，遂因爲姓。韓信名信都，亦有義。今猶有申屠姓，亦有信都。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '5253/7219/6706;5462/9802/5420', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5741, '夏脩俠;公孫都', '', '夏脩俠，1公孫都。2', '[顏注-1]：夏桀爲湯所滅，其族遂稱夏氏。又陳宣公之孫、御叔之子號夏徴舒，夏齧、夏區夫皆其後也，亦爲夏氏。脩俠，言其修行游俠之事也。\n[顏注-2]：黃帝姓公孫氏，支庶遂以爲姓也。又公子之子皆號公孫。魯 公孫敖、鄭 公孫閼、齊 公孫蠆、晉 公孫尨、衞 公孫朝，凡此等類，其後皆爲姓矣。略舉大綱，不可具記。漢有公孫賀。都，美稱也。詩曰：「洵美且都。」又曰：「不見子都。」', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '3373/2686/5060;716/8514/4007', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5742, '慈仁它;郭破胡', '', '慈仁他{慈仁它}，1郭破胡。2', '[顏注-1]：慈氏，本高陽氏才子之後也，美其宣慈惠和，因以爲姓。仁他者，所愛及遠也。\n[顏注-2]：虢叔，周王季之子也，受封於虢，其地今陝州 陝縣是也。後爲晉所滅，虢公醜奔周，遂姓郭氏。郭者，虢聲之轉也。又齊地有郭氏之墟，葢古國。國滅之後亦爲郭姓。齊有郭榮，此其族也。破胡，言能克匈奴。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '6747/4953/8995;4152/6046/2678', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5743, '虞尊偃;憲義渠', '', '虞尊偃，1憲義渠。2', '[顏注-1]：帝舜之後，或爲虞姓。又周太伯之弟仲雍，號曰虞仲，嗣太伯之後。武王克商，封虞仲之庶孫爲之後。其地今在河東，謂之虞公。爲晉所滅，其後亦爲虞氏。尊偃，偃仰自尊高也。\n[顏注-2]：憲氏之先，本爲周之布憲，司寇之屬官也，其後以爲姓焉。義渠，國名也，後爲縣，在北地，以縣爲名也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '3110/9881/5156;6723/8404/7265', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5744, '蔡游威;左地餘', '', '蔡游威，1左地餘。2', '[顏注-1]：周 蔡叔者，文王之子，爲周公所誅，而封其子仲於蔡，是爲蔡侯。其後爲楚所滅，遂稱蔡氏。游威，威之遠揚也。\n[顏注-2]：左丘明，本魯之左史，繼守其職，遂爲姓焉。又楚左史倚相，末裔亦爲左氏。地餘，言土地有餘，封邑廣大也。漢有歐陽地餘。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '556/4306/8147;3028/9022/3247', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5745, '𨝸平定;孟伯徐', '', '譚平定{𨝸平定}，1孟伯徐。2', '[顏注-1]：譚，小國也，爲齊所滅，譚子奔莒，因姓譚焉。平定，謂無憂虞也。\n[顏注-2]：魯桓公子慶父之後號孟孫氏，其後稱孟氏焉。又衛侯之兄孟縶，其後亦曰孟氏。伯，長也。徐者，舒緩之稱也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '4155/3074/4576;9774/4967/1238', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5746, '葛感軻;敦倚蘇', '葛坎坷', '葛轗軻{葛感軻}[葛坎坷]，1敦倚蘇。2', '[顏注-1]：夏時諸侯有葛伯者，爲湯所征，遂失其國，因稱葛氏。又春秋時亦有葛，嬴姓之國也，其後亦爲葛氏。轗軻，言坎𡒄不平也。蓋欲效孟子名軻字子居耳。\n[顏注-2]：敦氏，秦文公時史敦之後也。倚蘇，言爲萌庶所倚賴，喜於來蘇也。', '急就篇', '急就篇', '1', DEFAULT, DEFAULT, '458/6913/9595;2039/5047/256', '458/9086/9121', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5747, '耿潘扈;焦滅胡', '耿潘嘏', '耿潘扈[耿潘嘏]，1焦滅胡。2', '[顏注-1]：耿，地名也，在河北，祖乙所都，舊居之者以爲姓也。又有耿國，在平陽 皮氏，晉獻公滅之，其後亦稱耿氏。漢有耿壽昌。潘，水名也，在榮陽。扈，廣大也，言生此水土而廣大也。\n[顏注-2]：武王克殷，封神農之後於焦，末葉因爲焦氏。又有姫姓小國，亦謂之焦，爲晉所滅，後稱焦氏。滅胡，猶言破胡也。', '急就篇', '急就篇', '1', DEFAULT, DEFAULT, '7800/7377/4025;6488/7443/2678', '7800/7377/1462', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5748, '晏奇能;邢麗奢', '', '晏奇能，1邢麗奢。2', '[顏注-1]：齊有晏弱，本齊之公族也，號晏桓子。桓子生嬰，曰晏平仲，其後遂爲晏氏。又有晏圉、晏父戎、晏氂，皆其族也。奇能，謂有異材也。\n[顏注-2]：邢，本姫姓之國，周公之胤也，爲衞所滅，因稱邢氏。麗奢，言美而不陋也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '4211/3060/6413;4061/6288/6653', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5749, '邵守實;宰安期', '', '邵守實，1宰安期。2', '[顏注-1]：邵穆公，周卿士也，繼守其職。邵伯廖、邵武公，皆其後也，因爲邵氏焉。秦有邵平，漢有邵信臣。守實，謂篤於言行，不虚詭也。\n[顏注-2]：宰氏，周大夫宰孔之後也。安期，取古人安期生之名也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '4051/4596/4587;4595/4578/4282', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5750, '俠卻敵;代焉于', '', '俠卻敵，1代焉于。2', '[顏注-1]：俠氏，韓相俠累之後也。卻敵，謂能折衝禦寇也。\n[顏注-2]：代，國名也，在常山之北。代王爲趙襄子所滅，其遺族遂稱代氏焉。焉，安也。于，曲也。焉于，言無所屈撓也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '5060/5787/2029;5083/2490/3070', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5751, '司馬襃;尚自於', '', '司馬襃，1尚自於。2', '[顏注-1]：程伯休父，周宣王時有平徐方之功，錫以官族，爲司馬氏。其後有適晉者，司馬侯、司馬叔游、司馬彌牟、司馬烏、司馬寅皆是也。項羽立司馬卬爲殷王，漢 司馬遷卽其族也。襃，大也。\n[顏注-2]：前已有「尚次倩」，今又言「尚自於」，一姓再見也。尚，亦作掌，葢魯大夫黨氏之後也。黨亦音掌。自於猶言自益也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '5773/6137/5303;711/2224/2488', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5752, '陶熊羆;解莫如', '', '陶熊羆，1解莫如。2', '[顏注-1]：陶唐氏之後，或單稱陶。又虞閼父爲周陶正，其後亦稱陶氏。成王封康叔以殷人七族，亦有陶氏焉。「惟熊惟羆，男子之祥。」故以爲名。\n[顏注-2]：解，地名也，在河東，因地爲姓，故晉國多解氏焉。解張、解揚、解狐，皆其族也。漢有解光、解延年。莫如，言無有能如之者也。漢有毛莫如。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '9689/6414/6415;2851/701/8245', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5753, '樂欣諧;童扶疏', '童枎疏/童夫須', '樂欣諧，1童扶疏[童枎疏/童夫須]。2', '[顏注-1]：樂氏亦再見也。欣諧，喜於和合也。\n[顏注-2]：顓頊之子號老童者，其後因以爲姓。扶疏，言衆而盛多也。', '急就篇', '急就篇', '2', DEFAULT, DEFAULT, '3764/5538/1547;1746/7856/9789', '1746/3594/9789/0/1746/6676/5708', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5754, '痛無忌;向夷吾', '', '痛無忌，1向夷吾。2', '[顏注-1]：痛氏本盛國之後，實姫姓也。周穆王嬖寵盛姫早死，穆王哀痛不已，加禮葬之，遂改其族，謂之痛氏。無忌，言無所避忌也。古有僊人宋無忌。\n[顏注-2]：向者，國名，本姜姓也，其後以爲氏。又宋之向氏，與宋同姓，桓公生向父肹，遂爲向氏。向戌、向儀、向寧，皆其後也。夷吾，欲效管仲之名也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '4699/8416/6873;4561/6615/837', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5755, '閣并訢;竺諫朝', '', '閎并訢{閣并訢}，1竺諫朝。2', '[顏注-1]：閎氏之先，本周之閎人。閎，所以止扉，今之門柣是也。職典其事，遂爲姓焉。漢有閎孺。并訢，言衆並訢喜也。\n[顏注-2]：竺氏本天竺國人也，来歸於漢而稱竺氏。天竺卽身毒也，亦謂之捐毒。漢有竺次者，卽其人焉。云諫朝者，言可備諍臣於朝廷者也。以朝韻吾者，古有此音，盖相通也。班固幽通賦曰：「巨滔天而泯夏，考遘愍以行謡。終保己而貽則，里上仁之所廬。」類此甚多，不可具載。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '7761/5228/1544;9019/1537/4289', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5756, '續增紀;遺失餘', '', '續增紀，1遺失餘。2', '[顏注-1]：續氏，晉大夫續簡伯之後也，號狐鞫居，以食續地，更爲續氏。增紀，言高年也。\n[顏注-2]：遺氏，本魯 季氏家臣南遺之後也。失餘，言所失之餘，惟此一字也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '8550/9092/8542;1174/7978/3247', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5757, '錦繡縵;毛;離雲爵;乘風縣鐘華洞樂', '雀', '錦繡縵䋃{毛}離雲爵[雀]，1乘風縣鐘華洞樂。2', '[顏注-1]：錦，織綵爲文也。繡，刺綵爲文也。縵，無文之帛也。䋃，謂刺也。離雲，言爲雲氣離合之狀也。爵，孔爵也。言織刺此象，以成錦繡繒帛之文也。自「離雲爵」以下至「鳧翁濯」，其義皆同。今時錦繡、綾羅及氍毹、毾㲪之屬，摹寫諸物，無不畢備，其來久矣。一曰離謂長離也，雲謂雲氣也。長離，靈鳥名也。作長離、雲氣、孔爵之狀也。\n[顏注-2]：乘風，一名爰居，一名雜縣，蓋海鳥也。言爲乘風之狀。作簨虡以懸鐘，又爲華藻之形，兼列衆樂之器，以成文章也。洞猶通也，言遍載其文綵也。', '急就篇', '急就篇', '3', DEFAULT, DEFAULT, '4930/8607/8606;5387;2294/7578/3203;3412/8979/5707/9388/3886/7189/3764', '2286', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5758, '豹首落莫兔雙鶴;春草雞翹鳧翁濯', '', '豹首落莫兔雙鶴，1春草雞翹鳧翁濯。2', '[顏注-1]：豹首，若今獸頭錦。落莫，謂文綵相連，又爲兔及雙鶴之形也。鳥二枚曰雙。\n[顏注-2]：春草，象其初生纎麗之狀也。雞翹，雞尾之曲垂也。鳧者，水中之鳥，今所謂水鴨者也。翁，頸上毛也。旣爲春草雞翹之狀，又象鳧在水中引濯其翁也。一曰春草、雞翹、鳧翁，皆謂染彩而色似之，若今染家言鴨頭緑、翠毛碧云。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '6113/5703/549/701/6295/2365/2412;683/680/2291/2252/1958/2249/7421', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5759, '鬱金;半見緗白䋤;縹綟緑紈皁紫硟', '𩰪金', '鬱金[𩰪金]半見緗白䋤，1縹綟緑紈皁紫硟。2', '[顏注-1]：自此以下，皆言染繒之色也。鬱金，染黃也。緗，淺黃也。半見，言在黃白之閒，其色半出，不全成也。白䋤，謂白素之精者，其光䋤䋤然也。\n[顏注-2]：縹，靑白色也。綟，蒼艾色也。東海有草，其名曰䓞，以染此色，因名綟云。緑，靑黃色也。紈皁，黑色。紫，靑赤也。硟，以石輾繒，色尤光澤也。', '急就篇', '急就篇', '1', DEFAULT, DEFAULT, '3850/9261;725/5476/8768/4931/8778;8614/8634/8613/8767/680/8625/6044', '3202/9261', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5760, '烝栗絹紺縉紅繎;靑綺綾縠靡潤鮮', '', '烝栗絹紺縉紅繎，1靑綺綾縠靡潤鮮。2', '[顏注-1]：烝栗，黃色若烝孰之栗也。絹，生白繒，似縑而疏者也，一名鮮支。紺，靑而赤色也。縉，淺赤色也。紅，色赤而白也。繎者，紅色之尤深，言若火之然也。\n[顏注-2]：靑，靑色也。綺，卽今之繒。綾，今之雜小綾也。縠，今梁州白縠。靡潤，輕渜也。鮮，發明也。言此繒旣有文綵而又鮮潤也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '6431/4393/8612/8628/8621/8626/8557;3189/8595/8605/8596/7698/7352/7648', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5761, '綈絡縑練素帛蟬;絳緹絓紬絲絮;緜', '', '綈絡縑練素帛蟬，1絳緹絓紬絲絮綿{緜}。2', '[顏注-1]：綈，厚繒之滑澤者也，重三斤五兩，今謂之平紬。絡，卽今之生䌤也，一曰今之綿紬是也。縑之言兼也，并絲而織，甚緻密也。練者，煮縑而熟之也。素，謂絹之精白者，卽所用寫書之素也。帛，總言諸繒也。蟬，謂繒之輕薄者，若蟬翼也。一曰縑已練者，呼爲素帛，若今言白練者也。\n[顏注-2]：絳，赤色也，古謂之纁。緹，黃赤色也。抽引麤繭緒紡而織之曰紬。紬之尤麤者曰絓，繭滓所抽也。抽引精繭出緒者曰絲。漬繭擘之，精者爲綿，麤者爲絮。今則謂新者爲綿，故者爲絮。古亦謂綿爲纊。纊，字或作絖。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '8599/8727/8598/8600/8776/4929/8869;8619/8623/8529/8603/8782/8726;8515', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5762, '㠲敝囊橐;服瑣緰;貲;繒', '', '㠲敝囊橐不直錢，1服瑣緰㠿{貲}與繒連。2', '[顏注-1]：㠲者，㡜殘之帛也。敝，敗衣也。有底曰囊，無底曰橐。言㠲帛及敝敗囊橐，無所堪任，賣之不售也。\n[顏注-2]：服瑣，細布織爲連瑣之文也。緰㠿，緆布之尤精者也。言此二種雖曰布類，其質精好，與繒相連次也。繒者，帛之總名，謂以絲織者也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '4884/4943/3905/3904;5449/154/8747;3991;8592', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5763, '貰貸賣買販肆便;資貨市贏匹幅全', '', '貰貸賣買販肆便，1資貨市贏匹幅全。2', '[顏注-1]：貰，賖也。貸，假與也。出曰賣，入曰買，賤買而貴賣之曰販。肆，謂坐市行列也。言販賣及買，皆因市肆以獲便宜也。\n[顏注-2]：市亦買也。言以資財賄貨，市取贏餘之物，多積布帛，匹幅皆全，無虧損也。四丈曰匹，兩邊具曰幅。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '3970/3952/3867/3980/3979/6070/5088;3942/3940/3325/3963/8424/4865/3285', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5764, '綌紵枲緼;綸組縌綬', '', '綌紵枲緼裹約纏，1綸組縌綬以高遷。2', '[顏注-1]：綌謂葛之麤者。紵，織紵爲布及疏之屬也。枲，麤麻也。緼約猶束縛也。言貧乏之人，無好綿帛，但以此物自苞裹纏束爲衣被也。\n[顏注-2]：綸，糾靑絲綬也。組，亦綬類也，其小者以爲冠纓。縌者，綬之系也，言其迎逆綬也。綬者，受也，所以承受環印也，亦謂之繸。秩命不同，則綵質各異，故云「以高遷」。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '8740/8743/4532/8758;8654/8649/8651/8648', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5765, '量丈尺寸斤兩銓;取受付予相因緣', '', '量丈尺寸斤兩銓，1取受付予相因緣。2', '[顏注-1]：銓，稱也。言量布帛者，則計其丈尺及寸，稱絲綿者，則數其斤兩。\n[顏注-2]：言以相付及受取領納，皆緣丈、尺、斤、兩以定其數，故得分明無虧失也。予，相授予也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '5245/1464/5426/1981/9476/4813/9373;1926/2519/5058/2510/2162/3924/8659', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5766, '稻黍秫稷粟麻秔;餅餌麥飯甘豆羹', '', '稻黍秫稷粟麻秔，1餅餌麥飯甘豆羹。2', '[顏注-1]：稻者，有芒之穀總名也，亦呼爲稌。黍，似穄而黏，可以爲酒者也。秫，似粟而黏，亦可爲酒。稷、粟一種，但二名耳，亦謂之粢。麻謂大麻及胡麻也。秔謂稻之不黏者，以別於稬也。秔，字或作稉。\n[顏注-2]：溲麫而蒸熟之則爲餅，餅之言并也，相合并也。溲米而蒸之則爲餌，餌之言而也，相黏而也。麥飯，磨麥合皮而炊之也。甘豆羮，以洮米泔和小豆而煮之也，一曰以小豆爲羹，不以醯酢，其味純甘，故云甘豆𡙡也。麥飯、豆羮，皆野人農夫之食耳。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '4437/4509/4435/4433/4394/4536/4441;3214/1875/3350/3222/3038/3096/1871', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5767, '葵韭蔥䪥蓼蘇;䕬;蕪荑鹽豉醯酢醬', '', '葵韭蔥䪥蓼蘇薑{䕬}，1蕪荑鹽豉醯酢醬。2', '[顏注-1]：葵，衛足之菜，傾葉而蔽日者也。韭之言久也，一種久生。蔥，靑白雜色之名也。䪥，一名鴻薈。蓼有數種，菜長銳而薄、生於水中者，曰水蓼；葉圓而厚、生於澤中者，曰澤蓼，一名虞蓼，亦謂之薔。而許叔重云：「蓼，一名薔虞。」非也。蘇，一名桂荏。薑，禦溼菜也，辛而不葷，故齊者不撤焉。\n[顏注-2]：蕪荑，無姑之實也。無姑，一名橭榆，生於山中，其莢圓厚，剥取樹皮，合漬而乾之，成其辛味也。爾雅曰：「無姑，其實夷。」故謂之蕪荑也。鹽，生於鹹水者也，古者夙沙氏初煮海爲鹽，其後又出河東大鹵、臨卭火丼焉，今則處處有之矣。豉者，幽豆而爲之也。醯、酢，亦一物二名也。醬，以豆合麫而爲之也，以肉曰醢，以骨曰臡。醬之爲言将也，食之有醬，如軍之須將，取其率領進導之也，一作漿。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '260/4541/633/4544/262/256;261;544/321/7719/4508/3153/9862/9864', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5768, '芸蒜薺芥茱萸;老菁蘘荷', '', '芸蒜薺芥茱萸香，1老菁蘘荷冬日藏。2', '[顏注-1]：芸卽今芸蒿也，生熟皆可啗。蒜，大、小蒜也，皆辛而葷。薺，甘菜也，其實名蒫。芥亦有大、小二種。茱萸，似榝而大，食者貴其馨烈，故云茱萸香也。\n[顏注-2]：菁，蔓菁也，一曰冥菁，一曰蕪菁，又曰芴菁。蘘荷，一名蓴苴，莖葉似薑，其根香而脆，可以爲菹，又辟蠱毒。言秋種蔓菁，至冬則老而成就，又收蘘荷，並蓄藏之，以禦冬也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '394/631/400/632/484/485;5377/276/275/433', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5769, '棃;柹;柰桃;棗杏瓜棣饊飴餳', '', '棃柿{柹}柰桃待露霜，1棗杏瓜棣饊飴餳。2', '[顏注-1]：言此四果，皆得霜露之氣乃能成熟。夏則待露，秋則待霜，故總云待露霜也。棃，一名山樆。\n[顏注-2]：因說衆果，遂及口實也。棣，常棣也，其子熟時正赤色，可啗，俗呼爲山櫻桃，隴西人謂之棣子。饊之言散也，熬稻米飯使發散也。古謂之張皇，亦目其開張而大也。以糵消米，取汁而煎之，渜弱者爲飴，言其形怡怡然也。厚強者爲餳，餳之爲言洋也，取其洋洋然也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '3418;3420;3424/3426;4398/3423/4547/3518/3213/3211/3212', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5770, '菜果蓏;甘麮', '', '園菜果蓏助米糧，1甘麮殊美奏諸君。2', '[顏注-1]：木實曰果，草實曰蓏。言園圃種菜及殖果蓏，貧者食之，以免饑饉，故云助米糧也。\n[顏注-2]：甘麮者，煮麥爲甘粥也。麮之言齲也，謂齲爛也。一曰麮者，糗也。麮、糗聲相近，實一物也。今人通以熬米麥爲之麨。甘麮者，以蜜和糗，故其味甘也。奏，進也。言此殊絕甘美之食，宜進之於君上。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '558/3568/242;3038/3359', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5771, '袍襦表裏曲領帬;襜褕袷複;習;絝;褌', '褺/襲', '袍襦表裏曲領帬，1襜褕袷複褶{習}[褺/襲]袴{絝}褌。2', '[顏注-1]：長衣曰袍，下至足跗。短衣曰襦，自膝以上。一曰短而施要者襦。衣外曰表，內曰裏。著曲領者，所以禁中衣之領，恐其上擁頸也。其狀闊大而曲，因以名云。帬卽裳也，一名帔，一曰襬。\n[顏注-2]：襜褕，直裾襌衣也。謂之襜褕者，取其襜襜而寬裕也。衣裳施裏曰袷，褚之以綿曰複。褶謂重衣之最在上者也，其形若袍，短身而廣袖，一曰左衽之袍也。袴謂脛衣也，大者謂之倒頓，小者謂之䘨𧘈。袴之兩股曰襱，合襠謂之褌，最親身者也。', '急就篇', '急就篇', '3', DEFAULT, DEFAULT, '5275/5320/5261/5262/8444/5620/4871;5292/5258/5322/5307;2240;8661;4874', '5317/0/5274', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5772, '襌衣蔽;厀;布毋繜;鍼縷補縫;定;紩緣', '袒', '襌衣蔽膝{厀}布毋繜，1鍼縷補縫綻{定}[袒]紩緣。2', '[顏注-1]：襌衣，似深衣而襃大，亦以其無裏，故呼爲襌衣。蔽膝者，於衣裳上著之，以蔽前也，一名韍，又曰韠，亦謂之幨。布毋繜者，薉貉女子以布爲脛空，用絮補核，狀如襜褕。薉貉者，東北之夷也。說者或云毋繜，布名，非也。\n[顏注-2]：鍼，所以縫衣也。縷，線也。修破謂之補，縫解謂之綻，納刺謂之紩，純邊謂之緣。', '急就篇', '急就篇', '5', DEFAULT, DEFAULT, '5323/5254/550;5785;4912/8357/8664;9334/8676/5343/8679;4576;8681/8659', '5342', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5773, '履舄;沓襃;䋐;䪗;紃;靸鞮卬角褐韤巾', '', '履舄鞜裒{沓襃}䋐縀{䪗}紃，1靸鞮卬角褐韤巾。2', '[顏注-1]：單底謂之履，或以絲爲之。複底而有木者謂之舄。鞜，生革之履也。裒，謂鞜之深大者也。䋐，織綵爲之，一名車馬飾，卽今之織成也。縀，履跟之帖也。䋐縀，以䋐爲縀也。紃，緣履之圓絛也。賈誼諫曰：「今人賣僮僕者，爲之繡衣絲履徧諸緣。」又曰：「美者黼繡，庶人之妾以緣其履。」是則古之履飾，通用紃之屬也。一曰紃者，屬五綵而爲之，若今之刺繡鞾矣。\n[顏注-2]：靸謂韋履頭深而兌平底者也，今俗呼謂之跣子。鞮，薄革小履也。卬角，屐上施也，形若今之木履而下有齒焉，欲其下不蹶，當卬其角，舉足乃行，因爲名也。褐，織毛爲衣也，或曰麤衣也。韤，足衣也。一曰褐謂編枲爲韤也。巾者，一幅之巾，所以裹頭也。一曰裹足之巾，若今人裹足布也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '5432/2489;3050/5303;8667;3393;8669;1803/1805/5223/2827/5359/3395/4856', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5774, '裳韋不借;完堅耐事踰比倫', '', '裳韋不借爲牧人，1完堅耐事踰比倫。2', '[顏注-1]：韋，柔皮也。裳韋，以韋爲裳也。不借者，小屨也，以麻爲之，其賤易得，人各自有，不須假借，因爲名也。言著韋裳及不借者，卑賤之服，便易於事，宜以牧牛羊也。\n[顏注-2]：耐，堪任也。比，例也。倫，類也。言著此衣履屨者，必須完全堅韌，堪任事務，經歴長久，乃得踰於等類，不破壊也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '4872/3384/7706/5077;4585/1951/6077/1934/1341/5229/5038', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5775, '屐屩𦂌麤羸寠貧;旃裘;索睪;蠻夷民', '', '屐屩𦂌麤羸寠貧，1旃裘𩌈𩍜{索睪}蠻夷民。2', '[顏注-1]：屐者，以木爲之，而施兩齒，所以踐泥。屩，卽今之鞋也。𦂌，圓頭掩上之履也。麤者，麻枲雜履之名也，南楚、江 淮之閒通謂之麤。羸，困弱也。寠，無禮者也。貧，無財者也。言羸寠貧之人，無有華飾也。\n[顏注-2]：𩌈𩍜，胡履之缺前雍者也。言蠻夷之人，唯以氈爲裘，而足著𩌈𩍜也。今西羌其服尚然。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '5437/5436/8752/6289/2350/4613/3986;4299/5375;3873/6647;8933/6615/8359', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5776, '去俗歸義來附親;譯導贊拜稱妾臣', '', '去俗歸義來附親，1譯導贊拜稱妾臣。2', '[顏注-1]：去其本俗，歸於徳義，附化而親近也。\n[顏注-2]：譯，傳言也。導，引也。贊，見也。始來歸義，言語未通，必須傳譯導引，進見而拜，依臣妾之禮也。一曰贊，助也，謂譯導者佐助之也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '3165/5097/1073/8404/3348/9662/5514;1722/1987/3949/7844/4495/1747/1953', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5777, '戎伯總閱什伍鄰;稟食縣官帶金銀', '', '戎伯總閱什伍鄰，1稟食縣官帶金銀。2', '[顏注-1]：戎謂編士卒之列也。百人爲伯，則置長以總統閱視之也。五人爲伍，二伍爲什，五家爲鄰。又依此法，各有部署，安居服役，皆遵制命，無乖剌也。篇本伯，字或作貉，貉卽上薉貉之類者也。言戎貉者，廣說四夷也。\n[顏注-2]：稟食縣官，官給其食也。帶金銀者，或得封侯、或受職任，皆佩印也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '8379/4967/8568/7783/5069/5068/4008;3343/3206/5707/9618/4867/9261/9262', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5778, '鐵鈇鑚錐釜鍑鍪;鍛鑄鉛錫鐙錠鐎', '', '鐵鈇鑚錐釜鍑鍪，1鍛鑄鉛錫鐙錠鐎。2', '[顏注-1]：因說金銀，遂陳用器也。鐵鈇，以鐵爲莖刃也。一曰以鐵爲椹也。錐，所以刺入也。鑚，所以穿通也。釜，所以炊煮也。大者曰釜，小者曰鍑，北燕 朝鮮 洌水之閒謂之錪。鍪，似釜而反脣。一曰：鍪者，小釜類，卽今所謂鍋也，亦曰鏃𨰠。\n[顏注-2]：凡金鐵之屬，椎打而成器者謂之鍛，銷冶而成者謂之鑄。鉛，靑金也。錫，一名鈏，在銀鉛之閒，卽今白鑞也。鐙，所以盛膏夜然燎者也，其形若杆，而中施釭，有柎者曰鐙，無柎者曰錠。柎謂下施足也。鐎謂鐎斗，溫器也，似銚而無緣。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '9270/9427/9371/9367/1861/9301/9302;9288/9279/9265/9266/9320/9319/9311', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5779, '鈐𨬍;鉤銍斧鑿鉏;銅鍾鼎鋞鋗;匜;銚', '', '鈐䥦{鈐𨬍}鉤銍斧鑿鉏，1銅鍾鼎鋞鋗鉇{匜}銚。2', '[顏注-1]：鈐䥦，大犂之鐵。鈎卽鎌也，形曲如鉤，因以名云。亦謂之鍥。銍，刈黍短鎌也。斧，所以伐木也。鑿，所以穿木也。鉏，去草之具也，一名兹基。\n[顏注-2]：銅鍾，以銅爲鍾。鍾，酒器也。鼎，三足兩耳㸑器也。鋞，溫器，圓而直上也。鋞，字或作鈃，鈃似鍾而長頸也。鋗亦溫器也。鉇，系而提之銚。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '9351/9352;1460/9359/9477/9343/9350;9268/9294/4409/9298/9312;8429;9309', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5780, '釭鐧鍵鉆冶錮鐈;竹器簦笠簟籧篨', '', '釭鐧鍵鉆冶錮鐈，1竹器簦笠簟籧篨。2', '[顏注-1]：釭，車轂中鐵也。鐧，軸上鐵也。施釭鐧者，所以護軸，使不相摩墾也。鍵以鐵，有所豎關，若門牡之屬也。鉆以鐵，有所鑷取也。冶，銷金鐵之鑪也。錮者，鑄而補塞之，令其堅固也。鐈者，以鐵有所輔助，若橋梁之形也。\n[顏注-2]：竹器，總言織竹爲器也。簦、笠，皆所以禦雨也。大而有把，手執以行，謂之簦。小而無把，首戴以行，謂之笠。虞卿躡屩擔簦，卽謂此也。織竹爲席謂之簟，織葦而麤文者，籧篨也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '9418/9417/9314/9361/7517/9284/9296;2866/1440/2967/2968/2912/2913/2914', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5781, '𥫱篅箯筥䉛箅篝;簁箄箕帚筐篋簍', '', '𥫱篅箯筥䉛箅篝，1簁箄箕帚筐篋簍。2', '[顏注-1]：𥫱、篅皆所以盛米穀也。以竹木簟席若泥塗之則爲𥫱，𥫱之言屯也，物所屯聚也。織草而爲之則曰篅，取其圓團之然也。竹器之盛飯者，大曰箯，小曰筥。筥，一名䈰，受五升。一曰箯者，織竹之輿也。䉛，炊之漉米箕也，或謂之縮，或謂之𥰞，或謂之𠤰。箅，蔽甑底者也，其字從畀。篝，一名笿，盛杯器也，亦以爲薰籠，楚人謂之牆居。\n[顏注-2]：簁，所以籮去麤細者也，今謂之篩。大者曰簁，小者曰箄，其字從卑。箕，可以簸揚及去糞，帚，所以掃刷，古者杜康作箕帚。筐亦筥屬也，筥圓而筐方。篋，長笥也，言其狹長篋篋然也。簍者，疏目之籠，亦言其孔樓樓然也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '2943/2944/2948/2922/2917/2919/2933;2926/2927/3019/4903/2961/2924/2930', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5782, '橢杅槃案桮閜盌;蠡;斗;參升半巵觛', '枓', '橢杅槃案桮閜盌，1蠡升{斗}[枓]參升半巵觛。2', '[顏注-1]：橢，小桶也，所以盛鹽豉。杅，盛飯之器也，一曰齊人謂盤爲杅，無足曰盤，有足曰案，所以陳舉食也。桮，飲器也，一名㔶。閜，大桮也。盌，似盂而深長。盌，字或作椀，其音則同。\n[顏注-2]：蠡升，瓢蠡之受一升者，因以爲名，猶今人言勺升耳。參升，亦以其受多少爲名也。半者，受五升之半，謂二升五合也。此二者皆罃壺之類也。巵，飲酒圓器也。觛謂觶之小者，行禮飲酒角也。', '急就篇', '急就篇', '3', DEFAULT, DEFAULT, '3716/3659/3706/3708/3705/7758/3140;8967;9491;4317/9507/725/5775/2855', '3711', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5783, '𡭐;椯;榼椑榹匕箸籫;𦉈;缶盆盎;罋;罃壺', '𦓚', '槫{𡭐}{椯}[𦓚]榼椑榹匕箸籫，1甀{𦉈}缶盆盎甕{罋}罃壺。2', '[顏注-1]：椯，小巵也，上有蓋。榼，盛酒之器，其形榼榼然也。椑，圓榼也，一曰厚榼也。匕，匙也，亦謂之柶。箸，一名梜，所以梜食也。籫，盛匕箸之籠也。\n[顏注-2]：甀，小口罌也。缶、盆、盎一類耳。缶卽盎也，大腹而斂口。盆則斂底而寬上。缶，字或作瓿，瓿甊，小罌也。甕謂盛酒漿米粟之甕也。罃，長頸缾也。壺，圓器也，腹大而有頸。', '急就篇', '急就篇', '2', DEFAULT, DEFAULT, '5776;3738;3715/3714/3707/5217/2929/2937;3291;3287/3148/3147;3294;3296/6642', '5777', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5784, '甑瓽甂甌瓨;罌;盧;纍繘繩索絞紡纑', '罏', '甑瓽甂甌瓨甖{罌}盧[罏]，1纍繘繩索絞紡纑。2', '[顏注-1]：甑，一名甗，亦謂之鬵，又呼爲鉹。瓽，大盆也。甂、甌，瓦杅也，其形大口而庳。一曰甌，小盆也。瓨，短頭長身之罃也。甖，甀之大腹者也。盧，小甕，今之作盧酒者，取名於此。\n[顏注-2]：纍，大索也。繘，汲索也，一名綆。繩謂紨兩股以上，總而合之者也。索，總謂切撚之，令𦂳者也。一曰麻絲曰繩，草謂之索。絞卽糾也。紡謂紡切麻絲之屬爲纑縷也，已紡而成謂之紗。', '急就篇', '急就篇', '3', DEFAULT, DEFAULT, '8456/8459/8466/8460/8462;3290;3144;8686/8720/8694/3873/6629/8547/8736', '8451', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5785, '簡札檢署槧牘家;版;柞', '椵;笮', '簡札檢署槧牘家[椵]，1板{版}柞[笮]所產谷口斜。2', '[顏注-1]：竹簡，以爲書牒也。札者，木牒，亦所以書之也。檢之言禁也，削木施於物上，所以禁閉之，使不得輒開露也。署謂題書其檢上也。槧，板之長三尺者也，亦可以書。謂之槧者，言其修長漸漸然也。牘，木簡也，旣可以書，又執之以進見於尊者，形若今之木笏，但不挫其角耳。家，伏几也，今謂之夾膝。\n[顏注-2]：板謂木瓦也。柞，屋棧也，亦謂之簀。谷口在北，卽今之雲陽治谷是也。斜口在南山，今斜谷是也。言二處皆足林木，板柞從之而出生也。一曰谷口不出板笮，而言谷口斜者，止謂斜谷之口也。陽陵 朱安世曰：「斜谷之木，不足爲我械。」', '急就篇', '急就篇', '1;3', DEFAULT, DEFAULT, '2893/3770/3771/4842/3769/4403/4557;4401;3483', '3487;2908', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5786, '科斗䵷蝦蟇;鯉鮒蟹鱓鮐鮑鰕', '', '水蟲科斗䵷蝦蟇，1鯉鮒蟹鱓鮐鮑鰕。2', '[顏注-1]：水蟲，總言在水之蠱也。科斗，一名活東，亦曰活師，卽蝦蟇所生子也。未成蝦蟇之時，身及頭並圓而尾長，漸乃變耳。䵷，一名螻蟈，色靑，小形而長股。蝦蟇，一名蟼，大腹而短脚。\n[顏注-2]：鯉，卽今之鯉魚也。鮒，今之鰿魚也，亦呼爲鯽。蟹，八足二螯，旁行。鱓，似蛇。鮐，海魚也。鮑，亦海魚，加之以鹽而不乾者也。鰕，謂今之海蝦，堪爲鮓脯。及所呼鰕米者，又所在水中小鰕，可生啗若煼而食之，皆是也。鰕之大者爲鰝。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '4496/9491/9000/8915/8916;7596/7611/8919/7638/7652/7664/7666', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5787, '妻婦聘嫁齎;𠈪;僮;奴婢私隸枕牀杠', '', '妻婦聘嫁齎媵{𠈪}僮，1奴婢私隸枕牀杠。2', '[顏注-1]：妻者，齊夫之名。婦者，服事舅姑之稱。聘謂因媒而問也。嫁謂自家而往適人也。齎者，將持而遣之也。媵，送女也。僮謂僕使之未冠笄者也。言婦人初嫁，其父母以僕妾財物將送之也。\n[顏注-2]：奴婢，男女賤者之稱也。隸，附著之義也。私隸者，言屬著私家，非給公役者。枕，所以支頭也。牀，所以坐臥也。杠者，牀之橫木也，亦謂之兆。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '8131/8132/7812/8127/3951;5109;4951;8163/8162/4431/1948/3680/3679/3676', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5788, '蒲蒻藺席帳帷幢;承塵戸㡘絛繢總', '緃', '蒲蒻藺席帳帷幢，1承塵戸㡘絛繢總[緃]。2', '[顏注-1]：蒻謂蒲之柔弱者也。藺，草名也，亦莞之類也。蒲蒻可以爲薦，藺草可以爲席。自上而下覆謂之帳，帳者，張也。在旁蔽繞謂之帷，帷者，圍也。形如車蓋者謂之幢，言其童童然也。\n[顏注-2]：承塵，施於牀上以承塵土，因爲名也。戸㡘，戸上之幔也，字或作簾。絛，一名偏諸，織絲縷爲之，所以懸係承塵戸㡘，因爲飾也。繢亦絛組之屬也，似纂而色赤。總，以絲縷爲之，所以束髮也，一曰䋐屬也，所以緣飾衣裳也，字或作縱，音義皆同。', '急就篇', '急就篇', '2', DEFAULT, DEFAULT, '332/333/330/4904/4882/4881/4918;7914/6290/7722/4880/8666/8540/8568', '8668', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5789, '鏡籢疏;比;芬薰脂粉膏澤筩', '梳', '鏡籢疏[梳]比各異工，1芬薰脂粉膏澤筩。2', '[顏注-1]：鏡籢，盛鏡之器，若今鏡匣也。櫛之大而麤、所以理鬢者謂之疏，言其齒稀疏也；小而細、所以去蟣蝨者謂之比，言其齒密比也。皆因其體而立名也。各異工者，言施用不同，廢一不可也。\n[顏注-2]：芬者，薀糅其質以爲香也。薰者，燒取其煙以爲香也。一曰芬薰，總舉香草之大稱也。脂謂面脂及唇脂，皆以柔滑膩理也。粉謂鉛粉及米粉，皆以傳面，取光潔也。粉之言分也，研使分散也。膏澤者，雜聚取衆芳以膏煎之，乃用塗髮，使潤澤也。筩者，本用竹筩，其後轉用金玉雜物寫竹狀而爲之，皆所以盛膏澤者也。', '急就篇', '急就篇', '1', DEFAULT, DEFAULT, '9291/2936/9789;5229;237/294/2706/4378/2613/7222/2947', '3684', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5790, '沐浴揃搣寡合同;襐飾刻畫無等雙', '', '沐浴揃搣寡合同，1襐飾刻畫無等雙。2', '[顏注-1]：濯髮曰沐，澡身曰浴。揃搣，謂鬄拔眉髮也，盖去其不齊整者。寡，少也。寡合同者，言其妍瀞，少對偶也。\n[顏注-2]：襐飾，盛服飾也。刻畫，裁製奇巧也。無等雙，亦名殊絕，異於衆也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '7411/7413/7903/7904/4608/3271/4803;5327/4896/2765/1944/8416/2896/2365', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5791, '係臂琅玕虎魄龍;璧碧珠璣;枚瑰;罋', '玟瑰', '係臂琅玕虎魄龍，1璧碧珠璣玫瑰{枚瑰}[玟瑰]罋。2', '[顏注-1]：琅玕，火齊珠也。一曰石之似珠者也。言以虎魄爲龍，并取琅玕係著臂肘，取其媚好且珍貴也。篇本龍，字或作瓏字，盖謂以玉爲龍者也。\n[顏注-2]：璧，玉璧也。肉倍好謂之璧。肉謂外邊之質，好謂孔子，言質大而孔小居一倍也。碧，縹玉也。圓者曰珠，不圓曰璣，皆蚌之陰精也。玫瑰，美玉名也，以美玉爲罋。罋，汲缾也。言旣帶珠璧，又服玫瑰之罋，備衆寳也。今俗人以雜寳爲鎑之屬，帶於嬰兒頸下，此古之舊事歟？春秋左氏傳所云「鞶鑑」及「瑶罋」，皆其類也。或曰：珠之尤精者曰玫瑰。', '急就篇', '急就篇', '3', DEFAULT, DEFAULT, '5166/2627/198/199/3118/5824/7689;106/185/189/197;3574/196;3294', '195/196', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5792, '玉玦環佩靡從容;射鬾辟邪除羣凶', '', '玉玦環佩靡從容，1射鬾辟邪除羣凶。2', '[顏注-1]：肉好若一謂之環，言孔及質廣狹豐殺正相齊也。半環謂之玦，衡、璜、琚、瑀、㻫、衝牙之佩。靡者，順美之稱也。從容，言行止有節度者也。\n[顏注-2]：射鬾、辟邪，皆神獸名也。鬾，小兒鬼也。射鬾，言能射去鬾鬼。辟邪，言能辟禦妖邪也。謂以寳玉之類，刻二獸之狀以佩帶之，用除去凶災而保衛其身也。一曰射鬾，謂大剛卯也，以金玉及桃木刻而爲之，一名㱾攺，其上有銘，而旁穿孔，系以綵絲，用係臂焉，亦所以逐精魅也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '81/124/108/4960/7698/5227/4589;3310/5829/5798/4149/9692/2353/4529', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5793, '竽瑟空侯琴筑箏;鐘磬鞀簫鼙鼓鳴', '', '竽瑟空侯琴筑箏，1鐘磬鞀簫鼙鼓鳴。2', '[顏注-1]：竽，笙類也。列管瓠中，施簧管端，宮管在中央，三十六簧。瑟，庖犧氏所作也，長七尺二寸，二十七弦，今則二十四。大瑟謂之灑，長八尺一寸，廣尺八寸者也。空侯，馬上所彈也，一名坎侯。琴，神農所作也，長三尺六寸六分。五弦，曰宮、商、角、徴、羽。周文王增二弦，曰少宮、少商。大琴謂之離，二十七弦者也。筑，形如小瑟而細頸，以竹擊之。箏亦瑟類也，本十二弦，今則十三。\n[顏注-2]：鐘則以金，磬則以石，皆所用合樂也。古者倕作鍾，母句作磬。大鐘謂之庸，中者謂之剽，小者謂之棧。大磬謂之毊。鞀，貫把鼓也，摇而鳴之，大者謂之麻，小者謂之料。簫，一名籟，編管而列之，參差象鳳翼也。大者二十四管，長一尺四寸，謂之言；小者十六管，長尺二寸，謂之筊。鼙，騎鼓也，其形似鞀而庳薄。鼓之言郭也，張郭皮革而爲之也。大者謂之鼖，小者謂之應。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '2987/8408/4648/3313/8407/2998/2999;9388/6041/1811/2991/3086/3083/2481', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5794, '五音總會歌謳聲;倡優俳笑觀倚庭', '', '五音總會歌謳聲，1倡優俳笑觀倚庭。2', '[顏注-1]：五音，宮、商、角、徴、羽也。聲成文謂之音。會，謂金、石、絲、竹、匏、土、革、木總合之也。長言謂之歌，齊歌謂之謳。\n[顏注-2]：倡，樂人也。優，戲人也。俳謂優之䙝狎者也。笑謂動作云爲皆可笑也。倚，立也。觀倚庭者，言人来觀倡優，皆倚立於庭中也。倚，字或作伎，謂觀俳倡之伎於庭中也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '9727/1738/8568/3276/5543/1572/7810;5140/5091/5141/3011/5486/5047/5928', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5795, '侍酒行觴宿昔酲;廚宰切割給使令', '', '侍酒行觴宿昔酲，1廚宰切割給使令。2', '[顏注-1]：侍酒，謂侍坐而飲酒也。行觴，傳觴也。一曰侍酒，佐酒也。行觴，使佐酒者行觴也。昔，夜也。病酒曰酲。謂經宿飲酒，故致酲也。\n[顏注-2]：廚，庖屋也。宰，主烹餁者。臠䐑曰切，胖解曰割。給，供也，主供此使役也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '5053/9807/1266/2856/4602/4252/9854;5935/4595/2760/2778/8586/5101/5779', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5796, '薪炭𦻃葦;膹膾炙胾', '', '薪炭𦻃葦炊孰生，1膹膾炙胾各有形。2', '[顏注-1]：取木而然之曰薪。木之已燒者曰炭。薍爲𦻃，謂荻也。其新生者曰菼。葭爲葦，謂蘆也。二者亦薪之類，可然燎也。炊孰生者，謂烝煮生物，使之爛孰也。\n[顏注-2]：膹，麤切生肉也。膾，細切生肉也。亦或以魚。炙謂炙之於火上也。胾，大臠也。各有形，言別異不同也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '622/6446/644/645;2713/2717/6584/2715', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5797, '酸;鹹;酢淡;肌䐞脯腊魚臭腥', '殠鮏', '酸醎{鹹}酢淡辨濁淸，1肌䐞脯腊魚臭腥[殠鮏]。2', '[顏注-1]：言飲食衆品，滋味各別，淸濁異宜也。大酸謂之酢，平薄謂之淡。\n[顏注-2]：肌，肉也。肉表皮裏曰䐞。膊而乾者謂之脯，全骨全乾謂之腊。腥者，魚之臭。', '急就篇', '急就篇', '4', DEFAULT, DEFAULT, '9859;7718;9862/7392;2598/2711/2685/4252/7580/6363/2705', '2546/7659', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5798, '酤酒釀醪稽;㯳程;棊局博戲相易輕', '桱桯;㑥輕', '酤酒釀醪稽極程{㯳程}[桱桯]，1棊局博戲相易輕[㑥輕]。2', '[顏注-1]：買酒曰酤，醖之曰釀，汁滓酒曰醪。稽，分也。極，盡也。言所飲之酒，或酤或釀，皆欲作經分，然後成熟，極盡功程，乃爲善也。古者儀狄作酒醪，禹嘗而美，遂疏儀狄。杜康又作秫酒。\n[顏注-2]：棊局，謂彈棊、圍棊之局也。博亦局戲也，十二棊、六箸。古者烏曹作博。凡人相與爲棊博之戲者，因有爭心，則言辭輕侮，失於敬讓，故云相易輕也。', '急就篇', '急就篇', '2;3', DEFAULT, DEFAULT, '9825/9807/9810/9818/3891;3752/4497;3756/958/1468/8385/2162/6134/9521', '3678/3677;5151/9521', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5799, '冠幘簪簧結髮紐;頭頟頞䪼眉目耳', '', '冠幘簪簧結髮紐，1頭頟頞䪼眉目耳。2', '[顏注-1]：冠者，冕之總名，備首飾也。幘者，韜髮之巾，所以整嫧髮也，常在冠下，或但單著之。冠、幘非一稱也。簪，一名笄。簧卽步摇也。結髮謂作結也。紐謂結之䰎也。凡結之可解者曰紐。\n[顏注-2]：頭者，首之總名也。頟，顙也。頞，鼻莖也。䪼，兩頰之權也。眉，在目上之毛也。目，眼也。耳，主聴者也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '4799/4868/5466/2989/8577/5730/8653;5601/5612/5613/5665/2219/2096/7794', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5800, '鼻口脣舌齗牙齒;頰頤頸項肩臂肘', '', '鼻口脣舌齗牙齒，1頰頤頸項肩臂肘。2', '[顏注-1]：鼻，所以引氣也。口，所以言食也。脣，口端也。舌主知味，又所以言也。齗，齒根肉也。牡齒曰牙。齒者，總謂口中之骨，主齰齧者也。\n[顏注-2]：面兩旁曰頰，下頷曰頤。頸，頭莖也。項謂頸後頭下也。肩，髆也。臂，手上也。肘，臂曲節也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '2233/780/2602/1444/1279/1323/1278;5615/7827/5619/5621/2624/2627/2629', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5801, '捲;宛;節㕚拇指手;胂腴胸脇喉咽髃', '拳;𢯲', '捲[拳]捥{宛}[𢯲]節㕚拇指手，1胂腴胸脇喉咽髃。2', '[顏注-1]：捲謂掌握也。腕，手臂之節也。節，衆指之節也。㕚，指甲也。拇，大指也，一名將指。指，總謂衆指也。及掌謂之手。\n[顏注-2]：胂，夾脊肉也。腴，腹下肥也。胸，膺上也。脅，肋旁也。喉卽喉龍。咽，所以吞咽物也。髃，肩前也。', '急就篇', '急就篇', '1;2', DEFAULT, DEFAULT, '8047;4609;2877/1907/7831/7832/7829;2622/2632/5810/2618/786/789/2573', '7833;7834', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5802, '腸胃腹肝肺心主;脾腎五藏膍齊;乳', '𣬈臍', '腸胃腹肝肺心主，1脾腎五藏膍齊[𣬈臍]乳。2', '[顏注-1]：腸，大小腸也。胃，糓府也。腹者，肚之總名。謂之腹者，取厚爲義也。肝，木藏也。肺，金藏也。心，火藏也。主者，言心最在中央，爲諸藏之所主。\n[顏注-2]：脾，土藏也。腎，水藏也。五藏，總謂肝、肺、心、脾、腎也。膍齊卽毗齊也。', '急就篇', '急就篇', '2', DEFAULT, DEFAULT, '2612/2610/2631/2608/2606/6705/3184;2607/2605/9727/695/2680/4395;7705', '6702/2630', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5803, '尻髖脊膂;要;背呂;股腳;厀髕;脛爲柱', '', '尻髖脊膂腰{要}背呂，1股腳膝臏{厀髕}脛爲柱。2', '[顏注-1]：尻，脽也。髖，髀上也。膂，夾脊內肉也。呂，脊骨也。\n[顏注-2]：股，髀肉也。腳，足也。膝，脛頭節也。髕，膝蓋也。脛，胻骨也。爲柱者，言在髖髀之下，總載衆支，如屋之有柱也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '5409/2578/8110/4632;1785;2617/4632;2636/2637;5785/2579;2638/1882/3627', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5804, '專;踝跟踵;相近聚;矛鋋鑲盾刃刀鉤', '腨;歱', '𨄔{專}[腨]踝跟踵[歱]相近聚，1矛鋋鑲盾刃刀鉤。2', '[顏注-1]：𨄔，腓膓也，字或作腨，其音亦同。踝，足之內外踝也。足後曰跟，亦謂之踵。跟猶根也，下著於地，如木根也。踵者，鍾也，上體任之，力所鍾聚也。相近聚者，𨄔踝及跟踵不離逖也。\n[顏注-2]：矛，酋矛也，長二丈。鋋，鉄把小矛也，江淮吳越或謂之鍦。鑲者，亦刀劒之類，其刃卻偃而外利，所以推攘而害人也。盾，一名瞂，亦謂之干，卽今旁排也。刃，總言諸兵刃也。刀，大小衆刀也。鉤亦鑲屬也，形曲如鉤而內利，所以拘牽而害人也。', '急就篇', '急就篇', '1;2', DEFAULT, DEFAULT, '1985;1329/1328/1358;2162/1180/5238;9508/9402/9285/2221/2812/2744/1460', '2641;1065', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5805, '鈒戟鈹鎔劒鐔鍭;弓弩箭矢鎧兜;牟', '鍪', '鈒戟鈹鎔劒鐔鍭，1弓弩箭矢鎧兜鉾{牟}[鍪]。2', '[顏注-1]：鈒，短矛也。戟，枝刃之矛也，楚謂之孑。鈹，大刀也，刃端可以披決，因取名云。鎔謂刀之鎪刃爲道者也，亦取其創含容之義。鐔，劒刃之本入把者也。鍭，劒口也。\n[顏注-2]：以角曰弓，以木曰弧。一曰有緣謂之弓，無緣謂之弭。古者揮作弓，弓之施臂而機發者曰弩，以竹曰箭，以木曰矢。古者夷牟作矢。鎧，甲也，亦謂之介。兜鉾，首甲也，古謂之胄。', '急就篇', '急就篇', '3', DEFAULT, DEFAULT, '9401/8377/9335/9286/2814/9397/9411;8479/8499/2867/3309/9413/5471;751', '9302', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5806, '鐵;錘;過;杖梲柲杸;輜軺轅軸輿輪;康', '抶;𥬲;輬', '鐵[抶]錘檛{過}[𥬲]杖梲柲杸，1輜軺轅軸輿輪𨎍{康}[輬]。2', '[顏注-1]：鐵錘，以鐵爲錘，若今之稱錘，亦可以擊人，從兵器之例。張良所用擊秦副車卽此物也。麤者曰檛，細者曰杖。梲，小棓也，今俗呼爲袖梲，言可藏於懐袖之中也。柲，櫕也，謂積竹之杖也。一曰柲者，總言矛戟之把也。杸亦杖名也，古者以積竹八觚爲殳，長一丈二尺，建於兵車，旅賁以先驅，而軍士所執殳者名之杸。司馬法曰「執羽從杸」是也。杸與殳音同。一曰杸、殳古今字也。\n[顏注-2]：輜，衣車四面皆蔽也。軺，輕車也。轅，輈也。軸，所以穿轂而轉也。著輪曰車，無輪曰輿。輪，總謂輻轂夷輞也。𨎍謂輿中空處，所用載物也。𨎍之言空也，爾雅曰：「漮，空也。」', '急就篇', '急就篇', '1;3;6', DEFAULT, DEFAULT, '9270;9378;1110;3741/3744/3748/1961;9516/9520/9558/9545/9528/9549;4459', '8054;2975;9519', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5807, '輻轂輨轄輮;軝桑;軹軾軫軨轙軜衡', '', '輻轂輨轄輮䡻䡦{軝桑}，1軹軾軫軨轙軜衡。2', '[顏注-1]：輻者，輪之轑也。轂謂輻所湊也。輨，轂端之鐵也。轄，竪貫軸頭制轂之鐵也。輮，車輞也，關西謂之輮，言其柔曲也。或謂之䡻，言其緜連也。䡻，字或作䡑，其音同。䡦者，轂中之空，受軸處也。\n[顏注-2]：軹，軸兩頭也，一曰輪之小穿也。軾，車前橫木也。軫，車後橫木也。軨，兩轄之系也。故路車之轄，施小旛者謂之飛軨，車轂閒橫竹也。轙，車衡上貫轡環也。軜，驂馬內轡繫軾前者也。衡者，橫也，橫木在馬頸上者。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '9555/9550/9557/9576/9547;9552/3860;9553/9531/9542/9540/9565/9566/2844', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5808, '蓋轑俾倪;軶;縛棠;轡勒鞅;𩎍絆;羈;繮', '𣥺', '蓋轑俾倪枙{軶}縛棠[𣥺]，1轡勒鞅韅靽{𩎍絆}羈韁{繮}。2', '[顏注-1]：蓋，車上蓋也。轑，蓋弓之施爪者也。謂之轑者，言若屋之椽轑也。俾倪，持蓋之杠，在軾中央，環爲之，所以止蓋弓之前卻也。枙在衡上，所以枙持牛馬之頸也。縛在車下，主縛軸令與相連，卽今所謂鉤心也。棠，𨅨也，在車兩旁，以蹂距幰，使不得以崎也。\n[顏注-2]：在首曰轡，亦謂之勒；在頸曰鞅，在掖曰韅，在足曰靽。羈，絡頭也，謂勒之無銜者也。韁，馬紖也。凡此皆所以制御馬者也。', '急就篇', '急就篇', '3', DEFAULT, DEFAULT, '586/9568/5098/5099;9562;8580/3432;8783/1837/1846;1823/8712;4848;8708', '1066', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5809, '鞇;絥土薄;鞍鑣鐊;靳靷䩸䩞色焜煌', '', '鞇𩊙靯𩍿{絥土薄}鞍鑣鐊，1靳靷䩸䩞色焜煌。2', '[顏注-1]：鞇，車中所坐蓐也。𩊙，韋囊，在車中，人所憑伏也，今謂之隱囊。靯𩍿，車中重薦也。鞍，所以被馬，取其安也。鑣，卽馬轡之銜也，亦謂之钀。鑣之言苞也，所以包斂馬口者也。或曰鑣者，銜兩傍之鐵，今之排沬是也。鐊，馬面上飾也，以金銅爲之，俗謂之當顱。\n[顏注-2]：靳，驂馬之帶也。靷謂當胸者也。䩸䩞，以毛毳飾鞍也。色焜煌者，言其光采盛也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '610;8704/9021/561;1833/9425/9423;1824/1826/1834/1835/5793/6510/6509', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5810, '革;轖䰍;漆油黑蒼;室宅廬舍樓殿堂', '', '革𩍙髤{轖䰍}漆油黑蒼，1室宅廬舍樓殿堂。2', '[顏注-1]：革𩍙，車藉之交革也，一曰重革之幭，所以覆車笭也。笭，車前曲欄也。髤漆者，以漆漆之；油者，以油油之，皆所以爲光色而御塵泥。其色或黑或蒼，故云「黑蒼」也。\n[顏注-2]：室，止謂一室耳。宅，總言院宇之中也。廬，別室也，一曰田野之室也。舍謂人於其中舍息也。樓謂重屋離樓然也。殿謂室之崇麗有殿鄂者也。凡正室之有基者，則謂之堂。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '1791;9539/3897;7015/7040/6542/538;4559/4558/5927/3275/3653/1970/9056', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5811, '門戸丼竈廡囷京;榱椽欂櫨瓦屋梁', '', '門戸丼竈廡囷京，1榱椽欂櫨瓦屋梁。2', '[顏注-1]：大曰門，小曰戸。丼，所以汲也。竈，所以炊也。廡，周屋也。廡謂幠覆之也。囷，圓倉也。京，方倉也。一曰京之言矜也，寳貴之物可矜惜者藏於其中也。\n[顏注-2]：榱卽椽也，亦名爲桷。欂櫨，柱上之枅也，自陜以西呼之爲㭼。瓦屋，以瓦覆屋也。梁，屋上梁也。一名杗廇。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '7732/7722/3191/4638/5932/3919/3331;3642/3641/3633/3634/8452/5422/3787', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5812, '泥塗堊墍壁垣牆;榦楨;版;栽度圜方', '', '泥塗堊墍壁垣牆，1榦楨板{版}栽度圜方。2', '[顏注-1]：泥塗，作泥以塗飾之，及塞隟穴也。堊，白土也。墍，仰塗也。壁，辟也，辟禦風雨也。垣，援也，人所依阻以爲援衛也。牆，障也，所以自障蔽也。\n[顏注-2]：榦楨，築牆之植木，謂竪立者也。板，牆板也。栽，築牆也。度圜方者，量計所宜，先立基址，然後築也。詩曰：「縮版以載。」', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '7108/9152/9063/9062/9050/9047/3347;3620/3608;4401;3618/1929/3909/5455', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5813, '墼壘廥廏庫東箱;屏;廁淸溷糞土壤', '庰', '墼壘廥廏庫東箱，1屏[庰]廁淸溷糞土壤。2', '[顏注-1]：墼者，抑泥土爲之，令其堅激也。墼壘，累墼而爲障蔽也。一曰壘，軍壁也。廥，芻槀所居也。廏，生馬所聚也。庫，兵車所藏也。東箱，東序之屋也。\n[顏注-2]：屏，僻宴之名也。廁之言側也，亦謂僻側也。淸，言其處特異餘所，常當加潔淸也。溷者，目其穢濁也。屏、廁、淸、溷，其實一耳。柔土曰壤，言屏廁之地，以糞穢則其土爲壤也。', '急就篇', '急就篇', '2', DEFAULT, DEFAULT, '9065/9110/5941/5937/5936/3846/2969;5423;5944/7202/7207/2493/9021/9031', '5943', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5814, '碓磑扇隤舂簸揚;頃町界畝畦埒封', '', '碓磑扇隤舂簸揚，1頃町界畝畦埒封。2', '[顏注-1]：碓，所以舂也。磑，所以䃺也，亦謂之䃀。古者雍父作春，魯班作磑。扇，扇車也。隤，扇車之道也。隤，字或作𨽟，𨽟之言墜也，言旣扇之，且令墜下也。舂則簸之揚之，所以除穅秕也。揚，字或作颺，音義同。\n[顏注-2]：田百畝爲頃，平地爲町。一曰町，治田處也。周制：百步爲畝。自漢以来，二百四十步爲畝。田邊謂之界，田區謂之畦。今之種稻及菜爲畦者，取名於此。一說五十畝曰畦。埒謂田閒堳道也，一說謂庳垣也，今之圃或爲短牆，蓋埒之謂也。封謂聚土以爲田之分界也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '6051/6050/7724/9647/4524/3020/7958;5221/9173/9187/9181/9184/9053/9076', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5815, '疆畔畷伯耒;𤛿鉏;種樹收斂賦稅租', '', '疆畔畷伯耒犂鋤{𤛿鉏}，1種樹收斂賦稅租。2', '[顏注-1]：疆，比田之界也。畔，分半田之際也。畷，兩伯閒竪道也。耒，手耕曲木也。古者倕作耒，今之曲把㭉鍬，其遺象也。犂亦耕具也。犂之言利，則發土而絕草根也。鋤之言助也，助苗去穢也。\n[顏注-2]：種，藝也。樹，殖也。春夏種殖，秋冬收斂，農之常業也。斂財曰賦，斂穀曰稅，田稅曰租，皆所以供公家之用也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '9202/9186/9189/4967/2820;761/9350;4422/3560/2048/2024/3983/4488/4487', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5816, '君;穫秉把插捌;杷;桐梓樅㮤榆;杶;樗', '攈;柭', '捃{君}[攈]穫秉把插捌[柭]杷，1桐梓樅㮤榆椿{杶}樗。2', '[顏注-1]：拾遺曰捃，刈取曰穫。捃，字或作攟，音義皆同。一束曰秉，一把曰把。插者，擔也，兩頭鑯銳，所以插刺禾束而擔之也。無齒爲捌，有齒爲杷，皆所以推引聚禾穀也。或曰插者，鑿也，此說失之。鍫自有臿名，今此方說收斂之具，無涉金鐵，非鍫臿之臿也。\n[顏注-2]：桐卽今之白桐木也，一名榮。梓，楸類也，一名椅。樅，葉似柏者也。松，有黃、赤、白三種。榆，白枌也。椿，字或作櫄，其音同。樗，似椿而木虚惡，唯堪薪燎。', '急就篇', '急就篇', '1;2', DEFAULT, DEFAULT, '839;4465/1919/7877/7897/8104;3695;3539/3463/3548/3545/3541;3470;3455', '7987;3742', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5817, '槐檀荆棘葉枝扶;辛;騩騅駮驪駵驢', '𩥍', '槐檀荆棘葉枝扶，1騂{辛}[𩥍]騩騅駮驪駵驢。2', '[顏注-1]：槐，似櫰而葉小，又黃色。檀，堅韌木也。荆，一名楚。棘，酸棗之樹也，一名樲。葉枝扶者，言此衆樹枝葉扶疏，分布茂盛也。\n[顏注-2]：騂，馬黃赤色也。騩，淺黑色也。蒼白雜色曰騅，色不純曰駮，深黑色曰驪，赤馬黑髦曰駵。駵，字或作騮，音義同。', '急就篇', '急就篇', '2', DEFAULT, DEFAULT, '3523/3529/488/4399/495/3571/7856;9756;6146/6149/6243/6144/6147/6247', '6258', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5818, '騏駹馳驟怒步超;牂羖羯羠䍮羝羭', '', '騏駹馳驟怒步超，1牂羖羯羠䍮羝羭。2', '[顏注-1]：騏者，靑驪之馬，文如綦也。駹，驄馬雜色也。一曰面顙皆白謂之駹。疾步曰驟。怒步者，言其氣盛而行步動作也。超，踰越也，謂馬之駿者，踰越於駑也。以超韻驢者，音如此，已解在「竺諫朝」章中。\n[顏注-2]：牂，吳羊之牝也。羖，夏羊之牡也。羖之犗者爲羯，謂㓺之也。羠亦騬羊也。又西方有野羊，大角，牡者曰羱，牝者曰羠，並以時墮角，其羱角尤大，今人以爲鞍橋。羠角差小，可以爲刀子把。爾雅曰：「羱，如羊。」卽謂此也。䍮，羊未卒歲也，一曰夷羊重百斤者爲䍮。羝，牂羊之牡也。羭，夏羊之牡也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '6143/6154/6210/6206/6879/1081/983;2342/2344/2345/2346/2339/2340/2343', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5819, '六畜蕃息豚豕豬;豭豶狡犬野雞雛', '', '六畜蕃息豚豕豬，1豭豶狡犬野雞雛。2', '[顏注-1]：六畜：牛、馬、羊、豕、鷄、犬，人所畜飬者也。蕃，滋也。息，生也。豚謂豕之始生者也。豕者，彘之總名也。豕之三毛聚者曰豬，而春秋左氏傳曰：「旣定爾婁豬。」爾雅曰：「豕子，豬。」然則亦其通稱也。豕之絕有力者曰䝈，其牝曰豝。\n[顏注-2]：豭，牡豕也。豶，犗豕，亦謂之䝐。狡犬，匈奴中大犬也，鉅口赤身。一曰狡，少犬也，謂狗之有懸蹄者也。野雞生在山野，鷮雞、鶡雞、天雞、山雞之類皆是也。凡鳥子生而啄食者皆曰雛。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '9728/9198/675/6706/6110/6078/6079;6086/6085/6307/6303/9171/2291/2292', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5820, '犙㸬特犗羔犢駒;雄雌牝牡相隨趨', '', '犙㸬特犗羔犢駒，1雄雌牝牡相隨趨。2', '[顏注-1]：犙，三歲牛也。㸬，二歲牛也，一曰長脊之牛也。犗，㓺牛也。特，羊子曰羔，牛子曰犢，馬子曰駒。\n[顏注-2]：飛曰雄雌，走曰牝牡。詩云：「雄狐綏綏。」書稱「牝雞無晨。」亦互言之，無所滯也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '735/734/731/737/2335/733/6140;2313/2314/732/729/2162/1102/980', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5821, '糟;穅;汁滓稾莝芻;鳳爵鴻鵠鴈鶩雉', '', '糟糠{穅}汁滓稾莝芻，1鳳爵鴻鵠鴈鶩雉。2', '[顏注-1]：糟，酒粕也。糠，米皮也，亦謂之蠱。汁，瀋也。滓，澱也。稾，禾稈也。莝，細斫稾也。蒭，刈生草也，字本作芻，音義同。言此皆所以飼牛馬羊豕也。\n[顏注-2]：鳳，神鳥也，其狀麟前而鹿後，虵頸魚尾，龍文龜背，燕頷雞喙，鶴立鴛思，五采備舉，一名鶠，其雌曰皇。爵謂神爵以下衆雜爵也。神爵之形，或大如鷃，黃喉白頸，黑背而腹斑文；或大如鳩而背五色。鴻，水鳥也，其色正白。鵠，黃鵠也，一舉千里，其鳴聲鵠鵠云。鴈亦鴻類也，其色蒼黑。鶩，一名舒鳬，卽今之鴨也。雉有十四種，其文采皆異焉。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '4366;4459;7396/7383/4476/615/611;2370/3203/2415/2414/2423/2424/2289', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5822, '鷹鷂鴇鴰翳雕尾;鳩鴿鶉鴳中網死', '', '鷹鷂鴇鴰翳雕尾，1鳩鴿鶉鴳中網死。2', '[顏注-1]：鷹，一名来鳩，亦曰爽鳩。鷂，一名題肩，亦曰擊征，又名負爵。色類甚多，皆鷙鳥也。鄭康成以擊征爲鷹，失之矣。鴇，大鳥，其肉出尺胾，今俗呼爲獨豹。豹者，鴇聲之訛耳。鴇，字或作𪀀，音讀亦同。鴰者，鶬也，關西謂之鴰鹿，山東謂之鴰捋，皆象其鳴聲也。又呼爲錯落，亦鶬聲之轉也。翳謂凡鳥羽之可隱翳者也，舞者所持羽翿以自隱翳，因名爲翳云。今雅樂文康部所持者，卽此物也。一曰翳者，謂華蓋也，今之雉尾扇，是其遺象。雕亦大鷙鳥也，一名鷻，其尾尤盛，故特稱之耳。\n[顏注-2]：鳩有雜種，其名非一。𪁛鳩、鶌鳩、䳫鳩、鷑鳩、䳕鳩，鳲鳩之類是也。鴿，似䳕鳩而色靑白，其鳴聲鴿鴿，因以名云。鶉，䳺屬也，一名鷯，其雄曰鶛，雌曰痺，其子曰鳼。鴳謂鴳雀也，一名雇，今俗呼爲鴳爛堆。言此衆鳥皆爲人所捕，觸於羅網而致死也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '2296/2455/2437/2446/2274/2295/5428;2376/2382/2307/2478/231/4815/2562', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5823, '弋;鵲鴟梟驚相視;豹狐距虛豺犀兕', '鷳', '鳶{弋}[鷳]鵲鴟梟驚相視，1豹狐距虛豺犀兕。2', '[顏注-1]：鳶卽今俗所呼老鴟者也。鵲者，亦因鳴聲以爲名也。其爲鳥也知来，作巢則避太歲。上已言鳶、下又言鴟者，謂𪁪鴟、茅鴟、怪鴟之屬，非止一也。梟，土梟也。驚相視者，敘其衆多雜居處也。\n[顏注-2]：豹，似烏塗而圜文。狐，妖獸也，鬼所乘。距虛，卽蛩蛩也，似馬而有靑色。一曰距虛似驘而小。豺，深毛而狗足。犀，黑色，似水牛而豬頭、大鼻、庳脚，脚有三蹄，其頂頟及鼻凡有三角，亦有一角者，善食棘刺。兕，似野牛而色靑，重千斤，一角，角甚大。', '急就篇', '急就篇', '1', DEFAULT, DEFAULT, '8366;2489/2297/3834/6218/2162/5477;6113/6382/1402/5234/6117/769/6133', '2454', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5824, '貍兔飛;吾;狼麋𪊨;麇麈麖麀皮給履', '', '貍兔飛鼯{吾}狼麋𪊨，1麇麈麖麀皮給履。2', '[顏注-1]：貍，一名豾，亦謂之𧴀，江淮陳楚謂之爲猍，其子𧳙。兔，其子曰嬎；絕有力者，欣。飛鼯，一名飛蠝，又曰鼯鼠，亦曰夷由，卽今俗呼飛生者也。狼，似靑狗，走則健顧，其牡者曰貛，牝者曰狼，其子曰獥，絕有力者曰迅。麋，似鹿而大，冬至則解角，目上有眉，因以爲名也。其牡者曰麔，牝者曰麎，其子曰䴠，絕有力者曰狄。𪊨。\n[顏注-2]：麇，卽今之麞也，牡者曰麌，牝者曰麜，其子曰麆，絕有力者曰豜。麈似鹿，尾大而一角，談說者飾其尾而執之以爲儀。麖似大鹿，一角而牛尾。鹿牡者曰麚，牝者曰麀，其子麛，絕有力者曰𪊑，皮可以給履舄之用也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '6126/6295/7694;837;6379/6272/6276;6277/6281/6279/6275/1988/8586/5432', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5825, '寒氣泄注腹臚;張;痂疕疥癘癡聾盲', '', '寒氣泄注腹臚脹{張}，1痂疕疥癘癡聾盲。2', '[顏注-1]：寒氣，中寒冷之氣也。泄，利也，言水穀漏泄也。注者，注易之病，一人死、一人復得，氣相灌注也。腹前曰臚。脹謂腹鼓脹也。一曰臚，皮也。\n[顏注-2]：痂，創上甲也。疕謂薄者也。疥，小蟲攻齧皮膚，漼錯如鱗介也。癘，惡疾也。癡，不慧也。耳不聞聲曰聾，目不見色爲盲也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '4615/4376/7065/7277/2631/2599;8488;4745/4717/4744/4747/4797/7813/2200', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5826, '癰疽瘛瘲痿痹;痙;疝瘕顚;疾狂失響', '瘨', '癰疽瘛瘲痿痹痮{痙}，1疝瘕顚[瘨]疾狂失響。2', '[顏注-1]：癰之言壅也，氣壅否結，裏腫而潰也。癰之久者曰疽。瘛瘲，小兒之疾，卽今癎病也。痿，不能行也。痹，風溼不仁也，一曰痿，偏枯也。痮，四體強急，難用屈申也，字或作疢，音義並同。\n[顏注-2]：疝，腹中氣疾上下引也。瘕，癥也。顚疾，性理顚倒失常，亦謂之狂獝，妄動作也。失響者，失音不能言也。', '急就篇', '急就篇', '3', DEFAULT, DEFAULT, '4741/4739/4786/4713/4753/4754;4768;4729/4746/5608;4696/6370/7978/1739', '4704', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5827, '瘧瘚瘀痛瘼;溫病;消渴;歐逆;欬懣讓', '㾺;㵣;屰;曩', '瘧瘚瘀痛瘼[㾺]溫病，1消渴[㵣]歐逆[屰]欬懣讓[曩]。2', '[顏注-1]：瘧，寒熱休作之病，言其酷虐也。瘚者，氣從下起，上行入心脇也。瘀，積血之病也。痛，總謂諸痛也。瘼者，無名之病，常漠漠然也。一曰齊人謂瘵病曰瘼。溫病，病於溫氣者也。\r\n[顏注-2]：消渴者，引飲不止也。嘔逆，吐而不下食也。欬，嗽也。懣，煩悶也。讓，大便節薀積而利也。', '急就篇', '急就篇', '1;3;4;5', DEFAULT, DEFAULT, '4748/4734/4728/4699/4705;6997/4697;7343/7345;5559/1124;5579/6892/1692', '4720;5564;1443;4234', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5828, '癉熱瘻痔眵𬞱䀶;篤癃𤸬癈迎醫匠', '', '癉熱瘻痔眵𬞱䀶，1篤癃𤸬癈迎醫匠。2', '[顏注-1]：癉熱，黃病也。瘻，久創也。痔，蟲食後之病也。眵謂眇𧢗，目之蔽垢也。𬞱，目眥傷赤也。䀶，目視不正也。\n[顏注-2]：篤，重病也。癃，疲病也。𤸬，損耗也。癈，四支不收。醫匠，療病之工也，古者巫彭初作醫。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '4772/6512/4726/4752/2185/2186/2188;6198/4784/4794/4711/1125/9855/8426', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5829, '灸刺和藥逐去邪;黃芩伏苓礜茈胡', '', '灸刺和藥逐去邪，1黃芩伏苓礜茈胡。2', '[顏注-1]：灸，以火艾灼病也。刺，以箴刺之也。和藥，合和衆藥也。草木金石、鳥獸蟲魚之類，堪愈疾者，總名爲藥。凡人正氣不足，則邪氣入體而病生焉，故敘攻病云「逐去邪」也。\n[顏注-2]：黃芩，一名空腸，一名腐腸，一名內虛，一名妬婦。伏苓，一名茯菟，生大松樹下，蓋松脂所化作也。礜，礜石也，一名靑分，一名制石，又名澤乳，亦曰食鹽。茈胡，一名地薰，一名山菜。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '6474/2806/846/576/1178/3165/4149;9203/408/5163/365/6016/380/2678', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5830, '牡蒙甘草;菀藜蘆;烏喙附子椒芫華', '苷艸', '牡蒙甘草[苷艸]菀藜蘆，1烏喙附子椒芫華。2', '[顏注-1]：牡蒙，一名黃昏。甘草，一名蜜草，一名蕗，一名蘦，一名大苦。菀謂紫菀、女菀之屬也。藜蘆，一名蔥菼，一名山蔥。\n[顏注-2]：烏喙，形似烏之觜也。附子，附大根而旁出也。此與烏頭、側子、天雄本同一種，但以年歲遠近爲殊，採之有異，功用亦別。說者云，一歲爲側子，二歲爲烏喙，三歲爲附子，四歲爲烏頭，五歲爲天雄。椒謂秦椒及蜀椒也。椒之大實者名檓。芫華，一名魚毒，漁者煮之以投水中，魚則死而浮出，故以爲名。其根曰蜀桑，其華可以爲藥。芫，字或作杬，爾雅曰：「杬，魚毒。」郭景純解云：「大木，生南方，皮厚汁赤，堪藏卯果。」此說誤耳。其生南方用藏卯果者，自別一杬木，乃左思吳都賦所云「緜杬杶櫖」者耳，非毒魚之杬也。', '急就篇', '急就篇', '1', DEFAULT, DEFAULT, '729/649/3038/680;452/672/277;2488/783/9662/9766/486/464/3886', '302/240', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5831, '半夏皁莢艾橐吾;芎藭厚朴桂栝樓', '䒷蔞', '半夏皁莢艾橐吾，1芎藭厚朴桂栝樓[䒷蔞]。2', '[顏注-1]：半夏，五月苗始生，居夏之半，故爲名也。一名地文，亦名守田。皁莢樹，一名雞栖。艾，一名冰臺，一名醫草。橐吾，似款冬而腹中有絲，生陸地，華黃色，一名獸須。\n[顏注-2]：芎藭，一名胡藭，一名香果，其葉名蘪蕪。厚朴，一名厚皮，一名赤朴，凡木皮皆謂之朴，此樹皮厚，故以厚朴爲名。其樹亦名榛，其子名逐析。桂謂菌桂、牡桂之屬，百藥之長也。栝樓，一名果臝，一名王瓜，亦曰天瓜。禮月令：「孟夏之月，王瓜生。」鄭康成以爲萆挈，非也。', '急就篇', '急就篇', '2', DEFAULT, DEFAULT, '725/3373/680/511/389/3904/837;284/285/3339/3573/3431/3759/3653', '398/377', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5832, '款東貝母;䕬;狼牙;遠志續斷參土瓜', '', '款東貝母薑{䕬}狼牙，1遠志續斷參土瓜。2', '[顏注-1]：款東卽款冬也，亦曰款涷，以其凌寒叩冰而生，故爲此名也。生水中，華紫赤色，一名兔奚，亦曰顆東。貝母，一名蝱，一名藥實，一名苕華，一名勤母。其根正白，形頗似貝，故呼貝母。薑謂生薑、乾薑也。狼牙，一名牙子，一名狼齒，又名犬牙。\n[顏注-2]：遠志，主益智惠而強志，故以爲名。一名葽繞，一名棘蒬。其葉名小草，亦目其細小也。續斷，一名接骨，卽今所呼續骨木也。又有草續斷，其葉細而紫色，根亦入藥用。參謂人參、丹參、紫參、玄參、沙參、苦參也。土瓜，一名菲，一名芴。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '5540/3846/3936/8141;261;6379/1323;1198/6709/8550/9487/4317/9021/4547', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5833, '亭歴桔梗龜骨枯;雷矢雚;菌藎兔盧', '𦻃', '亭歴桔梗龜骨枯，1雷矢雚[𦻃]菌藎兔盧。2', '[顏注-1]：亭歴，一名丁歴，一名蕇，一名狗薺。桔梗，一名利如，一名梗草。龜骨謂龜甲也。枯者，言其已死，骨乾枯也。\n[顏注-2]：雷矢卽雷丸也，又名雷實。雚菌，一名雚蘆，生東海池澤及渤海 章武，此雚蘆之地所生菌也。舊云是鸛矢所化，故其爲藥毒烈，而去腹中痼病焉。藎草治久欬，殺皮膚小蟲，又可以染黃而作金色。兔盧卽兔絲也。色黃而細者爲兔絲，一名兔縷，一名唐，一名蒙，一名女蘿，一名玉女，一名赤網。麤而色淺者爲兔盧，盧亦縷也。一名兔纍。纍者，繩索之意也。', '急就篇', '急就篇', '2', DEFAULT, DEFAULT, '3322/1070/3482/3543/8996/2569/3605;7528/3309/2324;477/304/6295/3144', '644', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5834, '卜問譴祟父母恐;祠祀社稷叢臘奉', '', '卜問譴祟父母恐，1祠祀社稷叢臘奉。2', '[顏注-1]：言家有不安，所以卜問。及云鬼神譴責，用致禍祟，故家長恐懼也。以龜曰卜。\n[顏注-2]：祠者，伺而祭之。祀者，祭無已也。社，地主也；稷，先農也，故求福焉。叢謂草木岑蔚之所，因立神祠也。戰國䇿曰：「恒思有神叢，恒思之悍少年，與叢博。」史記曰：「吳廣之次所旁叢祠中，夜構火。」皆謂此叢也。一曰叢者，合聚諸神而祭之也。臘，接也，廣祭百神也。奉謂供其粢盛、牲特、幣帛，以事神也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '2074/843/1689/68/1908/8141/6944;41/31/64/4433/1750/2667/1756', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5835, '謁禓塞;禱鬼神寵;棺;椁;槥櫝遣送踊', '賽;息', '謁禓塞[賽/息]禱鬼神寵，1棺槨{椁}槥櫝遣送踊。2', '[顏注-1]：謁，告請也。禓，道上之祭也。塞，報福也。禱，求助也。言旣請禱，又報賽之，故爲鬼神所寵祐也。\n[顏注-2]：言死亡者，則爲棺槨槥櫝以遣送之，隱痛之極，故擗踊也。棺，所以斂身。槨，所以斂棺也。擗，拊心也。踊，跳躍也。孝經云：「擗踊哭泣，哀以送之。」', '急就篇', '急就篇', '1;1', DEFAULT, DEFAULT, '1483/65/9095;51/5821/25/4597;3829;3832;3831/3682/1145/1144/1346', '4001;6706', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5836, '喪弔悲哀面目腫;哭泣祭;餟;墳墓冢', '', '喪弔悲哀面目腫，1哭泣祭醊{餟}墳墓冢。2', '[顏注-1]：喪謂遭喪、持喪也。於字，哭亡爲𠸶。弔謂問終者也，於字，人持弓爲弔。上古葬者，衣之以薪，無有棺槨，常苦禽鳥爲害，故弔問者持弓會之，以助彈射也。\n[顏注-2]：醊謂連續之祭也。墳，封土而高之也。大防曰墳，故取以爲名也。墓，壙穴也。謂之墓者，言其幽暗常昏暮也。墳之高者爲冢，言其象山頂也。爾雅曰：「山頂，冢。」', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '978/5185/6905/936/5697/2096/2663;977/7437/30;3264;9143/9142/5815', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5837, '宦學諷詩;孝經;論;春秋;尚書;律令文', '', '宦學諷詩 孝經 論，1春秋 尚書 律令文。2', '[顏注-1]：宦，仕也。言欲仕學者，必當先諷讀詩及孝經、論語也。詩，孔子所刪爲三百篇者也。孝經，孔子爲弟子曾參所說也。論語，孔子與弟子言，及應對時人之語也。詩，字或作誦。\n[顏注-2]：春秋，孔子約史記而修之也。天有四時，春爲陽中，萬物以生；秋爲陰中，萬物以成。故錯互舉之，苞十二月而爲名也。尚書，孔子所修帝王之書也。謂之尚書者，言自上古以来，其事久遠也。律之言率也，制法以率下也。一曰律，述也，具述刑名也。令，命爲政也。言宦學之人，又當皆習讀此等也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '4594/2073/1491/1489;5386/8532;1511;683/4493;711/1943;1257/5779/5725', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5838, '治禮掌故砥厲身;智能通逹多見聞', '', '治禮掌故砥厲身，1智能通逹多見聞。', '[顏注-1]：治禮，贊禮儀者也。掌故，主故事者也。皆屬太常。言始爲此職，尚未高顯，宜謹飭其身，敦修學行也。云砥厲者，蓋譬諸金鐵磨利之也。細石曰砥，黑石曰厲。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '7089/12/7830/2005/5984/5985/5250;2231/6413/1134/1163/4336/5476/7811', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5839, '名顯絕殊異等倫;抽擢推舉白黑分', '', '名顯絕殊異等倫，1抽擢推舉白黑分。2', '[顏注-1]：以自砥厲，故得然也。\n[顏注-2]：賢者升擢，不肖黜退，是爲白黑有分別也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '836/5692/8548/2536/1778/2896/5038;7995/7996/7848/7959/4931/6542/708', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5840, '迹行上究爲貴人;丞相御史郎中君', '', '迹行上究爲貴人，1丞相御史郎中君。2', '[顏注-1]：迹行，行之迹也。上究，聞於天子也。究，意也。言終始昭著，曾無玷缺，則簡在帝心，爲貴臣矣。\n[顏注-2]：此卽貴人之位也。丞，承也。相，助也。言上承天子而佐助之也。御史，御史大夫也，職副丞相。郎中，郎中令也，掌宮殿門戸及從官，並秦所置，而漢因之。君，褒尊大官之名也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '1093/1266/6/4673/1882/3981/4950;1757/2162/1258/1933/4139/231/839', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5841, '進近公卿傅僕勲;前後常侍諸將軍', '', '進近公卿傅僕勲，1前後常侍諸將軍。2', '[顏注-1]：公卿之被引進而親近天子者，有此傅、僕、勲。傅，太傅也，傅天子以徳義。僕，太僕也，主爲天子御車馬。勲，光禄勲也，漢武帝改郎中令爲光禄勲。\n[顏注-2]：在天子之前後，卽導從者，則有常侍之官及諸將軍也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '1113/1180/716/5797/5044/1753/9213;1069/1250/4872/5053/1488/1983/9572', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5842, '列侯封邑有土臣;積學所致非鬼神', '', '列侯封邑有土臣，1積學所致非鬼神。2', '[顏注-1]：列侯，第二十爵也。邑，所食之邑。言封爲列侯者，旣獲邑之土地，又得因有家臣也。\n[顏注-2]：言饗此爵禄，皆由勤學卽可致之，非別有鬼神之力。此所以深勸學僮也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '2772/3313/9076/4004/4320/9021/1953;4467/2073/9484/3367/7696/5821/25', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5843, '馮翊;京兆執治民;廉絜平端撫順親', '倗翼', '馮翊[倗翼]京兆執治民，1廉絜平端撫順親。', '[顏注-1]：自此以下，汎論職務也。漢武帝始分京師爲三輔，京都爲京兆，左則爲馮翊，右則爲扶風。京兆，言高而有衆也。絕高曰京，十億曰兆。一曰京，大也。馮翊，言輔贊政教也。馮，輔也。翊，贊也。扶風者，言扶助風化也。', '急就篇', '急就篇', '1', DEFAULT, DEFAULT, '6203/2264;3331/2081/6648/7089/8359;5949/8754/3074/6682/7922/5654/5514', '5014/7695', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5844, '姦邪並塞皆理馴;變化迷惑別故新', '', '姦邪並塞皆理馴，變化迷惑別故新。1', '[顏注-1]：下有愚迷之人，則能化喻，使其變改，去故之非，行今之是也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '8348/4149/6698/9095/2227/147/6224;2020/5216/1167/6865/2567/2005/9489', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5845, '更卒歸誠自詣因;司農少府國之淵', '', '更卒歸誠自詣因，1司農少府國之淵。2', '[顏注-1]：卒，給使役者也。更，言其去来更代也。詣，𠉀至也。因，就也。謂更卒之徒，厭苦疲倦，常多逃匿，苟求脫免。若逢善政，則懐徳感恩，来陳誠款，自詣官寺，就作役也。\n[顏注-2]：司農，領天下錢穀，以供國之常用。少府，管池澤之稅及關市之資，以供天子。少者，小也。府者，聚也，財物所聚也。非郊廟兵戎大用，故謂之少府。司此二者，百物在焉，故以深泉爲喻也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '2021/5361/1073/1525/2224/1580/3924;5773/1787/705/5924/3917/3861/7211', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5846, '遠取財物主平均;臯陶造獄法律存', '', '遠取財物主平均，1臯陶造獄法律存。2', '[顏注-1]：言遠方之輸賦稅者，或以雜物充之，價有貴賤，儻與京師不等，又當有轉送費用，不欲劳擾，故立平凖均輸之官，各就所在受納，不損於私，而官有利也。\n[顏注-2]：臯陶，舜臣名，亦號庭堅。命爲士官，始制囹圄，法律備焉。欲言刑罰之事，故先陳之也。獄之言埆也，取其堅牢也。字從二犬，所以守備也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '1198/1926/3939/771/3184/3074/9030;6662/9689/1114/6392/6262/1257/9778', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5847, '誅罰詐僞劾罪人;廷尉正監承古先', '', '誅罰詐僞劾罪人，1廷尉正監承古先。2', '[顏注-1]：誅，責也。罰，治也。劾，舉案之也。詐僞則責治，有罪則舉案。\n[顏注-2]：廷尉，掌刑獄之官，秦所置也，漢亦因之。廷者，平也，朝之處也。尉者，武官之號也。兵獄同制，故曰「廷尉」。廷尉屬官有正、有監。言因承上古先代而置之。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '1711/2798/1669/5136/9250/4824/4950;1261/6472/1087/5247/7914/1461/5472', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5848, '總領煩亂決疑文;變鬭殺傷捕伍鄰', '', '總領煩亂決疑文，1變鬭殺傷捕伍鄰。2', '[顏注-1]：煩亂則領理，疑議則詳決，此獄官之職也。\n[顏注-2]：變鬭者，爲變難而相鬭也。殺傷，相傷及相殺也。捕，收掩也。有犯變鬭傷殺者，則同伍及鄰居之人皆被收掩也。隣伍，解已在前。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '8568/5620/5683/9744/7274/9780/5725;2020/1893/1979/5158/8069/5068/4008', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5849, '亭長游徼共雜診;盜賊繫囚榜笞;𩪡', '', '亭長游徼共雜診，1盜賊繫囚榜笞臋{𩪡}。2', '[顏注-1]：亭長，一亭之長，主逐捕盜賊。游徼，鄉之游行徼循，皆督察姦非者也。雜猶參也。診，驗視也。有被殺傷者，則令亭長與游徼相參而診驗之，知其輕重曲直也。秦 漢之制，十里一亭，亭有高楼，所以候望，鄉嗇夫治之。游徼卽嗇夫之所統也。\n[顏注-2]：繋囚，拘縶之也。榜笞，箠擊之也。臋，脽也。獲盜賊者，則拘縶而捶擊其脽，考問其狀也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '3322/6069/4306/1232/1776/5335/1708;5596/8382/8731/3928/3751/2982;5410', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5850, '朋黨;謀敗相引牽;欺誣詰狀還反眞', '攩', '朋黨[攩]謀敗相引牽，1欺誣詰狀還反眞。2', '[顏注-1]：同謀之中，一人事敗，則牽引朋黨，皆發露也。\n[顏注-2]：囚繫之徒，或欺詐閉匿，或誣冤良善，旣被考詰，窮治由狀，乃歸實也。', '急就篇', '急就篇', '1', DEFAULT, DEFAULT, '2370/6564;1507/2041/2162/8493/755;5588/1609/1696/6335/1142/1920/5215', '7918', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5851, '坐生患害不足憐;辭窮情得具獄堅', '', '坐生患害不足憐，1辭窮情得具獄堅。2', '[顏注-1]：干紀速辜，人所同嫉者也。\n[顏注-2]：旣窮其辭，又得其情，則鞠訊之吏，具成其獄，鍛練周密，文致堅牢，不可反動也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '9069/3876/6938/4616/7706/1326/6962;9761/4674/6707/1254/1771/6392/1951', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5852, '籍受證驗記問年;閭里鄉縣趣辟論', '', '籍受證驗記問年，1閭里鄉縣趣辟論。2', '[顏注-1]：簿籍所受，計其價直，并顯證以定罪也。記問年者，具爲書記，抵其本屬，問年齒也。幼少老耄，科罪不同，故問年也。\n[顏注-2]：里門曰閭。里者，本其所居之里也。里屬於鄉，鄉統於縣。趣謂催速之也。辟，法也。里鄉及縣，遞相催速，使早報問，則依憲法而論決也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '2887/2519/1699/6176/1567/843/4484;7741/9169/4187/5707/982/5798/1511', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5853, '鬼薪白粲鉗釱髡;不肯謹慎自令然', '', '鬼薪白粲鉗釱髡，1不肯謹慎自令然。2', '[顏注-1]：此謂輕罰，非重罪者也。鬼薪，主取薪柴以供祭祀鬼神也。白粲，主擇米，取精白粲粲然者也。以鐵錔頭曰鉗，錔足曰釱。𩮜髮曰髡。\n[顏注-2]：己則招之，又誰咎也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '5821/622/4931/4351/9363/9364/5762;7706/2734/1520/6714/2224/5779/6423', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5854, '輸屬詔作谿谷山;箛篍起居課後先', '', '輸屬詔作谿谷山，1箛篍起居課後先。2', '[顏注-1]：輸屬，言配入其處也。詔勑別有所輸作也。一曰詔書處罰，令其輸作也。山瀆無所通曰谿，泉出通川曰谷。一曰水注川曰谿，注谿曰谷。配於谿谷及山，徒役之也。\n[顏注-2]：箛，吹鞭也。篍，吹筩也。起居，謂晨起夜臥及休食時也。言督作之司，吹鞭及竹筩，爲起居之節度。又校其程課，先者免罰，後者懲責也。今之伎倡欲相號令者，則吹指爲節，蓋吹鞭之遺事。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '9578/5429/1529/5075/7503/7502/5850;3000/3001/1017/5404/1539/1250/5472', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5855, '斬伐材木斫株根;犯禍事危置對曹', '', '斬伐材木斫株根，1犯禍事危置對曹。2', '[顏注-1]：斬，截也。伐，攻取也。材謂木梃也。枿栽曰株，樹本曰根。言徒役之人給此事也。\n[顏注-2]：犯禍，犯法而致禍也。事危，其事傾危也。旣被驗問，則置立對辭於曹府也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '9610/5167/3612/3413/9479/3565/3564;6343/67/1934/6009/4844/1751/3051', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5856, '謾訑首匿愁勿聊;縛束脫漏亡命流', '僇', '謾訑首匿愁勿聊[僇]，1縛束脫漏亡命流。2', '[顏注-1]：謾訑，巧黠不實也。首匿，爲頭首而藏匿罪人也。勿聊，無聊賴也。\n[顏注-2]：命者，名也。旣被縛束，忽脫漏，則亡其名籍而流迸也。', '急就篇', '急就篇', '1', DEFAULT, DEFAULT, '1598/1597/5703/8420/6926/6073/7802;8580/3899/2653/7446/8413/840/7476', '5172', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5857, '攻擊劫奪檻車膠;嗇夫假;左;扶致牢', '', '攻擊劫奪檻車膠，1嗇夫假佐{左}扶致牢。2', '[顏注-1]：言強盜、羣盜相與攻擊劫奪人者，吏捕得之，載以檻車，又加膠漆，取周密也。檻者，車如檻形，義在幽閑，防泄露也。一曰膠者，謂膠罪人之目，使不得開，絕變難也。擊，字或作剽，剽亦劫也。\n[顏注-2]：嗇夫，鄉之有秩者也。假佐，縣之假吏也。扶致牢者，扶持罪人而致之於牢獄也。一曰假佐者，嗇夫之權佐也。謂之牢者，言若豢豕之牢，亦取其堅牢也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '2052/8066/9248/2320/3826/9514/2729;3346/6676/5076;3028;7856/3367/757', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5858, '疻痏保辜;謕呼號;乏興猥逮詗讂;求', '嫴;敻', '疻痏保辜[嫴]謕呼號，1乏興猥逮詗讂[敻]求。2', '[顏注-1]：歐人皮膚腫起曰疻，歐傷曰痏。保辜者，各隨其狀輕重，令歐者以日數保之，限內致死，則坐重辜也。謕呼號者，被歐之人稱痛酷也。號，或作獋，音義同。\n[顏注-2]：律有乏興之法，謂官有所興發，而輒稽留闕乏其事也。猥，盛多也。逮，傳捕也。猥逮者，矯爲官府，多有逮捕也。詗謂知處密告之也。讂，隱語也，謂傎伺官府利害，隱密其事，有所追求也。一曰乏興之人，棄家逃匿，故官司逮捕，詗讂而求也。', '急就篇', '急就篇', '1;3', DEFAULT, DEFAULT, '4760/4761/4952/9758;1655/826/3069;1088/1783/6322/1147/1702/1703;5375', '8254;2093', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5859, '輒覺沒入檄報留;受賕枉法忿怒仇', '', '輒覺沒入檄報留，1受賕枉法忿怒仇。2', '[顏注-1]：檄者，以木爲書，長二尺。報者，處當罪人也。言有乏興、猥逮及詗讂者，其事發覺，身則沒入爲奴婢，或沒其家財，則爲檄書，處當其罪而留之也。\n[顏注-2]：以財求事曰賕。言受人財者，枉曲正法，忿怒良直，反爲仇讎也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '9536/5511/7299/3281/3772/6651/9197;2519/3988/3592/6262/6874/6879/5173', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5860, '讒;諛爭語相;牴;觸;憂念緩急悍勇獨', '', '䜛{讒}諛爭語相觝{牴}觸，1憂念緩急悍勇獨。2', '[顏注-1]：䜛，相譖也。諛，諂言也。爭語，謂各持辯說也。有爭語者，常相觝距而擊觸也。一曰爭語，謂交相譖毀也。\n[顏注-2]：言無賴之人，不自修整，以䧟罪惡，臨機迫猝，更生邪計，妄爲凶悍，當窘厄也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '1688;1591/2521/1477/2162;764;2840;3368/6721/8781/6811/6834/9245/6357', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5861, '𠧟;肯省察諷諫讀;涇水注渭街術曲', '', '廼{𠧟}肯省察諷諫讀，1涇水注渭街術曲。2', '[顏注-1]：迺肯，猶言寧肯，謂不肯也。平居之時，旣無立操之方，又違從善之義，有諷諫者，不肯省察而習讀之，所以遂成過惡，以至喪敗也。一曰危急之後，方自省躬而讀諷諫，思改過也。\n[顏注-2]：此說京畿之內也。涇水岀安定 汧頭山，至陽陵而入渭。四逹之道曰街，邑中之道曰術，里中之道曰曲。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '3053;2734/2220/4583/1491/1537/1493;7006/6984/7277/7007/1268/1267/8444', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5862, '筆研籌筭膏火燭;賴赦救解貶秩禄', '', '筆研籌筭膏火燭，1賴赦救解貶秩禄。2', '[顏注-1]：此記官府之中給長史以下之具也。筆，所以書也，一名不律，亦謂之聿。研，所以研墨也。籌，所以付與；筭，所以計度，皆以竹爲之。膏火爲燈炬、蜜燭。\n[顏注-2]：此說在官有罪，幸逢赦令，及人救解，得免重罰，降其秩次，而損其禄也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '1941/6048/3002/3009/2613/6416/6477;3964/2033/2030/2851/3985/4468/15', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5863, '邯鄲河閒沛巴蜀;潁川臨淮集課録', '', '邯鄲河閒沛巴蜀，1潁川臨淮集課録。2', '[顏注-1]：此略說諸郡名也。邯鄲，趙國所都也。河閒，言在兩河之閒也。沛者，本秦 泗水郡屬縣也，漢興，更爲沛郡。巴，巴郡也。蜀，蜀郡也。\n[顏注-2]：潁川，因潁水爲名也。臨淮，居淮水之旁也。集課録者，諸郡各上其計，總會京師，次其名録。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '4066/4067/6986/7762/7100/9753/8828;7061/7483/5248/7053/2368/1539/9278', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5864, '依溷汙染貪者辱', '', '依溷汙染貪者辱。1', '[顏注-1]：此戒守宰以下也。依，近也。溷，廁也。言近溷廁者，則被汙染。貪賄賂者，必致戮辱。以財物比於糞穢，爲害染人也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '5048/7207/7350/7427/3984/2229/9796', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5865, '漢地廣大;無不容盛', '', '漢地廣大，無不容盛。1', '[顏注-1]：此爲終篇，述帝徳也。容盛，猶言覆載也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '7009/9022/5940/6598;8416/7706/4589/3141', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5866, '萬方來朝;臣妾使令', '', '萬方來朝，臣妾使令。1', '[顏注-1]：威𤫊所被，無思不服。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '9735/5455/3348/4289;1953/1747/5101/5779', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5867, '邊境無事;中國安寧', '', '邊境無事，中國安寧。1', '[顏注-1]：遐邇謐淸，兵革靡用也。境，字或作竟，古文通也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '1209/9156/8416/1934;231/3917/4578/3057', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5868, '百姓承徳;陰陽和平', '', '百姓承徳，陰陽和平。1', '[顏注-1]：至諴感神，上玄降祉也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '2232/8112/7914/1224;9623/9624/846/3074', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5869, '風雨時節;莫不滋榮', '', '風雨時節，莫不滋榮。1', '[顏注-1]：仁及草木，皆茂暢也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '8979/7527/4191/2877;701/7706/7235/3538', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5870, '災蝗不起;五穀孰成', '', '災蝗不起，五穀孰成。1', '[顏注-1]：每獲豐年，倉廪實也。天反時爲災。蟲食苗曰蝗。五穀者，黍、稷、麻、菽、麥也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '6489/8867/7706/1017;9727/4485/1886/9749', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5871, '賢聖並進;博士先生', '', '賢聖並進，博士先生。1', '[顏注-1]：野無隱𨓜，皆升朝也。博士者，多聞之士。先生，謂老成之人也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '3945/7803/6698/1113;1468/226/5472/3876', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5872, '長樂無極老復丁', '', '長樂無極老復丁。1', '[顏注-1]：擊壤行歌，喜寬政也。老復丁者，家有高年，則蠲其子孫，免賦役也。', '急就篇', '急就篇', '', DEFAULT, DEFAULT, '6069/3764/8416/3626/5377/1226/9747', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5873, '淵;懿;邃;嘖;深', '', '淵、懿、邃、賾{嘖}，深也。', '', '廣詁第一', '小爾雅', '', DEFAULT, DEFAULT, '7211;6645;4677;915;7036', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5874, '封;巨;莫;莽;艾;祁;大', '鉅;漠;茻', '封、巨[鉅]、莫[漠]、莽[茻]、艾1、祁，大也。', '[音義-1]：牛盖切', '廣詁第一', '小爾雅', '2;3;4', DEFAULT, DEFAULT, '9076;3033;701;702;389;4063;6598', '9448;7132;700', 'null', 'null');
