INSERT INTO `t_han_nga` VALUES (5876, '蓋;戴;燾;蒙;冒;覆', '盍;冡', '蓋[盍]、戴、燾、蒙[冡]、冒，覆也。', '', '廣詁第一', '小爾雅', '1;4', DEFAULT, DEFAULT, '586;1779;6522;649;4809;4854', '3181;4805', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5877, '鐘;崇;府;衆;積;灌;叢;樸;聚', '中;𦼉', '鐘[中]、崇、府、衆、積、灌[𦼉]、叢、樸1，聚也。', '[音義-1]：博木切', '廣詁第一', '小爾雅', '1;6', DEFAULT, DEFAULT, '9388;5882;5924;5237;4467;7044;1750;3607;5238', '231;644', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5878, '閱;搜;履;庇;具', '數;體;比', '閱、搜[數]、履[體]、庀{庇}[比]，具也。', '', '廣詁第一', '小爾雅', '2;3;4', DEFAULT, DEFAULT, '7783;8092;5432;5957;1771', '2011;2587;5229', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5879, '攻;爲;詰;相;旬;宰;營;匠;治', '工', '攻[工]、爲、詰、相、旬、宰、營、匠，治也。', '', '廣詁第一', '小爾雅', '1', DEFAULT, DEFAULT, '2052;1882;1696;2162;5808;4595;4631;8426;7089', '3030', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5880, '蠲;祓;禋;屑;潔', '', '蠲、祓、禋、屑，潔也。', '', '廣詁第一', '小爾雅', '', DEFAULT, DEFAULT, '8829;49;29;5406;7469', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5881, '勿;蔑;微;末;沒;無', '末', '勿、蔑[末]、微、末、沒，無也。', '', '廣詁第一', '小爾雅', '2', DEFAULT, DEFAULT, '6073;2332;1236;3566;7299;8416', '3566', 'null', 'null');
