INSERT INTO `t_han_nga` VALUES (5884, '邵;媚;旨;伐;美', '妭', '邵、媚、旨、伐[妭]，美也。', '', '廣詁第一', '小爾雅', '4', DEFAULT, DEFAULT, '4051;8185;3043;5167;2356', '8160', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5885, '賢;襃;䋣;優;饒;夥;多', '', '賢、裒{襃}、繁{䋣}、優、饒、夥，多也。', '', '廣詁第一', '小爾雅', '', DEFAULT, DEFAULT, '3945;5303;8707;5091;3246;4337;4336', '', 'null', 'null');
