INSERT INTO `t_han_nga` VALUES (5889, '索;搴;探;襃;鉤;掠;采;畧;取', '𡩡;捊', '索[𡩡]、搴、探、裒{襃}[捊]、鉤、掠、採{采}、畧，取也。', '', '廣詁第一', '小爾雅', '1;4', DEFAULT, DEFAULT, '3873;8002;8003;5303;1460;8099;3794;9192;1926', '4617;7911', 'null', 'null');
