INSERT INTO `t_han_nga` VALUES (5891, '固;歷;镾;宿;舊;尚;久', '久', '固、歷、彌{镾}、宿、舊[久]、尚，久也。', '', '廣詁第一', '小爾雅', '5', DEFAULT, DEFAULT, '3929;1070;6071;4602;2325;711;3409', '3409', 'null', 'null');
