INSERT INTO `t_han_nga` VALUES (5896, '承;弟;班;列;次', '𩢾', '承、弟、班、列[𩢾]，次也。', '', '廣詁第一', '小爾雅', '4', DEFAULT, DEFAULT, '7914;3401;222;2772;5586', '6212', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5897, '戸;悛;格;扈;止', '', '戸、悛、格、扈，止也。', '', '廣詁第一', '小爾雅', '', DEFAULT, DEFAULT, '7722;6794;3603;4025;1064', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5898, '幽;曀;闇;昧;冥', '', '幽、曀、闇、昧，冥也。', '', '廣詁第一', '小爾雅', '', DEFAULT, DEFAULT, '2502;4229;7772;4194;4313', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5899, '最;凡;目;質;要', '', '最、凡、目、質，要也。', '', '廣詁第一', '小爾雅', '', DEFAULT, DEFAULT, '4810;9020;2096;3972;1785', '', 'null', 'null');
