INSERT INTO `t_han_nga` VALUES (5901, '而;乃;爾;若;汝', '', '而、乃、爾、若，汝也。', '', '廣詁第一', '小爾雅', '', DEFAULT, DEFAULT, '6076;3052;2090;599;7019', '', 'null', 'null');
