INSERT INTO `t_han_nga` VALUES (6093, '寡;煢;嫠', '赹', '凡無妻、無夫通謂之寡。寡夫曰㷀{煢}[赹]1，寡婦曰嫠2。', '[音義-1]：渠營切，亦作惸\n[音義-2]：陵之切', '廣義第四', '小爾雅', '2', DEFAULT, DEFAULT, '4608;7702;8355', '1015', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6094, '屬婦;逮', '', '妾婦之賤者謂之屬婦。屬1，逮也。逮婦之名，言其微也。', '[音義-1]：時欲切', '廣義第四', '小爾雅', '', DEFAULT, DEFAULT, '5429/8132;1147', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6095, '幸', '', '非分而得謂之幸1。', '[音義-1]：扶問切', '廣義第四', '小爾雅', '', DEFAULT, DEFAULT, '6625', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6096, '讓', '', '詰責以辭謂之讓。', '', '廣義第四', '小爾雅', '', DEFAULT, DEFAULT, '1692', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6097, '淫;烝;報;通', '勽', '男女不以禮交謂之淫。上淫曰烝，下淫曰報[勽]，旁淫曰通。', '', '廣義第四', '小爾雅', '3', DEFAULT, DEFAULT, '7223;6431;6651;1134', '5809', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6098, '慙;愧;戁;恧;逡', '夋', '不直失節謂之慙，慙，愧也。面慙曰戁1，心慙曰恧2，體慙曰逡[夋]。', '[音義-1]：乃版切\n[音義-2]：女六切', '廣義第四', '小爾雅', '5', DEFAULT, DEFAULT, '6959;8346;6725;6960;1161', '3364', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6099, '大行', '', '諱死謂之大行。', '', '廣名第五', '小爾雅', '', DEFAULT, DEFAULT, '6598/1266', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6100, '蘇', '', '死而復生謂之蘇。', '', '廣名第五', '小爾雅', '', DEFAULT, DEFAULT, '256', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6101, '阽', '', '疾甚謂之阽1。', '[音義-1]：余廉切', '廣名第五', '小爾雅', '', DEFAULT, DEFAULT, '9691', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6102, '戚;近;從', '', '請天子命曰「未可以戚先王」，請諸侯命曰「未可以近先君」，請大夫命曰「未可以從先子1」。', '[音義-1]：牆容切', '廣名第五', '小爾雅', '', DEFAULT, DEFAULT, '8402;1180;5227', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6103, '櫬;柩', '', '空棺謂之櫬，有屍謂之柩。', '', '廣名第五', '小爾雅', '', DEFAULT, DEFAULT, '3830;8442', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6104, '賵;襚', '', '饋死者謂之賵1，衣服謂之襚2。', '[音義-1]：撫鳯切\n[音義-2]：徐醉切', '廣名第五', '小爾雅', '', DEFAULT, DEFAULT, '3996;5365', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6105, '殔;池;壙;竁;窆;封', '', '埋柩謂之殔1。殔坎謂之池。壙謂之竁2。下棺謂之窆。塡竁謂之封。', '[音義-1]：羊至切\n[音義-2]：昌縜切，又充芮切', '廣名第五', '小爾雅', '', DEFAULT, DEFAULT, '2544;7259;9116;4680;4681;9076', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6106, '宰;冢;壟;塋', '', '宰，塚{冢}也。壟，塋也。', '', '廣名第五', '小爾雅', '', DEFAULT, DEFAULT, '4595;5815;9144;9141', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6107, '殤', '', '無主之鬼謂之殤。', '', '廣名第五', '小爾雅', '', DEFAULT, DEFAULT, '2538', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6108, '織;繒', '', '治絲曰織。織，繒也1。', '[音義-1]：慈陵切', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '8533;8592', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6109, '布', '', '麻、紵、葛曰布。布，通名也。', '', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '4912', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6110, '纊;緜;絮;纊', '', '纊，綿{緜}也。絮之細者曰纊。', '', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '8728;8515;8726;8728', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6111, '繒;縞;素', '', '繒之精者曰縞1，縞之麄者曰素。', '[音義-1]：古老切', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '8592;8601;8776', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6112, '葛;絺;綌', '', '葛之精者曰絺，麄者曰綌。', '', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '458;8739;8740', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6113, '元服', '', '在首謂之元服。', '', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '2/5449', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6114, '弁髦', '', '弁髦，大古冠，布冠而敝之者也。', '', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '5469/5736', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6115, '題;定;顚;顔;顙;頟', '頂', '題、定[頂]、顚、顔、䫙{顙}，額{頟}也。', '', '廣服第六', '小爾雅', '2', DEFAULT, DEFAULT, '5611;4576;5608;5602;5610;5612', '5609', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6116, '璽;印', '', '璽謂之印。', '', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '9077;5791', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6117, '紱;綬', '', '紱謂之綬1。', '[音義-1]：分物切', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '4927;8648', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6118, '襜褕;童容', '', '襜褕謂之童容1。', '[音義-1]：蚩占切。容朱切', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '5292/5258;1746/4589', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6119, '藍縷', '襤褸', '布褐而紩之謂之藍縷[襤褸]1。', '[音義-1]：直質切', '廣服第六', '小爾雅', '1', DEFAULT, DEFAULT, '282/8676', '5283/5269', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6120, '絝;裳', '', '袴{絝}謂之裳。', '', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '8661;4872', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6121, '冉', '襜', '蔽膝謂之袡{冉}[襜]1。', '[音義-1]：蚩廉切', '廣服第六', '小爾雅', '1', DEFAULT, DEFAULT, '6075', '5292', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6122, '厲', '', '帶之垂者謂之厲。', '', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '5985', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6123, '幎', '', '大巾謂之幂{幎}1。', '[音義-1]：莫狄切', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '4877', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6124, '楃;幕', '', '覆帳謂之幄。幄{楃}，幕也。', '', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '3674;4883', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6125, '簀;笫', '', '簀，牀笫也1。', '[音義-1]：士莊切', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '2909;2910', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6126, '扇;䈉', '', '大扇謂之䈉。', '', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '7724;2956', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6127, '杖;梃', '', '杖謂之梃。', '', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '3741;3580', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6128, '鍵;籥', '𨷲', '鍵謂之籥[𨷲]1。', '[音義-1]：巨偃切', '廣服第六', '小爾雅', '2', DEFAULT, DEFAULT, '9314;2891', '7774', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6129, '弈', '', '棊局謂之弈。', '', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '1770', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6130, '屨;逹屨', '', '在足謂之屨。屨，尊者曰逹屨。逹，謂之金舄，舄而金絇也。', '', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '5433;1163/5433', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6131, '侯;鵠;正;執', '臬', '射有張布謂之侯。侯中者謂之鵠1。鵠中者謂之正，正方二尺。正中者謂之𣙗{執}[臬]2，𣙗方六寸。', '[音義-1]：古毒切\n[音義-2]：諸城切。倪結切，亦作臬', '廣器第七', '小爾雅', '4', DEFAULT, DEFAULT, '3313;2414;1087;6648', '3761', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6132, '棘;戟', '', '棘，戟也。', '', '廣器第七', '小爾雅', '', DEFAULT, DEFAULT, '4399;8377', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6133, '戚;鉞;斧', '戉', '鏚{戚}1、鉞[戉]，斧也。', '[音義-1]：鎗歷切', '廣器第七', '小爾雅', '2', DEFAULT, DEFAULT, '8402;9422;9477', '8401', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6134, '干;瞂;盾', '𢧀', '干[𢧀]、瞂1，盾也。', '[音義-1]：房越切，又徒本切', '廣器第七', '小爾雅', '1', DEFAULT, DEFAULT, '1441;2222;2221', '8381', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6135, '戈;戟', '', '戈，句孑戟也1。', '[音義-1]：古侯切。吉列切', '廣器第七', '小爾雅', '', DEFAULT, DEFAULT, '8375;8377', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6136, '鞘;室;鞞;棓;珌;飾', '琫', '刀之韒{鞘}謂之室，室謂之鞞1。鞛{棓}[琫]2、珌3，鞞之飾也。', '[音義-1]：布頂切\n[音義-2]：布孔切\n[音義-3]：壁吉切', '廣器第七', '小爾雅', '4', DEFAULT, DEFAULT, '1850;4559;1813;3743;128;4896', '127', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6137, '服;弢', '', '矢服謂之弢1。', '[音義-1]：他刀切', '廣器第七', '小爾雅', '', DEFAULT, DEFAULT, '5449;8498', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6138, '船;艇;符;頭;舳;尾;艫;楫;橈', '', '小船謂之艇1，艇之小者曰䒀{符}2。船頭謂之舳，尾謂之艫。楫謂之橈。', '[音義-1]：待鼎切\n[音義-2]：薄故切', '廣器第七', '小爾雅', '', DEFAULT, DEFAULT, '5440;5452;2899;5601;5442;5428;5443;3790;3593', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6139, '龍;轅;輈;軫;枕;較;榦;衡;軶;烏喙', '', '車轅上者謂之𨏠{龍}1，轅謂之輈，軫謂之枕，較謂之幹{榦}2。衡，軶也，軶上謂之烏喙。', '[音義-1]：力公切\n[音義-2]：訖岳切', '廣器第七', '小爾雅', '', DEFAULT, DEFAULT, '7689;9558;9559;9542;3680;9533;3620;2844;9562;2488/783', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6140, '纍;綆;繘', '', '纍1、綆，繘也2。', '[音義-1]：力追切\n[音義-2]：以律切', '廣器第七', '小爾雅', '', DEFAULT, DEFAULT, '8686;8721;8720', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6141, '韜;索;繩', '絛', '縚{韜}[絛]1，索也。大者謂之索，小者謂之繩。', '[音義-1]：他刀切，亦作絛', '廣器第七', '小爾雅', '1', DEFAULT, DEFAULT, '3388;3873;8694', '8666', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6142, '䋫;索', '', '詘而戾之爲䋫1，繆而紾之爲索2。', '[音義-1]：側莖切\n[音義-2]：章忍切，或作縝', '廣器第七', '小爾雅', '', DEFAULT, DEFAULT, '8695;3873', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6143, '墉;坰;陴', '', '墉地謂之坰。墉牆謂之陴1。', '[音義-1]：頻彌切', '廣器第七', '小爾雅', '', DEFAULT, DEFAULT, '9084;3324;9702', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6144, '邍;大原', '', '{邍}高平謂之大原。', '', '廣器第七', '小爾雅', '', DEFAULT, DEFAULT, '1204;6598/7496', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6145, '汪;池', '', '汪，池也。', '', '廣器第七', '小爾雅', '', DEFAULT, DEFAULT, '7159;7259', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6146, '汭', '', '水之北謂之汭。', '', '廣器第七', '小爾雅', '', DEFAULT, DEFAULT, '7146', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6147, '廣;衍', '', '澤之廣者謂之衍。', '', '廣器第七', '小爾雅', '', DEFAULT, DEFAULT, '5940;7138', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6148, '稾;稈;芻', '犓', '藁{稾}謂之稈1，稈謂之芻[犓]，生曰芻。', '[音義-1]：古旱切', '廣物第八', '小爾雅', '3', DEFAULT, DEFAULT, '4476;4475;611', '758', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6149, '穀;粒;菜;蔬;穗;穎;𢧵;銍', '', '穀謂之粒1，菜謂之蔬。禾穗謂之穎，截{𢧵}穎謂之銍2。', '[音義-1]：（⿰禾立）力入切，亦作粒\n[音義-2]：陟栗切', '廣物第八', '小爾雅', '', DEFAULT, DEFAULT, '4485;4358;558;691;4449;4448;8388;9359', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6150, '拔;揠;擢', '', '拔心曰揠1，拔根曰擢。', '[音義-1]：烏轄切', '廣物第八', '小爾雅', '', DEFAULT, DEFAULT, '7997;7998;7996', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6151, '把;秉;筥;稯;秅', '', '把謂之秉，秉四曰筥1，筥十曰稯2。〔稯十曰秅。〕', '[音義-1]：居許切\n[音義-2]：於求切', '廣物第八', '小爾雅', '', DEFAULT, DEFAULT, '7877;1919;2922;4498;4500', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6152, '棘;棗', '', '棘之實謂之棗。', '', '廣物第八', '小爾雅', '', DEFAULT, DEFAULT, '4399;4398', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6153, '桑;葚', '', '桑之實謂之葚1。', '[音義-1]：食荏切，又作（⿱艸⿰禾甚）、椹', '廣物第八', '小爾雅', '', DEFAULT, DEFAULT, '3860;480', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6154, '柞;象', '樣', '柞之實謂之橡{象}[樣]1。', '[音義-1]：徐兩切', '廣物第八', '小爾雅', '2', DEFAULT, DEFAULT, '3483;6135', '3479', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6155, '土;根', '', '〔桑土謂之桑根。〕', '', '廣物第八', '小爾雅', '', DEFAULT, DEFAULT, '9021;3564', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6156, '陽鳥;鴻;鴈', '', '去陰就陽謂之陽鳥，鴻、鴈是也。', '', '廣鳥第九', '小爾雅', '', DEFAULT, DEFAULT, '9624/2369;2415;2423', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6157, '慈烏;雅烏;鸒;燕烏;蒼烏', '', '純黑而反哺者謂之慈烏。小而腹下白、不反哺者謂之雅烏，鸒也1。白項而群飛者謂之燕烏，白脰烏也2。〔大而白脰者謂之蒼烏。〕', '[音義-1]：羊茹切\n[音義-2]：田候切', '廣鳥第九', '小爾雅', '', DEFAULT, DEFAULT, '6747/2488;2280/2488;2386;7688/2488;538/2488', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6158, '豕;彘;豬;豚;豜;豵', '', '豕，彘也。彘，猪{豬}也，其子曰豚。豕之大者謂之豜1，小者謂之豵2。', '[音義-1]：古賢切\n[音義-2]：子紅切', '廣獸第十', '小爾雅', '', DEFAULT, DEFAULT, '6078;6106;6079;6110;6084;6082', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6159, '巢;窠', '', '鳥之所乳謂之巢，鷄、雉所乳謂之窠。', '', '廣獸第十', '小爾雅', '', DEFAULT, DEFAULT, '3894;4652', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6160, '場;堀;潛;槮', '罧;滲', '鹿之所息謂之場，兔之所息謂之窟{堀}，魚之所息謂之潜{潛}[罧]，潜，槮[滲]也1，積柴水中而魚舍焉2。', '[音義-1]：桑感切\n[音義-2]：如字', '廣獸第十', '小爾雅', '3;4', DEFAULT, DEFAULT, '9147;9055;7290;3598', '4831;7205', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6161, '䞨;步;仞;尋;常', '', '跬{䞨}1，一舉足也。倍跬謂之步。四尺謂之仞，倍仞謂之尋，尋，舒兩肱也。倍尋謂之常。', '[音義-1]：丘弭切', '度', '小爾雅', '', DEFAULT, DEFAULT, '1050;1081;4955;1984;4872', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6162, '墨;丈;端;兩;匹;束', '', '五尺謂之墨，倍墨謂之丈，倍丈謂之端，倍端謂之兩1，倍兩謂之匹，匹有五謂之束。', '[音義-1]：力讓切', '度', '小爾雅', '', DEFAULT, DEFAULT, '9078;1464;6682;4813;8424;3899', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6163, '盛;溢;匊', '', '一手之盛謂之溢1，兩手謂之掬{匊}。', '[音義-1]：時征切。弋質切', '量', '小爾雅', '', DEFAULT, DEFAULT, '3141;7400;5805', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6164, '匊;豆;區;釜;藪;缶;鍾;秉;斛', '', '掬，一升也。掬{匊}四謂之豆，豆四謂之區，區四謂之釜，釜二有半謂之藪，藪二有半謂之缶，二缶謂之鍾，二鍾謂之秉。秉，十六斛也。', '', '量', '小爾雅', '', DEFAULT, DEFAULT, '5805;3096;8419;1861;563;3287;9294;1919;9492', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6165, '銖;兩;捷;舉;鋝;鍰;斤;衡;稱;鈞;石;鼓', '䄷', '二十四銖曰兩1，兩有半曰捷，倍捷曰舉，倍舉曰鋝2，鋝謂之鍰3，二鍰四兩謂之斤，斤十謂之衡，衡有半謂之秤{稱}，秤二謂之鈞，鈞四謂之石[䄷]，石四謂之鼓。然則鼓四百八十斤也。', '[音義-1]：慵朱切\n[音義-2]：龍輟切\n[音義-3]：胡關切', '衡', '小爾雅', '11', DEFAULT, DEFAULT, '9374;4813;8089;7959;9375;9376;9476;2844;4495;9379;6011;3083', '4501', 'null', 'null');
